INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (1, '刘长健、丰辉', '学工办/团委', '周荃', '2017-05-16 00:00:00', '信息学院举办特斯拉线圈制作大赛', '近期，有这么一群人，离开空调开放的寝室，离开吸引力十足的电脑，组队走进了科学的副本——特斯拉线圈制作活动。据悉，特斯拉线圈不仅仅是被用在游戏或艺术方面，它拥有重大意义的用途，比如利用特斯拉线圈可以实现电能的无线传输，且该方式传输效率高、对生态破坏性小。比赛以两人一组，在150分钟之内根据活动所提供的电路图及材料发挥自己的创意进行特斯拉线圈。一个人在缠绕着初级线圈和次级线圈、另外一人在焊接着电路。经过一番激烈的角逐，每个小组都展现了各自的个性风格与创新能力，评审团共选出一、二、三等奖、最佳技术制作奖、最佳创意奖各一名。这次活动的成功举办，极大地调动了同学们参与活动的积极性，培养了同学们的思考能力，团队协作能力，以及动手能力并且对同学们的专业技术能力提升有了很大的帮助。', 'http://info.jhc.cn/2017/0516/c201a79927/page.htm', 'http://info.jhc.cn/_upload/article/images/0c/c8/978bee9f42ec92acfdc15ada0c72/829075f1-ab03-45b7-9d68-fa2409445089.jpg|http://info.jhc.cn/_upload/article/images/0c/c8/978bee9f42ec92acfdc15ada0c72/f71bb6cc-bb7c-4ce7-87be-2695c7c7f4e2.jpg', '2017-05-17 14:12:53.418391');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (2, '吕威威', '学工办/团委', '周荃', '2017-05-15 00:00:00', '信息工程学院顺利举办第十四届田径运动会', '5月13日，信息工程学院第十四届田径运动会在我校体育场顺利完成各项预定赛事，圆满落下帷幕。此次运动会，全院共44个班，448名运动员参加了10个大项的角逐。经过一天紧张的比赛，应电164班和计算机151班分获大二大一年级组第一名，电信151班等4个班级获得精神文明奖。据悉，此次比赛中应电152班王震昊同学打破学院记录一项（大二男子铅球）。', 'http://info.jhc.cn/2017/0515/c201a79888/page.htm', 'http://info.jhc.cn/_upload/article/images/2b/a8/7e95b166413c9d335eed44fa0b76/76cccdcc-d033-425c-a7f6-185d7cf4a250.png|http://info.jhc.cn/_upload/article/images/2b/a8/7e95b166413c9d335eed44fa0b76/d878b1c7-7ab1-4f32-a3a2-824f6197e182.png', '2017-05-17 14:12:53.446109');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (3, '周荃', '办公室', '周荃', '2017-05-12 00:00:00', '信息学院六届一次教职工暨工会会员代表大会顺利闭幕', '5月11-12日，学院召开六届一次教职工暨工会会员代表大会，23名正式代表及4名列席代表参加大会。      会上，陈海荣院长作了题为《抓机遇，重落实，显特色，全力开创教育教学改革新局面》的工作报告。学院五届教代会期间在专业内涵建设、教学改革创新、人才培养质量提升、校企合作开展、师资团队结构优化、科研与服务能力提升、教育国际化等方面取得的成果；报告也指出了学院目前存在的问题和面临的挑战，尤其在适应高考制度改革、专业内涵提升、本科及中外合作办学等方面挑战颇多，报告明确提出了今年的工作任务，为学院今后和谐发展、特色发展、内涵发展、创新发展描绘了蓝图，明确了方向。贾美倩书记作了《分工会工作报告》。会议期间，代表们立足学院发展，积极献计献策，认真听取并原则审议通过了《院长工作报告》和学院系列制度修订稿，选举产生了第六届工会委员会委员。大会号召全院上下要紧跟时代步伐，继续以昂扬的斗志和饱满的热情，振奋精神，开拓创新，全力以赴实现学院新跨越而努力奋斗。', 'http://info.jhc.cn/2017/0512/c201a79792/page.htm', 'http://info.jhc.cn/_upload/article/images/5c/45/13b4113543618d9b33714e719f1f/4f7181b5-854e-46f0-b58c-4440ab2dfd9d.jpg|http://info.jhc.cn/_upload/article/images/5c/45/13b4113543618d9b33714e719f1f/a2552832-d68e-49df-9491-153f9121faf6.jpg', '2017-05-17 14:12:53.468471');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (4, '花有清', '电气自动化工程部', '周荃', '2017-05-11 00:00:00', '信息学子获2017年全国高职高专院校技能大赛“自动化生产线安装与调试”竞赛一等奖', '2017年5月8-9日， 2017年全国职业院校技能大赛“自动化生产线安装与调试”赛项在天津中德应用技术大学落下帷幕，我校信息学院从全国79支代表队中脱颖而出，喜获比赛一等奖。据悉，比赛以YL_335B自动生产线实训装置为平台，大赛与世界技能大赛接轨，分为两阶段进行。其中，第一阶段（4小时）要求选手对原有平台的三个工作站进行安装、电气设计与接线、组态设计与调试、变频器与伺服驱动器调试、PLC程序设计等任务；第二阶段要求选手完成两个工作站的安装与调试，其中包含一个未知站，选手根据提供的技术手册、安装图纸，完成该未知站的安装与调试，并与第一阶段完成的任务进行衔接，实现整条生产线的自动运行。本次赛制的改革，除了要求参赛队员在气动控制、传感器应用、PLC控制、变频器、伺服驱动、MCGS组态设计等方面具备扎实的专业理论知识和较强的实践能力，还需有很强的自学能力及根据现场任务的情况做出良好的应变能力。经过激烈角逐，最终，由我院花有清、楼蔚松两位老师指导，郭明超、胡浩海两位学生组成的代表队凭借出色发挥喜获比赛一等奖。学校领导对全国职业技能大赛十分重视，校长钟依均、教务处处长刘鲁平、信息学院院长陈海荣来到赛场看望参赛师生。', 'http://info.jhc.cn/2017/0511/c201a79666/page.htm', 'http://info.jhc.cn/_upload/article/images/b1/73/5863486b40c99614449bd46f8ec7/c1ab69ef-11bb-4a94-8d57-637d70412127.png|http://info.jhc.cn/_upload/article/images/b1/73/5863486b40c99614449bd46f8ec7/cb9dcde1-474f-41bd-a08f-0a3a1897fddb.png', '2017-05-17 14:12:53.490485');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (5, '苏红富、王静、余炜', '通信与网络工程部', '周荃', '2017-05-09 00:00:00', '信息学子获2017年全国高职高专院校技能大赛”物联网技术应用”竞赛一等奖', '2017年5月8日，全国高职高专院校技能大赛”物联网技术应用”竞赛在天津轻工职业技术学院举行，共有来自全国高职院校的94只代表队参加。由我校信息学院王静、余炜老师教师指导，金茂鑫、朱杰、陈国锋等三位同学组成的浙江代表队获得全国一等奖的好成绩。学校领导对职业技能大赛十分重视，校长钟依均、教务处处长刘鲁平到来到赛场看望参赛师生，并向获奖师生表示祝贺。据悉，此次物联网技术应用大赛以综合型商场智能化系统为设计背景，围绕影院环境监控、超市购物、停车场停车计费等项目展开设计与实现。在4小时时间内完成综合型商场智能化系统布局、设备安装、系统组网与调试，传感及网络层嵌入式程序开发，商场智能化系统PC端程序开发，商场智能化系统Android端程序开发，商场智慧云系统应用开发，商场智能化系统设计报告等多项考核任务，对专业技能及团队配合有着极高的要求。通过比赛，提高了学生的专业技能，为物联网应用技术新兴产业培养了高质量技能型人才。', 'http://info.jhc.cn/2017/0509/c201a79609/page.htm', 'http://info.jhc.cn/_upload/article/images/fd/a6/0a721e0944e9885ca2559bfb1490/ad0db04b-46df-42b0-8b10-13071dcd08a1.jpg', '2017-05-17 14:12:53.512838');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (6, '徐华丹', '教科办', '周荃', '2017-05-04 00:00:00', '信息学院2017年“六个一批”项目建设研讨会顺利召开', '5月3日，2017年“六个一批”项目建设研讨会在东实北215召开。2017年“六个一批”项目建设负责人、在线开放课堂项目负责人、教科办相关人员参加了会议。会上，赵敏笑副院长就“六个一批”项目建设标准、验收方法、建设经费使用情况及建设平台与使用进行了简单说明，并对在线开放课堂项目的建设标准和建设关键点也进行了分析。最后，重点介绍了省高校微课比赛的要求。与会教师对微课比赛各类问题进行了热烈讨论。据悉，此次省微课比赛分视频类和文本类。视频类选任教课程中有一定关联的重点或难点制作成单个时长5-10分钟的3个微课，作为一件作品参赛。文本类从教师、教学或者学生会面临的实际问题入手，主题应该体现实用性。5月底，学院将组织专家评审，对于未获学校示范微课立项而获得奖项的微课作品，学校、学院奖给予适当的经费补助。', 'http://info.jhc.cn/2017/0504/c201a79412/page.htm', 'http://info.jhc.cn/_upload/article/images/c6/57/7d4d22294d95b53acae6e098b8e8/90800ab3-848e-4e23-b9f9-5781271797ed.jpg', '2017-05-17 14:12:53.535141');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (7, '吴章豪、丰辉', '学工办/团委', '周荃', '2017-05-03 00:00:00', '信息学院在全院团员青年中开展“一学一做”主题教育实践活动', '为深入学习习近平总书记系列重要讲话精神，深化共青团改革攻坚，大力推进从严治团，切实增强团员的先进性和光荣感，信息学院在全院团员青年中集中开展“学习总书记讲话，争做合格共青团员”主题教育实践活动。据悉，此次教育实践为期半年，活动分为“青马工程”大学生领袖成长营活动、“学习总书记讲话、争当金华青年铁军”大讨论活动、“怎样做一名合格团员”主题班团活动、星级团支部评比、志愿者活动等八项活动，以此倡导团员青年用实际行动践行习总书记讲话精神，保持和增强团组织的先进性。', 'http://info.jhc.cn/2017/0503/c201a79258/page.htm', NULL, '2017-05-17 14:12:53.557227');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (8, '何碧峰', '学工办/团委', '周荃', '2017-05-02 00:00:00', '信息学院开展实训楼消防逃生演练', '为进一步加强校园安全工作，增强学校师生消防安全意识，提高学生对火灾的应急扑救处置能力，近日，信息学院开展17年上半年实训室消防逃生演练及灭火器材使用培训，全院200余位师生参加此次演练。活动中演练负责人向全体学生提出了“三个点”的消防意识要点：第一，同学们平时一定要保持良好的消防安全习惯，禁止将火种带入教学楼、寝室等人员聚集场所，从根源消除火灾隐患。第二，666110是我校的报警求救电话，当有火灾发生时，应第一时间拨打火警求救电话向火警求救如火情较大的时候也可直接拨打119火警电话。第三，面对火灾一定要冷静、镇定、不慌乱，采取正确的自救、求救措施。逃生演练结束后，学院邀请了保卫处技防科相关人员给师生们介绍灭火器等消防设施的使用原理及注意事项，并亲自演练了灭火器的使用方法。观摩结束后，学生们亲自体验了现场灭火的过程，同时在校消防队的指导接上消防水带打开消火栓学习专业消防队的灭火方式和注意要点。 “此次演练让我们学到了很多东西，当发生火灾时我们学会了如何自救如何逃生，而且深入学习了灭火器材的使用，更好的为我们的安全生活学习打下基础。”学生袁子江说到。', 'http://info.jhc.cn/2017/0502/c201a79204/page.htm', 'http://info.jhc.cn/_upload/article/images/37/ba/ca8fa6304dd8a1c0e3b1db4fc43b/a704b78f-6ce9-49ab-ae7c-5500edb71d81.jpg|http://info.jhc.cn/_upload/article/images/37/ba/ca8fa6304dd8a1c0e3b1db4fc43b/9ddec071-8b0e-49ea-9acd-1360192d8fc4.jpg', '2017-05-17 14:12:53.579471');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (9, '苏红富、王静、余炜', '通信与网络工程部', '周荃', '2017-04-25 00:00:00', '信息学子获2017年浙江省高职技能大赛“物联网技术应用”竞赛一等奖和二等奖', '4月22日，浙江省高职高专院校技能大赛”物联网技术应用”竞赛在宁波城市职业技术学院举行，共有来自省内11所院校16只代表队参加。由我院通信与网络工程部副主任苏红富领队，通信与网络工程部王静、余炜、卢航远等教师指导，李鑫、叶俊泽、陈国锋三位同学组成的代表队获得一等奖，并顺利取得国赛参赛资格；金茂鑫、郭献煌、朱杰三位同学组成的代表队获得二等奖。据悉，此次物联网技术应用大赛以“图书馆门禁及监控系统”为考核点，在4小时时间内完成物联网基本理论测试，图书馆门禁及监控系统布局、安装、组网与调试，传感及网络层嵌入式程序开发，图书馆门禁及监控系统PC端程序开发，图书馆门禁及监控系统Android端程序开发，图书馆门禁及监控系统设计报告等多项考核任务。通过比赛，将提高学生的专业技能，为物联网应用技术新兴产业培养更多的高质量技能型人才。', 'http://info.jhc.cn/2017/0425/c201a78615/page.htm', 'http://info.jhc.cn/_upload/article/images/0c/61/22b4e4ab429b96fb0db74d0f4d1b/cf7f44f9-2fb0-4a45-90a8-3319093fe716.jpg', '2017-05-17 14:12:53.601887');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (10, '郭天鹏', '学工办/团委', '周荃', '2017-04-24 00:00:00', '宾虹中学游学团来我院专业职业体验', '4月22日中午，宾虹中学师生一行100余人来我院做专业参观和职业体验。此次参观体验以“互联网+学生”为主题，学院设计了物联网技术体验之旅、智能电子技术体验之旅、现代自动化技术体验之旅三条参观路线，对接计算机应用技术、软件开发技术、应用电子技术、计算机网络技术、物联网技术、电气自动化技术6个专业的最新技术在现代工业和生活中的实际应用。宾虹中学师生一行分4组，先观看了专业介绍视频，随后参观了科实南楼的啤酒生产线和现代自动化各项竞赛训练场，在东实训北楼的物联网体验厅、网络安全实训室、物联网创新中心，亲身体验了智能家居控制、VR/AR应用，观看智能车、嵌入式小车展示。整个参观体验总用时2个小时，宾虹中学带队班主任赵老师说，“这次体验让我也学到了很多，老师和学生的讲解都很耐心，高一学生们对IT专业表现出了极高的兴趣和热情”。', 'http://info.jhc.cn/2017/0424/c201a78571/page.htm', 'http://info.jhc.cn/_upload/article/images/98/8f/34156ec34a7cb826a79397ea420c/152c91ad-eeda-46e3-9d0c-e06ba39cb78b.jpg', '2017-05-17 14:12:53.624235');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (16, '何碧峰', '学工办/团委', '周荃', '2017-04-19 00:00:00', '“蕙质兰心，秀外慧中”女生专题系列活动开幕', '为了进一步提高女生的综合素质，充分展现当代女大学生崇高的理想追求、丰富的内涵修养和高雅的审美情操，彰显新时期女生自尊、自信、自立、自强的风采，引领女生成长成才。近日，信息学院开展“蕙质兰心，秀外慧中”女生专题系列活动。系列活动包括“横幅签名送祝福”、“幸运颜色，幸运女生”、“心灵手巧­——手工制作”、“温馨卡片传递真情”等等。活动形式丰富多彩，既陶冶了学生情操又丰富了学生的课余生活，同时还锻炼了学生的动手能力。据悉，在系列活动的最后，还将针对全校的女生开展“拥抱健康”女生专题讲座。该讲座将请到女生生理心理研究专家，可以让更多的女生关注身心健康，关注自己，也让更多的人关注女性。让女生更加乐观向上自信的面对学习、感情、生活，提高女大学生的自尊、自立、自爱、自强的自我意识。', 'http://info.jhc.cn/2017/0419/c201a78321/page.htm', NULL, '2017-05-17 14:12:57.457826');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (11, '吴琼、丰辉', '学工办/团委', '周荃', '2017-04-24 00:00:00', '信息工程学院第二十七期党校顺利开班', '近日，信息学院第二十七期业余党校开班。学院党委书记、党校校长贾美倩，团委书记丰辉，电气自动化专业支部书记花有清，通信与网络专业支部书记朱墨池，成教支部书记俞冬丽以及82名党校学员参加本次开学典礼。今年，学院党校除安排校内外老师授课外，还邀请了企业党建负责人以支部为单位对学员进行小班化培训。考核成绩由平时成绩、考试成绩、时政热点案例分析（包括读书讨论心得）、社会实践调查报告、红色节目表演、文明监督岗六个部分构成，同时结合党支部负责人、专业辅导员、党校管理人员对学生的测评结果为党校学员建立学习档案，并记入学生个人档案和个人思想品德实践课中，作为其他考核评比的依据。院党委书记贾美倩希望学员们通过党校学习切实提高思想认识，增强使命感和责任感，自觉践行社会主义核心价值观，勤奋学习、深入实践，积极奉献社会，在真学实做上面下功夫见成效。', 'http://info.jhc.cn/2017/0424/c201a78567/page.htm', 'http://info.jhc.cn/_upload/article/images/30/c5/520318574994b6ce1567098cb34b/b50eb275-2712-412f-892d-5000b54500b5.jpg', '2017-05-17 14:12:53.646386');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (12, '陈晓龙', '信息化事业部', '周荃', '2017-04-24 00:00:00', '信息学子在浙江省第14届大学生程序设计竞赛上喜获金奖', '4月22日，从浙江省第14届大学生程序设计竞赛上传来喜讯，由我校信息学院梅旭时、王伟斌、陈焕通老师指导的4支参赛队伍顽强拼搏、不负众望，取得了1金1银2铜的好成绩，其中戴钰泽、林成钱、罗君威团队获得金奖，周宇成、任威、陈云标团队获得银奖，王建源、王良海、黄胜瑾团队和赖江南、尤金平、苏万彬团队获得铜奖。据悉，大学生程序设计竞赛素有“计算机界的奥林匹克”之美誉，今年的比赛在浙江大学举行，吸引了来自全省82所本专科院校的287支队伍同场竞技。大赛提倡在压力较大的情况下，培养学生的创造力、团队合作精神以解决竞赛的问题，从而挑选和发掘世界上最优秀的程序设计人才。参赛选手必须在5小时内解读全英文命题，完成编程并进行测试和调试，对参赛学生的逻辑分析能力、策略制定和脑力方面具有极大的挑战性。近一年来，信息学院已形成程序设计竞赛的常态化运行管理机制，从每年的10月到次年5月，分为周赛、月赛及年度总决赛，指导老师会针对竞赛题目的英文解释和算法实现进行辅导，最终选拔优秀选手参加省赛。同时，学院还积极探索将竞赛模式引入日常课程教学，开发竞赛和教学一体化网络平台，实现以教带赛，以赛促学的新模式，进一步深化了学院目前积极倡导的网络化教育教学改革。', 'http://info.jhc.cn/2017/0424/c201a78495/page.htm', 'http://info.jhc.cn/_upload/article/images/87/41/d651851046df9d69a7189f29f473/db127b1c-8873-4f5b-a87b-d06cf04980b9.jpg', '2017-05-17 14:12:53.739828');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (13, '陈晓龙', '信息化事业部', '周荃', '2017-04-24 00:00:00', '信息学子获2017年浙江省高职技能大赛”移动互联网应用软件开发”竞赛一等奖', '4月22日，2017年浙江省高职高专院校技能大赛“移动互联网应用软件开发”在杭州浙江经贸职业技术学院举行，共有来自省内院校的16支代表队参加。由我校信息工程学院赵敏笑副院长领队，由信息化事业部陈晓龙、颜钰琳、吕焱飞等教师指导，候立志、王亚森、谢国权三位同学组成的代表队获得一等奖，并顺利取得国赛参赛资格。据悉，此次移动互联应用软件开发赛项以智能交通行业应用为考核点，采用实际操作形式，现场编程。比赛通过“系统文档”“程序排错”“功能编码”及“创意设计”四种形式，考查参赛选手实际工程项目的编码能力、文档编写能力、综合分析能力、技术架构设计能力、创意创新能力、大数据分析能力。考核技术点包括：UI、Activity、Service、Broadcast Receiver和Content Provider四大组件、资源使用、网络编程、Handler/多线程/定时器、多媒体、手势识别、数据存储、业务逻辑。', 'http://info.jhc.cn/2017/0424/c201a78488/page.htm', 'http://info.jhc.cn/_upload/article/images/99/b0/454f6bcb4b2f8187a0c92322b719/79ee19f7-55c3-49b7-bbdb-a3d635331c78.jpg', '2017-05-17 14:12:53.757781');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (14, '张娴', '电气自动化工程部', '周荃', '2017-04-24 00:00:00', '信息学子获2017年浙江省高职技能大赛“光伏电子工程的设计与实施”竞赛二等奖', '4月20日，2017年首届浙江省高职技能大赛“光伏电子工程的设计与实施”在衢州职业技术学院举行，共有来自省内院校的10支代表队参加。由我校信息工程学院陈海荣院长领队，自动化专业主任马汝星、教师张娴指导，谢恩满、贺泽彬、张金标三位同学组成的代表队获得二等奖，并顺利取得国赛参赛资格。本赛项参赛选手根据光伏系统规划描述，运用新能源电子技术、单片机、PLC编程、嵌入式系统开发等知识，完成从光伏电子工程系统设备安装，光伏电子控制模块开发与调试，管控系统设备安装，光伏电子工程系统运行调试，到区域能源分析规划等任务。通过比赛，将提高学生的专业技能，为光伏工程技术、电子信息技术等新兴产业培养更多的高质量技能型人才。', 'http://info.jhc.cn/2017/0424/c201a78483/page.htm', 'http://info.jhc.cn/_upload/article/images/7a/e6/6bb741944e48832392ed3a14c483/23e64913-a69f-4f87-aa9a-24f1df9e4f9c.jpg', '2017-05-17 14:12:53.77976');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (15, '周荃', '办公室', '周荃', '2017-04-20 00:00:00', '找问题 求方法 扎实推进学院科研工作', '为了营造良好的科研氛围，推动教师教育教学能力向科学研究能力的积极转化，学院于4月19日召开科研工作会议，并邀请校科研中心主任王瑞敏作指导。会上，学院科研副院长王成福就2017年学院科研考核制度和各层次项目申报计划作了说明，鼓励教职工认真申报项目，并围绕校级重点学科及团队建设、科研硬件条件建设、高层次项目申报等方面给各项目部及相关教师做了细化指导。研讨中，各专业主任代表专业教师反映了科研工作中遇到的问题，希望在高层次平台的对接、横向项目向纵向项目的转化、校企合作中教师实践的深化等方面有所突破。王瑞敏处长分享了自己项目申报的经验，他谈到，科研团队的建设就像师傅带徒弟，团队负责人要多组织项目研讨和子项目的深入研究，做到资源共享，团队成员在任务中学习提升，逐渐成长并能够独立承担项目。教师要注重横向到纵向的思维转变和成果转化，在应用设计中提炼理论研究水平，同时把研究项目反哺到教学、竞赛中。当然，还要合理利用校企合作的资源深入企业一线，通过“百博入企”“顶岗实习”等项目由专业主任牵头，带动教师入驻这些好的项目和平台，与新技术与市场接轨，进一步提升教师综合实力。下一阶段，学院还将持续开展此类活动，广泛了解教职工对科研工作的所思所想，从制度上规范、从操作中落实，扎实提升学院科研实效。', 'http://info.jhc.cn/2017/0420/c201a78401/page.htm', NULL, '2017-05-17 14:12:57.42488');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (17, '石天星 叶 旋', '浙江日报', '周荃', '2017-04-18 00:00:00', '《浙江日报》报道我院志愿者无人机助力高效巡河', '金华学子治水带上好点子无人机助力高效巡河 “返航点已刷新，可以起飞。”日前，金华市新狮街道接力塘水库边，无人机升空，朝面前的水域飞驰而去。河岸边，手握无人机控制器的大学生们相视而笑。用无人机查找水面污染物，是金华职业技术学院与社会实践基地安地镇、新狮街道等地联合开展“剿灭劣Ⅴ类水 　IT志愿者来助力”系列活动时，金职院信息工程学院几个大学生志愿者想出来的好点子。今年年初，金职院应用电子技术专业大二学生王志康和李宏宇等10名信息工程学院的大学生志愿者一起来到梅溪河巡河，发现人工巡河耗时久、盲点多、死角不易发现，有没有什么好办法能提高巡河效率？王志康灵机一动——让无人机来代替人工巡河！刚好李宏宇最近买了一台无人机，大家一拍即合，决定带上无人机“河小二”开始巡河。 “我们去上次查到有垃圾的地方看看，不知现在垃圾有没有被清理掉。”李宏宇双眼盯着固定在无人机控制器上的手机显示屏，上面是无人机云台相机在近100米空中实时“俯瞰”到的蜿蜒曲折的河道、绿色的树丛、混凝土浇筑的排水管道……屏幕左下方的辅助工具能实时进行地图定位、截屏保存，方便后续清理工作的开展。上次巡河时，无人机在一处排水管道旁边发现了几乎阻塞河道的垃圾，这次再飞到这处排水管道上空盘旋，发现垃圾已被清运走了。无人机“河小二”遥控半径4到5公里，人巡河时走不进去的地方，它却能“看”得一清二楚，一块电池可以支持往返巡航20多分钟。自从在梅溪河巡河时崭露身手后，多地团委都向金职院志愿者们发出召唤——带着无人机来巡河吧！ “用无人机巡河就是我们专业同学助力‘五水共治’过程中的一个创新。”金职院应用电子技术专业主任余红娟说。 “肉眼能识别的污染源，可以用无人机巡河来识别，但像水体中的有机物、氨氮、总磷是否超标，肉眼是看不出来的，只有通过化学实验才能检测出来。”金职院环境监测与控制技术专业主任郑钊说，该专业目前正与科技企业合作开展水质检测，师生们学习实践的一部分就是通过实地采样和化学实验来分析造成河流污染的原因。如今，将专业知识用于“五水共治”，在护水行动中发挥聪明才智，已成为金职院老师和同学们的自觉行动。新闻链接：http://zjrb.zjol.com.cn/html/2017-04/18/content_3047973.htm?div=-1', 'http://info.jhc.cn/2017/0418/c201a78240/page.htm', 'http://info.jhc.cn/_upload/article/images/6e/c1/9ef8f30d465389fd882741f82c14/f5cde29f-69e4-48ed-b132-1b3f8fc2ed4a.jpg', '2017-05-17 14:12:57.480799');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (18, '刘子坚', '电子产品开发部', '周荃', '2017-04-18 00:00:00', '信息学子获2017年浙江省高职技能大赛“电子产品芯片级检测维修与数据恢复”赛项一等奖', '4月15日，2017年浙江省高职技能大赛“电子产品芯片级检测维修与数据恢复”在嘉兴职业技术学院举行，共有来自省内院校的13支代表队伍参加。由我校信息工程学院诸葛坚、余红娟、廖任秀、张莹等四位老师指导，首次参赛就取得骄人成绩，其中吴章豪、潘建彬两位同学组成的代表队获得一等奖（第2名），并顺利取得国赛参赛资格，陈明大、邹云涛两位学生组成的代表队获得了三等奖。“电子产品芯片级检测维修与数据恢复”赛项，共分两个任务，任务一主要依据大赛组委会提供的技术文件实现对电路板功能修复；任务二主要使用规定软件对故障存储介质或损坏性文件进行修复和数据恢复。通过比赛，学生的专业技能得到了很大的提高，也将吸引更多的学生进入数据恢复领域，为日后更好的创业就业打下基础。', 'http://info.jhc.cn/2017/0418/c201a78220/page.htm', 'http://info.jhc.cn/_upload/article/images/21/13/906b18f14b5c95a6b21995310b4a/4c78faa7-c8f8-4dd1-953b-72cb2503fd39.png', '2017-05-17 14:12:57.502365');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (19, '应武', '信息化事业部', '周荃', '2017-04-17 00:00:00', '信息学子获2017年浙江省高职技能大赛“虚拟现实（VR）大赛”一等奖', '4月15日，2017年浙江省高职技能大赛“虚拟现实（VR）设计与制作竞赛”在台州职业技术学院举行，共有来自省内院校的39支代表队伍参加。由我校信息工程学院陈海荣院长领队，宣翠仙、应武、叶继阳三位老师指导，其中陈炀丰、芦峰、梁少雄三位学生组成的代表队，获得了一等奖的好成绩，并顺利取得国赛参赛资格。吕祖龙、吴杰锋、蒋紫月获得了二等奖的好成绩。 “虚拟现实（VR）设计与制作竞赛”赛项，共分两个任务，任务一主要使用网龙开发的101VR编辑器开发VR交互，任务二主要为使用unity3D开发VR交互，通过3DMax建立指定模型，导入unity3D，完成虚拟场景。通过比赛，学生的专业技能得到提高，将吸引更多的学生进入VR世界，为创业就业打下基础。', 'http://info.jhc.cn/2017/0417/c201a78158/page.htm', 'http://info.jhc.cn/_upload/article/images/24/16/286eaf7d49818eb2a30d8648fe72/04d5c5cc-e691-4d66-a8a4-45e0b75e2f90.jpg', '2017-05-17 14:12:57.524547');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (20, '蔡金晶', '教科办', '周荃', '2017-04-17 00:00:00', '信息学院召开中新合作新生家长见面会', '4月15日是我校自主招生综合素质测试的日子，为了增进家长对学校办学特色、专业设置等情况的了解，信息学院组织召开新生家长见面会，50多位新生家长代表，学院领导、学工办、教科办和三个中外合作专业负责人一同参加了会议。会上，院长陈海荣就中新合作的办学概况，国际合作与交流、学生培养与就业等方面做了详细介绍，着重介绍了学院办学理念、特色与亮点。15级中新班的学生代表夏启成、孙宁波各自分享了中外合作专业的学习生活经历。通过交流，家长们纷纷表示，学院专业实力很强，孩子今后的成长成才有保证，彻底打消了自己的顾虑，坚定了报考我校的信念。', 'http://info.jhc.cn/2017/0417/c201a78156/page.htm', 'http://info.jhc.cn/_upload/article/images/43/21/d7abd0c640be92766a830556ce86/6419b223-34ae-456b-8d24-a070ee5c2364.jpg', '2017-05-17 14:12:57.546746');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (21, '缪小芬', '金华晚报', '周荃', '2017-04-13 00:00:00', '《金华晚报》报道我院技能大赛', '比武缪小芬 4月8—9日，2017年浙江省高职高专院校技能大赛暨全国职业院校技能大赛选拔赛在金职院举行，由该校信息学院承办的“软件测试”“嵌入式技术与应用开发”和“风光互补发电系统安装与调试”三个赛项，共有来自全省各地19所高职院校的96位选手参加比赛。据悉，技能大赛是职业院校各类竞赛中含金量最高的项目之一，其中“软件测试”赛项是国内首次举办。     本报记者 缪小芬 摄新闻链接：http://epaper.jhnews.com.cn/site1/jhwb/html/2017-04/12/content_1826370.htm', 'http://info.jhc.cn/2017/0413/c201a78049/page.htm', 'http://info.jhc.cn/_upload/article/images/10/22/f749da0640dfbd555d35a6710e21/f1f32c6f-f30d-43ef-90b9-6bb139e3a5b2.jpg', '2017-05-17 14:12:57.569088');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (22, '丁晨', '通信与网络工程部', '周荃', '2017-04-13 00:00:00', '信息学子获2017年浙江省“4G全网建设技术”赛项三等奖', '4月8日，2017年浙江省高职院校技能大赛“4G全网建设技术” 赛项在浙江交通职业技术学院落下帷幕，我校由信息工程学院苏红富主任领队，张嵚、丁晨两位老师指导，周凯、汤皓炜和杨天宇、梁爱波四位学生组成的2个代表队参赛，面对其他院校的老将，我校参赛选手沉着冷静，积极应战、团结协作。本赛项用完全仿真的方式组织竞赛，比赛所考察的知识与技能，其内容广度、深度、多样性以及实践意义均远超以往类似赛项。参加本次比赛的选手共有来自全省高职院校的12支代表队，赛项主要内容包括理论考试、4G网络配置与优化、4G网络故障排除三部分，各部分均包括无线接入网、核心网及承载网等方面内容。该赛项紧密结合我国移动通信产业发展规划及现代通信技术的发展方向，顺应信息通信（ICT）技术快速发展的趋势，引入LTE无线接入网、EPC核心网以及承载网的典型应用场景，通过竞赛充分考察选手的4G全网规划、设备部署与联调、业务对接测试、网络性能优化和故障处理等相关技能，直接对接学生通信网络有关就业。经过3个小时的激烈角逐，周凯、汤皓炜两位同学的代表队获得大赛三等奖，充分展现了学生的职业道德与团队协作精神。', 'http://info.jhc.cn/2017/0413/c201a78048/page.htm', 'http://info.jhc.cn/_upload/article/images/84/ac/4332c0ba4f829bd500ce85c1801b/0c262ab6-d271-40ed-8547-8c2503011120.jpg', '2017-05-17 14:12:57.59135');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (23, '丰辉', '学工办/团委', '周荃', '2017-04-11 00:00:00', '信息工程学院举办律动之音大赛落下帷幕', '4月8日晚6点，由信息工程学院主办的“音为梦响，声而不凡”律动之音决赛在稻花香广场顺利举行。现场百余名学生欢聚一堂，共享音乐盛宴，聆听校园好声音。经过半决赛的层层选拔，10位选手脱颖而出，在舞台上一展歌喉，角逐冠亚季军。决赛分为团队合唱，个人独唱两个环节。开场青春逗文艺社劲爆的舞蹈《hate》，活跃了现场气氛。第一轮的团队比拼中，选手们纷纷拿出看家本领：颜文睿与夏智涛一首《最初的梦想》，优美的伴奏加上独特的音色，令人动容；黄宇鸣与李泳波则给现场观众带来民谣《故乡》，粗糙的歌喉让现场阵阵欢呼，完美的气息配上绝佳的唱功，堪称完美。第二轮是个人独唱环节，陆天祺带来的一首动人情歌《独家记忆》，情到深处自动人，实在难以忘怀，观众听的如痴如醉；李泳波则另辟蹊径，带来一首《斑马，斑马》，将现场推向一个小高潮；黄宇鸣同学则带来一首陈奕迅的《不要说话》，使现场观众仿佛置身演唱会，颜文睿则带来一首耳熟能详的民谣《天空之城》，优美的歌声，观众陶醉在其中。比赛过程中，选手们都拿出十八般武艺，尽情地享受舞台。最终经过两轮比拼，徐佳甲荣获金华职业技术学院信息工程学院律动之音比赛冠军，曹鑫泽、黄宇鸣分别斩获亚、季军，李泳波获得最佳人气奖。律动之音活动是信息工程学院的品牌特色活动之一，几年来，活动的精彩度和影响力不断升级，得到了师生的一致好评。此次活动促进了我院文化艺术活动的开展，为热爱音乐的大学生们提供了挑战自我的舞台，积极展示了学子的才艺风采和校园的多元氛围，充分体现了大学生锐意创新、青春向上的精神风貌。', 'http://info.jhc.cn/2017/0411/c201a77964/page.htm', 'http://info.jhc.cn/_upload/article/images/e8/c4/122492974f828ccc82cfd5c25191/e070ecfa-a502-4665-8659-f20c056cef09.jpg', '2017-05-17 14:12:57.613355');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (30, '花有清', '教科办', '周荃', '2017-04-05 00:00:00', '信息学子2017年 “自动化生产线安装与调试”赛项获佳绩', '4月1日，由浙江工业职业技术学院承办的浙江省高职高专院校技能大赛暨全国选拔赛“自动化生产线安装与调试”赛项落下帷幕，由我校信息工程学院花有清、楼蔚松两位老师指导，胡浩海、郭明超两位学生组成的代表队以扎实的基本功和良好的应变能力，克服了比赛中碰到的各种困难，经过5个小时的激烈角逐，获得第2名的好成绩，并顺利取得国赛参赛资格。此次比赛共有来自全省10所高职院校的10支代表队参加，比赛以浙江亚龙公司生产的YL_335B自动生产线实训装置为平台，赛项内容涉及到YL_335B自动生产线实训装置机械部分安装、PLC程序设计与调试、组态设计与调试、变频器与伺服驱动器调试、系统故障排除等方面，要求参赛队员在气动控制、传感器应用、PLC控制、变频器、伺服驱动、MCGS组态设计等方面具备扎实的专业理论知识、较强的实践能力和良好的团队协作精神。', 'http://info.jhc.cn/2017/0405/c201a77652/page.htm', 'http://info.jhc.cn/_upload/article/images/40/0c/6c98a29548f6a7f1f2939586fa57/e0c5c771-8c16-4778-8c9d-619018391382.jpg', '2017-05-17 14:13:01.769939');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (24, '周荃', '办公室', '周荃', '2017-04-11 00:00:00', '信息学子连夺省高职高专技能大赛三项一等奖', '4月8-9日，2017年浙江省高职高专院校技能大赛“软件测试”“嵌入式技术与应用开发”和“风光互补发电系统安装与调试”赛项在我院举行，信息学院代表队取得骄人战绩，连夺三项一等奖。据悉，“软件测试”赛项以基于网络的“资产管理系统”应用为考核点，采用实际操作形式，现场制作测试用例，完成软件测试工作，共有来自省内13所院校的13支学生队伍参加。“嵌入式技术与应用开发”赛项主要分为硬件电路制作与工艺以及嵌入式程序设计与软件调试等两个环节进行，共有来自省内8所院校的13支队伍参加。“风光互补发电系统安装与调试”赛项则主要包括光伏供电装置、风力供电装置、逆变与负载系统等内容，共有来自省内4所院校的6支队伍参加。经过激烈比拼，最终，由我院陈晓龙、陈尧妃两位老师指导，陈莉莉、潘志鹏、戴钰泽三位同学组成的代表队获“软件测试”赛项一等奖；由刘子坚、范灵芝两位老师指导，沈俊尧、周鹏强、姜豪三位同学组成的代表队以及由廖任秀、徐晶晶两位老师指导，刘康、何坚栋、王郑伟三位同学组成的代表队分获“嵌入式技术与应用开发”赛项一等奖和三等奖；由马汝星、李瑞东两位老师指导，朱勤鑫、马宣刚、陈钦杰三名同学以及陈超、周宇磊、乔田地三名同学组成的两支代表队则分获“风光互补发电系统安装与调试”赛项一等奖与三等奖。', 'http://info.jhc.cn/2017/0411/c201a77946/page.htm', 'http://info.jhc.cn/_upload/article/images/e5/4a/da280503425bbb665a415bb08d7c/9b28b738-7ab7-4515-9490-8f0e601d9459.jpg|http://info.jhc.cn/_upload/article/images/e5/4a/da280503425bbb665a415bb08d7c/deaddc9c-16fc-498b-a8a9-ed5182378878.jpg|http://info.jhc.cn/_upload/article/images/e5/4a/da280503425bbb665a415bb08d7c/145609f0-bf99-4531-916b-3f3bf9068307.jpg', '2017-05-17 14:12:57.636246');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (25, '胡平', '信息化事业部', '周荃', '2017-04-11 00:00:00', '信息学子2017年浙江省“云计算技术与应用”赛项获一等奖', '4月8日，2017年浙江省高职院校技能大赛“云计算技术与应用” 赛项暨全国职业院校技能大赛选拔赛落下帷幕，由我校信息工程学院陈晓龙主任领队，胡平、王伟斌两位老师指导，黄胜瑾、王建源、罗君威三位学生组成的代表队以扎实的基本功、良好的心理素质、有效的应变能力，团结协作，经过4个小时的激烈角逐，获得了一等奖的好成绩，并顺利取得国赛参赛资格。此次比赛共有来自全省高职院校的15支代表队参加，由浙江交通职业技术学院承办，赛项主要内容是根据业务需求和实际的工程应用环境，实现云计算平台架构的规划设计，完成云计算网络、服务器、存储服务器的互联和配置，完成云计算基础架构平台、云计算软件服务等平台软件的部署、配置和管理，通过云平台实现虚拟桌面、大数据分析、云存储等各类云应用部署、运维和开发，撰写开发与工程文档等内容。大赛要求参赛队员在云计算技术基础、云计算平台规划设计、云平台搭建、虚拟桌面、大数据挖掘分析和云存储等多种云应用部署、运维和开发方面具有前沿知识、技能、职业素养和团队协作能力。', 'http://info.jhc.cn/2017/0411/c201a77942/page.htm', 'http://info.jhc.cn/_upload/article/images/5c/c2/75d251844fa48e5cc271c6da3fd7/ed9b66eb-3ec0-460b-9219-8b99c783e219.jpg', '2017-05-17 14:12:57.657926');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (26, '时宽兵、王至康', '金华新闻网', '周荃', '2017-04-10 00:00:00', '金华新闻网报道我院承办省职业院校技能大赛', '省职业院校技能大赛在金举行金华新闻网4月9日消息 记者 时宽兵 通讯员 王至康 文/摄今天，省职业院校技能大赛暨全国职业院校技能大赛选拔赛，在金职院举行，来自全省各地19所高职院校的96位选手，在为期两天的比赛时间里，参加嵌入式技术与应用开发、软件测试、风光互补发电系统安装与调试3个项目的比赛。据介绍，通过技能大赛，提升高职院校学生职业素养、操作动手、沟通协作等能力，培养实用性人才，选拔出优秀选手，参加全国职业院校技能大赛。新闻链接：http://www.jhnews.com.cn/2017/0409/751621.shtml', 'http://info.jhc.cn/2017/0410/c201a77932/page.htm', 'http://info.jhc.cn/_upload/article/images/f8/8c/7468360b4c2a9185681d29dea366/71f3f468-3670-46a1-a89e-6157a058ba00.jpg|http://info.jhc.cn/_upload/article/images/f8/8c/7468360b4c2a9185681d29dea366/7037d5c0-16d5-4a32-acb8-c45c3ddbac38.jpg', '2017-05-17 14:12:57.680099');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (27, '周华', '校学生处', '周荃', '2017-04-08 00:00:00', '我校学子赵锦坤入围省“十佳大学生”评选活动30强', '近日，从浙江省“十佳大学生”评选活动组委会传来好消息，我校学子赵锦坤凭借匠心筑梦、砥砺前行的感人事迹从全省百余名优秀大学生中脱颖而出，成功入围30强。据悉，此次高职院校仅4人入围。赵锦坤，中共党员，我校自动化专业在校生，先后获国家奖学金、百名学习标兵、校十佳大学生等荣誉。在专业上，他苦练技能，精益求精，每天训练接、套、压线近2000多个点，先后斩获省级、国家级职业技能竞赛一等奖，创造了浙江省风光互补国赛项目历史最好成绩；在团队里，他是领头雁，其带领的自动化142班团队，在专业技能大赛中捷报频传，先后5人获得国赛一等奖，更被评为2016年全国高校“活力团支部”、省百强“活力团支部”；在生活中，他乐学好施，用一技之长回馈社会，利用周末与假期深入社区开展志愿服务，三年来累计完成1500余件家电维修。另据了解，浙江在线网站近期专门开辟专栏介绍30名候选人的基本情况和主要事迹，同时刊登选票、设立网上投票站（http://edu.zjol.com.cn/system/2017/03/27/021476776.shtml），欢迎全校师生踊跃为赵锦坤及其他候选人投票。', 'http://info.jhc.cn/2017/0408/c201a77860/page.htm', 'http://info.jhc.cn/_upload/article/images/db/c8/4fb5e6f84481b590536057ce3284/0a2e73d6-6f1f-4a2b-b69f-5509c0bfb861.jpg', '2017-05-17 14:12:57.702394');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (28, '徐华丹', '教科办', '周荃', '2017-04-07 00:00:00', '2016级电信本科班学生完成导师制项目汇报', '<style type="text/css"> 	 	  4月6日晚，2016级电信本科班学生进行了导师制项目汇报。会上，同学们展示了各式各样的作品，有晶莹剔透的立方体LED宝塔灯，还有造型别致的电子徽章，并详细介绍了电路工作原理、PCB设计过程、电路板制作流程、焊接电路注意事项，分享了编程学习过程中遇到的种种经历。通过汇报，同学们展示了学习劳动成果，提升了团队的凝聚力和协调沟通能力。“听着同学的汇报，好像自己又把导师制项目重新做了一遍。”“通过这次汇报，看到了别人的努力和收获，要向他们学习。”汇报结束后同学们纷纷表示。据悉，导师制项目完成后，学生们需要把总结报告和项目相关资料都上传到Moodle平台，并填写“第二课堂”学分申报表，学院将根据学生完成项目情况确认学分。', 'http://info.jhc.cn/2017/0407/c201a77808/page.htm', 'http://info.jhc.cn/_upload/article/images/a7/53/2245e638439fbc5ffd78d2db5f7b/59d658b2-ecd6-4c75-96ae-d2b90c8d8d0d.jpg|http://info.jhc.cn/_upload/article/images/a7/53/2245e638439fbc5ffd78d2db5f7b/2f6e8f41-6fbf-4989-99f7-28e718f6234b.jpg|http://info.jhc.cn/_upload/article/images/a7/53/2245e638439fbc5ffd78d2db5f7b/6f4fea2a-c5fd-4b60-942b-84975096517a.jpg|http://info.jhc.cn/_upload/article/images/a7/53/2245e638439fbc5ffd78d2db5f7b/ca438eb2-3980-43ce-a7e8-07a23e663b77.jpg', '2017-05-17 14:12:57.724481');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (29, '', '浙江卫视', '周荃', '2017-04-06 00:00:00', '浙江卫视报道我院航拍学生的“河长”日记', '视频地址：http://www.cztv.com/videos/xwsyd/3322809.html?from=timeline&isappinstalled=0', 'http://info.jhc.cn/2017/0406/c201a77747/page.htm', 'http://info.jhc.cnundefined', '2017-05-17 14:13:01.699362');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (31, '徐露诗', '学工办/团委', '周荃', '2017-04-05 00:00:00', '春日芳菲 书香校园——信息学院开展海报设计大赛', '“哇！这张海报真是太酷了，我投它一票”东教一楼大厅里，悬挂着一张张设计精美的海报，同学们围在海报面前，准备为心仪的作品投上一票。据悉，为了提高学生的阅读积极性，培养学生自主学习的良好习惯，信息学院举办了“春日芳菲，书香校园”为主题海报设计大赛，鼓励同学们将喜欢的书籍、励志电影通过海报的形式推荐给身边的小伙伴们。同学们的参与积极性十分高涨，短短一周就收到学生六十余作品，其中包括了手绘、PS、AI设计作品，充分展现了信息学子的专业特长。这次比赛让全院学生受到了一次极好的文化教育和艺术熏陶，丰富了同学们的课余文化生活。最终，自动化162班刘茜希同学凭借精美的手绘海报《沉睡魔咒》获得了一等奖。', 'http://info.jhc.cn/2017/0405/c201a77651/page.htm', 'http://info.jhc.cn/_upload/article/images/cc/0b/d9bd929c47bdb069adb8d019f502/ce320283-a6f1-4c99-8dc8-55f66a3c7125.jpg', '2017-05-17 14:13:01.791939');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (32, '周荃', '办公室', '周荃', '2017-03-30 00:00:00', '信息学院举办科研项目申报专题讲座', '为了进一步规范项目申报书撰写，提高课题申报质量，3月28日，信息学院分管科研工作副院长、校级创新团队负责人王成福针对金华市社科联项目申报进行专题辅导，20余名项目申报人参加。讲座中，王成福分析了我院近几年市社科联项目申报、立项的情况，围绕项目选题、申报书撰写、过程研究及成果转化等方面分享了他的经验。他结合重点选题方向做了选题示范，并指出，课题申报选题是关键，市社科联项目要突出对地方经济社会发展和社会急需人才培养等现实问题开展针对性研究，要立足新形势下的我市发展理念、战略规划和十三五建设目标等提出研究课题，要开阔视野，结合本职工作深入挖掘项目研究的社会价值。对申报书撰写要求和规范格式进行了详细解读，归纳出一些撰写技巧。王成福鼓励教师们要开阔视野，认真思考，组建研究团队，踏踏实实地开展研究工作，切实为解决我市经济社会发展中的难点与热点问题提供智力支撑。据悉，信息学院将做好学院十三五科研规划的落实工作，分方向组建科研团队，重视科研平台建设，修订科研考核与激励政策，规范课题管理，加大各类课题的申报辅导力度，营造良好的科研氛围。', 'http://info.jhc.cn/2017/0330/c201a77335/page.htm', 'http://info.jhc.cn/_upload/article/images/67/1d/71c319394d67ab097492aa3db8fb/ee70ac8a-8a06-45ae-bfe8-83fbbb5955d6.jpg|http://info.jhc.cn/_upload/article/images/67/1d/71c319394d67ab097492aa3db8fb/29b6a4f3-4230-488a-b13b-32d5a16c36a6.jpg', '2017-05-17 14:13:01.814255');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (33, '蔡金晶', '教科办', '周荃', '2017-03-29 00:00:00', '校2017国（境）外专家学术系列讲座之二：职业教育+专业英语=PVQC', '近日，台湾师范大学技术职业教育研究中心研究员戴长熙应邀来我校作“职业教育+专业英语=PVQC”专题讲座，信息学院200余名师生聆听了此次讲座。讲座中，戴长熙围绕“由专业英语学习到技能学习时的改变对当代大学生产生的影响”这一主题，畅谈了自己的看法与经验。他表示，PVQC指的是目前全球流行和公认的专业英文词汇能力国际认证。一个具有国际竞争力的大学生必须具备由广博的专业知识与竞争基础能力构成的双核心专业能力，其中的专业语文能力则是由专业英文词汇、计算机综合应用能力和建立国际化与跨领域合作能力共同培养而成的，因此专业英语学习在现阶段显得尤其重要。此外，戴长熙还让学生现场演练了专业英语的学习系统操作，并介绍了国际专业认证考试，让同学们受益良多，同时也对专业英语的学习充满了信心。', 'http://info.jhc.cn/2017/0329/c201a77321/page.htm', 'http://info.jhc.cn/_upload/article/images/a2/6a/f07dd6d6442c98afba19aba7db10/60161268-3ae9-41da-85f4-06ec461382fe.jpg', '2017-05-17 14:13:01.836461');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (34, '王成福', '信息工程学院', '周荃', '2017-03-22 00:00:00', '四年制高职电信专业学生前往浙江理工大学开展游学活动', '为了深化校际合作共同培养高端技术技能人才，我校四年制高职（本科）电子信息科学与技术专业2015级和2016级共98学生，于2017年3月21日前往浙江理工大学开展游学活动。同学们先后参观了浙江理工大学的校史馆、图书信息大楼、实验室等，聆听了浙江理工大学信息学院副院长蒋明峰教授所做的学校学院概况介绍、学科专业导论、数字图像处理、人工智能等以及马汉杰教授所做的DSP技术等学术报告，听取了高年级学生的学习经验介绍并进行了互动交流，亲身体验了浙江理工大学的学习氛围、社团活动、食堂就餐等，为同学们进一步了解学科专业发展、找准自己的定位、毕业后继续攻读专业硕士等均起到了较好的推动作用，并对浙江理工大学良好的学习氛围留下了深刻的印象。本次游学活动得到了我校信息学院和浙江理工大学信息学院领导的高度重视，对活动内容进行了精心策划和周密安排，确保本次活动取得了良好效果，也促进了双方的合作与交流，为今后深化高职与本科院校的合作办学、师生互动、探索四年制高职人才培养等奠定了良好的基础。', 'http://info.jhc.cn/2017/0322/c201a77057/page.htm', 'http://info.jhc.cn/_upload/article/images/f2/a8/3d283fdf4baeb418e69d54cef34a/31d4bb5c-8239-4f7d-b437-c725373bca83.png|http://info.jhc.cn/_upload/article/images/f2/a8/3d283fdf4baeb418e69d54cef34a/ba4d7f7e-58ed-4835-ac57-11ed81acb8c7.png', '2017-05-17 14:13:01.858644');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (35, '丰辉', '浙江新闻网', '周荃', '2017-03-20 00:00:00', '《浙江新闻网》报道我院志愿者用无人机巡河 助力治水', '青春力量助力治水 金职院“河小二”动用无人机来巡河2017-03-20 13:59  |   浙江新闻客户端 记者 郎擎宇 通讯员 丰辉3月18日，婺城区安地镇梅溪河上方缓缓飞过一架正在执行特殊任务的无人机。 “看这里，有一处治水盲点。”金华职业技术学院信息学院的学生志愿者李宏宇边操作手中的设备边说。原来，这架无人机正在协助志愿者们巡河。近期，金华职业技术学院信息学院10名志愿者来到安地镇，首次利用专业所学的无人机技术，代替人工巡河，参与到安地镇的五水共治工作中。实践出的金点子，巡河有了“秘密武器”几天前，金职院信息工程学院的10名志愿者来到安地镇团委开展五水共治志愿者活动，他们分成两个小组在梅溪河附近巡河。巡河半天后，志愿者李宏宇发现，人工巡河存在耗时久、盲点多、死角不易发现的问题。想起在学校学习过驾驶无人机航拍技术，他想那是否能让无人机来代替人工巡河呢？利用现代信息技术巡河的想法得到了团队成员和安地镇的团委书记胡琦的支持。3月18日，李宏宇等志愿者从学校带来了一架无人机，再次来到安地镇梅溪河附近，志愿者们启用无人机担任“河小二”，无人机缓缓飞过河道上方，对河道进行空中全景拍摄，确保整条河道保洁巡查实现全覆盖。借助无人机传回的画面，志愿者们可以很清晰地查看河道水体颜色、排水口等情况。志愿者们很快就通过无人机显示器发现了河道一角有一处生活垃圾，胡琪立刻派人前往污染点进行清理工作。无人机巡河一趟下来，李宏宇觉得很高兴：“无人机电池可以支持的巡航时间约20分钟，遥控半径在5000米左右，飞行速度约每秒10米，如果有多块备用电池，巡河效率会比人工高不少的。”政校合作，共同剿灭劣V类水不久前召开的全省高校思想政治工作会议提出，社会是高校学子参与实践的大平台，青年人应深入“五水共治”的火热一线，发挥自身专业优势，用实践行动助力“治水”。为了积极响应团省委“大力弘扬志愿精神，踊跃投身五水共治”、金华团市委“青春聚力生态廊道建设”的部署，金职院与社会实践基地安地镇牵手合作开展“剿灭劣V类水，IT志愿者来助力”系列活动。安地镇团委书记胡琦说：“巡河用上无人机，工作效率提高了很多，这些学生志愿者给我们带来了很多工作上的便利，真的很感谢他们”。据悉，接下来，金职院信息学院的“河小二”们将继续为安地镇进行巡河、巡街工作，实时监测水质和村镇街区的卫生死角，为美丽金华建设出力。原文地址：http://zj.zjol.com.cn/news/587926.html?from=singlemessage&isappinstalled=1', 'http://info.jhc.cn/2017/0320/c201a76938/page.htm', 'http://info.jhc.cn/_upload/article/images/f9/25/4e95c6d04748ad7f73673e6234ef/05aa6718-82f2-4e5e-a44b-5ee6ee27c3ac.jpg|http://info.jhc.cn/_upload/article/images/f9/25/4e95c6d04748ad7f73673e6234ef/e5cae1b5-c629-4403-9333-12e1f1abd270.jpg|http://info.jhc.cn/_upload/article/images/f9/25/4e95c6d04748ad7f73673e6234ef/59987028-3091-48a8-b516-09f7fb0abb7d.jpg', '2017-05-17 14:13:01.880968');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (41, '周荃', '办公室', '周荃', '2017-03-10 00:00:00', '义乌工商职业技术学院电子商务学院院长一行来我院考察交流', '近日，义乌工商职业技术学院电子商务学院院长王晓明一行3人来我院考察交流。学院领导，办公室主任出席了交流活动。座谈中，陈海荣代表学院对客人的来访表示欢迎，并简要介绍了学院的发展状况。宾主双方围绕二级学院管理体制等内容展开深入交流。', 'http://info.jhc.cn/2017/0310/c201a76317/page.htm', NULL, '2017-05-17 14:13:02.015891');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (248, '周荃', '', '系统管理员', '2015-12-29 00:00:00', '《金华晚报》报道我院志愿者禁限燃宣传活动', '前两天，金职院信息学院青年志愿者大队共10名志愿者联合金华江南第二陵园共同向市民宣传无烟祭祀。以“鲜花换烟火”的形式，呼吁广大市民，要积极倡导无烟祭祀。很多前来扫墓的市民纷纷为其点赞。　　12月21日，金华开发区三江街道东莱社区在文荣医院门口开展了禁限燃放烟花爆竹签名活动。宣传活动中居民纷纷加入宣传队伍，得到了广大居民的支持。　　三江街道阳光社区也组织志愿者在辖区内宣传禁限燃放烟花爆竹。吴彰义 汤鑫严 摄 原文地址：http://epaper.jhnews.com.cn/site1/jhwb/html/2015-12/25/content_1655154.htm', 'http://info.jhc.cn/2015/1229/c201a54617/page.htm', NULL, '2017-05-17 14:14:17.880255');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (36, '徐露诗、周荃', '学工办，办公室', '周荃', '2017-03-16 00:00:00', '“语”过天晴 拯救一切不开心——信息学院开展学生（个体）咨询工作', '当你在学习中、生活中碰到难以释怀的烦恼该怎么办？当你遇到青春的雨季会怎么度过？为了深入贯彻“以生为本”的教育理念，有效发挥辅导员在学生成长成才路上引路人的职业角色，信息工程学院大力推动并规范开展学生（个体）咨询谈话工作，采用固定咨询和机动咨询结合，发挥辅导员个人专业特长，引导学生主动约谈老师，构建一条防患于未然的师生沟通桥梁。据了解，以往的学生谈话较多是老师在寝室、办公室约谈或者个案发生后再处理，反应速度相对比较滞后。本学期，学院专门开辟“语”过天晴心理咨询室，组建一支由党委书记、学工办/团委负责人及全体辅导员组成的咨询队伍，每周一到周四安排两位老师专门接待咨询，其他时间机动。学生（个体）咨询谈话方式多样，可因时、因人、因事而异，采取面谈为主、社交媒体或通讯工具交谈为辅的方式进行。学生（个体）咨询工作开展以来，已有数十位同学向各个辅导员进行了包括恋爱心理、职业规划、学业辅导等方面的咨询。“以前在生活中遇到难题都只能向爸妈求救，但是爸妈也不太理解我在学校的生活，现在可以向辅导员寻求帮助，老师给我的建议挺实用，给了我很多启发，我的心情也轻松了许多”一位刚结束咨询的同学这样说道。', 'http://info.jhc.cn/2017/0316/c201a76786/page.htm', 'http://info.jhc.cn/_upload/article/images/9b/3a/6426f9f5472382b6ec51704554c8/77b365c2-4811-4286-ace0-d9ea1bf73c1e.jpg', '2017-05-17 14:13:01.903161');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (37, '周荃', '办公室', '周荃', '2017-03-14 00:00:00', '信息学院党委理论中心组专题学习《浙江省直属机关党员及基层党组织问责办法（试行）》', '13日下午，信息学院党委理论中心组组织专题学习了《浙江省直属机关党员及基层党组织问责办法（试行）》和胡正明书记在全体教职工大会上的讲话，研究部署学院落实意见。会上，全体中心组成员认真宣读了学习文件并展开讨论，大家表示《办法》的实施进一步规范和强化了党的问责工作，从严加强对党员的经常性教育管理和监督，党员干部要将理论学习与提升业务水平相结合，把思想政治工作贯穿教育教学全过程，扎实推进新形势下的思想政治工作。接下来，信息学院党委将以“两学一做”学习教育实践活动和《学生品德养成与实践》课程实施为契机，创新学习方法和实践活动，持续推进基层党建工作和文化育人工程。', 'http://info.jhc.cn/2017/0314/c201a76664/page.htm', NULL, '2017-05-17 14:13:01.925616');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (38, '何碧峰', '学工办/团委', '周荃', '2017-03-14 00:00:00', '“睛”彩人生，珍“Eye”永恒——信息学院志愿者活动', '眼睛是心灵的窗户，是人体最宝贵的感觉器官，为了培养孩子从小爱眼，加强爱眼意识将爱眼知识落实到每个家庭和幼儿。近日，信息学院的志愿者们来到江滨社区丁丁幼儿园给小朋友和家长们上了一堂生动的儿童护眼课。志愿者们通过儿歌、讨论、道具体验等形式让幼儿了解近视眼是怎样形成的，教育幼儿讲究用眼卫生，带领幼儿一起学习眼保健操。下午放学的时候给家长送上一份《关于爱眼护眼告家长书》，让家长也重视起幼儿近视的危害性，做到家校合力保护好儿童的视力健康。 “孩子是祖国的未来，我们每个人都有责任和义务去保护和帮助幼儿健康成长。通过此次活动，引导幼儿亲身体验眼睛的重要作用，懂得要爱护眼睛。让护眼行动从小做起，从现在做起。”志愿者张敏感慨地说。', 'http://info.jhc.cn/2017/0314/c201a76663/page.htm', 'http://info.jhc.cn/_upload/article/images/12/62/7f04e8c8470b8fa718d596ff3e51/ea2b5923-a713-4e97-92fc-66e6506722c3.jpg|http://info.jhc.cn/_upload/article/images/12/62/7f04e8c8470b8fa718d596ff3e51/6d1543a0-c7ca-4fa0-9861-7df08b5da40f.jpg', '2017-05-17 14:13:01.947704');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (39, '徐华丹', '教科办', '周荃', '2017-03-10 00:00:00', '信息学院积极推进四年制高职导师制“实施网络化，成果多元化”', '近期，为进一步推动四年制高职导师制的“实施网络化、成果多元化”，信息学院分别召开了导师研讨会和学生推进动员会。      经研讨，导师们一致认为需要加强每周汇报制度的实施，每个项目组及时制定周计划，反馈问题并总结成果。学生在项目实施过程中，需要按时完成每项任务并形成文档，把一系列过程资料及时上传Moodle平台。同时要规范导师和学生的日常互动，通过Moodle平台讨论问题、查看资料、提供建议等，实行网络化管理。      在学生推进会上，院领导王成福强调，导师制项目实施注重学生综合能力的培养，项目组的过程资料都可以转化成论文、专利等各种成果，实现成果多元化。学院将根据导师制项目成果完成学生第二课堂学分互认。', 'http://info.jhc.cn/2017/0310/c201a76321/page.htm', 'http://info.jhc.cn/_upload/article/images/f5/39/74f1ce24442e87f0949db3ce0daa/dfc3fbab-a511-4db9-9360-6611bbf020da.jpg|http://info.jhc.cn/_upload/article/images/f5/39/74f1ce24442e87f0949db3ce0daa/1d8e446b-caf8-4112-9a1d-97a48297930b.jpg', '2017-05-17 14:13:01.969949');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (40, '余红娟', '电子产品开发部', '周荃', '2017-03-10 00:00:00', '杭州电子科技大学罗友教授给电子信息与科学技术专业学生上专业导论课', '3月7日，杭州电子科技大学罗友教授应《电子信息类学科导论》课程之邀来院给电子信息与科学技术专业16级学生作《大学生创新思维知识与能力体系的构建与发展》专题讲讲座。罗教授根据自己从事部队飞机系统故障维护经历与大学任教从事电子技术及新能源领域应用等行业企业科研、教学、竞赛教练工作经验，对创新的定义、重要性以及创新能力的培养途径等作了详细阐述。他指出，电子技术是一个工具，“电子+“应用领域可以换发出大量的创新思维，学习电子技术要与各行各业的人合作与沟通。他表示，创新思维的发展包括创新能力的培养与创新素质的养成，再有能力的人如果不去实践，不具备优良的个人品质、强烈的事业心和责任感以及吃苦耐劳、坚持不懈的精神，也会失去创新成功的机会，影响创新能力的发挥。罗教授列举了很多民间创新，鼓励学生要积极参与实践，他说，创新往往是在50%的已有知识与50%的未知知识的共同应用中产生，因而学生只有参加竞赛、参加企业实践活动、参加教师的教学与科研，形成自觉的探索未知领域的勇气和毅力，才能形成自己的知识、能力与素质体系。学生在三个小时的讲座中，仔细听课，做好记录，课后与罗教授进行互动与交流，师生双方意犹未尽。罗教授对学生说，今后越来越多的岗位需要创新思维，逐渐替代低水平重复劳动的岗位，而新技术从外文转换成中文需要3-5年时间，他鼓励学生要学好英语、抢占高地，树立忧患意识，通过行动成就自己的梦想！', 'http://info.jhc.cn/2017/0310/c201a76319/page.htm', NULL, '2017-05-17 14:13:01.992046');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (59, '周荃、丁晨', '办公室', '周荃', '2016-12-22 00:00:00', '分享访学见闻  培育国际化师资团队', '近日，信息学院召开出国境学习经验交流汇报会，赴新西兰怀卡托理工学院学习的师生们与大家分享了他们访学期间的所见所闻。汇报中，电子产品开发部的刘子坚老师通过课程观摩深入感受了新西兰的教学，重点介绍了怀卡托理工学院以“学习者为中心”的教室和实验室布置、重视学生在教学活动中的主体地位、小组小班化组织教学、教学方式灵活、师生善用Moodle平台资源以及集休闲、娱乐、餐饮、学习于一体的Hub等等，基于学习感受，刘子坚老师还对下一阶段的学习和工作做了一定的革新规划；通信与网络工程部的丁晨老师则以第一视角的方式，先由出发至到达，细致描绘了在新西兰的所见所闻，并重点介绍了新方专为这次研修的老师所组织的教学方法课程-PDAL（Professional development and Learning）,介绍中丁晨老师所提及的Student-centered Learning（学生为中心的学习）与Inquiry-based Learning（基于资讯的学习）正好与目前主流的教育理念翻转课堂不谋而合，他提出：教师的本质是确保学生在课堂上学习的发生，并提出Facilitator（辅助者）一词与老师们共勉；信息化事业部的胡芳芳老师重点介绍了新方专业课的运作模式，从课前、课中、课后三个不同环节图文并茂地展示了此次观摩的众多专业课的各环节：资源丰富且不拘一格的MOODLE、详尽清晰的课程计划及评分标准、高效直观的现场打分及课堂反馈、丰富多彩的分组汇报、琳琅满目的课外资源、贴近生活的课堂活动等等，让与会人员感同身受，让与会人员真切感受到了访学活动带来的文化熏陶和思维转变。同时，参加首届赴新夏令营的学生用自己拍摄和制作的游学视频展示了这段美好的游学经历，还现场邀请老师们体验了毛利文化碰鼻礼。汇报会有效地实现了教育资源的共享，信息学院将继续重视教育国际化发展，培养具有国际化视野的教师团队和具有国际竞争力的创新人才。', 'http://info.jhc.cn/2016/1222/c201a73781/page.htm', 'http://info.jhc.cn/_upload/article/images/5a/4f/e71f6dc04bd9984b06a7422b98cf/e954fde6-119c-428c-bb52-6ce1ac2db183.jpg', '2017-05-17 14:13:11.537609');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (42, '花有清', '教科办', '周荃', '2017-03-10 00:00:00', '2017年“嵌入式技术与应用开发”、 “风光互补发电系统安装与调试”、”软件测试”省赛赛项技术研讨会在我院召开', '3月9日，2017年全国职业院校技能大赛 “嵌入式技术与应用开发”、“风光互补发电系统安装与调试”、“软件测试”赛项浙江省选拔赛技术研讨会在信息学院召开，来自浙江机电职业技术学院、温州职业技术学院、浙江经济职业技术学院等17所高职院校的30余位专家老师一同研讨大赛规程，会议由赵敏笑副院长主持，教务处吕美巧副处长参加了会议。会上，各校老师就“嵌入式技术与应用开发”、“风光互补发电系统安装与调试”、“软件测试”省赛的竞赛规程进行了热烈的讨论与交流，并在竞赛技术规范、竞赛设备的软硬件平台、竞赛内容、竞赛时间、每校参赛队伍数、组队方式和竞赛流程等方面达成了共识。“嵌入式技术与应用开发”赛项研讨“风光互补发电系统安装与调试”赛项研讨“软件测试”赛项研讨据悉，我校已多次承办了“嵌入式技术与应用开发”、“风光互补发电系统安装与调试”两个赛项的国赛选拔赛，“软件测试”赛项是今年首次列入国赛项目，也是我校首次组织承办该项赛事。', 'http://info.jhc.cn/2017/0310/c201a76295/page.htm', 'http://info.jhc.cn/_upload/article/images/6f/0f/5e114bb148799cc08fdc50440514/39746e97-11f3-4f79-b031-68c2a74550f2.jpg|http://info.jhc.cn/_upload/article/images/6f/0f/5e114bb148799cc08fdc50440514/811a5b8f-0f92-4550-9420-0028ae7ea2f9.jpg|http://info.jhc.cn/_upload/article/images/6f/0f/5e114bb148799cc08fdc50440514/7566371a-82b5-4272-9ae7-2d539f1b83f7.jpg', '2017-05-17 14:13:02.036744');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (43, '徐露诗', '学工办/团委', '周荃', '2017-03-09 00:00:00', '你爱阅读  我来赠书——信息学院展开赠书悦读活动', '“读书，是为了说话更有底气，能够心平气过地面对这个世界”应电164班薛纪克拿着他心爱的书本《你在忙什么》，这样与其他同学分享他的阅读心得。  据悉，为了鼓励学生养成良好的阅读习惯，信息学院继图书馆晚自习、爱阅读主题班会之后，又开展了“书香漫漫 赠书悦读”为主题的赠书活动，向图书馆借阅量前十名的同学赠送图书，十位同学分别获得100 -50元的购书金额的奖励。物联网162班沈威同学对历史充满兴趣，选择了《明朝那些事儿》、软件161班闫旭同学则早已计划好要利用这笔奖励金额购买《计算机科学导论》，希望在专业上有所精进……同学们拿到书后，都迫不及待将书打开，希望一睹为快。“现在能够静下心来读书的人越来越少，我想多利用大学时光，尽可能地多读书，书里面有许多前人留下的思想与行为值得我们去学习”一位获赠的同学这样说道。', 'http://info.jhc.cn/2017/0309/c201a76266/page.htm', 'http://info.jhc.cn/_upload/article/images/fe/2d/bc3062f744d99c80dceb6a924ed1/2b37601f-0aec-4b7e-9801-b20ea76e3a7a.jpg', '2017-05-17 14:13:06.653561');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (44, '徐华丹', '教科办', '周荃', '2017-03-09 00:00:00', '技能竞赛培训经验分享', '为了取得优异的竞赛成果，3月8日，信息学院邀请近年多次在国一类竞赛荣获一等奖的电气自动化专业主任马汝星介绍竞赛培训经验并做技能竞赛动员，全体专业主任和竞赛指导老师参与了会议。会上，马汝星介绍，电气自动化专业竞赛培训周期多为一年，整个培训分为两个阶段。第一阶段，学生每周训练一个小项目，通过训练，学生掌握理论知识、熟悉设备培养技能；第二阶段，学生进行针对性强化训练，在操作速度、操作规范性、完成质量等方面按照竞赛严格要求。培训期间，指导老师记录学生的进步和问题，总结得失，并及时掌握学生思想动态。与会人员就培训期间学生安全、学生管理以及课程替代方面展开了热烈讨论。据悉，今年信息学院参加了多个赛项的比赛，参加竞赛指导的老师和竞赛的学生都为历年之最。会后，指导老师将制定精细的培训计划和考核方案，关注学生训练情况以及同年级学生的传帮带情况，并需要完成培训项目库的积累，形成指导教材。', 'http://info.jhc.cn/2017/0309/c201a76244/page.htm', 'http://info.jhc.cn/_upload/article/images/48/e2/1d2d0d6f49f8a2b43f5677d89c41/939ad51e-8f24-4c42-8d38-4e28f04f7012.jpg', '2017-05-17 14:13:06.70376');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (45, '丰辉', '学工办/团委', '周荃', '2017-03-07 00:00:00', '学雷锋 送服务', '“这些小伙子们真能干！”3月5日，在第54个学雷锋日到来之际，信息学院组织志愿者前往市经济技术开发区中村社区进行家电维修服务，深受居民好评。活动现场，志愿者们相互协作，边认真登记信息，边仔细检查、分析每一台电脑、家电存在的问题，并积极进行处理。经过一天时间的辛勤努力，共解决电脑9台，包括电脑清灰和系统重装、电脑驱动等问题，义务维修电风扇、电吹风、煤气灶等家电20余件。“助人是快乐之本，在志愿服务中收获快乐，让我觉得很幸福。”志愿者章萍萍开心地说道。据悉，此次活动为学院学雷锋志愿服务月活动内容之一，接下来，他们还将通过志愿服务队伍建设、主题活动开展、品牌项目打造、社区志愿服务常态化等四个途径开展一系列特色鲜明的活动，进一步弘扬雷锋精神，传递社会正能量。', 'http://info.jhc.cn/2017/0307/c201a76127/page.htm', 'http://info.jhc.cn/_upload/article/images/74/e1/bc5eaf16491aad0692f52961fa84/9534d516-761e-44a1-b027-99f7ef86d511.jpg|http://info.jhc.cn/_upload/article/images/74/e1/bc5eaf16491aad0692f52961fa84/c6b7a512-8785-4701-85aa-8efa2944d6c4.jpg', '2017-05-17 14:13:06.725944');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (46, '', '金华日报', '周荃', '2017-03-06 00:00:00', '《金华日报》报道我院志愿者活动', '学雷锋日， 志愿者在行动昨天，金职院信息工程学院21名志愿者来到市区江南街道中村社区，志愿者们发挥自己所学专业特长，为居民义务维修电脑、电脑除尘等志愿服务，以实际行动学习雷锋好榜样。新闻链接：http://epaper.jhnews.com.cn/site1/jhrb/html/2017-03/06/content_1813022.htm', 'http://info.jhc.cn/2017/0306/c201a76054/page.htm', 'http://info.jhc.cn/_upload/article/images/5c/6b/7b497e0242af942ff194db941816/b0d8e03a-ba1e-4ad5-8f7b-bba1b4187459.jpg', '2017-05-17 14:13:06.748063');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (47, '周荃', '办公室', '周荃', '2017-03-06 00:00:00', '信息学院学习传达全省高校思政工作会议精神', '2月27日，信息学院党委召开理论中心组学习会，全体成员、中层干部认真学习了省委书记夏宝龙在全省高校思想政治工作会议上的讲话精神。全体成员认真学习了讲话精神，并结合学院工作展开激烈讨论。大家一致认为，要牢牢守住高校这块前沿阵地，坚持社会主义办学方向，引导广大师生做社会主义核心价值观的坚定信仰者、积极传播者、模范践行者，并立足于地方产业不断提升人才培养质量。学院将坚持“转型发展、特色发展、创新发展、开放发展”的办学原则，以立德树人为根本，以服务产业发展为宗旨，以提高人才培养质量为核心，以优质队伍建设为重点，以提升治理能力为抓手，不断深化“五位一体”育人模式，积极推动各项改革，实现结构、内涵的全面优化，提高学校的综合实力，将人文素养作为学生全面发展的基础贯穿人才培养全过程。', 'http://info.jhc.cn/2017/0306/c201a76047/page.htm', NULL, '2017-05-17 14:13:06.770294');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (48, '朱苏航', '电子产品开发部', '周荃', '2017-03-06 00:00:00', '我院公派出国留学项目进展顺利', '2017年1月7日，中国驻新西兰奥克兰领事馆副总领事罗斌辉前往汉密尔顿看望国家公派出国留学人员，一起陪同前往的有汉密尔顿的杨宇明议员、怀卡托周报的朱总编以及领事馆教育组房领事等其他工作人员。我院电子产品开发部朱苏航老师作为2016年度的国家公派访问学者参加了这次座谈会。会上，罗总领事向各位学生、学者表达了祖国的问候，希望大家保证安全，珍惜这样的工作和学习机会，学以致用、学以发展、学以贡献。罗领事仔细地询问了各位专家学者的学习、工作和生活状况，提出了一些具体要求。当我院朱苏航老师介绍我校情况并作怀卡托合作办学成果展示时，我校中新办学的模式和成果获得在座领导和公派人员的一致称赞。大家畅所欲言，相互交流经验，以及工作和学习进展情况，并表示定会珍惜来之不易的留学时光，刻苦学习，努力工作，争取取得好成绩，按期回国，报效祖国。', 'http://info.jhc.cn/2017/0306/c201a76045/page.htm', 'http://info.jhc.cn/_upload/article/images/19/9a/76cff72d4abe84d62a211c33a9b4/b2227706-ccf7-4a2d-8824-1d59d4e47599.png', '2017-05-17 14:13:06.79246');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (49, '徐露诗', '学工办/团委', '周荃', '2017-02-27 00:00:00', '回味寒假，展望新学期——信息学院开展新学期首次主题班会', '近日，信息学院开展了本学期的第一期主题班会，本次班会主要围绕“寒假经历”这一主题展开，各班级开展的形式多样，同学们配合积极，现场气氛十分热烈。同学们借助本次班会，与小伙伴们分享了寒假时丰富的经历。有的同学假期里打工体验生活，了解了社会，增长了才干，也发现了自己的不足之处；有的同学则选择了“远方”，背上行囊到向往的地方旅游，在这过程中不仅欣赏了优美的风景，开阔了视野，还提高了自身的独立性；还有的同学则坦白“交代”寒假大部分时间在“葛优瘫”，暂时离开了忙碌的学校生活，在假期里尽情地休息，为下个学期做准备……同学们的寒假经历中，有收获也有遗憾，但是每一位同学都下了决心，希望在新的学期能够取得更好的成绩，有更充实的学习经历。', 'http://info.jhc.cn/2017/0227/c201a75726/page.htm', 'http://info.jhc.cn/_upload/article/images/fe/15/1bff4dd94a9bb465661c492a200c/21b17a34-16ec-412a-b30e-48a4380abe55.jpg', '2017-05-17 14:13:06.814885');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (50, '叶梦婷、章珺', '浙江新闻客户端', '周荃', '2017-01-17 00:00:00', '《浙江新闻客户端》报道我院留学生在金华高铁站当志愿者', '金华高铁站里外国友人当志愿者2017-01-16 17:31  |   浙江新闻客户端记者 叶梦婷 通讯员 章珺 今天（1月16日），春运的高铁金华站内，可以看到不少穿着志愿者在人群中穿梭引导。在这些人当中，不少人都会注意到一个皮肤黝黑的外国友人，说着一口不算太流利的中文，接受旅客的问询。他中文名叫唐平，来自卢旺达，是金华职业技术学院信息工程学院的留学生。他今年大二，今年他不回国，就留在了学校。今天，他不少同学都到高铁金华站开展志愿服务，他也就一起来了。 “以前就听说过春运，很多人会赶着回家过年，不过这是第一次看到。”唐平说，他自己也遇到过需要别人帮助的时候，如果有人能伸出援手，他会觉得很感激，所以他觉得做志愿者很有意义，能够帮助别人是一件很开心的事情。 3点半左右，高铁候车室里旅客不少，采访期间，还有一位外国友人前来问询，唐平和他一问一答，没一会就帮旅客解决了问题。 “之前有三位来自国外的旅客丢失了车票，工作人员和他们沟通有些困难。”唐平说，他就在中间充当翻译，发现车票正好被人捡到放在了服务台，国外的旅客找回车票以后非常感谢。一天下来，唐平已经记不清楚自己为多少位旅客提供了帮助，这让他很开心。他说，春运期间，他有空就会参与志愿者活动。原文链接：http://m.8531.cn/news/534951.html?t=1484563007782&weixin_share_count=5&from=singlemessage&isappinstalled=0&ismobilephone=2', 'http://info.jhc.cn/2017/0117/c201a75321/page.htm', 'http://info.jhc.cn/_upload/article/images/8f/86/9258942e4049a751759a57f5a047/fcb484cf-5097-49e9-9d6a-c72dfcfefc7a.jpg|http://info.jhc.cn/_upload/article/images/8f/86/9258942e4049a751759a57f5a047/b6841a8b-b525-4150-bfa0-eef9e23e0b9b.jpg|http://info.jhc.cn/_upload/article/images/8f/86/9258942e4049a751759a57f5a047/f6a831eb-2495-4ff8-b8d6-679997fbf783.jpg', '2017-05-17 14:13:06.837069');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (51, '时宽兵、王志康', '学工办/团委', '周荃', '2017-01-17 00:00:00', '《金华日报》报道我院留学生当春运志愿者', '留学生当春运志愿者记者 时宽兵 通讯员 王至康 文/摄     昨天，在金华高铁站候车室里，来自金职院信息工程学院的23名志愿者。利用寒假在春运期间为旅客提供服务。在金职院信息工程学院学习的卢旺达留学生唐平（中文名）也加入到志愿者队伍当中，帮助旅客查看车票信息，指引检票口，给外籍旅客当翻译，忙得不亦乐乎。原文链接： http://epaper.jhnews.com.cn/site1/jhrb/html/2017-01/17/content_1797111.htm?from=singlemessage&isappinstalled=1', 'http://info.jhc.cn/2017/0117/c201a75320/page.htm', 'http://info.jhc.cn/_upload/article/images/8e/df/aca0eae448cfb1c12c467bd3f328/b8362bac-c486-415e-b8cf-cce428cf608b.jpg', '2017-05-17 14:13:06.859248');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (52, '丰辉', '学工办/团委', '周荃', '2017-01-13 00:00:00', '我院举行2017年新年联欢晚会暨颁奖典礼', '近期，信息学院举办2017年新年联欢晚会暨颁奖典礼，全院共600余名师生观看了演出。一支充满活力的爵士舞《man you love blues》拉开了晚会的序幕，同学们释放着激情，舞动着青春；特色手影舞表演，同学们在黑暗中用双手呈现了笑容、love与IT等字样，展现出信息学子追求爱与梦想；信息艺术分团带来的乐队表演《怎么说我不爱你》，这是他们怀揣梦想追逐，向金职大声的表白的方式；小品《我的路》，风趣幽默，意味深长，同学们表达着在求学路上的荆棘与坎坷，悟出人生真理，只有不断努力，超越自我。才是通往成功的大道；师生共同演绎的歌曲《我的学生时代》，引发阵阵掌声，那是师生共同美好的校园回忆。晚会期间，学院领导还为2016年度在省级以上技能竞赛获奖学生颁发了荣誉证书，进一步激发信息学子对专业知识学习的热情。', 'http://info.jhc.cn/2017/0113/c201a75104/page.htm', 'http://info.jhc.cn/_upload/article/images/79/ef/210afd604182be06344900fc79c4/95643eec-5f1e-41f9-9854-12895b18a00c.jpg|http://info.jhc.cn/_upload/article/images/79/ef/210afd604182be06344900fc79c4/11ffea4e-c886-4cf4-b061-c404980202bb.jpg|http://info.jhc.cn/_upload/article/images/79/ef/210afd604182be06344900fc79c4/5ce2bab6-59b1-40df-8012-242eff82ed42.jpg|http://info.jhc.cn/_upload/article/images/79/ef/210afd604182be06344900fc79c4/798c607c-dfc6-4aa8-bc26-2e7e086a5485.jpg', '2017-05-17 14:13:06.883063');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (53, '王成福', '信息学院', '周荃', '2017-01-05 00:00:00', '民盟金职院支部召开2016年度总结大会', '1月4日，民盟金职院支部召开2016年度工作总结大会，支部19位盟员参加了会议。会议回顾总结了2016年度支部的盟务工作情况。一年来，支部立足本职服务社会，积极参政议政，认真开展调查研究，主动撰写社情民意信息，以《健康饮食指导队》支部品牌建设为抓手，健全服务机制，完善服务内容，扩充服务功能，有序地开展健康钦食的指导服务活动，为休闲农家乐的健康发展多做贡献。由于支部各项工作突出而被民盟金华市委会评为市级先进盟组织，刘根华、华玉亮、李旭升、徐恩平被评为市级先进盟员，王成福被民盟浙江省委会评为省级先进盟员。支部组织盟员认真学习了市委书记赵光君在市委六届十三次全体（扩大）会议上的讲话精神，充分理解金华当前及今后一个时期的发展目标是：“全面小康、浙中崛起”，“走在前列、共建金华”，通过共建共融共享共赢等方式，推进县域经济向都市区经济转型发展，为支部2017年的参政议政信息工作指明了方向。支部盟员纷纷对2017年的支部工作献计献策，同时支部委员会出台参政议政信息工作奖励办法，更好地调动和发挥支部盟员的工作积极性，力争使2017年支部的参政议政、社会服务、政治协商工作相互促进, 凸显特色，共同迈上新台阶。', 'http://info.jhc.cn/2017/0105/c201a74565/page.htm', 'http://info.jhc.cn/_upload/article/images/10/44/4139df854ceea8e16fa13c240c2f/71fa0f6d-e963-4d80-a06f-3db7350eb3ba.jpg', '2017-05-17 14:13:06.903826');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (60, '余红娟', '电子产品开发部', '周荃', '2016-12-13 00:00:00', '信息工程学院举行首届高职四年制电子信息科学与技术专业指导委员会', '12月11日，我校举行首届电子信息科学与技术四年制高职专业指导委员会会议，校党委副书记余党军、胡野，指导委员会委员，相关职能部门负责人以及信息学院相关人员参加。 胡野首先向与会者们简要介绍了四年制高职电子信息科学与技术专业办学意义、成效与困难，浙江理工大学相关负责人表示金职院在校企合作工作方面一直做得很扎实，成立专业指导委员会对形成四年制高职教育特色意义重大。会上，余党军宣读了《信息工程学院关于成立四年制高职教育人才培养试点电子信息科学与技术（电力电子技术方向）专业指导委员会的通知》，并给各位专家委员颁发了聘任证书；信息学院相关负责人作了《高职四年制电子信息科学与技术专业建设情况与十三五规划》、《1+N多元导师制实践与成效》等汇报；与会专家针对高职四年制电子信息科学与技术专业人才培养方案与课程设置进行了研讨，专家们提出，电力电子技术是现代电子技术的主流技术，金职院电子信息科学与技术专业培养电力电子技术方向人才，学生一定深受社会欢迎，建议区别于本科与研究生，做好专业定位，以技术应用性人才为培养目标，将企业产品的标准要求、工艺要求、管理要求引入专业、引入课程、引入教学，建议让学生多做一些不给答案的产品设计，产品参数与企业产品对接，把学生早一点放在应用性产品的设计与开发训练上，可以让学生去企业做毕业设计。专家们强调，课程建设应从基础课程开始改革，不要给学生枯燥的讲解，而是把有趣的项目与社团活动引入专业，培养学生的兴趣与探究学习能力，企业家则表示要多指导学生的职业生涯，让学生发挥自身优势，自如应对企业用人选拔。专家们表示一定要配合学校做好任期内的各项任务，帮助学校、专业、学生，做好联合培养工作。最后陈院长一行带领专家们参观了信息工程学院的实验室，专家们表示学校的办学条件优越，完全有能力把学生培养成优秀的人才。会后不少专家发朋友圈称赞金职院不愧是一流的高职，具有一流的高效的工作团队。据悉，为成立与开展本次专业指导委员会，信息工程学院开展了百名校友调研，分赴宁波、杭州、绍兴、嘉兴、金华五个重点区域，由院领导和专业带头人带队实地考察了25家企业，其中有环保设备巨头菲达集团与天洁集团，LED光源生产巨头阳光集团，宁波方太、宁波波导、宁波公牛，杭州矽力杰半导体公司等，本次专业指导委员会由浙江大学马皓教授担任主任，浙江理工大学庄红副院长担任副主任，由来自浙江理工大学、浙大城市学院、杭州电子科技大学、浙江师范大学的6名教授，全省20家企业老总、技术总工、人事部经理，其中有优秀校友代表4人组成了阵容强大的专家队伍，为我校电子信息科学与技术专业的人才培养出谋划策。会议期间，来自绍兴杭一电器的胡克飞总经理与工程师一起带着四名绍兴籍学生对美丽的金职院风景进行无人机航拍，鼓励学生学有所成，回家乡工作，服务于家乡的建设。通过走出去请进来，信息工程学院未雨绸缪，对应生源地，建设就业与实习基地，为学生及其家乡企业牵线搭桥，希望学生规划好未来，回报家乡建设。', 'http://info.jhc.cn/2016/1213/c201a73249/page.htm', 'http://info.jhc.cn/_upload/article/images/a3/66/4d60867342999764ccf601c434d5/6b4b585c-91ff-47b5-b949-f3aee82cbbef.jpg|http://info.jhc.cn/_upload/article/images/a3/66/4d60867342999764ccf601c434d5/c3b86d17-4f4e-4ae3-828d-6dd5cc3a02f9.jpg|http://info.jhc.cn/_upload/article/images/a3/66/4d60867342999764ccf601c434d5/fda7160b-1f50-4611-940a-dea26419211c.jpg', '2017-05-17 14:13:11.559789');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (54, '石天星、叶旋、宣翠仙、余红娟', '浙江新闻客户端', '周荃', '2017-01-05 00:00:00', '《浙江新闻客户端》报道我校开设VR、无人机也开专业方向', '从今天起，浙江新闻客户端记者将带大家走近一个个高职“独门”专业。所谓“独门”专业，就是全省只此一家，或为最早，或为特异。高职教育的特点是办学灵活，紧跟市场变化，设置专业的嗅觉十分敏锐。“独门”专业中，反映着时代的飞速变化，也蕴含着地域文化的积淀，既有科技与时尚感并存的新兴专业，也有深植于传统、浸润着工匠精神的古老专业。独门专业，反映的是历史的传承，是时代的呼唤，也是为职业教育的发展倾注心血的人们投身教改的勇敢尝试。记者今天走进第一家学校金华职业技术学院，向大家推介两个高职独门专业：VR和无人机。金华职业技术学院开出了VR和无人机专业！千真万确！有的小伙伴感叹：怎么这么快？要知道，2016年才是全球公认的VR元年，把VR作为专业，要学什么呢？至于无人机，这不是摄影大哥用来航拍的吗？VR有多火？企业忙着找学校培养人才 “令我们郁闷的是，即使开出30万元以上年薪，还是找不到VR相关专业的人才。”2016年11月22日，国内游戏业巨头网龙科技旗下子公司“网龙华渔教育”副总经理高锋与金华职业技术学院正式签约，以订单班的形式合作开展VR专门人才培养。高锋表示，“网龙华渔教育”今后计划与全国300所高校合作培养VR人才，与国家开放大学（原电大系统）达成远程教育方面的合作意向，“与金职院的合作是我们在浙江的基石。”这意味着，金职院信息学院计算机应用技术专业下正式增设一个专业分支——虚拟现实技术方向。这个专业方向将开设多媒体技术应用、C#程序设计、2D动画编程、三维动画制作、Unity3D动画编程以及2D交互媒体制作、3D交互媒体制作等实训课程。通过打造虚拟现实体验馆、虚拟现实实训室、虚拟现实工作室“三位一体”实训中心，引入企业讲座与应用体验进校园、引入企业教学化项目进课程，引入企业研发项目进工作室，校企合力从“技术应用体验、项目课程学习、创新创业实践”3个层面逐步提升学生的新技术应用与创新实践能力。 “要应对虚拟现实发展元年产业发展态势，开启我校虚拟现实相关实训室建设项目、虚拟现实课程与资源建设，和网龙的对接必不可少。”金华职业技术学院教务处处长刘鲁平说。2016年10月，由刘鲁平带队，信息学院院长、计算机应用技术专业的老师们一行多人专程前往福建参观网龙总部的VR产业基地，通过VR/AR沉浸式设备，现场体验了VR在创客教育、旅游资源分发、动车VR训练、家居设计等方面的应用。更早之前，2016年6月，信息学院计算机应用技术专业与上海形拓科技有限公司、科维城（上海）信息科技有限公司共同组织了“对接新技术、虚拟现实技术进校园”校企合作系列活动。金职院的师生在活动现场通过虚拟现实头盔、眼镜等交互设备，体验化工灾难模拟与应急演练、建筑施工模拟、风景区游览、工业仿真装备测试等沉浸式虚拟现实3D交互场景；计算机14级、15级的60余名工作室学生还参与了3D建模项目的企业培训与项目承接，及时跟进虚拟现实3D场景建模基础技能训练。金职院师生在体验虚拟现实场景。 “时不我待。必须探索培养交互媒体制作方向上的人才，进一步提高我校毕业生在U3D开发、3D场景建模等新技术紧缺岗位上的适岗能力，同时为即将实习的同学开拓更多新型实习岗位。”金职院信息工程学院院长陈海荣说。在网龙与金职院签约的同时，“网龙华渔教育”华东地区总监史巍也给金职院的同学们带来了一场名为“VR虚拟世界，未来已来——VR行业发展现状与趋势”的主题讲座。史巍指出，虚拟现实在我国发展势头迅猛、应用非常广泛，急需大量VR策划、U3D开发、全景VR制作、AR开发等工程人才。“希望同学们加强表达沟通、应用写作等职业素养的养成，抓住VR发展契机，尽早对接VR岗位，做好职业规划。”无人机只用来航拍？没那么简单2016年11月22日，在金华职业技术学院第十六届IT科技文化节上，信息工程学院应用电子技术专业开展无人机飞行表演，吸引了众多师生观看与拍照，无人机在预先设定的飞行路线上进行环校园上空全自动飞行，按照设定路线平稳飞回并轻盈降落……金职院信息工程学院大三学生余健斌玩无人机还不到一年，他跟另外两个同学一起在学校的创业一条街上开了一个摄影工作室，每接一次航拍单子能赚1000元，现在每个月月收入超过2万元。“现在金华能做无人机航拍的摄影师很少，价格也普遍比我高，我的差异化竞争策略取得了效果，生意源源不断。”小余笑着说。 “其实，无人机是一个非常新的领域，需要很多专业人才。”陈海荣告诉记者，学习无人机可不仅仅能成为航拍摄影师，在国内，无人机已被广泛用于影视航拍、气象探测、抗震救灾、边防巡查、农林植保、安防监控、勘探与测绘等行业领域。“无人机产业在浙江发展特别快，企业迫切需要人才进行无人机飞行操控、维修维护、应用推广，无人机专业人才在一些公司表现好的，每月月收入接近1万元。”2016年，金职院信息学院应用电子技术专业经过对新兴高新技术产业进行详细调研，确定在原专业建设基础上开设无人机、新能源、医疗电子等专业方向，分流培养，以促进学生高端就业。其中，无人机方向与浙江大学无人机培训基地合作，与省内外数家无人机企业共建无人机驾驶、维修维护、运营推广等岗位的就业基地。应用电子技术专业将“无人机应用技术”列入专业培养方向，从2016届开始招生，开设《无人机原理与系统组成》《无人机遥控装置与驾驶》《无人机装机与调试》等课程；2017年还将投入200万元建设“无人机应用实训室”。计划与卓群航空科技、北京韦加无人机、浙江大学无人机驾驶培训基地等合作，开展课程开发与无人机驾驶培圳项目；与浙江杭一电器有限公司、金华市翼天科技有限公司等多家省内有开发与生产无人机项目的企业开展校企合作研讨，为学生的就业与培养开设无人机应用技术讲座，聘请无人机行业资深驾驶员与航拍达人进行校园试飞与航拍。 “在校相关专业大学生要紧跟时代潮流，不仅要掌握无人机技能，还要具备扎实的专业功底，拥有‘无人机+行业’的复合型能力。”2016年11月底，杭州中汇通航航空科技有限公司总经理戴滨应金职院之邀在信息学院做了题为“无人机行业现状与发展趋势”专题讲座。他认为，“无人机”行业是一个高度综合的行业，不仅因为无人机本身的技术复杂，综合性高，更因为无人机在各行各业的广泛应用。在金华职业技术学院第十六届IT科技文化节上，学生观看无人机飞行表演。另一位主讲嘉宾、绍兴卓群航空科技有限公司总经理李于民则向同学们展示了无人机在电力巡线、农业植保、消防灭火、交通监察等行业领域的应用，并详细介绍了无人机在研发、制造、销售、运营等岗位的就业需求与薪资待遇。他表示，无人机技术逐渐成熟，对研发人员就业需求稳定，学历要求更高，无人机应用推广人员需求量猛增，无人机驾驶培训供不应求，适合高职学生入职发展。新闻链接：http://zj.zjol.com.cn/news?id=525010', 'http://info.jhc.cn/2017/0105/c201a74547/page.htm', 'http://info.jhc.cn/_upload/article/images/66/22/728e21e84d8497af5ac87b1b29da/c9627a9d-bc6f-4483-9fd2-a411c5e0e6e5.jpg|http://info.jhc.cn/_upload/article/images/66/22/728e21e84d8497af5ac87b1b29da/bbfc5abf-bc0a-4bb2-aa3e-3672ab39ed10.png|http://info.jhc.cn/_upload/article/images/66/22/728e21e84d8497af5ac87b1b29da/7c7135c7-cbb1-4864-8d0b-c6a12ccb4e51.png|http://info.jhc.cn/_upload/article/images/66/22/728e21e84d8497af5ac87b1b29da/0d3eaf40-de22-4aff-90de-15a8e753888f.jpg|http://info.jhc.cn/_upload/article/images/66/22/728e21e84d8497af5ac87b1b29da/86873683-2713-4ea9-ba34-683703ea2aac.png|http://info.jhc.cn/_upload/article/images/66/22/728e21e84d8497af5ac87b1b29da/7e3f3521-b3f0-456d-a689-5ab21e0ea5b3.jpg|http://info.jhc.cn/_upload/article/images/66/22/728e21e84d8497af5ac87b1b29da/1905dcfb-2fd4-4f4f-a0d8-5f01a46d63ee.png|http://info.jhc.cn/_upload/article/images/66/22/728e21e84d8497af5ac87b1b29da/b1fa4625-1598-4965-970e-5c1413024f1b.jpg|http://info.jhc.cn/_upload/article/images/66/22/728e21e84d8497af5ac87b1b29da/f09eefe1-5c58-4f4f-a2c9-d57f4e27eaae.png', '2017-05-17 14:13:06.926333');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (55, '金婵娟、丰 辉', '学工办/团委', '周荃', '2017-01-04 00:00:00', '信息团支部荣获全国高校“活力团支部”称号', '近日，由团中央组织开展的2016年全国高校“活力团支部”创建遴选活动结果揭晓，我校信息学院自动化142班团支部以投票环节全省高校排名第一、综合评审环节全省高职院校排名第一的喜人成绩，荣获2016年全国高校“活力团支部”称号。　据悉，该团支部是一个由34名男生和1位男教师组成的支部，秉承“创新、奋进、公益”的建设理念，营造“团结友爱，奋发进取”的活力氛围。在团支部建设中，他们注重完善团支部团员教育评议等相关制度，曾获“校先进团支部”、“校五四红旗团支部”等荣誉。他们勤奋好学，用心书写IT男的工匠精神，两年半以来，获全国大学生技能大赛一等奖5人次，省赛一等奖3人次，二等奖5人次；校三十佳荣誉称号3人次；国家奖学金1人次、浙江省省政府奖学金2人次、国家励志奖学金4人次、三好学生3人次。同时，他们还是一个热爱公益的集体，积极参加家电维修服务、护航G20安保服务、无偿献血等公益活动，受到了中新网、金华日报、金华晚报等多家媒体报道。据了解，为贯彻落实《共青团中央改革方案》有关精神，深入实施高校基层团支部“活力提升”工程，团中央学校部于2016年秋季开展高校“活力团支部”创建遴选活动。活动面向全国各高校（含高职院校、民办高校、独立学院）的学生团支部（总支），包括班级、社团、活动项目、实验室、宿舍等各类学生团支部（总支）。经过校内创建、资格审核、省级遴选、网络投票和专家评审等环节，共评选产生1000个团支部为2016年全国高校“活力团支部”，浙江省有37个团支部获此殊荣。', 'http://info.jhc.cn/2017/0104/c201a74397/page.htm', 'http://info.jhc.cn/_upload/article/images/4d/db/851ca94044efa93e0a54d82abb74/15f28382-b913-4244-b898-a0dd67488a22.jpg|http://info.jhc.cn/_upload/article/images/4d/db/851ca94044efa93e0a54d82abb74/41f20986-efc5-469c-a568-c958c8dae4e7.jpg', '2017-05-17 14:13:06.948281');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (56, '花有清', '教科办', '周荃', '2016-12-30 00:00:00', '信息学院多举措细化毕业教学环节管理', '随着2017届毕业生即将进入人才培养方案的最后一个环节——毕业教学环节，信息学院根据教育部、财政部、人力资源和社会保障部联合下发的《职业学校学生实习管理规定》的相关精神，采取了一些新措施，以强化2017届毕业生毕业教学环节的管理。一是信息学院结合学校的教学质量管理系统，实现了毕业教学环节的信息化管理，学生的顶岗实习单位信息、企业指导教师信息、顶岗实习周记、顶岗实习总结及顶岗实习成果都要求在学校的教学质量管理系统中完成；二是对所有的2017届毕业生交了实习责任险；三是对2017届毕业生进行了指纹考勤管理，每天公布当天学生的考勤情况；四是强化毕业设计选题的管理，对部分来自工程实际或科研项目的课题，在计算教师工作量时给予一定的政策倾斜；五是要求每位指导教师根据学生所写的顶岗实习周记，每周推送一篇优秀顶岗实习周记，并在学院微信公众号上进行推送。毕业教学环节是检验学生综合素质、考验教师过程管理能力的重要一环，信息学院多举措进一步细化过程管理，把具体工作落实到各负责人、毕业设计指导老师及相关学生，未雨绸缪，确保毕业教学环节顺利开展。', 'http://info.jhc.cn/2016/1230/c201a74133/page.htm', 'http://info.jhc.cn/_upload/article/images/f3/eb/65702f874e4f867e77fc96e6f856/fa73df78-50d5-4597-83f3-d6d24d61bd22.jpg|http://info.jhc.cn/_upload/article/images/f3/eb/65702f874e4f867e77fc96e6f856/21257815-c09d-444f-9ab3-928f32d76e27.jpg', '2017-05-17 14:13:06.970456');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (57, '周荃', '办公室', '周荃', '2016-12-27 00:00:00', '信息学院召开下半年廉情分析会', '为进一步贯彻落实学校党委文件精神，深入推进学院党风廉政建设，12月26日下午，信息学院召开下半年廉情分析会，院党风廉政建设领导小组成员、三办一部及实训中心负责人参会。会上，各部门负责人结合工作实际针对岗位职责存在的风险点作了深入剖析，分析查找了各类经费使用、财务报销、职称评定、公务接待、耗材采购管理、评奖评优等各环节的隐患风险，并提出了相关材料要及时移交学院存档、各条线规范经费报销审批流程等具体举措。院纪检委员贾美倩则对下一阶段学院党风廉政建设工作作了部署，学习传达了省委十三届十次全会精神。目前，学院完成了科职及以下岗位廉政风险点及防范措施排查，接下来，学院将继续推进制度修订和工作流程完善，把党风廉政建设与学院各项工作有机融合，营造风清气正的良好氛围。', 'http://info.jhc.cn/2016/1227/c201a73980/page.htm', NULL, '2017-05-17 14:13:11.451292');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (58, '蔡金晶、周荃', '办公室', '周荃', '2016-12-22 00:00:00', '英语怎么学 大师来解答', '19日，大学生英语口语及职业生涯规划讲师，浙江重点初高中英语教师教研培训特邀讲师，慧美网络科技有限公司执行董事、副总裁，国英教育创办人郭兴祥Kevin老师应邀来我校作“大学双语精英人才是这样炼成的”专题讲座，信息工程学院院领导、中新合作班和本科班的师生们纷纷慕名前来聆听。郭老师说“我对一切来真的，一切才会真的来”，并分享了自己的英语学习之路：在大学期间克服一切内外部压力坚持学习英语，吸引和带动三百多名同学每天早起进行英语晨读、晚读，到大二实现了经济独立，大三成为一家教育培训集团的教学总监，大四创立国英教育。谈到“为什么要学习英语”，郭老师建议把英语当做一项语言工具来掌握，在学会了这门交流工具的同时顺便把英语考试通过不是很棒？英语学习就如同我们学会中文的过程，听力能力要先从声音模仿开始，模仿5-8首英文歌来提高语感，选择一部适合自己词汇量的英文电影并截取其中1-2分钟的片段反复模仿录音直至能够脱口而出。阅读材料要从“读弱点、读兴趣点、读热点”出发进行选择，从阅读中增加自己的词汇量，培养英语学习环境，用英语解决实际的生活问题。通过两个小时的现场体验，师生们都觉得受益匪浅，掌声此起彼伏。据悉，信息学院将以此活动为突破口，陆续开展新西兰周等系列师生英语学习活动，营造国际化学习生活氛围，让更多的学生努力尝试，成为能说一口流利的英语的双语精英人才。', 'http://info.jhc.cn/2016/1222/c201a73782/page.htm', 'http://info.jhc.cn/_upload/article/images/c9/a6/a61a9e6048b5b4fc090f1dbffec0/8d2b626b-6d36-4157-a1bd-99ab0312807a.jpg', '2017-05-17 14:13:11.515294');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (61, '苏红富、王静', '通信与网络工程部', '周荃', '2016-12-13 00:00:00', '物联网技术在养殖业的应用调研', '12月10日，通信与网络工程部一行3人，到杭州慧牧科技有限公司进行养殖物联网应用调研，并参观了公司在桐乡的现代化养殖基地。在杭州慧牧科技，市场总监陆家祥介绍了该公司在养殖物联网方面的研究与行业应用情况。主要涉及以下几个方面：一、环境监控，采集主场养殖厂的温度、湿度、氨气等环境数据，并根据所采数据控制风机、地暖、灯光等进行环境调控；二，饲料喂养系统，根据群养、独栏、保育等不同方式和阶段采用个性化喂养系统，系统能进行自动送料、加湿搅拌、清洗、进食检测、体重检测、身份检测等功能，能记录每头猪的进食、生长情况；三、身份识别系统，采用自主RFID芯片植入猪体内，进行猪身份识别并检测猪生命体征参数；四、视频监控系统，实时监控猪厂运行情况；五、养殖分析管理平台，云端显示猪厂环境、进食、运行情况，自主分析猪厂、分栏、单猪成长曲线等。在桐乡华腾牧业，基地负责人带老师们参观了慧牧科技物联网系统在真实养猪厂的应用运行情况，感受科技给传统农业养殖带来的重大变革。在参观考察过程中，双方就物联网行业应用、技术发展、合作意向等方面进行了深入探讨。', 'http://info.jhc.cn/2016/1213/c201a73242/page.htm', 'http://info.jhc.cn/_upload/article/images/3d/d2/c1f5764c4a679c6af8bd64a02de2/59ab47a6-6e4a-4507-a0d4-b1f5a8348fa6.jpg', '2017-05-17 14:13:11.582062');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (62, '徐露诗', '学工办/团委', '周荃', '2016-12-13 00:00:00', '展现班级风采·我们是快乐的伐木累', '“朋友的情谊呀比天还高比地还辽阔，那些岁月我们一定会记得”，一曲高亢悦耳的《好兄弟》的伴奏下，台上四个大男生肩搭着肩，一起投入地演唱着，台下的观众也被他们一起带动为他们轻轻地合着。近日，信息学院举办了首届别开生面的班服设计比赛，分别有自动化161、计算机161等7个新生班级参加。在比赛过程中，同学们淋漓尽致地发挥了自己的创意，有的班级将亲爱的班主任动漫化并设计在了班服的正中央，象征着对班主任的尊敬与喜爱；有的班级则把自己的专业课C语言中的“C”符号印在了班服上，并体现了计算机和鼠标的形象，充分展示了信息学子的专业特色；还有的班级不走寻常路，两人配合，一人介绍设计创意，另一人在黑板上直接画出班服样式，让观众们感受到了他们的用心。无论是怎样的设计，都体现了同学们对班集体深深的归属感。最终，计算机163班以独到的创意以及精彩的表现，获得了本次比赛的第一名，班服设计大赛在掌声中落下了帷幕。', 'http://info.jhc.cn/2016/1213/c201a73240/page.htm', 'http://info.jhc.cn/_upload/article/images/38/9f/c64be88b4257b2d235cb9a8d1503/0e02bc9f-7310-4806-a986-207929f0646e.jpg', '2017-05-17 14:13:11.604335');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (63, '周荃', '办公室', '周荃', '2016-12-09 00:00:00', '我院顺利举办优秀教师代表先进事迹报告会第二组活动', '12月8日下午，我校优秀教师代表先进事迹报告会第二场拉开帷幕。商务学院童芳、机电学院赵娜、经管学院陈妍等三位优秀教师代表在报告会上先后发言，信息、机电、公共学院教职工及学生代表共300余人聆听了报告会。报告会由信息学院副院长王成福主持。  会议现场  报告中，“学生最喜欢的老师”童芳与大家分享了自己多年来致力于“翻转课堂”创新英语教学，帮助学生把英语学习变成一种生活习惯的点滴感悟；赵娜讲述了和学生一同成立专业社团悉心耕耘，并同步开展微课与翻转课堂，让学生在各类竞赛中绽放光彩；陈妍从“以职业需求为导向开展教学”、“以育人为本为理念鼓舞学生”、“以积极乐观态度关爱学生”三个角度分享了她陪伴学生走过顶岗实习、兼职创业的成长故事，努力践行 “勤勤恳恳教书，踏踏实实育人”的座右铭。教师代表们的事迹平凡却真实，饱含着对学生的关怀、对教育事业的热爱，深深感染了在场的每一位听众，激起了大家的强烈共鸣。据悉，为大力宣传优秀教师的先进事迹，激励广大教师以先进为榜样，进一步深化学校师德师风建设，在全校教师中掀起创先争优热潮，学校近期组织省“三育人”先进个人、省高校优秀教师、第六届“学生最喜欢的老师”的优秀教师代表分5场向全校教职工作先进事迹报告。', 'http://info.jhc.cn/2016/1209/c201a73184/page.htm', 'http://info.jhc.cn/_upload/article/images/5f/f0/f1a6da294f5d83fbcc045bbe8b31/6af1e2d4-60fd-4781-a77d-01fccf1fe18d.jpg', '2017-05-17 14:13:11.626519');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (64, '苏红富、王静', '信息工程学院', '周荃', '2016-12-08 00:00:00', '到武义看物联网改变农业生产', '12月7日，通信与网络工程部4位教师带领6位物联网创新班的同学到武义调研，考察物联网技术在农业领域的实际应用。在武义王宅镇四八店村水果专业合作社负责人潘勋剑的水果基地，种植着无花果和桑椹两种水果，总共50多亩。基地日常管理只有1人，武器就是潘勋剑自主开发的一套农业物联网管理系统。该系统能对土壤及环境温度进行实时监测；能根据环境变化情况自动对果树进行灌溉、施肥；可以实时监测风速，当风速过高（如8级）时，会自动开启大棚外的3套高空喷灌设备迅速向棚顶喷水，薄膜遇水后重量增加，从而让大棚自身的防风能力得以加强；采用视频监控系统对基地实时监控，查看基地运行情况并扑捉水果偷盗人员图像等。考察过程中，潘勋剑与师生一道进行了友好沟通，并答应作为我校物联网专业学生考察基地，还探讨了双方在科研合作方面的一些想法。据悉，潘勋剑的水果基地是省农科院数字农业的科研基地之一，是全省最早一批运用物联网产品的农业生产基地之一。', 'http://info.jhc.cn/2016/1208/c201a73112/page.htm', 'http://info.jhc.cn/_upload/article/images/4b/da/c9bea9b74b1f972bdf6b179983a4/6b7fab52-1346-486c-a7f4-7999f4af1303.jpg', '2017-05-17 14:13:11.648775');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (65, '何碧峰', '学工办/团委', '周荃', '2016-12-06 00:00:00', '“衣份爱心，绿色行动”捐衣活动', '赠人玫瑰，手留余香，捐一件衣物，献一片爱心！近日，信息学院志愿者举办了“衣份爱心，绿色行动”捐衣活动，为西部偏远地区的贫困留守儿童送去暖意，让他们过一个温暖的冬天。志愿者们走进学生寝室，礼貌地询问同学们是否有闲置的旧衣物，并邀请同学们加入到捐衣物献爱心的行列。了解到活动的意图后，同学们纷纷拿出自己闲置不穿的旧衣物，短短几小时，志愿者们就收集到棉衣棉裤等三大箱。“我们希望多做一些力所能及的事，温暖那些需要帮助的人，把爱传递到他们身边！”志愿者丁港纳说道。据悉，此次捐衣活动由信息学院联合金华市红十字协会共同开展，收集到的衣物由志愿者统一登记整理后送至红十字协会进行后续处理，最终送到孩子们手中。', 'http://info.jhc.cn/2016/1206/c201a73031/page.htm', 'http://info.jhc.cn/_upload/article/images/4c/5b/a5e138b0444493ce0e535ad264f6/4ba7633b-f07b-4b89-b1cc-b17fc709bff4.jpg', '2017-05-17 14:13:11.671002');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (249, '周荃', '', '系统管理员', '2015-12-29 00:00:00', '信息工程学院电气自动化技术专业建设指导委员会会议顺利召开', '为在工业4.0背景下进一步推进专业内涵建设，提升人才培养质量，探索校企合作、工学结合的高职人才培养模式，12月25日下午学院召开电气自动化专业2015级人才培养方案研讨会。出席会议的有金华市汉生机电设备制造有限公司副总经理舒柏和高级工程师，浙江连成环保科技有限公司人事部白静经理，金华东盛电子科技有限公司吕中秀高级工程师、金华华东环保设备有限公司工程部蒋超经理、徐州上若科技有限公司刘有来高级工程师，以及信息工程学院副院长赵敏笑、电气自动化项目部全体教师。   会上，电气自动化专业主任马汝星首先对《2015级电气自动化专业人才培养方案》进行了解读，对方案中职业领域、岗位定位、工作任务和职业能力分解及相关课程建设进行了深入细致的讲解。然后围绕工业4.0和数字化工厂介绍了专业实验室建设规划及2015年机器人实训室的建设方案。随后专业指导委员会委员对人才培养方案内容、实验室建设计划展开了讨论。专业指导委员会各委员高度肯定了电气自动化专业2015级人才培养方案的完整性和科学性，指出课程设置与教学内容全面，以公共基础课为依托，能将通用能力和基本素质融入到培养方案中。同时也对方案修订和实验室建设提出了三点建议：一是课程设置中缺少以智能制造为基础的检测和视觉系统设计相关课程；二是与智能制造相关的工业现场总线方面的教学内容偏少；三是机器人实训室的建设能够充分考虑到对地方企业的示范带动作用。为了培养适应“中国制造2025”和工业4.0形势下的高技能人才，电气自动化技术专业通过大量的调研，修订了2015级专业人才培养方案，适应“分流分层”专业人才培养模式的改革。提出了围绕“机器换人、两化融合”的2016-2019年实验室建设方案。会上，各位专家结合自身企业特点，认真审议了电气自动化专业人才培养方案和电气自动化技术专业未来三年的实验室建设方案，并提出了很好的意见。建议电气自动化技术专业人才培养应更好地智能制造时代，对接工业4.0和中国制造2025，强化学生综合职业素质培养。电气自动化项目部将按照学校的总体要求，认真吸纳专家的意见和建议，完善电气自动化专业人才培养方案和实验室建设方案，提高专业教学质量。学院副院长赵敏笑在会上指出，此次研讨会的召开对进一步优化、提升和完善我专业人才培养方案，提高实验室建设质量，全面提升人才培养水平具有重要的意义。会后，电气自动化专业将认真梳理与会专家提出的建议和意见，紧紧围绕工业4.0和“中国制造2025”的大环境，以提升教育质量为中心，修订好本专业人才培养方案和实验室建设计划。   （电气自动化工程部 马汝星）', 'http://info.jhc.cn/2015/1229/c201a54614/page.htm', 'http://info.jhc.cn/_upload/article/images/c5/1d/abfda007444f8b7488743706487a/15ce40e1-a7a5-41bf-8b66-b6cf1c6e6591.jpg', '2017-05-17 14:14:17.902465');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (66, '何碧峰', '学工办/团委', '周荃', '2016-12-01 00:00:00', '无危则安，无损则全', '“学生事，无小事”。为了做好安全工作，确保学生的健康快乐成长，近日，信息工程学院邀请了我校保卫处处技防科科长蒋加陆作了主题为“无危则安，无损则全”的安全知识讲座，全院200余名学生参加。蒋科长从交通安全、人身安全、财产安全、消防安全等多个方面透过一件件生动的现实案例给同学们普及了相关知识。他认为，安全是伴随人一辈子的大事，要时刻关注，遇到安全突发状况要保持冷静，在发生问题时要及时报告，拨打校园110，避免慌张错乱造成伤害进一步加重。在讲到消防安全时，蒋科长把我校发生的一些火灾事故的残骸拿上讲台，这些支离破碎的事故残骸有电水壶、热得快、电吹风，暖手宝，甚至还有饮水机。同学们不禁感概，生活中稀松平常的一个小家电，如果不正确使用，就有可能给自己带来安全隐患，给寝室、给学校造成安全事故。一个个贴合实际的真实校园案例让同学们揪心不已，讲座后同学们直呼：“安全太重要了，以后一定要随时注意，时刻敲响安全警钟！”', 'http://info.jhc.cn/2016/1201/c201a72545/page.htm', 'http://info.jhc.cn/_upload/article/images/78/fb/be7b8434433da8448fc9e3803a8d/fa440176-7c65-47ea-8b00-c10a5b6355df.jpg', '2017-05-17 14:13:11.711068');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (67, '徐露诗', '学工办/团委', '周荃', '2016-12-01 00:00:00', '面对挫折 保持快乐', '“对于强者来说，挫折就像垫脚石，可以站得更高；而对于弱者，挫折就是绊脚石，让他一蹶不振”。近日，信息学院邀请了校心理健康中心曲倩老师，面向16级学生开展了以“面对挫折”为主题的心理健康教育讲座。在讲座中，曲倩老师形象地将应对挫折三种方式分别比喻为胡萝卜、鸡蛋以及咖啡粉。她介绍道：当这三种事物遇到热水以后，原先坚硬的胡萝卜越煮越软，脆弱易碎的鸡蛋却变得牢固了不少，而咖啡粉却融于热水，改变了水的味道，变成了一杯真正香醇的咖啡。那么，同学们你们想成为哪一种呢？同学们思考后，纷纷表示想做“咖啡”。接着，曲老师重点阐释了造成大学生挫败感的来源，引导同学们积极调整面对挫折的态度，由原来的排斥抗拒，逐渐转变为接纳利用挫折，学会在失败中成长。最后，曲老师放映一段视频《缺失的那一角》，视频中主角不断地在寻觅自己缺少的一部分，熬过了重重困难，经历了种种挫折，最终在收获与失去了明白了缺憾的重要性，引发了学生思考如何在实际生活中寻找快乐，保持乐观。曲老师凭借形象的比喻、平实的语言、生动的案例，与同学们的亲身感受产生了强烈的共鸣，赢的了大家的阵阵掌声。“我觉得这位老师说得很好，进入大学以后，我有时也感到迷茫和无所适从，听了这个讲座后，我明白失败并没有那么可怕，我会开始换一种角度去看待挫折”一位同学听完讲座这样说道。', 'http://info.jhc.cn/2016/1201/c201a72534/page.htm', NULL, '2017-05-17 14:13:11.748882');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (68, '周荃', '办公室', '周荃', '2016-12-01 00:00:00', '常州信息职业技术学院网络与通信工程学院来我院考察交流', '11月29日上午，常州信息职业技术学院网络与通信工程学院院长杨诚一行4人来我院考察交流。学院院长，通信与网络工程部、信息安全创新实验室负责人和相关教师出席了交流活动。座谈中，陈海荣代表学院对客人的来访表示欢迎，并简要介绍了学院的发展状况。宾主双方围绕专业建设、信息安全与管理专业国家教学资源库项目合作等内容进行了研讨。', 'http://info.jhc.cn/2016/1201/c201a72537/page.htm', NULL, '2017-05-17 14:13:11.770987');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (69, '金璐', '金华日报', '周荃', '2016-12-01 00:00:00', '《金华日报》报道我校"VR+教育"人才培养', '游戏业巨头“网龙科技”与我市合开订单班“VR+教育”第一批人才将由金华造金璐金职院学生快乐体验VR产品 时宽兵 摄今年被称为“VR元年”。上周，我国游戏业巨头网龙科技来到我市，与金职院签约，将以订单班的形式培养一批VR人才，作为大规模进军VR领域的基础性措施之一。　　“网龙科技”签约金职院　　“想象一下，你不仅是和朋友们在线分享美好的时刻，而是分享所有的经历与冒险。”这是Facebook的CEO马克·扎克伯格在2014年3月宣布以20亿美元收购Oculus公司时，对后者的主打产品Rift VR所做的美好构想。　　全球的VR热潮已经点燃。无论从进入该行业的企业数量，还是从社会资本投资规模，或者从媒体报道的篇幅频率来看，VR正在成为新宠。　　在硬件方面，VR头盔、VR摄像装置等一款接着一款发布；在内容制作方面，《纽约时报》等媒体机构已开始尝试使用VR技术进行报道，圣丹斯电影节上参赛的虚拟现实影片今年硬件达到33部。　　在我国，百度在2015年推出VR频道，阿里巴巴收购的优酷也开始支持360度全景视频，腾讯发布了全套虚拟现实领域战略，乐视推出乐视VR一体机和头盔，小米开始筹建探索实验室投入VR领域，华谊兄弟投资了VR硬件生产商暴风魔镜……可以期待不久的将来，虚拟现实应用会在文化产业各领域迅速铺开。　　由于视觉冲击是VR的显著优势之一，所以VR在游戏、电影等视觉消费领域率先获得认可和欢迎。我国游戏业巨头“网龙科技”（代表作“幻灵游侠”“魔域”等）就是弄潮儿之一。　　在游戏厂商中，“网龙”一向以跨界方面嗅觉灵敏著称。他们曾有高价把17173卖给搜狐、把91无线卖给百度的历史。涉足VR领域后，除建起全国最大VR基地外，“网龙”还组建了面向教育业务的子品牌“网龙华渔教育”。在今年举行的中国教育装备展中展示了101 VR沉浸教室、101 VR创客教室、101智慧课堂解决方案、VR全景体验等多项与VR相关的新产品。　　上周，“网龙华渔教育”与金职院信息工程学院签约，正式合作开展VR专门人才培养。　　“VR+教育”的人才风口将至　　代表“网龙华渔教育”来金签约的是副总经理高锋。据他介绍，“网龙”很早就开始研究VR和AR的技术发展，并应用于游戏领域，但“网龙”要做的并非某一类简单的VR业务，而是搭建VR开放平台，构建VR生态圈，实现全产业链的持续发展。　　目前，“网龙”已将VR技术应用于游戏、教育、家居、旅游、娱乐等领域，其中教育领域的应用是未来战略的重点部分。比如，今年东盟十国外长会的时候，他们打造的VR动车实训的产品就被有关部门带去，作为展示内容之一。　　“在这个过程中，我们最郁闷的是，即使开出30万元以上的年薪，还是找不到VR相关专业的人才。”高锋说，因为这个领域太新了，所以他们只能自己培养，提供课程和院校合作，招收相关专业的学生进入VR领域，并保证解决部分学生的就业。在他们的计划中，将在全国找300所高校合作，目前已经在福建有合作高校，另外与国家开放大学（原电大系统）达成远程教育方面的合作，还跟人力社保部在合作搞专业的VR人才初中高级的技能证书体系。“与金职院的合作是我们在整个浙江乃至东南的基石。”　　学VR的学生未来可以从事哪些岗位？高锋回答说，“网龙”打造的VR开放平台涉及VR内容生产、第三方内容制作、多行业应用解决方案、产品分发、人才输出，包含硬件、软件、内容、资讯、分发、体验、人才培训、外包等全产业链，目前各行各业都可以实现“VR+”，很多行业都是万亿级的市场。在其中，将可能创造出更多的新工种和就业机会———多年前搜索引擎行业创造出了“搜索引擎优化”这个行业，未来时代，VR编辑师、美化师，可能仅仅是开始……VR专业引发95后学生强烈兴趣　　“我觉得，成为一名VR游戏设计师会是很酷的事！”金职院计算机应用技术专业学生刘潇禹说，VR是一个先进的技术，但是上手难度不是很高，VR游戏在他眼中最大的优势就是带入感。本学期他已学习了为游戏3D建模的软件课程，未来进入VR领域，布置游戏场景、游戏人物，用代码编写游戏程序等，都是他喜欢的工作。　　“我们原本设置了‘网页开发’和‘交互媒体’两个专业方向，今年除了两个学生外，其他人都要求学‘交互媒体’，因此另一个方向的班没有开起来。”该专业老师宣翠仙说，这两个方向，是根据学生的就业走向定的。金华有不少信息产业园，程序员供不应求，因此前者原本选的人不少；然而，近年来学生们对于后者的兴趣在增加，更多地选择移动互联网、手机网页设计等工作。VR其实就是“交互媒体”的延伸，课程上与学生们之前学的紧密相连，不存在转专业方向的难度。而且这样新鲜的事物，很符合95后大学生的口味，因此虽然大三学生并不涉及“VR订单班”培养，但“网龙”的工程师来校讲座后，很多应届毕业生都对进入VR领域产生了明显兴趣。　　宣翠仙说，金职院信息工程学院本身就是校优秀专业，师资实力强。虽然根据教育部的专业申请规则，VR目前还不能办成一个新专业，但可以先设置“虚拟现实技术”专业方向，满足学生的需求。虽然暂时还没有专门的VR老师，但在企业的帮助下，他们有信心培养出我国“VR+”领域第一批师资。　　助力金华打造“东方VR之都”　　此次合作的促成者是金华市政府。前段时间，有一个政府方面的参观团前往福建，到了“网龙”总部参观，在了解到对方的人才需求后，推荐了金职院信息工程学院。在学校方面去福建考察后，双方一拍即合，最终签约。　　这批学生毕业后，将给我市建设“东方VR之都”带来人才优势。近年来，金华的信息经济产业一直备受瞩目，统计数据显示，截至2015年底，金华信息经济产业规模超2000亿元，全市有电子商务企业4.3万家，信息软件企业3840家，网商30多万户，全市共有各类信息经济园区62个，信息经济总量规模多年稳居全省第二。　　高锋说，之前对于金华互联网方面的产业热度就有所耳闻，听说过金华在电子商务、网红直播等方面都走在了全国前头。他们对金华政府的印象也非常好，接下来可能会有更多合作。新闻链接：http://epaper.jhnews.com.cn/site1/jhrb/html/2016-12/01/content_1779783.htm', 'http://info.jhc.cn/2016/1201/c201a72535/page.htm', 'http://info.jhc.cn/_upload/article/images/59/fc/c5d36d544211818c7cbc51284979/3821186e-79fc-4bcb-87ed-0797831e5107.jpg', '2017-05-17 14:13:11.793111');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (70, '陈晓龙', '信息化事业部', '周荃', '2016-11-30 00:00:00', '信息工程学院3名教师参加金华市第三批“百名博士入企计划”', '2016年11月29日，金华市金东区第3批“百博入企”对接会在金华市文化中心302会议室举行。本次“百博入企”活动共吸引了上海交大、浙江大学、浙江工业大学等院校的博士、教授到会，并当场与金东区相关企业签署入企协议。信息工程学院王成福教授、陈晓龙博士也分别与金东区浙江大维高新技术股份有限公司和金华致维科技信息有限公司签署了博士入企业协议，并就下一阶段的产学研工作展开讨论。包括此前我院陈海荣博士教授与婺城区天下谱局金华文化股份有限公司已经签署的博士入企协议，我院已经有3位老师参与了金华市 第三批“百博入企”计划。据悉，金华市“百博入企”计划是深入实施人才强市战略，推进创新驱动发展的重要举措，是促进高校院所和我市企业共享人才资源、科研成果的具体行动，有效推进高校、园区和企业产学研合作。', 'http://info.jhc.cn/2016/1130/c201a72415/page.htm', NULL, '2017-05-17 14:13:11.815612');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (71, '何碧峰', '学工办/团委', '周荃', '2016-11-29 00:00:00', '消防演练，为暖冬保驾护航', '为进一步提高全院师生的消防安全意识和应急自救能力，近日，信息工程学院举行了寝室消防应急疏散演练。为了实施好这次演练，学院制定了详细的消防疏散演练方案，对各班逃离的路线及每位配班老师的职责、每层楼梯口的值班老师等内容都作了明确的规定。演练开始前，演练负责人向学生介绍了简单的逃生注意事项和方法。12时15分，随着哨声响起，全院参演200多名学生在各班老师的指引下，捂住口鼻，弯着身体快速有序地按照既定的逃生路线撤离到寝室楼外空地处，从警报声响起到最后一名学生撤离到广场，仅用时1分30秒。学生安全撤离到空地后，各班迅速检查人数，确保每一名学生都已安全撤离。消防疏散演练活动圆满结束，再次增强了广大师生消防常识和消防自救本领，提高学校应对突发事件的应急能力，更是为了带动家庭、倡导全社会要时刻牢记“消防安全不可疏忽大意”。', 'http://info.jhc.cn/2016/1129/c201a72343/page.htm', 'http://info.jhc.cn/_upload/article/images/49/fb/8b655af84e538e969ae64b81a49b/82e6baf0-f831-4fa1-9603-50c894a10ae0.jpg|http://info.jhc.cn/_upload/article/images/49/fb/8b655af84e538e969ae64b81a49b/0f2632f1-e531-41af-b988-6913bf3a2068.jpg', '2017-05-17 14:13:15.955133');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (100, '徐华丹', '教科办', '周荃', '2016-10-28 00:00:00', '信息学院举办青年教师助讲培养考核', '近期，信息学院由分管领导、高级职务教师、督导组成的考评团对青年教师进行了试讲考评。每位教师经过15分钟的说课和25分钟的授课，充分展示了他们良好的教育教学素养。在说课时从说学生、说教学内容、说教法、说效果入手，有理有据，设计出一节完美的教学内容。在授课时运用多种教学方法，丰富的教学媒体，突出教学重点，达到教学效果。经过1-2年的学习发展，我院的青年教师都已经成长为合格的教育工作者和教学工作者。青年教师们表态：今后还将继续努力学习，成为优秀的教育者，为学校、为学生服务。', 'http://info.jhc.cn/2016/1028/c201a70918/page.htm', 'http://info.jhc.cn/_upload/article/images/75/0b/560f3c1941dea1c5785c33b3cb86/6a2c7237-1d95-47f0-84c5-826411b9a5ee.jpg', '2017-05-17 14:13:25.118751');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (72, '周荃', '办公室', '周荃', '2016-11-29 00:00:00', '扬红色文化 做合格党员', '为扎实推进学院“两学一做”学习教育，弘扬雨花英烈精神，坚定共产主义理想信念，11月26日，信息工程学院党委组织全体教职工党员来到南京雨花台烈士陵园、南京中山陵等爱国主义教育基地参观学习。全体党员在雨花台烈士纪念碑前瞻仰革命先烈，共唱国歌。随后参观了《弘扬雨花英烈精神，做合格共产党员——“两学一做”学习教育专题展》和“正气乾坤”廉政教育展区。每到一处展板前，大家都会驻足倾听。大量珍贵的文史资料透过声、光、图片、实物等技术手段回顾着先辈们为理想信念而奋斗的革命故事，深深地牵动着每一位党员同志的心。第二站，全体党员在党委书记的带领下谒拜了中山陵，纪念孙中山先生诞辰150周年，缅怀孙中山先生为民族独立、社会进步、人民幸福建立的不朽功勋。党员同志们席地而坐，在博爱坊前开展“两学一做”学习研讨，结合十八届六中全会精神、《关于新形势下党内政治生活的若干准则》、《中国共产党党内监督条例》等内容对照学习。参观结束后，党员同志们纷纷表示南京之行受益颇丰，要在先辈们的事迹中汲取能量，自觉加强党性锤炼，继承和发扬爱国为民的精神。信息学院党委将以此次活动为契机，弘扬红色文化，传承革命精神，建设一支强大的党员队伍。', 'http://info.jhc.cn/2016/1129/c201a72322/page.htm', 'http://info.jhc.cn/_upload/article/images/ad/a1/193f4fb047c484c1adbf2334fbec/43a37178-e2ad-4e32-91d0-d01bc216a0b2.jpg|http://info.jhc.cn/_upload/article/images/ad/a1/193f4fb047c484c1adbf2334fbec/0dd36c93-27c3-45f1-93ff-7e157d37ff11.jpg|http://info.jhc.cn/_upload/article/images/ad/a1/193f4fb047c484c1adbf2334fbec/6b9cbc4a-004a-4855-8f90-5e8f963e06aa.jpg|http://info.jhc.cn/_upload/article/images/ad/a1/193f4fb047c484c1adbf2334fbec/9c9e3705-9a06-4779-a8f5-56996b52407c.jpg', '2017-05-17 14:13:16.016322');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (73, '钱靖', '学工办/团委', '周荃', '2016-11-25 00:00:00', '信息工程学院举办2016年电子信息类专场招聘会', '11月19日，信息工程学院电子信息类专场招聘会在大学生活动中心广场顺利举办。此次招聘会得到了企业与毕业生的高度重视，共有42家招聘企业进场招聘，岗位需求量达到700多人，满足广大毕业生的需求。此次招聘会的成功举办不仅使同学们得到了充分表现自我的机会，让校园更加充满活力和朝气，而且让同学们认清了自己的不足，在以后的工作和生活中有目的的培养自己的能力，完善自我。信息工程学院也将有计划、有目标的开展好毕业生就业工作，抓住校园招聘会的机会，为毕业生做好就业服务工作。', 'http://info.jhc.cn/2016/1125/c201a72178/page.htm', 'http://info.jhc.cn/_upload/article/images/b7/0d/a0ded71b4118963429679e3fc90c/8f122055-2e32-4b62-94ee-82da21e8ee6e.jpg', '2017-05-17 14:13:16.038053');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (74, '徐华丹', '教科办', '周荃', '2016-11-24 00:00:00', '信息学院进行16年度“六个一批”教学创新项目验收', '11月23日，信息学院对2016年度“六个一批”教学创新项目通过PPT汇报的方式进行验收。各位项目负责人围绕验收标准，从课程定位、创新项目设计、项目实施、项目资源建设、项目实施以及项目实施效果等方面进行了汇报。验收组对汇报提出了改进建议：汇报内容的组织；注重创新、突出特色。据悉，由学院领导和专业主任组成的验收组成员按照“对照标准，全面检查；注重实施，边建边用；个性创新，体现成果。”的验收原则对每门课程进行验收。验收将在Moodle平台上进行，并采取听取每门课PPT汇报（说课）、资料审阅、随堂听课、说课评价、成果评价、学生满意度调查等相结合的验收方式。', 'http://info.jhc.cn/2016/1124/c201a72088/page.htm', 'http://info.jhc.cn/_upload/article/images/49/d2/04f49742484d9062d46ce5aa363d/b5561697-4236-4e2e-8eff-0b3adafe20d6.jpg', '2017-05-17 14:13:16.060442');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (75, '徐露诗', '学工办/团委', '周荃', '2016-11-24 00:00:00', '从“心”沟通 与学生对话', '“请问当你蹲着与对方说话时，你是什么感受”近日，信息学院举办以“人际沟通能力”为主题的教师团体辅导，通过新形式切实提高教师在实际工作中与学生的沟通能力。据悉，本次心理团体辅导邀请到了医学院副院长、具有资深心理咨询经验的吴一玲老师作为主持人。吴老师用一场“大风吹”的游戏拉开了本次团辅的序幕，教师们积极地参与到热身游戏中，充分地调动了情绪。接着，吴老师将各个老师分为AB两人一小组，通过三个不同实景模拟让教师们切身感受到了影响沟通效果的要素。居高临下，A老师站在椅子上保持不说话，B老师蹲着不断对A老师沟通；123米交流，保持三米以外的沟通距离，随着交流的深入，两人逐渐把距离缩短；背对背聊天，AB两位老师背对背聊天，不能转身。三个游戏虽然简单，但是在场的老师分享时，都一致感受到了在沟通时非语言交流的重要性。然后，吴老师进一步让教师们根据自身实际工作生活中的沟通表现，如距离、声调、表情、动作等细节方面为自己打分，老师们活动中不断进行反思，了解到了平日在沟通方面的不足，并且有针对性加以改善。实景模拟后，吴老师请各个小组代表分享亲身感受，一位老师这样说道：当我蹲在地上的时候我想到了我的学生，当他们与我们沟通时，也一直是仰望的感觉，未来我在与他们沟通时，也要尝试着蹲下来，和他们保持平等的姿态。', 'http://info.jhc.cn/2016/1124/c201a72087/page.htm', 'http://info.jhc.cn/_upload/article/images/5f/3d/1364d669426086995ebd14b1405d/59206716-2b02-4a7e-a1f1-52bf7902fa07.jpg', '2017-05-17 14:13:16.082584');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (76, '陈晓龙、陈莉莉、王建源', '信息化事业部', '周荃', '2016-11-24 00:00:00', '信息工程学院常态化举办ACM程序设计竞赛', '日前，我校第十六届IT科技文化节之ACM程序设计竞赛正如火如荼地开展。整个比赛从每年的11月到次年5月，分为周赛、月赛及年度总决赛，最终选拔优秀选手参加次年在浙江大学举行的省大学生程序竞赛。赛后老师会针对月赛竞赛题目的英文解释和算法实现进行辅导。已形成常态化的激励和管理机制。本月月赛于11月20日13:00在东实训楼机房火热开赛。比赛共吸引了我院软件、计算机、应用电子、自动化、物联网等5个专业的30支队伍、90名大一、大二的同学参加。竞赛时间共4小时，而题目总数却高达10题，每一道题对应着一种颜色气球。问题成功解决会升起一只对应颜色的气球。鲜艳绚丽的色彩下，弥漫着一股无色无味的硝烟。一题、两题、三题……比赛进行的愈来愈激烈了。每队桌前都挂起了不同色彩的气球，现场的气氛也越来越紧张。沙沙的翻书声，低声的讨论，纸上不停的演算，键盘上舞动的双手。随着时间的流逝题目也变得越来越难各类错误也不断出现了，答案错误50%、编译错误等。选手们开始进入了艰苦的卡壳区，但是我们的选手并没有放弃依然坚持在我们的无形战场上。倒计时开始，就在题目即将关闭的最后一分钟，原本第二名的选手跃然而上反超成为第一。这就是我们ACM大赛的精神：不到最后一秒决不放弃。经过激烈比赛，苏万彬同学荣获大一组一等奖，First Blood队 (王建源，黄胜瑾，王良海)荣获大二组一等奖。', 'http://info.jhc.cn/2016/1124/c201a72086/page.htm', 'http://info.jhc.cn/_upload/article/images/44/8e/52676a0e4c1da5fafe70d4a68383/923cc12c-8720-405d-b1e9-ad903a6bb6c0.png', '2017-05-17 14:13:16.104762');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (77, '宣翠仙', '信息化事业部', '周荃', '2016-11-24 00:00:00', 'VR虚拟世界，未来已来——信息学院举办虚拟现实主题讲座', '11月22日，信息工程学院邀请网龙网络控股福建华渔教育科技有限公司华东地区总监史巍来校作“VR虚拟世界，未来已来——VR行业发展现状与趋势”主题讲座。史巍结合国内外大量虚拟现实技术应用案例，阐述了虚拟现实关键技术参数、VR/AR原理，虚拟现实行业发展现状与趋势、人才需求状况等内容。他指出，虚拟现实在我国发展势头迅猛、应用非常广泛、急需大量VR策划、U3D开发、全景VR制作、AR开发等工程人才，同时呼吁同学们加强表达沟通、应用写作等职业素养的养成，抓住VR发展契机，尽早对接VR岗位，做好职涯规划。讲座激发了广大同学走进VR的热情，现场互动气氛热烈，毕业班同学在后续现场招聘环节纷纷向公司了解岗位需求情况，投递个人简历。据悉，此次虚拟现实主题讲座，是我校第十届科技文化节的“走进VR”系列活动之一。系列活动还包括VR应用体验，VR现场招聘及校企签约仪式。我校与网龙网络控股福建华渔教育科技有限公司将能力合作，筹划在计算机应用技术专业增设VR方向，并从培养方案优化、基地建设、课程开发、科研项目、师生培训、技能竞赛等方面深入开展合作，助力“VR东方之都”打造。    VR主题讲座', 'http://info.jhc.cn/2016/1124/c201a72084/page.htm', 'http://info.jhc.cn/_upload/article/images/13/41/5ba8ca5f43a68cf6dd57986350f9/466d0711-9558-4c24-8d95-9fc8a8d3cde3.jpg', '2017-05-17 14:13:16.126983');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (78, '余红娟', '电子产品开发部', '周荃', '2016-11-24 00:00:00', 'IT科技文化节“无人机表演”展示现代科技魅力', '2016年11月22日在金华职业技术学院第十六届IT科技文化节上，信息工程学院应用电子技术专业开展无人机飞行表演，吸引了众多师生观看与拍照，无人机在预先设定的飞行路线上进行环校园上空全自动飞行，按照设定路线平稳飞回并轻盈降落，迎来了阵阵掌声。据悉，应用电子技术专业继示范建设成果固化几年后，经过对新兴高新技术产业的详细调研，确定在原专业建设基础上开设了无人机、新能源、医疗电子等专业方向，分流培养，以促进学生高端就业。其中无人机方向与浙江大学无人机培训基地合作，与省内外数家无人机企业形成无人机驾驶、维修维护、运营推广等岗位的就业基地。绍兴卓群航空科技李于民总经理在明德报告厅作了“无人机行业现状与发展趋势讲座”，向学生展示无人机在电力巡线、农业植保、消防灭火、交通监察等行业领域的应用，向学生详细介绍了无人机在研发、制造、销售、运营等岗位的就业需求与薪资待遇，会后很多学生争相报名参加无人机培训，希望通过自身努力，能够获得无人机驾驶许可证，进入到这个以属于年轻人的科技舞台上。', 'http://info.jhc.cn/2016/1124/c201a72082/page.htm', 'http://info.jhc.cn/_upload/article/images/76/e3/f99431b54039b7ba11a9b1e5123c/fa456b14-2cdf-44d7-acb8-c3f1588b53f1.jpg|http://info.jhc.cn/_upload/article/images/76/e3/f99431b54039b7ba11a9b1e5123c/c37d6b93-d8e0-4cce-aca8-7c8610582260.jpg', '2017-05-17 14:13:16.149234');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (79, '徐露诗丰辉', '学工办/团委', '周荃', '2016-11-23 00:00:00', '学校顺利举办科技创新体验日活动暨校第十六届IT科技文化节', '“哇！效果好逼真，真是太刺激了”11月22日中午，金华职业技术学院大学生活动中心一楼大厅人头攒动，好不热闹。原来学校正在举办第十六届IT科技文化节，全校的学生都被科技创新体验区中所展示的高科技产品吸引，排队希望能够感受一把。校党委委员、副院长龚永坚，卓群航空科技有限公司总经理李于民、北京师范大学智慧教育研究院VR行业应用中心副主任史巍、学校相关部门负责人、信息学院领导及师生悉数出席，龚永坚对各位到场并对本次文化节提供技术支持、设备支援的企业表达了真挚的感谢，并寄语在场的同学们珍惜本次机会，切实提高自身创新创业及综合职业能力，成为推进大众创业、万众创新的生力军。本次IT科技文化节科技创新体验日活动内容丰富： 3D打印机、学生自主研发的手机APP体验，让同学们真切地感受到了新科技的魅力；火爆的虚拟现实技术体验（VR）体验区，同学们借助VR强大的视觉效果，身临其境地感受到了与僵尸对战、骑马打仗的刺激，逼真的视觉效果让体验的同学惊呼连连，对VR技术向往不已；而亲手操控无人机飞行，看着无人机随着遥控操作不断改变方向与高度，令同学们感受到了科技研发的强大；更不用说智能机器人方阵，随着音乐，自发地做出统一整齐的舞蹈动作，让在场的同学们啧啧称奇。“今天体验到VR这项技术太神奇了，我以后希望能从事研发VR技术的工作，也成为技术性人才”一位计算机专业的同学这样说道。据悉，本届科技节在原有特色上，又邀请了虚拟现实、无人机、信息安全等反映电子信息技术新发展的多家行业领军企业参加。他们不仅带来众多设备现场表演，而且还举办技术论坛、现场招聘等活动，他不仅是新科技的展示，更是为学生的专业发展、学生高质量就业提供了机会。活动当天，学校党委副书记余党军和网龙控股公司福建省华渔教育科技有限公司副总经理高锋进行了校企合作签约仪式。', 'http://info.jhc.cn/2016/1123/c201a72028/page.htm', 'http://info.jhc.cn/_upload/article/images/2f/e4/e757897044d4913339be3924aa57/a4a6d069-9029-4528-a443-9c85fcc28e29.jpg|http://info.jhc.cn/_upload/article/images/2f/e4/e757897044d4913339be3924aa57/c363bb8e-1c50-482f-aa30-272f44702bf0.jpg|http://info.jhc.cn/_upload/article/images/2f/e4/e757897044d4913339be3924aa57/fec4141e-c7c0-4a43-8e5c-db92f882f3e5.jpg|http://info.jhc.cn/_upload/article/images/2f/e4/e757897044d4913339be3924aa57/a659d33a-bfe9-4a93-9b61-79f940744fc2.jpg', '2017-05-17 14:13:16.171484');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (80, '钱媛、丰辉、曹胜任', '金华晚报', '周荃', '2016-11-23 00:00:00', '《金华晚报》报道我院“男光棍”班  用心书写IT男的工匠精神', '这个时代男孩有“危机”？NO！这个班清一色的男生,个个有“匠心”        金职院信息学院优秀班级、优秀团支部、先进团支部、校先进团支部、校五四红旗团支部…　　如果这些荣誉，都集中在某个班级，你一定会惊讶，为这个班竖起大拇指点赞！　　在金职院信息学院，就有这样一个班级，他们由34个男生和1个男老师组成，这也是金职院信息学院唯一一个全部由“男光棍”组成的男生班———自动化142班。　　在很多人眼中，与女生相比，男生调皮、好动、叛逆、不服管，也许还特别有个性。很多带班老师，都怕班里男生多，男生多了，就意味着班级吵吵嚷嚷，不好管。金职院这个“男生班”，却让全校老师刮目相看，他们不仅团结友爱，互帮互助，而且勤奋好学，成果丰硕，集众多“优秀头衔”于一身。　　打地铺，住实验室……　　他们用心书写IT男的工匠精神　　其实，在大学校园里，理工科专业的班级，大多女生较少，不过一个女生都没有的“男生班”，还是挺稀奇的！自动化142班的男生们，就戏称自己班为“光棍班”。　　班里没有美女，校园生活确实单调了一点。不过，这群男生却能折腾，在男生世界里玩出了自己的名堂。据班主任曹胜任说，班里有热衷于公益的“公益达人”，有坚持不懈的“运动达人”，有博览群书的“阅读达人”等，不过最让他自豪的还是这群IT男生的“工匠精神”。2014年，信息学院根据电气自动化技术专业的就业岗位能力需求，开展分层分流人才培养探索，创建了电气自动化专业创新班，这个班以培养学生的创新实践能力为目标，根据每周一任务，一学期一项目，开展教学。每个同学根据个人爱好，在PLC编程、组态设计、电气线路施工、伺服变频控制方面进行创新能力的培养。当时，“光棍班”也有7位同学被选拔进入创新班学习。其中赵锦坤、温从汉、赵赛赛三人还参加了风光互补实训系统安装与调试技能竞赛，杨晓康、郑鸿宇参加了现代电气实训系统安装与调试技能竞赛，杨轲、南如幸参加了自动化生产流水线安装与调试技能竞赛，他们中有5人次获得了全国一等奖，3人次获省一等奖，4人次获省二等奖。　　赵锦坤是“光棍班”的班长，他说创新班的日子很难忘，非常辛苦，训练强度很大，但是很有收获。“我记得，在国赛前夕，我们在不到一个月的时间里，单单风光互补项目就练完了7麻袋的电线。”省赛过后，赵锦坤和同学进入了国赛训练，除了技能训练，傍晚时分老师还会给他们安排体能特训，确保大家有足够的精力与体力来应对残酷的比赛。　　“有时候，几场训练下来，我们已经疲惫不堪了，但是我们‘好基友’就互相鼓励，不懈怠，然后针对各自的问题进行讨论总结。稍作休息和整顿后，投入到高强度的训练中来。”曹胜任老师说，这样的训练强度，一般的女生很难坚持下来，幸亏这是一群精力旺盛的“臭小子”，他们也很认真刻苦，每天的内容都是训练，很枯燥，但一直乐此不疲。有时候，为了利用好时间，他们干脆打地铺，直接住在了实验室里。　　近一个月，多家高科技企业，如汇川公司、ABB公司等到电气自动化技术专业招收毕业生，这些集技术与才气于一身的同学们成了多家单位的抢手货。　　男生之间的较劲：　　少了斤斤计较，多了你追我赶　　“赵锦坤学习认真、工作负责，赵赛赛勇于开拓创新，杨晓康有较强的交际能力，南如幸有较强的逻辑思维能力，温从汉有较强的组织策划能力，杨轲擅长计算机技术、动手能力强，郑鸿宇是自动化专业顶尖的技术能手。”在曹胜任看来，这个“工匠7人组”是他们“光棍班”男生的一个缩影。　　曹胜任说，在“光棍班”中，全班男生分成5个学习小组。上课时，老师一提出问题，小组之间就相互探讨，男生之间的合作，少了斤斤计较，多了你追我赶，他们“别人比我强，我就向他学，一转身，超越他，成就他；别人比我弱，我就帮助他，一转身，让他帮助更多的人”。　　学生南如幸说，大家学习上互帮互助，在这样的氛围中生活，男生们不断成长，既提升了专业技能，大家也越来越自信了。还有，男生喜欢动手，闲不住。周末休息的时候，他们就会去参加一些小制作、小发明活动，将自己学到的知识付诸实践。　　“光棍班”宣言：　　也许我们不浪漫，可我们很公益、很感恩！　　在金职院求学两年来，“光棍班”的男生们也许不够浪漫，却足够man！他们班，不仅是全院优秀班级、技能竞赛“获奖户”，还是运动会上的“常胜将军”，获得过院运动会团体第三名。　　不过，最让“光棍班”男生自豪的是，全班男生都是信息学院家电维修社成员，这意味着每学期，他们都会参加家电维修协会组织的下乡维修活动。各式各样的家电，摆满了维修现场，他们对每一件故障电器，进行细心登记，并检测维修，利用自己的技能一一排除故障。“用我们所学的知识，为社区居民带去便利，是一件很开心的事。”男生们这样告诉记者。　　每年重阳节，他们还会一起去金华市老人活动中心探望孤寡老人；雷锋纪念日里，他们会参加金华市雷锋文化馆举办的纪念雷锋活动，并捐助爱心基金。　　曹胜任老师说，能成为“光棍班”的班主任，很幸运。他和学生之间的感情，亦师亦友，但更多时候像兄弟一样。都说女生心细，懂得感恩，但这群大男孩，每年都会记得给他这个班主任过生日，每次都有惊喜和感动。　　“我记得去年生日时，我们班学生说过这么一句话，让我印象很深。他们说，等毕业了，要把班里获得的所有荣誉都收集好，挂到我办公室里。他们要争取挂一面墙，让我开心！”这句话，让班主任曹胜任至今都很感动。　　“谁说男生不懂得感恩？”曹胜任说，这34个男生他会一直记住，然后看着那面“光荣墙”，感叹“能当他们的班主任，真的很幸福”。 本报记者 钱媛/文 通讯员 丰辉/摄原文链接：http://epaper.jhnews.com.cn/site1/jhwb/html/2016-11/23/content_1776607.htm', 'http://info.jhc.cn/2016/1123/c201a72027/page.htm', 'http://info.jhc.cn/_upload/article/images/cc/13/f58ec8664ce19fe55d78ff9fefb8/c77bb962-d231-4c34-89f2-05997f2bf16a.jpg|http://info.jhc.cn/_upload/article/images/cc/13/f58ec8664ce19fe55d78ff9fefb8/e27a23e4-dc84-4a7f-abc6-e6fa0c9f5fca.jpg', '2017-05-17 14:13:16.19377');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (81, '何碧峰、张敏', '学工办/团委', '周荃', '2016-11-23 00:00:00', '信息学院志愿者走进敬老院开展暖冬行动', '敬老、爱老、养老、助老是中华民族的传统美德，为了教育当代大学生关爱老人，学会感恩，感谢老人们曾对社会所做贡献，弘扬敬老、爱老美德。近日信息学院志愿者带上鲜花水果来到了位于大黄山上的橄榄山老人公寓，看望孤寡老人。志愿者们为老人们整理房间打扫卫生，表演节目，并将自己亲手编织的围巾手套等保暖送给老人，给他们送去了冬日的温暖，受到了老人们的一致好评。 “看到老人们开心的笑容，听到他们感激的话语，知道他们有晚年幸福的生活，我们长时间的准备都是值得的。”志愿者夏启成激动的说道。现在还有很多孤寡老人、留守儿童需要我们去帮助和关心，志愿者们呼吁希望有更多的同学可以加入到他们的队伍中，给更多需要温暖的人群送去更多的关爱。', 'http://info.jhc.cn/2016/1123/c201a72023/page.htm', 'http://info.jhc.cn/_upload/article/images/85/e3/4a4fce6c480d9f5fe388781cde3f/5f5aaad4-c0fb-4fb1-a95d-e20f24282551.jpg|http://info.jhc.cn/_upload/article/images/85/e3/4a4fce6c480d9f5fe388781cde3f/971600bf-e864-4d85-8f20-1b1587c8c709.jpg|http://info.jhc.cn/_upload/article/images/85/e3/4a4fce6c480d9f5fe388781cde3f/3679538b-19f9-4d7b-82f0-d792da1f5596.jpg|http://info.jhc.cn/_upload/article/images/85/e3/4a4fce6c480d9f5fe388781cde3f/2f1d77d7-eba4-4ccf-b620-df6b76a0bb4a.jpg', '2017-05-17 14:13:16.218085');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (82, '周荃', '办公室', '周荃', '2016-11-21 00:00:00', '民盟金职院支部考察遂昌新农村旅游经济发展情况', '2016年11月18日至19日，民盟金华职业技术学院支部在支部五位班子成员的带领下，前往遂昌、红色古镇-王村口、南尖岩等地考察新农村旅游经济发展情况。近些年，浙江省有个引人注目的现象，就是农家乐的大发展。过去，农民普遍通过饲养牛、羊、猪等来增加经济收入，但也带来环境污染等问题，如今一座座闲置已久的农房，摇身变成农家乐、洋家乐、农家客栈等特色民宿，每到周末，更是一床难求，人气爆棚。在浙江经济结构调整的非常时期，浙江农家乐超出预期、一枝独秀的“加速度”发展，不能不引起人们的高度关注，但在新农村旅游经济快速发展的同时，如何解决生活污水直排污染、垃圾与秸杆焚烧引起大气污染、大量没有有效处理的秸秆堆在水沟中引起水源污染，以及如何做大做强农村特色旅游，破译新常态时期经济社会发展新的规律和运行路径等方面，还需要我们进一步思考，为参政议政准备素材。为此，支部盟员在欣赏南尖岩茂林修竹、飞泉瀑布、云海变幻、高山梯田等美景的同时，认真思考休闲度假、生态旅游以及农家特色小吃、特色商品销售所带来的旅游客源，重视对旅游生活污染的生态处理方法。在红色古镇-王村口，盟员们先后考察了清代古建筑天后宫、宏济桥、蔡相庙、挺进师师部旧址和清代古街等历史遗迹和革命旧址，接受了爱国主义教育，增长了民族自尊心和历史使命感。盟员们纷纷表示，尽自己所能做好本职工作的同时，努力做好民主党派肩负的参政议政、民主监督和政治协商三大职能。', 'http://info.jhc.cn/2016/1121/c201a71870/page.htm', 'http://info.jhc.cn/_upload/article/images/11/3b/c7580bf64f12b9fc0ceec3924013/c6bfade6-cf9a-4e76-b7e5-17e30cad44f9.jpg', '2017-05-17 14:13:16.24016');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (107, '丰辉', '学工办/团委', '周荃', '2016-10-18 00:00:00', '信息学院顺利完成团学组织干事招新工作', '为了使信息学院团学组织工作更好的开展，经历了长达半个多月的招新选拔于2016年10月18日顺利完成。信息学院团学组织的纳新主要是通过自主报名、面试、干部综合能力测试、公示等程序来进行。并分别由四大组织（学生会、团委、宿管会、社管会）牵头，通过两轮面试来考察应聘学生的语言表达能力、工作能力、兴趣爱好、自身优势等。据悉，今年参加团学组织应聘的同学共计644人，比往年的多了150多人报名，学生的积极更高。经过层层筛选，精心搭配，最终四大学生组织共录用了316名新成员——干事，并进入试用期。对于大多数同学来说，这是进入大学校园之后第一次展现自我、锻炼能力的机会，同学们都很珍惜，纷纷表示要更尽我所能，全心全意服务同学。', 'http://info.jhc.cn/2016/1018/c201a70520/page.htm', NULL, '2017-05-17 14:13:25.274353');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (83, '徐华丹', '教科办', '周荃', '2016-11-21 00:00:00', '信息学院探索四年制高职导师制深化实施新方法', '为了办出四年制高职特色、提高人才培养质量，信息学院在总结四年制高职实施导师制工作一年来的经验基础上，出台了一系列管理制度，规范导师制工作流程，创新导师制运行方式，并于2016年11月18日召开实施动员会，2015级和2016级电信班共98位学生、信息学院领导和各位导师参加了本次会议。在会上，王成福副院长对新的导师制运行方式进行了介绍：根据一年多导师制实施情况，结合学生对导师制工作的反馈意见，学院对导师制试点工作进行了相应的调整，出台了导师制管理办法，规范了导师制工作流程与评价方法，将导师来源从本专业扩大到相关的其他专业，将项目载体由单一的专业创新项目扩大到创业设计、专业创新、管理创新、活动创意与组织、个人管理等方面，允许导师申报项目和学生自己申请项目相结合，其唯一判据是只要能提高学生专业能力或者提升学生的核心素养的项目，均可作为导师制的项目。据悉，本次导师制共选拔导师20名，导师申报新项目15项，学生自己申请项目2项，实施过一轮的实物制作项目3项，项目内容涉及电气控制、智能控制、程序设计、生产图纸设计、微信推送应用开发、竞赛组织设计等方面，通过双向选择确定师生结对关系，开展项目实施工作。学生将在导师或者高年级学长的指导下，从查阅资料、撰写开题报告、项目实施到总结交流，整个过程都将实施信息化管理，并根据项目完成质量的考核，与导师报酬、学生第二课堂学分相挂钩，确保导师制实施效果。', 'http://info.jhc.cn/2016/1121/c201a71869/page.htm', NULL, '2017-05-17 14:13:16.262426');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (84, '徐露诗', '学工办/团委', '周荃', '2016-11-21 00:00:00', '你知道吗？龙川北苑04南的好去处——信息学院打造宿舍休闲角', '“在茶花盛开的地方，有一座美丽的校园……”龙北04楼南近日经常传来阵阵悠扬的旋律，原来是信息学院的男生们正在宿舍的休闲角利用课余弹吉他排练唱歌。据悉，为了丰富学生的课余生活，信息学院近日为学生们精心打造休闲角，以营造温暖、和谐的学习、生活、娱乐场所。休闲角位于每一层靠北边的一尺空地，面积虽小但采光极好，信息学院的同学们充分地利用了这方寸天地，不仅购置了桌椅、绿植、书本以及书报架，并且用素雅的墙纸覆盖了墙面，整个休闲角在同学们的布置下，一改以往单调的样子，焕然一新，生机勃勃。同学们也非常喜欢这个阳光明媚的休闲角，经常能在课余时间看见同学们在休闲角一起下棋、阅读、讨论和排练的身影，人来人往，热闹非凡。 “公寓原先就是我们在学校的家，是我们除了教室之外呆的时间最多的地方，感谢学院打造了这个好地方，让我对宿舍产生了更加强烈的归属感”一位正在休闲角阅读书籍的同学这样说道。', 'http://info.jhc.cn/2016/1121/c201a71867/page.htm', 'http://info.jhc.cn/_upload/article/images/f6/f1/91132ed645c2b22223cb85fbab7a/5fe00c12-c3a7-4b34-a3dd-bb53164d0ffb.jpg', '2017-05-17 14:13:16.284834');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (85, '丰辉', '学工办/团委', '周荃', '2016-11-21 00:00:00', '信息学院第二十六期业余党校开班', '11月17日，信息学院第二十六期业余党校正式开班，181名学员参加培训。据悉，此次培训为期两个月，结合当前正在开展的“两学一做”专题教育活动，本期党校培训学院将邀请企业党建负责人、校内外专家开展讲座。同时同学们进行时政热点案例分析（包括读书讨论心得）、社会实践调查、红色节目表演、文明监督岗等，理论与实践结合，提高学生的思想觉悟和党性修养，深入地实践，积极奉献社会。', 'http://info.jhc.cn/2016/1121/c201a71764/page.htm', NULL, '2017-05-17 14:13:20.378986');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (86, '徐华丹', '教科办', '周荃', '2016-11-18 00:00:00', '信息学院开展兼职教师培训活动', '为提高兼职教师的教育教学能力，提升兼职教师教学质量。近期，信息工程学院举办了兼职教师教育教学基本能力培训活动，活动分专、兼教师相互评课、兼职教师座谈会两个阶段开展，学院兼职教师、专业主任和优秀专任教师、相关领导参加了此次培训活动。在活动中，各项目部专业主任和专任教师对本专业的兼职教师进行推门听课，了解上课情况，同时，也邀请兼职教师对优秀专任教师听课，学习先进的教学理念和教学方法。在随后的兼职教师座谈会上，兼职教师和专任教师互相分享教学心得与经验，共同探讨如何将企业实践经验与教学过程相结合，做好工学结合课程开发，并就课堂教学、校企合作、学生顶岗实习工作等进行深入专题探讨，形成一致意见。最后，学院向兼职教师讲解了学校对有关教学活动中的教学规范、教师考核等政策，通过兼职教师培训活动的开展，为专、兼教师提供了相互学习交流的机会，提高了兼职教师在教学设计、教学方法及课堂教学组织的各项能力，为人才培养质量提供保障。', 'http://info.jhc.cn/2016/1118/c201a71704/page.htm', NULL, '2017-05-17 14:13:20.440627');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (87, '吕威威', '学工办/团委', '周荃', '2016-11-18 00:00:00', '“书画入寝”寝室书画大赛', '发扬艺术之光，传承书画风采。近日，信息学院在东教学楼举办了“书画入寝”寝室书画大赛。据悉，本次活动吸引了近百名学生参加。比赛现场，同学们各显神通，一幅幅作品栩栩如生、遒劲有力，一笔一划尽展中华汉字的无穷魅力。作品内容丰富，富有个性化，简笔画、速写、漫画、素描、水彩画……样样俱全。经过激烈角逐，评委老师评选出一、二、三等奖项，其中，软件161班陈思萍和计算机161班汪顶力同学的作品获得一等奖。赛后，获奖作品装裱后将在寝室文化角展出，展示学生才华，丰富寝室文化。', 'http://info.jhc.cn/2016/1118/c201a71703/page.htm', 'http://info.jhc.cn/_upload/article/images/31/9a/17038d334d478972e93a49bfd88b/90d0418a-d0a7-4d00-ac50-4640de6f8174.jpg', '2017-05-17 14:13:20.462732');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (88, '贾美倩', '信息工程学院', '周荃', '2016-11-18 00:00:00', '在深圳，有这样一群年轻人——走访信息学院校友', '近日，信息学院党委书记贾美倩老师随学校校友办领导及老师，走访信息学院在深圳就业创业的优秀校友们。此次校友行中，分别走访了在深圳旭昴物联科技有限公司、深圳市南山医院、广东东莞市文通机械设备有限公司就业的6名校友，并且与校友们进行了深入的交流。深圳市旭昴物联科技有限公司是一家专注于物联网无线连接技术的研究和开发，我院2014届毕业生丁慧鹏、陈佳俊、韩银峰、郑德灿、王超等就职于该公司，目前公司正着力研发城市智能停车收费系统、精细化农业管理系统等引领时代潮流的项目，而丁慧鹏等人凭借自己在学校练就的扎实基本功和不断进取的精神，在有着众多的本科生、研究生学历的公司中脱颖而出，不仅是上述两个研发项目的核心成员，而且已经成为了该公司的技术骨干。吕周平毕业于2003年，现就职于深圳市南山区医院，任软件开发组组长，是医院软件系统开发与服务的核心成员，同时也是深圳市南山区医疗数据库服务器管理和维护全南山区his软件上线和新模块研发的核心成员。他目前正着手研发医疗大数据应用、急诊前管理系统及家庭医生系统等领域，今年有望上线的“移动医疗保支付的分级诊疗与社康双向转诊”系统，将为提升社区医疗服务提供保证。这些优秀的校友中，有在学校参加过国家级、省级技能竞赛的高手，有担任过班干部的组织能手，也有组织过社团活动的热心人，他们纷纷表示，是学校搭建了平台让他们学到过硬的技术本领，是老师的潜心指导给了他们学习技能的信念，是工作中团队的力量助推了他们的成长，是深圳这块热土赋予了他们敢“闯”不甘平庸的品格。他们认为，虽然目前吃住可以简单地对付，但在工作上却从不马虎，经常加班到深夜，周末也是经常呆在公司看资料查信息，时刻保持着强烈的创新、研究、奋进的信念。', 'http://info.jhc.cn/2016/1118/c201a71702/page.htm', NULL, '2017-05-17 14:13:20.485023');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (89, '沈晨、周荃', '综合', '周荃', '2016-11-07 00:00:00', '我校成功举办金华市“楼兰”杯第二届网络安全竞赛及安全论坛活动', '11月5日，金华市“楼兰”杯第二届网络安全竞赛在我校信息工程学院信息安全实训室举行，这是我校举办的第二届全市网络安全竞赛。竞赛由市公安局、网信办、经信委指导，金华职业技术学院主办，上海云盾信息技术有限公司、金华安远信息安全检测技术有限公司协办，此外还得到省公安厅网络安全保卫总队和杭州电子科技大学的大力支持。竞赛旨在提供一个网络与信息安全领域学术和信息的交流平台，促进高校、政府、企业等行业的信息安全交流，为培养优秀的网络与信息安全专业人才创造条件，为企事业单位在信息安全领域的沟通搭建桥梁。竞赛吸引了45名来自浙江师范大学和我校的学生报名参加，此外还有来自金华地区政企信息部门主管、信息安全企业及信息安全从业人员近百人前来观摩。比赛采用CTF（夺旗）模式，以在线网络通关竞赛形式举行，题型全部为实操题，竞赛持续5小时，比赛期间选手们不能使用通讯工具，参会嘉宾在观战厅通过大屏幕看到实况播送。今年的比赛题型较去年略微增加一些难度，主要涉及WEB安全、编程开发等方面，而这几个方面涵盖绝大多数的安全方向，可在一定意义上较为准确的评估一个比赛选手的综合水平。开幕式上，校长钟依均代表学校向前来参会的领导嘉宾和参赛队伍表示欢迎，他说道，学校自2014年调集精锐师资力量组建网络安全攻防实验室、信息安全联合创新实验室，两年多来协助检测政企网站600余家，发现漏洞总数近万个，数据服务多次提供重要线索，获得好评。同时实验室毕业生连续两届被天融信、启明星辰等一线安全企业整批次录用。我校将继续创新人才培养模式，加强产学研结合，推进政校企合作，为网络安全领域的发展提供更多助力。在网络安全论坛现场，浙江省公安厅网安总队蔡林总工程师讲解了如何提高社会公众的网络安全防范意识；上海财大王黎明教授讲解了大数据环境下的数据分析以及其发展和应用的前景；上海云盾副总经理钱铮介绍云防护技术在网站安全防御中的应用；我校信息安全创新实验室沈晨介绍了金华信息安全数字地图，直观呈现了全市网络安全状况，与会嘉宾还就信息安全问题展开了交流研讨。经过一天的紧张角逐，信息学院学生代表学校参赛，姚轶麒获一等奖，雍新安、包风琦获二等奖，王鑫，王晴获三等奖。随后，大会为获奖选手颁发了证书和奖金。作为连续三年成为浙江省网络与信息安全信息通报中心的技术支撑单位（省内高校唯一），信息安全创新实验室致力于打造面向政企的信息安全培训、检测、维护、产品研发等综合信息安全服务模式，为我市网络经济发展发挥更大作用。', 'http://info.jhc.cn/2016/1107/c201a71413/page.htm', 'http://info.jhc.cn/_upload/article/images/20/73/efa3d8ee4587b873bf0b69f48c11/df229006-8330-427f-b57a-a33c3150f58c.jpg|http://info.jhc.cn/_upload/article/images/20/73/efa3d8ee4587b873bf0b69f48c11/e851d452-5a40-448b-abde-a6686482ff78.jpg|http://info.jhc.cn/_upload/article/images/20/73/efa3d8ee4587b873bf0b69f48c11/3856e54b-aac1-45aa-bd18-b470eacf26b4.jpg|http://info.jhc.cn/_upload/article/images/20/73/efa3d8ee4587b873bf0b69f48c11/2e43db00-72ce-4aab-824a-4701408e0c41.jpg|http://info.jhc.cn/_upload/article/images/20/73/efa3d8ee4587b873bf0b69f48c11/94769f5f-b842-47f7-80d7-dc38c6d8227b.jpg', '2017-05-17 14:13:20.507137');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (108, '钱靖', '学工办/团委', '周荃', '2016-10-18 00:00:00', '金华职业技术学院2016年校庆日活动之李万正校友《创人生》创业经验分享座谈会', '10月15日上午，信息学院优秀创业校友座谈会在东实训楼北楼会议室召开，座谈会邀请了优秀创业校友杭州西铭电子有限公司总经理李万正与在校创业学生代表分享创业体会，并为学校开展创新创业工作、校友与母校共同发展建言献策，学院赵敏笑副院长出席会议并讲话。李万正2002年毕业于信息工程学院应用电子专业，在校期间担任院学生会主席一职，在座谈会上，李万正向在校创业学生代表真情分享了自己在创业过程中的经验、教训和体会，并围绕创业理念、创业心态、创业基础、更新知识结构、创业初期的困难应对、合伙人关系、商务来往、在校生创业的优势和劣势等方面畅所欲言。李万正在多年的创业过程中，一直坚信成功10%靠运气，90%靠的是勤奋；从最初的原始创业资金的积累，到最后的成功，靠的也正是他的这份勤奋和坚持，他总说要做一件事，无论失败与成功，总要试一试，闯一闯，不行你还可以掉头；但是你如果不做，总走老路子，就永远不可能有新的发展。靠着这样的一股冲劲，李万正先是就职于杭州西湖电子研究所先后任销售总监、所长助理，这些工作，让李万正无论是销售市场还是技术专利方面都得到了一定的经验，为之后的创业奠定了基础。李万正表示对母校有着很深的思念之情，大学三年的生活学习让他学会了怎样去做一个正直且有理想的人，而其所创办的杭州西铭电子有限公司依旧与母校有着很多校企之间的合作，正应了那句话：“今天我以母校为荣，明天母校以我为荣”。', 'http://info.jhc.cn/2016/1018/c201a70487/page.htm', 'http://info.jhc.cn/_upload/article/images/e0/54/66830ba34469996536a4c83e8e52/c4cac8a7-7bd8-4c88-9d2d-a6c84e815427.jpg|http://info.jhc.cn/_upload/article/images/e0/54/66830ba34469996536a4c83e8e52/da3e7fc2-eaee-4df0-8ba5-b0115debe403.jpg', '2017-05-17 14:13:25.296752');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (90, '吕威威', '学工办/团委', '周荃', '2016-11-07 00:00:00', '变废为宝——“废品换盆栽”环保活动', '“寝室没废品了，都换盆栽了！”只要拿10个废弃的物品就能换回一盆充满生机的盆栽。近日，信息学院学生公寓楼下举办了一场以环保为主题的“废品换盆栽”活动。活动现场的一位负责老师介绍，在日常的寝室管理工作中发现，废品堆积是一个重要问题，学生寝室生活中一部分具有回收价值的物品，如：塑料瓶、废纸、易拉罐、废电池等被当成垃圾丢弃。这些废弃物如果随意丢弃将对环境造成严重污染。但是这些垃圾被重新回收利用就可以节约大量资源。一盆小小的盆栽可以吸收室内有害气体，缓解眼部疲劳，舒缓紧张的心情。“以废换绿”的活动吸引了众多同学参加，活动开始不到十分钟，同学们纷纷从寝室拎来废纸箱、塑料瓶等废品，向工作人员兑换绿植，随后，前来兑换的同学迅速排起了长队，工作人员有条不紊的点数着废品数量，短短的一个多小时， 100个盆栽就被热情的同学们兑换一空。“今天这个活动真好，合理的处理了废品，盆栽放在寝室里还能美化环境。”一名同学手拿盆栽开心的说道。通过活动增强大学生手机可再生垃圾并且二次利用的意识，同时，向同学们宣传环保知识。倡议学生从身边小事做起，加入到保护环境的队伍中。', 'http://info.jhc.cn/2016/1107/c201a71403/page.htm', 'http://info.jhc.cn/_upload/article/images/ff/ad/e14d316e4a52a5b251ec63a0ba87/9e60df93-7869-4d1c-bbbc-89d559dbe3ae.jpg', '2017-05-17 14:13:20.531194');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (91, '吕威威', '学工办/团委', '周荃', '2016-11-07 00:00:00', '信息学院公寓吉尼斯精彩纷呈', '“加油！你可以的，马上就到终点了！”11月5日，校运动场上呐喊声此起彼伏，为奔跑在指压板上的同学加油，原来是信息学院正在举办“青春向上活力飞扬”公寓吉尼斯活动。 此次活动内容丰富，形式多样，除压板百米大赛外，还有单手托乒乓球赛跑、“金鸡”独立、穿针引线、定点投篮、迷你铁人三项等九项内容，各个赛事集趣味性、挑战性、实用性、技能性于一体，吸引了各年级同学们纷纷以寝室为单位积极参与其中。其中，“奔跑吧”指压板百米赛竞争最为激烈，选手们相互协作，咬牙坚持，冲过终点，出色的表现赢得了围观者的阵阵喝彩。 活动负责老师表示，响应“走下网络、走出宿舍、走向操场”的号召，比赛丰富了学生的课余生活，展示出新时代大学生的蓬勃朝气和竞技热情，更增强了寝室成员之间的团队凝聚力！', 'http://info.jhc.cn/2016/1107/c201a71401/page.htm', 'http://info.jhc.cn/_upload/article/images/3f/b2/27d3d64043aaa60563b4d4e005df/02dc5829-7e43-4b89-aaea-8fe11b2eeca6.jpg', '2017-05-17 14:13:20.552364');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (92, '周荃', '办公室', '周荃', '2016-11-07 00:00:00', '信息工程学院党委组织观看《永远在路上》', '11月2日，信息工程学院党委组织全体党员观看大型反腐专题片《永远在路上》，接受反腐倡廉警示教育，结合“两学一做”认真研讨。据悉，该专题片反映了党的十八大以来，以习近平同志为核心的党中央深入开展反腐斗争，对贪污腐败零容忍，从严治党净化了党的干部队伍，赢得了民心。观影活动是对全体党员的一次精神洗礼，大家纷纷表示坚决拥护党中央从严治党、高举反腐利剑的决策。同时，全体党员要以典型案例为戒，在新形势下坚定不移地践行全心全意为人民服务的宗旨，牢记作风建设永远在路上。', 'http://info.jhc.cn/2016/1107/c201a71400/page.htm', NULL, '2017-05-17 14:13:20.573939');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (93, '徐露诗', '学工办/团委', '周荃', '2016-11-04 00:00:00', '金华我懂你 走进婺文化', '“金华的地方戏婺剧是浙江有名的戏曲，《僧尼会》、《黄金印》、《西施泪》都是其中比较有影响的剧目”信息学院于近日举办了以“金华 我懂你”为主题的班会活动，希望借由这次机会，带领新生们了解金华，走进婺州文化。据悉，本次班会通过景、食、乐三个方面，全面而细致向同学们介绍了金华这座美丽而宁静的城市。历史悠久、享誉中外的双龙洞景区，林木葱郁、水碧如镜的黄大仙景区，文化气息浓厚的八咏楼，做工精致的婺州窑，无不让同学们心向往之；清香怡人的佛手，口味鲜香的火腿，酥脆可口的酥饼又让在座的同学们垂涎三尺；经典婺剧《僧尼会》，更是凭借演员精彩的表演赢得了同学们的阵阵掌声。除了介绍金华的传统文化之外，还向同学们展示了金华的现代气息：佛手文化园、金华婺剧院、彩虹桥、新体育馆，无不彰显着金华蓬勃发展的趋势。 “以前对金华的了解不深，经过这次班会，发现金华真是一座美丽而生动的城市，有好多有趣的地方我现在迫不及待地想去玩一玩呢”一位同学开完班会这样说道。', 'http://info.jhc.cn/2016/1104/c201a71281/page.htm', NULL, '2017-05-17 14:13:20.596097');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (94, '程相源、朱墨池', '通信与网络工程部', '周荃', '2016-11-03 00:00:00', '网络专业在校园网络中心进行实景教学', '中新合作网络专业二年级学生在专业技术课学习之后，在中外教师的带领下，再次回到校园网络中心，从专业课程的角度重新认识IDC机房和规范化的机房管理。中心曹主任代表学校欢迎外教和同学们。邓涛老师介绍了IDC机房的主要设备和功能以及它们在校园网络中的作用。通过再次学习，学生非但认识了几千元到几十万元的各种设备，而且通过近距离观察服务器、网络存储、核心交换机等关键设备的部署方式和工作状态，加深了对网络的理解和认识，提升了网络设备类课程的学习兴趣。参观中，同学们迫不及待地与老师讨论起机房的设备安装、通风方式、供电保障方面的问题。图 1 网络153班图 2 网络152班图 3 曹主任与Prateek Gupta 握手图 4Prateek Gupta与同学交流图 5 校园网络中心实景参观', 'http://info.jhc.cn/2016/1103/c201a71231/page.htm', 'http://info.jhc.cn/_upload/article/images/e1/8b/c9e33e244e5ea00845160378b001/64d2fb98-07d7-4c92-bfdc-47a567729a8d.jpg|http://info.jhc.cn/_upload/article/images/e1/8b/c9e33e244e5ea00845160378b001/d17aacd2-15fa-4e15-9d9a-7ef26080d568.jpg|http://info.jhc.cn/_upload/article/images/e1/8b/c9e33e244e5ea00845160378b001/67e355a4-7ae3-4000-b297-522367ca1e79.jpg|http://info.jhc.cn/_upload/article/images/e1/8b/c9e33e244e5ea00845160378b001/aa53a9b6-af20-4a91-b560-5cba255a4e86.jpg|http://info.jhc.cn/_upload/article/images/e1/8b/c9e33e244e5ea00845160378b001/1adca9e8-18c7-42ce-a3e2-05a09fac5e31.jpg', '2017-05-17 14:13:20.618397');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (116, '吕威威', '学工办/团委', '周荃', '2016-09-30 00:00:00', '姜汤去寒意 话语传温暖', '“同学们，喝点姜汤去去寒吧！”军训结束后，从信息学院寝室内传来一声声暖心的叫喊，原来是信息学院党委书记贾美倩正在走访新生寝室，亲切慰问军训后的学生。据了解，在贾书记的带领下，学工办主任、全体辅导员、班主任及班主任助理拿热水壶从金湖餐厅打来一壶壶姜汤，给军训后的同学送至寝室。寝室里，贾老师详细查看同学们的身体状况，并提醒同学们在国庆假期期间要注意安全。一杯杯暖暖的姜汤，为新生去除寒意，一句句关心的话语，为新生带来温暖。“到学校短短两周时间，已经深深感受到了这个大家庭的温暖，在以后的三年大学时间里，我一定会好好学习！”新生孙晨凯说到。', 'http://info.jhc.cn/2016/0930/c201a69726/page.htm', NULL, '2017-05-17 14:13:31.663788');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (95, '徐露诗', '学工办/团委', '周荃', '2016-11-03 00:00:00', '谁是新一代信息学院超级演说家', '“青春，我们的征途就从脚下开始”近日，信息学院举办16级“超级演说家”大赛，十二位选手齐聚一堂，展现了新一代信息学子的风采。本次比赛分为PPT展示和即兴演讲两个环节。首轮的PPT展示中，十二位选手分别以“我青春，我行”、“我行，我青春”为主题，选手们以饱满的感情，洪亮的声音，与在场同学们分享在大学三年的青春应该充分把握，珍惜当下的每一分每一秒，赢得在场观众的阵阵掌声；后一轮的即兴演讲以“钥匙”、“钱”、“透明胶带”、“笔”等这些随处可见的日常生活用品为题，让选手随机抽取并发表演讲。有的选手分享了他的亲身经历：曾经为了坚持创业赚钱的梦想与父母产生了争执，创业的过程让他认识到了赚钱的艰难，因而更加珍惜现有的生活；有的选手则借“钥匙解锁”的概念，回忆了往昔自己被挫折打败不能振作，因为知己的启发而解开心锁，重新出发的历程……强大的现场反应能力，积极向上的演讲内容，自然流露的真挚感情，让在场的同学们也颇有感触。经过激烈的竞争，物联网专业的何泽君同学凭借他稳健的台风、流利的语言和精彩的演讲内容夺得了本次比赛的第一名，成为了当之无愧的超级演说家，此次比赛在热烈的气氛中落下了帷幕。', 'http://info.jhc.cn/2016/1103/c201a71230/page.htm', 'http://info.jhc.cn/_upload/article/images/51/f0/eb091ba242ac9c6950f95c9293fd/5cbd8d49-20c0-49e1-9b60-79ff321565b9.jpg', '2017-05-17 14:13:20.641119');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (96, '丰辉', '学工办/团委', '周荃', '2016-11-02 00:00:00', '信息学院举办“婺文化”进校园知识竞赛', '“金华的市花是什么？”、“金华戏叫什么？”、“金华江又名是什么？”。为了更好的让新生了解金华这座具有深厚历史文化底蕴的城市，同时也为了丰富新生的校园生活。近期，信息学院学院通过团委微信公众平台发布“婺文化”知识竞赛题，在全院师生范围内举行“婺文化”进校园知识竞赛。共25题，要求在20分钟内完成答题，最终根据个人答题时间长短和答对题目数系统自动排名。学院共500余名师生参与了在线答题，共评选出一等奖5名，二等奖25名，三等奖30名。据悉，信息学院开展婺文化进校园活动，一直定位于“婺州文化的当代传承”，还通过班团政治课、主题班会、新媒体平台、“跟着学姐走进金华物质文化遗产和非物质文化遗产”等活动来推广婺州文化，对在金学子进行长期的、潜移默化的文化教育，让在金学子在浓郁的文化氛围中传承“婺文化”。', 'http://info.jhc.cn/2016/1102/c201a71181/page.htm', NULL, '2017-05-17 14:13:20.662836');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (97, '周荃', '办公室', '周荃', '2016-11-01 00:00:00', '信息工程学院党委理论中心组专题学习十八届六中全会精神', '11月1日下午，信息工程学院理论中心组召开专题会议，学习传达了十八届六中全会精神并部署了相关工作。十八届六中全会聚焦全面从严治党重大问题，以制定新形势下党内政治生活若干准则为重要议程，特别强调要增强全社会的创造活力和团结和睦。全体成员认真学习了《中国共产党第十八届中央委员会第六次全体会议公报》内容及精神解读，讨论中，大家一致认为，全体领导干部特别是党员领导干部要提高党性修养和政治担当，积极响应党中央的号召，“努力使劳动者人人有知识，个个有技能”，立足本职做好职业教育。据悉，信息学院党委安排部署了十八届六中全会精神的学习贯彻工作，通过党建抓手促进学院“十三五”规划实施，以优异成绩迎接党的十九大召开。', 'http://info.jhc.cn/2016/1101/c201a71073/page.htm', NULL, '2017-05-17 14:13:20.685098');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (98, '叶继阳、宣翠仙', '信息化事业部', '周荃', '2016-10-31 00:00:00', '我校赴福建考察VR产业应用及体验中心建设情况', '为推动我校2017年虚拟现实相关实训室项目的建设，10月27日-28日，教务处处长刘鲁平教授、信息学院院长陈海荣教授及计算机应用技术专业相关教师一行赴福建考察网龙网络公司总部VR产业基地。我校一行首先参观了网龙网络海西基地VR体验中心，通过VR/AR沉浸式设备现场体验了VR在创客教育、旅游资源分发、动车VR训练、家居设计等方面的应用。随后，我校人员听取了网龙华渔教育副总林丰关于VR产业应用、网龙VR战略、网龙华渔教育“VR+教育”布局的情况汇报。双方就VR体验中心建设、职业资格认证、课程建设、在线资源开发等方面的合作进行了深入交谈，公司表示将全力支持近期在我院科技节中举办VR体验展和VR产业应用讲座。据悉，网龙网络公司是中国网络游戏、VR产业的领军企业，入选全国文化企业30强、福布斯（Forbes）“中国潜力企业榜”、荣登《财富》（Fortune）“卓越雇主”。公司其下网龙华渔未来教育科技有限公司专注教育VR，近年来陆续启动VR教育内容分发平台、VR教育资源库、101VR沉浸教室、101VR创客教育教室等系列项目，并于2016年发布全球首个VR开放平台。我校与网龙网络公司的对接，是应对虚拟现实发展元年产业发展态势，开启我校虚拟现实相关实训室建设项目、虚拟现实课程与资源建设的有力举措。', 'http://info.jhc.cn/2016/1031/c201a71017/page.htm', 'http://info.jhc.cn/_upload/article/images/49/d7/8cf8700f4cb8a03e93b846069a5e/375b99d6-a759-4142-a4f4-0407db3cb259.jpg|http://info.jhc.cn/_upload/article/images/49/d7/8cf8700f4cb8a03e93b846069a5e/50fc6972-950b-402a-a17f-c9d086da3b94.jpg|http://info.jhc.cn/_upload/article/images/49/d7/8cf8700f4cb8a03e93b846069a5e/a208d402-f0ae-4fbb-b9f7-605702b9a035.jpg', '2017-05-17 14:13:20.707331');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (99, '徐露诗', '学工办/团委', '周荃', '2016-10-28 00:00:00', '如何帮助你，我的“问题”学生——信息学院开展心理健康专题学习沙龙', '“共情，就是设身处地地站在学生的角度考虑他所面临的问题”，近日，信息学院组织全体班主任，举办学生心理健康教育主题学习沙龙，邀请校心理健康教育中心专业辅导老师周颖向班主任辅导员传授如何对待“问题”学生的技巧与方法。周颖老师从如何鉴别心理异常切入，从注意点、技巧以及产生心理异常的六种学生类型详细而生动地向班主任辅导员老师们介绍了如何识别需要关注的学生和对待心理异常学生的方法。周老师在沙龙中说道：面对“问题”学生时，老师的态度比方法更加重要，让学生充分地感受到你真诚的态度，他们才能对你敞开心扉；保持中立不批判的态度，无条件地接纳学生，哪怕是在你看来古怪的行为和想法；以及在约谈的过程中，适当地选择开放式问题和封闭式问题，都有助于帮助班主任老师有效地走进“问题”学生的内心，从而真正帮助到他们。同时，周老师还结合了实际工作中所遇到的案例，强调了在帮助与疏导的过程中，班主任老师要充分与学生共情，耐心地探求异常现象背后的深层原因以及不要给学生贴“标签”。生动的案例、实际可行的操作办法、专业可靠的建议，博得了在场所有班主任教师的阵阵掌声。沙龙尾声的互动环节，许多班主任老师争相就日常工作中遇到的实际问题向周老师寻求专业的建议。 “从前遇到有这方面问题的学生，我总是束手无措或者操之过急，今天听了周老师的课，我受到了很大的启发，以后要尝试着换一个角度去看待这些同学”一位班主任这样说道。', 'http://info.jhc.cn/2016/1028/c201a70926/page.htm', 'http://info.jhc.cn/_upload/article/images/34/74/627b3c554d798776b0bedfab6dc5/dbe08c7d-5973-428b-978e-cceb5c2cdcfe.jpg', '2017-05-17 14:13:25.088774');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (101, '丰辉', '学工办/团委', '周荃', '2016-10-27 00:00:00', '金华市学联驻会主席来信息学院“传经送宝”', '10月26日下午，信息学院举行“青春共约，共话学生骨干成长路”活动，第一期邀请到了金华市学联驻会主席周翔、朱音来信息学院交流分享他们的学生干部成长历程，学院63名学生干部参与了座谈交流。朱音主席分享了她个人工作经验，她说“做学生干部一是要有恒心完成看似无望的事，二是有能力发现一些可有可无的事情，三是要有度量去容忍不能容忍的事情。”她还结合自己参加G20志愿者工作的经历，鼓励学生干部要多参与志愿者服务活动，服务社会。周翔主席分享了组织工作的经验，他指出做学生干部要有责任心、要有坚持的勇气、要有团队合作精神和创新精神。并传达了习近平在知识分子、劳模、青年代表座谈会上的讲话精神，鼓励学生干部要做“有理想、有追求、有担当、有作为、有品质、有修养”的六有大学生。最后，由信息学院学生干部就日常管理、新媒体建设、学生服务、学生干部工作方法、理念等多方面存在的问题向市学联主席进行提问，现场气氛非常活跃，两位主席对每一个问题都予以了耐心、详细、毫无保留的解答。会后，参与活动的学生干部与学联主席互留联系方式，希望能够在未来的学习、工作中进一步的交流与学习，提升自身修养与素质，更好的服务同学们。', 'http://info.jhc.cn/2016/1027/c201a70868/page.htm', 'http://info.jhc.cn/_upload/article/images/a9/19/eddd709b4ab3b973fbf1674fd95d/ef266a80-6010-4e54-bf1f-bcb84450e9ca.jpg|http://info.jhc.cn/_upload/article/images/a9/19/eddd709b4ab3b973fbf1674fd95d/ba331eb3-57f7-49c7-844c-3e44a3084c17.jpg', '2017-05-17 14:13:25.141039');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (102, '徐华丹', '教科办', '周荃', '2016-10-27 00:00:00', '总结四年制导师制阶段实施经验，探索新举措', '四年制导师制实施一年来，效果如何？学生的反响又怎样？面对进入大二的学生和新一批的学生，导师制又将需要哪些改变？近日，信息学院召开了四年制导师制研讨会，商讨解决问题。通过学生的调查报告，王成福院长肯定了指导教师和学生的辛苦付出，取得一定的成果。但也存在一些问题，如：部分学生积极性不高，学习态度存在问题，导师制对其影响不大。针对存在的问题，陈海荣院长提出了项目驱动下“1+N”导师制的指导模式改革。从项目任务库开发、选择机制、互动机制、评价模式和评价方法、信息化管理平台等多角度对导师制进行改革。大家针对学生的表现，纷纷对新的导师制指导模式进行讨论，需要在项目的选取、学生评价方法上进行合理的设置，已满足不同层次学生的要求。据悉，针对学生的不同特点和个体差异，导师们将开发从简单到综合，系列化、分层次、多形式的导学项目，明确项目的能力目标和学习成果。为导师制的进一步实施确定载体。', 'http://info.jhc.cn/2016/1027/c201a70867/page.htm', 'http://info.jhc.cn/_upload/article/images/6b/ba/8b6922404ad69094c45a9f363ee2/ba8dee37-8f45-4673-a75e-11c29eebb6ac.jpg', '2017-05-17 14:13:25.163183');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (103, '陈晓龙、陈莉莉', '信息化事业部', '周荃', '2016-10-27 00:00:00', '师生携手，共创和谐信息', '一方有需要，多方来支持，信息学院的老师和同学们很好地展现了满满的正能量。 10月25日下午，信息工程学院东实训ACM实验室因专业布局需要调整到东实训南505室，机房里有36套安装好的桌椅和电脑需要搬迁。由于实训室门框较小，6张连接在一起的电脑桌需要拆开才能搬进去，时间紧任务重，学院青年志愿者大队汤鑫严队长接到任务后紧急派出了10余名队员支援实训中心。在拆装电脑桌过程中需要6角螺丝刀，而当时实验室没有工具，严重影响搬迁任务。了解此情况后，来自应电161的柳佳鑫、李鑫波、翁祥旭三位志愿者立即从相应实验室借来工具，保证了搬迁进度。下课后，软件技术专业15级学生、陆建鹏、花有清、吕焱飞几位等老师也主动加入到搬迁活动中。虽然是临时组队，来自不同部门，但是大家齐心协力、各自分工。有的负责拆桌椅，有的负责装桌椅，有的负责搬迁桌椅、电脑，有的负责搞卫生，配合默契。短短2个小时，就顺利完成搬迁任务，确保第2天教学秩序不受影响。', 'http://info.jhc.cn/2016/1027/c201a70866/page.htm', 'http://info.jhc.cn/_upload/article/images/72/87/d3cb1c624e39b19f66a56923c230/60efc8ee-005c-4a88-8b27-e86dda44d8f8.jpg|http://info.jhc.cn/_upload/article/images/72/87/d3cb1c624e39b19f66a56923c230/ec0a04c8-7520-46ed-8f1c-07f639cf684e.jpg', '2017-05-17 14:13:25.185531');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (104, '丰辉', '学工办/团委', '周荃', '2016-10-27 00:00:00', '谈谈暑期社会实践那些事儿', '“累了，就在高铁站的休息间靠一会，然后继续工作……”10月25日，一场别开生面的暑期社会实践分享会在信息学院火热进行，学长姐们与学弟妹们倾心长谈，分享自己的实践感悟。据悉，为帮助2016级团学新干事熟悉相关工作，提高办事积极性，学院特别邀请两支实践团队传授实践经验与体会。两支实践团队结合视频，与大家分享了各自的实践故事，有的讲述了自己不畏酷暑，配合高铁站人员做好G20安检与服务工作的艰辛历程；有的则向大家述说了科技支教时遇到的感人故事与生活点滴，为316名新生干事们上了社会实践第一课，让大家受益匪浅。据了解，接下来，学院还将组织开展“摄影摄像技巧培训”、“活动策划与管理”等培训，进一步提高干事业务素质和实践能力。', 'http://info.jhc.cn/2016/1027/c201a70865/page.htm', NULL, '2017-05-17 14:13:25.207768');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (105, '徐露诗', '学工办/团委', '周荃', '2016-10-20 00:00:00', '“我是‘室’界的主人”——信息学院举办第九届宿舍文化节开幕式暨宣誓仪式', '“我将以自己的实际行动为身边的同学树立榜样，严格遵守学院宿舍章程制度……”东教传来响亮庄严的宣誓声，19日中午，信息学院第九届宿舍文化节正式开幕式。宿管会副主席为全体新生寝室长详细介绍了学院卫生、夜不归宿以及大功率检查的制度，特别强调了学院制定的二十条卫生标准和夜不归宿的严重性，呼吁寝室长向成员们传达正规的请假流程，共同维护好自己的“小家园”。仪式上，党委书记贾美倩老师勉励大家：寝室长不要小瞧自己，同样也是学院重要的学生干部，要时刻意识到身为学生干部的责任与带头作用。在寝室文明建设工作层层推进的过程中，寝室长与宿管会要发挥作用，带领同学，协助辅导员老师，共同创建一个充满正能量的公寓环境。据悉，本届宿舍文化节的各项活动非常丰富，分别有：“我爱我家”寝室全家福照片大赛、“温馨家园”寝室美化大赛、“废品换盆栽”、“书香进寝室”……一项项活动无不是致力于让全体同学参与到构建一个更安全、舒适且富有特色的居住环境的行动中来。', 'http://info.jhc.cn/2016/1020/c201a70595/page.htm', NULL, '2017-05-17 14:13:25.229968');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (106, '丰辉', '学工办/团委', '周荃', '2016-10-19 00:00:00', '喜迎校庆日，信息学子改编歌曲《一笑倾城》赠与母校', '“金湖上，初晨阳；绿茵场，枫叶黄；你出现，在身旁；回忆开始明朗。。。。。。” 10月18日，喜迎校庆日，这首来自信息学院软件141班方亮同学改编的歌曲，在信息学院微信公众平台上温情上线了。质朴的歌词表达的是信息学子对金职无限的热爱和对新生的祝福，赢得了无数网友的转发和点赞，也激发了不少老校友的金职情怀。据悉，这是方亮同学和他的团队继《金职版青花瓷》后的又一佳作。第二次为金职改编的歌曲，方亮同学说：“我花了两周时间改词，后知后觉体会自己写的词，到后面蛮感动的。”谈到改编这首歌曲的创作背景，方亮表示源于那一点点心酸和不舍。“这学期读完，我就要实习了，我希望能有一些创作来纪念我的大学生活，送给我热爱的大学。刚好今天是校庆日，我们团队选择在今天上线，是非常有意义的。”同时，他还带领团队拍摄的三组校庆版金职美景照，倾注了“笑里倾城，起忆金职；烟雨旖旎，相忆金职；年华静走，最忆金职”的感情。《微微一笑很金职》改编：方亮我走过悄然无声流淌着的旧时光接收到满心期待想要拥抱的梦想这一页还记录了浅浅淡淡的忧伤遇见时你在心上   翻开后书写新章金湖上  初晨阳绿茵场  枫叶黄你出现  在身旁回忆开始明朗微笑扬  做导向时光慢  烟云赏 久盼来到大学心动一场不曾想一见如故写进诗两行也收藏点点滴滴情节多难忘故事的开场散播着缥缈想象我写下希望想和你站军姿当新生的品尝想和你牵着手看花田的芬芳酸和甜突涌上管滋味多荒唐都有你在场你是我青春剧情不动声色的张扬也是我莞尔一笑而去走心的愿望这校园春花秋实可别辜负好时光庭院下紫藤向阳   草地上浅笑轻唱长桥边  柳成行学士路  桂花香你走过风景旁最美是这模样暖风起  碧云上叶挂满  泛微光原来这里校园惹人回望就曾听一笑倾城青春多难忘还记得一见如故写进诗两行故事的收尾泛滥着未知慌张我张开想像想和你站军姿当新生的品尝想和你牵着手看花田的芬芳笑与泪都分享管情节多跌宕我们散场', 'http://info.jhc.cn/2016/1019/c201a70559/page.htm', 'http://info.jhc.cn/_upload/article/images/ad/ca/ae816baf461b94c883f9361c00b4/5c4ee6d1-a08b-4dbc-bb71-19d318aadf70.jpg|http://info.jhc.cn/_upload/article/images/ad/ca/ae816baf461b94c883f9361c00b4/0749cf12-4881-4c72-b205-65deee19e2be.jpg|http://info.jhc.cn/_upload/article/images/ad/ca/ae816baf461b94c883f9361c00b4/cc21d82c-4378-4e3e-85a9-a4083dffca49.jpg', '2017-05-17 14:13:25.252214');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (109, '徐露诗', '学工办/团委', '周荃', '2016-10-18 00:00:00', '金华职业技术学院2016年校庆日活动之徐春华校友《互联网+医疗行业现状与机遇》讲座', '“同学们，这个时代的行业痛点催生了互联网医疗的发展，而两个行业之间的交互性人才正是现阶段所稀缺的”10月15日下午，信息学院在西教1四楼报告厅开展了2016年的校庆日活动，邀请了优秀校友浙江大学工学硕士、MBA、高级工程师徐春华回到母校，为新生们带来以“互联网+医疗现状与发展趋势”为主题的创业讲座。徐春华校友以现阶段医疗行业“看病难、看病贵、医疗纠纷增多”等现实问题切入，详细地介绍了现有医疗环境需要借助互联网改革，突破，重构，并提出参考国外医疗系统的为蓝本，在未来构建出“健康中国”的大构架，同时对在座的计算机、软件等相关专业的新生们提出要求，希望大家抓住这个机遇，把握在校三年时间，寻找到自身的发展目标，并且认真学习专业，在互联网医疗的领域能够有所发展。精彩的演讲，务实的内容，充满希望的就业前景，让在座的同学们兴致勃勃。“入学一个多月，我本来没有认真思考过未来的规划，听完这次讲座，我又对专业多了一份信心，我会认真学习专业，像这位校友一样拥有成功充实的人生”一位软件专业的新生这样说道。', 'http://info.jhc.cn/2016/1018/c201a70505/page.htm', 'http://info.jhc.cn/_upload/article/images/e9/6b/3199365e44c78cab9de5498c7d5b/a563e9d2-f2e7-4291-9664-9406ee51cd94.jpg|http://info.jhc.cn/_upload/article/images/e9/6b/3199365e44c78cab9de5498c7d5b/a2f8e90a-fa96-4ac7-9201-85716c167160.jpg', '2017-05-17 14:13:25.319046');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (110, '徐露诗', '学工办/团委', '周荃', '2016-10-18 00:00:00', '信息学院2016级全体新生召开主题班会：我的大学我的梦', '“青春如同奔流的江河，一去不回来不及道别”东教传来一阵阵优美的旋律，伴着吉他伴奏声、鼓掌声、喝彩声，好不热闹。原来是信息学院正在组织全体新生班级开展主题班会：我的大学我的梦。各个新生班级准备充分，参与热烈：自动化班级将开学初迄今为止的报道、军训、团辅班会以及同学们日常的生活照作为素材，制作了短小的视频，将一个月之间相处的点点滴滴一一呈现，展现了班级的友爱团结、共同进步的班级梦想；应电班级则组织同学们书写关于大学的小梦想，制作大学时光胶囊……而学长学姐们更是使出浑身解数，利用班会时间宣传社团，风行轮滑社社员们展现了轮滑的速度与激情，黑泡乐社请来了本土节目主持人阿楼表演了吉他弹唱，社长更是展现了精湛的拉丁舞，引得同学们惊呼连连，叫好声一片。无论是新生自己还是学长学姐，都用自己的努力点亮了16级同学们的大学梦。“这次的主题班会真的很棒，给了我们一个舞台展现自己，更让对大学又多了一份期待”一位自动化专业的新生这样说道。', 'http://info.jhc.cn/2016/1018/c201a70485/page.htm', NULL, '2017-05-17 14:13:25.343151');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (111, '', '学工办/团委', '周荃', '2016-10-17 00:00:00', '《金华日报》报道我院志愿者“世界粮食日”活动', '爱惜粮食 从我做起昨天是世界粮食日，金职院信息工程学院青年志愿者大队举行“爱惜粮食，文明就餐，从我做起”主题活动。志愿者通过自制KT宣传板，向正在就餐的同学宣传节约粮食的重要性，提倡爱惜粮食，文明就餐。原文链接：http://epaper.jhnews.com.cn/site1/jhrb/html/2016-10/17/content_1762389.htm', 'http://info.jhc.cn/2016/1017/c201a70425/page.htm', 'http://info.jhc.cn/_upload/article/images/b6/ba/d5a36de04792ae54d0fabcea9df2/3db7478c-c46e-4d6e-8a14-050ec4e91fcf.jpg', '2017-05-17 14:13:25.363238');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (112, '周荃', '办公室', '周荃', '2016-10-14 00:00:00', '深圳职业技术学院电子与通信工程学院来我院考察交流', '10月14日上午，深圳职业技术学院电子与通信工程学院副院长王永学一行6人来我院考察交流。学院院领导，办公室、实训室及通信与网络工程部负责人出席了交流活动。座谈中，陈海荣代表学院对客人的来访表示欢迎，并简要介绍了学院的发展状况。宾主双方围绕通信技术、计算机网络技术专业建设、课程体系建设情况等内容进行了研讨，并就相关专业实训室建设、校企合作、教学行政管理等情况作了交流。', 'http://info.jhc.cn/2016/1014/c201a70404/page.htm', 'http://info.jhc.cn/_upload/article/images/d5/65/3e703b4d41c1a1eaaeda26455f8b/b36788ab-a98e-4e47-8a1d-c5b9646ae681.jpg', '2017-05-17 14:13:25.385953');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (113, '周荃', '办公室', '周荃', '2016-10-13 00:00:00', '浙江工业职业技术学院设计与艺术分院来我院考察交流', '10月13日上午，浙江工业职业技术学院设计与艺术分院教学督导室主任陈军一行4人来我院考察交流。学院教学副院长，通信与网络工程部、信息化事业部负责人出席了交流活动。座谈中，赵敏笑代表学院对客人的来访表示欢迎，并简要介绍了学院的发展状况。宾主双方围绕人才培养定位和培养方案制订、创新班运行与管理、教学督导等内容进行了研讨。', 'http://info.jhc.cn/2016/1013/c201a70358/page.htm', NULL, '2017-05-17 14:13:31.587936');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (114, '周荃', '办公室', '周荃', '2016-10-11 00:00:00', '信息工程学院党委理论中心组开展专题学习', '为深入推进“两学一做”学习活动和党风廉政建设工作，10月10日，信息学院理论中心组组织专题学习。成员们认真学习了《苦干实干一心为民——李保国先进事迹报告会发言摘编》，深刻感悟李保国同志从一个学农爱农的青年教师，逐步成长为一名执着于太行山区人民脱贫致富的山区综合治理专家的奋斗历程，感受到他心系人民、无私奉献的报国情怀。大家在讨论中纷纷表示，将以此次学习为契机，扎实推进“两学一做”学习教育各项活动，充分发挥自身的职业优势，在服务人民中寻找和实现自身价值。会上，还就学院党风廉政建设自查和专项检查工作做了部署。', 'http://info.jhc.cn/2016/1011/c201a70286/page.htm', NULL, '2017-05-17 14:13:31.6194');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (115, '周荃', '办公室', '周荃', '2016-10-09 00:00:00', '共聚茶话会 暖暖重阳节', '又逢重阳佳节，我院举行离退休老同志茶话会，四十余位老同志与学院新领导班子欢聚一堂。会上，学院党委书记贾美倩向老同志们致以节日的问候，就学院各项工作的开展情况她感谢大家一如既往地关心和支持学院的建设和发展，希望老同志们继续为学院的各项工作建言献策，并祝大家身体健康，安度晚年。院办主任陈德仙还将工资增资部分作了介绍，外教代表也到场对老同志们送上洋气的祝福。老同志们在会上积极发言，感谢学院一直以来对大家的关心和照顾，祝愿学院的明天更加美好。', 'http://info.jhc.cn/2016/1009/c201a70199/page.htm', 'http://info.jhc.cn/_upload/article/images/39/21/81583bc64526b92360b350362eb5/8440061c-92f1-4aeb-a552-bf6a0b90b62d.jpg|http://info.jhc.cn/_upload/article/images/39/21/81583bc64526b92360b350362eb5/5f7d61c6-355d-4e11-99c2-ee11464f4eed.jpg', '2017-05-17 14:13:31.6431');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (250, '周荃', '', '系统管理员', '2015-12-23 00:00:00', '王成福副院长撰写论文《潜心高职研究，提高履职能力》荣获盟省委征文二等奖', '近日，学院副院长王成福撰写论文《潜心高职研究，提高履职能力》荣获民盟浙江省委员会举办 “加入民盟为什么、历史责任是什么、我为浙江做什么”大讨论活动征文二等奖，为高职教育改革与发展做好建言献策工作树立了典型。', 'http://info.jhc.cn/2015/1223/c201a54613/page.htm', NULL, '2017-05-17 14:14:17.924762');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (117, '吕威威', '学工办/团委', '周荃', '2016-09-30 00:00:00', '信息学院新生校园定向赛', '“我到最后一个点了，东教学楼，完成啦！”只见几名同学手拿地图，飞奔进东教学楼内，原来是信息学院正在进行始业教育场外测试，“新生校园定向赛”在校园内火热开跑。在短短的半个多小时里，校园里上演了一场精彩纷呈的角逐大赛。据悉，本次比赛共有新生800余人参加。本次活动在东教学楼设置了起点和终点，共有两条路线，环绕整个校园，每条途中都设置了10个签到点，包含图书信息大楼、后勤服务中心、大学生活动中心、体育馆等校园标志性地点。尽管天公不作美，但可爱的新生们热情高涨，循着地图去探访那些“神秘地点”，校园内四处可见他们开心的笑脸和奔跑的背影。很多完成赛事的同学在签到处与工作人员留下了属于他们的至美一拍，还有同学气喘吁吁地说：“虽然大雨磅礴，但是很开心！”据了解，作为信息学院新生始业教育的场外测试，“新生校园定向赛”兼具趣味性和实用性，它让新生们在娱乐中熟悉校园环境，为今后的大学生活提供帮助！', 'http://info.jhc.cn/2016/0930/c201a69717/page.htm', 'http://info.jhc.cn/_upload/article/images/3f/41/1881819846229c2ddefdaa48a01e/0c11565e-2704-47bf-a9ed-15a86070c509.jpg', '2017-05-17 14:13:31.686155');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (118, '王伟斌、陈晓龙', '信息化事业部', '周荃', '2016-09-28 00:00:00', '信息学院信息化事业部项目工作室建设成效初显', '9月27日，金华市天下谱局信息发展有限公司与信息学院信息化事业部合作开发的“识字求助”软件项目进行初期汇报验收工作，项目开发小组介绍了项目的执行情况，参与验收人员针对汇报情况提出了整改要求，预计项目在年底前能够如期交付使用。这是信息化事业部项目工作室的又一重要成果。据悉，信息学院信息化事业部探索软件人才培养新途径，通过成立项目工作室，承接校企合作软件企业的软件开发项目，将有较好软件专业知识的学生组织起来，由项目部教师进行指导。能够在毕业之前即参与实际软件项目开发，从而学以致用，使学生得以提前熟悉软件开发流程、方法，为今后就业打下更坚实的基础，并通过传帮带的方式，由这些学生将相关经验介绍给下一届学生，从而实现知识、能力的传承。通过这一模式，目前信息化事业部已经与致维·科维城信息科技有限公司、金华路灵灵物联网科技有限公司、金华市亿博网络科技有限公司等软件企业共同开发了多个项目，反响极好，取得了学生学到知识和经验、教师获得课堂教学项目、校企实现深度合作的多赢结果。', 'http://info.jhc.cn/2016/0928/c201a69363/page.htm', 'http://info.jhc.cn/_upload/article/images/c4/f7/25a12134421db7b3375542c3946b/b34a4d31-35d0-4132-b189-1e9d2229665a.jpg', '2017-05-17 14:13:31.708212');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (119, '徐露诗', '学工办/团委', '周荃', '2016-09-27 00:00:00', '信息工程学院举办2016级新生始业教育暨迎新晚会', '动感十足的舞蹈、悦耳轻快的歌声……实验剧场的掌声与喝彩声一阵盖过一阵，原来是信息工程学院正在举行2016级新生始业教育暨迎新晚会。晚会的节目可谓精彩纷呈，开场的一段荧光舞和机械舞引得同学们惊呼连连；学长演唱的金职改编版《青花瓷》，配上了校园风景作为背景，不禁让在场的新生们沉醉；更不用说走心的校友演讲：软件专业毕业生奚东耀，现在已经是上海某公司的正式员工，毕业一年，年薪已达20余万，更是给新生们注射了一剂强心剂，让同学们对未来的大学生活更加充满了信心；学院还为全体新生们准备了一份神秘的礼物——迎新视频，当大家惊奇地发现镜头下自己的身影，观众席中时不时爆发出一声惊呼或者欢笑……最终，在学生代表的引领下，全体668名新生庄严的宣誓，而晚会也在誓词中缓缓落下帷幕。“感谢学院为我们安排这么精彩的晚会，我们也一定会在三年里认真学习专业，不辜负学院对我们的期待。”一名新生如是说。', 'http://info.jhc.cn/2016/0926/c201a69282/page.htm', 'http://info.jhc.cn/_upload/article/images/fa/a6/604f242749c789c0912bde32ae39/3a83536d-e295-4b7d-9560-7b15df7d69af.jpg|http://info.jhc.cn/_upload/article/images/fa/a6/604f242749c789c0912bde32ae39/dfa97ddd-9a07-4d16-a375-43947fec921c.jpg|http://info.jhc.cn/_upload/article/images/fa/a6/604f242749c789c0912bde32ae39/286ae3bd-cc8c-4ba1-9c6c-568c2425cbd3.png|http://info.jhc.cn/_upload/article/images/fa/a6/604f242749c789c0912bde32ae39/de5f8bb9-5725-4a31-8301-92bbefd9c3f0.jpg|http://info.jhc.cn/_upload/article/images/fa/a6/604f242749c789c0912bde32ae39/aece2460-076d-4703-9606-e1a1d92fdf0d.jpg', '2017-05-17 14:13:31.730465');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (120, '俞冬丽', '继教部', '周荃', '2016-09-26 00:00:00', '第46次全国计算机等级考试在信息工程学院圆满落幕', '9月24日，第46次全国计算机等级考试在信息工程学院圆满落幕。本次考试共有考生889人，其中一级考生626人，二级考生260人，三级考生3人。受金华市考试院的委托，信息学院承担了本次考试的重任。今年6月就开始从考试报名、现场确认、考试考务、计算机系统维护、试场监控等各个环节进行了全面系统的部署。本次考试为期一天，16个批次分4个考场同时进行，考务人员做好服务工作，监考老师履行好职责，严肃考场纪律，认真监考，将各种可能出现的问题处理在萌芽状态，确保了万无一失，考试组织实施的各环节零差错。据悉，本次考试金华市只设金职院信息学院一个考点，学院高度重视，认真负责，高质量完成了本次考试任务。', 'http://info.jhc.cn/2016/0926/c201a69279/page.htm', NULL, '2017-05-17 14:13:31.752852');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (121, '徐露诗', '学工办/团委', '周荃', '2016-09-26 00:00:00', '信息学院举办特色专业交流会', '夜晚的金职校园十分静谧，但东教与科实南楼却人声鼎沸，好不热闹，原来是信息学院各专业正在开展各专业新老生交流会。为了让新生尽早对所在的专业有所了解，学院在始业教育期间，安排各个专业的优秀学长学姐们，围绕专业特点、课程安排以及未来就业形势等方面，与新生进行讨论交流。学长们使出了浑身解数，自动化专业安排新生们参观了三菱啤酒生产线实验室，演示了啤酒生产线的工作流程；软件专业则带领新生们使用了最新的苹果机房，让同学们尽情领略了最新的软件编程技术；计算机专业则为同学们带来了最新的VR（虚拟现实）技术，其中特别为中新合作班的同学们介绍了出国留学的要求和资助政策……交流会中，先进尖端的设备、良好的就业前景、耐心可亲的学长们，都不禁让新生们直呼“高大上”，会后，还有不少同学围在学长旁边继续讨论。 “原本我对大学生活没有明确的规划，经过和学长的交流后，我非常渴望能够通过三年的努力，完成前往新西兰留学的梦想。”一位中新合作班的学生这样说。', 'http://info.jhc.cn/2016/0926/c201a69276/page.htm', 'http://info.jhc.cn/_upload/article/images/7f/71/b5f76b6b4dc7a65af92fe65e7c9f/24345a1f-ac10-4107-9cde-cc32ee56e332.jpg|http://info.jhc.cn/_upload/article/images/7f/71/b5f76b6b4dc7a65af92fe65e7c9f/14889760-63d1-469d-b7ad-05faccc59c7e.jpg', '2017-05-17 14:13:31.774985');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (122, '吕威威', '学工办/团委', '周荃', '2016-09-26 00:00:00', '信息学院举行“新生杯”拔河比赛', '“一二三！加油！一二三！加油！”东实训楼前的加油声、呐喊声，好不热闹！原来是信息学院正在进行“新生杯”拔河比赛，全体新生纷纷以极大的热情参与其中。当比赛的哨声一响起，队员们使出浑身力气把绳子向后拉，绳子中间的红线左右摇摆。两边队员都整齐地往后倒、向后退，班主任也在队伍旁边助阵加油，恨不得抓起绳子和学生一起努力，啦啦队此时竭力大喊“加油”！号子声、呐喊声，此起彼伏，飘荡在整个广场上空。最终，网络161班过关斩将，拿下桂冠！据悉，信息学院连续多年举办此项活动，是始业教育的一项重要内容，不仅促进各班级之间的交流，增强学生的集体荣誉感，更培养了学生的团队协作能力。', 'http://info.jhc.cn/2016/0926/c201a69272/page.htm', 'http://info.jhc.cn/_upload/article/images/c8/92/f8292aeb4dea9df6d62296fb8998/299d208e-ad5f-459c-9bec-40023f1d91cd.jpg', '2017-05-17 14:13:31.797136');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (123, '王伟斌、陈晓龙', '信息化事业部', '周荃', '2016-09-23 00:00:00', '信息学院计算机支部结合“十三五”规划开展“两学一做”学习活动', '9月22日，信息学院计算机支部召开“两学一做”学习活动，全体教师和学生党员参加。本次学习的主题为“能否践行社会主义核心价值观，守住政治底线、法律底线、道德底线，做一名合格党员”。在热烈的学习讨论中，支部党员认为大学作为培养社会主义事业建设者和接班人的重要阵地，应把培育和践行社会主义核心价值观作为新时期社会主义大学的重要使命。大学培育和践行社会主义核心价值观应探寻多种路径。学习活动中，支部还结合部门“十三五”规划内容，提出了要以学科竞赛、工作室等形式带动人才培养和学科发展的思路。通过巩固原有的ACM竞赛、移动互联竞赛、多媒体竞赛等内容，还要积极拓展新的竞赛内容，力争在国赛等方面有新的突破，并以赛带教，以赛促学，为专业发展服务。另一方面，要积极利用还工作室机制，进行分流培养，满足不同学生的学习需求，通过校企合作等形式，使学生的专业学习更具主动性，更具实战性。', 'http://info.jhc.cn/2016/0923/c201a69242/page.htm', 'http://info.jhc.cn/_upload/article/images/fc/70/f3e7ca244ea7bf8f0a0b97ee9dd2/cd6f45c5-bd48-4e58-80e3-38780e327cc5.jpg', '2017-05-17 14:13:31.81947');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (124, '周荃', '办公室', '周荃', '2016-09-23 00:00:00', '我校举行四年制高职电子信息科学与技术专业2016级新生开学典礼', '9月22日下午，我校隆重举行浙江省四年制高职教育人才培养试点——电子信息科学与技术专业（电力电子技术方向）2016级新生开学典礼。浙江理工大学党委副书记周文龙，校长钟依均，两校相关职能部门及二级学院负责人、50名新生参加开学典礼。     典礼现场   校长钟依均致辞  典礼在雄壮的国歌声中拉开帷幕。钟依均首先致辞，代表学校对新生们的到来表示欢迎，对周文龙一行的到来表示感谢。他说，去年5月，我校成为浙江省首批四年制高职人才培养试点院校。一年来，在浙江理工大学的指导下，试点工作进展顺利，在学生精神风貌、学习态度、团队合作、师生关系、学生对学校的认同等方面都显现出良好的开端。钟依均希望新生们一要珍惜机会，肩负使命，当好一名先行者和探路人，为学弟妹们树立榜样；二要融入大学生活，学会独立、自学、合作与关爱，在新的求学阶段做好选择，为自己的目标拼搏；三要尽快适应大学的学习模式，自觉自律、主动有效管理时间；四要珍惜校园生活，加强身体锻炼，学习之余积极投身校内外的社会实践活动，以饱满的姿态投入到大学生活中。     浙江理工大学党委副书记周文龙致辞  周文龙说，注重创新创业教育是浙江理工大学的鲜明特色，也是让两所院校走到一起的共同信仰和追求，双方共享师资、课程、教学设施、校内外实习实训场所等优质教学资源，共同培养高端技术技能人才。周文龙对新生们提出三点希望：一要勇担责任，充分认清自身肩负的历史使命，做好职业教育改革的先行者和探路者；二要从小事做起，发扬工匠精神，成为素质高、能力强、技艺精的优秀人才，利用好两校合作提供的平台带来的便利；三要文理兼修，不断提升自身的综合素质，大学学习是知识不断积累、人生境界不断提升、气质气韵不断升华的旅程，要利用大学时光，将自己锻炼成适应现代社会需要的复合型人才。教师代表廖任秀、老生代表何峰和新生代表毛建清先后发言。据悉，自去年项目启动以来，学校先后成立了试点工作领导小组和高职本科教学部，从专业建设、教学改革和学生管理与服务等方面精心安排，选拔优秀教师担任教学工作，重视课程教学的反馈及改进，不断提高课程教学效果，并根据学生的学情和个性特长，积极探索“多元结合、分层递进”的人才培养模式，通过试行项目化教学、多元导师制、小班化教学、分层分类教学等措施，不断提高学生的实践与创新能力。2015级学生第一学期英语B级通过率达到96%，在电子设计、智能车等省级及以上科技竞赛中获得二等奖3项、三等奖2项，获奖学生达15人次，教育教学取得了良好效果。', 'http://info.jhc.cn/2016/0923/c201a69209/page.htm', 'http://info.jhc.cn/_upload/article/images/d3/cc/b22942964cc9801bce0049e11400/38e7b3a8-11a8-45c9-9a03-eefe0aa296a9.jpg|http://info.jhc.cn/_upload/article/images/d3/cc/b22942964cc9801bce0049e11400/b3b70906-e81a-47e4-8249-46001c9f934f.jpg|http://info.jhc.cn/_upload/article/images/d3/cc/b22942964cc9801bce0049e11400/95bb42dc-4f16-471b-b458-4d026a0feb07.jpg|http://info.jhc.cn/_upload/article/images/d3/cc/b22942964cc9801bce0049e11400/fac908ba-b70e-45bd-af3f-26121ddee36a.jpg', '2017-05-17 14:13:31.841683');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (125, '', '宣传部', '周荃', '2016-09-23 00:00:00', '我校举行怀卡国际学院第二届新生开学典礼', '9月18日上午，我校隆重举行怀卡托国际学院第二届新生开学典礼。新西兰怀卡托理工学院校长马克，董事亚伦、桑德拉，副校长大卫、盖伊，校长钟依均出席开学典礼，中新两校相关职能部门负责人，信息学院负责人、外籍教师、全体新生参加典礼。典礼由副校长、怀卡托国际学院院长杨艳主持。     开学典礼现场  校长钟依均致辞钟依均首先致辞对马克一行的到来表示欢迎。他说，怀卡托国际学院办学一年来，中新两方学校领导高度重视、互访频繁、沟通顺畅，双方教师和管理人员努力工作、协作融洽，办学水平取得长足进步。钟依均希望同学们尽快融入大学生活，学会独立、学会照顾自己，在寝室、班集体中要学会宽容、学会团队合作；尽快适应教学模式，养成自主学习的习惯，多与教师沟通交流，同时重视提高自己的英语水平，为今后继续学习深造做好准备；珍惜校园生活，养成良好的身体素质和心理素质，在成长中树立切实可行的阶段性目标并为之拼搏，早日成为具有国际竞争力的电子信息技术领域的栋梁之才。‍‍‍‍‍‍  新西兰怀卡托理工学院校长马克讲话马克代表新西兰怀卡托理工学院对新生们的到来表示欢迎。他说，科技的发展给整个国际化与经济带来了非常大的冲击，也对未来学生的技能需求带来了新的要求与挑战，这需要大家在国际教育方面不断更新与改变。马克希望借此合作项目，促进新中两校的相互交流与学习，共享优质的教学资源，推动双方教育事业再上新的台阶，同时，希望学生通过学习，涉猎到两个不同国家、学校的不同教学理念和方法，成长为一名能从容解决各种社会挑战问题的全能型国际人才。最后，马克衷心希望下次还有机会再来金华，共同见证和庆祝新生学业上的成功与收获。中新教师代表朱墨池、克雷格以及新老学生代表周睿、江程涛先后发言。据悉，怀卡托国际学院2016级新生共计 217 人，其中，应用电子技术专业53人、计算机网络技术专业 82人、计算机应用技术专业82 人，学生总数已达385人。', 'http://info.jhc.cn/2016/0923/c201a69199/page.htm', 'http://info.jhc.cn/_upload/article/images/87/d4/bc50487f42d4aac09e496f01588f/3be65a35-14cc-4cf5-9f2d-00a5c81112a5.jpg|http://info.jhc.cn/_upload/article/images/87/d4/bc50487f42d4aac09e496f01588f/927029ec-3a50-486f-bdae-ddd31c0404e9.jpg|http://info.jhc.cn/_upload/article/images/87/d4/bc50487f42d4aac09e496f01588f/51eab683-25ba-439d-97f2-3322d55778fa.jpg', '2017-05-17 14:13:31.864094');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (126, '徐露诗', '学工办/团委', '周荃', '2016-09-21 00:00:00', '退伍兵哥哥来报到 带领室友打造新一代网红寝室', '毛巾竖着对折悬挂在架子上，开口朝窗；牙杯从大到小依次排列，牙膏在左，牙刷在右，杯子有把手的要朝外……看到如此细致整洁的寝室卫生标准，这难道是在部队吗？并不是，这是信息学院龙川北苑04南男生寝室151房间的新面貌。这原本只是一间普通的男寝，但是最近它变得不再普通，因为它迎来了新主人——软件163班的林王未同学，曾经他是软件专业的同学，在14年时光荣入伍，两年后，他退伍复学，重新成为信息学院的一份子。他入住寝室后，把军队的标准也带到了寝室里，从毛巾悬挂的朝向、纸巾盒的摆放位置等方面都做了精细而全面的规定。在他的带动下，寝室焕然一新，整洁有序，室友们把卫生标准配图发到了网上，短时间内就达到了几千次的访问与转载，网友们纷纷留言，认为寝室干净清爽，彰显了新一代大学生的精神风貌，值得在大学推广，而151寝室俨然成了新一代的网红寝室。在校领导走访新生寝室时，这间网红寝室也受到了以钟依均校长为首的各个领导的亲切问候，钟校长对寝室的卫生情况高度赞扬，希望同学们能够继续保持下去。', 'http://info.jhc.cn/2016/0921/c201a69136/page.htm', 'http://info.jhc.cn/_upload/article/images/ea/fd/68d98ed6490789d9f9522169591f/d1a070db-7073-4895-a326-6340cc94abcc.jpg|http://info.jhc.cn/_upload/article/images/ea/fd/68d98ed6490789d9f9522169591f/d18935cd-c68e-45b7-a947-5391c4aadf31.jpg', '2017-05-17 14:13:31.886254');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (127, '郭天鹏', '学工办/团委', '周荃', '2016-09-20 00:00:00', '校长钟依均夜访我院2016级新生', '9月19日晚7点，校长钟依均一行在相关职能部门负责人的陪同下，走访新生公寓看望我院2016级新生，欢迎同学们加入到金职院大家庭，齐心协力，共创美丽金职。钟依均在走访我院新生寝室时，亲切了解了新生的生活情况和家庭情况，勉励新生既要学知识和技能，也要重视自身素养的提升，既要读好书，也要学会做人和做事。走访中，有3个寝室都有当兵退伍回来的学生，他们军队化的内务整理规范得到了钟院长的肯定，他还勉励两位退伍复学的男生，要将部队优良作风带到学校，带领同学抓好组织性和纪律性。同时，校长钟依均希望新生能不怕苦，不怕累，学好专业知识，把握好就业前景和就业方向。同学们表示会认真做好职业生涯规划，决不辜负学校、家长对他们的期望。', 'http://info.jhc.cn/2016/0920/c201a69064/page.htm', 'http://info.jhc.cn/_upload/article/images/6d/f5/ded774e54df5b5d31dc53c655a64/4b2673e2-cf79-4bdb-856d-f32de15993ab.jpg', '2017-05-17 14:13:35.875603');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (143, '', '', '周荃', '2016-07-06 00:00:00', '信息学院召开2016年“六个一批”项目建设汇报会议', '根据“六个一批”项目进程安排，学院于近日召开了“六个一批”项目建设汇报会。会上，各位项目负责人展示了课程需要建设的内容和建设的进度，展示了教学大纲、PPT、习题集、微视频等各项教学资源和Moodle平台建设使用情况。通过此次汇报，大家觉得“六个一批”项目建设任务艰巨，院领导就如何提高项目汇报水平进行指导。据悉，下半年“六个一批”项目将重点完善Moodle平台的建设和使用，学院将以此作为项目检查的依据。', 'http://info.jhc.cn/2016/0706/c201a56295/page.htm', 'http://info.jhc.cn/_upload/article/images/9d/03/6d7180d5448c9eeb9ea3049e41c5/b9929213-f44e-46d6-a8cf-c45cc6ea2d39.jpg', '2017-05-17 14:13:40.920357');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (128, '徐露诗', '学工办/团委', '周荃', '2016-09-20 00:00:00', '信息学院新生首次主题班会：用团辅来一场破冰之旅', '“你叫余航淇，杭州人！哈哈，我赢了”“你是鲍亚星，兰溪人”信息学院首次主题班会上，同学们的声音此起彼伏，好不热闹。原来是新生班主任们，正在组织“心脏病”团辅游戏，同学们反响热烈，兴趣高涨。为了帮助同学们平稳度过适应期，班主任们希望借助团辅的魅力，让新生们拉近距离，建立班级规范，并为未来的友善、信任的团队气氛打下基础。本次班会主要为两个团辅游戏，分别是“心脏病”与“幕布猜人”。“心脏病”主要是6名同学们组成小组，由主持人随机发牌，发到两张相同扑克牌的成员迅速说出对方的姓名，率先报出姓名的一方则为赢家；“幕布”游戏则是两个小组各自挑选一名成员蹲在幕布两端，幕布放下，在各自组员的提醒下快速报出对方的姓名。新奇的形式，有趣的游戏，激发出了同学们的热情与积极性，在班主任和班助的组织下，同学们全情投入，教室里时不时传出一阵阵欢笑声。游戏结束后，班主任还组织同学们分享了参与的感受，并在全班同学的见证下共同签订了班级公约。一位新生在班会结束后说：经过这次班会，本来陌生的同学一下子熟悉了起来，很期待和这些可爱的新朋友一起走过未来的三年时光。', 'http://info.jhc.cn/2016/0920/c201a69061/page.htm', 'http://info.jhc.cn/_upload/article/images/ca/39/dd2549b54d7f97aacfdc183dc7d8/aaad34c2-4294-4658-8f49-d505484aa76a.jpg|http://info.jhc.cn/_upload/article/images/ca/39/dd2549b54d7f97aacfdc183dc7d8/f0f89213-181a-414f-8924-6621a0c7d537.jpg', '2017-05-17 14:13:35.908819');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (129, '', '办公室', '周荃', '2016-09-19 00:00:00', '信息学院召开2016级四年制高职学生家长座谈会', '为进一步做好四年制高职专业人才培养工作，加强学校和学生家长的沟通与联系，加深2016级新生家长对学院和四年制高职专业的了解和认识，学院可以充分倾听家长对迎新工作和学院人才培养工作的期望和建议，信息学院于9月18日下午在东实楼北楼207会议室召开新生家长见面座谈会。15位新生家长代表，学院王成福副院长和应电专业主任余红娟老师参加了座谈交流。座谈会上，王成福副院长就金职院的办学历史，信息学院的发展规模、专业设置、师资队伍、办学条件、国际合作与交流、学生培养与就业等方面做了介绍，重点对四年制高职专业批办、学院和浙江理工大学合作办学情况、专业面向就业领域、实践教学条件、多元导师制实施和学生学科技能竞赛获奖等进行了详细介绍。他告诉家长们，在学校和浙江理工大学大力支持下，学院非常重视四年制专业人才培养工作，聘请浙江大学、浙江理工大学、浙江师范大学、浙大城市学院的多名专家教授以及学院最好的老师给学生上课，组织学院优秀教师团队，分小组对学生进行项目导师制培养，部分学生知识技能提升很快，且养成了良好的学习习惯。3位本科学生在2016年8月参加省第六届大学生电子设计竞赛荣获本科组二等奖1项、三等奖1项，充分展示了信息学院推行“多元导师制”教学模式对提升本科人才培养质量的成效。最后，王院长对学生家长关心的英语四级证书要求、出国交换生、继续攻读硕士学位、学生就业前景等一一进行了答复，并告诉家长学院会十分关心孩子在大学期间每个阶段的成长，一定会与家长一道共同培养优秀的下一代，希望家长们放心。学生家长们真切的感受到了学院的热情和诚恳，对学院育人环境表示满意。本次座谈会的举办，进一步完备了学院迎新工作程序，加强了本科教育的家校联系工作，为本科生思想政治教育工作深入开展，进行了新的探索和实践。', 'http://info.jhc.cn/2016/0919/c201a68781/page.htm', 'http://info.jhc.cn/_upload/article/images/8e/38/9f4538df4e36b7e3b9ec52c71ea2/c6df91ba-3f23-47ab-8fb7-27efc34f4362.jpg', '2017-05-17 14:13:35.930944');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (130, '徐露诗', '学工办/团委', '周荃', '2016-09-19 00:00:00', '“我来金职了”——信息学院开展特色迎新', '“同学，请问你是信息学院的新生吗？”随着一声声亲切的问候，9月18日，信息学院的特色迎新拉开帷幕，而热情的志愿者们早已准备就绪，迎接2016级的小鲜肉们。为了更好地迎接远道而来的新生及其家长们，学院进行了一系列精心的安排：东教大厅悬挂着巨幅海报，上面拼凑了200余张新生的笑脸，一张张青春洋溢的脸庞拼凑成了一颗巨大的爱心，不仅展示了新生的风采，也传达了学院真挚的欢迎之情；时光胶囊，邀请同学们给三年后的自己写一封信，学院代为保存，等到毕业之际来临再返还给同学们；特别的志愿者，学院安排了全国十佳社团——风行轮滑社的社团成员们，穿着轮滑鞋在东教的门口为新生们指引道路……新生们在穿着轮滑鞋的志愿者的指引下，一走入大厅发现照片墙都惊喜不已，马上去寻找自己的照片，并拉着家长和志愿者们一道，留下了进入大学的第一张珍贵的照片，在书写时光胶囊时，信纸上一笔一划记录下的都是同学们对三年大学时光的期待与希望。面对亲切的志愿者，真诚热心的班主任，新生们都顺利地完成了报道手续，其中一位新生说：没想到学院为了迎接我们花了这么多的心思，让我心里感受到了学院的温暖，并让我对未来的三年充满了期待。', 'http://info.jhc.cn/2016/0919/c201a68780/page.htm', 'http://info.jhc.cn/_upload/article/images/b0/b6/78a53be44b62996d25dda16c66c7/0e77c5f8-79b3-439a-b694-a337a12648a4.jpg|http://info.jhc.cn/_upload/article/images/b0/b6/78a53be44b62996d25dda16c66c7/b73bbf56-424b-4543-9313-b3ec2e8f8bb6.jpg|http://info.jhc.cn/_upload/article/images/b0/b6/78a53be44b62996d25dda16c66c7/a7e4209c-2631-452c-8255-02cb3100fcec.jpg', '2017-05-17 14:13:35.95504');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (131, '陈德仙、蔡金晶', '办公室、教科办', '周荃', '2016-09-19 00:00:00', '怀卡托国际学院首届赴新夏令营活动顺利闭营', '近日，怀卡托国际学院首届赴新夏令营活动顺利闭营， 11名同学亲赴新西兰汉密尔顿，和新西兰怀卡托理工学院师生一起，“携手同创，共话未来”。怀卡托国际学院首届赴新夏令营活动由信息工程学院主办，活动为期12天。参加首届夏令营活动的学生分别来自应用电子技术、计算机网络、计算机应用三个中新合作专业。所有的同学都是第一次赴新西兰，大多数同学都是首次走出国门。　夏令营活动内容丰富多彩，毛利传统文化欢迎式、英语翻转课堂、校园寻宝游戏等精彩活动贯穿整个营期，参加夏令营的学生还参访了新西兰汉密尔顿的博物馆、汉密尔顿花园等著名的文化建筑场馆，获得了怀卡托理工学院的学习证书及纪念T恤，深切体会了新西兰怀卡托理工学院“以生为本”的怡人环境、良好学习氛围及新西兰纯净自然的历史文化。访问期间，同学们也准备了精彩的演讲及表演，表达了对怀卡托理工学院热情接待的真诚感谢。不少学生表示，通过夏令营，不仅结识了很多新朋友，拓展了自己的国际视野，激发了同学们出国留学的愿望，对怀卡托国际学院今后的招生及办学具有良好的促进作用。WINTEC校园欢迎仪式欢送会', 'http://info.jhc.cn/2016/0919/c201a68779/page.htm', 'http://info.jhc.cn/_upload/article/images/ae/58/7752311f41748e428b7d3ab7dae3/e115eb7a-1091-45a3-91bb-1931422e8b50.jpg|http://info.jhc.cn/_upload/article/images/ae/58/7752311f41748e428b7d3ab7dae3/03c26e01-bd3d-47b2-a719-7c7033552399.jpg|http://info.jhc.cn/_upload/article/images/ae/58/7752311f41748e428b7d3ab7dae3/4d54fa5e-d893-4575-8cd9-91b8bcaadc06.jpg', '2017-05-17 14:13:35.975278');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (132, '徐露诗', '学工办/团委', '周荃', '2016-09-19 00:00:00', '信息学院组织新生班主任团体辅导培训', '为了帮助新生们平稳度过适应期，信息学院近期组织新生班主任团体辅导培训，希望借由团体辅导的魅力，在第一次班会上，让新生们拉近距离，建立班级规范，并为未来的友善、信任的团队气氛打下基础。培训会上，围绕“破冰”的主题，主要内容为两个团辅游戏——“心脏病”游戏、“幕布猜人”。“心脏病”游戏，心脏病主要借助扑克牌，参加的十位同学在主持人的组织下，由随机发到两张相同扑克牌的成员迅速互相说出对方的姓名，输家需要收下赢家所有的扑克牌，一轮后由扑克牌最多的成员成为小组组长；幕布游戏则是两个小组各自挑选一名成员在幕布两端，幕布放下，由各自成员的提醒下报出对方的姓名。游戏不仅能够很好地调动学生的积极性，并且促使学生熟悉彼此的姓名，并且培养了团体的默契与合作，有利于班级的凝聚与团结。新生班主任们在学工办辅导员的组织下亲身参与到团辅游戏中来，并准备在第一次班会上实施。一位班主任会后说到：这次通过亲身参与体会到了团辅的魅力，希望能够在第一次班会中发挥团辅的效果，让我的班级成为一个团结向上的大家庭。', 'http://info.jhc.cn/2016/0919/c201a68676/page.htm', NULL, '2017-05-17 14:13:35.997804');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (133, '丰辉', '学工办/团委', '周荃', '2016-09-14 00:00:00', '信息学院两支实践队在金华市首届志愿服务项目大赛上喜获佳绩', '暑假期间，由金华团市委、金华市文明办、金华市民政局、金华市8890便民服务中心、金华市志愿者协会共同主办的金华市首届志愿服务项目大赛隆重举行，我校信息学院“百村千户实”实践队的“为民家电维修服务”项目与E.就通新媒实践队的项目“建E+书屋，助农民工子女行动”项目凭借项目的创新性与广泛好评的实践效果，与全市近百余支志愿服务队进行PK，最终荣获两枚铜奖。据悉，这两支实践队是信息学院以当下建设“科技强国”为目标，并结合学生所学专业而组建的。队员们不仅可以巩固自己的专业知识，还能利用专业所学的去帮助他人。该实践队利用寒暑假在金华多个村内建立“E+书屋”，供民工子弟和留守儿童免费浏览，帮助他们完成读书梦！“百村千户实践队”在各大社区和村落设立家电维修站点，免费维修损坏的电器，倡导节约、低碳生活。受到了社会各界的提倡与赞扬。', 'http://info.jhc.cn/2016/0914/c201a68653/page.htm', 'http://info.jhc.cn/_upload/article/images/a1/0c/47565d0141e18218c9af1c51bdb9/601ab6ad-3b78-4f44-a27b-088dc1d04af2.jpg', '2017-05-17 14:13:36.020233');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (134, '俞冬丽', '继教部', '周荃', '2016-09-14 00:00:00', '信息学院技能特色班始业教育显特色', '9月10日，信息学院迎来了116名全日制技能特色班新生。在学院党委书记贾美倩的指导下，顺利完成了第一阶段的始业教育课。课程主要分3天进行，内容包括安全教育、专业教育、主题班会、逛遍校园、日常点点通等内容。报到当天，现场就给每位新生发放了安全告知书，让新生及家长现场加班级QQ、微信群，提高安全意识。专业教育由陈晓龙博士授课，在轻松愉快的互动模式下进行，让同学们了解了即将学习的HTML5、CSS3、JavaScript等核心的Web前端技术、精美的网页制作、便捷的手机应用开发等知识点。主题班会每个班一个主题，以立“规矩”为准。同学们最开心的是逛遍校园环节，花几个小时走遍了校园的角角落落，摸清了校史馆、实验室、体育馆、食堂的具体位置。日常点点通则由班主任助理手把手教新生学会如何冲电费、水费、一卡通等日常事务。新生始业教育作为新生入学后的“第一课”，对培养新生的环境适应能力，缩短角色转换的适应期，使之尽早掌握大学生活的主动权起着非常重要的作用。通过始业教育，学生对今后的学业充满激情和期待。', 'http://info.jhc.cn/2016/0914/c201a68652/page.htm', 'http://info.jhc.cn/_upload/article/images/bb/04/15c0c33343bca3a6e4fa727634a4/48788e23-837f-453b-9d6d-9fefd39cf212.jpg|http://info.jhc.cn/_upload/article/images/bb/04/15c0c33343bca3a6e4fa727634a4/174cf731-9324-4ce9-8c8c-04d7b8f1a9b3.jpg', '2017-05-17 14:13:36.042149');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (135, '郭天鹏', '学工办/团委', '周荃', '2016-09-12 00:00:00', '信息学院召开2016级新生班班主任助理培训会议', '9月11日，学院召开2016级新生班班主任助理培训会议。本次会议是16级新生班班助的第一次集合培训，由学院党委书记贾美倩老师进行指导。会议从专升本介绍、班助职责、部门分工、优秀班助经验分享、思想提升等几个方面对新生班助进行系统培训。贾美倩书记重点介绍了本学期为2016级新生开设的《学生职业素养实践课体验课》和大一新生素质养成教育活动安排，建议班助参照素质养成教育活动安排表合理安排新生班活动。会后，自动化161班助杨晓康说到，“这次培训让我非常实用，让我学到了很多，对我今后的工作会有很大的帮助。”据悉，学院将新生班班主任助理培训。', 'http://info.jhc.cn/2016/0912/c201a68520/page.htm', 'http://info.jhc.cn/_upload/article/images/3b/9b/7b7f54f54b4aa9247f86b2f8a6b6/1aaf9790-d11e-4d82-bae5-048a5ea0b548.jpg', '2017-05-17 14:13:36.064351');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (136, '徐露诗', '学工办/团委', '周荃', '2016-09-07 00:00:00', '信息学院邀请专家开展讲座 共同研讨新学年学生工作', '为促使学院学生工作向精细化方向推进，信息学院围绕“项目化管理为驱动，班级团队建设为载体，推进学生素质养成”主题，邀请浙江大学学工部副部长林伟连博士与农学院教师叶荣晖博士，开展了学生工作专题讲座。讲座中，叶荣晖博士创新式地将传统国学引入班级管理理念，依托班级项目化管理，扎实推进创新创业工作，数年实践中，叶博士的多位学生中成功申报新苗计划，硕果累累；林部长则从领导力角度切入，明确班主任服务型领导者的新型定位，从培养班级管理团队、共建集体愿景等五大方向，向与会的教师们传授了宝贵的经验。讲座的尾声，信息学院院长陈海荣由供给侧改革引发的思考切入，强调了作为班主任要提高服务水平，融洽人际关系，并进一步明确班主任的重要地位；党委书记贾美倩则从“转变观念，树立服务学生的工作理念；以学生成长成才为中心，建立班级工作目标与愿景；精细管理推进学生素质养成”等三方面作出要求，希望从班级团队建设、班级管理项目化、学生素质养成课程化、始业教育年度化、主题班会系列化、文明寝室精细化、晨练制度化等举措入手，推进我院学生工作的进一步发展，为学生素质养成助力。', 'http://info.jhc.cn/2016/0907/c201a68383/page.htm', 'http://info.jhc.cn/_upload/article/images/74/b2/81ca8489424ba1f4cbc2a7fc5cad/56293209-583f-4b1b-8543-0b832fe9343e.jpg', '2017-05-17 14:13:36.086538');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (144, '周荃', '办公室', '周荃', '2016-07-01 00:00:00', '北海职业学院领导一行来我院考察交流', '6月30日上午，北海职业学院领导一行3人来我院考察交流。学院教学副院长，应用电子技术专业主任、实训中心负责人出席了交流活动。座谈中，赵敏笑代表学院对客人的来访表示欢迎，并简要介绍了学院的发展状况。宾主双方围绕专业结构调整及规划经验、应用电子技术专业校企合作、课程体系建设、专业人才培养方案制订等内容进行了深入探讨与交流。', 'http://info.jhc.cn/2016/0701/c201a54728/page.htm', NULL, '2017-05-17 14:13:40.942774');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (137, '郭天鹏', '学工办/团委', '周荃', '2016-09-07 00:00:00', '暨市长点赞我校信息学院G20高铁志愿者', '9月1日上午，金华市市委副书记、市长暨军民来到高铁站，慰问了我校信息学院18名火车站志愿者们。暨市长与汤鑫严握手，汤鑫严是18人团队的队长，面对市长，最让他印象深刻的一段对话，是这么说的：“你是哪里人？”“我是嘉兴人。”“放假了，怎么不回家？”“作为在金大学生，我想为金华铁路安全出一份力，为美丽金华作贡献。”“志愿服务让城市更美好，你们是最美金华人！”金华市团委领导说，“你们这18个人的团队，就像马云的18罗汉，做事有意义，做事有毅力，如果金华的每一个团队都和你们这18罗汉一样，那就一定能成功！”从7月9号到9月7号，离志愿者服务结束不远了，每天6点起来，天还没亮，就从金职院出发，前往火车站为来来往往的乘客提供服务，8点收工回到寝室9点。一天忙下来，连续工作13个小时，两个月来，他们不畏酷暑、风雨无阻，却无怨无悔，默默付出。 这些二十出头的年轻人，稚嫩的脸庞上，却透着一股子坚毅的精气神，他们当中，有的人即便是站得脚起了一个个水泡，也不吭声；还有的人为了引导乘客有序排队，日复一日，嗓子都喊哑了，第二天，含上几片金嗓子喉宝，接着来……这批穿着红色小马甲的小志愿者们，使火车站充满了生机和活力。', 'http://info.jhc.cn/2016/0907/c201a68382/page.htm', 'http://info.jhc.cn/_upload/article/images/df/a1/d18b40134886b27742d29e067023/ad0fa6b5-01bf-4c7a-8da6-bb85333bbb38.jpg', '2017-05-17 14:13:36.108841');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (138, '张小杭、史冬强', '电气自动化工程部', '周荃', '2016-08-13 00:00:00', '信息学子在第十届“三菱电机杯”全国大学生电气与自动化大赛上喜获佳绩', '2016年8月2日-8月5日，第十届“三菱电机杯”全国大学生电气与自动化大赛在天津大学举行，包括天津大学、东南大学等高校在内的40余所高校的66支代表队参加了比赛。由我校信息学院张小杭、史东强老师指导，有15位同学组成的5支参赛队分别代表金华职业技术学院和浙江理工大学参加了“微电网控制”、“伺服定位随动控制”两个项目的竞赛，共取得了两个二等奖、两个三等奖的好成绩；特别是，刚刚才是大一的三位本科生钱诗怡、李钟跃、乔希在导师的指导下，刻苦训练、不畏强手，取得了二等奖；三位通信专业的卢旺达留学生从组队到参赛只有两个月，他们从“零”开始，每天训练至深夜，最终取得了三等奖。至此我校已在该赛项上连续参赛7年，每年均斩获优异成绩。本次大赛以“智造创想，技创未来”为主题，要求学生围绕“微电网控制”、“伺服定位随动控制”、“工业机器人控制”三个选题，利用PLC、变频器、触摸屏、伺服、机器人等自动化产品，对自动化产品的机械和电气系统进行软硬件设计、安装与调试，考查学生的自动化系统应用能力和职业素养。近年来，信息学院电气自动化技术专业深入探索和实践“师徒结对”为主要形式的学生创新能力培养体系；通过赛项内容与课程内容深度对接、竞赛指导团队与核心课程团队一体化培养、竞赛平台打造与师生创新能力培养相结合、自动化创新实验室面向学生全天候开放等措施，显著提升了专业内涵和核心竞争力。仅今年，参与“师徒结对”的40余位学生中，共获得国家一等奖2项、二等奖2项、三等奖3项，省级一等奖2项、二等奖4项，75%的学生在各级大赛中均取得优异成绩。', 'http://info.jhc.cn/2016/0813/c201a68271/page.htm', 'http://info.jhc.cn/_upload/article/images/b2/fa/2d76f158465a9fe7fd060bb07235/a2ddd6fe-8d49-40a4-8b6a-dbeef8195b42.png|http://info.jhc.cn/_upload/article/images/b2/fa/2d76f158465a9fe7fd060bb07235/1d09062a-233d-49a2-8037-1829a8a48509.png|http://info.jhc.cn/_upload/article/images/b2/fa/2d76f158465a9fe7fd060bb07235/82ec3720-8ad2-4634-a397-e06189afa471.png', '2017-05-17 14:13:36.131068');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (139, '余红娟 张莹', '电子信息开发部', '周荃', '2016-08-07 00:00:00', '信息学子荣获2016年“TI杯”浙江省大学生电子设计竞赛2项一等奖', '近日，从浙江省大学生电子设计竞赛组委会公布的“2016年浙江省第六届大学生电子设计竞赛（TI杯）获奖名单”获悉，我校信息学院代表队共获得2项一等奖、2项二等奖、2项三等奖和1项成功参赛奖，其中有一个代表队同时又获得竞赛最高奖“TI杯”，获奖总成绩位列全省高职院校第一。今年的电子设计竞赛共有7项选题，专科与本科相同选题；我校根据专业培养特点选择了开关电源、智能小车和简易电子称3个项目。尤为可喜的是，信息学院参加本次竞赛的参赛学生一半是由大一学生组成，其中3个由高职四年制本科大一新生组成的代表队不畏强手，在与省内本科组的竞争中获二等奖1项、三等奖1项，充分展示了信息学院推行“多元导师制”教学模式对提升本科人才培养质量的成效。TI杯是由美国著名的集成芯片制造商TI公司冠名赞助。近日TI公司又与国家教育主管部门签署合作协议，将在今后连续十年冠名每年举办的省级电子设计竞赛和二年举办一次的国家级大学生电子设计竞赛。信息学院于2012年与TI公司合作共建“TI电子创新联合实验室”，期间TI公司大学计划部先后向联合实验室免费提供了MSP430、Cortex-M3等一系列开发套件和实验板，折合器件设备价值达80万元左右，为我校电子类专业学生的创新能力培养提供了有力支持。', 'http://info.jhc.cn/2016/0807/c201a68256/page.htm', NULL, '2017-05-17 14:13:36.153335');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (140, '余红娟 陈群挺', '电子产品开发部', '周荃', '2016-07-23 00:00:00', '信息学子荣获2016年全国大学生“恩智浦”杯智能汽车竞赛浙江赛区选拔赛一等奖', '近日，第十一届全国大学生“恩智浦”杯智能汽车竞赛浙江赛区选拔赛在浙江科技学院举行，共吸引了以本科院校为主的全省39所高校的231支代表队参赛，我校信息学院学子与本科院校学生同台竞技，沉着自信、不畏强手、喜获佳绩，徐健伟、胡文鑫、王振伟组成的金职6队获得一等奖，叶慈良、张彬彬、高尔明组成的金职3队以及何锋、吴高伟、张光聚组成的金职4队获得两个二等奖，沈俊尧、邵银强、王浩组成的金职5队获得三等奖。该赛项共分光电组、电磁直立组、摄像头组、电轨组、信标组、双车追逐组等六个组别；我校代表队参加光电组、电磁直立组、摄像头组、电轨组等四个组别的赛项。智能车竞赛的主要内容为竞速表演，考核选手对车身设计、飞思卡尔单片机、电子电路设计与焊接装配技术、传感技术、电机驱动技术等知识和技能的综合应用，是对学生专业技能和综合职业能力的考验。尤为可喜的是，两支才是大一的四年制高职本科生组队第一次参赛就获二等奖，这也是信息学院面向高职四年制本科生推行“多元导师制”教学模式创新的一个阶段性成果。', 'http://info.jhc.cn/2016/0723/c201a67633/page.htm', 'http://info.jhc.cn/_upload/article/images/78/71/c3bac73a4cf0a201a6fc5372aeff/f8693119-c11a-4bfe-ba18-65738fe39253.jpg', '2017-05-17 14:13:36.175443');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (141, '郭天鹏', '学工办/团委', '周荃', '2016-07-23 00:00:00', '信息学子火车站做志愿者为G20护航', '7月9号，金华职业技术学院信息工程学院18名志愿者出现在金华高铁站和普铁站，在迎接暑期返乡高峰的同时，为即将来临的G20峰会保驾护航。“请问G46到杭州在哪里检票”、“请问这辆车停运了怎么办”、“请问……”到金华火车站的第一天，就让志愿者们忙得不可开交。此次，18名志愿者共分有安检、取票引导、服务台、出口、进站口等工作岗位。面对繁忙的工作，志愿者们反而热情高涨，其中有几位第一次参加志愿者服务的同学表示，“虽然事情都很琐碎，但是能为别人服务感觉挺好的”。据悉，截止到目前，18名志愿者已经在金华火车站工作了整整4天，他们的志愿服务还要持续到9月7号G20来临，他们的精神和热情在感动着我们。金华日报相关报道：http://epaper.jhnews.com.cn/site1/jhrb/html/2016-07/20/content_1730235.htm', 'http://info.jhc.cn/2016/0723/c201a67632/page.htm', 'http://info.jhc.cn/_upload/article/images/03/0e/877c33e849de9f84eb5938a97aba/4d7e2d35-7413-4cd3-b91f-1b25b362f748.jpg|http://info.jhc.cn/_upload/article/images/03/0e/877c33e849de9f84eb5938a97aba/2433ffee-e101-4b4c-a90d-263b62a79881.jpg|http://info.jhc.cn/_upload/article/images/03/0e/877c33e849de9f84eb5938a97aba/cc7ffb55-f708-469c-8240-40fbf1b96a57.jpg', '2017-05-17 14:13:40.868723');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (142, '徐露诗', '学工办', '周荃', '2016-07-13 00:00:00', '“E＋学堂”暑期开课 科技下乡普及青少年', '“小妹妹，你知道什么是互联网＋吗？”“小弟弟，你知道怎么利用两节电池和一些简单的材料制作小风扇吗？” 。近日，信息工程学院“E·就通”新媒实践队前往傅村与安地，把科技知识带下乡，开设“E+学堂”，帮助当地的孩子们度过一个充实、丰富的假期。虽然7月的阳光热力不减，但是志愿者们实践热情也同样高涨，到达目的地后就放下行囊，拿着宣传资料，走街串巷，为后续的活动进行宣传与调研，希望能够让村里小朋友们加入到“E＋学堂”活动中来。在这个过程中，难免受到村民们的质疑与不理解，但是志愿者们用真诚的态度，耐心的讲解和阳光的笑容，打消了村民与孩子们心中的怀疑，经过志愿者们的宣传，最终有30余名孩子报名成为“E＋学堂”的学生。“E＋学堂”中，志愿者们不仅为孩子们准备了关于“互联网＋”的小知识，还教会孩子们如何动手制作简易小电扇等小制作。孩子们在志愿者充满互动与轻松的讲解中，听得津津有味，制作小电扇时也全神贯注，在大哥哥大姐姐的帮助下，孩子们的小电扇动起来了，学堂里传来了一串串充满成就感的笑声。一位家长说：“这样的活动真是太有意思了，小孩子平时在家只能看电视玩手机，我们也没有时间陪伴他们，参加了这个课堂不仅让他们了解最新的科技知识，还能让他们自己动手做和思考，一下子激起了他们对科技的兴趣，一举两得，感谢E·就通实践队的同学们！”', 'http://info.jhc.cn/2016/0713/c201a66072/page.htm', 'http://info.jhc.cn/_upload/article/images/7c/bb/8f5af61d4e34b262b1ab8a615433/008f88ba-b732-4bf7-8ff4-ea8e8f2f8a55.jpg|http://info.jhc.cn/_upload/article/images/7c/bb/8f5af61d4e34b262b1ab8a615433/b61414db-5659-4a0b-bba6-00959ae72eac.jpg', '2017-05-17 14:13:40.89823');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (145, '周荃', '', '周荃', '2016-07-01 00:00:00', '铭记革命历史 弘扬革命精神——纪念建党九十五周年系列活动', '为了纪念建党95周年，6月30号下午，信息学院组织师生党员观看“党章、党旗和党徽的历史”和“金华革命斗争史”的图片展，随后到金华市革命烈士纪念碑前重温入党誓言。大家怀着无比崇敬的心情，观看了“党章、党旗和党徽的历史”和“金华革命斗争史”的图片展。师生党员仔细观看了每一块展板，从这130多幅珍贵的历史照片中学习了党章、党旗、党徽的形成和演变历程，透过图片深深感受了金华人民不畏强敌、敢于斗争、勇于胜利的爱国情怀和革命精神。随后，师生党员来到金华市革命烈士纪念碑前，对因保家卫国献出青春热血的2100名革命英烈表示深深的哀悼，新老师生党员面向党旗，庄严宣誓。通过此次活动，扎实推进了“两学一做”学习教育，坚定广大党员的信念信仰，增强党员意识和宗旨意识，在工作学习中充分发挥党员的先锋模范作用。一位党员同志说道：要牢记昨天的苦难辉煌，无愧今天使命担当，不负明天复兴梦想！', 'http://info.jhc.cn/2016/0701/c201a54727/page.htm', 'http://info.jhc.cn/_upload/article/images/be/78/fd4d201f47c78151dcc6436f229f/ec0fc83b-cce6-4490-989f-f249827eaf2a.jpg|http://info.jhc.cn/_upload/article/images/be/78/fd4d201f47c78151dcc6436f229f/9f86e5ff-10ab-4417-98c9-2ecd93f7e5fe.jpg', '2017-05-17 14:13:40.96554');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (146, '周荃', '', '周荃', '2016-06-28 00:00:00', '《金华日报》报道信息学院志愿者义卖图书筹善款', '义卖图书筹集善款  昨天，金职院信息工程学院20名青年志愿者，分成5组来到市区长宁路、下官桥路等地，开展图书义卖活动。他们将学校毕业生捐赠的工具书，电子、计算机等专业书籍，以及500多本小说、漫画等课外读物，通过义卖的方式筹集善款，由学校统一用于帮助贫困地区学生。（记者时宽兵 通讯员汤鑫严） 新闻链接：http://epaper.jhnews.com.cn/site1/jhrb/html/2016-06/26/content_1721414.htm', 'http://info.jhc.cn/2016/0628/c201a54726/page.htm', 'http://info.jhc.cn/_upload/article/images/03/58/f562d32548a893fbe5435ffa3430/fb0f30db-32be-46e7-9162-9521d108d1a8.jpg', '2017-05-17 14:13:40.986999');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (147, '何碧峰', '学工办/团委', '周荃', '2016-06-28 00:00:00', '炎炎夏日，牢记消防安全', '为进一步加强全院师生的消防安全知识，本着“预防为主，防消结合”的宗旨，提高师生的安全意识和应急自救能力，近日，信息工程学院举行了消防应急疏散演练。 为了实施好这次演练，学院制定了详细的消防疏散演练方案，对各班逃离的路线及每位配班老师的职责、每层楼梯口的值班老师等内容都作了明确的规定。演练开始前，演练负责人给学生介绍了简单的逃生注意事项和方法。正午时分，随着铃声响起，全院参演300多名学生在各班老师的指引下，捂住口鼻，弯着身体快速有序地按照既定的逃生路线撤离到学院小广场，从警报声响起到最后一名学生撤离到广场，仅用时1分30秒。学生安全撤离到广场后，各班迅速检查人数，确保每一名学生都已安全撤离。清点完人数后，由保卫处技防科科长蒋加陆老师带领指导学生如何正确使用消防器材。此次消防疏散演练活动不仅是为了再次增强广大教师和学生的消防知识常识，进一步增强消防自救的本领，提高学校应对突发事件的应急能力，更是为了带动家庭、倡导全社会要时刻牢记“消防安全不可疏忽大意”。', 'http://info.jhc.cn/2016/0628/c201a54725/page.htm', 'http://info.jhc.cn/_upload/article/images/2e/83/242ddc5a444f92b8a6520cd53984/02db2149-ab37-4516-be70-35a119694f83.jpg|http://info.jhc.cn/_upload/article/images/2e/83/242ddc5a444f92b8a6520cd53984/b8c679cd-e4f8-42f6-b471-7be061442a24.jpg', '2017-05-17 14:13:41.009689');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (148, '郭天鹏', '学工办/团委', '周荃', '2016-06-22 00:00:00', '携手创作，共享快乐之摇摇灯设计', '6月18日下午，信息工程学院家电维修协会在东实训楼大厅开展“携手创作，共享快乐之摇摇灯设计”比赛活动。这是一场科技与技术的演绎，将手摇充电宝与LED灯相结合，制作手摇充电宝和充电灯。摇摇灯是利用切割磁感线原理手摇充电的一种自制电灯，此次比赛共吸引30多名学生参加，卢旺达的留学生们也前来参赛。比赛现场，同学们分为3人一组，由工作人员给每组发放相同的器材和工具；检查完器材后，经历了3小时制作，每组人员分工明确，电路焊接、裁剪出合适的外壳、线圈捆绑等等，一件件作品呈现在我们面前；评委根据设计理念、成品外观、发电效果等进行综合评价，最终由自动化151谢恩满等3名同学组成的小组获得第一名。今后，我们还会开展诸如此类的科技创作活动，给广大同学提供了一个挑战自我，展现个人创新能力的平台，让同学们可以把自己的专业知识都运用到比赛中。以激发同学们的创作能力、团队协作能力。', 'http://info.jhc.cn/2016/0622/c201a54724/page.htm', 'http://info.jhc.cn/_upload/article/images/ab/a0/ea8f66344dcf80cbca1e94e24a19/bbaf86b3-e3b9-4b8e-a73b-4453f76311ad.jpg', '2017-05-17 14:13:41.031828');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (149, '周荃', '', '系统管理员', '2016-06-21 00:00:00', 'IT男的多肉心——信息学院打造绿色休闲角', '信息学院龙川北04号楼的四楼，采光极好的东北角，摆放着舒适的桌椅，在窗台上还陈列着数盘绿茸茸的多肉植物，娇艳欲滴，生机勃勃，吸引着过往男生的目光，也引起了整个学院的热烈讨论。多肉的主人是信息学院网络142班的胡宇麟同学，种植多肉一直是他的兴趣与爱好。在班主任、辅导员的启发下，他将自己的成果都移到了东北角，这里光照充足，而且能让更多的同学们欣赏到多肉植物的美丽。而在胡同学的带领下，越来越多的男生们开始用绿色植物装饰寝室，学院的寝室文明建设工作也进一步深化到了同学们的内心与行动中。   （徐露诗 学工办/团委）', 'http://info.jhc.cn/2016/0621/c201a54723/page.htm', 'http://info.jhc.cn/_upload/article/images/f3/6b/5cd307884960a8fecbc87bac5577/c9394b9c-d699-4fc3-bad5-4bc9220e26aa.jpg', '2017-05-17 14:13:41.053761');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (150, '周荃', '办公室', '周荃', '2016-06-21 00:00:00', '顺德职业技术学院电子与信息工程学院领导一行来我院考察交流', '6月20日上午，顺德职业技术学院电子与信息工程学院院长宋玉宏一行8人来我院考察交流。学院领导班子，继教部、各项目部负责人出席了交流活动。 座谈中，陈海荣代表学院对客人的来访表示欢迎，并简要介绍了学院的发展状况。宾主双方围绕二级学院管理、校园文化建设、实训场所建设与管理等内容进行了深入探讨与交流。', 'http://info.jhc.cn/2016/0621/c201a54722/page.htm', 'http://info.jhc.cn/_upload/article/images/38/2c/817dde97446a857d5629bb576296/d6011e4a-3d53-466b-8c39-d5202e66e23d.jpg', '2017-05-17 14:13:41.075998');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (151, '周荃', '', '系统管理员', '2016-06-20 00:00:00', '信息学子2016年浙江省高职高专院校技能大赛“自动化生产线安装与调试”赛项获佳绩', '6月18日，由浙江工业职业技术学院承办的2016年浙江省高职高专院校技能大赛“自动化生产线安装与调试”赛项落下帷幕，由我校信息工程学院叶红芳、楼蔚松两位老师指导，南如幸、杨轲两位学生组成的代表队沉着应战、努力拼搏，经过5个小时的激烈角逐，获得第2名的好成绩。   此次比赛共有来自全省8所高职院校的8支代表队参加，比赛以浙江亚龙公司生产的YL_335B自动生产线实训装置为平台，赛项内容涉及到YL_335B自动生产线实训装置机械部分安装、PLC程序设计与调试、组态设计与调试、变频器与伺服驱动器调试、系统故障排除等方面，要求参赛队员在气动控制、传感器应用、PLC控制、变频器、伺服驱动、MCGS组态设计等方面具备扎实的专业理论知识、较强的实践能力和良好的团队协作精神。                                       （叶红芳、花有清）', 'http://info.jhc.cn/2016/0620/c201a54721/page.htm', 'http://info.jhc.cn/_upload/article/images/c9/70/5e4a23f24fec9b760b3496ed13a8/644f7c8d-2550-4dff-9bd6-f08d1555b0fa.jpg', '2017-05-17 14:13:41.098183');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (152, '周荃', '', '系统管理员', '2016-06-20 00:00:00', '饮水不忘思源  悠悠反哺之情——信息学院毕业生捐赠支援实训室', '信息学院东实南楼实训室的大厅里出现了两套舒适的桌椅，上课的教师同学们时不时会在那里休息讨论，这两套桌椅的出现为师生们提供了便利，也让实训楼充满了浓浓的人情味道。   同学们不禁疑问这两套休闲桌椅是哪里来的？原来是信息学院的毕业生们，为了感恩母校老师们的教导与关怀，集体为实训室订购了桌椅。这不仅展示了信息学子饮水不忘思源的优良品质，也充分显示了师生们在日常学习互动中产生的深深感情。一位参与了捐赠的毕业生说：“从前实训楼是我们的主要学习的场合，无论是上课还是准备竞赛，老师都同我们一起在实训室奋战，如今要离开了，真的想为母校留下些什么，感谢母校以及老师们的栽培，也希望后来的学弟学妹们能用功学习，不辜负母校的期待。”（徐露诗 学工办/团委）', 'http://info.jhc.cn/2016/0620/c201a54720/page.htm', 'http://info.jhc.cn/_upload/article/images/39/32/e6b94a8645cea97a40b792aa4f25/222ef1ca-478f-4a0d-8536-8f770d808f3b.jpg', '2017-05-17 14:13:41.120434');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (153, '周荃', '', '系统管理员', '2016-06-17 00:00:00', '信息学院召开廉情分析会', '6月16日下午，信息学院召开廉情分析会，对上半年的党风廉政情况进行回顾总结，院党风廉政建设领导小组成员、中层及以上干部参会。会上，各部门、项目部负责人对上半年党风廉政建设作了汇报，重点分析查找了财务报销、职称评定、社会培训、技能考证、耗材管理、学生信息保密、评奖评优等各环节的隐患风险，院纪检委员对学院下半年工作进行部署。据悉，信息学院已完成科职以下岗位廉政风险点排查，与各部门、各项目部签订党风廉政责任书，接下来，学院将继续严肃工作纪律，推进制度执行，把党风廉政建设与学院各项工作有机融合，营造风清气正的良好氛围。 （周荃 办公室）', 'http://info.jhc.cn/2016/0617/c201a54719/page.htm', NULL, '2017-05-17 14:13:41.143622');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (154, '周荃', '', '系统管理员', '2016-06-16 00:00:00', '信息学院开展“卫生大扫除，先锋在行动”活动', '为进一步深化“两学一做”学习教育活动，营造干净、整洁的实训环节，近日，信息工程学院电气自动化专业党支部组织师生党员走进实训室，开展“卫生大扫除，先锋在行动”活动。   支部的15名师生党员带头践行实训室7S管理规范，将“整理、整顿、清扫、清洁、安全、节约”贯穿到实训室的每个角落。整整1800平方的实验室，扫地、拖地、抹桌子、整理储物柜、摆放仪器，大家干得热火朝天、汗流浃背，却毫无怨言。经过一个多小时的共同努力，实训室的机房、通道、卫生死角都被清理干净，赢得了实验员和任课老师的一致好评。大家还检查了门、窗、电源后才安心的离开。   通过此次活动，有效提升了实训室的整体环境，还发挥了党员为民服务的积极作用，大家表示希望支部多组织类似的活动，培养节约意识、安全意识，提升职业素养。（叶红芳 教科办）', 'http://info.jhc.cn/2016/0616/c201a54718/page.htm', 'http://info.jhc.cn/_upload/article/images/ae/7f/8588c8ce4e989579465bb28cbf20/947f37f6-b695-4c62-814c-f08fdd50e095.jpg|http://info.jhc.cn/_upload/article/images/ae/7f/8588c8ce4e989579465bb28cbf20/0fca2a5e-ae99-4597-a5dc-1d2b2e07cdea.jpg', '2017-05-17 14:13:41.16522');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (155, '周荃', '', '系统管理员', '2016-06-15 00:00:00', '我院四年制高职专业人才培养方案通过专家认证', '6月14日，教务处张雁平副处长、信息工程学院陈海荣院长、王成福副院长、电子产品开发部余红娟主任在浙江理工大学参加高职本科电子信息科学与技术专业人才培养方案专家论证会。   本次专业人才培养方案专家论证会主要是针对浙江理工大学电子类三个专业以及我院首批四年制高职本科专业共四个专业进行论证，会上四个专业负责人对人才培养方案修订进行了汇报。杭州电子科技大学章坚武教授、中国计量大学徐文龙教授、浙江理工大学徐伟强教授、中浙信科技咨询有限公司张华春高工、欧姆龙通灵自动化系统（杭州）有限公司余盛高工5位专家听取汇报并进行论证，经过长达三小时的讨论最后专家对四个专业的人才培养方案提出了评审意见。其中，专家们在听取我校高职本科人才培养方案汇报后首先肯定了职业教育特色明显的评价，并对我校分流培养、分层教学的因材施教做法给予充分的肯定，也提出了修改建议：补充课程之间的关系图使课程之间关系表达更直观；明确电子设计竞赛、程序设计竞赛等学科竞赛可替代相关课程学分；设置“企业家讲座”课程，增强学生了解企业文化；并对相关课程进行更名修订等。专家们的意见为我院进一步完善四年制高职人才培养方案、培养更加符合我省经济转型升级发展要求的一线工程技术人员奠定了良好的基础。 （余红娟 电子产品开发部）', 'http://info.jhc.cn/2016/0615/c201a54717/page.htm', 'http://info.jhc.cn/_upload/article/images/c3/a2/82a79eaf45519836a7bab7a59ae5/d7ef2043-da4e-43cb-b52f-8ad5cf7e38b8.jpg', '2017-05-17 14:13:45.850623');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (186, '周荃', '', '系统管理员', '2016-05-13 00:00:00', '我院健儿省青少年体育舞蹈锦标赛喜获佳绩', '近日，第九届省青少年体育舞蹈锦标赛圆满落幕，我校体育舞蹈队发挥出色，喜获一等奖5项、二等奖和三等奖各3项。据悉，本次比赛由省体育总会、省国际体育舞蹈协会主办，下设少儿组、青年组两个组别。比赛包括摩登舞、拉丁舞、六人舞、等级赛等共107个项目，参赛人数达1473人次。我校选派了26名运动员参加，经过激烈角逐，我院网络151班华佳刘裕等两位同学组成的代表队获得C组拉丁舞、青年双人拉丁舞恰恰恰单项第一名。   此前，华佳刘裕刚刚获得了“永恒之心”杯第二届中国·金华体育舞蹈公开赛成人组拉丁舞恰恰单项、成人组拉丁舞牛仔单项第一名和“永恒之心”杯第十二届金华体育舞蹈锦标赛青年组拉丁舞第一名。 （郭天鹏、周荃）', 'http://info.jhc.cn/2016/0513/c201a54686/page.htm', 'http://info.jhc.cn/upload/20160506dancing.jpg', '2017-05-17 14:13:56.221943');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (156, '郭天鹏', '学工办/团委', '周荃', '2016-06-14 00:00:00', '青春逐梦  行远思恩——记信息学院2016届学生毕业典礼暨欢送晚会', '6月13日晚六点半，信息工程学院全体2016届毕业生手持学院为同学们精心制作的《原力生长 青熟不语》观影票进入实验剧场，参加2016届学生毕业典礼暨欢送晚会。毕业典礼以“青春逐梦，行远思恩”为主题，分“忆明天，只叹匆匆那年”、“看今天，铭记母校恩情”、“展明天，遥望美好前程”三个篇章进行。荧光舞的炫目开场，引来了毕业生的热烈掌声；由在校生改编的《金职版青花瓷》唱出了金职全景；自编自导自演的微视频《走过金职的那三年》,充分演绎了毕业生与老师、与同学、与校园难舍难分的情怀，令人潸然泪下；小品《最后一课》饱含着毕业生对老师们无限感激和不舍之情；班主任及学工办老师一曲《栀子花开》,表达了对同学们深深的祝福与关怀。最后，由大三毕业班主任老师演唱一首《时间煮雨》更是将现场的气氛推向了高潮，真挚感人。据悉，典礼举办当天，学院还在实验剧场门口摆放了火车头喷绘板供同学们合影留念，希望毕业生带着梦想，奔向更加美好灿烂的未来!', 'http://info.jhc.cn/2016/0614/c201a54716/page.htm', 'http://info.jhc.cn/_upload/article/images/8d/e5/9d6f6dfc423fb66398c8f4d30cc1/e9ca8d6c-abe3-4742-a5d4-0aabc2b07c82.jpg|http://info.jhc.cn/_upload/article/images/8d/e5/9d6f6dfc423fb66398c8f4d30cc1/df302b49-3843-4ad9-a2a7-86b03dbf5a08.jpg', '2017-05-17 14:13:45.876397');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (157, '周荃', '', '系统管理员', '2016-06-14 00:00:00', '2016国（境）外专家学术系列讲座之六：新西兰的电影和音乐', '近日，新西兰怀卡托理工学院IT技术教师劳埃德应邀为我校师生作“新西兰的电影和音乐”专题讲座，百余名师生聆听了讲座。   讲座中，劳埃德结合剪辑视频，从新西兰的首部电影开始，一直到全球皆知的“指环王三部曲”与“霍比特人三部曲”，从故事、生活、科幻、爱情等多个类型为在座同学们打开了新西兰电影的大门，让大家更为直观地认识了新西兰电影的发展史。此外，劳埃德还以新西兰第一支著名乐队“分裂尖端”（Split Enz）为例，向师生们详细介绍了新西兰的流行音乐，受到了师生们的极大欢迎。   不少学生表示，远在南半球的新西兰对全球的电影和音乐有着举足轻重的地位，通过此次讲座让他们对此有了较为全面的了解，不仅拓宽了视野，也增长了见识。 （丁晨 通信与网络工程部）', 'http://info.jhc.cn/2016/0614/c201a54715/page.htm', 'http://info.jhc.cn/_upload/article/images/d4/88/2290778648d1a16bb5d34ddbec31/0f1aca13-f17f-4b6d-9b6e-1826b0feefeb.jpg|http://info.jhc.cn/_upload/article/images/d4/88/2290778648d1a16bb5d34ddbec31/06368b11-96e5-4bfe-a66d-d16f29f319db.jpg', '2017-05-17 14:13:45.89859');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (158, '周荃', '', '系统管理员', '2016-06-12 00:00:00', '信息学院首届英语口语大赛落下帷幕', '“He is a very handsome man and from Korea ”近日，信息学院举办首届英语口语大赛，一位选手面对投影幕布上放映的韩流明星宋仲基的照片，用一连串流利的英语向在场观众介绍道。据悉，此次比赛共有10名选手入围，邀请新西兰外教克雷格作为评委代表，全院近百名师生到达现场为选手们加油鼓劲。本次大赛分三分钟演讲、看图描述以及情境模拟交流三个环节。其中，“三分钟演讲”环节要求选手围绕“诚信”主题展开即兴演说，有的向大家述说了发生在自己身边的诚信小故事；有的通过介绍先进人物事迹，阐述了诚信的重要性；还有的结合自身经历，分享了自己对诚信的心得与感悟。“看图描述”环节把时下的流行元素融入主题，同学们通过抽签选取图片，并用英语就图片的内容发表自己的理解。“情境模拟交流”环节则要求选手两两一组，根据情境设计5分钟的对话。寝室里打扫卫生、食堂里讨论菜色、校园问路指路等等一一被搬上了舞台。这不仅锻炼了日常交流的能力，新颖的比赛形式更是引发了现场观众的一阵阵热烈的讨论和掌声。经过三轮激烈的角逐，计算机153班夏启成、网络152班林子淳、物联网151班孟旭超等同学凭借流畅的表达和标准的发音夺得了比赛的一、二、三等奖。 “原先觉得英语口语很困难，在平时的时候张不开口，经过这次比赛，觉得还挺有趣的，以后我会找老师、同学多交流！” 一位同学赛后说道。 （徐露诗 学工办/团委）', 'http://info.jhc.cn/2016/0612/c201a54714/page.htm', NULL, '2017-05-17 14:13:45.920905');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (159, '周荃', '', '系统管理员', '2016-06-11 00:00:00', '信息学院开展“对接新技术、虚拟现实进校园”校企合作系列活动', '近日，信息学院计算机应用技术专业与上海形拓科技有限公司、科维城（上海）信息科技有限公司开展“对接新技术、虚拟现实技术进校园”校企合作系列活动，学院领导、计算机应用专业教师、计算机应用14、15级学生共计100余人参加了活动。   活动围绕虚拟现实案例体验走进学院、虚拟现实前沿讲座走进课堂、地理信息系统（WebGIS） 3D建模项目走进工作室三大内容进行。教师和学生通过虚拟现实头盔、眼镜等交互设备，身临其境地体验了化工灾难模拟与应急演练、建筑施工模拟、风景区游览、工业仿真装备测试等沉浸式虚拟现实3D交互场景，多感官感受虚拟现实新技术应用带来的奇妙体验；通过“虚拟现实进学校”技术分享会，计算机应用14级学生与教师了解了虚拟现实发展现状、主流技术、行业应用与岗位需求；通过计算机14级、15级60余名工作室学生参与3D建模项目的企业培训与项目承接，及时跟进虚拟现实3D场景建模基础技能训练。      据悉，2016年是IT业界虚拟现实元年。校企对接虚拟现实技术系列活动，是信息学院计算机应用技术专业探索交互媒体制作方向人才培养的积极尝试，对提高U3D开发、3D场景建模等新技术紧缺岗位的适岗能力、开拓计算机14级学生的新型实习岗位将起到积极推进作用，将有效促成该专业交互媒体创新创业工作室的校企共营。 （宣翠仙、应武、叶继阳 信息化事业部）', 'http://info.jhc.cn/2016/0611/c201a54713/page.htm', 'http://info.jhc.cn/_upload/article/images/6c/4d/109dbc59473ba6b893eed14d0d94/ea24e4aa-a8fe-4d5a-86a7-b1bdb81b8cf1.jpg|http://info.jhc.cn/_upload/article/images/6c/4d/109dbc59473ba6b893eed14d0d94/2e7883dc-815e-4ae0-be0c-9e01d3d17bed.jpg|http://info.jhc.cn/_upload/article/images/6c/4d/109dbc59473ba6b893eed14d0d94/9bf6acc2-b573-4019-8ad3-cd1f3e231475.jpg', '2017-05-17 14:13:45.943095');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (187, '周荃', '', '系统管理员', '2016-05-12 00:00:00', '五月红似火 正是采桑时——记信息学院女工活动', '为活跃和丰富广大女教职工的业余生活，增进同事间的交流，5月11日，信息学院工会组织女工会员活动，赴金东区采摘桑葚。   久雨初晴，风和日丽，正是桑葚成熟季节，一颗颗乌黑的桑葚缀满枝头，玲珑可爱。进入果园，老师们便迫不及待地置身于桑园中，拎着小篮子开始采摘，不时拍照留念，欢声笑语传遍山野。   桑和梓是古代家宅旁边常栽的树木，进而桑梓也被赋予了故乡和家人的寓意。此次活动让大家暂离电脑，放松身心，在清新的户外感受大自然的魅力，更增进了学院女教职工之间的交流。会员们纷纷表示，“很高兴学院为大家组织这样的活动，大家闲话家常，也勾起了儿时的诸多回忆。在田里乡间，又感受到了那份童真、快乐与思念”。 （周荃 办公室）', 'http://info.jhc.cn/2016/0512/c201a54685/page.htm', 'http://info.jhc.cn/upload/20160511act1.jpg|http://info.jhc.cn/upload/20160511act2.jpg', '2017-05-17 14:13:56.244128');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (160, '周荃', '', '系统管理员', '2016-06-08 00:00:00', '信息学院五届三次教职工暨工会会员代表大会顺利闭幕', '6月7-8日，学院召开五届三次教职工暨工会会员代表大会，18名正式代表及8名列席代表参加大会。   会上，陈海荣院长作了题为《凝心聚力  锐意进取  推动学院科学快速发展》的工作报告。报告客观地总结了学院“十二五”期间在专业内涵建设、教学改革创新、人才培养质量提升、师资团队结构优化、科研与服务能力提升、教育国际化等方面取得的成果，也指出了学院目前存在的问题和面临的挑战，明确提出了“十三五”规划和今年的工作任务。贾美倩书记作了《分工会工作报告》。会议期间，代表们立足学院发展，积极献计献策，认真听取并审议通过了《院长工作报告》和《信息学院“十三五”发展规划》。“十三五”规划明确了创新发展的理念，提出了打造四大实践平台的设想，阐释了实施七大计划及其主要举措，既结合实际又具前瞻性，为学院今后和谐发展、特色发展、内涵发展、创新发展描绘了蓝图，明确了方向。   大会号召全院上下要紧跟时代步伐，继续以昂扬的斗志和饱满的热情，开拓创新，同心同德，全力以赴为学院新的跨越而努力奋斗。 （周荃 办公室）', 'http://info.jhc.cn/2016/0608/c201a54712/page.htm', 'http://info.jhc.cn/_upload/article/images/15/20/609f98bf45ad9479b5f8bb0a1373/e35a9191-b48a-40d6-8466-7fcaab95ba9b.jpg|http://info.jhc.cn/_upload/article/images/15/20/609f98bf45ad9479b5f8bb0a1373/884c567d-8f89-4815-b4a5-e6eae2726a6f.jpg', '2017-05-17 14:13:45.965951');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (161, '周荃', '', '系统管理员', '2016-06-07 00:00:00', '信息学院公寓文化建设——读书海报', '你喜欢看电影吗？你最近又读了什么书？随着图书馆晚自习，读书英雄榜等一系列文明修身活动之后，信息学院又准备推出书籍、电影推荐活动。为了充分展示学生的风采，进一步提高学生的综合素质，信息学院鼓励学生将自己最近喜欢书籍或者电影的观后感写出来，并以海报的形式在公寓楼下的宣传栏进行张贴，目的在于将更多优秀的文学和电影作品推荐给身边的小伙伴们。   一张张色彩缤纷，内容充实的海报，不仅丰富了公寓的文化，更加显示信息学子的积极健康的生活态度及良好的学习习惯，为进一步深化文明寝室建设添加了新的动力。一位制作海报的同学们表示：以往阅读完一本好书后就很想推荐给其他小伙伴，但是都没有什么好的途径，现在通过宣传海报，不仅可以把好看好玩的书推荐给其他同学，而且还能展示自我，以后也会积极地参与到这项活动中来的。 （徐露诗 学工办/团委）', 'http://info.jhc.cn/2016/0607/c201a54711/page.htm', NULL, '2017-05-17 14:13:45.987442');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (162, '周荃', '', '系统管理员', '2016-06-07 00:00:00', '百里毅行，走出信息团学精神', '6月4日上午，信息工程学院的84名学生干部在老师带领下，举行“百里毅行”野外徒步行走活动。   此次活动历时9个小时，上午8点半从学校出发步行10公里，从东实训楼出发，途径金职院西门、金帆街、330国道、金华石材综合市场、宾虹西路、最后到达金华农耕文化园。一路上同学们步伐坚定，井然有序，相互帮助，相互鼓励，凭着毅力坚持在路上，没有丢下一个同学。到达目的地后，同学们分组进行厨艺比拼，大家分工合作，洗菜，切菜，烧菜，团队协“做”，忙的不亦乐乎，一桌桌美味相继出炉。下午5点半，全体学生干部沿原路返回，圆满完成此次百里毅行。信息学院通过组织长距离徒步运动来锻炼学生的体力，磨炼意志，加强学生干部的团队合作精神。(学工办/团委  郭天鹏)', 'http://info.jhc.cn/2016/0607/c201a54710/page.htm', 'http://info.jhc.cn/_upload/article/images/0e/e2/8deb9e0a4e60bb74fcde132828cc/cd566a59-373a-4452-8099-4aa1fac0a584.jpg', '2017-05-17 14:13:46.012205');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (163, '周荃', '', '系统管理员', '2016-06-06 00:00:00', '信息学院举办表情包设计大赛', '“你们看到了吗？最近新出的表情包好好笑，哈哈！”近日，由信息学院举办的“走进生活--表情包设计大赛”顺利结束。活动自开办以来，吸引了众多同学的眼球，前来投稿的人络绎不绝。活动中，同学们捕捉生活瞬间，发挥想象，运用专业技术制作出一张张精彩的幽默表情图。有的集合了寝室生活中室友留下的搞笑瞬间，有的记录下活动中同学们的不经意露出的表情，更有的拍摄下老师讲课时的神情，再通过鬼斧神工一般的ps技术让“上图”同学老师哭笑不得，令人忍俊不禁。最后，通过微信投票的方式，竞选出最具人气的表情包，并在“金职院信息团委”公众号内进行展示。“此次活动，鼓励同学们利用专业技能发挥创造性思维，展示自我，很好的将专业知识与活动结合了起来。”一位专业老师说道。 （夏斌、吕威威 学工办/团委）', 'http://info.jhc.cn/2016/0606/c201a54709/page.htm', NULL, '2017-05-17 14:13:46.031982');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (164, '周荃', '', '系统管理员', '2016-06-03 00:00:00', '信息学院教师参加高等教育出版社在线开放课程培训', '近日，受高等教育出版社邀请，信息学院应用电子技术专业部分老师参加了电子计算机类在线开放课程及新形态一体化教材立项项目建设中期研讨与微课开发实战培训会。   会上，与来自全国的教师分享了在线开放课程、新形态一体化教材立项项目验收要求、建设经验和数字化资源优化等，分享了翻转课堂的线上线下混合式教学实践的情况。由全国微课开发与制作技术知名专家刘万辉团队主持进行微课开发技术实战培训。通过培训，对PPT教学课件的整体设计、逻辑设计有了初步了解，学会PPT动画设计的方法与技巧，学习了Focusky发散性思维工具软件、Photoshop软件、VideoScribe手绘视频软件、Camtasia Studio录屏软件等软件制作微课以及优化微课。据悉，模拟电子技术、数字电子技术、电力电子技术已获得了高等教育出版社的一体化教材和在线开放课程建设立项任务。 （徐华丹、余红娟 教科办、电子产品开发部）', 'http://info.jhc.cn/2016/0603/c201a54708/page.htm', 'http://info.jhc.cn/_upload/article/images/4d/01/f8895746446eba181f24b36767eb/4a52bc7f-659c-40f5-a4e7-f0f9788df952.jpg', '2017-05-17 14:13:46.054291');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (165, '周荃', '', '系统管理员', '2016-06-01 00:00:00', '我院健儿省学生体育竞赛喜获佳绩', '近日，2016年浙江省大学生乒乓球锦标赛暨第十三届全国学生运动会乒乓球热身赛圆满落幕，我校乒乓球队队发挥出色，喜获团体第一名。据悉，本次比赛由省教育厅、省体育局主办。我院自动化133班杨康等同学组成的代表队获得、男子乙组团体第一名、男子乙组双打单项第二名。(学工办/郭天鹏)', 'http://info.jhc.cn/2016/0601/c201a54707/page.htm', NULL, '2017-05-17 14:13:46.076492');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (166, '周荃', '', '系统管理员', '2016-05-30 00:00:00', '信息学院计算机类专业校企合作多点开花', '经过双方多次交流和协商，近日，我校信息学院与天下谱局金华文化股份有限公司就古文字识别的汉字库建设工程达成合作意向。天下谱局金华文化股份有限公司是一家专业的民间史记录服务机构，是国内首家将传统文化引进电子商务的领航企业。浙江省委书记夏宝龙近期曾到该公司调研文化建设情况。此轮合作，信息学院将发挥博士入企的优势，主要参与字体库的算法设计、识别系统平台建设任务，而天下谱局则为信息学院师生提供项目资金、项目开发环境、学生顶岗实习岗位等内容。   据悉，在此项目之前，信息学院已经与金华路灵灵物联网科技有限公司就O2O智慧物联网络平台运行优化及实施项目的技术服务签署相关协议，与科维城（上海）信息科技有限公司签署战略合作框架协议，就将该公司部分建筑建模工作委派给信息学院师生进行建模工作达成一致。至此，信息学院在网站建设、软件开发、数字媒体等计算机专业相关领域均部署了合作企业及具体项目，实现校企合作短时间内的多点开花。在此轮校企合作中，信息学院寄希望让学院师生将所学与企业实践有机结合，让学校和企业的设备、技术实现优势互补、资源共享，让教师走出课堂，融入企业项目，让学生走出校园，更好的了解社会对专业知识的要求，以切实提高育人的针对性和实效性，提高高职技能型人才的培养质量。 （陈晓龙、王伟斌  信息化事业部）', 'http://info.jhc.cn/2016/0530/c201a54706/page.htm', 'http://info.jhc.cn/_upload/article/images/8c/70/971954064edca1d18b6ba8dca536/41443ccb-2b74-4614-b393-265df17c388e.jpg', '2017-05-17 14:13:46.098693');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (167, '周荃', '', '系统管理员', '2016-05-30 00:00:00', '信息学子蝉联全国职业技能大赛“风光互补发电系统安装与调试”赛项一等奖', '5月29日，在南京工业职业技术学院举行的2016年全国职业院校技能大赛（高职组）“康尼”杯风光互补发电系统安装与调试赛项圆满落幕，我校信息学院代表队继去年获得一等奖后，再次以第2名的优异成绩蝉联一等奖。此次比赛共来自全国51支代表队参加，要求选手在4个小时之内完成光伏、风力供电装置的安装与调试、监控系统的组态编程及调试等任务，赛项对接新能源应用领域，内容涉及光伏发电、风力发电及电能转换等知识，对选手技能水平、团队合作要求很高。我校信息学院电气自动化技术专业2014级学生赵锦坤、温从汉、赵赛赛组成的代表队不畏强手，努力拼搏，以扎实的专业理论知识、较强的实践能力和良好的团队协作精神，展现了我校信息学子的风采。   据悉，信息学院近年来十分重视学生的创新能力培养，通过“教师、学长、学弟”三者参与构建“师徒结对”创新班，采用“一学期一项目、一周一任务”的形式建全学生创新能力培养的长效机制，取得显著成效。今年，信息学院共获6个国赛参赛资格，所有赛项全部获奖，并取得“2项一等奖、1项二等奖和3项三等奖”的优异成绩，为学校争得了荣誉。 （马汝星 周荃）', 'http://info.jhc.cn/2016/0530/c201a54705/page.htm', 'http://info.jhc.cn/_upload/article/images/74/4d/e2c9a6bd47f1b4a70d51803279ef/ccb88fa3-fe1d-40db-a9c9-8c235cf40d66.jpg', '2017-05-17 14:13:46.120909');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (168, '周荃', '', '系统管理员', '2016-05-29 00:00:00', '省内25所中职校齐聚金职院共商中职对接四年制高职人才培养', '5月26日，应邀全省25所骨干中职学校齐聚金华职业技术学院，共同研讨中职与四年制高职对接人才培养研讨会。校领导胡野、相关职能部门负责人、信息学院和机电学院负责人出席会议，会议由教务处刘鲁平处长主持。会上，学校党委副书记胡野对前来参会的中职学校领导表示热烈欢迎，他指出，四年制高职专业的试点过程中，面临着专业培养目标与学生基础知识之间的众多困难，迫切需要优化中职课程设置，对接四年制高职专业的培养要求。本次研讨会是我校与各中职生源学校经过前期大量的工作基础上达成的共识，是积极响应国家构建现代职业教育体系的政策导向，努力推动中高职教育协调发展的有益尝试。本次研讨会的主题是适应区域产业升级发展需求，明晰人才培养目标，优化专业结构设置，实现中职教育和四年制高职教育的有机衔接，为我省经济社会发展培育更多高端技术技能型人才。   信息学院负责人向与会领导汇报了学院首批四年制高职试点专业——电子科学与技术专业人才培养的探索与实践，就多元导师制的实施情况以及与浙江理工大学的合作办学情况作了汇报分享。机电学院就今年刚刚获批四年制高职试点所提出的“学研互动、双能并举”为导向的四年制高职人才培养方案作了汇报。首批四年制高职学生代表还现场展示了导师制教学成果——智能车项目，获得中职校领导的好评。   中职校校长与我校就四年制高职专业的招生政策、中高职专业人才培养对接、课程体系与教学内容改革、校内外实训基地建设、资源共享等方面交流了各自意见。新昌县大市聚镇职业中学的副校长提出中职计算机类学生能否适应电子信息科学与技术专业的学习，温岭市职业技术学校的副校长提出中职学生的英语基础能否适应四年制高职的英语学习等问题。学校针对这些问题进行逐一答复：根据专业学习情况的分析，计算机类学生和电工电子类学生在专业学习方面没有明显差距，关键取决于学生的学习态度；首批四年制高职学生采用英语分层分类教学，第一学期英语B级通过率达到96%。中职校对我校首批四年制高职人才培养的成果给予了高度肯定。与会代表均表示，要进一步完善中职与四年制高职的课程对接，加强双方的联系与合作，积极促成和发挥中职校输出“优秀毕业生”、高职校要培养“有特长”的技术技能型人才。期间，我校还邀请到金华市教育局教研室副主任、教授级特级教师朱孝平博士所做的讲座《找寻课程教学中的核心变量》，他提出“学习投入、学习体验、学习产出”是影响课程教学的核心变量，教学环境、班主任工作、教学管理等也对课程教学效果产生一定的影响，这为职教课程的改革提出了新对策。   本次探讨会非常及时、成效显著，进一步厘清中职校与高职院校在四年制高职人才培养对接路上的责任与分工，也将进一步推进我校四年制高职人才培养各项工作有序开展。 （周荃 办公室）', 'http://info.jhc.cn/2016/0529/c201a54704/page.htm', 'http://info.jhc.cn/_upload/article/images/30/07/8d1220a6410c99860c334e04ef4c/e147cad2-1b95-4739-ae21-646af2b142a9.jpg|http://info.jhc.cn/_upload/article/images/30/07/8d1220a6410c99860c334e04ef4c/70c40b11-0a82-4207-a8a6-435973946553.jpg|http://info.jhc.cn/_upload/article/images/30/07/8d1220a6410c99860c334e04ef4c/a391d660-bdf7-446e-b4e8-c0e25858d329.jpg', '2017-05-17 14:13:46.143264');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (169, '周荃', '', '系统管理员', '2016-05-27 00:00:00', '深圳职业技术学院电子与通信工程学院来我院考察交流', '5月27日上午，深圳职业技术学院电子与通信工程学院专业教师一行4人来我院考察交流。学院领导班子，电子产品开发部相关教师出席了交流活动。座谈中，陈海荣代表学院对客人的来访表示欢迎，并简要介绍了学院的发展状况。宾主双方围绕专业建设、课程建设、校企合作等内容进行了研讨，重点就共建电子信息工程技术专业资源库进行深入商讨。 （周荃 办公室）', 'http://info.jhc.cn/2016/0527/c201a54703/page.htm', NULL, '2017-05-17 14:13:51.252046');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (170, '周荃', '', '系统管理员', '2016-05-25 00:00:00', '信息学子荣获2016年全国职业院校技能大赛“移动互联网应用软件开发”赛项二等奖', '5月21日至23日，2016年全国职业院校技能大赛高职组“联想”杯移动互联网应用软件开发赛项在山东科技职业学院圆满落幕，我校信息学子沉着应战、努力拼搏，经过4个小时的激烈角逐，最终获得了二等奖的好成绩。   据悉，本次比赛共有来自全国30个省的95所高职院校的代表队参加，竞赛以智能农业行业应用为考核主题，包括“系统设计”、“程序排错”、“功能编码”和“创意设计”几个模块，通过实际操作综合考察选手在移动互联网应用的创新性、操作性、实用性。最终我校信息学院14级学生印世波、王麒皓、谢慧云三位同学组成的代表队凭借扎实的专业功底、稳定的竞技发挥和优秀的团队合作，喜获佳绩，为学校争得了荣誉。 （颜钰琳、陈晓龙 信息化事业部）', 'http://info.jhc.cn/2016/0525/c201a54702/page.htm', 'http://info.jhc.cn/_upload/article/images/cb/fc/c62e213b469194df76574d555fe8/64a63bdc-d166-479d-906f-1aef5e980702.jpg', '2017-05-17 14:13:51.303947');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (171, '周荃', '', '系统管理员', '2016-05-24 00:00:00', '《金华日报》报道我校信息学院志愿者帮建美丽乡村', '志愿者帮建美丽乡村   昨天，金职院的学生志愿者来到婺城区安地镇七间屋村，用废弃木材制成花盆、指示牌，并为盆栽设计装饰，给墙面彩绘，帮助村民建设美丽乡村。（记者时宽兵 通讯员 汤鑫严）      新闻链接：http://epaper.jhnews.com.cn/site1/jhrb/html/2016-05/24/content_1708929.htm', 'http://info.jhc.cn/2016/0524/c201a54701/page.htm', 'http://info.jhc.cn/_upload/article/images/b9/33/d9d2eea1412abfbb648cd5426656/eebde610-4e80-4860-b418-4776ae88da2c.jpg', '2017-05-17 14:13:51.33245');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (172, '周荃', '', '系统管理员', '2016-05-24 00:00:00', '信息工程学院全体党员干部赴“江南第一家”开展廉政教育学习', '5月21日，信息工程学院党委组织全体教职工党员赴浦江“江南第一家”开展廉政教育学习活动，进一步推进党风廉政建设及廉政风险防控机制建设。   活动中，党员们走过九座气势宏伟的牌坊，参观了郑氏宗祠，并观看了廉政动画片《郑义门》。党员们通过实地考察和生动形象的动漫故事直观地深入感受郑氏家族“300年不分家，173人为官无一贪官”的事迹，无不被《郑氏规范》168条家规祖训所震撼。弘扬传承“郑义门”廉洁家风文化具有深刻的时代意义，学院将以此次活动为契机，强化全体党员的思想、作风教育，营造风清气正、廉洁自律的党员队伍。 （周荃 办公室）', 'http://info.jhc.cn/2016/0524/c201a54700/page.htm', 'http://info.jhc.cn/_upload/article/images/94/f0/0820b6154570b1a4085f39b9b8b6/e4544de0-120c-40d2-a61a-040bee4c6016.jpg', '2017-05-17 14:13:51.354703');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (173, '周荃', '', '系统管理员', '2016-05-20 00:00:00', '规划职业蓝图，书写美好人生——信息学院举办大学生职业生涯规划大赛', '为了提高学生的就业能力，引导学生对三年大学生活进行合理有序的安排，信息学院于5月19日举办了第4届大学生职业生涯规划大赛，经过层层筛选，共有12位选手进入决赛。比赛流程主要分为选手PPT演讲及评委提问环节。比赛过程中，选手们通过精美的PPT，围绕自我认知、自我分析、环境分析等角度，结合自身兴趣及特长，全面深入地对自我发展进行了分析与规划。         有的选手因为热爱网页设计，计划成为一名web前端开发设计师，而有的选手则基于对自身兴趣与性格的探索，决定向程序设计开发员一职进发。令人印象深刻的一位电气自动化专业的选手，通过SWOT分析，综合考虑了自身的优势、劣势、机遇及危机，结合自身的发展倾向--德国的工匠精神，希望成为一名精益求精的设计师，并且还为自己准备了plan B:一名优秀的自动化产品销售员。这都无不体现了当代大学生的新思路，新智慧。经过12位选手的逐一展示，最终计算机153班夏启成、计算机151班王坤、泮李津等同学凭借扎实充分的准备，激情的演讲最终分获一、二、三等奖的荣誉。 （徐露诗 学工办/团委）', 'http://info.jhc.cn/2016/0520/c201a54699/page.htm', 'http://info.jhc.cn/_upload/article/images/68/8a/2b5cf37a4af794e29604ad5c025c/fa3c7ba9-a6f3-4cea-8c1e-e63fe20e0238.jpg', '2017-05-17 14:13:51.377122');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (174, '周荃', '', '系统管理员', '2016-05-20 00:00:00', '信息学院召开本科师生座谈会', '为了深入了解本科学生在本学期学习情况，反映学生在学习中遇到的问题，为后续的教学工作提供参考和经验，应学校督导处要求，信息学院于近日分两场展开教师和学生座谈。会上，大家畅所欲言，就课程设置、教材选取、教学管理、课堂教学手段、学生学习情况及遇到的问题等方面发表了各自的意见及建议。徐澍敏处长肯定了学院四年制高职人才培养的各项工作，会后将与相关部门与老师做好沟通，并继续关注和支持我院的工作。   据悉，学院将深化与浙江理工大学在管理、教学上的衔接与合作，同时加强与四年制高职人才培养试点院校的学习交流。 （徐华丹  教科办）', 'http://info.jhc.cn/2016/0520/c201a54698/page.htm', 'http://info.jhc.cn/upload/20160520meeting.jpg', '2017-05-17 14:13:51.399172');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (175, '周荃', '', '系统管理员', '2016-05-20 00:00:00', '信息工程学院以“两学一做”促党风廉政建设', '为进一步深化“两学一做”学习教育活动，加强党风廉政教育，近日，信息学院党委组织党政领导班子成员和全体中层干部、全体支委及以上人员专题学习党风廉政建设。会议传达学习了省委十三届九次全会精神和校党风廉政建设工作会议精神以及《中共金华职业技术学院委员会落实党风廉政建设监督责任的实施办法（试行）》、《中共金华职业技术学院委员会落实党风廉政建设责任制实施方法（试行）》有关精神，并结合学校党风廉政建设工作会议精神和《信息学院2016年党风廉政建设工作计划》进行了讨论交流，同时对《信息学院2016年“两学一做”学习教育方案》作了全面部署。大家一致认为，广大党员干部要明确党风廉政建设的主体责任，提高廉政意识、风险意识、法制意识，不断完善制度机制，加强风险防控；要求全院上下精心组织、积极谋划，坚持领导带头、党员带动，以点带面，发挥媒体作用，营造浓厚氛围，做好分类指导和督导检查，切实推进学院改革发展。 （陈德仙、周荃 办公室）', 'http://info.jhc.cn/2016/0520/c201a54697/page.htm', NULL, '2017-05-17 14:13:51.421526');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (176, '周荃', '', '系统管理员', '2016-05-20 00:00:00', '信息学院召开“六个一批”建设推进会', '为进一步贯彻落实《金华职业技术学院课堂教学创新实施计划》文件精神，遵循边建边用，受益学生的原则，提高课堂创新项目的实施质量和效果，近日，信息学院召开了项目建设推进会。会上，各位项目负责人对项目建设的内容和验收标准有了更明确的认识。今后项目的工作重点在教学材料的制作和完善以及课堂教学的落实。据悉，信息学院将对“六个一批”创新项目进行跟踪、检查、督导、评价，包括：公开课，推门听课，检查Moodle平台建设使用情况，定期汇报项目建设进度等。 （徐华丹 教科办）', 'http://info.jhc.cn/2016/0520/c201a54696/page.htm', NULL, '2017-05-17 14:13:51.443759');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (177, '周荃', '', '系统管理员', '2016-05-19 00:00:00', '金职院“信息杯”第二届教职工羽毛球比赛成功举办', '金职院“信息杯”第二届教职工羽毛球比赛于5月16-18号在学校体育馆成功举行。本次比赛由校工会主办，信息工程学院承办、公共基础学院协办，比赛项目为混和团体比赛（设领导双打、混和双打（两场）、男子双打、男子单打五场比赛组成），共有15个分工会组织队伍报名参赛，参赛队员150余名。   在三天的比赛过程中，各参赛选手在球场上挥洒汗水与笑容，运动员在比赛中腾挪跳跃，身姿飒爽，跳杀，网前截杀，后场吊球，将球技发挥的淋漓尽致，赢得了现场观众的阵阵喝彩，特别是在淘汰赛阶段，各参赛队在选手的出场顺序进行排兵步阵，给比赛增加了特殊的魅力。最后获得本次比赛前八名的是医学院分工会、总院机关分工会、后勤分工会、信息工程学院分工会、师范学院分工会、公共基础学院分工会、金义网络经济学院分工会、艺术设计学院分工会。此次比赛的成功举办，不仅丰富了教职工的业余生活，增进大家对体育锻炼的兴趣，提高了各学院的集体凝聚力，同时也展现了广大老师团结协作、积极向上、努力拼搏、不断进取的精神风貌。 （吕远红 周荃 花有清）', 'http://info.jhc.cn/2016/0519/c201a54695/page.htm', 'http://info.jhc.cn/_upload/article/images/54/03/f2d6f8c54ca8a415341d486ccd08/898791b6-4ce5-4bec-ae6d-ef8a2b34a230.jpg', '2017-05-17 14:13:51.465902');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (178, '周荃', '', '系统管理员', '2016-05-18 00:00:00', '《金华晚报》报道我院代表队获得国赛一等奖', '金职院学子获全国职业技能大赛一等奖 5月11日，2016年全国职业院校技能大赛“现代电气控制系统安装与调试”赛项在天津中德应用大学圆满落幕，金职院信息学院代表队发挥出色，首次参赛即获得全国一等奖的好成绩。本次竞赛共有来自全国27个省、自治区、直辖市的71支代表队，共142名选手参加。竞赛总计4小时，内容涉及PLC编程、触摸屏组态设计、网络通讯设置、伺服电机与步进电机控制、变频调速等多方面的专业理论知识，全面考察选手在电气元件安装、线路连接及制作、电路原理图绘制、机床电路排故等方面的实践动手能力和团队协作精神。经过两天的激烈角逐，最终由金职院信息学院花有清和楼蔚松两位老师、郑鸿宇和杨晓康两名学生组成的代表队荣获冠军。（周荃 缪小芬） 原文地址：http://epaper.jhnews.com.cn/site1/jhwb/html/2016-05/18/content_1707071.htm', 'http://info.jhc.cn/2016/0518/c201a54694/page.htm', NULL, '2017-05-17 14:13:51.48811');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (179, '周荃', '', '系统管理员', '2016-05-17 00:00:00', '对接虚拟现实新技术，深化计算机应用技术专业人才培养', '近日，我校信息学院计算机应用技术专业教师代表受邀参加全国职业院校计算机类专业指导委员会年会。会上，我校教师与来自教育部、计算机类专业指导委员会、行业企业、高职院校的专家学者和一线教师，围绕计算机类新一轮专业目录调整后的专业教学标准制定进行了深入交流，重点研讨计算机应用技术、移动应用开发、软件技术、数字媒体应用技术、云计算技术与应用5个专业如何与虚拟现实、云计算、大数据、移动互联新技术对接，并就新的建设方案的形成进行了院校汇报、行业企业发言与分组研讨。 我校信息学院计算机应用技术专业结合专业建设基础，就“交互媒体制作”方向2D及3D虚拟现实技术的课程融入、创业工作室运行、实训室建设与大连东软件信息学院等院校同仁进行了深入交流，从岗位走向、课程设置、实训室建设、校企合作、就业创业等方面积极商讨对策。在教指委推动下，我院荣幸加入了数字媒体应用技术、计算机应用技术专业的教学标准起草小组。会后，与会教师调研了VR企业北京一拍科技有限公司、爱迪斯通（北京）科技有限公司，听取企业专家和技术人员对VR课程建设、VR实验室建设方案的建议和意见，并体验了最新的沉浸式VR设备和典型应用案例。虚拟现实技术是借助计算机系统及传感器技术生成三维环境，创造出一种崭新的人机交互方式，通过调动用户各种感官（视觉、听觉、触觉、嗅觉等）来享受更加真实的、身临其境的体验。随着硬件性能的提升和成本的大幅度降低，近年来虚拟现实产品获得了广泛发展。2016年，是虚拟现实元年。美国消费电子展、上海消费电子展上，虚拟现实产品成为展会的绝对主角。虚拟现实技术衍生的多媒体技术开发工程师、VR图像视频研发工程师、Unity 3D VR工程师、新媒体专员等岗位成位新的稀缺、高薪岗位。与虚拟现实新技术的对接，是我院计算机应用技术专业对接IT新技术，深化“交互媒体制作”方向人才培养推进的积极举措。（叶继阳、宣翠仙 信息化事业部）', 'http://info.jhc.cn/2016/0517/c201a54693/page.htm', NULL, '2017-05-17 14:13:51.510531');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (180, '周荃', '', '系统管理员', '2016-05-17 00:00:00', '信息学院启动学生干部培训', '为了进一步加强学生干部队伍建设，近日，信息学院第三期青马工程大学生领袖成长营学生干部培训班正式开班。  据悉，培训为期一个月，参加培训的对象主要包括学生干部、社团社长和网格长100余人，内容主要包括入职宣誓仪式、主题讲座、素质拓展、团队建设团体辅导、学生干部工作专题讨论等，以切实提升学生干部组织、管理、协调等能力。（丰辉 学工办/团委）', 'http://info.jhc.cn/2016/0517/c201a54692/page.htm', 'http://info.jhc.cn/upload/2016qingma3.jpg', '2017-05-17 14:13:51.532567');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (181, '周荃', '', '系统管理员', '2016-05-16 00:00:00', '我院顺利举办第十三届体育运动会', '5月14日，信息学院第十三届运动会在我校体育场顺利完成各项预定赛事，圆满落下帷幕。   此次运动会，全院共38个班，396名运动员参加了20个大项的角逐。经过一天紧张的比赛，软件141班和计算机151班分获大二大一年级组第一名，网络151班等4个班级获得精神文明奖。   据悉，此次比赛中物联网141班毕玉娜、自动化141班林锦俊2位同学打破学院记录2项，这也是这两位同学第二次打破学院记录。 (郭天鹏 学工办/团委)', 'http://info.jhc.cn/2016/0516/c201a54691/page.htm', 'http://info.jhc.cn/upload/20160514Smeeting.jpg|http://info.jhc.cn/upload/20160514Smeeting1.jpg', '2017-05-17 14:13:51.554873');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (182, '周荃', '', '系统管理员', '2016-05-16 00:00:00', '第二届信息好声音——律动之音决赛', '“南山南，北秋悲，南山有谷堆……”。5月12日晚六点半，师范小礼堂余音袅袅，第二届信息好声音——律动之音决赛在此火热进行。    参加决赛的共有8组选手，决赛分为2轮，第一轮是选手两两组合合唱一首歌曲，4组选手帅气十足，边唱边跳，感染观众；第二轮是选手的个人表演。《海阔天空》、《天下》、《最美的太阳》……一首首曲目被选手演绎的淋淋尽致，用上道具，用上乐器，清耳悦心、韵味十足。邀请到学生处、师范学院、学院学工办的老师和文艺部部长担任决赛评委。   最终，自动化151班鲍亚星凭借一首《悟空》获得了第二届信息好声音的冠军，应电153班金仁洲获得第二名，计算机152班江程涛获得第三名。 (郭天鹏 学工办/团委)', 'http://info.jhc.cn/2016/0516/c201a54690/page.htm', 'http://info.jhc.cn/upload/20160512singing2.jpg|http://info.jhc.cn/upload/20160512singing.jpg', '2017-05-17 14:13:51.577109');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (183, '周荃', '', '系统管理员', '2016-05-13 00:00:00', '温州职业技术学院信息技术系主任一行来我院考察交流', '5月13日上午，温州职业技术学院信息技术系主任田启明一行5人来我院考察交流。学院领导班子，网络专业主任、信息安全创新实验室负责人出席了交流活动。座谈中，赵敏笑代表学院对客人的来访表示欢迎，并简要介绍了学院的发展状况。宾主双方围绕软件专业与网络专业人才培养方案、校企合作、实训室建设等内容进行了研讨，重点就信息安全创新实验室建设展开深入交流。 （周荃 办公室）', 'http://info.jhc.cn/2016/0513/c201a54689/page.htm', NULL, '2017-05-17 14:13:56.142572');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (184, '周荃', '', '系统管理员', '2016-05-13 00:00:00', '常州工程职业技术学院机电与汽车工程学院党总支书记刘素芳一行来我院考察交流', '5月13日上午，常州工程职业技术学院机电与汽车工程学院党总支书记刘素芳一行4人来我院考察交流。学院领导班子，办公室、团委负责人出席了交流活动。座谈中，贾美倩代表学院对客人的来访表示欢迎，并简要介绍了学院的发展状况。宾主双方围绕二级学院管理、学生素质教育、校园文化建设、岗位业务等内容进行了深入探讨与交流。 （周荃 办公室）', 'http://info.jhc.cn/2016/0513/c201a54688/page.htm', NULL, '2017-05-17 14:13:56.177419');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (185, '周荃', '', '系统管理员', '2016-05-13 00:00:00', '信息学院举办校园安全教育讲座', '5月11日晚，信息学院邀请到金华市市公安局江南分局苏孟派出所副所长楼胜君为全院大一学生主讲“大学生安全教育”主题讲座。讲座上，楼所长向同学们通报了开学至今发生的校园安全案件，目前校园案件以诈骗和盗窃为主，“校园安全工作要抓早抓小”，他从非接触性诈骗防范、盗窃防范、人身安全和消防安全这四个方面展开，强调学校务必加强学生安全法制教育，坚决杜绝网上兼职刷单；学生要提高自我防范意识，妥善保管个人财物，平常在寝室里注意检查门房锁或加装简易的技防设施；自行车尽量停放在有人员管理或有监控设施的地段等等。希望同学们将安全教育落实到日常生活中，预防各类事故的发生。   通过安全教育讲座，同学们树立了正确的安全观念，提高了安全防卫意识，加强了安全知识普及，并收获到许多实用的安全小技巧和自我保护的方法，为今后的学习生活提供了很大帮助。（徐露诗 学工办/团委）', 'http://info.jhc.cn/2016/0513/c201a54687/page.htm', 'http://info.jhc.cn/upload/20160511securityLec.jpg', '2017-05-17 14:13:56.199606');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (188, '周荃', '', '系统管理员', '2016-05-12 00:00:00', '信息学子喜获全国职业技能大赛“现代电气控制系统安装与调试”赛项一等奖', '5月11日，2016年全国职业院校技能大赛“现代电气控制系统安装与调试”赛项在天津中德应用大学圆满落幕，我校信息工程学院代表队发挥出色，首次参赛即获得全国一等奖的好成绩。   本次竞赛共有来自全国27个省、自治区、直辖市的71支代表队，共142名选手参加。竞赛内容涉及PLC编程、触摸屏组态设计、网络通讯设置、伺服电机与步进电机控制、变频调速等多方面的专业理论知识，比赛时间为4小时，要求选手在电气元件安装、线路连接及制作、电路原理图绘制、机床电路排故等方面具备很强的实践动手能力和良好的团队协作精神。经过两天的激烈角逐，由我校信息学院花有清、楼蔚松两位老师指导，郑鸿宇、杨晓康两位学生组成的代表队克服了竞赛训练时间短、专业知识技能综合要求高等困难，在比赛中凭借扎实的专业理论知识、良好的实践动手能力和严格执行教师赛前制定的技战术，最终喜获比赛一等奖。 （李瑞东、马汝星 教科办/电气自动化工程部）', 'http://info.jhc.cn/2016/0512/c201a54684/page.htm', 'http://info.jhc.cn/upload/20160511ZDHNO1.jpg', '2017-05-17 14:13:56.266377');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (189, '周荃', '', '系统管理员', '2016-05-12 00:00:00', '金华中心血站来我院开展献血活动', '“听说东实训楼广场有献血活动”，“真的吗？我们一起去献血吧”。5月11日中午，信息学院邀请金华中心血站在东实训楼广场组织了“献血献爱心·血浓情更浓”主题献血活动。   献血活动历时5个小时，活动现场热情洋溢。在老师的带领下，整场活动整齐有序，同学们有条不紊地进行填表、验血、献血等步骤。本次活动我院共计献血90余人，体现了信息学院师生奉献、有爱、互助的精神，也向全体师生传递了满满的正能量。据悉，12日金华中心血站将去金湖餐厅门口举行献血活动。一位刚献完血的同学拿着献血证和小礼物，激动地说到，“针插进血管的时候有点痛，后面就没感觉了，我献了300ml，感觉还行，下次我还会参加献血活动”。 （郭天鹏  学工办/团委）', 'http://info.jhc.cn/2016/0512/c201a54683/page.htm', 'http://info.jhc.cn/upload/20160511volunteerBD.jpg', '2017-05-17 14:13:56.310792');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (190, '周荃', '', '系统管理员', '2016-05-12 00:00:00', '信息学子喜获2016年全国职业院校技能大赛高职组物联网技术应用比赛三等奖', '5月9日，由天津轻工职业技术学院承办的2016年全国职业院校技能大赛“物联网技术应用”赛项落下帷幕，由我校信息工程学院派出的代表队喜获团体三等奖。   本次比赛有共来自全国高职院校70支代表队参加，比赛要求学生根据客户需求，自行进行需求分析、系统设计、硬件选型、网络搭建，以及进行包括服务端、客户端、移动端、硬件芯片端的代码开发和配置，从而实现整套物联网应用产品的开发。内容涉及物联网应用环境安装部署、物联网感知层设计开发、物联网Android平台应用设计开发、物联网PC平台应用设计开发、嵌入式设计开发。要求参赛学生在系统设计、物联网工程部署、物联网设备调试、嵌入式产品开发、Android应用开发、.net应用开发等方面具备扎实的专业理论知识、较强的实践能力和良好的团队协作精神。本次比赛所采用的比赛方式对参赛选手的综合能力提出了很高的要求，参赛师生团队克服训练时间短、竞赛内容涉及面广等各种困难，细致准备、艰苦训练。最终由余炜、王静两位老师指导，董国辉、胡淳榜、杨小龙三位学生组成的代表队沉着应战、努力拼搏，经过4个小时的激烈角逐，获团体三等奖的好成绩。  （苏红富 通信与网络工程部）', 'http://info.jhc.cn/2016/0512/c201a54682/page.htm', 'http://info.jhc.cn/upload/20160509wulianwNO3.jpg', '2017-05-17 14:13:56.33306');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (191, '周荃', '', '系统管理员', '2016-05-12 00:00:00', '信息学子荣获2016年全国职业院校技能大赛“嵌入式技术与应用开发”赛项三等奖', '5月8日，2016年全国职业院校技能大赛高职组“嵌入式技术与应用开发”赛项在天津电子信息职业技术学院开赛，共有来自全国26个省级行政单位的155支代表队参与了角逐。信息学子赵培荣、徐建伟和潘鑫、胡文鑫组成的两支代表队分别荣获“嵌入式产品装配调试”和“嵌入式应用开发”分赛项团体三等奖。   不同于往年，今年的比赛结合通讯信息领域的快速发展增加了“嵌入式产品装配调试”分赛项，要求选手在8个小时内完成功能电路板的故障排除、焊接、调试及交通巡逻车的装配，并按照要求编写控制程序，使交通巡逻车在模拟的交通环境中自动完成二维码识别、超声波测距、红外通信、光照强度检测、隧道排风、清障等赛道任务。“嵌入式应用开发”分赛项要求选手在32项赛道任务中随机抽取任务流程表编写Android应用程序，运用无线通信、传感器检测等技术，使移动终端能自动控制交通巡逻车在模拟的交通环境中完成各项赛道任务，Android智能设备上实时定位显示交通巡逻车的行进路径。本赛项以培养“工匠精神”为主题，并将操作安全、节能降耗、团队合作等职业素养纳入考核范围。要求选手不仅具备扎实的基本功和较强的心理素质，还需有很强的临场应变、思维分析能力。 （范灵芝、刘子坚  教科办/电子产品开发部）', 'http://info.jhc.cn/2016/0512/c201a54681/page.htm', 'http://info.jhc.cn/upload/20160508QRSNO3.jpg', '2017-05-17 14:13:56.355314');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (192, '周荃', '', '系统管理员', '2016-05-12 00:00:00', '浓情五月天，感恩母亲节', '“母亲”是天下最美的词语，是千万游子最为感动的词语，5月7日-8日信息学院开展了以“浓情五月天，感恩母亲节”系列活动。   据悉，此次活动分为两天举行，第一天由馨言心理社和院青大队联合在江北人民广场举办“感恩母亲，千里传情”活动，活动中邀请了路过的小朋友用画笔画出心中最美的妈妈，画完后孩子们拿着画和现实中的最美妈妈合照留作纪念，让孩子以轻松的心态对母亲心怀感恩，同时许多群众自觉的参与到我们活动中，在明信片上写下对母亲的“情话”表达感恩之情同时送上节日祝福。第二天，在东教学楼大厅开展“‘微孝’母亲节”活动，同学们积极参与，通过短信和便利贴向远在家乡的母亲传达了最美好的节日祝福。乌鸟私情，愿乞终养。此次活动，让我们心怀感恩，理解母亲的辛苦，感激养育之恩。                                   （何碧峰、陈青霞学工办/团委）', 'http://info.jhc.cn/2016/0512/c201a54680/page.htm', 'http://info.jhc.cn/upload/20160508mothersD.jpg', '2017-05-17 14:13:56.377605');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (193, '周荃', '', '系统管理员', '2016-05-06 00:00:00', '我的助教成长记', '“助教不仅仅是协助、翻译，而是真正意义上的合作教师。”5日，信息学院网络专业的两名优秀助教丁晨和宋群老师向全院助教作了助教的经验分享。通过半年的摸索和尝试，成功实践了助教成长四步曲：外教完成教学材料、共同探讨材料可行性、与外教共同授课、反馈授课提出整改。大家一致认为，成为一名优秀的助教，就要学会管理课堂，学习外教的“零距离”教学法。   据悉，为了更好地提升中新班的教学质量，信息学院为每门外方授课课程安排一名专业教师作为课程助教，在辅助外方教师完成教学任务、提高学生学习有效性的同时，也为专业教师开拓视野、提升教学国际化水平起到了积极作用。此次活动是“中新合作教学”系列活动之一，学院先后开展了集体备考、优秀学生经验分享会等活动。 （蔡金晶 教科办）', 'http://info.jhc.cn/2016/0506/c201a54679/page.htm', 'http://info.jhc.cn/upload/20160505coT.jpg', '2017-05-17 14:13:56.401442');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (194, '周荃', '', '系统管理员', '2016-05-06 00:00:00', '信息学院举办青年教师教学技能比赛', '近日，信息学院举办青年教师教学技能比赛，全体教师到场观摩学习。比赛中，7位教师结合教育教学规律和课程特点，利用微视频、PPT、板书、实践操作、软件仿真等教学手段，把各个知识充分展现出来。课上，有严谨的专业知识、有风趣幽默的话语、有形象生动的实例，充分展示了教师们扎实的基本功与先进的教学理念。评委们通过深入研究评分细则，坚守公平公正的原则，从教学设计、课堂教学和教学反思进行评比，评选出各个奖项。   据悉，本次获胜教师将代表学院参加校第九届青年教师教学竞赛。 （徐华丹 教科办）', 'http://info.jhc.cn/2016/0506/c201a54678/page.htm', 'http://info.jhc.cn/upload/20160505Tcomp.jpg', '2017-05-17 14:13:56.422006');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (195, '周荃', '', '系统管理员', '2016-05-05 00:00:00', '信息工程学院扎实推进“两学一做”学习教育活动', '5月4日，信息工程学院党委在东实北215会议室举行“两学一做”学习教育再部署活动暨中心组理论学习会，信息学院中层及以上人员、各支部书记参加了会议。会议通报了中组部、省委、市委及校党委组织部关于“学党章党规、学系列讲话，做合格党员”等方案通知精神，专题学习了习近平总书记关于党员干部要自觉增强“四个意识”的系列讲话精神。同时对各支部及全体党员就“两学一做”的学习要求及方法进行了再次明确。会议要求全体师生党员以支部为单位，以组织生活为形式，通过集中学习、个人自学等多种形式开展对党章党规、习近平总书记系列讲话和党的十八大重要会议的学习，通过这些学习夯实理论基础，提升党员修养，增强思考能力，提高业务水平。会议强调，通过在全体党员中开展“两学一做”学习教育活动，引导全体党员增强政治意识、大局意识、核心意识、看齐意识，团结带领广大师生，向学校政策看齐，向教育教学改革要求看齐，树立“立德树人”的大局意识，树立“一切为了学生、为了一切学生”的核心意识，切实做好学院改革、发展各项工作，为学院“十三五”建设贡献力量。 （陈德仙 办公室）', 'http://info.jhc.cn/2016/0505/c201a54677/page.htm', NULL, '2017-05-17 14:13:56.444178');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (196, '周荃', '', '系统管理员', '2016-05-04 00:00:00', '信息学院自动化142班荣获校“五四红旗团支部”称号', '为贯彻落实党的十八大、十八届三中、四中、五中全会和习近平总书记系列重要讲话精神，推动全校团的基层组织建设再上新台阶，学校于4月25日开展“我的支部，我的团”五四红旗团支部评比活动。本次评比总共有15个团支部参加，其中我院有两个团支部入围评比。此次评比采用PPT汇报的形式，由每个入选的团支部代表上台进行PPT汇报，限时6分钟，各团支部就本团支部的“思想引领”、“组织建设”、“活动开展”、“特色工作”四个方面进行了精彩的展示。最终，在激烈的竞争中，自动化142团支部凭借扎实的基础团建、党建，优秀的学习、活动、特色建设成果获得优秀“五四红旗团支部”的荣誉称号！ （吕威威 学工办/团委）', 'http://info.jhc.cn/2016/0504/c201a54676/page.htm', NULL, '2017-05-17 14:13:56.466353');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (197, '周荃', '', '系统管理员', '2016-04-29 00:00:00', '信息学院开展新任学生干部团体辅导培训', '“我是外联部 龙佳乐”“我是社管会 林威”响亮的声音在大学生活动中心回响，原来是信息学院正在针对新选上来的副部进行团体辅导培训，而第一个环节就是大声地报出自己的名字。为了进一步强化副部们的责任意识，学院为小鲜肉们安排了以“团结共进”、“坚持不弃”为主题的系列培训。   培训由何碧峰老师、徐露诗老师带领开展，50多位新任学生干部通过“心电感应”、“同甘共苦”、“拍七令”、“心有千千结”等游戏互动体验团队建设和合作的重要性。在“同甘共苦”这一环节中，每一位同学围成圈不断地向中心靠近，随着包围圈越来越拥挤，大家都不得不思考如何能够保持圆圈而不被挤开，最后一只只手都紧紧地牵在了一起，通过合作没有一位成员被挤出圆圈。一位学生副部在参与团辅培训后表示：从前对合作与坚持的体会并不深刻，觉得把自己部门的事务做好就可以，经过这次培训明白了合作的重要性，会将今天学习到的东西运用到今后的实际工作中去的。 （徐露诗 学工办/团委）', 'http://info.jhc.cn/2016/0429/c201a54675/page.htm', 'http://info.jhc.cn/upload/20160428tuanfu.jpg', '2017-05-17 14:14:01.042491');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (198, '周荃', '', '系统管理员', '2016-04-27 00:00:00', '《金华晚报》报道我院学生创作金职版《青花瓷》', '改编《青花瓷》，送给所有“金职人”一群理工男做了件很文艺范的事 　　一曲改编的《青花瓷》，勾勒出金职院里的唯美中国风；一个好听的男声，深情地演绎了这首歌；一张张有故事的配图，带给“金职人”满满的回忆……把这些元素整合在一起，就成了一条“爆款”微信。让人没想到的是，改编歌词的、演唱的、摄影的、编辑微信的，全都是金职院信息工程学院的理工男。　　金职版《青花瓷》，瞬间火了　　“婺城故里忆江南，画卷行人迷。金职初遇的烟云，一隔数年起……”日前，该学院团委微信公众平台发布了金职版《青花瓷》。细读歌词，有对新生的欢迎，有对毕业生的告别，有对青春的回忆，有对母校的深情。每个“金职人”都可以从中找到触动自己的那个点。没过几天，这条微信的阅读量就将近6000了。　　唯美的歌词里，带着故事；精挑细选的配图，又对应着一句句歌词。正如微信末尾那一小段文字所写的：“在金职院，藏着不期而遇的风景；在金职院，有着撩动心扉的故事。这一切，只因你走过。这些回忆，只能由你一一来拆开。”　　读者的热情瞬间被点燃了，很多人纷纷留言。　　“不悔梦归处，只恨太匆匆！任何关于金职的话题，都是我们青春里不可卸载的回忆，我们会再去那里寻找当年的自己。”　　“全是我大学回忆！”　　“2005年毕业，建工学院，朱锐路过回忆。”　　“让我们这些毕业多年的学子，回忆满满！”　　“挑个好天气回去吧！”　　文艺范的理工男，比比皆是　　改编歌词的，是该学院软件141班的方亮。他喜欢看书、写作，平时会写写歌词，最近还准备写一本书，到时出版，作为留给自己的大学纪念。大一时，他就进了学院新闻中心，采编、摄影、后期制作、微信编辑等，样样都会。他还加入了学校热风文学社，并且是副社长。“文学社总共20多人，其中5个是我们学院的。”除了爱好文学的，还有玩乐器的、摄影的、摄像的……总之，文艺范的理工男比比皆是。这跟该学院向来注重培养学生的人文素养不无关系。　　方亮最喜欢的词作家是林夕，“他的词比较有味道”。自己写歌词，有时容易有时难。方亮说，他改编《青花瓷》，第一天写了第一段歌词，就卡住了。过了两天，忽然有了灵感，于是一气呵成。他在歌词里，写下了从学校东门进来的一路风景，浓缩了许多回忆。　　配图主要是方亮和另外3个同学拍的，花了好几天时间。为了取一个好角度，他们爬到教学楼顶楼拍照。同一个景，会拍很多次，直到满意为止。　　这首歌的演唱者，是该学院自动化151班的鲍亚星。他本身喜欢中国风的歌曲，于是，老师一推荐，他就兴冲冲地去录音棚录歌了。“我经常忘词，录了很多遍才完成。在唱歌方面，我不是专业的，但我用的感情比较深。因为我是金华人，在这个城市待了21年，对它很熟悉。这里的每个地方都很吸引我。”这种真情实意，流淌在歌声里，感染了很多人。　　方亮说，改编这首《青花瓷》，从策划到最终出炉，串联了很多乐趣，如今得到认可，他们很高兴。接下来，他们想再做一个视频版的，希望更多人感受到其中的爱校情怀。 　　本报记者 姚艳霞     原文地址：http://epaper.jhnews.com.cn/site1/jhwb/html/2016-04/27/content_1699536.htm', 'http://info.jhc.cn/2016/0427/c201a54674/page.htm', NULL, '2017-05-17 14:14:01.093833');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (199, '周荃', '', '系统管理员', '2016-04-27 00:00:00', '缘来有电，移动无忧——记学院绿色环保充电宝制作比赛', '为了响应国家绿色环保的理念，丰富同学们的课余生活。4月24日，信息学院社团委员会举办了以“缘来有电，移动无忧”绿色环保充电宝制作比赛。   据悉，此次比赛共吸引了40多名学生前来参加。比赛现场，同学们分为4人一组，在专业人员的指导下，分工协作，连接线路、充电池、制作外形，忙得不可开交。经过近3小时的认真制作，一件件兼具实用性和时尚性的手摇充电宝“新鲜出炉”。评委根据发电效果、环保理念以及用时长短等进行了综合评定，最终，由王益明等4名同学组成的小组获得第一名。赛后，参赛学生表示，“此次活动教会他们制作充电宝的原理，完成制作后很有成就感”。这次社团活动让同学们把专业的知识运用到实践中，从制作中了解原理和专业知识。  （郭天鹏  学工办/团委）', 'http://info.jhc.cn/2016/0427/c201a54673/page.htm', 'http://info.jhc.cn/upload/20160424charger.jpg', '2017-05-17 14:14:01.115934');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (200, '周荃', '', '系统管理员', '2016-04-25 00:00:00', '用声音渲染天空——寻找信息律动之音', '4月22日，信息学院在东实训楼大厅举行了信息好声音复赛，共有15名学生入围，由学工办老师、学生干部以及师范学院相关专业学生组成评委团。复赛以3人一组PK的形式进行淘汰赛。选手们在台上尽情展示、动情演绎，整场比赛气氛热烈，一曲曲动听的旋律在东实训楼上空悠扬回响，掌声喝彩声经久不息。最终，医仪141班饶文彬、应电152班王俊程等5位选手胜出，进入下一轮决赛。现场观众啧啧称赞，“学生唱的都很好听，我听完了全程，希望决赛的时候也能来听”。据悉，信息好声音的决赛将于5月初进行，除了复赛晋级的5位选手外，将由微信投票选出3名选手共同赴赛。  （郭天鹏  学工办/团委）', 'http://info.jhc.cn/2016/0425/c201a54672/page.htm', NULL, '2017-05-17 14:14:01.136055');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (201, '王伟斌、梅旭时', '信息化事业部', '周荃', '2016-04-24 00:00:00', '信息学子在省程序设计竞赛斩获佳绩', '“持续5个小时的赛程”、“全英文题目”、“303支参赛队”，这些显目的关键字都指向同一项赛事——浙江省第13届大学生程序设计竞赛。4月23日，在浙江大学美丽的紫金港校区落下帷幕的此项赛事中，我校信息学院4个参赛队不负众望，斩获2银2铜的好成绩。   ACM大学生程序设计竞赛素有“计算机界的奥林匹克”之美誉，因其公平、公正、公开的赛事特点，每年都吸引众多大学生编程爱好者参加。今年的浙江省大学生程序设计竞赛，吸引了全省77所本专科院校的303支参赛队伍，其影响之大、规模之广，在众多大学生竞赛中独树一帜。在这集齐了全省大学生编程精英的年度赛事中，信息学院的参赛选手沉重应战，以自己扎实的编程基本功和顽强的心理素质，在5个小时的马拉松式赛程中，发挥稳定，成绩斐然，其中张洁、王良海、王建源和谢慧云、陈涛、王麒皓2个团队获得银奖，另2个团队获得铜奖。据悉，在“互联网+”的国家战略下，全社会对编程人才的需求急剧增加，而高端程序员更是企业争相抢夺的“香饽饽”。信息学院紧紧抓住软件行业发展机遇，多途径、多举措培养高端软件开发人才。通过参加大学生程序设计等学科竞赛，以赛促学，以赛促教，将赛事组织形式引入日常教学，提升了学生学习兴趣，也提高了程序设计类课程教学效果；通过开展校企合作，引入企业软件开发案例，使学生编程学习与企业实际相融合，更具实践性；及时调整专业培养方案，将最新软件开发技术引入人才培养方案，使学院培养的技术人才始终是行业急需专业人才。近几年的麦可斯调查显示，软件专业毕业生的薪资水平多年位列学校各专业榜首，显示出信息学院在培养高端软件人才上各举措成效初显。 （王伟斌、梅旭时信息化事业部）', 'http://info.jhc.cn/2016/0424/c201a54671/page.htm', 'http://info.jhc.cn/_upload/article/images/a1/e9/f5acd1454de088b3b24e7069492d/f3596c2e-9371-460a-a919-b98d133fc640.jpg', '2017-05-17 14:14:01.156997');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (202, '周荃', '', '系统管理员', '2016-04-22 00:00:00', '信息学院学生于校首届国学经典知识竞赛总决赛夺得季军', '为了弘扬国学知识，激发大学生对国学经典的兴趣和热爱，金职院首届经典国学知识竞赛正在火热进行中。经过两轮激烈的角逐，我院由六组选手入围总决赛。4月21日，总决赛现场上演了一场高水平的国学知识巅峰对决。必答题、抢答题、风险题……赛场上的每个环节都精彩纷呈。场内不时回荡着精彩回答引爆全场的掌声，也有参赛选手失误抢答的遗憾声，一幕幕场景让人印象深刻。最终，我院由黄方婷、吕梦娜、林子淳三名大一学生组成的队伍凭借扎实的国学知识功底和良好的心理素质夺得季军，充分展示了我院学生的风采。通过本次比赛，同学们走近了国学，更丰富了自身的文化底蕴，很多学生表示很久没有看到如此精彩的脑力对决，今后将会多多关注并参与此类活动，希望这样的活动能继续展开。 （徐露诗 学工办/团委）', 'http://info.jhc.cn/2016/0422/c201a54670/page.htm', NULL, '2017-05-17 14:14:01.178551');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (203, '丰辉', '学工办/团委', '周荃', '2016-04-22 00:00:00', '信息学院业余党校创新举措，确保培训起实效', '4月22日，信息学院第二十五期业余党校开班，81名学员参加了学习。   据悉，为有效提升业余党校教学效果，加强学生思想政治教育，提高学生解决问题、分析问题的能力，信息学院对业余党校课程体系进行了创新和优化，最终增强业余党校工作的系统性，形成模块化的课程内容，多元化的课程形式，小班化的教学管理，网络化的考核方式，团队化的师资队伍。今年，学院党校除安排校内外老师授课外，还邀请了企业党建负责人以支部为单位对学员进行小班化培训。考核成绩由平时成绩、考试成绩、时政热点案例分析、社会实践调查报告、红色小品表演、文明监督岗六个部分构成，同时结合党支部负责人、专业辅导员、党校管理人员对学生的测评结果为党校学员建立学习档案，并记入学生个人档案和个人思想品德实践课中，作为其他考核评比的依据。 （丰辉 学工办/团委）', 'http://info.jhc.cn/2016/0422/c201a54669/page.htm', 'http://info.jhc.cn/_upload/article/images/46/fd/b8a646a5496da322d72bd852114c/c15937bb-e279-4ad0-bba1-6601a22b943c.jpg', '2017-05-17 14:14:01.200835');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (204, '周荃', '办公室', '周荃', '2016-04-21 00:00:00', '信息学院举办中外师生气排球友谊赛', '“克雷格，加油！”“传球、传球！”20日下午，校排球场上热闹非凡，中新网络专业师生气排球友谊赛正在火热进行。   此次比赛在筹备阶段就得到了中外师生的积极响应，共有四支队伍报名参赛，每队由一名外教和四名学生组成。比赛分为两组，两两对决。排球场上气氛活跃，展开了一轮轮比拼较量。中外师生首次合作，配合默契，互相加油鼓劲；场外拉拉队也激情洋溢，为队员们呐喊助威。   据悉，为了让学生劳逸结合，走出寝室，感受运动的魅力，信息学院举办首届中外师生气排球比赛。继气排球比赛之后，学院还将陆续开展“新西兰文化周”、“师生足球赛”等活动。  （周荃办公室）', 'http://info.jhc.cn/2016/0421/c201a54668/page.htm', 'http://info.jhc.cn/_upload/article/images/a5/30/ecb63b7a46979f683937a8a6e244/4ad125ae-5944-403f-9795-c889818d7a23.jpg|http://info.jhc.cn/_upload/article/images/a5/30/ecb63b7a46979f683937a8a6e244/f769c9d3-94ce-4e8e-a8d3-a973c4d95956.jpg', '2017-05-17 14:14:01.22314');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (205, '周荃', '', '系统管理员', '2016-04-19 00:00:00', '信息学子在2016年全国职业院校技能大赛“移动互联网应用软件开发”赛项浙江省选拔赛上喜获二等奖', '4月14日，2016年浙江省高职高专院校技能大赛“移动互联网应用软件开发”竞赛暨全国职业院校技能大赛选拔赛在浙江经贸职业技术学院隆重举行，我院吕焱飞、颜钰琳老师指导，软件专业14级印世波、王麒皓、谢慧云三位同学组成的代表队，沉着应战、努力拼搏，经过3个小时的激烈角逐，最终获得了二等奖的好成绩。本次比赛共来自全省15所院校的15支代表队参加，比赛以智能农业行业应用为考核主题，突出软件创意设计，面向移动互联网应用软件开发岗位技能，综合考察移动互联网应用的创新性、操作性、实用性和市场前景，以及在此过程中参赛选手体现的综合素质，同时兼顾团队协作精神和职业道德素养综合评定。竞赛采用实际操作形式，要求选手在规定时间内完成现场任务。考核技术点包括：智能农业应用，Android编程开发（UI，数据库编程，网络编程,多媒体应用，数据分析等）。比赛内容包括“程序排错”及“功能模块编码”和“系统详细设计文档编写”三个栏目板块，考查参赛选手对实际问题的综合分析能力，对技术架构的设计能力、对移动互联网应用开发技术的掌握程度以及操作的熟练程度。比赛主题重点突出行业发展特别是移动互联网应用软件的发展要求与趋势，满足社会对移动互联网应用软件开发技能型人才的市场需求。通过竞赛准备，引领了我院软件专业建设、课程体系建设、人才培养方案和人才培养模式等方面的改革方向，为有效提升软件专业办学水平提供了坚实的基础。 （颜钰琳  信息化事部）', 'http://info.jhc.cn/2016/0419/c201a54667/page.htm', NULL, '2017-05-17 14:14:01.245229');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (206, '苏红富、余炜', '通信与网络工程部', '周荃', '2016-04-19 00:00:00', '信息学子在2016年全国职业院校技能大赛“物联网技术应用”赛项浙江省选拔赛上喜获佳绩', '4月10日，由嘉兴职业技术学院承办的2016年全国职业院校技能大赛“物联网技术应用”赛项浙江省选拔赛落下帷幕，由我校信息工程学院派出的2只代表队喜获团体一等奖和三等奖。   本次比赛有共来自全省8所院校的13支代表队参加，比赛以新大陆物联网工程应用实训系统2.0为平台，内容涉及智慧城市物联网应用环境安装部署、物联网感知层设计开发、智慧城市物联网Android平台应用设计开发、智慧商超物联网PC平台应用设计开发。要求参赛学生在物联网工程部署、物联网设备调试、嵌入式产品开发、Android应用开发、.net应用开发等方面具备扎实的专业理论知识、较强的实践能力和良好的团队协作精神。本次比赛采用了新大陆最新的一代物联网实训系统，对参赛选手的综合能力提出了很高的要求，参赛师生团队克服训练时间短、参赛设备整体更新换代、竞赛内容涉及面广等各种困难，细致准备、艰苦训练。最终由余炜、王静两位老师指导，胡淳榜、杨小龙、何思超三位学生组成的代表队沉着应战、努力拼搏，经过4个小时的激烈角逐，获团体第2名的好成绩。 （文/苏红富图/余炜  通信与网络工程部）', 'http://info.jhc.cn/2016/0419/c201a54666/page.htm', 'http://info.jhc.cn/_upload/article/images/3a/35/0c605507483b8d52a3978127d760/20d9a4b1-e91b-40e8-bbfa-fb9020d79be2.jpg', '2017-05-17 14:14:01.267409');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (207, '林雪华', '学工办/团委', '周荃', '2016-04-15 00:00:00', '贴心服务、立体宣传，信息学院多形式开展提前招生现场精准宣传', '4月15日，信息工程学院提前招生现场确认现场场面热闹、活跃，前来确认的考生们收到学院精心制作的欢迎卡片，上面注明了确认环节结束后可自行选择智能电子技术、物联网技术、现代自动化技术等实训室参观路线，真实体验物联网技术、网络通信技术、ESL外教课堂，更有1对1中外合作班师生志愿者引导和讲解，让考生及家长充分感受到学院的贴心服务和到位的专业宣传介绍。   在东实训楼南楼，现场展示了信息学子在国家级技能竞赛场上获奖的彩色瞬时及学子作品，考生及家长时不时惊叹于物联网技术、信息安全技术、安全防范技术、智能电子技术在现代生活中的应用，更被外教小班化教学的课堂魅力深深吸引。在东实训楼北楼，大厅里循环展播了新西兰怀卡托理工学院的宣传视频、我院学生自制视频，考生和家长们被信息学子创作的“大学里等初遇——金职版《青花瓷》，写给所有金职人的回忆”打动；走进专业教师办公室，老师们和考生、家长们亲切地交流。一位来自宁波的家长表示，参观实训室后，他们更确信选择计算机网络专业是明智的，如果顺利入学，全家人都会督促孩子要认真学习专业知识，往网络安全技术方向高端就业去发展，努力把握去新西兰怀卡托理工学院留学拿本科的机会。   据悉，此次提前招生入围学生能一志愿选择中新合作办学专业，学院为此精心选拔了一支80人的中新合作班师生志愿者队伍，为考生和家长提供现场引导和讲解。16日上午，学院院长还将主持家长会，与家长们谈中新合作办学理念、专业特色和近一年的办学成效，力争提前招生最后环节的专业精准宣传，做好服务留人、专业宣传留人。 （林雪华  学工办/团委）', 'http://info.jhc.cn/2016/0415/c201a54665/page.htm', 'http://info.jhc.cn/_upload/article/images/29/99/71c23a304bf3a5bcfa4931875c83/05053858-af89-499f-b023-83b46e06d6c9.jpg|http://info.jhc.cn/_upload/article/images/29/99/71c23a304bf3a5bcfa4931875c83/a7de9c96-e5d7-4caa-944c-ae57f3434221.jpg', '2017-05-17 14:14:01.304888');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (208, '周荃', '', '系统管理员', '2016-04-15 00:00:00', '警校联训共推网络空间安全发展', '4月15日上午，全市公安网络安全业务培训班在金华职业技术学院信息学院信息安全实训室展开实训。实训内容包括网站安全漏洞分析、网络安全事件调查取证等环节，课堂上除了警校双方的专家学者经验分享外，更有模拟操作等实践环节；课后的交流活动也让参训民警表示受益匪浅。   自2014年市公安局与金华职业技术学院共建网警训练基地以来，警校双方优势互补合作紧密，在网警支队指导下，金职院信息安全创新实验室师生发挥网络安全专业特长，参与到网络安全应急响应、安全事件溯源分析等研究活动中，协助解决一批耗时长，人员投入需求量大的疑难技术问题；同时也在实践中培养优秀学生、积累经验，共同维护和推动网络空间安全发展。 （沈晨 教科办）', 'http://info.jhc.cn/2016/0415/c201a54664/page.htm', 'http://info.jhc.cn/upload/20160415wangj.jpg', '2017-05-17 14:14:01.334224');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (209, '周荃', '', '系统管理员', '2016-04-12 00:00:00', '《金华晚报》报道我校技能大赛', '比武    4月10日，由浙江省教育厅主办、金职院承办的2016年浙江省高职高专院校技能大赛“嵌入式技术与应用开发”和“风光互补发电系统安装与调试”赛项，在该校信息学院拉开序幕。此次比赛也是全国职业院校技能大赛浙江省选拔赛，有来自10所省内的24支学生队伍参加了“嵌入式技术与应用开发”比赛，来自3所省内院校的6支学生队伍参加了“风光互补发电系统安装与调试”比赛。去年，金职院代表队在这两项国赛中取得一等奖的佳绩，今年在两项省赛中获得一等奖，有望参加国赛。（记者 缪小芬） 原文地址：http://epaper.jhnews.com.cn/site1/jhwb/html/2016-04/12/content_1693797.htm', 'http://info.jhc.cn/2016/0412/c201a54663/page.htm', 'http://info.jhc.cn/upload/20160410dasai0.jpg', '2017-05-17 14:14:01.356475');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (210, '周荃', '', '系统管理员', '2016-04-12 00:00:00', '《钱江晚报》报道我校技能大赛', '玩转智能小车，金职院学生拿了第一 4月9-10日，全国职业院校技能大赛其中5个赛项的浙江省选拔赛在金职院举办。“嵌入式技术与应用开发”赛项吸引了来自全省10所高职院校的24支学生队伍。5个小时的比赛中，要求参赛选手通过安卓编程，实现嵌入式Android智能车的自动运行。利用平板电脑，通过无线方式控制小车，在赛道地图上完成采集多种环境参数及自动识别车牌、交通信号灯等任务。最终，金职院信息学院代表队获得浙江省赛两个第一名的好成绩。5月，他们还将在国赛中一展身手。“比赛考验的就是学生的综合应用能力、制作工艺和安全操作规范。近年来，我院学生队伍在全国职业技能大赛中都获取了不凡的成绩，去年在‘嵌入式技术与应用开发’赛项上获得一等奖，这些成绩都离不开整个团队日复一日的实战演练。”金华职业技术学院参赛队的指导老师刘子坚说。信息工程学院引入了让学生爱不释手的嵌入式Android智能车，同时构建了一支由教师与同学组成的开发团队，为金华企业提供物联网相关的技术服务。比如与金华市特种设备检测中心合作完成了基于物联网的起重机检测系统，当前正与金华市益迪仪器设备有限公司合作开发医疗设备远程故障监测系统。 本报通讯员 周荃 本报记者 吕艺真 原文地址：http://qjwb.zjol.com.cn/html/2016-04/12/content_3311113.htm?div=-1', 'http://info.jhc.cn/2016/0412/c201a54662/page.htm', NULL, '2017-05-17 14:14:01.3787');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (211, '周荃', '办公室', '周荃', '2016-04-12 00:00:00', '信息学子喜获省高职高专院校技能大赛3项一等奖', '4月10日，由浙江省教育厅举办、金华职业技术学院承办的2016年浙江省高职高专院校技能大赛“嵌入式技术与应用开发”和“风光互补发电系统安装与调试”赛项在我校信息工程学院拉开帷幕。此次比赛也是全国职业院校技能大赛浙江省选拔赛。来自省内10所院校的24支学生队伍参加了“嵌入式技术与应用开发”比赛，来自省内3所院校的6支学生队伍参加了“风光互补发电系统安装与调试”比赛。经过激烈的比拼，最终，由我校信息学院范灵芝、林洁老师指导，徐健伟、赵培荣两位同学组成的代表队以及由林洁、诸葛坚老师指导，潘鑫、胡文鑫两位同学组成的代表队分获“嵌入式技术与应用开发”赛项一等奖；由马汝星、曹胜任老师指导，温从汉、赵锦坤、赵赛赛三位同学组成的代表队获得“风光互补发电系统安装与调试”赛项一等奖。比赛现场，各参赛选手都表现得自信满满，有备而来。“我们很有信心”脸上洋溢着自信的笑容，说这话的正是学院“电子设计协会”的会长、来自金职硬件1队的赵培荣。“之前也参加过类似的竞赛，所以我有信心，保证稳定发挥，努力取得好成绩。”坐在另一边的是来自纺服硬件1队的刘勇。“我现在心情挺平静的，希望在竞赛中能够拿到一个好成绩。”    据悉，“嵌入式产品开发”是现代信息技术综合应用的赛项，该赛项的主体任务是利用平板电脑通过无线方式控制智能车在赛道地图上完成二维码识别、颜色识别、拍照、超声波测距、红外通信、光照强度检测等赛道任务。“风光互补发电系统安装与调试”赛项以光伏发电和风力发电工作过程中的实际工作任务为载体，以光伏发电和风力发电系统的组装调试为主要内容，赛项旨在推动高职院校新能源技术应用专业及相关专业的建设和成果展示，是“互联网+新能源技术”在教学上的真实应用体现。 （周荃  办公室）', 'http://info.jhc.cn/2016/0412/c201a54661/page.htm', 'http://info.jhc.cn/_upload/article/images/a2/84/ea508c4347a5a578405db51779bd/0318ec45-0f78-46fb-89a0-f83e1b0eeace.jpg|http://info.jhc.cn/_upload/article/images/a2/84/ea508c4347a5a578405db51779bd/b2c38224-2ff6-448c-8ecc-b201c4de0510.jpg', '2017-05-17 14:14:06.368257');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (212, '周荃', '办公室', '周荃', '2016-04-11 00:00:00', '校校全面合作 共同探索四年制高职人才培养', '4月8日至9日，浙江理工大学信息学院丁佐华院长一行4人来我校考察指导，并就电子信息科学与技术（电力电子技术方向）四年制高职人才培养、两校优势发挥、合作办学深化等问题与我校信息学院领导班子、试点建设团队进行深入探讨，教务处负责人、四年制高职试点全体学生参加了研讨会。   研讨会上，我校信息学院负责人向丁院长一行汇报了半年来的试点工作进展，介绍和展示了所开展的“以项目驱动为导向的项目导师制”取得的成效，就下一步引入浙江理工大学导师、行业企业导师、学长团队构建“多元导师下学研共同体模式”提出了设想和工作计划。丁院长一行十分赞赏和支持我校开展的多元导师制模式。双方进一步就支持试点专业建设团队年青教师攻读博士学位、双方教师结对提升科研能力、双方联合开展技术研究与科研攻关、双方互派本科交换生等事宜达成了广泛共识；进而为推动两校全面深入合作，打造高水平师资团队，办好四年制高职人才培养试点奠定坚实基础。   在与学生面对面交流时，丁院长代表浙江理工大学信息学院向全体同学在金职院能享受到优异的教学条件、获得校院两级领导和老师的高度重视表示祝贺。他鼓励同学们要珍惜时光、学有所长，并结合自身职业成长经历，指出“出身”不重要，关键取决于自己的努力程度。丁院长还就同学们关心的英语对个人成长的重要性、如何渡过大学生活、如何继续攻读硕士学位、能否参加浙江理工大学的学术社团活动等问题进行了一一解答。同学们听了丁院长的讲话很受启发和鼓舞。交流过程中，同学们踊跃发言，就与浙江理工大学本科生的互动，两校在学术资源、教学资源、社团资源等方面共享与交流深入讨论，两校与会领导认真听取和记录了同学们提出的意见和建议，并给予了积极回应。 （办公室）', 'http://info.jhc.cn/2016/0411/c201a54660/page.htm', 'http://info.jhc.cn/_upload/article/images/db/e2/89cda5474df18604df591afa6ba0/77030be4-4346-4309-9f65-49fc29dd2a56.jpg|http://info.jhc.cn/_upload/article/images/db/e2/89cda5474df18604df591afa6ba0/566a5063-4941-44f6-8902-2a1ebaf5ef80.jpg', '2017-05-17 14:14:06.427891');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (213, '蔡金晶', '教科办', '周荃', '2016-04-07 00:00:00', '中外合作办学不能简单拿来——信息学院召开中外合作专业教学研讨会', '为进一步推动我院中外合作办学专业的教学质量，日前，我们信息学院以院级教研活动的形式组织相关专业主任、任课教师、班主任、助教进行了教学研讨。   会上，大家头脑风暴，就如何更好地做好中外合作专业的课堂教学工作、提高教学效果，专业课助教的工作职责，以及如何调动学生积极性和主动性等问题展开了大讨论。经过充分讨论，大家一致认为，在合作共赢的中外合作办学机制下，坚持引进国外优质教育资源，结合课程自身的特点，针对不同层次的学生实施分层分类等创新的课堂教学模式十分必要，对于外方优质的教学资源，不能简单地采用“拿来主义”，要进行进一步的消化吸收，要与各专业的课程建设和课堂教学改革相结合。大家相信，经过不断地努力，我们学院的中外合作专业一定会越办越好。  （蔡金晶教科办）', 'http://info.jhc.cn/2016/0407/c201a54659/page.htm', 'http://info.jhc.cn/_upload/article/images/30/de/6dd53427496b966673a076ae6215/e7ac400a-0eaf-4c5e-a003-55017a1087c6.jpg', '2017-05-17 14:14:06.445843');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (214, '周荃', '', '系统管理员', '2016-04-05 00:00:00', '加强对外交流与学习，加快移动应用开发专业建设', '3月31日—4月2日，信息学院软件技术专业4名教师在专业主任陈晓龙教授带队下，参加了由全国高等院校计算机基础教育研究会高职高专计算机与电子商务专委会等组织召开的移动应用软件开发专业建设研讨会。本次会议召开的背景是，在教育部发布的《普通高等学校高等职业教育（专科）专业目录（2015年）》中，移动应用开发成为备受关注的新增专业之一。国务院“互联网+”行动计划的实施，进一步促进了移动互联网产业的发展，在产业快速发展和行业人才紧缺的环境下，迫切需要高职院校加快移动应用开发专业建设和人才培养的进度。来自大连东软信息学院等全国100多所高职院校的200多名软件技术方向的专家学者和一线教师，围绕如何制定符合行业需求的移动应用开发专业培养目标、课程体系、实训环境等进行了深入交流与探讨，分享了极具特色的移动应用开发专业建设思路和实施经验。我校信息学院目前虽未开设移动应用开发专业，但在软件技术专业中已开设了IOS、Android等移动应用开发相关课程，并且近几年积极派遣教师外出进修学习，提升移动应用开发能力和水平，加大相关实训环境建设投入，组建移动应用开发工作室。这一系列举措，其目的都是为了适应当前移动互联网的急速发展环境，培养社会急需的移动应用开发人才，为开设移动应用开发专业做准备，使移动应用开发人才培养更加体系化、规范化。（王伟斌、陈晓龙 信息化事业部）', 'http://info.jhc.cn/2016/0405/c201a54658/page.htm', NULL, '2017-05-17 14:14:06.467965');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (215, '徐露诗', '学工办/团委', '周荃', '2016-04-01 00:00:00', '祭扫烈士陵园，传承革命精神', '2016年4月1日，金职院信息学院青大队、青春痘文艺社、大学生自我服务协会和邓小平理论协会共30名志愿者来到金华革命烈士纪念墓，带着崇敬的心情，缅怀革命烈士。   30名志愿者在国旗、团旗护卫队的带领下，踏着整齐的步伐，来到纪念碑前，由团学生代表带领着全体志愿者进行宣誓与默哀。稍时，志愿者们还主动为陵园义务劳动，为革命烈士陵园送去一片整洁与清净。烈日当空，志愿者们不惧炎热，一直坚持到最后，发挥了当代大学生艰苦朴素、吃苦耐劳的精神。   志愿者的行动得到了前来进行祭奠活动的金华市民的一致好评与肯定。我们的志愿者也表示，通过了这次的扫墓与义务劳动，对革命烈士传承的精神有了更深切的感悟，对自己肩膀上的责任也更加坚定。 （徐露诗学工办/团委）', 'http://info.jhc.cn/2016/0401/c201a54657/page.htm', 'http://info.jhc.cn/_upload/article/images/96/f5/0549f60c43d3a75fef45e5ef8a32/174b4aa5-e551-4a92-8696-4a6c833020cd.jpg|http://info.jhc.cn/_upload/article/images/96/f5/0549f60c43d3a75fef45e5ef8a32/21898bdf-bb26-4778-bb66-32db8a0bbbd1.jpg', '2017-05-17 14:14:06.49025');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (216, '周荃', '', '系统管理员', '2016-04-01 00:00:00', '信息学院举办“电力电子技术”学术讲座', '3月31日，省著名电力电子技术专家、浙江大学教授马皓应邀来我校信息学院作学术讲座，电子产品开发部全体教师与电子信息专业学生聆听了讲座。     讲座中，马皓结合自身工作经验，向大家详细介绍了电力电子技术含义与应用、新型电力电子器件等。对于师生普遍关心的“国内与世界电力电子器件应用的优劣势”问题，马皓表示，国家正加大投入研发制造新器件，虽然与国际上存在一些差距，但是基于SiC材料的新器件开发已经跟上国际水平，而在电力电子技术器件应用领域，国内与国际发展则基本持平。讲座现场气氛热烈、互动频繁。师生们纷纷表示，通过讲座，不仅让他们对于电力电子应用领域有了更加全面地了解，也为他们进行技术研发开阔了新视野。 （余红娟 电子产品开发部）', 'http://info.jhc.cn/2016/0401/c201a54656/page.htm', 'http://info.jhc.cn/upload/20160331mahao1.jpg|http://info.jhc.cn/upload/20160331mahao.jpg', '2017-05-17 14:14:06.512461');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (217, '', '学校', '周荃', '2016-04-01 00:00:00', '我院辅导员在省辅导员职业能力比赛中取得新突破', '3月31日，第五届浙江省高校辅导员职业能力大赛落下帷幕，我校喜获二等奖与三等奖各1项，取得了本赛事以来我校最好成绩。   此次比赛由浙江省委教育工委主办，自今年1月正式启动，经过前期各高校校内选拔、全省复赛，共有来自全省的40名选手入围最终决赛，高职高专院校共有8名选手入围决赛。比赛分为主题演讲、谈心谈话、案例分析三个环节展开，我院信息学院辅导员徐露诗凭借扎实的知识储备、过硬的职业技能和昂扬的精神风貌，表现优异，荣获二等奖，成为我校参加该赛事以来在获奖等级上取得新突破。同时，我校还荣获高职院校唯一的最佳组织奖。据悉，比赛中，副校长龚永坚带领由相关部门及辅导员组成的观摩团到场观摩学习，激励大家不断提升与完善辅导员的职业技能，增强服务意识，提高工作水平。', 'http://info.jhc.cn/2016/0401/c201a54655/page.htm', 'http://info.jhc.cn/_upload/article/images/2d/9c/29d740b7435e9fbbfa1db6012024/e7992ed8-c4a6-4604-9811-23ce9f533b29.jpg', '2017-05-17 14:14:06.534626');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (218, '周荃', '', '系统管理员', '2016-03-31 00:00:00', '我院学生社团喜获“全国百佳体育公益社团”荣誉称号', '近日，2015年度“全国百佳体育公益社团”评选揭晓，我院风行轮滑社获此殊荣。风行轮滑社成立于2005年，现有会员近百人，由全校喜爱轮滑的学生组成。协会定期组织会员观看教学影像与动作指导，并开展平地花式训练、轮滑比赛等各类活动，借此丰富大学生课余生活，提高大学生身体素质，营造良好校园文化氛围。协会曾被评为金华市“十佳魅力社团”，并多次获得校五星级社团、“优秀社团”等荣誉称号。据悉，本次评选活动于2015年11月启动，由团中央学校部、全国学联秘书处联合开展，旨在发挥好学生社团在开展大学生“走下网络、走出宿舍、走向操场”主题群众性课外体育锻炼活动中的重要作用。通过校级、省级团委的推荐，经专家审核评定，浙江省共有6个学生社团获评，我校也是其中唯一一所高职院校获此称号。 （丰辉 学工办/团委）', 'http://info.jhc.cn/2016/0331/c201a54654/page.htm', NULL, '2017-05-17 14:14:06.557022');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (224, '周荃', '', '系统管理员', '2016-03-23 00:00:00', '信息工程学院党委开展“两学一做”专题教育学习活动', '近日，信息学院党委开展“两学一做”专题教育学习，巩固拓展党的群众路线教育实践活动和“三严三实”专题教育成果，面向全体党员深化党内教育。党员同志认真学习了党章党规及习近平总书记系列讲话精神，就学院如何深入开展更加广泛和常态化的党员学习教育活动展开研讨。大家一致表示，要依托“两学一做”学习教育活动，深入开展思政主题教育活动，筑牢思想防线，提振党员干部“精、气、神”，引导广大师生党员结合学院工作有序开展组织生活、教学常规、志愿服务等各项工作。  （周荃 办公室）', 'http://info.jhc.cn/2016/0323/c201a54648/page.htm', NULL, '2017-05-17 14:14:06.690495');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (219, '花有清、曹胜任', '电气自动化项目部', '周荃', '2016-03-29 00:00:00', '信息学子荣获2016年全省高职高专院校技能大赛暨全国职业院校技能大赛浙江省选拔赛赛项二等奖', '3月26日，2016年全省高职高专院校技能大赛暨全国职业院校技能大赛浙江省选拔赛“现代电气控制系统安装与调试”赛项在宁波职业技术学院落下帷幕，由我校信息学院花有清、楼蔚松两位老师指导，郑鸿宇、杨晓康两位学生组成的代表队沉着应战、努力拼搏，经过4个小时的激烈角逐，获得第三名（二等奖）的好成绩。  本次竞赛共有12个代表队参加，比赛内容涉及到PLC编程、触摸屏组态、网络通讯设置、驱动器参数设置等专业理论知识，要求在电器元件安装、线路连接及制作、电路原理图绘制、典型机床电路的排故等方面有较强的实践动手能力及良好的团队协作精神，对参赛选手的软件编程、硬件安装、软硬件调试等综合能力提出了较高的要求。参赛师生团队克服了竞赛训练时间短（竞赛设备本学期开学后才到校）、专业知识技能综合要求高等困难，精心制定训练计划，首次参加该赛项即获得二等奖的好成绩。 （花有清、曹胜任电气自动化项目部）', 'http://info.jhc.cn/2016/0329/c201a54653/page.htm', 'http://info.jhc.cn/_upload/article/images/a2/03/fec5f85d4f84bcc501915225a0fa/3f8af6d5-5735-4cd2-86a8-8243c3658707.jpg', '2017-05-17 14:14:06.579089');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (220, '周荃', '', '系统管理员', '2016-03-25 00:00:00', '信息学院360度绩效考核选拔学生干部', '完善学生干部选拔培养机制，组建优秀的学生干部队伍，信息学院施行全面的学生干部360度选拔考核机制。前期学院共有110名学生报名参加学生干部的选拔，信息学院从多元角度评价参选学生的表现，历经学生自主报名、主席团推荐、班主任推荐、两轮面试环节、班级个人操行考核、寝室考核环节，最终评选出50名副部级学生干部。据悉在试用期内，学院将结合文明修身年活动和文明寝室建设，对副部级学生干部实行试用期考核，学院将组织正部级以上学生干部、辅导员不定期走访检查参选学生干部寝室，了解学生动态，从寝室生活、学习、思想品德、值班考勤、工作绩效等方面对参选学生的自律性进行考核。信息学院此次学生干部选拔避免了再考核中极容易发生的“光环效应”、“居中趋势”、“个人偏见”和“考核盲点”。选拔过程中同学们纷纷反映“没想到学院的考核这么严密，我们的各项表现都在考察范围内，想当一名学生干部着实不容易啊，我们一定好好珍惜，并以身作则并带动身边的同学共同进步。” （丰辉 学工办/团委）', 'http://info.jhc.cn/2016/0325/c201a54652/page.htm', NULL, '2017-05-17 14:14:06.601337');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (221, '蔡金晶', '教科办', '周荃', '2016-03-25 00:00:00', '信息学院外教情系学生寝室文明建设', '日前，信息学院新西兰外教Lloyd参与了学院开展的“教师走进寝室”活动，来到中外合作办学三个专业男生寝室，感受中国寝室课堂，参与学院的寝室文明建设。   走进寝室，Lloyd就被整洁的环境所惊叹。他称赞说：“男生们非常棒！”随后，Lloyd和大家分享了新西兰的寝室文化，他表示，在新西兰都是一个一间。虽然文化不同，但是大家一起创造美好环境的努力是一样的。在交流中，Lloyd还以身示范，和同学一起动手叠被子、整理书柜。   据悉，信息学院“教师走进寝室”该活动展开以来，已有多名外教走进寝室，开展学业指导、生活帮扶和思想引导。是学生体验到了别样的寝室文化。 （蔡金晶教科办）', 'http://info.jhc.cn/2016/0325/c201a54651/page.htm', 'http://info.jhc.cn/_upload/article/images/46/b3/6d70655844ceb9a50ea3235a1077/586fb440-ea4b-4a3b-9bd3-01c8430dfb4f.jpg|http://info.jhc.cn/_upload/article/images/46/b3/6d70655844ceb9a50ea3235a1077/1e2873cd-ce49-477f-86d2-5c4d961467f4.jpg', '2017-05-17 14:14:06.623721');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (222, '周荃', '', '系统管理员', '2016-03-24 00:00:00', '育人于细处 习惯之为常：信息学院教师主动融入学生“寝室圈”', '寝室是大学生的“第一社会”、“第二家庭”、“第三课堂”，在学生成长成才中有着不可替代的作用。为落实浙江省文明寝室创建工作，日前，信息学院教师们来到联系寝室，为同学们上了生动一课。教师们以身示范，现场演示了如何地寝室内的物品进行收纳整理。要求学生做到“四统一、五规范”，即设置标识统一、物品摆放统一、作息时间统一、标准要求统一。据悉，信息学校要求教师积极融入学生“寝室圈”，把学生寝室作为密切师生联系、增进教管融合、推动全员育人的重要阵地。学院建立文明寝室师生联系制度，向每位教师干部发“师生联系卡”，在每间寝室门口标明联系老师名字，鼓励教师干部经常深入学生寝室开展学业、思想、生活指导，对教师干部深入寝室情况进行不定期通报，并将教师联系寝室情况与教师个人、所在部门的年度考核挂钩。院领导每人联系1幢寝室楼、1个中层负责一层楼，带头深入寝室，全面了解学生情况，形成了“人人参与学生寝室文明创建、人人关心学生成长成才”的良好氛围。 （蔡金晶 教科办）', 'http://info.jhc.cn/2016/0324/c201a54650/page.htm', NULL, '2017-05-17 14:14:06.646145');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (223, '周荃', '', '系统管理员', '2016-03-23 00:00:00', '加强对外交流学习，促进移动应用开发人才培养', '近年来，中央提出了国家创新驱动发展战略和“互联网＋”行动计划，在促进经济转型的同时，也对高校创新创业教育深化改革和创新人才培养提出了新要求和新挑战。3月19-20日，受Apple公司邀请，信息学院部分教师参加了由Apple公司组织的“高校互联网iOS移动开发应用研讨会”。会上，通过企业应用案例与高校实践经验分享、课程建设与开发技术讲座、校企合作实训解决方案推广、平台展示体验与互动交流等环节，信息学院教师与来自浙江大学等省内高校同行一起，分析了Apple与教育，分享了关于互联网学习平台和移动互联网创业人才培养的产业界应用案例、高职院校iOS移动应用开发经验与思考。近年来，信息学院抓住移动互联网大发展和智能移动终端不断普及的背景，积极探索软件技术、计算机应用技术等专业在此背景下的人才培养定位，通过行业调查、企业人才需求分析，及时调整计算机类专业人才培养方案，将移动应用开发纳入人才培养体系，提升学生在就业市场的竞争力。通过选派教师外出进修学习、与移动应用开发企业加强校企合作、建设移动开发实训室、参加各类移动开发应用教学研讨会等，培养师资力量，做实教学资源，紧跟行业发展，一系列举措，使得信息学院在移动互联网迅猛发展的背景下，为培养社会所急需的合格移动应用开发人才打下了坚实基础。 （王伟斌、胡平 信息化事业部）', 'http://info.jhc.cn/2016/0323/c201a54649/page.htm', NULL, '2017-05-17 14:14:06.668106');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (225, '朱苏航、刘子坚', '电子产品开发部', '周荃', '2016-03-22 00:00:00', '走近行业，促进就业', '第29届浙江国际科研、医疗仪器设备技术交流展览会于2016年3月16日到3月19日在白马湖国际会展中心召开。为了让同学们近距离触摸行业，医用电子仪器与维护专业全体教师及医仪141级的全体同学参加了这次展会。   今年的展会，相较于往年有一些变化。展会地址的改变，使得参展的格局也有了较大的变化。展会共有四百余家公司与厂家参展，与往年以进口设备和代理商为主的方式不同，今年参展单位以国产高端大型的医疗设备居多，且以厂家的方式参展。通过此次展会，我们深深的感觉到整个医疗器械行业的改革已经呼之欲出。    每年的医展会，我们医仪专业都积极参与。通过类似这样的活动，学生们对医疗仪器有了零距离的接触，对医疗设备有一些感性的认识，并对将来就业岗位有更多的了解。这对于提高专业的就业率，及提升专业在省内医疗行业的知名度都意义匪浅。此外，在展会上，我们还遇见许多以往各届的学生，及正在顶岗实习的医仪131班的学生。这些人中，有的已经成为公司销售和售后骨干，有的已自主创业，有的成为了医院的设备科科长或维修工程师。展会期间，不断有公司有意与我们合作，共同培养人才。医仪专业经过几年的努力，已在医疗器械行业中开花结果。                                   （朱苏航、刘子坚电子产品开发部）', 'http://info.jhc.cn/2016/0322/c201a54647/page.htm', 'http://info.jhc.cn/_upload/article/images/a9/8c/34b8011f413f93a84433741e1445/51d57a71-ce93-43d7-9e11-48fbd2f01260.jpg', '2017-05-17 14:14:11.524655');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (226, '周荃', '', '系统管理员', '2016-03-21 00:00:00', '四位教授主讲电子信息科学与技术专业导论课面对面指导高职本科学生专业规划！', '近日，浙江师范大学的余水宝教授、浙江理工大学的蒋明峰教授、浙大城市学院的任国海教授、浙江大学的马皓教授应邀给电子信息科学与技术专业本科生讲授专业导论课。专业导论课从“大学生电子创新设计知识与能力体系的构建与发展”、“电子信息科学与技术专业信息处理方法与案例分析”、“电力电子技术创新能力培养的教学理念与实训模式”、“电力电子技术行业前景与就业方向指导”四个专题给本科学生介绍如何建立本科的学习方式与思维方式，如何把握电力电子技术与新能源应用领域的发展，为适应该产业需求学生应达到的能力、素养及知识技能，指导四年制高职本科学生吸收高职教育优势，扬长补短，正确定位专业目标，更好地规划大学学习与未来发展。据息，信息工程学院非常重视电子信息科学与技术专业导论课的教学，通过学生小组学习，给学生布置学习任务，结合导师制，指导学生调研企业、调研电子信息大类专业设置情况，调研学科竞赛与创新创业方向，安排四场教授专题讲座，指导学生从研究电子信息学科分类、行业现状、对比相关专业教学计划，在多学科交叉融合、高度综合性应用背景下，明确学习方向与目标，选择专业学习兴趣点及重点培育方向，通过导论课学习和听专家讲座，学生认真听讲，做好录音与笔记、写总结谈感想。同学们纷纷表示专业导论课的开设为他们开拓了视野，对专业学习内容学习方法有了新认识，对考研途径、创新能力培养途径、电力电子产业方向都有了深刻的认识，表示今后要利用好大学所提供的环境与条件，提高自制能力，主动学习，树立长远的学习目标与学习规划，打好基础，早作准备，争取更高的学习进修机会，顺利获得本科的专业水平。                                             （余红娟 电子产品开发部）', 'http://info.jhc.cn/2016/0321/c201a54646/page.htm', NULL, '2017-05-17 14:14:11.568402');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (227, '周荃', '', '系统管理员', '2016-03-14 00:00:00', '分享日志 以人育人', '近期，信息工程学院通过网站专栏、微信公众平台等形式分享毕业顶岗实习学生的优秀“实习日志”，在教师、学生、家长、校友中引起较大反响，得到了他们的“点赞”。这里分享的每一篇优秀日志，都记录了同学们从学校走向社会这一过渡期留下的一个个动人的心路历程……比如在上海蓝鸥科技有限公司实习的软件131班学生王越亚在日志中描述了自己在上海实习期间养成自主学习、自我总结的习惯，明确了未来的奋斗目标，并且激励自己不断坚持，一直坚持，习惯坚持！又比如在昆山弗兰德机电工程技术有限公司实习的应电143班学生李荣在日志中讲述了人生中的第一次出差经历，师傅的教导和现场的实操让他坚信：肯上进，有想法，肯吃苦，坚持下去就会有未来。当我们阅读着用真实体验记录下来的一篇篇实习日志，会深刻感受到同学们从走向岗位时的迷茫、体验实习时的艰辛，到收获经验时的满足、得到赞许时的喜悦。我们共享学生的心路故事，它在字里行间里表达了对岗位的体验和对未来的思考，它激励我们在校的同学要珍惜学习时光，潜心于自身素质、技艺和能力的提高；也启发我们的老师不断研究和改进教学，努力让每一位学生成长成才。学院秉承“以人育人、共同发展”的育人理念，通过选编优秀日志和总结的活动形式，共享学生的心路历程，激励在校的同学要珍惜学习时光，潜心自身素质、技艺和能力的提高，也启发我们的老师不断研究和改进教学，努力让每一位学生成长成才。                                   （李瑞东 教科办）', 'http://info.jhc.cn/2016/0314/c201a54645/page.htm', NULL, '2017-05-17 14:14:11.590741');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (228, '丰辉', '学工办/团委', '周荃', '2016-03-14 00:00:00', '树绿色理想，倡绿色生活', '3月12日，信息学院20名同学来到了金华市秋滨幼儿园，展开了以“树绿色理想，倡绿色生活”为主题的志愿者服务活动。在幼儿园老师的协助下，志愿者将小朋友分成三组。分别下发颜料、画笔、环保袋等材料，引导小朋友们尽情发挥想象力，以“环保”为主题，画出自己心中的环保袋。色彩交织，油墨泼洒，精神飞扬，小朋友们聚精会神地画着心中的“环保”，大树、小河、太阳等常见却又美好的事物跃然纸上。孩子们纯真的心性与欢快的笑声洗礼着我们的身心，重新领悟了环保的概念：让大树常青，河水长清，太阳常暖……  本次活动受到幼儿园全体师生的大力支持与赞扬，体现了当代大学生树绿色理想，倡绿色生活的理念，并让小朋友从小树立良好的绿色意识，增他们保护环境的意识，把这种绿色生活理念传承下去。 （丰辉 学工办/团委）', 'http://info.jhc.cn/2016/0314/c201a54644/page.htm', 'http://info.jhc.cn/_upload/article/images/a1/f1/d2236b28462f95b46ba200c8135c/4028b06b-519d-4238-aa5e-74bbc576a72d.jpg|http://info.jhc.cn/_upload/article/images/a1/f1/d2236b28462f95b46ba200c8135c/03ec1c5a-d869-41fc-89eb-fb3370e68fda.jpg', '2017-05-17 14:14:11.61325');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (229, '周荃', '', '系统管理员', '2016-03-14 00:00:00', '信息学院“三示范”促进文明寝室创建', '为响应省教工委、省教育厅的号召，切实开展学院文明寝室建设，营造良好寝室文化氛围，近日，信息学院采取“三示范”新措施。“三示范”即按照《文明寝室卫生评比细则》在各寝室区域内、各个班级、各个学生干部寝室中创建示范寝室，树立标杆，作为其他寝室学习的榜样，并形成相互督促模式，从而整体改善宿舍环境。此外，学院还将举办趣味活动，充分调动学生参与文明寝室创建的积极性，以全员参与的方式，切实推进文明寝室创建工作。 （吕威威 学工办/团委）', 'http://info.jhc.cn/2016/0314/c201a54643/page.htm', NULL, '2017-05-17 14:14:11.636738');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (230, '叶红芳、曹胜任、周荃', '教科办、办公室', '周荃', '2016-03-14 00:00:00', '先锋微公益 美化我校园', '阳春三月，春暖花开。在一年一度植树节来临之际，信息学院党委发动党员同志开展美化校园微公益，在金湖环路沿线开展了义务植树活动，用实际行动推进校园生态文明建设。   植树现场，大家热情高涨，铲土填坑、挥锹培土、夯实土层。学院党政班子领导亲临现场，率先垂范，和党员们一道挥锹植树。经过近一小时的奋战，金湖环路旁的空地种上了高高的香樟树，为学校增添了一道靓丽的风景线。   此外，信息学院党委还积极发动党员同志深入社区，围绕“微心愿”、“文明创卫”等主题继续践行微公益，激发党员同志的能量，多为人民办实事、办好事。 （叶红芳、曹胜任、周荃）', 'http://info.jhc.cn/2016/0314/c201a54642/page.htm', 'http://info.jhc.cn/_upload/article/images/0c/86/117fe5bb45579fc38d6e4b36e49c/85839156-ae5e-4bd8-abb0-35ff0d82db64.jpg', '2017-05-17 14:14:11.657357');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (231, '周荃', '', '系统管理员', '2016-03-14 00:00:00', '信息工程学院开展图书馆晚自习活动', '“周日可以不在教室上晚自习啦”“不去教室去哪里？去图书馆！”学院近日举办图书馆晚自习活动，积极探索培养同学主动学习的新途径。学院每周将根据大一班级自习的整体纪律与考勤情况制作质量排行榜，前两名的班级填写申请表并交由学工办老师处，即可在周日全班进入图书馆完成晚自习的任务。进入图书馆后，同学们可以借阅喜爱的书籍，相比在教室中，拥有了更多的自主性。据悉，图书馆晚自习是信息学院“文明修身 爱上读书”系列活动之一，后续将开展“图书英雄”、“爱读书”主题班会、英语领读、经典文学作品欣赏等一系列活动。学院希望通过以上举措，引导学生养成主动学习，珍惜时间的好习惯，切实提高学生素质，在学院内营造勤奋好学的良好氛围。 （徐露诗 学工办/团委）', 'http://info.jhc.cn/2016/0314/c201a54641/page.htm', NULL, '2017-05-17 14:14:11.679804');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (232, '周荃', '', '系统管理员', '2016-03-10 00:00:00', '信息工程学院召开 “嵌入式产品开发”、 “风光互补发电系统安装与调试”赛项技术研讨会', '3月9日，学院召开浙江省高职院校技能大赛暨国赛选拔赛“嵌入式产品开发”、“风光互补发电系统安装与调试”赛项技术研讨会，浙江机电职业技术学院、温州职业技术学院等8所高职院校的指导教师参加了研讨会，会议由赵敏笑副院长主持，教务处张雁平副处长参加了会议。会上，各校老师对“嵌入式产品开发”、“风光互补发电系统安装与调试”的竞赛规程进行了热烈的讨论与交流，并在竞赛技术规范、竞赛设备的软硬件平台、竞赛内容、竞赛时间、每校参赛队伍数、组队方式、以及竞赛流程等方面达成了共识。   据悉，我校自2010年至今已承办了四届“嵌入式产品开发”国赛选拔赛， “风光互补发电系统安装与调试”赛项是我校首次组织承办。                                                            （花有清、余红娟）', 'http://info.jhc.cn/2016/0310/c201a54640/page.htm', 'http://info.jhc.cn/upload/20160309hezhao.jpg', '2017-05-17 14:14:11.701919');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (233, '周荃', '办公室', '周荃', '2016-03-09 00:00:00', '湖州职业技术学院机电与汽车工程学院院长左希庆一行来我院考察交流', '3月8日下午，湖州职业技术学院机电与汽车工程学院院长左希庆、书记刘竞带队实训办、教研室工作人员一行5人来我院考察交流。学院领导班子，实训中心负责人及通信、自动化专业负责人出席了交流活动。   陈海荣代表学院对客人的来访表示欢迎，并陪同参观了我院教学实训场所。座谈会上，宾主双方围绕专业实训基地职业文化氛围营造、二级学院绩效奖励考核办法、管理制度建设与实施、大学生创新创业人才培养等内容进行了深入探讨与交流。 （周荃 办公室）', 'http://info.jhc.cn/2016/0309/c201a54639/page.htm', 'http://info.jhc.cn/_upload/article/images/e2/3d/0262768c4b00a68744ad507a6086/d486c4a1-f8f8-4695-9a5c-1d0c6197316e.jpg|http://info.jhc.cn/_upload/article/images/e2/3d/0262768c4b00a68744ad507a6086/2fa657eb-b554-4242-a897-5db3028edc04.jpg|http://info.jhc.cn/_upload/article/images/e2/3d/0262768c4b00a68744ad507a6086/bf60fd7e-cd32-4241-9062-c100eee7094e.jpg', '2017-05-17 14:14:11.724187');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (234, '郭天鹏', '学工办/团委', '周荃', '2016-03-07 00:00:00', '信息学院召开本科班寒假企业调研报告会', '为交流与分享企业调研成果，更好地了解专业相关的行业企业发展状况，3月4日下午，学院召开本科班寒假企业调研报告会，院领导、专业老师、学工办老师和全体本科班学生参加了报告会。会上，46位本科学生通过PPT展示，依次进行了企业调研汇报，汇报内容分为三个部分：企业介绍，包括企业历程、企业文化、企业特色；企业产品展示，列举企业的主要产品，介绍这些产品的优劣；心得体会，简述自己调研过程，发现自己不足之处，最重要的是从企业发展中，找寻自己发展方向。在听取了46位同学的汇报后，陈海荣院长和王成福副院长进行了点评，并由专业老师进行打分。   汇报结束后，本科导师团队进行了讨论，对导师制下一步工作提出了改进建议，重点是要丰富指导项目及方式，每周要安排2个晚上到实验室进行实践操作或者完成网上作业；另外，要针对本科生的专业特点，组织第二课堂活动，锻炼他们的语言沟通与社会交往能力。 （郭天鹏 学工办/团委）', 'http://info.jhc.cn/2016/0307/c201a54638/page.htm', 'http://info.jhc.cn/_upload/article/images/94/b8/9e032f5043b1ba214dc034e73758/dfd98463-f648-4c8f-8122-9e8409e08988.jpg', '2017-05-17 14:14:11.746413');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (235, '丰辉', '学工办/团委', '周荃', '2016-03-04 00:00:00', '信息学院学雷锋志愿服务月启动', '3月5日是全国第53个“学雷锋纪念日”，3月3日，信息学院开展了寒假社会实践先进个人表彰会暨“情系雷锋月，爱洒三月天”学雷锋志愿者服务月启动仪式，拉开了全院志愿服务的序幕。在启动仪式上，表彰了20名院级寒假社会实践先进个人和25名优秀调研论文获得者，仪式结束后全体党员志愿者自发组织开展了实训护航—实训室清洁的活动。     据悉，本次学雷锋志愿服务月活动形式多样，有通过学院团委微信公众平台寻找身边最美雷锋人，“E·就通”新媒实践队进社区进行微信互动教学，建设微社区，还有360°网络安全卫士在行动等活动，使学生在活动中提升文明修养与素质。 （丰辉 学工办/团委）', 'http://info.jhc.cn/2016/0304/c201a54637/page.htm', 'http://info.jhc.cn/_upload/article/images/81/79/5d8ce8ad4c8fba7ea3c603661473/f5b2525d-1ee3-422f-94ae-0b6ce3f3877d.jpg|http://info.jhc.cn/_upload/article/images/81/79/5d8ce8ad4c8fba7ea3c603661473/c7f56611-b6da-468c-9200-40bce46946cb.jpg', '2017-05-17 14:14:11.768683');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (236, '蔡金晶', '教科办', '周荃', '2016-02-25 00:00:00', '中新教师共同研讨专业课程实施', '近日，新西兰怀卡托理工学院的四位教师在中国区项目负责人Mark、国际事务人事处处长 Deborah、人事处官员Nicole的陪同下来校任教。   在访期间，新方人员考察了我校外教的住宿环境、教学办公场所，同时就ESL课程和专业课程的学习，与信息学院相关老师、中新班学生进行了深入的研讨与座谈。在教师研讨会上，双方教师就专业课的协同教学展开讨论，探讨教学项目设置与实施、教学资源的开发与共享，并约定今后将定期召开教学研讨会，以保证中新班的教学质量。在学生座谈会上，中新班的学生代表积极用英语发言，表达他们对上一学期ESL课程的喜爱、对专业课程的期待、今后学习的打算和担忧，并与外教积极讨论，了解专业课程的教学内容和教学方式。通过研讨会和座谈会，增进了中新教师之间的相互理解，并让今年新加入怀卡托国际学院的老师能更好地了解学生现有的水平，为课程教学打下良好地基础。   据悉，为了更好地提升中新班的教学质量，学院为每一门外方授课的课程安排了助教，在辅助外方教师完成教学任务的同时，提升专业教师的国际化视野和教学水平。 （蔡金晶 教科办）', 'http://info.jhc.cn/2016/0225/c201a54636/page.htm', 'http://info.jhc.cn/_upload/article/images/e4/84/4c89eb6c4e19afed99216827f41e/1249024b-f201-4d47-b778-7027cda39b84.jpg|http://info.jhc.cn/_upload/article/images/e4/84/4c89eb6c4e19afed99216827f41e/9053d777-0266-4cb2-9b8b-10ad37197faf.jpg', '2017-05-17 14:14:11.792245');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (237, '周荃', '', '系统管理员', '2016-01-21 00:00:00', '喜报：我院党建工作取得新荣誉', '近日，中共金华市直属机关工作委员会下发了《金直委发〔2016〕1号》、《金直委发〔2016〕2号》文件，我院党建工作品牌“为民家电维修服务队”被授予金华市直机关一星党建知名品牌，我院学工办被授予金华市直机关党员示范岗。据悉，为民家电维修服务队近两年通过完善服务队管理机制、搭建服务新载体、创新服务队服务模式深化品牌活动内涵与载体，师生党员通过线上与线下，为金华市月亮湾社区等45个社区近2000余名市民提供家电维修、电脑维护、新媒体教学等方面的服务，深受市民好评，金华日报、金华晚报等多家媒体对服务队进行了报道。我院学工办团委为有效提高部门管理与服务水平，充分发挥部门党员的先锋模范作用，积极开展了“党员示范岗”的争创活动，有目标，有措施，在全院的学生管理工作中起到了积极地引领示范作用。 (丰辉  学工办/团委)', 'http://info.jhc.cn/2016/0121/c201a54635/page.htm', NULL, '2017-05-17 14:14:11.813012');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (238, '周荃', '', '系统管理员', '2016-01-21 00:00:00', '信息工程学院开展主题班会优秀班级展示', '为期5个月的学习生活即将到达尾声，在这个学期中，每个周四的主题班会是信息工程学院同学们晚自习一道亮丽的风景线。近期，根据各班在主题班会上的综合表现，信息学院对表现较优秀的班级进行成果展示与奖励，鼓励各个班级在下个学期再接再厉。周四主题班会活动紧紧围绕同学们的学习生活展开，着力丰富晚自习内容，提高同学们的综合素质，先后开展了以畅游校园、读书分享、恋爱心理、诚信考试等内容的主题班会。班级负责人根据主题，组织活动，寓教于乐，将积极向上的主题融入到轻松愉快的活动中去。   同时，各班还积极邀请班主任老师参与其中，增加了老师与同学们交流的机会。根据各班级活动组织、现场打分、过程材料等方面的表现，最后，软件151、自动化152、电信151等班级以其精心的准备、热烈的氛围以及良好的班会效果脱颖而出，获评学院优秀班级。学院对优秀的班级在全院范围进行展示，并以微信推送的方式对外推广。同学们也反映，主题班会不仅丰富了晚自习内容，在班级中也形成了良好的学习风气，下个学期会继续努力将主题班会越办越好。 （徐露诗  学工办/团委）', 'http://info.jhc.cn/2016/0121/c201a54634/page.htm', 'http://info.jhc.cn/_upload/article/images/ff/14/a291b45646b99b89ebef18fe5e17/f7a28f2b-8dca-4766-8be0-9f7d3035236f.jpg', '2017-05-17 14:14:11.835257');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (239, '周荃', '', '系统管理员', '2016-01-20 00:00:00', '信息学院召开2016年离退休教职工新春座谈会', '1月20日上午，信息学院举行2016离退休教职工新春座谈会，学院领导陈海荣、贾美倩、赵敏笑与40余位老同志欢聚一堂，共叙佳节。   学院党委书记贾美倩向老同志们介绍了学院2015年度的工作成就和工作亮点，她希望离退休老同志们能一如既往支持学院发展，并祝大家新年快乐、健康长寿。陈海荣院长代表学院感谢广大离退休教职工为学校发展所付出的辛勤努力，并致以新春的美好祝福。他向老同志们介绍了学院2015年四年制本科、新西兰国际合作、师资队伍培养、文化建设等方面的工作成绩，与老同志们闲话家常，解答老同志们所关心的问题，并希望老同志们多为学院发展建言献策。离退休老同志们由衷感谢学院一直以来耐心细致的关怀，充分肯定学院过去一年发展取得的成绩，并对学院办公场所的新面貌赞不绝口。大家说，虽然退休了，但还是心系学院发展，祝愿学院新的一年更上一台阶。 （周荃  办公室）', 'http://info.jhc.cn/2016/0120/c201a54633/page.htm', 'http://info.jhc.cn/_upload/article/images/9b/91/c6b48590450890f5cbc184fd191f/7946f2b8-f380-4450-90bb-6ec63a32ea32.jpg', '2017-05-17 14:14:17.649962');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (240, '周荃', '', '系统管理员', '2016-01-19 00:00:00', '“架起沟通的桥梁，共建和谐微社区”信息学院开展志愿者服务活动', '2016年1月17日，金华市志愿者协会直属服务总队联合金职院信息学院党员志愿者共25人，在金华市月亮湾社区开展志愿者服务活动。据了解此次志愿者服务一共有四个项目。分别为环保宣传、健康咨询、家电维修以及社区居民如何使用新媒体宣传点。   在环保宣传点，我们看到了一种新型酵素，是由过了新鲜期的水果制成，是一种天然合成的有机化合物。它可以抑制有害微生物，尤其是病原菌和腐败细菌的活动。志愿者向每家每户宣传如何制作环保酵素、新型酵素的用途。除了宣传酵素，他们还进行废旧衣服回收利用，将那些衣服剪成布条做成拖把。     在健康咨询点，志愿者们准备了血压仪，免费为社区的老人们测量血压，并根据老人的血压、心率情况提出一些建议。“谢谢你们，帮我的电水壶修好了。”一声声感谢从家电维修点传来，结合金职院信息学院的专业技能，他们开展了家电维修服务活动。看着一个个成功的案例，越来越多的市民将坏掉的家电拿到我们的维修点。小到收音机大到电饭煲、高压锅，志愿者们来者不拒，经过一天时间共成功维修了23个家电。社区的居民都对此次的服务活动大为赞扬，认为现在的大学生越来越有爱心，还有市民为我们的志愿者送来了食品。最后是社区居民如何使用新媒体，新媒体是新的技术支撑体系下出现的媒体形态，本次志愿者们主要是教市民如何使用微信和微博。当下社会，微信、微博非常的火热，可以丰富老年人的兴趣爱好，提高老人的生活水平。许多老人听了志愿者耐心的介绍后也颇有兴趣，开始了更深入解和咨询。虽然天气不是特别好，可是志愿者们的热情是一点也没有减少。修家电、测血压、介绍酵素以及宣传新媒体，他们乐在其中。最后，社区负责人陈队长给予了高度的评价，认为此次志愿者活动，成功的为90后的新青年树立了良好的榜样，市民的谢意和好评就是最好的证明，他希样这样的志愿者活动会越来越多的，让奉献精神越传越广！（郭天鹏、汤鑫严  学工办/团委）', 'http://info.jhc.cn/2016/0119/c201a54632/page.htm', 'http://info.jhc.cn/_upload/article/images/ac/28/9a5fc9d74ec09278cec0d90541ac/26e7c73d-c43d-45f0-aa54-952482a414ea.jpg', '2017-05-17 14:14:17.70235');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (241, '周荃', '', '系统管理员', '2016-01-12 00:00:00', '民盟金职院支部前往经济开发区调研乐乐小镇建设', '2016年1月12日，民盟金职院支部盟员王成福、金宏伟、华玉亮前往金华经济开发区管理委员会调研乐乐小镇建设情况。金华经济开发区管理委员会樊亚丽副主任介绍了乐乐小镇的建设规划、2015-2017三年建设目标以及面临的困难，双方就共同关心的热点问题进行了交流。据悉，乐乐小镇规划面积2.98平方公里，以“互联网休闲娱乐、大学生创业为主题”，力争经过三年建设，投资60亿元，集聚信息经济企业1000家，产业基地10亿元，实现电子商务交易额1200亿元以上，营业收入80亿元，税收5亿元，吸引大学生创业者1万人，旅游人次50万以上的目标，并逐步建成“二轴、四区”的布局，形成鲜明的小镇特色。但在建设过程中，也存在集聚度不高、人才紧缺等困难，需要市政府及有关部门优先给予解决。民盟金职院支部充分发挥专业特长，将在广泛调查研究的基础上，通过金华市两会以及民盟这条通道，向有关部门提出建设性意见，为乐乐小镇的建设建言献策。  开发区樊亚丽副主任接待民盟支部成员  双方交流乐乐小镇建设所面临的问题', 'http://info.jhc.cn/2016/0112/c201a54631/page.htm', 'http://info.jhc.cn/_upload/article/images/5c/d9/27d96a1947be927926bcfc9033d3/6bfa9f04-cd8f-4646-9536-243cdc265f54.jpg|http://info.jhc.cn/_upload/article/images/5c/d9/27d96a1947be927926bcfc9033d3/b2172c91-659a-4efb-9953-b8b67375664f.jpg', '2017-05-17 14:14:17.724696');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (242, '周荃', '', '系统管理员', '2016-01-08 00:00:00', '信息学子在校2015年学生三“十佳”评选中再创辉煌', '近日，倍受瞩目的校第七届“十佳大学生”、第五届“十大创新（创业）之星”及第四届“十大自强之星”评选结果揭晓。通过激烈角逐，我院学生再创辉煌，5人入选，1人提名。此次评选历时近两个月，共有57位候选人参与角逐。通过网络投票、报纸投票及候选人现场展示、专家评审，最终确定30名校三“十佳”学生。其中，我院学生王志强获第七届“十佳大学生”，钟高峰获第七届“十佳大学生”提名奖；吴姜宏、王阳光获第五届“十大创新（创业）之星”，程燕、李强强获第四届“十大自强之星”。 这成绩是信息学院前所未有的，在全校也是名列前茅，这是信息学子的努力，是所有信息学子的榜样。据悉，学院还将邀请获奖学生分批为我院全体大一新生进行一次演讲。从大一开始培养他们的精神，使大一新生产生学习的目标和动力。 （郭天鹏 学工办/团委）', 'http://info.jhc.cn/2016/0108/c201a54629/page.htm', NULL, '2017-05-17 14:14:17.746967');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (243, '周荃', '', '系统管理员', '2016-01-08 00:00:00', '信息学院全体大一新生参加新西兰主题讲座', '你知道新西兰的大学生是如何学习吗？你想知道新西兰的风土人情吗？你想领略新西兰的自然风光吗？这些问题都能在东教216教室找到答案。为了丰富同学们的晚自习内容，开拓同学们的眼界与知识面，信息学院学工办邀请新西兰游学归来的专业课教师为大一学生带来了精彩的新西兰主题讲座。讲座中，老师为同学们介绍了新西兰的风土人情，自然风光，并给大家分享了在新西兰著名学府怀卡托理工学院的学习生活经历和图文照片。当同学们看到新西兰万里无云的蓝天、清澈见底的海水时，无不对美丽的新西兰产生了向往；当了解了怀卡托理工学院先进的授课内容、人性化的教学管理、积极向上的学习风气，同学们不由得重新审视自己的人生目标，尤其是中新合作的5个班级的学生，更加激发起他们加倍努力的斗志与信念，将来能够亲身领略新西兰的大好风光，并且成为怀卡托理工学院的一员。讲座结束后，意犹未尽的同学们继续与老师沟通交流，同学们表示，这次讲座让他们感受到了国外的大学生真实的学习生活，受益匪浅，希望以后这样的讲座可以越来越多。 （徐露诗  学工办/团委）', 'http://info.jhc.cn/2016/0108/c201a54628/page.htm', NULL, '2017-05-17 14:14:17.769152');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (244, '周荃', '', '系统管理员', '2016-01-08 00:00:00', '信息学院举行2015年暖冬计划物资发放仪式', '1月7日中午，信息工程学院举行2015年暖冬计划物资发放仪式，学工办全体老师、2014、2015级贫困生参加仪式。仪式上，学工办主任林雪华介绍了2015年学院帮困助学整体工作情况，详细阐述了资助政策和我院学生受助情况，并鼓励在校生把贫穷作为奋斗的动力，磨练意志，励志成才，争取更多的资助帮助自己完成大学学业。2015年暖冬计划开展以来，我院286名贫困生中，有34名省外特困生享受寒假往返车费补贴，其余同学分别获得毛毯、围巾、手套、棉袜等暖冬物资，实际发放额达到4万元。贫困生代表计算机142班刘怀同学发言，感谢学校让自己有机会用双手解决家庭经济问题，没有了后顾之忧，从而自信的面对学习与挑战，并结合自身的经历和体验，与在校生共勉。随后，八位学生代表上台接受物资。仪式结束后，受助学生前往学院学工办领取受助物资。受助学生纷纷表示感谢，感受到了学院的关怀和老师的用心，将会更加努力地投入学习和工作。 （郭天鹏 学工办/团委）', 'http://info.jhc.cn/2016/0108/c201a54626/page.htm', NULL, '2017-05-17 14:14:17.791092');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (245, '周荃', '', '系统管理员', '2016-01-07 00:00:00', '“E往情深.缘聚信息”2016年信息工程学院新年联欢晚会', '1月5日晚，欢声笑语，气氛热烈，以“E往情深.缘聚信息”为主题，信息工程学院2016年新年联欢晚会在校实验剧场隆重举行。信息学院全体教师，全院学生代表，以及各学院嘉宾共同观看了这场盛宴，全场坐无缺席。新年，新的篇章。晚会分为3个环节：多彩节目、抽奖互动、颁奖环节。节目环节，华丽的舞姿，动人的音乐，震撼的乐队，齐声的朗诵，绝伦的武术，还有那令人捧腹大笑的小品。节目多式多样，学生才华横溢，令在场的全场师生难以忘怀，掌声不断；互动环节，采用了现场微博抽奖的形式，主持人活跃气氛，将气氛带向一个高潮；颁奖环节，分为班级奖项和个人竞赛奖项，由学院领导及中层领导干部为班级代表和获奖学生颁奖。展示了信息风采的同时，为学院学生树立起了学习的榜样。   “难忘今宵…难忘今宵…”一曲《难忘今宵》谱写了信息学院美好明天。今年的新年联欢晚会以“E往情深.缘聚信息”为主题，在校园内营造喜庆、祥和、团圆的跨年气氛。为信息学院所有师生，在2015年画上了一个圆满的句号。  （郭天鹏  学工办/团委）', 'http://info.jhc.cn/2016/0107/c201a54623/page.htm', 'http://info.jhc.cn/_upload/article/images/0e/a8/ec0805f54ee49f6869d36d99b201/7bef8620-048b-4765-9d2b-996bd7239dbd.jpg', '2017-05-17 14:14:17.813566');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (246, '周荃', '', '系统管理员', '2015-12-30 00:00:00', '信息学院为成教生开设技能培养和就业“绿色通道”', '为使成教生有过硬的专业技能和更广的就业路子，学院为成教生开设技能培养和就业“绿色通道”，让部分优秀的成教学生融入到全日制订单培养学习。近日，订单班传来喜讯，在订单班学习的8名成教生课堂表现突出，学习积极性高，经过培训，他们的专业技能上了一个新台阶。   据悉，9158订单班第一期课程是为期三周的html5培训，培训教师主要来自蓝鸥科技有限公司，培训结束后将迎来第二阶段选拔，并对优秀学生进行奖励，推荐就业。 （金珂、俞冬丽  继教部）', 'http://info.jhc.cn/2015/1230/c201a54621/page.htm', 'http://info.jhc.cn/_upload/article/images/d7/2e/f3952a4040fa90bf80269383e45c/330a9350-b5c4-4522-98f1-f41a79abd9bc.jpg', '2017-05-17 14:14:17.835923');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (247, '周荃', '', '系统管理员', '2015-12-30 00:00:00', '我校余党军副书记等3人入选新一届工业与信息化行指委和下属专业指导委员会委员', '2015年12月29日，工业和信息化职业教育教学指导委员会在北京召开工作会议,我校党委副书记余党军教授率信息工程学院相关负责人出席了大会。工业和信息化部人事教育司司长衣雪青、教育部职业教育与成人教育司副司长王扬南及新一届工业和信息化行业教育教学指导委员会委员及下属的计算机类、电子信息类、通信类和自动化类专业教学指导委员会委员等200余人参加会议。会议通过了上一届行指委及各专指委的工作报告、2016-2019年工作规划、行指委和专指委委员会章程以及相关制度等，并对新一届当选的行指委委员、专指委委员颁发了聘书。我校余党军教授入选工业和信息化行指委委员，余党军、陈海荣、余红娟分别入选计算机类、自动化类、电子信息类等3个专业教学指导委员会委员。 （电子产品开发部  余红娟）', 'http://info.jhc.cn/2015/1230/c201a54619/page.htm', NULL, '2017-05-17 14:14:17.858011');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (251, '周荃', '', '系统管理员', '2015-12-16 00:00:00', '我院召开第十三次学生代表大会', '12月15日晚上，信息工程学院在大学生活动中心109召开第十三次学生代表大会。学院180名代表和学生干部参加了本次大会。陈海荣院长和贾美倩书记出席会议并讲话。    大会审议并通过了第十二届学生会工作报告，选举产生了第十三届学生会主席团成员，增补了第三届团委委员。会议要求广大青年学生认真学习十八大精神、脚踏实地，勇于创新，为学院的学风和校风建设再作贡献。大会共收到186件提案，涉及教学、学生管理、文化实践、后勤服务等方面的问题，同时还给出了对学院团委、学生会工作，社团发展，宿舍安全等与学院同学密切相关的具有可操作性的建设意见，充分体现了我院青年学生关心学院工作和注重自身发展的主人翁意识。新一届委员会将对所有提案进行分类整理，立案转呈给学院有关部门，并做好跟踪、落实和反馈工作。最后会议全体成员奏唱《中国共产主义青年团团歌》，信息学院第十三次学生代表大会圆满闭幕。 （丰辉 学工办/团委）', 'http://info.jhc.cn/2015/1216/c201a54610/page.htm', 'http://info.jhc.cn/_upload/article/images/11/a4/ac48135740f09b3c98580cab07dd/a87ab9be-7622-44a7-adc4-31b651854a58.jpg', '2017-05-17 14:14:17.946925');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (252, '周荃', '', '系统管理员', '2015-12-16 00:00:00', '追随专业发展新技术，促进专业建设新思路', '12月10-12日，信息学院软件技术专业教师参加了在南京举行的“2015（第五届）中国未来网络发展与创新论坛”。论坛围绕当前网络面临的挑战与对策、未来网络的实验部署及软件定义网络（SDN）等技术发展趋势等重大议题展开深入讨论。未来网络将控制逻辑与转发分离，更加注重通过软件编程来实现网络数据包的转发，软件技术专业希望通过培养具有软件定义网络编程能力的学生来寻找本专业的学生培养特色。   本次论坛有由中国工程院、南京市人民政府联合主办，由江苏省未来网络创新研究院和下一代互联网工程中心共同承办。中国工程院院士邬贺铨、刘韵洁、李国杰等国内外顶级专家莅临，通过主题演讲、圆桌论坛、学术论坛等活动打造了高规格产业会议。与会期间，参会老师还前往解放军理工大学国家级实验教学示范中心、军队重点实验室——军用网络技术中心参观学习。并和该重点实验室负责人陈鸣教授、胡超博士，就目前我院《环境监控物联网技术创新团队》科研中存在的问题及解决办法进行研讨。同时就联合开展软件定义网络数据中心传输优化等项目联合申报、科研项目理论实践验证方法、项目申报指导等达成实质性合作意向。   为了开展教学合作，在会间隙还前往东软集团南京分公司东软睿道公司参观调研。该公司销售总监赵涛首先介绍了该公司与其他高校各个层面的合作模式，包括学院共建、专业共建和基地共建。我校就东软睿道CDIO的人才培养体系与我校软件技术专业的课程融合开展讨论，希望能够将企业的一个完整的开发案例引入到我校培养体系，在专业共建、基地共建、学生实训实习等方面开展实质性合作。（陈晓龙 王伟斌）', 'http://info.jhc.cn/2015/1216/c201a54607/page.htm', 'http://info.jhc.cn/_upload/article/images/80/e4/afde739f4697b41c945cdab2d9a6/1648dd13-258c-4e6c-b668-0318a62320e3.jpg|http://info.jhc.cn/_upload/article/images/80/e4/afde739f4697b41c945cdab2d9a6/a126d601-e8ea-4321-8153-a8cd93c7f91e.jpg', '2017-05-17 14:14:17.969195');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (253, '周荃', '', '系统管理员', '2015-12-16 00:00:00', '信息学院开展“我show，我做主”班服设计大赛', '一支画笔可以展现出无穷的创意，一个展示可以体现出设计的亮点。近日由信息学院馨言心理社举办的班服设计大赛正如火如荼地进行。有可爱的蜡笔小新，有斗志昂扬的路飞，有展示班级风貌的集体绘画……在一件件白T恤上展现出一幅幅唯美的艺术设计，体现了班级精神。据悉此次大赛是以现场手绘服装形式，手绘过程中，同学们集思广益，团结协作完成设计。后由班级代表介绍发言，对作品进行一番精彩的讲解，再由模特搭配特有的风格进行服装展示。最终，计算机151以富有创意的设计，生动的讲解和完美的展示获得冠军。         赛后同学们表示，此次比赛不仅为同学们提供了一个展现自我的平台，有益于激发同学们的想象力与创造力，同时提高了同学们的协作能力，团队意识。希望以后可以多办相类似的活动。 （尉宸阳  何碧峰）', 'http://info.jhc.cn/2015/1216/c201a54606/page.htm', 'http://info.jhc.cn/_upload/article/images/f0/b8/33b285064827a52fc58afb6797d7/9f93cc7a-a251-4def-9743-690ddf71b27b.jpg', '2017-05-17 14:14:29.60278');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (254, '周荃', '', '系统管理员', '2015-12-14 00:00:00', '民盟金职院支部前往武义调研职业教育服务新农村旅游经济建设情况', '12月13日，民盟金华职业技术学院支部一行13人前往武义调研职业教育服务新农村旅游经济建设情况。据悉，武义县在推进新农村建设中，充分利用古村落、古民居和畲乡特色，大力发展乡村旅游和民族特色游，并结合美丽乡村精品村建设有序开发民宿，使许多村落面貌焕然一新，但在进一步提高服务人员素质与水平、营造良好的健康饮食、生态旅游环境等方面仍有改进空间。为此，民盟金华职业技术学院一行13位盟员，充分发挥专业特长，前往武义宣平、俞源、坛头村等地实地考察，调研当地农村旅游经济发展现状以及职业教育对新农村建设的作用发挥情况，同时也是继续做好支部《健康饮食指导队》品牌建设工作，完善服务内容，扩充服务功能，有序地开展健康钦食指导服务活动，为休闲农家乐的健康发展多献良策。 （民盟金职院支部  王成福）', 'http://info.jhc.cn/2015/1214/c201a54605/page.htm', NULL, '2017-05-17 14:14:29.625496');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (255, '周荃', '', '系统管理员', '2015-12-14 00:00:00', '信息大富翁 青春向前冲——记信息学院举办第二届户外活动', '为了培养学生团队协作、互帮互助的精神，12日下午，信息学院在小操场举办了第二届户外正能量活动--“信息大富翁”。选手们分为三人一组，使用特制大骰子投掷，并依照点数，环绕跑道地图行进，地图上有 “吞水传音”、“倒水接力”、“ 趣味答题” 等16个游戏任务，完成便能得到相应的财富值。在活动中，同学们使出了浑身解数，分工合作，最后，“ 三人两足”绑在一起呐喊着口号冲过50米长的趾压板到达终点。     据悉，此次活动共有300余名师生参与。老师们表示，“信息大富翁”就是校园版“奔跑吧兄弟”，内容丰富而有趣，把学生们都带出了寝室。同学们也反映，通过了这次活动，不仅懂得了团结合作的重要性，也丰富了课余生活。  （学工办  吕威威）', 'http://info.jhc.cn/2015/1214/c201a54604/page.htm', 'http://info.jhc.cn/_upload/article/images/fa/33/9392eb774c92b3d70b40782a9183/d549d277-981b-4370-a78d-fcc0c100d072.jpg|http://info.jhc.cn/_upload/article/images/fa/33/9392eb774c92b3d70b40782a9183/80fd55eb-64fa-4515-866c-b1beaf5577e4.jpg', '2017-05-17 14:14:29.647894');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (256, '周荃', '', '系统管理员', '2015-12-14 00:00:00', '信息学院召开卢旺达留学生人才培养方案研讨会', '12月12日，信息学院召开了卢旺达留学生人才培养方案研讨会，院领导、教科办、通信与网络工程部、12位卢旺达留学生参加了会议。   会议首先由专业主任向留学生们介绍了我院通信专业面向的行业情况、培养方向与层次、主要就业岗位分析、专业课程体系架构及分学期课程安排、主干课程介绍以及往届专业顶岗实习情况等。会议中还就留学生关心的问题进行了解答和探讨。陈海荣院长对12位留学生来我院学习表示了热烈的欢迎，希望他们能在融入我院学生生活并学有所成。   据悉，这12位留学生是卢旺达政府公费派出到我校通信专业进行学习的，采用1+3模式进行培养，即1年中文学习加3年专业学习。 （苏红富 通信与网络工程部）', 'http://info.jhc.cn/2015/1214/c201a54603/page.htm', 'http://info.jhc.cn/_upload/article/images/40/d5/0b0ec6404d14b73c29a13ba4ab41/7e5234c2-6384-4441-b2f4-c006b07c16bc.jpg|http://info.jhc.cn/_upload/article/images/40/d5/0b0ec6404d14b73c29a13ba4ab41/b2f70493-a613-4ef4-a9d8-43aae6662cc7.jpg', '2017-05-17 14:14:29.669966');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (257, '周荃', '', '系统管理员', '2015-12-10 00:00:00', '《金华日报》报道我院志愿者微信教学', '微信教学 记者 时宽兵 通讯员 干晓凡 文/摄 　　12月8日，金职院信息工程学院青年志愿者大队学生来到市区三江街道新园社区，发挥所学专长，给新园、东莱、阳光、江滨、雅苑等社区工作人员开设微信教学课堂，教他们学习微信订阅号的使用和信息发布、宣传片等的制作方法。 新闻链接：http://epaper.jhnews.com.cn/site1/jhrb/html/2015-12/10/content_1648335.htm', 'http://info.jhc.cn/2015/1210/c201a54602/page.htm', NULL, '2017-05-17 14:14:29.692552');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (258, '周荃', '', '系统管理员', '2015-12-10 00:00:00', '《金华晚报》报道我院志愿者为社区干部讲课', '为社区干部讲课 12月8日，金职院信息学院E·就通实践队志愿者在市区月亮湾小区，讲了一堂微信课程，讲述公众平台运营、微信排版方面的操作等问题。来自东莱、阳光、江滨、雅苑、新园、寺前皇、天龙南国等社区以及三江派出所的21名主任和干部到现场听课。这次课堂分为三个阶段，首先是微信公众平台如何使用的介绍，第二阶段是提供给社区主任们的提问时间，最后一个阶段是现场教学。经过志愿者的讲解，听课的人都说收获很大。汤鑫严 吴彰义 摄 新闻链接：http://epaper.jhnews.com.cn/site1/jhwb/html/2015-12/10/content_1648633.htm', 'http://info.jhc.cn/2015/1210/c201a54601/page.htm', NULL, '2017-05-17 14:14:29.714404');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (259, '周荃', '', '系统管理员', '2015-12-09 00:00:00', '信息学院新媒体培训走进社区，助力智慧社区建设', '在“互联网+”的大背景下，基层政府如何更好地利用新媒体平台服务于民？12月8日下午，信息学院E.就通实践队志愿者应社区主任邀请在月亮湾社区演播大厅开展“互联网+社区服务，微信平台如何在社区中发挥作用”的教学。东莱、阳光、江滨、雅苑、新园、寺前皇、天龙南国社区以及三江派出所21名社区工作者参与了此次新媒体培训。 培训现场，信息学院学生汤鑫言、方亮利用专业所学与运营学院微信公众平台的实践经验，从“微信是什么？”、“如何运营社区微信公众平台？”、“如何利用新媒体服务于民”等内容展开培训。现场穿插着互动提问环节，及时解决了社区主人们实际运用过程中的问题。在实践操作中，志愿者们重点对几个常用的微信编辑器的使用进行了操作，以及微信排版和运作的流程做了详细的讲述。     通过这次微信培训，在场的工作人员学到了许多微信的使用方法和如何打造一个让社区居民更感兴趣的微信平台，大家受益匪浅。据悉，E.就通实践队在今年被评为浙江省大中学生暑期社会实践优秀团队，学生团队将继续为农村、社区普及新媒体和互联网知识。 （方亮 丰辉）', 'http://info.jhc.cn/2015/1209/c201a54600/page.htm', 'http://info.jhc.cn/_upload/article/images/0e/dc/2231380c42938d20f0a1fa34720b/f0120487-57e3-4622-859c-918526d2d3c3.jpg|http://info.jhc.cn/_upload/article/images/0e/dc/2231380c42938d20f0a1fa34720b/fc56377b-9530-4e57-ac74-c66c0bbabb57.jpg', '2017-05-17 14:14:29.736664');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (260, '周荃', '', '系统管理员', '2015-12-09 00:00:00', '信息工程学院电子设计创新平台通过验收', '近日学校邀请浙江大学城市学院任国海教授担任验收组组长，由来自高校和企业专家、学校相关职能部门人员组成的验收小组对信息工程学院承担的电子设计创新平台进行了项目验收。   据悉，电子设计创新平台是由信息工程学院应用电子技术专业教学团队和浙江师范大学电子信息工程专业教学团队共同自主研发的创新实验平台，研发历时近3年。该平台共由14个系列的功能模块、5个综合应用项目和融合7S管理理念的定制实验台等三大部分组成。研发团队在总结梳理全国大学生电子设计竞赛10余年竞赛赛题的基础上，利用各自带队参赛经验和工程实践对赛题进行教学化设计，形成了微控制器、线性电源总成、频率测量、滤波器、信号源、AD和DA转换、信号变换、数控电压电流源、微弱信号放大、传感及信号调理、开关控制与电机驱动等14个系列的典型电路功能模块。每个模块都体现设计电路最简、参数调试最方便、电路最稳定等特点，单个模块可独立应用于多门课程教学，也可以多个模块“积木式”重组形成综合创新项目或实现各种电子产品的样机试制，5个综合应用项目即由功能模块外加专用传感器和控制对象集成创新应用而成。体现了平台的创新性、灵活性和开放性。   电子设计创新平台为电子电气类专业课程教学、学生电子创新设计、教师科研等提供了优越的教科研平台，更为高职四年制相关课程提供了优秀的教学项目，也为目前开展的四年制高职“多导师制”试点提供了训练场所。同时，该平台在2015年暑期承担了“电子信息类专业骨干教师（嵌入式系统应用）培训（编号11151016）”国培项目，深受36位来自全国高职院校电子类专业教师的一致好评。                                                                                                                 （文 余红娟 刘子坚）', 'http://info.jhc.cn/2015/1209/c201a54599/page.htm', 'http://info.jhc.cn/_upload/article/images/0d/f9/6a7486364b4da06a303cf85ce3a0/799d9ef6-516a-410c-a6c8-f9c1ecc4c81c.jpg|http://info.jhc.cn/_upload/article/images/0d/f9/6a7486364b4da06a303cf85ce3a0/8f7f07a9-783a-480c-a85d-6f3ab2494e02.jpg', '2017-05-17 14:14:29.75933');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (261, '周荃', '', '系统管理员', '2015-12-08 00:00:00', '我院顺利举办优秀教师代表先进事迹报告会第二组活动', '12月3日下午，我校优秀教师代表先进事迹报告会第二场拉开帷幕。公共学院俞旭红、农学院王燕丽、制药学院胡玲霞、艺术学院程立珂等四位优秀教师代表在报告会上先后发言，信息、机电、公共学院教职工及学生代表共300余人聆听了报告会。报告会由信息学院党委书记贾美倩主持。  会议现场 贾美倩在讲话中指出，我们的身边有许多师德高尚、充满活力的教师，他们通过不懈的努力获得了来自教师同仁和学生的信任与尊重。希望全体教职工以先进教师为榜样，立足本职岗位，成就自己的理想，让学生享受高质量的职前教育，成为社会欢迎的人才。报告中，“学生最喜欢的老师”俞旭红与大家分享了自己多年来从事人际交往方面学习进修及授课的经验感悟；学生最爱的“猪老师”王燕丽运用行业生产一线的工作经验丰富教学案例，巧用手机打造魅力课堂，让学生轻松愉快学养猪；十佳教师胡玲霞有着15年企业经验，自06年入校以来一直在教学、科研上不断创新，传授的知识与企业接轨，让学生成为企业受欢迎的员工；十佳班主任、辅导员程立珂讲述了自己为专业、为岗位、为学生努力拼搏，始终传递向阳花正能量的感人经历。教师代表们的事迹平凡却真实，饱含着对学生的关怀、对教育事业的热爱，深深打动了在场的每一位听众，激起了大家的强烈共鸣。据悉，为大力宣传优秀教师的先进事迹，激励广大教师以先进为榜样，进一步深化学校师德师风建设，促进教师队伍素质的整体提高，学校近期组织省师德先进个人、2013—2014年度“三十佳”、第五届“学生最喜欢的老师”的优秀教师代表分5场向全校教职工作先进事迹报告。 （周荃  办公室）', 'http://info.jhc.cn/2015/1208/c201a54598/page.htm', 'http://info.jhc.cn/_upload/article/images/c6/37/3e5c94d24434b7d4e2790cdfdfdd/e7942afb-21ed-468d-a71f-e4a1ca80cbd6.jpg', '2017-05-17 14:14:29.78104');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (262, '周荃', '', '系统管理员', '2015-12-06 00:00:00', '信息学院召开招生回访反馈会', '为了进一步做好招生回访工作，12月3日下午，学院召开招生回访反馈会，各片区招生回访工作人员及继教部负责人参加，会议由党委书记贾美倩主持。   本次招生回访分为4组，每组都由一位院领导、一位专业主任、两位学工办老师组成。会上，4组人员分别就回访情况进行了汇报、交流，分析了各生源地学校的特点及需求。陈海荣院长进行总结，并就指导下一步的招生工作提出具体要求：一是招生队伍要合理搭配；二是加快做好中外合作和本科视频；三是要有质量意识，与生源地学校保持联系，满足他们的需求，充分挖掘在校生资源。反馈会结束后，与会人员表示，会议充分总结了招生回访工作中需要改进的地方，进一步明确了今后招生回访工作的重点。 （学工办/团委 郭天鹏）', 'http://info.jhc.cn/2015/1206/c201a54597/page.htm', 'http://info.jhc.cn/_upload/article/images/8a/ff/750e430c4329b7fd361076a780db/c2fd76f8-ae2d-42dc-ac78-fe0ff350d940.jpg', '2017-05-17 14:14:29.803423');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (263, '周荃', '', '系统管理员', '2015-12-06 00:00:00', '信息学院召开中新合作班英语教学会议', '12月3日，信息学院召开了中新合作班英语教学会议，学院领导、外教、助教、中新合作班班主任、教科办等参加会议。在热烈而轻松、中英文交流相结合的氛围中，Craig就本学期后期的英语教学安排做了详细说明：一周有三天时间做习题册，一天是对话听力，最后一天是写作和听力。为了更好地完成教学任务，学院将配备合适的教学场所和管理老师。另外，会上还讨论了下学期英语教学安排以及学生的分班教学工作。    据悉，由于ESL课程结束后，外教将返回新西兰，为了确保学生即使没有老师也能每天都有80分钟学习英语的时间，学院联合外教对英语教学做了细致的安排。  会议现场  Craig在做教学安排说明  和Richard热烈讨论中   （教科办 徐华丹）', 'http://info.jhc.cn/2015/1206/c201a54596/page.htm', 'http://info.jhc.cn/_upload/article/images/78/07/0e52b6dd40a48c155237faf4efbc/316f0153-6d2c-427e-92d3-de433db98fab.jpg|http://info.jhc.cn/_upload/article/images/78/07/0e52b6dd40a48c155237faf4efbc/06ff286d-ebaf-4875-9738-6669daa15d7b.jpg|http://info.jhc.cn/_upload/article/images/78/07/0e52b6dd40a48c155237faf4efbc/068bcf56-2707-4203-bbe4-3267ff7253e6.jpg', '2017-05-17 14:14:29.825659');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (264, '周荃', '', '系统管理员', '2015-12-06 00:00:00', '部门合作解难题，学校召开四年制高职工作小组会议', '12月3日上午，我校召开四年制高职教育人才培养试点工作小组会议。院长钟依均，相关职能部门人员和信息学院负责人参加了会议。会议由校党委副书记胡野主持。   会上，信息学院汇报了四年制高职人才培养试点工作的进展情况，与各职能部门围绕四年制高职办学工作中需要协调解决的问题进行了探讨与交流。校长钟依均对学校今后四年制高职人才培养试点工作提出了四点要求：一要高度重视四年制高职人才培养试点工作；二要各职能部门做好各项协调对接工作；三要及时、积极与浙江理工大学沟通，解决遇到的问题；四是多与其他试点院校沟通，吸取经验。胡野副书记提出：要善于宣传，加强服务，主动接受浙江理工大学的指导，勇于探索管理新方法、新路径、新体系，把四年制高职建设成高峰专业，各部门要明确职责，协同办好四年制高职教育。本次会议的召开将进一步推动我校四年制高职人才培养的各项工作，积极协调各部门职能，充分发挥我校教育教学的能力水平。  （教科办  徐华丹）', 'http://info.jhc.cn/2015/1206/c201a54595/page.htm', 'http://info.jhc.cn/_upload/article/images/87/f2/64b653ca4635b587065d029423dc/1793978a-fc1e-4152-ab39-c4b5c7e6b07e.jpg', '2017-05-17 14:14:29.847925');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (265, '周荃', '', '系统管理员', '2015-12-04 00:00:00', '信息工程学院2015年电子信息类专场招聘会', '近日，信息工程学院电子信息类专场招聘会在西教学楼一号楼顺利举办。此次招聘会得到了企业与毕业生的高度重视，共有29个招聘企业进场并且提供了600多个不同类型的岗位，满足广大毕业生的需求。此次招聘会的成功举办不仅使同学们得到了充分表现自我的机会，让校园更加充满活力和朝气，而且让同学们认清了自己的不足，在以后的工作和生活中有目的的培养自己的能力，完善自我。信息工程学院也将有计划、有目标的开展好毕业生就业工作，抓住校园招聘会的机会，为毕业生做好就业服务工作。  （学工办/团委  钱靖）', 'http://info.jhc.cn/2015/1204/c201a54594/page.htm', NULL, '2017-05-17 14:14:29.870068');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (266, '周荃', '', '系统管理员', '2015-12-03 00:00:00', '“科技创新券”助推高校智力更好地为社会服务', '要说今年在市区企业间什么话题最火，那就要数“科技创新券”了。市科技局推出“科技创新券”，向广大中小微企业和创业者派发“科技大红包”，有了它，我市中小微科技企业和创业者可方便享受省内700多家科技创新平台和载体提供的科研服务，研发成本将大幅降低，技术水平得到有效提升。据悉，今年8月份，我市首张科技创新券成功发放，为企业节约了75%的开支。信息学院充分发挥学科专业优势，积极响应学校科技创新号召，近日，学院组织各部门负责和首批学校科研创新团队学习科技创新券的申领使用流程，充分发挥高校智力优势更好地为金华当地企业服务，并就高校创新载体服务内容的申报与完善，如何提升科技服务品质，发挥高校智力、技术的优势为广大企业和创业者提供服务等方面进行了深入研讨。会上，还就教改论文的撰写组织了专题指导，从论文的素材来源、分类方法、确定选题、撰写方法、投稿技巧等方面进行了详细指导，并通过典型案例，从观点、结构、表达、成稿、修改等方面介绍了经验与做法，为教师如何总结提炼科研成果、撰写高质量的教改论文深到了良好的启示作用。   据悉，今年首年金华市区发放科技创新券的总额不少于300万元，采取自主申领、先领先得原则，同一企业或创业者一次性申领不超过2万元，每个企业和创业者可获得的科技创新券年度最高上限为5万元。金华各地市均已正式发放科技创新券。“科技创新券”似一股强流推动着企业的科技创新，也不断推动高校智力发挥自身优势，更好地为社会服务。 （办公室 周荃）', 'http://info.jhc.cn/2015/1203/c201a54593/page.htm', 'http://info.jhc.cn/_upload/article/images/cc/e9/d77b712e40cfa13c987d40fe6526/560ef952-93f7-472f-853d-bf3b11473721.jpg', '2017-05-17 14:14:29.892196');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (267, '周荃', '', '系统管理员', '2015-11-30 00:00:00', '信息工程学院党委赴丽水开展党员活动', '为进一步落实从严管理制度，深入开展“三严三实”专题教育活动，11月28日，信息学院党委组织全体教职工党员赴浙江省党史教育基地——浙西南革命根据地纪念馆开展党员活动。   在纪念馆门口，“喜迎红军”大型雕塑刻画出红军挺进师与群众鱼水相融，率领群众共同投身革命的场景，凝重而恢宏。全体党员在鲜红的党旗下郑重宣誓，重温入党誓词，随后参观了“建立党组织，开展武装斗争”、“创建浙西南革命根据地”、“抗日救亡，解放浙西南”3个展区等一系列重要历史史料和实物，挺进师爱民不扰民、刘英为群众治病疗伤、畲族群众保护伤员等生动案例的场景复原让党员们久久驻足，发人深省。     浙西南革命历史蕴藏着丰富的群众路线事例，是一部党的群众路线历史。在参观过程中，大家深刻感悟，作为一名共产党员，要坚持和践行群众路线，想群众所想，为群众办实事。我们必将传承红色信仰，坚定并满怀信心地建设好我们的家园。  （办公室  周荃）', 'http://info.jhc.cn/2015/1130/c201a54592/page.htm', 'http://info.jhc.cn/_upload/article/images/51/5d/c9f0822f49ba82c7f73f6fbca481/4378f8b3-8799-457d-bfc0-a37aae66b6f7.jpg|http://info.jhc.cn/_upload/article/images/51/5d/c9f0822f49ba82c7f73f6fbca481/f28d34b0-0a2d-463e-b87a-a67402d7dffd.jpg|http://info.jhc.cn/_upload/article/images/51/5d/c9f0822f49ba82c7f73f6fbca481/e2c89267-b6fe-480d-8752-be45fd9c7ce6.jpg', '2017-05-17 14:14:42.464032');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (268, '周荃', '', '系统管理员', '2015-11-30 00:00:00', '学习软件开发新知识 开展教学改革新模式', '为了进一步深化专业学习的历程化管理新模式，近日，信息学院信息化事业部组织专题教研活动，由吕焱飞老师介绍Git技术和阿里云技术，并展示如何具体实现教师教学与学生学习的网络化管理和历程化管理。Git是一个分布式的版本控制系统，用作Linux内核代码的管理。作为开源代码库以及版本控制系统，Github拥有140多万开发者用户。随着越来越多的应用程序转移到了云上，Github已经成为了管理软件开发以及发现已有代码的首选方法。阿里云是全球领先的云计算技术和服务提供商。阿里云对外开放其在云计算领域的技术服务能力。用户通过阿里云，用互联网的方式即可远程获取海量计算、存储资源和大数据处理能力。基于新一代的云平台远程部署系统业务，已经成为互联网公司和开发者的首选。   信息学院软件技术专业在探索教学改革的道路上不断尝试国内外先进工具与技术。课程教学层面，于2011年起引入Moodle社会化学习平台，探索基于“理实一体+自主学习”的混合式教学实施模式、构造基于“即时评价+形成性考核”的教学管理模式及对应的保障机制，大幅提高学生的学习积极性与学习效果；综合实践层面，于2014年起引入国内先进的分布式虚拟机服务“阿里云平台”，整合近年来软件专业师生在专业课程教学、学科竞赛、校企合作项目开发等方面取得的成果，建立源码库探索快速原型软件教学法，建立“学习历程档案袋”，探索“师徒制”教学模式在专业综合实训项目中的应用，形成具有软件专业特色的“历程化”实践教学模式。本次教研活动，主题明确，针对性强，是一次对专业教学新模式进行改革和探索的总结，相信必将推动信息化事业部在专业人才培养上迈上新台阶。 （ 文 陈晓龙 王伟斌 胡平 ）', 'http://info.jhc.cn/2015/1130/c201a54591/page.htm', 'http://info.jhc.cn/_upload/article/images/74/1e/bd229fb84524ae057182ae799661/6fe1ceba-0686-48f5-ae5d-9385a27999a4.jpg', '2017-05-17 14:14:42.554719');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (269, '周荃', '', '系统管理员', '2015-11-30 00:00:00', '信息学院首届英文美文朗诵大赛暨新生活动月闭幕式顺利召开', '“We are the world, we are the children ”这首由迈克尔·杰克逊创作的震撼人心的歌曲又再一次在金华职业技术学院的实验剧场被唱响。为了提高同学们学习英语的积极性，鼓励同学们张开嘴说英语、练英语，信息学院于11月26号举办了 “才华横溢，英绎信息”新生英文美文朗诵大赛，并且请来了新西兰的三位外教作为评委。   各个新生班级精心准备了形式多样化的节目。短剧《the bone in throat》分角色扮演，参赛同学用丰富的表情和肢体动作绘声绘色地带给观众们一个富含寓意的故事；歌曲《take me to your heart》凭借悠扬的旋律，引得全场同学在台下轻轻地附和；情诗《我喜欢你是静静的》则以轻柔的背景乐曲，饱含深情的诵读让同学们的心灵沉醉。经过激烈角逐，物联网151班以嘹亮的声音以及饱满的情感赢得比赛一等奖。最后，三位外教为大家弹奏演唱了新西兰的民歌，又将同学的热情点燃，比赛后续还陆续颁发了新生活动月各项比赛的奖项。     同学们表示，经过了这场比赛，除了能够展现自己的英语口语，还借这个机会增加了班级的凝聚力，希望以后这样的活动能够多多展开。 （学工办/团委 徐露诗）', 'http://info.jhc.cn/2015/1130/c201a54590/page.htm', 'http://info.jhc.cn/_upload/article/images/2b/f3/bb91789e44b3a5c4db69d22478bd/68cc8bb9-c556-4ca4-b022-30c7773c775a.jpg|http://info.jhc.cn/_upload/article/images/2b/f3/bb91789e44b3a5c4db69d22478bd/a8a71641-4baf-49ba-ba6d-d547553c9350.jpg|http://info.jhc.cn/_upload/article/images/2b/f3/bb91789e44b3a5c4db69d22478bd/bb2f0ddc-2190-4f63-87f4-3c74b0dbdfa8.jpg', '2017-05-17 14:14:42.574579');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (270, '周荃', '', '系统管理员', '2015-11-20 00:00:00', '信息学院召开出国境学习汇报会', '蓝天白云、现代化的教学设备和场所、以人为本的文化氛围、网络化的教学资源、完善的教育体系……。11月19日下午，在信息学院会议室，赴新西兰怀卡托理工学院学习的老师们正在汇报他们学习期间的所见所闻。        在会上，老师们从介绍新西兰的终身学习教育体系，以及与我院几个合作专业的专业培养计划的比较等几方面入手谈了这次培训的体会与启发。通过交流，与会教师从中进一步了解了怀卡托学院以人为本的文化氛围、教师严谨细致的工作作风、师生间融洽的教学关系。大家都认为怀卡托学院在教学理念、课堂教学实施等许多方面是值得我们学习与反思的。     下一步，学院将优化出国境培训方案，提高出国境教师团队的培训质量和效果，从而进一步提高课堂教学有效性，做强专业，提高人才培养质量。 （教科办  徐华丹）', 'http://info.jhc.cn/2015/1120/c201a54589/page.htm', 'http://info.jhc.cn/_upload/article/images/dd/f6/045e5d0346bd861d3e5847b27ebb/957a25d4-3cb4-440c-9605-1073396ffe7f.jpg', '2017-05-17 14:14:42.595044');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (271, '周荃', '', '系统管理员', '2015-11-18 00:00:00', '我院学子在全国大学生微创业大赛中获银奖', '近日，全国“2015年大学生微创业行动”获奖榜单新鲜出炉，我校信息学子余健斌的“自定忆摄影工作室”项目从全国近500个竞选项目中脱颖而出，斩获银奖，获得一万元创业扶持资金。12月初，他将赴北京大学领奖。据悉，“大学生微创业行动”由KAB全国推广办公室与广东省广发证券社会公益基金会联合举办，以“互联网+微创业”为主题，通过活动寻找好的微创业项目，给予大学生创业者以扶持和指导，鼓励大学生积极投身创业行动。此次活动共评出金奖10个，银奖20个，铜奖20个。余健斌为信息学院计算机141班学生，自大一开始，便与同学共同筹建“自定忆摄影工作室”并申报学校创业园，开始创业之旅，从事婚礼与产品拍摄、教师微课视频拍摄与制作以及各类校园文化活动。今年4月，余健斌在对义乌市场调研时发现，很多外贸企业产品很好，但由于无法被外商直观了解，从而影响产品出口。于是，他萌生了一个念头，将工作室重点定位于为阿拉伯国家地区的电子商务运营商，提供阿语产品介绍拍摄与制作、阿语企业宣传、商品摄影、图片处理、产品精致排版服务，使工作室实现互联网+产品营销目的。目前，团队已成功打开摄影市场，每月获纯利润近万元。 （学工办/团委 丰辉）', 'http://info.jhc.cn/2015/1118/c201a54588/page.htm', NULL, '2017-05-17 14:14:42.615709');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (272, '周荃', '', '系统管理员', '2015-11-17 00:00:00', '500强企业为信息学子开绿灯', '近日，北京天融信科技股份有限公司杭州分公司经理王余携人力资源、技术主管等一行3人来我校信息安全创新实验室点对点校招。信息学院赵敏笑副院长带领参观物联网、信息安全实训室。王余经理表示实验室成果与市场接轨度高，具备商业化价值。随后公司人事和技术对实验室核心学生进行笔试和面试，在反馈中表示对学生的实战经验，职业匹配度表示感到满意。为此，以人才引进的标准特招网络专业毕业生5人。特招员工实习期待遇等同于正式员工待遇，薪资和补贴合计约为5千元，转正后分级别月薪为6千元至2万元不等。   据悉，天融信科技股份有限公司成立于1995年，为上市公司。 2004年，跻身国内500强企业。是国内老牌网络安全厂商，防火墙一直位居国内第一，综合领域排名在前三甲之列。实训室成立于2013年，目前为省通报中心的技术支撑成员单位之一（省内唯一高校团队）。实训室成立之初就致力于网络技术的应用创新，建有天眼平台和社工库2大对外授权服务窗口。此次点对点特招是品牌企业对我院创新性人才培养模式和毕业生多样化就业工作的肯定，也是对刻苦钻研、技术立身的同学的极大鼓励。  （沈晨、朱墨池）', 'http://info.jhc.cn/2015/1117/c201a54587/page.htm', 'http://info.jhc.cn/_upload/article/images/a9/51/9947ad31444d998b054924ce88da/463ab78c-a767-4316-a21d-80ee36f476cc.jpg', '2017-05-17 14:14:42.638524');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (273, '周荃', '', '系统管理员', '2015-11-17 00:00:00', '信息工程学院举行寝室消防演习', '11月16日中午，信息学院在龙川北苑04南楼举行了寝室消防应急演练。院长陈海荣、学工办成员、学生干部及各班级学生代表参加了此次演习。“浓烟四起”，随着工作人员的哨声吹响，同学们按照事先制定的《消防演习方案》在“火灾”中井然有序、及时有效地撤离事故现场，反应迅速，动作到位。同学们纷纷表示，此次寝室消防演习非常有意义。   消防演习对于保障同学们安全、提高同学们的消防意识和能力非常有帮助，信息学院将举行更多的安全教育活动，减少、避免伤害事故发生，进一步培养我院学生的自我防护能力。 （吕威威、何碧峰）', 'http://info.jhc.cn/2015/1117/c201a54586/page.htm', 'http://info.jhc.cn/_upload/article/images/38/9f/78181f624d3ca233744645108185/1c37edb0-61bb-4634-b51b-38aa4315f92f.jpg', '2017-05-17 14:14:42.659877');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (274, '周荃', '', '系统管理员', '2015-11-16 00:00:00', '信息学院进行“六个一批”课堂创新项目预验收', '为了迎接学校对我院今年“六个一批”课堂创新项目建设工作的考核和验收，信息学院在前期对教学创新项目进行密集听课、评课的基础上，11月13日下午，学院召开各项目建设负责人会议，对各创新项目的建设情况进行了预验收。   通过听取各项目负责人的汇报，以及对相关材料的检查，各创新项目的总体建设情况还是较满意的，但也发现了一些问题，比如，优质平台课程在特色和创新上需要进一步提炼；精品资源共享课在资源的持续更新与共享方面还需要做更多工作；各项目的原创性比例需要进一步加强等。会后，各位课程建设负责人都表示，近期在改进检查中发现的问题的同时，将把更多的时间和精力用在项目总结和课堂实录上，保质保量完成建设任务。 （教科办  徐华丹）', 'http://info.jhc.cn/2015/1116/c201a54585/page.htm', 'http://info.jhc.cn/_upload/article/images/1d/de/43e93f994f7ebca98aac3eaa4cb4/233d1423-c682-44e4-b9a1-397ecf713a64.jpg', '2017-05-17 14:14:42.682116');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (275, '周荃', '', '系统管理员', '2015-11-16 00:00:00', '启动项目导师制以解决四年制高职教学之难题', '如何针对四年制高职教育的中职生源进行有效的理论教学？该如何解决普通物理、高等数学等逻辑思维性较强课程的教学难题？信息学院启动了项目导师制，选拔了13位专业基础深厚、工作责任较强、关心爱护学生，且具有副高以上职称或担任班主任担任导师，以项目实施为抓手，按学习小组与学生之间建立平等、互信的师生关系。  图一  导师制启动会现场  图二  师生结对  图三  小组热烈讨论中 据悉，学院针对中职学生原有理论基础知识薄弱、自主学习与自控能力不足，实施项目导师制，每位导师和5位学生结对，与学生之间建立平等、互信的师生关系，对学生进行心理疏导、思想引导、专业辅导、生活指导和工作能力培养，并根据学生的特点因材施教，以项目实施为载体，培养学生分析问题与解决问题的能力，学习和掌握撰写项目开题报告、技术总结报告、软件仿真与实物制作，制作项目汇报PPT等技能。导师每周至少对每位学生的项目进展情况进行检查，指出不足及努力方向，小组之间通过汇报交流，互相促进，共同提高，实现个性化的成长发展目标。最后，陈海荣院长强调：目标与行动要相结合，学习是为了应用，在行动导向下，在用中学，在学中用。合理利用业余时间，安装、应用软件，对项目进行仿真，两周后项目汇报时要有仿真结果。小组形成团队，多交流方法、理念，相互学习、提高，共享资源。以后有更多更好的项目可以做。期望同学们认真践行，勇于探索，主动学习代替被动学习，创意无限。仪式结束后，每个小组的学生和导师都进行了充分的交流，在导师指导下，各小组均制定了本学期的详细学习计划。 （王成福、徐华丹）', 'http://info.jhc.cn/2015/1116/c201a54584/page.htm', 'http://info.jhc.cn/_upload/article/images/d9/08/0393267d4284b5c3327afdaebf0a/e483f36a-e01f-43db-b885-5dd71b529aec.jpg|http://info.jhc.cn/_upload/article/images/d9/08/0393267d4284b5c3327afdaebf0a/ebebde32-091c-4d08-b565-3be3452d596a.jpg|http://info.jhc.cn/_upload/article/images/d9/08/0393267d4284b5c3327afdaebf0a/69e0eeba-a020-4f14-932c-0e7435b886d2.jpg', '2017-05-17 14:14:42.704291');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (276, '周荃', '', '系统管理员', '2015-11-10 00:00:00', '信息教师荣获2015年全国职业院校信息化教学大赛“信息化实训教学比赛”三等奖', '11月7号—9号，由教育部主办的2015年“凤凰创壹杯”全国职业院校信息化教学大赛在南京高等职业技术学校隆重举行，来自全国37个省自治区、直辖市和新疆建设兵团、计划单列市和解放军的989件作品，2277名教师参加比赛。比赛项目设中职组、高职组信息化教学设计比赛、信息化课堂教学比赛、信息化实训教学比赛等6个赛项。经过激励的角逐，我院由叶红芳、张小杭、曹胜任组成的教师代表队不畏困难、努力拼搏，喜获信息化实训教学比赛“PLC控制电动机启动电路的组装与调试”项目三等奖，为学校争得荣誉，这也是我校教师代表队在该赛项设立以来第一参赛。   赛项内容涉及到教学设计、说课、电路装调、排故和答辩五个环节。要求教师在PLC技术和触摸屏应用、信息化技术应用、教学能力等方面具备扎实的专业理论知识、较强的实践能力和良好的团队协作精神。                                                    （教科办  曹胜任）', 'http://info.jhc.cn/2015/1110/c201a54583/page.htm', 'http://info.jhc.cn/_upload/article/images/b0/e0/e8de662643a58e5fd7e0f90b2775/2fd38c94-88ff-4ca8-b723-a7ae135f1aa3.jpg', '2017-05-17 14:14:42.726588');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (277, '周荃', '', '系统管理员', '2015-11-06 00:00:00', '信息学子吴姜宏入选省百名大学生“农创客”', '近日，由省农业厅、省农村信用联合社、浙江在线新闻网站联合开展的寻找百名大学生“农创客”评选活动揭晓，我校信息学院应电143班吴姜宏同学入选，成为其中最年轻的在校农创客。 吴姜宏专业成绩优异，积极参与第二课堂的学习，现为信息学院学创青年俱乐部社长和“全国大学生创业实训营”学员。还未走出校园的他，在学校和学院的支持下，成功注册金华市贤仁科技有限公司并投入运营，成了名副其实的“校园企业家”，他申报的创业项目还成功入驻学校创业园、金谷创业园。接触过吴姜宏的人都说他头脑灵活、点子多。今年初，他与团队小伙伴推出“学创在线”平台，主营农村特产销售等，并为大学生们提供创业机会，学生注册后可成为平台的微客并在平台上代理销售产品，零成本投入就可以赚取10%至20%的佣金。微客还可以在平台上购买自己喜欢的产品，通过分享链接进行众筹、砍价、发红包等。上线不到一个月，“学创在线”就已发展了50多位校园合伙人、150多名学生创业者，团队还为师范学院等15家机构成功创建微网站、微信公众平台。吴姜宏说自己很看好互联网的市场，“如果一切顺利的话，我希望在今年年底到杭州各大高校建立自己公司的服务点，推广“学创在线”模式，争取在明年上半年推出属于自己的APP。” 据悉，此次评选出的“农创客”将有机会加入浙江省大学生现代农业创业俱乐部，免费参加浙江省农业博览会，与农业首席专家、农创大咖们结对子，免费获得技术指导等。同时，浙江省农村信用联合社还将给予每名“农创客”最高30万元的贷款授信。  （学工办/团委 丰辉）', 'http://info.jhc.cn/2015/1106/c201a54582/page.htm', NULL, '2017-05-17 14:14:42.748831');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (278, '周荃', '', '系统管理员', '2015-11-06 00:00:00', '分析学生特点探索四年制高职教育——信息学院召开教师座谈会', '为有效解决当前四年制高职教育教学中存在的问题，提高四年制高职课程教学质量及管理水平，学院于2015年11月4日召开了教师座谈会，教务处刘鲁平处长、招生办邱晓华主任、学院班子成员、本学期任课教师和电子产品开发部教师参加了座谈会，会议由王成福副院长主持。座谈会上，任课教师对本科学生的整体素质以及开课一个月以来的课程学习情况进行了分析与总结，他们认为本科学生学习欲望强烈、学习态度良好、课堂整体学习氛围不错，但由于学生在中职学校对物理、数学、英语等课程知识学习较少，导致学习基础薄弱、学习习惯和自制能力相对较差, 需要在相关课程教学中因材施教，探索行之有效的教学方法。另外，任课教师对东教学楼多媒体教学设备不够稳定的问题提出整改要求。刘鲁平处长针对教师反映的问题，提出了四点建议：（1）四年制本科办学是我们学校的试点，在教学管理等方面均需要积极探索与实践。如果一开始就给学生很难的知识理论，学生就有挫败感，影响学生学习的积极极性，所以需要提前引入导师制，给他们学一些擅长的东西，努力激发学生的学习积极性。（2）数学、物理是基础课程，主要培养学生的逻辑思维能力，是为后续专业学习打好基础的。可以适当增加课时，根据大学课程的学习需要适当补充一些高中的衔接知识。（3）在教学方法上，只要能发挥学生的学习积极性、主动性，各种方法均可以探索与实践。（4）要立足生源，做出特色，形成高职四年制本科办学特点。最后，学院班子对各位任课老师对本科学生的关爱，教学投入大量精力，花费大量心思，积极探索行之有效的教学方法等表示衷心的感谢，同时信息学院给每门课程提供5000元的课程建设费资助，以鼓励教师积极开展本科教学改革工作。同时，以项目为载体尽快落实专业教师与本科学生结对的指导工作，认真做好本科办学试点工作，决不辜负学校交给学院的任务。', 'http://info.jhc.cn/2015/1106/c201a54581/page.htm', NULL, '2017-05-17 14:14:42.771003');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (279, '周荃', '', '系统管理员', '2015-11-04 00:00:00', '信息学院理论中心组开展“三严三实”和“守规矩勇担当”专题教育活动', '11月2日，信息工程学院党委理论学习中心组开展“三严三实”和“守规矩勇担当”专题教育活动，学院中层及以上干部、各支部书记参加了专题教育活动。专题研讨中，与会人员结合各自思想及工作实际，对照党章党纪党规、习近平同志、刘云山同志的重要讲话，“三严三实”、“六个必须”等纪律要求，从思想觉悟、等方面深入开展了自我剖析与自我批评，明确了整改措施和努力方向。结合学院的发展实际，学院党委要求党员领导干部要重温入党誓词，强化责任担当意识，立足岗位努力提升自身觉悟修养，加强党员干部、骨干教师和管理人员三支队伍的建设，团队之间相互协调、相互支持。同时要求各党支部组织师生党员围绕“从严管理落实年”活动开展常态化的学习和研讨活动，并具体落实到各项实践工作中。据悉，信息学院党委10月份组织开展了“学党章遵党规守党纪学习活动”，每位学院中层党员（领导干部）上交一篇1500字的学习体会文章，并列出“不严不实”的问题清单，在学院党委理论学习中心组专题教育活动中进行汇报。  （陈德仙 周荃 办公室）', 'http://info.jhc.cn/2015/1104/c201a54580/page.htm', NULL, '2017-05-17 14:14:42.793164');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (280, '周荃', '', '系统管理员', '2015-10-30 00:00:00', '信息学院第二十四期业余党校开班', '10月30日，信息学院第二十四期业余党校正式开班，共有175名学员参加。 据悉，此次培训为期六个月，内容包括校内外专家讲座、学生讲师团宣讲、志愿者服务活动、社会实践、主题大讨论等内容。此外，党校还将在学员中开展“三严三实”专题学习活动以及学习习近平总书记在党的十八届四中全会上指出“七个有之”、“六个必须”纪律等内容。相关负责人表示，希望通过学习培训，能切实提高学员们的思想觉悟和政治意识，发挥其先锋模范作用。  （学工办/团委  丰辉）', 'http://info.jhc.cn/2015/1030/c201a54579/page.htm', NULL, '2017-05-17 14:14:42.815394');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (281, '周荃', '', '系统管理员', '2015-10-30 00:00:00', '校2015外籍专家学术系列讲座之九 ：新西兰怀卡托语言专家开讲奥特亚多瓦——长白云之乡', '10月30日下午，新西兰怀卡托理工学院高级语言教师、哲学和应用语言学双硕士学位理查德·休姆·劳伦斯应邀为我校师生作了一场题为“新西兰奥特亚多瓦——长白云之乡”的讲座，副院长杨艳、信息学院师生一百余人聆听了讲座。   位于太平洋南部有“长白云之乡”美誉的新西兰虽然历史较短，但决不缺乏壮美的自然风光。这个满是蓝天白云、人与自然和谐相处的国度，会带给你无比的热情和生命力。讲座中，理查德讲述了毛利人不畏艰险在海上航行发现了新西兰的探险之旅，他们初见这片陆地时误以为远方的雪山为白云，因而得名“长白云之乡”。理查德还为大家介绍了新西兰两位代表人物：为争取女权选举权做出特殊贡献的Kate Shephard和第一个登上世界最高峰的Edmund Hillary，在今天的新西兰元纸钞上都有他们的照片。还有流传至今的毛利见面礼——碰鼻礼，以及中新两国之间的交流交往。   讲座中，理查德还与两位怀卡托的同事用毛利语和英语演唱了新西兰国歌和著名毛利歌曲“POKAREKAREANA”，在场师生在歌声中一同感受美丽友善极具活力的新西兰。 （办公室  周荃）', 'http://info.jhc.cn/2015/1030/c201a54578/page.htm', 'http://info.jhc.cn/_upload/article/images/9f/79/2114c3e544b09392c1dbb2091926/1e5dd43c-7609-4dad-a08c-f2194228a123.jpg|http://info.jhc.cn/_upload/article/images/9f/79/2114c3e544b09392c1dbb2091926/dd204b91-21c7-49c1-be15-94a67b0e36f2.jpg', '2017-05-17 14:14:54.36915');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (282, '周荃', '', '系统管理员', '2015-10-30 00:00:00', '从严管理落实年：丰富学生学习内容  严抓新生早晚自习', '“天时不如地利，地利不如人和……”，早上7点就有朗朗书声从东教学楼传出，没错这是信息工程学院的学生在晨读。本学期，信息学院严抓新生早晚自习质量，给新生早晚自习制定了丰富多彩的学习内容。早自习以英语朗读和诵读《孟子》两项内容相结合的形式，充实学生晨读内容，提高学生的英语水平，了解国粹经典名著。晚自习的内容更是精彩万分，在完成每周作业的基础上，周日进行书法练习，在练字的同时，培养新生的心境；周一开展英语学习或专业学习，有时会邀请外教交流；周二、周三为名著鉴赏和阅读日，学院推荐学生阅读书目，要求学生一学期完成一定量的阅读数目，每月会公布班级、个人阅读排行榜，并在一学期中组织名著鉴赏竞赛；周四为班级主题班会活动日，学工办会提供每周主题班会主题、方案以及素材，由班主任及班主任助理指导班级开展。据悉，信息学院按照这一流程已经实行了4周。新生积极落实学院安排，对早晚自习的热情和兴趣明显提升。严抓新生早晚自习质量效果显著，使新生在思想、生活、学习、人与人沟通等各方面全面发展，真正做到了从大一抓起，培养新生的良好习惯。                                             （学工办/团委 郭天鹏）', 'http://info.jhc.cn/2015/1030/c201a54577/page.htm', NULL, '2017-05-17 14:14:54.509393');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (283, '周荃', '', '系统管理员', '2015-10-30 00:00:00', '不曾了解，不能言爱——信息学院“探究校园”主题班会', '校园是我们的第二个家，可是对这个美丽、亲切的校园，同学们有真正地了解她吗？你不曾在金湖前驻足，你如何会知道，夕阳下的金湖是如何波光粼粼？你不曾安静地在图书馆流连忘返，如何知道，图书馆的角落里，那些大师是怎样穿越千年告诉你他们的所思所见？你不曾亲自动手查询信息，你又怎么会知道那静静停留在东门的火车头代表了什么意义？你未曾静下心来，试着去深入地了解、探索这个你即将度过三年青春的地方，你没有用自己的脚一寸一寸丈量过你未来将称之为母校的地方，你怎么能对她产生归属感？为了让同学们更加了解美丽的金职院，信息学院在周四为全体新生班级组织了一次以“探究校园”为主题的班会。班会中，同学们通过PPT了解了校园的几个主要的景点和关于它们的小故事，接着请班里的同学走上讲台，与同学们分享了他们在校园中找寻到有趣的、令人印象深刻的景点。台上的同学讲得津津有味，台下的同学听得跃跃欲试，一场班会在你一言我一语中落下帷幕。班会结束后，同学们都迫不及待地想要试着再去探究美丽的金职院，相信经过这次班会，同学们会对金职院进一步地认识和熟悉，也将产生更深的依恋和归属。                                       （学工办/团委 徐露诗）', 'http://info.jhc.cn/2015/1030/c201a54576/page.htm', NULL, '2017-05-17 14:14:54.527323');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (284, '周荃', '', '系统管理员', '2015-10-30 00:00:00', '信息学院召开本科师生座谈会', '为了了解本科生学习、生活状况，提高本科教学质量，信息学院召开了本科师生座谈会，教务处刘鲁平处长、信息学院领导、教科办和学工办相关人员、电子产品开发部全体教师和电子信息专业的部分学生参加了会议。   座谈会上，刘鲁平处长明确此次座谈会的目的是了解学生对教师教学、学校社团活动和生活的状况，为学生解决实际问题。 “对我们来说，《普通物理》太难了！”“学校社团活动很多，有时候吃饭的时间都没有了。”“寝室同学之间很友好，卫生检查评语要更加有针对性一些。”“由于班级人数少，开班会的时候气氛比较难活跃，是否可以考虑2个班合班开？” 会上，每位同学畅所欲言，纷纷就学习情况、社团活动、寝室状况发表了看法及建议。最后，学院领导对学生提出的宝贵意见和建议表示感谢，就教学和管理中的不足之处向同学们表示歉意，并保证会在会后和相关部门与老师做好沟通，让以后的工作更加合理化。据悉，由于学生的来源不同，学院近期将对学生的基础进行摸底，并安排不同基础的学生进行补课，为后继课程的学习打好基础。学院还将安排专业老师作为本科学生的导师，指导学生如何学习。  （教科办  徐华丹）', 'http://info.jhc.cn/2015/1030/c201a54575/page.htm', 'http://info.jhc.cn/_upload/article/images/13/a1/9023dd6c4dd6965833fcd41a1a85/cdbdb665-015f-422b-a965-cab5a56714df.jpg', '2017-05-17 14:14:54.549672');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (285, '周荃', '', '系统管理员', '2015-10-29 00:00:00', '信息工程学院党委开展“学党章遵党规守党纪”学习活动', '根据学校有关要求，进一步落实从严管理制度，信息工程学院党委从10月13日—30日开展“学党章遵党规守党纪”学习活动，积极开展各项学习、自查和讨论。10月28日，信息工程学院党委在全院范围内以各支部为单位开展“三严三实”、“六个必须”纪律专题学习。前期，学院党委系统梳理了专题教育的学习资料，并要求学院领导班子、中层干部都加入到支部学习的队伍中。各支部采用集中学习和分组讨论的形式，深入学习了习近平总书记系列重要讲话精神和党中央有关部署，专题学习了《金华市党员领导干部从严管理“六个必须”纪律要求》。大家对照要求，结合各自岗位的工作实际，对新形势下党员同志特别是党员领导干部的思想政治建设及作风建设等方面进行了热烈讨论，认真查找梳理工作中不严不实的情况，并给出整改意见。党纪党规学习和实践是一场持久活动，有助于提升整个团队的凝聚力，有助于推动各项工作有序开展，信息工程学院党委将以此次学习活动为契机，在以后的学习工作生活中都要保持这种状态，将常抓不懈。  （周荃  办公室）', 'http://info.jhc.cn/2015/1029/c201a54574/page.htm', NULL, '2017-05-17 14:14:54.571897');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (286, '周荃', '', '系统管理员', '2015-10-29 00:00:00', '信息学院开展网格长培训', '信息学院高度重视网格长队伍建设，10月28日，学院学工办组织开展了网格长培训。此次培训由贾美倩书记主讲，学工办主任、全体辅导员参与其中。本次培训以网格长的来源、网格长工作职能、网格长应当严于律己为主线。其中，培训重点阐述了网格长的工作职能，要求收集掌握学生各类信息，平时在寝室里多走多交流，出现有问题马上反馈给上一级网格长，重点学生需要重点关注，从卫生、美化、人与人交流等方面提升寝室文化，帮助同学树立安全防范意识，了解党员知识。最后，贾美倩书记强调作为网格长一定要严于律己，要以解决实际问题为重点。   据了解，学院根据学生公寓网格的划分、学生述职考察等环节精心选拔了一批近30人的网格长队伍。培训结束后，辅导员和学生网格长都认为此次培训受益匪浅，让我们的工作有了一个明确的方向，对我们今后网格长的工作有实质性的指导作用。  （学工办/团委  郭天鹏）', 'http://info.jhc.cn/2015/1029/c201a54573/page.htm', 'http://info.jhc.cn/_upload/article/images/19/29/5a22f8684a039cd8fe7491c214d5/7bf88d01-d327-4f86-815b-c1b9853b04e1.jpg', '2017-05-17 14:14:54.594187');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (287, '周荃', '', '系统管理员', '2015-10-27 00:00:00', '特殊的英语课——信息学院教师掀起英语补习热', '如果外国友人问你“How are you？”，你会怎么回答？外教告诉你：不要再机械式地重复“Fine，thank you。And you？”啦！每周五下午，新西兰外教Craig给信息学院的管理人员和相关专业教师“补习英语”，Craig针对“补习班”老师们的英语水平精心设计教学环节，第一堂课就给大家重新讲解了“How are you？”的正确用法，鼓励老师们把自己的真实感受说出来。课堂中，现场模拟了日常交流的几个场景，澄清了“have to/must”、“maybe”、“should”等情态动词的用法，通过几轮的情景对话，老师们心领神会，很快理解了情态动词、不规则动词等的微妙区别。第二堂课，Craig首先考察了老师们的课后作业，大家都用上了上节课教的“I had to”回顾自己周末做的事。课上，Craig交给大家维持对话的法宝——“6W1H”开放式提问，还是围绕周末的活动展开，大家用“what、where、when、who、why、which、how”发问，把一个简单的提问扩展成一次对话。   据悉，信息学院中外合作办学项目开展以来，全院上下都洋溢着浓浓的英语风，不光是中新班的同学们学得很起劲，很多细微之处也发生了变化，老师的办公桌上多了英语书籍，手机上装了有道APP，办公楼里、实训场所大家碰见外教时都会主动用英语他们沟通。特殊的英语课，帮助中外教师在日常沟通的时候，表达更准确，氛围更加融洽。  （周荃  办公室）', 'http://info.jhc.cn/2015/1027/c201a54572/page.htm', 'http://info.jhc.cn/_upload/article/images/33/52/75c9871e4188a3c4d2b1072f33e7/cf94152a-e08f-4722-b689-bf60e299bb51.jpg', '2017-05-17 14:14:54.616278');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (288, '周荃', '', '系统管理员', '2015-10-27 00:00:00', '信息工程学院院长陈海荣与电信班本科学生“漫谈我的大学生活”', '10月25日，信息工程学院院长陈海荣博士为电子信息科学与技术（电信班）本科学生作了主题为“漫谈我的大学生活”的报告会。陈院长兴致勃勃地回忆起自己作为一名中专生进入大学后如何适应和规划大学生活？强调了一个积极向上的优秀班集体对自己人生发展的重要性。他详细例举了一系列同班同学在大学期间刻苦学习的感人事迹，以及走上工作岗位后取得的卓越成果。他指出，大学生活是大学生形成世界观、人生观、价值观的重要阶段，希望电信班本科同学要设定好争创一流的具体目标，不畏艰难、互相激励，个人与集体同成长，要以拥有一个坚韧拼搏的优秀班团队为荣。   陈院长要求每一位学生有两个问题应该自己问问：第一到金职院做什么？第二将来毕业后做什么样的人？希望大家要好好规划大学，规划人生。最后，他“成长树”、“职业教育能力图”等生动详实的图片指导大家如何规划自己的大学生活，结合自己的一日时间表激励同学们要珍惜时间，努力促进自己成长。电信班全体本科学生及电子专业教师参加了报告会。师生共同感受到陈院长以自身的成长经历与忧患意识来唤醒师生们共同成长，这次报告会感人至深。  （王成福、余红娟）', 'http://info.jhc.cn/2015/1027/c201a54571/page.htm', 'http://info.jhc.cn/_upload/article/images/97/1c/bf960fd24ebbaa0e1dcc99c854eb/3fe93cb7-e529-4250-b8cb-c8b2df0f06d9.jpg', '2017-05-17 14:14:54.639074');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (289, '周荃', '', '系统管理员', '2015-10-27 00:00:00', '信息学院举办学生最喜欢老师公开课活动', '“为人子，方少时，亲师友，习礼仪。”“上班要不要化妆？”“衬衣要求挺括、整洁、无皱褶。”“站的时候躯干挺直、头部端正、双肩放松。”这是信息学院学生最喜欢老师——张莹老师的公开课。今天上课内容为销售礼仪，参加公开课听课的老师们和学生一起学习了个人礼仪的仪容、仪表和仪态。大家了解了在正式场合如何穿西装，如何选择衬衫、领带、鞋子等；知道了如何训练自己的仪态，站、坐、走如何符合规范。一堂生动风趣、时代感强、贴切现实的公开课徐徐展现在大家面前。   公开课后，多位老师表示张莹老师的课有很多值得大家学习的地方。课前播放音乐调动学生兴趣，热爱学生，上课准备充分，充满自信，对授课内容侃侃而谈，讲解细致到位，PPT图片的选择贴切，教学基本功扎实。 （徐华丹 教科办）', 'http://info.jhc.cn/2015/1027/c201a54570/page.htm', 'http://info.jhc.cn/_upload/article/images/54/5d/0963993a48ee9bd860032be05a1e/12db8752-060f-4897-ac3a-acbe741c6705.jpg', '2017-05-17 14:14:54.66078');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (290, '周荃', '', '系统管理员', '2015-10-21 00:00:00', '信息学院第二十三期党校培训班结业', '10月20日，信息学院党校第二十三期入党积极分子培训班举行结业典礼。据悉，106学员在为期6个月的学习中，积极参加了理论授课、社会公益活动、优秀共产党员影片欣赏、暑期社会实践、撰写结业论文、结业考试等，有66名学员顺利结业，拿到证书。典礼的第二阶段，全体学员进行了“入党积极分子如何提高早晚自习学习效率”的讨论。在场学生们积极的发言，其中有学生说到：“我们应该早起锻炼学习，利用好这比较清醒的最佳时机用来多读书”，针对晚自习，有人答道，“对于大二学生我们可以选择在一个安静的场所看书，比如学校图书馆就是很好的选择，环境是很重要的。” （丰辉 学工办/团委）', 'http://info.jhc.cn/2015/1021/c201a54569/page.htm', NULL, '2017-05-17 14:14:54.683162');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (291, '周荃', '', '系统管理员', '2015-10-20 00:00:00', '信息学院举行2015年离退休教职工重阳节茶话会', '在重阳节即将来临之际，10月20日，我院举行离退休老同志茶话会，四十余位老同志与学院新领导班子欢聚一堂，共庆佳节。   会上，学院党委书记贾美倩给老同志们带去了节日的祝福，并就四年制高职、怀卡托中外合作等办学项目的工作进展给大家做了介绍。她感谢离退休职工为学院的发展和建设所做出的贡献，希望大家继续为学院的各项工作建言献策，并祝大家身体健康，安度晚年。老同志们在会上闲话家常，分享养生经验，讨论社会热点，感谢学院一直以来对大家的关心和照顾，祝愿学院的明天更美好。 （周荃  办公室）', 'http://info.jhc.cn/2015/1020/c201a54568/page.htm', 'http://info.jhc.cn/_upload/article/images/4d/22/f12dff784547b5dcd8d31acf56d2/d489ac4a-a4be-4888-9968-274c61143a11.jpg', '2017-05-17 14:14:54.705381');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (292, '周荃', '', '系统管理员', '2015-10-19 00:00:00', '“寝室5S”今天你了解了吗？', '虽然经常听见老师们把“7S”、“5S”挂在嘴边，但是具体是哪几个S却没有多少同学真正地了解。近日，信息学院新生班级开展了以“寝室5S管理”为主题的班会。在班会中，主持人用PPT向同学们介绍了寝室的四项主要规定。除此之外，主持人还通过展示以往的星级寝室的照片，邀请班级里寝室卫生状况较为良好的同学和大家分享他们的小窍门，鼓励同学们积极把寝室打造成在校园中的小家庭。在互动游戏中，同学们和寝室的“好哥们”、“好姐们”培养了默契，更加深了彼此之间的感情。同学们纷纷表示参加了这次的主题班会后，对如何在寝室快乐地生活有了更深的认识，对未来一个月的宿舍文化节的各项活动也充满了期待。 （徐露诗  学工办/团委）', 'http://info.jhc.cn/2015/1019/c201a54567/page.htm', NULL, '2017-05-17 14:14:54.727433');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (293, '周荃', '', '系统管理员', '2015-10-19 00:00:00', '信息学院承办金华中职网络专业教师省赛选拔赛', '10月17日，2015年金华市中等职业院校教师专业技术技能大赛“计算机网络技术”竞赛在信息学院落下帷幕。来自金华本籍、义乌、东阳、武义、磐安、兰溪、磐安等中职学校的12位教师在通信与网络工程部的网络安全实训室进行了3个小时的角逐。   此次竞赛由金华市教育局主办，旨在检验从事中职计算机网络课程及相关课程教学的在职专任教师的专业技术能力。为浙江省中等职业学校教师技能大赛选拔赛。我院在技术和组织方面为大赛提供了平台。通讯与网络项目部制定了技术文件、试题和搭建竞赛环境，并负责评审工作。学院严密组织竞赛，设有裁判组、技术保障组和服务组，各小组责任分离又相互协作，满足双重加密要求。本次竞赛以实际企业项目为载体，按能力递进设置赛题。分网络搭建、安全管理、渗透与防护三个阶段。综合运用网络安全实训室的虚拟化服务器集群、网络存储等云计算IT架构，既提升了比赛的层次，也锻炼了本校网络专业教师的技术能力。 （周丽丽、厉洁莹 朱墨池）', 'http://info.jhc.cn/2015/1019/c201a54566/page.htm', 'http://info.jhc.cn/_upload/article/images/26/a1/12125828436f8225c33c9e842156/e385621b-e975-4f43-a78d-3200978e3724.jpg', '2017-05-17 14:14:54.749789');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (294, '周荃', '', '系统管理员', '2015-10-19 00:00:00', '信息学院2014-2015学年国家奖学金评选圆满完成', '10月16日，信息学院进行2014/2015学年国家奖学金评选。学院成立了由院领导、学工办及辅导员、学生代表组成的国家奖学金评议小组。根据国家奖学金评审条件，筛选出王志强等7位候选人。评议小组在听取了7位候选人申请陈述的基础上，通过投票、讨论、综合评审，拟定王志强、徐晓晨2位学生为2014/2015学年国家奖学金获得者。评选结束后，党委书记贾美倩指出，2位国家奖学金获得者在学习、竞赛、寝室等方面表现都非常突出，实至名归；此外，落选的5位候选人也要给予他们更多的关注和支持。  （郭天鹏 学工办/团委）', 'http://info.jhc.cn/2015/1019/c201a54565/page.htm', NULL, '2017-05-17 14:14:54.772048');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (295, '周荃', '', '系统管理员', '2015-10-15 00:00:00', '精诚合作、互利共赢---信息学院与义乌市上溪中学培训合作签约仪式顺利举行', '10月13日，信息学院与义乌市上溪中学关于教师信息化培训合作签约仪式在东实北211举行。信息学院院长陈海荣、党委书记贾美倩、上溪中学副校长来运松、两校教师代表出席签约仪式。   仪式上，贾书记表示：要把“移动端课件制作”、“微课设计与制作”等培训项目做强做实。在平等互利、精诚合作的基础上，还要把校校合作模式往深度的、多元化的层次发展，希望与上溪中学成为紧密的、长期的、友好的战略合作伙伴。据悉，本次培训为期1个月，参加培训学员为112人次，课程分6大模块12个专题学习内容，信息学院将派6名骨干教师前往上溪中学传经送宝。 （俞冬丽 继教部）', 'http://info.jhc.cn/2015/1015/c201a54564/page.htm', 'http://info.jhc.cn/_upload/article/images/cf/3b/5c71617b4ae38319d3c9615a9817/4d47efec-0beb-4015-9acf-f942346bce15.jpg', '2017-05-17 14:15:06.550255');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (296, '周荃', '', '系统管理员', '2015-10-10 00:00:00', '“室”界因你而精彩，信息学院举行第八届寝室文化节开幕式', '10月8日下午，信息工程学院在东教216教室举行第八届宿舍文化节开幕式暨2015-2016学年第一次寝室长会议，拉开宿舍文化节序幕，信息学院全体学工人员、寝室长参加开幕式。   会上，用PPT展示学院宿管干部队伍风采、温馨提醒关于公寓居住的四大注意事项，优秀寝室长代表分享了寝室建设经验，并带领全体寝室长宣誓：将以自己的实际行动为身边的同学树立榜样，严格遵守学校宿舍章程制度，履行应有的职责；增强安全意识，提高文明素养，践行八荣八耻，共创文明楼层，共筑和谐公寓，共建温馨校园。   开幕式结束，信息学院党委书记贾美倩结合自己多年公寓管理工作经验对全体寝室长做专项培训指导。据悉，在宿舍文化节期间，还将举办“我就是我不一样的烟火”特色寝室评比、“释放青春·正能量”十佳寝室、十佳寝室长评比、“活力飞扬·青春向上”公寓吉尼斯大赛、 “一日之计在于晨”唤醒活动、“魅力视频·活力信息”创意视频大赛等贴近大学生活的特色活动。                                                                                                     （徐露诗 学工办/团委）', 'http://info.jhc.cn/2015/1010/c201a54563/page.htm', 'http://info.jhc.cn/_upload/article/images/2f/7b/d4a4c78d480cad4b35d85dd46045/c58b69c9-cac4-4790-97cc-8af735b23b37.jpg|http://info.jhc.cn/_upload/article/images/2f/7b/d4a4c78d480cad4b35d85dd46045/55597483-e411-4c62-b5b7-baaa6a9bee3f.jpg', '2017-05-17 14:15:06.574575');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (297, '周荃', '', '系统管理员', '2015-10-10 00:00:00', '信息学院学生部门首次采用情景模拟面试招新', '10月8日下午，东教学楼1-301教室，“咚咚咚”传来几声敲门声，4位学生走了进来说道，“各位商家你们好，我们是金华职业技术学院外联部学生，这次来是向你们推荐一个项目，我们会通过晚会摇一摇、自行车绕校园等形式，帮你们提高知名度，增加微信关注度”。许多路过的学生都忍不住望了进来。仔细打听后，才知道这是信息学院学生会外联部正在进行情景模拟面试。4人一组，辅导员和外联部干部给出情景或目标，给他们20分钟讨论，最后进行模拟面试选拔。据悉，这也是第一次采用这一模式。面试结束后，不少新生认为情景模拟面试比传统的问答式面试更能锻炼人，不仅给他们提供了一个表现机会，而且提升了他们的应变能力。 （郭天鹏 学工办/团委）', 'http://info.jhc.cn/2015/1010/c201a54562/page.htm', NULL, '2017-05-17 14:15:06.596767');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (298, '周荃', '', '系统管理员', '2015-10-08 00:00:00', '信息学院微信公众平台荣登浙江省高校新媒体院系风云榜周排行前三甲', '信息学院微信公众平台荣登浙江省高校新媒体院系风云榜周排行前三甲，首次入围全国高校新媒体院系风云榜前20强 近日，在浙江省团组织/高校新媒体风云榜10月6日榜单中，金职院信息学院团委官方微信平台（微信号：jhcxxxytw）风云指数为564.36 ，在浙江省各大高校的院系官方微信排行榜位列第三名，在全国高校新媒体风云榜（9.27—10.3）中，排名第十一位。这是信息学院自开通微信公众平台以来首次入围全国高校新媒体榜。9月30日，金职院信息团委微信平台上发布了一篇题为《这么多摄影作品，总有一样是你喜欢的》的主题微信，从新生开学季到军训季的时间转换，为了使新生更快的熟悉校园，适应大学生活，我们展开新生活动月之“大学流影，定格金职”的微型摄影大赛，让学生们挖掘校园中独特的人文气息与优美的风景。我们将学生作品展示在微信平台，短短数语的简介道出对大学的憧憬。据悉，该平台以“服务全体师生，展现信息魅力”为发展目标，以“传播正能量、构建和谐校园”为宗旨，紧密围绕社会关注现象，广大师生关心问题，及时有效的推送信息，加强学生思想政治教育。自开通以来，发布了“IT微国学”、“IT美文”、“IT资讯”、“IT访谈——《走进学长学姐的实习生活》”、“IT学子毕业季》等内容，原创采编了如《迎新季：我的大学，我的信息，我来了》、《填志愿之时，你是否看过那年金职院的景》等紧扣校园热点、贴近学生情感诉求的文章，引发了同学们广泛的关注和讨论。采编内容连续6次登上浙江高校新媒体风云榜风云榜。信息学院微记者及时采访，跟踪报导，要求发出的每一条微信精辟入位，全面采纳学院学生的创意，不断摸索和改善，以生动的文字，丰富的图片出现在订阅者的手机上。 （方亮 丰辉）', 'http://info.jhc.cn/2015/1008/c201a54561/page.htm', NULL, '2017-05-17 14:15:06.61896');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (299, '', '', '周荃', '2015-09-30 00:00:00', '我校隆重举行省四年制高职教育人才培养试点——电子信息科学与技术专业首届新生开学典礼', '9月18日下午，我校隆重举行浙江省四年制高职教育人才培养试点——电子信息科学与技术专业（电力电子技术方向）首届新生开学典礼。浙江理工大学副校长陈文兴，院长王振洪，两校相关职能部门及二级学院负责人、50名新生出席了典礼。典礼由党委副书记胡野主持。致辞中，王振洪代表学校首先对新生们的到来表示欢迎，对陈文兴一行的到来表示感谢。他说，我校与浙江理工大学合作举办电子信息科学与技术专业，这是学校的一件大事，更是学校发展史上的一个重要里程碑。王振洪希望同学们一是要珍惜机会，肩负使命，当好高职教育改革和试点的先行者和探路人；二是要尽快融入大学生活。学会自立，融入集体生活；三要尽快适应大学的学习模式，摸索学习方法，合理掌控时间，重视人的全面发展；四是珍惜校园生活，树立正确的人生观、价值观，保持良好心态，积极投身社会实践活动。王振洪最后表示，依托浙江理工大学的学科人才优势和我校二十余年的高职办学特色，两校紧密合作，一定能将四年制高职本科专业办成深受社会认可的品牌专业。相信同学们一定会立志高远、勇于担当、勤于学习、敢于创新，使自己成为一名素质高、能力强、技艺精、受欢迎的优秀大学生。陈文兴代表浙江理工大学对新生们表示欢迎，并简要介绍了理工大学办学历史和发展情况。他说，此次与金职院联合举办电子信息科学与技术专业，将有助于实现高职教育和本科教育的优势互补、理论教学和实践教学的有机结合，双方共享师资、课程、教学设施、校内外实习实训场所等优质教学资源。陈文兴希望同学们牢记浙江理工大学“求是笃实”的校风，秉承“厚德致远，博学敦行”的校训，树立远大目标，身体力行；发扬学校“求是笃实、创新创业“的优良传统，努力学习，成长为基础宽厚、德才兼备、适应性强、具有一定国际视野、较强创新创业精神和实践能力的高级应用型人才。陈文兴最后希望每一位同学都拥有一个充实而精彩的大学生活，期待着同学们明天能为浙江理工大学、金华职业技术学院增光添彩！据悉，为深化教育改革，探索发展本科层次职业教育，加快高端技术技能人才培养，我省于今年上半年开始实施四年制高职教育人才培养试点工作，全省共有20所高职院校参与申报，我校与浙江理工大学合作的电子信息科学与技术专业在激烈的竞争中脱颖而出，成为我省首批五所试点高职院校之一。四年制人才培养，依托高职优质资源、联合本科举办、发放本科文凭、高职院校办学，由两校共同制订培养方案，浙江理工大学负责学生学籍和学位管理，我校负责教学组织管理和学生日常管理，对完成学业并达到毕业相应要求的学生，由浙江理工大学颁发毕业证书和学士学位证书。', 'http://info.jhc.cn/2015/0930/c201a2529/page.htm', NULL, '2017-05-17 14:15:06.643123');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (300, '苏红富', '', '周荃', '2015-09-30 00:00:00', '创新英语课堂教学模式，提升教学有效性', '9月28日下午，杨艳副院长走进信息学院中新合作班教室，体验了一堂生动的英语课堂，对外教的教学方式、学生的积极以及信息学院的教学组织与管理参与给予了高度的评价。下午的课由新西兰的外教Bill 和Angeline讲授，主要教学内容有见面问候与提问、词性分类和阅读技巧等。在见面问候与提问环节中，师生之间、生生之间大声的交流，学生个个开口，十分流畅和自然。词性分类环节中，引导学生找出每种词性的英文解释与实例。阅读技巧中，通过一张火车票来让学生理解什么是“scaning”，通过给短文加标题让学生理解什么是“skimming”。  这几位外教的教学模式，实现“师生互动，生生互动”，课堂因“生”动，才生动！在“生”动中，顺其自然地培养了学生沟通表达能力，团队合作意识等职业核心能力，这是我们教师最应关注的“课程教学有效性。”      另外，信息学院和几位外教在教学组织与管理中也做了大量的工作。在中新班新生英语水平摸底测试中，先是用半天的时间将所有新生集中到东教216教室进行英语听力、阅读和写作能力测试；之后用了一天半的时间，分三间试场由外教与学生一对一交流10分钟，以测试学生口语能力；最后根据笔试与口语成绩的综合将原来5个班168位同学分成了三个层次9个小班。在整个分层考核与教学过程中，外教严谨细致的工作给信息学院的老师留下深刻的印象。此外，为满足小班化教学场地的要求，信息学院早在暑假期间就利用东实训楼5楼改造的时机，建成了3间适合小班化教学的多媒体教室。学院还为每位外教配备一名院内专任教师担任助教，以提升课堂教学组织与管理，同时提升专任教师的英语能力。', 'http://info.jhc.cn/2015/0930/c201a2524/page.htm', 'http://info.jhc.cnhttp://info.jhc.cn/upload/2015esl01.jpg|http://info.jhc.cnhttp://info.jhc.cn/upload/2015esl02.jpg', '2017-05-17 14:15:06.663603');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (301, '陈晓龙、王伟斌', '', '周荃', '2015-09-30 00:00:00', '新技术讲座为学生带来奇妙技术体验', '9月25日，一场别开生面的HTML5新技术讲座，把信息学院计算机、软件等专业的同学带入了一个新技术带来无限可能的新奇世界。来自蓝鸥科技（上海）有限公司的韩明洋讲师，结合自己对HTML5的理解和在阿里巴巴、淘宝等的软件开发工作经验，将当前移动互联网快速发展背景下，HTML5技术在网络标准、多设备跨平台、自适应网页设计、及时更新等方面的强大优势，通过精彩的案例展示，将同学们带到了一个奇妙的IT作品世界，引起了大家的强烈兴趣。现场掌声不断，许多同学表示，此次讲座既学习了新技术，也加深了在国家大力发展“互联网+”的背景下，作为计算机专业学生的使命感，以及今后的专业学习目标，并对专业的发展前景非常乐观。据悉，本次讲座是信息学院校企合作的一项成果。讲座导师所在的蓝鸥科技（上海）有限公司，是目前中国先进的移动互联网研发实训基地，也是国内最具创力的IT教育培训机构之一，其主要致力于iOS、Android和HTML5开发，与目前信息学院软件等专业人才培养技术目标相吻合。为加深校企合作，双方目前正通过新技术讲座、共建实验室、学生实训教学合作、互派教师交流等形式，发挥各自优势，为提升计算机专业群的人才培养质量，塑造高素质的软件开发人才，实现高薪就业的IT技能人才而努力。', 'http://info.jhc.cn/2015/0930/c201a2522/page.htm', NULL, '2017-05-17 14:15:06.685836');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (302, '丰辉', '', '周荃', '2015-09-30 00:00:00', '新生开学第一课——信息学院进行2015级新生始业教育', '9月24日晚，信息学院2015级新生始业教育——新生第一课在实验剧场热力开场。全体新生济济一堂，共同揭开了大学新生活的面纱。    这一“课”，庄严而又亲切，学院以《我眼中的大学生活》为题，给同学们上了生动的一课。学院为新生精心制作视频短片《那些年，伴我走过青春的大学》，短片播放着2014级学长学姐参加军训、IT科技文化节、新生活动月、社团文化节、元旦文艺晚会、技能竞赛获奖等故事，让同学了解即将伴随他们走过的三年大学生活是怎样的，引发学生深深思考该如何过好充实而有意义的大学生活。在物联网151班钱佳红同学的带领下，全院600余名新生，举起右手庄严宣誓，誓言铿锵有力。最后，学院领导、班主任、辅导员老师们上台与同学们互动，用歌声表达对同学们的欢迎与祝福。“这是一堂生动难忘的开学第一课，学长学姐们用不失风趣的语言与片段讲述青春的故事，我们的故事，牵引着在场每位新生的心，这些故事与片段是这样清新，让人豁然开朗。”来宁波的孙宁波如是说。', 'http://info.jhc.cn/2015/0930/c201a2517/page.htm', 'http://info.jhc.cnhttp://info.jhc.cn/upload/20150924shiyeedu.jpg', '2017-05-17 14:15:06.708207');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (303, '何碧峰', '', '周荃', '2015-09-30 00:00:00', '教育伊始，平安先行——记2015级新生安全教育主题班会', '秋冬季节是各类安全隐患的高发期，为切实加大学生安全教育，营造安全、文明、和谐的学习生活环境，加强新生安全意识，近日，由保卫处、信息学院学工办联合举办的“教育伊始，平安先行”安全教育主题班会在学院各新生班级开展。此次安全教育活动形式多样，内容丰富，内涵深刻，取代了以往口说传教式的宣传教育，以多媒体播放相关安全课件和警示片的形式宣传，从学生的日常生活入手，以实际的例子震撼的图片，演绎出学生日常生活中存在的种种安全隐患。会后各班还完成了针对学生防诈骗知识测验题和学习签订《学生安全目标责任书》。据悉，此次安全教育主题旨在加强学生安全防范意识，提高安全的重要性，使安全教育工作深入人心。在接下来的学习生活中，信息学院将采取安全教育课堂、宣传展板、主题班会等多种形式加强安全教育工作。', 'http://info.jhc.cn/2015/0930/c201a2514/page.htm', NULL, '2017-05-17 14:15:06.730242');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (304, '郭天鹏', '', '周荃', '2015-09-30 00:00:00', '信息学院举办“大气污染危害及防治对策”科普讲座', '为提高在校大学生的科普知识，使之成为科普宣传的主力军，9月22日中午，信息学院举办了“大气污染危害及防治对策”科普讲座，讲座由王成福副院长主讲。据悉，这也是金华市科协百场科普讲座之一。    讲座主要围绕我国公民科学素养现状、科普宣传工作的意义、我国大气污染现状及其产生的原因；大气污染的表现形式及其对人体、植物、环境的危害；大气污染的防治对策及预防措施等内容展开。王成福重点阐述了光化学烟雾、温室效应、酸雨、雾霾等大气污染的危害性，让学生自己认识到大气污染的严峻性；熟悉各类大气污染的防治对策和治理技术，懂得一些简单实用的防范措施，号召每一位大学生都应成为科普知识的宣传者。不少学生表示，这次讲座使他们系统了解了大气污染的产生、危害及防治的相关知识，并表示愿意成为一名科普知识的宣传者，为提高全民科学素养多做贡献。', 'http://info.jhc.cn/2015/0930/c201a2510/page.htm', 'http://info.jhc.cnhttp://info.jhc.cn/upload/20150922kepu.jpg', '2017-05-17 14:15:06.752403');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (305, '徐露诗 吕威威', '', '周荃', '2015-09-30 00:00:00', '破冰游戏——记2015级新生第一次班会', '为了迎接2015级新生，让初入校园的小鲜肉们感受到金职院大家庭的温暖，近日，信息工程学院全体新生班级开展了第一次的团辅式班会。在班会上，各位新同学都踊跃发言，不仅拉近了彼此的距离，也为今后三年的相处打下了良好的基础！新生班会上，班主任对新同学的加入表示欢迎，系统介绍了学院发展和专业建设情况，并对同学们大学学习生活寄予了期望。为了更快地打开同学之间的心扉，同学们分组进行了“破冰”游戏，原本陌生的、拘谨的气氛一下子热络了起来，大家笑着闹着，仿佛成为了认识了许久的好朋友。在老师的鼓励下，每个同学都踊跃发言，共同商讨班规，签下了自己的名字，定下了“三年之约”，这是对老师、对班级的承诺，也是对自己的诺言！如果说在班会之前，同学们还是懵懂的高中毕业生，那么破冰之后，大家仿佛都开始进入了“大学生”的角色。2015级新生班会在大家的笑闹声中画上了圆满的句号。', 'http://info.jhc.cn/2015/0930/c201a2505/page.htm', NULL, '2017-05-17 14:15:06.774732');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (312, '周荃', '', '系统管理员', '2015-09-22 00:00:00', '信息学院第十三届新生活动月开幕', '9月17日-9月18日，信息学院大一新生在东实训楼小广场前举行“律动青春，凝心聚力”拔河比赛，全体新生班主任、还有新西兰怀卡托国际学院的老师们也驻足观看，为同学们加油鼓劲，在齐声高喊的口号声中，团队的每一个人都拧成一股绳放手一搏。据悉，此次拔河比赛是信息学院举办的第十三届新生活动月系列活动的一项。   此次新生活动月活动主要包括 “翰墨飘香，艺满信息”书画比赛、“大学流影，定格金职”新生摄影比赛、“提升英语素养,展示英语魅力”新生英语美文朗诵大赛等丰富多彩的活动，以便让新同学早日融入新集体，凝聚班级力量，尽早适应并规划好大学生涯。 （丰辉 学工办/团委）', 'http://info.jhc.cn/2015/0922/c201a54554/page.htm', 'http://info.jhc.cn/_upload/article/images/88/5b/cdcd0f5543caab67386a37c63980/8b71e202-3446-4469-9083-db3a026c8b1b.jpg', '2017-05-17 14:15:18.697927');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (306, '周荃', '', '系统管理员', '2015-09-29 00:00:00', '我院“E•就通”新媒实践队荣获金华市第三届“青春行动”“优秀团队”', '近期，金华市第三届“青春行动”总决赛在金华广播电视总台拉开帷幕。我院“E·就通”新媒实践队荣获“优秀团队”荣誉称号，是我院第一次在该项目上获奖。信息学院“E·就通”新媒实践队旨在科普新媒体、推进农村信息化建设。2015年7月学主要围绕新媒体知识宣传、信息调研、专题讲座等在金华市婺城区黄堂村开展活动，并利用专业所学为村民开展讲座，让农村民众了解新媒体，认识新媒体，感知农村信息化建设的步伐。活动期间学生为53名村民实现了“微心愿”，并利用图书义卖所得为一位村民送上了一部智能手机。   据悉学院1200名学生参加了“长辈教我做人做事，我教长辈微信电商”主题社会实践活动。他们利用所学的专业知识，到各个社区、农村调研、授课，让长辈们更好地融入新媒体时代。  （丰辉 学工办/团委）', 'http://info.jhc.cn/2015/0929/c201a54560/page.htm', 'http://info.jhc.cn/_upload/article/images/02/32/f9312dd2453c9e9fe7402bf1ddef/0ec79d2b-3b7e-41e8-8a96-4b0f7248f499.jpg', '2017-05-17 14:15:06.796952');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (307, '周荃', '', '系统管理员', '2015-09-29 00:00:00', '创新英语课堂教学模式，提升教学有效性', '9月28日下午，杨艳副院长走进信息学院中新合作班教室，体验了一堂生动的英语课堂，对外教的教学方式、学生的积极以及信息学院的教学组织与管理参与给予了高度的评价。下午的课由新西兰的外教Bill 和Angeline讲授，主要教学内容有见面问候与提问、词性分类和阅读技巧等。在见面问候与提问环节中，师生之间、生生之间大声的交流，学生个个开口，十分流畅和自然。词性分类环节中，引导学生找出每种词性的英文解释与实例。阅读技巧中，通过一张火车票来让学生理解什么是“scaning”，通过给短文加标题让学生理解什么是“skimming”。   这几位外教的教学模式，实现“师生互动，生生互动”，课堂因“生”动，才生动！在“生”动中，顺其自然地培养了学生沟通表达能力，团队合作意识等职业核心能力，这是我们教师最应关注的“课程教学有效性。”    另外，信息学院和几位外教在教学组织与管理中也做了大量的工作。在中新班新生英语水平摸底测试中，先是用半天的时间将所有新生集中到东教216教室进行英语听力、阅读和写作能力测试；之后用了一天半的时间，分三间试场由外教与学生一对一交流10分钟，以测试学生口语能力；最后根据笔试与口语成绩的综合将原来5个班168位同学分成了三个层次9个小班。在整个分层考核与教学过程中，外教严谨细致的工作给信息学院的老师留下深刻的印象。此外，为满足小班化教学场地的要求，信息学院早在暑假期间就利用东实训楼5楼改造的时机，建成了3间适合小班化教学的多媒体教室。学院还为每位外教配备一名院内专任教师担任助教，以提升课堂教学组织与管理，同时提升专任教师的英语能力。 （苏红富）', 'http://info.jhc.cn/2015/0929/c201a54559/page.htm', 'http://info.jhc.cn/_upload/article/images/d8/ff/cce02f4f4495a7762a70fc8b86d2/a82ac8f8-5043-4c86-89a8-15369eaaa0d2.jpg|http://info.jhc.cn/_upload/article/images/d8/ff/cce02f4f4495a7762a70fc8b86d2/e76f7fb3-5aa8-433d-85fe-9e0ce02e61a9.jpg', '2017-05-17 14:15:06.819103');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (308, '周荃', '', '系统管理员', '2015-09-29 00:00:00', '新技术讲座为学生带来奇妙技术体验', '9月25日，一场别开生面的HTML5新技术讲座，把信息学院计算机、软件等专业的同学带入了一个新技术带来无限可能的新奇世界。来自蓝鸥科技（上海）有限公司的韩明洋讲师，结合自己对HTML5的理解和在阿里巴巴、淘宝等的软件开发工作经验，将当前移动互联网快速发展背景下，HTML5技术在网络标准、多设备跨平台、自适应网页设计、及时更新等方面的强大优势，通过精彩的案例展示，将同学们带到了一个奇妙的IT作品世界，引起了大家的强烈兴趣。现场掌声不断，许多同学表示，此次讲座既学习了新技术，也加深了在国家大力发展“互联网+”的背景下，作为计算机专业学生的使命感，以及今后的专业学习目标，并对专业的发展前景非常乐观。据悉，本次讲座是信息学院校企合作的一项成果。讲座导师所在的蓝鸥科技（上海）有限公司，是目前中国先进的移动互联网研发实训基地，也是国内最具创力的IT教育培训机构之一，其主要致力于iOS、Android和HTML5开发，与目前信息学院软件等专业人才培养技术目标相吻合。为加深校企合作，双方目前正通过新技术讲座、共建实验室、学生实训教学合作、互派教师交流等形式，发挥各自优势，为提升计算机专业群的人才培养质量，塑造高素质的软件开发人才，实现高薪就业的IT技能人才而努力。 （陈晓龙、王伟斌 信息化事业部）', 'http://info.jhc.cn/2015/0929/c201a54558/page.htm', NULL, '2017-05-17 14:15:06.841588');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (309, '周荃', '', '系统管理员', '2015-09-28 00:00:00', '新生开学第一课——信息学院进行2015级新生始业教育', '9月24日晚，信息学院2015级新生始业教育——新生第一课在实验剧场热力开场。全体新生济济一堂，共同揭开了大学新生活的面纱。   这一“课”，庄严而又亲切，学院以《我眼中的大学生活》为题，给同学们上了生动的一课。学院为新生精心制作视频短片《那些年，伴我走过青春的大学》，短片播放着2014级学长学姐参加军训、IT科技文化节、新生活动月、社团文化节、元旦文艺晚会、技能竞赛获奖等故事，让同学了解即将伴随他们走过的三年大学生活是怎样的，引发学生深深思考该如何过好充实而有意义的大学生活。在物联网151班钱佳红同学的带领下，全院600余名新生，举起右手庄严宣誓，誓言铿锵有力。最后，学院领导、班主任、辅导员老师们上台与同学们互动，用歌声表达对同学们的欢迎与祝福。“这是一堂生动难忘的开学第一课，学长学姐们用不失风趣的语言与片段讲述青春的故事，我们的故事，牵引着在场每位新生的心，这些故事与片段是这样清新，让人豁然开朗。”来宁波的孙宁波如是说。  （丰辉 学工办/团委）', 'http://info.jhc.cn/2015/0928/c201a54557/page.htm', 'http://info.jhc.cn/_upload/article/images/dd/0d/196541e84c3595f955c5c8487ef4/4cb3ad5c-218f-4feb-836d-51ed7d8a0a29.jpg', '2017-05-17 14:15:18.626643');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (310, '周荃', '', '系统管理员', '2015-09-24 00:00:00', '教育伊始，平安先行——记2015级新生安全教育主题班会', '秋冬季节是各类安全隐患的高发期，为切实加大学生安全教育，营造安全、文明、和谐的学习生活环境，加强新生安全意识，近日，由保卫处、信息学院学工办联合举办的“教育伊始，平安先行”安全教育主题班会在学院各新生班级开展。此次安全教育活动形式多样，内容丰富，内涵深刻，取代了以往口说传教式的宣传教育，以多媒体播放相关安全课件和警示片的形式宣传，从学生的日常生活入手，以实际的例子震撼的图片，演绎出学生日常生活中存在的种种安全隐患。会后各班还完成了针对学生防诈骗知识测验题和学习签订《学生安全目标责任书》。据悉，此次安全教育主题旨在加强学生安全防范意识，提高安全的重要性，使安全教育工作深入人心。在接下来的学习生活中，信息学院将采取安全教育课堂、宣传展板、主题班会等多种形式加强安全教育工作。 （何碧峰 学工办/团委）', 'http://info.jhc.cn/2015/0924/c201a54556/page.htm', NULL, '2017-05-17 14:15:18.653374');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (311, '周荃', '', '系统管理员', '2015-09-23 00:00:00', '信息学院举办“大气污染危害及防治对策”科普讲座', '为提高在校大学生的科普知识，使之成为科普宣传的主力军，9月22日中午，信息学院举办了“大气污染危害及防治对策”科普讲座，讲座由王成福副院长主讲。据悉，这也是金华市科协百场科普讲座之一。   讲座主要围绕我国公民科学素养现状、科普宣传工作的意义、我国大气污染现状及其产生的原因；大气污染的表现形式及其对人体、植物、环境的危害；大气污染的防治对策及预防措施等内容展开。王成福重点阐述了光化学烟雾、温室效应、酸雨、雾霾等大气污染的危害性，让学生自己认识到大气污染的严峻性；熟悉各类大气污染的防治对策和治理技术，懂得一些简单实用的防范措施，号召每一位大学生都应成为科普知识的宣传者。不少学生表示，这次讲座使他们系统了解了大气污染的产生、危害及防治的相关知识，并表示愿意成为一名科普知识的宣传者，为提高全民科学素养多做贡献。 （郭天鹏 学工办/团委）', 'http://info.jhc.cn/2015/0923/c201a54555/page.htm', 'http://info.jhc.cn/_upload/article/images/88/97/c61ff6674884be40feb8d79ecd67/e21fa768-0195-4b80-b870-7d3eb45ee8cb.jpg', '2017-05-17 14:15:18.675485');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (313, '周荃', '', '系统管理员', '2015-09-22 00:00:00', '破冰游戏——记2015级新生第一次班会', '为了迎接2015级新生，让初入校园的小鲜肉们感受到金职院大家庭的温暖，近日，信息工程学院全体新生班级开展了第一次的团辅式班会。在班会上，各位新同学都踊跃发言，不仅拉近了彼此的距离，也为今后三年的相处打下了良好的基础！新生班会上，班主任对新同学的加入表示欢迎，系统介绍了学院发展和专业建设情况，并对同学们大学学习生活寄予了期望。为了更快地打开同学之间的心扉，同学们分组进行了“破冰”游戏，原本陌生的、拘谨的气氛一下子热络了起来，大家笑着闹着，仿佛成为了认识了许久的好朋友。在老师的鼓励下，每个同学都踊跃发言，共同商讨班规，签下了自己的名字，定下了“三年之约”，这是对老师、对班级的承诺，也是对自己的诺言！如果说在班会之前，同学们还是懵懂的高中毕业生，那么破冰之后，大家仿佛都开始进入了“大学生”的角色。2015级新生班会在大家的笑闹声中画上了圆满的句号。 （徐露诗 吕威威  学工办/团委）', 'http://info.jhc.cn/2015/0922/c201a54553/page.htm', NULL, '2017-05-17 14:15:18.72004');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (314, '周荃', '', '系统管理员', '2015-09-18 00:00:00', '我校隆重举行省四年制高职教育人才培养试点——电子信息科学与技术专业首届新生开学典礼', '9月18日下午，我校隆重举行浙江省四年制高职教育人才培养试点——电子信息科学与技术专业（电力电子技术方向）首届新生开学典礼。浙江理工大学副校长陈文兴，院长王振洪，两校相关职能部门及二级学院负责人、50名新生出席了典礼。典礼由党委副书记胡野主持。致辞中，王振洪代表学校首先对新生们的到来表示欢迎，对陈文兴一行的到来表示感谢。他说，我校与浙江理工大学合作举办电子信息科学与技术专业，这是学校的一件大事，更是学校发展史上的一个重要里程碑。王振洪希望同学们一是要珍惜机会，肩负使命，当好高职教育改革和试点的先行者和探路人；二是要尽快融入大学生活。学会自立，融入集体生活；三要尽快适应大学的学习模式，摸索学习方法，合理掌控时间，重视人的全面发展；四是珍惜校园生活，树立正确的人生观、价值观，保持良好心态，积极投身社会实践活动。王振洪最后表示，依托浙江理工大学的学科人才优势和我校二十余年的高职办学特色，两校紧密合作，一定能将四年制高职本科专业办成深受社会认可的品牌专业。相信同学们一定会立志高远、勇于担当、勤于学习、敢于创新，使自己成为一名素质高、能力强、技艺精、受欢迎的优秀大学生。陈文兴代表浙江理工大学对新生们表示欢迎，并简要介绍了理工大学办学历史和发展情况。他说，此次与金职院联合举办电子信息科学与技术专业，将有助于实现高职教育和本科教育的优势互补、理论教学和实践教学的有机结合，双方共享师资、课程、教学设施、校内外实习实训场所等优质教学资源。陈文兴希望同学们牢记浙江理工大学“求是笃实”的校风，秉承“厚德致远，博学敦行”的校训，树立远大目标，身体力行；发扬学校“求是笃实、创新创业“的优良传统，努力学习，成长为基础宽厚、德才兼备、适应性强、具有一定国际视野、较强创新创业精神和实践能力的高级应用型人才。陈文兴最后希望每一位同学都拥有一个充实而精彩的大学生活，期待着同学们明天能为浙江理工大学、金华职业技术学院增光添彩！据悉，为深化教育改革，探索发展本科层次职业教育，加快高端技术技能人才培养，我省于今年上半年开始实施四年制高职教育人才培养试点工作，全省共有20所高职院校参与申报，我校与浙江理工大学合作的电子信息科学与技术专业在激烈的竞争中脱颖而出，成为我省首批五所试点高职院校之一。四年制人才培养，依托高职优质资源、联合本科举办、发放本科文凭、高职院校办学，由两校共同制订培养方案，浙江理工大学负责学生学籍和学位管理，我校负责教学组织管理和学生日常管理，对完成学业并达到毕业相应要求的学生，由浙江理工大学颁发毕业证书和学士学位证书。', 'http://info.jhc.cn/2015/0918/c201a54552/page.htm', NULL, '2017-05-17 14:15:18.742319');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (315, '周荃', '', '系统管理员', '2015-09-18 00:00:00', '我校隆重举行金职院怀卡托国际学院2015级新生开学典礼', '经过近两年时间的筹备，我校与新西兰怀卡托理工学院合作创办的非独立法人国际学院——金职院怀卡托国际学院终于迎来首批学生。9月18日上午，我校隆重举行金职院怀卡托国际学院2015级新生开学典礼。新西兰教育部代表、大中华区高级教育总监邓思远，怀卡托理工学院校长马克、副校长大卫；院长王振洪、党委副书记胡野出席开学典礼；两校相关职能部门、信息学院负责人、外籍教师、全体新生参加了典礼。典礼由副院长、怀卡托国际学院院长杨艳主持。   王振洪首先致欢迎辞，对邓思远、马克一行的到来表示欢迎。他说，我校历来高度重视教育国际化战略，积极拓展国际合作项目，提高地方高等职业教育的质量和国际化水平。此次中新合作创办怀卡托国际学院并迎来首批新生，学校将全力以赴支持和保证该项目的顺利实施，努力将它打造成为我校中外合作办学项目的典范。王振洪希望同学们尽快融入大学生活，学会自立、学会宽容、学会团队合作；尽快适应教学模式，自主学习，多与教师沟通交流，重视提高英语水平，为今后的学习深造做好准备；珍惜校园生活，养成良好的身心素质，在成长中树立切实可行的阶段性目标并为之拼搏。王振洪最后表示，依托金职院的优良学风和良好的教育环境，以及新西兰怀卡托理工学院的教育管理，一定能将双方的优质教育资源转换成中外合作办学的优势，将同学们培养成具有国际竞争力的人才。马克代表新西兰怀卡托理工学院对与我校开展合作表示感谢，并对首批新生的到来表示欢迎。他说，科技改变了我们的工作和生活，改变了国际间的交流与合作，世界各国在交流中互相学习、互相进步。马克希望同学们通过此次两校职业教育合作，学习到两个不同国家、学校的不同教学理念和方法，从而成为具有国际视野和国际能力的全能型国际人才；希望通过与金职院的精诚协作，推动两校的教育事业再上新的台阶，并为金华市与怀卡托市、浙江省与汉密尔顿市搭建起友谊合作的桥梁；希望金职院、怀卡托理工学院以及学生之间能密切联系，让同学们真切地感受到新中优质高职教育带来的支持和帮助，让同学们的未来大放异彩。最后，马克衷心的祝福并期待大家学有所成，学有所获。随后，王振洪与马克共同为金职院怀卡托国际学院揭牌，师生代表先后上台发言。   典礼开始前，金职院怀卡托国际学院召开第二届理事扩大会议。杨艳主持会议并介绍了第二届理事会筹备会议纪要内容。中新双方围绕“3+1”专升本合作项目、专业课程调整、赴新交流学生激励措施、建筑技术专业合作等进行了充分的讨论和协商，并初步达成共识。据悉，我校与新方项目合作期为五届，前期办学规模为900人，首批新生共计176名，其中，应用电子技术专业学生40名、计算机网络技术专业76名、计算机应用技术专业60名。学生考核成绩合格后，可获得由我校颁发的普通高等专科（高职）毕业文凭和新西兰怀卡托理工学院的信息通信技术文凭（六级）。我校将为国际学院每个专业的10名同学提供2万元的奖学金；新方也将每年为国际学院每个专业的2名同学提供赴新西兰留学一年的全额奖学金。', 'http://info.jhc.cn/2015/0918/c201a54551/page.htm', 'http://info.jhc.cn/_upload/article/images/60/f4/d8dac4f240be8edced21e0f88e94/1191d9ac-8056-432d-9a1c-ddd23f3aa03f.jpg|http://info.jhc.cn/_upload/article/images/60/f4/d8dac4f240be8edced21e0f88e94/e0d7ecdc-76ba-4c0a-85fa-54d275a2d046.jpg', '2017-05-17 14:15:18.844288');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (316, '周荃', '', '系统管理员', '2015-09-11 00:00:00', '信息学院将与天格信息公司组建“订单班”', '近日，信息学院信息化事业部部分师生在王成福副院长带领下，走进浙江天格信息技术有限公司，就组建校企合作“订单班”、在线教育模式及高层次科研项目合作等事宜进行洽谈，初步达成共识。在此次走访中，校企双方进一步探讨了根据金华信息技术行业发展状况，双方共同探求利益切入点，发挥浙江天格信息技术有限公司作为中国最大网络娱乐社区、网络文化活动公司的技术优势，成立主要面向培养移动终端应用开发的“9158订单班”，形成由行业参与指导、学校投入建设经费和场地、企业提供设备及技术支持的“行校企”共同合作的订单班模式。此外，双方还讨论了如何将视讯互动社区引入高职课程教学以及浙江科技厅等高层次项目申报等事宜，希望充分利用学校师资力量，建立校企深度合作、紧密结合，优势互补、共同发展的合作机制，达到“共赢”目的，提升高职课程教学的互动性、趣味性、有效性。   据悉，浙江天格信息技术有限公司创立于2005年，是中国最大网络娱乐社区、网络文化活动、专业平台策划公司。目前，旗下网站有聚乐、新浪秀以及99lover等一系列视讯互动社区，总员工近6000人，其中研发1500人以上，分布北京、上海、杭州等重点城市地区。 （陈晓龙、王伟斌 信息化事业部）', 'http://info.jhc.cn/2015/0911/c201a54550/page.htm', 'http://info.jhc.cn/_upload/article/images/09/a6/e64797384733aefcf0befe0c1f05/34adf61c-8caf-4c0e-9a96-d70725214c39.jpg', '2017-05-17 14:15:18.864531');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (317, '周荃', '', '系统管理员', '2015-09-10 00:00:00', '中外寿星共度中国味儿“双节”', '9月10日，信息学院阅览室热闹非凡。恰逢第31个教师节，学院在今天为9月份生日的十多位教职工举办集体生日会暨教师节座谈会。刚刚入职的2位新西兰英语外教也是9月份的寿星，他们也应邀加入，体验了在信息学院大家庭的第一次生日聚会。   生日会有了外教的加入变得更加精彩，外教Bill在三位外教中稍年长，他说自己担任英语教师多年，初次到金华短短两天的时间感受到了大家无限的热情与真诚，还在这里共度中国范儿的教师节和生日会，让他很难忘，也会很珍惜这段共事的时光。很多老师表示，自己在家都未必过生日，学院如此贴心的准备，让他们感受到了身在信息大家庭的温暖，学院有了外教的加入，今后也要把握机会，把英语大声说出来。寿星们在生日蛋糕上插上蜡烛，共同许愿，共唱生日歌。院领导为寿星们分发蛋糕，并派发了贴心小礼品。   据悉，学院自2015年开始每月为当月生日的老师们举办生日会，暑期进一步完善了办公区域、实训场所的布局改造，从多渠道加强人文关怀，努力为全院教职工营造和谐舒适的工作氛围。  （周荃  办公室）', 'http://info.jhc.cn/2015/0910/c201a54549/page.htm', 'http://info.jhc.cn/_upload/article/images/6f/6f/153035d74dd09ca6d9897e8c3a55/3978979b-f3be-43b0-856a-6a2e80b4fde7.jpg|http://info.jhc.cn/_upload/article/images/6f/6f/153035d74dd09ca6d9897e8c3a55/e5227931-8399-421b-9619-4dae42e0133c.jpg', '2017-05-17 14:15:18.886832');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (318, '周荃', '', '系统管理员', '2015-09-10 00:00:00', '信息学院召开首届中新合作专业ESP课程教学研讨会', '9月9日，金职院怀卡托国际学院首届中新合作专业ESP课程教学研讨会在信息学院召开。信息工程学院领导、新西兰怀卡托理工学院国际事务人事处处长黛博拉，中国项目负责人刘马克、新方首批ESP课程教师、中方专业负责人及助教参加研讨。会上，中新双方重点围绕新生入学后ESP课程分层考试的内容、形式、场地与时间安排，Moodle网络教学平台英文版的完善与运行等环节进行了深入洽谈与布置。同时，与会人员现场观摩ESP课程专用教室与外教办公室，讨论并明确了教学办公场所的布局完善、教仪配备、网络环境与文化建设等具体措施。为保障ESP课程教学质量，中新双方高度重视，不惜增加办学成本，在校内实训环境紧张的情况下，信息学院暑期加班加点，从原专业实训室中分离并重新装修3间ESP课程专用教室；同时，将原5个自然班170余人学生分成9个小班，实施小班化教学，为首批中新合作班的外教第一课营造环境温馨、秩序井然的教学氛围。   据悉，ESP（English for Specific Purposes）——专业用途英语课程是首届中新合作专业引进的第1门外教授课课程。该课程总计488学时，历时4个学期，比非中外合作班级的英语课程多出400多学时。中新合作班ESP课程研讨会中系列措施的实施，将有效促进应用电子技术、计算机应用技术、计算机网络技术专业首届中新合作班英语应用水平的提高，推进后续引进课程和金职院怀卡托国际学院后续项目的顺利实施。 （宣翠仙 信息化事业部）', 'http://info.jhc.cn/2015/0910/c201a54548/page.htm', 'http://info.jhc.cn/_upload/article/images/5c/27/98e68ccc416f837f47f7e9849fea/3540e96e-357d-4fed-a5a7-672b3240c05c.jpg', '2017-05-17 14:15:18.909059');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (319, '周荃', '', '系统管理员', '2015-09-10 00:00:00', '信息学院举行新兵入伍欢送会', '近日，信息学院举行了新兵入伍欢送会。信息学院继续教育部老师、班主任、入伍新兵、学生代表参加大会。欢送会上，校学生处副处长王芳对今年应征入伍的2名学生表示祝贺，为新入伍的大学生佩戴了大红花，并向他们赠送了纪念品。计算机Z1501班班主任勉励应征入伍的大学生，牢记责任，把参军入伍作为自己锻炼提高、施展才能、实现梦想的实践舞台。入伍学生谈了自己应征入伍的感受，他们既表达了对学校和老师同学的依依不舍，又郑重地表示一定会履行义务，坚守信念，用自己的热血青春和实际行动为祖国的国防事业奉献力量。   据悉，2015年金华职业技术学院在校学生有24人报名参军，最后应征入伍的学生共5名，其中2人来自信息学院计算机Z1501班。 （俞冬丽 继续教育部）', 'http://info.jhc.cn/2015/0910/c201a54547/page.htm', 'http://info.jhc.cn/_upload/article/images/96/05/9ca0c1334de7afe4627cd05bf559/0fa1c52c-6bd0-4086-9602-97cf77f7fe76.jpg', '2017-05-17 14:15:18.931209');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (320, '周荃', '', '系统管理员', '2015-09-10 00:00:00', '信息工程学院举行2015级新生班主任助理聘任仪式', '9月8日晚，学院举行2015级新生班主任助理聘任仪式暨培训会，学工办主任林雪华为42名新生班主任助理颁发了聘任证书。继续教育部主任俞冬丽、学工办全体辅导员参加会议。培训会上，对新生班主任助理提了几点要求：一是要发挥榜样作用，用自身的实际行动为新生树立学习和生活标杆；二是要明确工作职责，既要协助新生班主任做好相关工作，又不能越俎代庖，事事包办；三是要充分熟悉校园环境和学院师资力量、专业课程设置等，为引导好2015级新生更快更好得适应大学生活提前备好课。同时也就学历提升途径作了介绍。上一届优秀新生班主任助理在会上介绍了自己的工作心得与体会，把积累下来的经验和做法与大家分享。据悉，学院根据21个新生班级的实际需求，在2013、2014级优秀学生中通过自我推荐，班主任、党支部推荐，公开选拔面试，确定了2015级新生班主任助理名单。学工办还对新生班主任助理工作职责、考核奖惩、新生接待、始业教育等工作作了详细的介绍与布置。  （李飞 学工办/团委）', 'http://info.jhc.cn/2015/0910/c201a54546/page.htm', NULL, '2017-05-17 14:15:18.953781');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (321, '周荃', '', '系统管理员', '2015-09-09 00:00:00', '“打扫卫生，党员先行”——信息学院计算机支部开展主题实践活动', '近日，在东实训楼5楼，有一支特殊的队伍吸引了人们的注意，他们有的拿着扫帚扫地，有的握着拖把拖地板，有的拎着水桶擦玻璃，个个挥汗如雨，忙得不亦乐乎。这是信息工程学院计算机专业党支部师生党员和入党积极分子，正在进行的“打扫卫生，党员先行”的实践活动。                   为切实发挥党员的先锋模范作用，让全体师生有一个干净整洁的实训环境，在学院党委的指导下，计算机专业的师生党员和入党积极分子，利用课余时间走进实训楼义务劳动。打扫完后，看着自己的劳动成果，每个参与者都露出了会心的微笑。他们的举动，也受到了学院老师和同学们的一致好评。活动不仅使党员体验了生活，感受到了保洁员群体工作的不容易；也加深了支部成员间的沟通和交流，增强了党员意识，提高党性修养。长期以来，计算机专业支部积极通过多种形式的义务劳动来丰富党组织生活，鼓励学生党员从身边小事做起，时刻树立“为人民服务”意识，体现党员的先进性。  （ 文  陈晓龙、王伟斌）', 'http://info.jhc.cn/2015/0909/c201a54545/page.htm', 'http://info.jhc.cn/_upload/article/images/c0/3f/418c246047d1be4bb87b05e058b2/923aa9da-be1e-436b-99a0-cd24a6fd1ed3.jpg', '2017-05-17 14:15:18.975768');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (322, '周荃', '', '系统管理员', '2015-09-09 00:00:00', '成教新生开学第一课先学“规矩”', '9月6日，信息学院迎来了126名成教新生。在院党委书记贾美倩的指导下，继续教育部给成教新生上开学第一课。    课程包括安全教育、专业教育、适应教育、心理健康教育、纪律教育、党团建设等多项“规矩”。开学第一课，“规矩”虽多但课堂气氛活跃，到课率100%。课后还设置互动环节，通过和班主任、专业主任、学工线老师、老生代表的交流沟通，成教新生对学校有了全面的认识和了解。开学第一课，为广大新老师生提供了一个交流的平台，有助于新生更快、更好地的了解适应大学生活，明确大学学习目标，同时也为接下来各项新生工作的展开打下了基础。 （俞冬丽 继教部）', 'http://info.jhc.cn/2015/0909/c201a54544/page.htm', 'http://info.jhc.cn/_upload/article/images/2f/3d/8b34a9314242b759ab7e1da3f84e/8e0a47c7-c947-4061-a3f5-1f924aa6cdfa.jpg', '2017-05-17 14:15:18.998036');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (323, '周荃', '', '系统管理员', '2015-09-08 00:00:00', '信息学院13级优化方向学子在暑期专业实习中获企业表扬', '新学期伊始，我院收到来自浙江八方电信有限公司的表扬信，表扬我院5位学生在该公司暑期专业实习中圆满完成了项目任务。参加本次实习的同学均为信息学院13级优化方向的学生，分别来自网络131班和通信131班，是我院通信与网络工程部基于“宽基础、分方向”人才培养模式实践的第一届学生。5位同学实习岗位为浙江金华移动3G、4G替换工程项目华为工程督导。主要工作职责为：基站的站点安装、开通、调试及集成和测试工作；配合BSC&RNC工程师完成基站相关操作；处理日常基站故障；负责基站硬件施工的督导工作等。同学们虚心求教、努力学习，在不到2个月的时间里，初步掌握了无线通信工程的流程和规范，逐步具备了基站设备安装调测技能，很好的完成了项目主管安排的各项工作任务，得到了项目主管、公司领导及客户多方的一致好评。特别是王斌鑫、泮欣欣、杜添三位同学，表现更加出色，起到了积极的表率作用。据悉，浙江八方电信有限公司是一家集通信器材设备、软件产品的研发、生产、销售及电信工程设备安装、施工为一体的集团型国家级高新技术企业。下设通信器材、铁塔工程、电信工程和浙江安迪信信息技术有限公司四个分子公司。2004年7月23日，八方电信在新加坡主板市场成功上市，成为浙江省首家在新加坡上市的高科技企业。公司现有百余项国家专利，并具有国家电信工程专业承包壹级资质、通信信息网络系统集成乙级资质，是目前国内发展较迅速的通信网络设备及解决方案供应商之一。 （苏红富 通信与网络工程部）', 'http://info.jhc.cn/2015/0908/c201a54543/page.htm', NULL, '2017-05-17 14:15:32.270949');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (324, '周荃', '', '系统管理员', '2015-09-07 00:00:00', '学院领导夜访学生公寓看望学生', '9月7日是2016级成教新生报到、老生回校日，当晚学院党委书记贾美倩带领学院办公室、学工办、继教部负责人及有关班主任、辅导员、值班教师走访学生公寓，看望学生，欢迎成教2016级新生加入信息学院大家庭。在桃李8、9号楼及学士8号楼，贾书记带领大家走访了每一个成教新生寝室，与新生们亲切交谈，了解同学们入住寝室相关情况，希望新生们珍惜三年时光，养成良学习好习惯，快乐无悔地度过自己的大学生活。同学们对学院的关心表示感谢，同时表示会认真学习，不辜负学校、家长对他们的期望。在龙川北04、鹿田等公寓，贾书记一行与回校学习的老生们亲切交谈，了解老生们的假期生活，督促同学们回归学习状态，保持良好的学习氛围和生活习惯。开学第一天，返校及新报到学生纷纷忙于打扫寝室卫生，公寓楼内整体状况良好。  （陈德仙 办公室）', 'http://info.jhc.cn/2015/0907/c201a54542/page.htm', NULL, '2017-05-17 14:15:32.298672');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (325, '周荃', '', '系统管理员', '2015-09-06 00:00:00', '因材施教 突出核心能力培养——学校召开四年制高职本科通识课程教学研讨会', '9月6日，学校召开四年制高职电子信息科学与技术（电力电子技术方向）专业通识课程教学研讨会，教务处处长、公共基础学院副院长、信息工程学院班子成员和全体通识课程教师出席了会议。   会上，信息学院负责人介绍了四年制高职本科专业的教学准备、录取的50名本科生源、人才培养方案制定等情况。学校教务处就四年制高职本科专业人才培养要求广大教师做到因材施教，积极探索现代职业教育改革，突出学生一技之长，重视学生核心能力培养，提升专业内涵，做强高职本科试点专业。全体教师围绕四年制高职本科专业所开设的《职业规划》、《心理健康教育》、《英语》、《高等数学》、《普通物理》、《大学语文》、《高校“两课”》、《体育》等通识课程，并对这些课程如何针对中职学生的知识基础进行教学项目的选取、难易度与教学进度的把握、现代教育技术的应用、理论教学与实践教学的衔接以及课程考核评价方式等方面进行了研讨，为下一步如何针对中职生源因材施教，突出个人职业发展规划、信息获取、主动学习、与人交往、写作表达、团队协作等核心能力的培养奠定了良好的开端。本次高职四年制本科通识课程教学研讨会的召开，充分发挥学校学科综合、优秀教师集聚、各部门通力合作的优势，并且明确了课程建设立项计划与课程教学研讨方式，为下一步其它课程的教学起到引领带头作用，为探索四年制高职本科专业人才培养打下了坚实的基础。 （文 王成福 摄 周荃）', 'http://info.jhc.cn/2015/0906/c201a54541/page.htm', 'http://info.jhc.cn/_upload/article/images/52/53/d4866bb24058a5d10ed556172395/d6f90c8e-233d-40f0-8a5e-5e09ea6f367f.jpg', '2017-05-17 14:15:32.321082');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (326, '周荃', '', '系统管理员', '2015-08-29 00:00:00', '学校举办“现代和谐职业人与高职文化素质教育”学术报告', '8月28日下午，浙江经济职业技术学院党委副书记、全球最大的国际人才发展协会(ATD)特邀演讲专家、浙江物产管理学院常务副院长邵庆祥教授应邀来我校作了题为“现代和谐职业人与高职文化素质教育”的学术报告。信息学院、机电学院、金义学院相关人员聆听了报告。    在网络全覆盖的时代，面对兴趣多变的学生，怎样击中他们的“尖叫点”，实施有效的课堂教学？邵庆祥结合自身教研经验，围绕主题，透过大环境发展的视角，深入阐述了育人规格的定位、人才培养的目标、学校教育和教师在互联网背景下的发展方向。邵庆祥认为，“人人为师，人人为学”将是未来教育的发展趋势，因此学校功能和教师角色需要重新进行定义。学校要成为学生共享创意的文化场所，提供个性化的学习方案、开放式的交流平台、情景化的默会知识学习、艺术化的人文氛围体验。而教师则要成为人文关怀的精神导师，比拼更多的是人格魅力，能否设计并应用好智能化教学场景，通过现代技术手段激发学生身上的正能量。同时，邵庆祥指出，高职院校开展文化素质教育要以育人为导向，注重积累，从内容、形式、载体、途径等方面完善系统策划，紧跟当前产业转型升级的新需求和90后大学生的特点，不断创新活动载体和评价机制，提升教育的时代性、有效性和可持续性。整场报告数据详实、事例丰富、语言生动，令教师们获益匪浅。报告结束后，各参会学院表示，将结合自身专业及学生培养实际，借鉴先进的素质教育理念及做法，从顶层设计、各部门联动，扎实推进学生的素质文化教育。  （周荃 办公室）', 'http://info.jhc.cn/2015/0829/c201a54540/page.htm', 'http://info.jhc.cn/_upload/article/images/87/f9/3f59f2e741209165c8cea2430697/c3aca364-5eff-4e8c-a239-92a16ab624c7.jpg|http://info.jhc.cn/_upload/article/images/87/f9/3f59f2e741209165c8cea2430697/9543dbf7-854a-4d11-ad0c-cc023f2b0a07.jpg', '2017-05-17 14:15:32.343275');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (327, '', '王伟斌', 'admin', '2012-06-01 00:00:00', '《金华晚报》报道我院青年志愿者服务社区', '新闻链接：http://epaper.jhnews.com.cn/site1/jhwb/html/2012-05/31/content_1472788.htm', 'http://info.jhc.cn/2012/1109/c201a570/page.htm', 'http://info.jhc.cn/_upload/article/images/80/cf/5fa2f9e54f699d64ebe96355a164/2903d37b-2f29-4967-86bf-8e1545a0072e.jpg', '2017-05-17 14:15:32.36556');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (328, '', '王伟斌', 'admin', '2012-05-30 00:00:00', '信息学子在全国高等院校计算机核心技能与信息素养大赛中喜获佳绩', '5月26至28日，由教育部高等学校计算机基础课程教学指导委员会等7家单位联合主办的“全国高等院校计算机核心技能与信息素养大赛总决赛”在北京华北电力大学举行，由全国202余所院校11781名学生经过初赛、复赛层层选拔，产生了668名选手代表参加了最终的总决赛。经过紧张激烈角逐，代表信息学院参加总决赛的软件101班毛阿里同学与电商102班糜超同学不负众望，均获得了二等奖的好成绩。 本次大赛以美国Certiport发行的IC3项目国际认证考试为竞赛标准，完全按照全球化认证标准的要求，采用在线考试形式，自动阅卷，当场评分。考试内容包括计算机基础，应用软件，网络应用与安全三个科目。 大赛点亮人生，技能改变命运。通过本次大赛，既是对高职技能型人才计算机应用水平的检验，也是对学生动手能力和团队合作能力的一次锻炼，更是各院校互相学习、借鉴经验、增进交流的良好机会。计算机基础课程改革应更加关注培养信息时代学生的实践应用能力，为提高学生的职业素养、就业竞争力开拓出一条新思路。 （ 文  陈兴威 ）', 'http://info.jhc.cn/2012/1109/c201a584/page.htm', NULL, '2017-05-17 14:15:32.387886');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (329, '', '王伟斌', 'admin', '2012-05-30 00:00:00', '信息学院开展校企合作', '5月28日，杭州步步行贸易有限公司张勇总经理一行6人到信息学院进行考察。张勇总经理此行是就医疗仪器专业人才的培养工作与信息学院展开研讨，学院领导出席了此次研讨会。 会上，龚永坚教授向客人介绍了我院的基本情况，以及我校当前已有的与企业合作培养人才的模式；张总介绍了步步行贸易有限公司的基本情况，并就公司对医疗仪器人才的需求进行了深入介绍。双方经过深入探讨，对医疗仪器专业人才培养模式进行了分析并对后续合作奠定了基础。 下午，杭州步步行贸易有限公司营销总监、售后支持总监结合公司的实际需要向医仪091、101的同学介绍了作为销售工程师、维修工程师必备的技能和素质。同学们普遍反映收获巨大。 此次研讨及交流，既为专任教师与企业工程师搭建了桥梁，又为同学们进一步认识专业奠定了基础，意义深远。 （ 文 刘子坚 ）', 'http://info.jhc.cn/2012/1109/c201a581/page.htm', NULL, '2017-05-17 14:15:32.409962');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (330, '', '王伟斌', 'admin', '2012-05-29 00:00:00', '感谢母校培养 难忘师生情谊', '5月28日上午，实验剧场响起了《青春纪念册》、《我的未来不是梦》、《毕业生》等一首首耳熟能详的毕业季歌曲，信息学院2012届毕业生欢聚一堂，在此参加为他们举行的毕业典礼。   三年的校园生活，给同学们留下了许多美好的回忆，真挚的同学情谊，难忘的读书时光。而最让同学们印象深刻的，莫过于班主任了。毕业典礼上，班级代表给班主任献花，全体毕业生向班主任老师行谢师礼环节给大家留下了深刻印象，一个简单的谢师礼，表达了同学们对母校、对老师的感恩之情。     典礼上，毕业生代表回顾了大家三年的学习生活，军训的辛苦、老师的教诲、考试的紧张，让大家似乎又回到了那一段段逝去的岁月，言语中有不舍，有依恋，有感恩，但过去的终究已经过去，他更多的则是勉励大家把握未来，学会思考和抉择，实现自己美好理想。教师代表、在校生代表的发言，则给毕业生们送上了最美好的祝愿。   龚永坚院长代表学院发言，他感谢毕业生们三年来以自己的勤奋努力，为自身进步，为学院发展所做出的努力，并期待同学们今后继续支持学院的建设。他也提醒同学们毕业后不要忘记学习，在快速发展的时代中，继续钻研专业知识，勇于面对困难和挑战，善于把握机遇，珍惜青春岁月，早日实现自己的人生理想。 在《放心去飞》的歌声中，毕业典礼落下帷幕。                 （ 文 王伟斌  摄影 汪铭铠）', 'http://info.jhc.cn/2012/1109/c201a587/page.htm', 'http://info.jhc.cn/_upload/article/images/5c/43/4fbd4f2547b3a85811bdf6a0c735/1da2b211-4fad-4a02-82f3-aec586c24b4e.jpg|http://info.jhc.cn/_upload/article/images/5c/43/4fbd4f2547b3a85811bdf6a0c735/c617e190-ba8a-49e6-9145-8cf3f0def7d2.jpg|http://info.jhc.cn/_upload/article/images/5c/43/4fbd4f2547b3a85811bdf6a0c735/14a7bcac-4315-4575-b060-5f4ef3e66d37.jpg|http://info.jhc.cn/_upload/article/images/5c/43/4fbd4f2547b3a85811bdf6a0c735/250155c7-fdda-438a-9570-2ff7e0a44a9e.jpg|http://info.jhc.cn/_upload/article/images/5c/43/4fbd4f2547b3a85811bdf6a0c735/8a022957-806b-4bcb-a758-e66fab9a55c0.jpg', '2017-05-17 14:15:32.432206');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (331, '', '王伟斌', 'admin', '2012-05-23 00:00:00', '搭建交流平台，共享工作经验', '为促进我院学生干部队伍建设，发挥先进班集体的示范作用，促进班级建设经验交流和相互学习，提升班长的工作能力，5月22日下午，信息学院召开班长工作经验交流会。大一、大二年级全体班长、学工办全体成员参加了会议。 会上，4位班长作了典型发言，介绍他们班级的学习情况、管理班级遇到的情况以及解决的措施，他们和所有班长一起分享了自己在班级成长中的心得与先进班集体建设经验，并与参会班长进行了互动交流。随后班长们就自己班级存在问题在会中与各位学生干部进行交流，交流会现场气氛热烈，掌声不断，使本次交流会达到了一个新的高潮。 参加此次交流会的学生干部纷纷表示自己受益匪浅，希望今后能经常组织学生干部多交流、多学习，同时，相信有了这些宝贵的经验与方法，各班的班长一定能走出迷茫，找到正确的方向，更好得管理班级，为我院的学生工作的开展提供新的动力。 （ 文 汪铭铠 杜凤英）', 'http://info.jhc.cn/2012/1109/c201a597/page.htm', NULL, '2017-05-17 14:15:32.454457');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (332, '', '王伟斌', 'admin', '2012-05-20 00:00:00', '信息学子在省大学生学科与技能竞赛中再创佳绩', '5月19日，从浙江省“万家购物杯”第七届大学生电子商务竞赛上传来喜讯，由信息学院吕焱飞老师指导，金佳垒、徐跃慧、王世奇三位同学组成的竞赛团队不畏强手、敢于挑战，他们选送的“基于Heritrix和Shpinx的比较购物搜索引擎”项目经过初赛、决赛，最终获得一等奖，这也是信息学院第一次在该赛项上实现一等奖零的突破。 至此，信息学子今年已在省级一类学科与技能大赛上共获3个一等奖、2个二等奖、3个三等奖，并有2个赛项将代表浙江省参加6月底的全国大学生职业技能大赛。信息学院近年来形成的选拔、培训、再选拔、再培训的学生竞赛队伍遴选和辅导教师竞赛团队建设长效机制越来越展示出优势。 （ 文 吕焱飞 ）', 'http://info.jhc.cn/2012/1109/c201a601/page.htm', 'http://info.jhc.cn/_upload/article/images/a3/e7/b85610684fa6b3f853fa76b843ac/81907d08-6b53-434e-9c65-5dbbed196436.jpg', '2017-05-17 14:15:32.476654');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (333, '', '王伟斌', 'admin', '2012-05-18 00:00:00', '心手相连 共助成长', '在“全国助残日”即将到来之际，5月17日，在信息学院团委的带领下，信息学院党员、学生干部、心理协会成员一行20余人走进金华市启明学校，开展了一次助残活动。活动中，残障学生们参与到心理游戏、心声倾听、“心语心愿”等活动中，用微笑和努力诠释了残疾人的乐观、开朗和坚强。 本次助残活动丰富了残疾儿童的课内外生活，这样志愿服务也将一直持续下去，以我们微薄的力量给他们带去知识和欢笑。 （ 文 金乐 ）', 'http://info.jhc.cn/2012/1109/c201a609/page.htm', 'http://info.jhc.cn/_upload/article/images/9a/8e/2d378d114ab1a88a5678dc96ff76/95a837b8-47ce-4165-b4b4-b01baccc010d.jpg|http://info.jhc.cn/_upload/article/images/9a/8e/2d378d114ab1a88a5678dc96ff76/c736f013-c7f7-4ed6-9081-4db4870c240a.jpg|http://info.jhc.cn/_upload/article/images/9a/8e/2d378d114ab1a88a5678dc96ff76/513c969f-c32c-4a8b-b8ae-3b4e21480c7a.jpg', '2017-05-17 14:15:32.498848');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (334, '', '王伟斌', 'admin', '2012-05-18 00:00:00', '信息学院召开党建工作研讨会', '5月16日，信息学院召开党建工作研讨会。会议由学院党总支书记朱登胜主持，学院各支部委员等党务工作者参加了本次会议。 本次党建工作研讨会是学院贯彻落实校党委在全校党员干部中开展“科学发展主题培训行动计划”活动的通知要求，坚持“干什么学什么、缺什么补什么”的原则确定研讨、培训内容针对性开展的业务研讨培训活动。 会上，学院组工纪检委员首先传达了学校党务干部培训会的有关精神和决定，随后就党员发展的具体过程和工作进行了详细讲解，要求各党支部在党员管理和发展党员的过程中要严格按照发展党员工作实施细则和党员发展的流程进行，相关部门人员就明确入党培养联系人、介绍人工作职责等方面讲解了党员发展过程中的注意事项。与会人员围绕党员发展工作、基层组织建设等问题展开积极讨论，为学院的党建工作发展献计献策。 党总支朱登胜书记做最后总结，他充分肯定了精心组织的本次会议即将带来的成效，要求全体党务工作者要以严谨的态度和求真务实的作风对待党务工作，并从学生党员发展要求、培养人的确定、发展程序规范性等方面对党员发展工作提出明确要求。同时，他还强调各支部要充分运用创造性思维，有效组织开展本支部丰富多彩的党员活动，扎实推进学生支部党员先锋岗的建设，通过活动让党员特别是学生党员提升组织归属感，增强基层党支部的凝聚力和战斗力，在创先争优活动中将基层组织建设年活动真正落到实处。 （ 文 林雪华 ）', 'http://info.jhc.cn/2012/1109/c201a606/page.htm', NULL, '2017-05-17 14:15:32.521125');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (335, '', '王伟斌', 'admin', '2012-05-15 00:00:00', '母爱无疆，感恩更美', '为增强学生感恩教育，增进学生与家长之间温馨融洽的关系，加深大家对母亲的感恩之情，5月13日母亲节，信息学院“母爱无疆，感恩更美”母亲节感恩活动如火如荼地展开。在对经典电影《妈妈再爱我一次》的回顾中，在影片煽情感人的气氛渲染下，同学们纷纷拿出手机，给母亲送去节日的问候，大声地表达感激之情。 此次活动除电影展外，还包括母亲节朗诵宣传，母亲节海报设计大赛，“我为母亲许个愿”感恩红丝带，“学会沟通”团体恳谈会等内容。通过此次活动，在全院学生中营造了一个向母亲表达爱意，表达感恩之情的浓厚氛围。  （ 文 黄蓉 ）', 'http://info.jhc.cn/2012/1109/c201a618/page.htm', NULL, '2017-05-17 14:15:32.543337');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (336, '', '王伟斌', 'admin', '2012-05-14 00:00:00', '信息学子在2012年浙江省高职高专院校技能大赛喜获佳绩', '5月12日，浙江省高等学校学生技能大赛暨全国职业院校技能大赛“电子产品设计与制作(基于FPGA技术)”落下帷幕。我校信息学院派出的两支代表队喜获一、二等奖各1项，为学校争得了荣誉。   此次竞赛共有来自省内10余所高职院校的25支代表队参赛，竞争异常激烈。竞赛内容是以现代电子设计自动化技术应用为目标，以基于XILINX的FPGA、VHDL硬件描述语言为比赛平台，是高职高专院校首次在高端SOPC技术领域开展的技能赛项。为在该项赛事上取得好成绩，我校信息学院十分重视比赛准备工作，竞赛师生克服困难、加强培训、科学备战。比赛中，经过9个小时的高强度激烈角逐，我校代表队不负众望，取得骄人成绩：由肖旭、彭召翔、吴宏星同学组成的代表队荣获第2名（一等奖），张晓磊、刘龙强、周吉祥同学组成的代表队荣获第4名（二等奖），其中第2名代表队将代表浙江省于6月底赴天津参加全国职业院校技能大赛。 近年来，信息学院十分重视教学改革和创新实践教育，注重建立学生创新能力培养的长效机制，在课程与赛项对接、选拔与培训机制、指导团队培养等方面努力实践，积累了丰富的经验。学院还积极探索将竞赛模式引入课程教学，构建竞赛和教学一体化的网络自主学习平台，通过“以教促赛、以赛促教”， 不断探索创新型高技能人才培养模式。 （ 文 徐华丹 刘子坚 ）', 'http://info.jhc.cn/2012/1109/c201a621/page.htm', 'http://info.jhc.cn/_upload/article/images/63/22/cceb6d6c46baa1cc90f0ece758be/47e3d52a-db23-4cfc-9133-d690d565f76e.jpg|http://info.jhc.cn/_upload/article/images/63/22/cceb6d6c46baa1cc90f0ece758be/765324d8-e7ee-4465-be2c-14e59a078499.jpg', '2017-05-17 14:15:32.565643');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (337, '', '王伟斌', 'admin', '2012-05-10 00:00:00', '信息学院举办第五届宿舍文化节', '为彰显积极向上的宿舍文化，引导学生创建文明和谐的寝室氛围，5月9日，信息学院第五届宿舍文化节开幕。      据悉，本届宿舍文化节分“文明公寓”、“活力公寓”两部分进行，开展包括优秀公寓评比、卫生安全宣传、寝室美化设计大赛、宿舍全家福摄影比赛、毕业生文明离校等在内的九项活动。      （ 文 汪铭铠 ）', 'http://info.jhc.cn/2012/1109/c201a626/page.htm', NULL, '2017-05-17 14:15:44.369593');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (338, '', '王伟斌', 'admin', '2012-05-09 00:00:00', '发挥参政议政职能，服务金华经济发展', '日前，由民盟金华职业技术学院支部领衔参与的市民盟提案《加强金华市区信息产业园区统筹规划与管理的建议》列入金华市2012年市政府领导领办重点建议和重点提案目录。这是我校民盟支部服务金华经济发展，充分发挥参政议政职能的又一突出成绩。 《加强金华市区信息产业园区统筹规划与管理的建议》提案是在金华市信息产业“十二五”发展规划提出大力发展信息产业和信息经济的背景下提出的，符合金华市全面实施“创业富民、创新强市”总战略和工业强市、服务业兴市的目标。作为主要领衔参与单位，该提案是我校民盟支部充分发挥我校电子信息学科专业优势和政校企合作成立的金华高新IT学院的有利条件，以科学发展观构建市区电子信息产业统筹规划与管理体系为调研方向进行了调研后提出的，充分展示了我校教师在参政议政上的专业优势和组织能力。 （文 王伟斌 ）', 'http://info.jhc.cn/2012/1109/c201a629/page.htm', NULL, '2017-05-17 14:15:44.416269');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (339, '', '王伟斌', 'admin', '2012-05-07 00:00:00', '信息学子泳池展英姿', '在日前结束的浙江省2012年大学生游泳锦标赛上，我校战绩喜人，其中也不乏信息学子的身影。其中我院网络112班程凯奇同学代表学校参加的比赛中，获得400米自由泳金牌、200米混合泳接力金牌、200米自由泳银牌、400米自由泳接力银牌，展现了信息学子在泳池中的英姿，为学院争得了荣誉。', 'http://info.jhc.cn/2012/1109/c201a651/page.htm', NULL, '2017-05-17 14:15:44.444344');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (340, '', '王伟斌', 'admin', '2012-05-07 00:00:00', '信息学子在浙江省大学生职业技能大赛项目竞赛荣获第一名', '5月6日，浙江省大学生职业技能大赛暨全国职业院校技能大赛选拔赛 “风光互补发电系统安装调试”项目竞赛在浙江机电职业技术学院举行。由我校信息学院张益鹏、许希助、雷焕道三位同学组成的代表队团结协作、顽强拼搏、不负众望，荣获比赛成绩第一名，为学校争得了荣誉。 为在“风光互补发电系统安装调试”竞赛项目上取得好成绩，信息学院领导十分重视比赛准备工作，参赛师生克服种种困难，夜以继日，强化训练近二个月，参赛师生通过研究比赛方案、举行模拟比赛以切实提升团队实力，最终以较大优势获得该赛项第一名。 据悉，该赛项的竞赛内容涉及光伏供电装置、光伏供电系统、风力供电装置、风力供电系统、逆变与负载系统、监控系统的安装、接线、调试、PLC程序设计和分析，以及故障检测与排除。我校代表队将代表浙江省参加于6月底在天津举行的全国职业院校技能大赛。  （ 文 张小杭 ）', 'http://info.jhc.cn/2012/1109/c201a647/page.htm', NULL, '2017-05-17 14:15:44.466342');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (341, '', '王伟斌', 'admin', '2012-05-07 00:00:00', '信息学院举行第九届田径运动会', '你追我赶   蓄势待发   一跃而起   咬牙坚持   齐头并进   开心一刻   金色奖杯 5月5日，信息学院第九届田径运动会在校运动场隆重举行，校党委副书记余党军、学工部部长吴寿松、教务处副处长吴雄彪应邀出席开幕式，学院全体教师和大一大二学生参加运动会。 本届运动会上，来自学院33个班的389名运动员在100米短跑、跳远、跳高等17个比赛项目上进行了激烈角逐，赛场上运动员们你追我赶，不相上下，赛场外观众们加油声、呐喊声此起彼伏，广播里激扬的音乐使田径场精彩不断、激情有余，全院师生完全融入到了运动的无限活力之中。 经过紧张、激烈的比赛，最终电商112班、计算机103班分获大一、大二年级团体总分第一名，应电111等4个班级获得精神文明奖，另有沈梦瑶、吴晓悦2位同学打破3项学院田径运动会纪录。运动健儿在赛场上表现出来的积极进取、顽强拼搏的精神，保证了本届运动会的圆满成功，也展示了全体信息学子的良好素质和精神风貌。 （ 文 林雪华  摄影 王伟斌）', 'http://info.jhc.cn/2012/1109/c201a631/page.htm', 'http://info.jhc.cn/_upload/article/images/5a/20/a8f08c72455493b1e922996d52e2/834d81f7-9c41-4fd7-84ce-b782c0acb3ea.jpg|http://info.jhc.cn/_upload/article/images/5a/20/a8f08c72455493b1e922996d52e2/3bd6e6a9-5fca-4dac-b0a0-423a2dbd2124.jpg|http://info.jhc.cn/_upload/article/images/5a/20/a8f08c72455493b1e922996d52e2/62d54575-1417-4692-96c2-76ed82289f07.jpg|http://info.jhc.cn/_upload/article/images/5a/20/a8f08c72455493b1e922996d52e2/bf9cf71a-ace1-4cb4-9bc2-5a9a8e1b340d.jpg|http://info.jhc.cn/_upload/article/images/5a/20/a8f08c72455493b1e922996d52e2/974b046b-2cab-45e8-a611-ccfcad33e2e4.jpg|http://info.jhc.cn/_upload/article/images/5a/20/a8f08c72455493b1e922996d52e2/559f8fd2-4c3b-496c-b585-f6cc2310110a.jpg|http://info.jhc.cn/_upload/article/images/5a/20/a8f08c72455493b1e922996d52e2/edebba53-bb42-43b5-929a-c014932cff28.jpg', '2017-05-17 14:15:44.488608');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (342, '', '王伟斌', 'admin', '2012-05-04 00:00:00', '信息学院举行“五四”运动93周年纪念活动', '今天是“五四”青年节，信息学院举行纪念“五四”运动93周年暨新党员入党宣誓、老党员重温誓词仪式。学院党总支书记朱登胜、各学生支部书记、学生党员参加了本次仪式。   伴随着庄严的《国际歌》，全体师生党员齐举右拳，面对鲜红的党旗庄严宣誓，洪亮的誓言在信息学院大楼回响。以“爱国、进步、民主、科学”为主要内涵的“五四”精神，激励了一代又一代的有志青年为中华民族的建设富强作出了巨大的贡献，学院希望通过举行入党宣誓仪式，以更好地弘扬“五四”精神，使广大党员更加坚定共产主义的理想信念，加强党性修养，正确把握时代内涵，以振奋的精神为学院发展服务。 仪式上，朱登胜代表学院党总支向青年学生致以节日的问候，他说，同学们在学校读书期间能够加入中国共产党是一种无尚的光荣，同时也意味着将承担更大的责任，肩负更艰巨的使命。党员同志们要牢记对党的誓言，坚定入党信念，刻苦学习，胸怀理想，努力提高自身的综合素质，充分发挥党员的先锋模范作用，为将来成为社会有用的人才打下良好的基础。 信息学院党总支将以纪念“五·四”运动为契机，结合基层党组织建设活动，开展在实训室设立学生党支部党员先锋岗等创先争优活动，在全院上下营造基层组织建设年的浓厚氛围。 （ 文 周荃 ）', 'http://info.jhc.cn/2012/1109/c201a654/page.htm', 'http://info.jhc.cn/_upload/article/images/fd/60/2d83625b4df6a51ab24d330573ea/bc3a31a4-37a7-4fb4-b083-42b88dceee66.jpg|http://info.jhc.cn/_upload/article/images/fd/60/2d83625b4df6a51ab24d330573ea/d0ddf810-42db-4d20-a3e0-a08255713abf.jpg', '2017-05-17 14:15:44.510974');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (343, '', '王伟斌', 'admin', '2012-05-03 00:00:00', '我院学生参加市城管局“城市啄木鸟”公益活动', '新闻链接：http://jhtv1.36tv.cn/view-192380.html', 'http://info.jhc.cn/2012/1109/c201a662/page.htm', 'http://info.jhc.cn/_upload/article/images/be/f7/8defbdc648339519285529731253/cb98a189-61b8-4bd5-a11e-0cc71208ada4.jpg', '2017-05-17 14:15:44.533111');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (364, '', '王伟斌', 'admin', '2012-03-16 00:00:00', '信息学院举行本科学历教育新生开学典礼', '3月15日，信息学院举行杭州电子科技大学本科学历教育（金华教学点）开学典礼。杭州电子科技大学成教学院院长胡建萍、信息学院党总支书记朱登胜及相关部门负责人、本期新学员参加开学典礼。 通过参加全国成人高校统一考试，信息学院2012年共有242名同学被杭州电子科技大学录取，成为函授班学员，他们将在3年内学完杭电所有本科课程，并根据杭州电子科技大学《学位工作实施细则》的规定，在符合一定条件下被授予学士学位。这是信息学院与杭州电子科技大学开展合作办学，定向培养本科学历学生的崭新模式，它为同学们学历提升提供了一条捷径，目前受到了同学们的普遍欢迎。 （ 文 单天德 俞冬丽 ）', 'http://info.jhc.cn/2012/1109/c201a762/page.htm', NULL, '2017-05-17 14:15:57.245382');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (344, '', '王伟斌', 'admin', '2012-04-25 00:00:00', '信息学院开展学生干部办公自动化设备培训', '科学技术飞速发展，工作节奏不断加快，现代办公设备是提高办公效率不可缺少的工具。为使学生干部能熟练使用现代办公设备，进一步提高工作效率，日前，信息学院对全体学生干部开展了一场办公自动化设备的专题培训。 培训主要针对学生干部在涉及到办公自动化设备如打印机、传真机、投影仪等办公软件、硬件时的使用方法。培训会上，主讲老师为学生干部讲授并详细地演示了如打印机如何使用、打印机卡纸如何解决，如何快速打印几百张证书等日常工作中经常遇到的问题。 此次培训活动极大地提高了同学们学习办公自动化设备的积极性，增强了学生会成员的服务能力，为更好地开展团学工作奠定了坚实的基础。 （ 文 汪铭铠 ）', 'http://info.jhc.cn/2012/1109/c201a668/page.htm', NULL, '2017-05-17 14:15:44.555289');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (345, '', '王伟斌', 'admin', '2012-04-24 00:00:00', '《金华晚报》报道我院青年志愿者参与社区服务', '新闻链接：http://epaper.jhnews.com.cn/site1/jhwb/html/2012-04/18/content_1452465.htm', 'http://info.jhc.cn/2012/1109/c201a672/page.htm', 'http://info.jhc.cn/_upload/article/images/58/bd/5838f98f4d65807d1b46f018d843/3e85f8cd-6730-489f-8784-d6c115e6cec7.jpg', '2017-05-17 14:15:44.577628');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (346, '', '王伟斌', 'admin', '2012-04-23 00:00:00', '学校举行2012年教师参加社会实践任务审核情况反馈会', '4月20日，学校2012年教师参加社会实践任务审核情况反馈暨信息工程学院经验介绍现场会在信息学院会议室举行，院长王振洪、学校相关职能部门负责人、各二级学院院长及分管工作副院长等参加会议。   教育督导处吴仁艳处长首先通报了我校2012年教师参加社会实践任务审核情况，她用具体数据分析了我校教师参加社会实践的现状，介绍了各学院一些特色、亮点以及目前存在的不足。   信息学院龚永坚院长在会上介绍了信息学院关于教师参加社会实践的经验及具体做法。2011年，信息学院多举措推动教师社会实践改革。形式上，改变以往暑期集中的模式，转为常年不定期的教师联系企业技术人员形式，解决教师“怕人家不理、怕自身技术不行、怕辛苦”的问题；管理上，实施二级督查制度，灵活督查形式，以教学质量管理网站为平台，力求社会实践过程管理的时效性、连贯性、透明化；考核上，全程跟踪教师的计划制订、日志填写、实践成效、总结报告质量等，以年终总评的方式进行。通过改革，信息学院的教师社会实践工作在计划完成、管理模式创新、时间和空间的延续性等方面达到了预期效果，教师社会实践质量明显提升。     王振洪院长在听取汇报后总结发言。他说，教师社会实践工作关系学校全局，一定要有思考、有组织、有总结、有反馈，各二级学院要结合自身实际积极反思，制定切实可行的实施方案，保障社会实践工作有序开展。他对2012年社会实践工作提出四个方面的要求：第一，思想上再重视。按照国家教育部、浙江省教育厅的有关文件精神及学校“十二五”规划，教师参加社会实践的必要性和重要性不言而喻，教师参加社会实践工作关系到学校发展、教学质量保障、人才培养质量提升、教师个人能力提升等诸多层面，要在思想上充分重视这项工作。第二，行动上再体现。对2012年的社会实践工作，各二级学院院长要亲自抓，要统筹好实践时间、场所，兼顾好教学，充分重视社会实践工作的解读和传达，要实打实的组织好、安排好、审核好社会实践工作，真正体现社会实践的成效；职能部门要在充分听取二级学院及一线教师意见的基础上，分析、研判现有的政策制度，开展整改完善工作，做到政策不多不少不虚不空，精致管用。第三，设计上再多元。学校规模大、专业多，因此社会实践不可能套用一个模式。职能部门及二级学院要多思考、多创新，在体现形式的灵活性、政策的科学性、教师的自主性的同时，更加注重教师社会实践个性化设计，各学院可以通过经验交流、教师座谈等形式取长补短，多层、分类、多元实施，人性化落实。第四，过程要求更加严格。学校和各二级学院要对教师参加社会实践工作的过程管理及考核更加严格要求，通过切实有效的管理，让教师从思想上和行动上都统一到学校的要求中来，提高社会实践的实施成效。最后，王振洪要求学校相关职能部门要有规划、有计划、有部署、有检查、有反馈，及时将相关情况反馈到分管领导及二级学院，切切实实指导二级学院推动社会实践工作，保障2012年教师社会实践工作有序、有效开展。 （ 文 王伟斌 陈德仙 ）', 'http://info.jhc.cn/2012/1109/c201a684/page.htm', 'http://info.jhc.cn/_upload/article/images/db/37/18f351354412a3ec212138745600/f3fa2c19-f577-4259-a204-d1fb4bbdc68b.jpg|http://info.jhc.cn/_upload/article/images/db/37/18f351354412a3ec212138745600/bc5fde13-ceb7-4ccd-83e3-fdccf859f4ff.jpg|http://info.jhc.cn/_upload/article/images/db/37/18f351354412a3ec212138745600/0ad374a5-4bc2-462f-8dc9-c22dc2daf811.jpg|http://info.jhc.cn/_upload/article/images/db/37/18f351354412a3ec212138745600/6450753d-aee7-42b4-95a5-9958c40e19b9.jpg|http://info.jhc.cn/_upload/article/images/db/37/18f351354412a3ec212138745600/caa36745-1ebb-4f0a-bc42-3a7dfab82a46.jpg', '2017-05-17 14:15:44.599785');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (347, '', '王伟斌', 'admin', '2012-04-23 00:00:00', '以环保之心，绘地球之美', '4月22日是“世界地球日”，信息学院举办了“以环保之心，绘地球之美”为主题的拼图倡环保行动，用回收的废旧资源拼成中国的版图，以倡导大家保护环境。 活动前期，学院去各班级宣传本次环保行动，得到很好的反响。大家纷纷回收塑料瓶、易拉罐、报纸等物品。活动当天，同学们陆陆续续的参与到活动中，大家勾勒出版图的同时也为环保做出了贡献。 这次活动的目的并不仅仅是绘制版图，更重要的是提升同学们的环保意识，希望大家能够将环保意识实践于以后的生活中。校园环保的践行并不仅仅是在今天这一天，需要我们切切实实行动起来，用我们的拼搏和奋斗去推动一股清新的风，让他吹遍整个校园，真正环保我心。 （ 文 汪铭铠 金乐 ）', 'http://info.jhc.cn/2012/1109/c201a681/page.htm', NULL, '2017-05-17 14:15:44.622164');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (348, '', '王伟斌', 'admin', '2012-04-23 00:00:00', '信息学子在2012年省“计算机网络应用”大赛上成功冲奖', '4月21日，从浙江省高等职业院校大学生“计算机网络应用”技能大赛上传来喜讯，由信息学院孙强、邢晓丰、鲁彬彬组成的团队喜获三等奖。 此次大赛内容新、覆盖面广，包括了三网融合和物联网的最新内容，同时，我院参赛团队面临诸多挑战，16种竞赛设备中的半数以上设备我院不具备，也没有类似设备。在资金、设备严重短缺的情况下，凭借科学的选拔与组队、能力主导的训练模式、科学严谨的训练安排，最终凭借扎实的基础技能和技术能力冲击奖牌成功。 本次大赛是全国职业院校技能大赛省级选拔赛，共有19个院校参赛，大赛一等奖获奖比例10%，二等奖获奖比例20%，三等奖获奖比例30%，我院喜获团体总分第8名。 （ 文 朱墨池）', 'http://info.jhc.cn/2012/1109/c201a677/page.htm', NULL, '2017-05-17 14:15:44.644371');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (349, '', '王伟斌', 'admin', '2012-04-16 00:00:00', '信息学院在省第九届大学生程序设计竞赛中喜获佳绩', '4月14日，从浙江省第九届大学生程序设计竞赛赛场传来喜讯，我校信息学院选派的三支参赛队不负众望，在长达5个小时的不间断马拉松式编程比赛中，发挥稳定，取得一银一铜的好成绩，其中钟燕、季延炜、叶洙晶三人团队喜获银奖，朱一帆、洪晖岳、沈璐三人团队获铜奖。   据悉，ACM大学生程序设计竞赛是一项国际性赛事，素有“大学生计算机奥林匹克竞赛”之称，旨在培养大学生的创新思维和利用计算机分析问题、解决实际问题能力，锻炼大学生的综合素质，提高计算机应用水平。今年的比赛在浙江大学举行，共吸引了来自全省78所本专科高校的305支队伍同场竞技。 信息学院通过几年的实践，积累了较丰富的参赛经验，形成了一套选拔、培训、再选拔、再培训的学生竞赛队伍遴选和辅导教师竞赛团队建设机制。这几年，学院还积极探索将竞赛模式引入日常课程教学，开发竞赛和教学一体化网络平台，实现以教带赛，以赛促教的新模式，进一步深化了学院目前积极倡导的网络化教育教学改革。 （ 文 王伟斌 梅旭时 ）', 'http://info.jhc.cn/2012/1109/c201a698/page.htm', 'http://info.jhc.cn/_upload/article/images/43/09/0b18de9144f281cee5e526f42b08/348fde36-b311-4167-be67-c5431441f3bd.jpg|http://info.jhc.cn/_upload/article/images/43/09/0b18de9144f281cee5e526f42b08/7b7425ac-0fcf-4ada-aec1-adfd3856ea61.jpg', '2017-05-17 14:15:44.666477');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (350, '', '王伟斌', 'admin', '2012-04-13 00:00:00', '信息学院召开四届三次教职工暨工会会员代表大会', '4月12日-13日，信息学院召开四届三次教职工暨工会会员代表大会，校党委副书记余党军、校工会副主席朱秀英莅临大会指导，27名正式代表及10名列席代表参加大会。   余党军首先致辞，他说，2011年信息学院在示范建设后能主动加压，不断在教学科研等方面进行研究探索，特别是高新IT学院的组建、CDIO工程教育改革的实施，取得了显著成果。他希望信息学院在新的一年中能够继续开拓创新，同心同德，不断实现学院新发展、取得新成绩。校工会副主席朱秀英代表学校工会向大会召开表示祝贺，他认为信息学院工会过去一年的工作有特色、有亮点，充分发挥了工会组织的职能。 信息学院本次“双代会”是在“十二五”开局取得显著成绩的背景下召开的，具有承上启下的重要意义。大会全面贯彻学校四届三次教代会精神，结合本学院实际，在认真总结过去一年经验的基础上，提出了新一年的工作要求。   会上，龚永坚院长做了题为《总结经验找差距,锐意进取续新篇》的工作报告。报告指出，2011年信息学院以校企利益共同体——金华高新IT学院的组建为开局，在校党委、校行政的正确领导下，信息学院思政党建工作常抓不懈，创先争优活动取得显著成果；不断深化校企合作，专业内涵建设水平显著提升；完善网络教学管理平台，推进校内外基地建设，教学软硬环境建设不断改善；进一步加大师资培养力度，教师的业务水平和能力显著提高；创新科研机制，激发科研活力，科研成果成绩喜人；招生就业工作继续保持稳定发展；狠抓学风、考风建设，引导和构建了积极向上的校园文化；财务制度不断规范，教职工福利稳步提升，学院收支保持平衡。   报告也实事求是地指出了目前学院发展中还存在的教师整体科研水平不高、社会服务能力不强、师资队伍高职称比例偏低、专业发展缺少更多特色和亮点、成教创收总量不高等问题。明确地提出2012年学院将围绕学校中心工作，继续深化金华高新IT学院各项工作、探索CDIO工程教育改革等重点工作内容，紧紧依靠全院教职员工，充分发挥教职工的聪明才智，解放思想、寻找差距，高质量完成各项任务，为实现市委市府提出的“做强做大金华职业技术学院”的目标，认真履行好信息学院承担的职责。   与会代表对学院工作报告进行了认真审议并进行了分组讨论，代表们充分行使民主管理、民主监督权利，对目前学院的各项工作建言献策，畅所欲言。大家认为院长工作报告对学院一年工作进行了认真总结，取得的成绩令人振奋，鼓舞人心； 2102年所提出的工作目标明确、思路清晰，能够指导学院工作的开展。闭幕式上，针对代表们普遍关心的绩效工资改革、人才用房、工会活动、学风建设的长效机制建设等问题，学院领导和相关部门负责人进行了政策解读和说明。 大会要求全体代表在今后的工作中继续保持主人翁姿态和高度的责任感，履行好代表职责，在广大教职工中宣传、传达、贯彻本次大会精神，在全院上下形成团结协作、求真务实的良好氛围，振奋精神，携手共进，为实现学院新阶段发展目标而努力奋斗！ 会议还听取和审议了工会工作报告、四届三次教代会提案征集情况报告等内容。 （ 文/摄影 王伟斌 ）', 'http://info.jhc.cn/2012/1109/c201a705/page.htm', 'http://info.jhc.cn/_upload/article/images/f1/f1/27dd6d7f4fdeb7ef3662b0ccce6e/e1d3838a-19c4-4aee-b9e5-098848a98f7a.jpg|http://info.jhc.cn/_upload/article/images/f1/f1/27dd6d7f4fdeb7ef3662b0ccce6e/a5169bc0-e7e6-45dd-a78f-1f4c896aa8da.jpg|http://info.jhc.cn/_upload/article/images/f1/f1/27dd6d7f4fdeb7ef3662b0ccce6e/8805df92-f4e0-457c-87ff-768eeb6cbf56.jpg|http://info.jhc.cn/_upload/article/images/f1/f1/27dd6d7f4fdeb7ef3662b0ccce6e/20d7450f-3439-4466-be00-3429b3e1f491.jpg|http://info.jhc.cn/_upload/article/images/f1/f1/27dd6d7f4fdeb7ef3662b0ccce6e/1ff8b602-8ca2-4b7f-b0fa-4c22d09148c4.jpg', '2017-05-17 14:15:44.688789');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (351, '', '王伟斌', 'admin', '2012-04-12 00:00:00', '信息学院党校举行第十七期入党积极分子培训班开学典礼', '4月12日，信息学院举行第十七期入党积极分子培训班开学典礼。学院党校校长、党总支书记朱登胜，党校校务委员会成员及187名学员参加了本次开学典礼。 经过学生自荐、班主任推荐、党校校务委员会逐层筛选，共有187名学员参加本次党校培训。朱登胜对全体学员提出殷切的希望：希望学员们树立端正的入党动机，学习和实践科学发展观，积极参加社会实践活动，在服务和工作中成长，为创建和谐社会、和谐校园贡献自己的力量。党员代表丁慧慧同志、学员代表陈洁同学先后发言，向党组织表达了同学们参加党校的喜悦心情和认真学习的决心。 开学典礼上成立了本期党校培训班团支部，并宣读了党校章程、学员守则、党校优秀学员评定规则和教学课程安排，培训班将围绕党的理论知识学习、公益活动、观看优秀共产党员影片等环节陆续展开。 （ 文 周荃 ）', 'http://info.jhc.cn/2012/1109/c201a717/page.htm', NULL, '2017-05-17 14:15:56.942924');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (352, '', '王伟斌', 'admin', '2012-04-11 00:00:00', '信息学院理论学习中心组举行学习会', '近日，信息学院召开理论中心组学习会。学院党总支书记朱登胜主持学习会，学院全体中层及以上干部、各党支部书记参加了集中学习。 会上，朱登胜传达了学校理论中心组学习会精神，带领大家研读了中共中央组织部《关于在创先争优活动中开展基层组织建设年的实施意见》等文件，并就开展“思想大解放，作风大转变”主题实践活动和做好基层党组织分类定级工作进行动员部署。 院长龚永坚在学习会上发言，他说，今年是实施“十二五”发展规划的重要一年，全校开展“思想大解放，作风大转变”主题实践活动是适应当前实际形式需要。学院各部门、全体教职员工要增加危机感，按照学校党委、学院党总支的统一部署，紧密结合学院实际，寻找差距，破解难题，以“思想大解放，作风大转变”活动为契机，加强学习，解放思想，不断提高自己，为学院提升内涵、增强核心竞争力贡献力量。 按照总院统一部署，信息学院本次“思想大解放，作风大转变”主题实践活动从今年3月开始至12月底结束，分三个阶段进行：一是动员部署，制定方案阶段（3月中旬以前）；二是全面落实，深入推进阶段（3月下旬至11月底）；三是总结评议，巩固提高阶段（12月）。 学习会后，学院各部门将围绕“十问”开展解放思想大讨论活动，明目标，找差距，正式启动主题实践活动。 （ 文 林雪华 ）', 'http://info.jhc.cn/2012/1109/c201a721/page.htm', NULL, '2017-05-17 14:15:56.9785');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (353, '', '王伟斌', 'admin', '2012-04-10 00:00:00', '信息学院为毕业生开辟海外高端就业区', '4月6日，信息学院通信与网络工程部选派的程毅然等首批3名应届毕业生奔赴马来西亚吉隆坡的SONWAY地区进行毕业实习，第2批同学也将于本月奔赴马来。这标志着信息学院与广东南方通信建设有限公司建立的毕业生海外高端就业取得实质性成果。 本学期初，信息学院与广东南方通信建设有限公司海外事业部建立联系，推荐优秀的网络、通信类毕业生赴海外工作。毕业生出国性质是劳务输出，和劳务公司签订顶岗实习和劳动合同，南建公司与劳务公司签订用工合同，薪资待遇同工同酬，享有正规劳务输出的境外保险等待遇。顶岗实习月薪5000元，时间3个月，实习结束后，签订劳动合同。 据悉，广东南方通信建设有限公司成立于1993年，注册资金1.4亿，资产总值5.35亿元，2010年业务规模达到10亿元。下属10个子公司，海外事业部是其中之一。2009年，公司取得通信工程施工总承包壹级资质以及高新技术企业认定，承接华为、爱立信等公司的大型海外工程项目。 (  文 朱墨池 )', 'http://info.jhc.cn/2012/1109/c201a723/page.htm', NULL, '2017-05-17 14:15:57.00076');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (354, '', '王伟斌', 'admin', '2012-04-09 00:00:00', '春暖时节 志愿先行', '正值春暖花开适宜出游时节，近日，信息学子前往金华动物园开展了志愿服务活动。本着“奉献、友爱、互助、进步”的志愿服务理念，志愿者们积极配合动物园管理处的工作，引导游客参观游览、维持秩序、帮游客拍照等等。虽然在工作中遇到了许多困难，但志愿者们始终保持着高度的责任感，一直坚守岗位，认真负责的工作态度获得了景区工作人员和安地乡镇府领导的一致认可。 活动结束后，志愿者们纷纷感慨：我们是志愿者，我们有一种荣誉感和使命感，游客们赞许的眼光也让我们体会到了志愿服务的快乐。  （ 文 金乐 ）', 'http://info.jhc.cn/2012/1109/c201a729/page.htm', NULL, '2017-05-17 14:15:57.022929');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (355, '', '王伟斌', 'admin', '2012-04-09 00:00:00', '信息学院举行“神采飞扬”英语演讲比赛', '为提高学生英语口语水平，提供一个展现自我、激励他人的舞台，日前，信息学院翔宙英语协会和翼天演辩社联合举办了以“I CAN I DO I WIN”为主题的英语演讲比赛。     经过两个星期的比赛，最终有12名来自不同班级的选手进入总结赛，争夺比赛的冠军。场上选手们充分运用自己所学，讲述自己的梦想与坚持，引发场下观众的深思。最终由来自计算机112班的冯磊同学获得了本次比赛的冠军。     本次英语演讲比赛旨在提高学生的英语口语水平，提升学生学习英语的意识，同时创建一个展现自我的舞台，展现大学生积极向上的精神风貌。 （ 文 金乐 ）', 'http://info.jhc.cn/2012/1109/c201a727/page.htm', NULL, '2017-05-17 14:15:57.04513');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (356, '', '王伟斌', 'admin', '2012-04-06 00:00:00', '信息学院召开教学工作研讨会', '4月6日，信息学院召开教学工作研讨会，院长龚永坚、各项目部主任、专业主任参加，会议由陈海荣副院长主持. 会上对学校教学工作会议精神作了传达，围绕学校组织对我院校内实训（验）室教仪设备状况和2011-2012学年第一学期实践教学材料检查所反映的问题进行了通报与点评；对学科与技能竞赛备战情况进行了逐个排查与落实；对省内顶岗实习检查所暴露的问题进行了深入的研讨。 龚永坚要求教科办、项目部，加强实训室管理制度建设，对照目前存在的突出问题，各司其责，认真做好实训室的规范管理工作，同时要求各项目部结合部门实际，进一步强化工作的计划性、有效性，努力提升专业建设年度成效。 信息学院十分重视教学工作研讨，通过部门工作周志、每周教学工作例会等有效沟通方式协调、处理工作难点，以常态化管理促进学院专业建设的各项工作稳步推进。 （ 文 邱晓华 ）', 'http://info.jhc.cn/2012/1109/c201a731/page.htm', NULL, '2017-05-17 14:15:57.067343');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (357, '', '王伟斌', 'admin', '2012-04-01 00:00:00', '信息学院召开成教教学工作会议', '3月31日，信息学院召开成教教学工作会议，总结成教教学工作经验，探讨成教教学新措施、新方法。 会上，教学督导通报了近期成教教学工作的基本情况，围绕怎样搞好我院成教教学工作，与会人员进行了热烈讨论和充分交流。大家认为，成教教学要因材施教，推行分层次考核、分阶段排课，突出过程考核，授课过程中教师要配合讲一些育人、创业、兴趣方面的知识，学生更容易接受，效果会更好。大家建议继教部进一步完善教学检查汇报机制，加强课堂考勤管理，充分发挥学生党员的榜样作用。 会议认为，成教教学工作直接影响到学院安全、稳定和创收，需要各个部门团结一心，群策群力，通力合作；要在成教教学工作中多创新，为提高成教教学质量多想办法，出点子，谋发展；要靠管理的规范、教学质量的提升来做强、做大，从而使成教教学工作更上层楼。 ( 文 单天德 俞冬丽 )', 'http://info.jhc.cn/2012/1109/c201a735/page.htm', NULL, '2017-05-17 14:15:57.089606');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (509, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '校企联谊 实现共赢', '昨天，金职院信息工程学院的学生与5173网络公司的员工进行了一场趣味运动会，在气球接力、多球搬运接力跑等项目中，校企间不仅体现了合作，而且实现了共赢。该学院负责人介绍说，学院每年培养的学生有很大一部分到互联网企业就业，这种近距离的校企联谊沟通，有助于学生了解学习方向，为实习和就业打下良好的基础。 新闻链接：http://epaper.jhnews.com.cn/site1/jhrb/html/2010-11/07/content_1206920.htm', 'http://info.jhc.cn/2012/1109/c201a1896/page.htm', 'http://info.jhc.cn/_upload/article/images/72/58/6f20a5ab47588c0014f429ab4dfe/f74819fb-b1c3-4991-8d9f-c321a6d9c083.jpg', '2017-05-17 14:18:18.856444');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (358, '', '王伟斌', 'admin', '2012-03-31 00:00:00', '信息学院组织省内学生顶岗实习现场检查工作', '为进一步了解学生在企业顶岗实习情况，增进与学生顶岗企业的联络，切实做好顶岗实习指导工作，3月26日至4月1日，信息学院组织教师分16组赴杭州、宁波、温州等省内各地市近500家企业开展顶岗实习现场检查与企业调研工作。 检查组教师每到一个企业便详细询问同学们的工作状况、遇到的困难及日常生活等各方面情况，聆听学生对顶岗实习工作的体会，鼓励大家积极做好岗位工作，提升实践技能，认真完成学校关于毕业设计、顶岗实习的各项要求。检查过程中，教师们还积极与学生所在企业进行交流沟通，向企业了解学生的工作表现，收集企业用人评价意见，征求企业顶岗实习工作建议，了解企业招聘计划及用人需求，以此掌握第一手的企业人才需求与人才评价标准，反馈到人才培养过程中，提高学生就业竞争力。 信息学院已全面实施网络化毕业教学环节管理。作为“12345”顶岗实习管理模式的一项内容，省内顶岗实习现场检查常态化工作得到了学生的认可和企业的好评。学院期望通过这一举措，强化顶岗实习过程管理，提升顶岗实习整体质量，同时为学院专业建设更好地结合实践、贴近企业实际积累有效信息，进一步优化人才培养工作，最终实现学生的优质就业。 ( 文 陈静泓 )', 'http://info.jhc.cn/2012/1109/c201a739/page.htm', 'http://info.jhc.cn/_upload/article/images/1a/da/8a6c0243463395698ea9886f1a19/12a832d7-5c5b-4ea5-9a4d-49c7826f7d9f.jpg|http://info.jhc.cn/_upload/article/images/1a/da/8a6c0243463395698ea9886f1a19/ada8d1ba-82a3-4024-b25b-467750bebb9a.jpg', '2017-05-17 14:15:57.112915');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (359, '', '王伟斌', 'admin', '2012-03-28 00:00:00', '信息学院开微博促学生就业', '当前正值2012届毕业生择业高峰期，为进一步做好2012届毕业生就业工作，信息学院日前开通“就业微博”，积极开拓毕业生就业新渠道。 近年来，微博用户人数急剧增长，在校生使用量极其庞大。信息学院顺应这一形势，利用微博传递信息快速的优势，及时将企业的招聘信息、学院的就业指导工作准确地传递给每一位毕业生，进一步丰富就业信息发布平台形式，也为毕业生求职提供方便，此举受到了毕业生们的欢迎和好评。微博开通当天发布招聘信息57条，被110多人关注并转发。 ( 文 赵倩 )', 'http://info.jhc.cn/2012/1109/c201a746/page.htm', NULL, '2017-05-17 14:15:57.134125');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (360, '', '王伟斌', 'admin', '2012-03-23 00:00:00', '创新教学模式，开拓学生视野，提升专业建设水平', '3月22日，信息学院安排医仪1001班同学参加了在杭州举行的第25届中国（浙江）国际科研、医疗仪器技术设备交流展览会，通过这一创新教学形式，开拓了学生视野，提升了专业教学效果。 据悉，第25届中国（浙江）国际科研、医疗仪器技术设备交流展览会是浙江省最具规模及影响力的国际医疗器械领域年度盛会。展览会通过整合资源，为国内外医疗器械厂商提供了一个出口和内贸的平台，每次年会上都会同期邀请政府、医院、专家、学者、厂商举办多场学术论坛，因此吸引和聚集了众多医疗器械公司参展。 参观过程中，专业教师利用展览会上琳琅满目的仪器设备及其他丰富载体，结合学校学习的知识，融会贯通，给同学们现场授课，在“实境”中让学生理解课堂知识，感受专业氛围。听了老师讲解，许多同学说，展览会上见到了很多不同类型的医疗器械。平时在课堂上有些只能通过电脑看到的呼吸机、碎石机，在展览会上看到了实物，对设备的立体结构了解更为深入；平时接触和操作的中频治疗仪、超声雾化器等器械，构造比较单一，在展览会上接触了种类更丰富且更先进的仪器；还见识了很多课堂上没有学过的多功能止痛及肌肉训练系统、视力筛查仪、清洗机、视频检耳镜等设备。接触到的很多影像设备，使同学们对课堂学到的理论知识有了更深的体会；聆听的专家讲座，感受了一次校外专家教学，专家们的精彩演讲，使同学们对医院的管理有了更多的了解。 信息学院组织此次活动，旨在通过展览会，让同学们对专业的就业前景及发展空间有更详细的定义和更明确的规划，认识专业的内在潜力和无限活力。展览会上，许多公司负责人提出的基础扎实、素质高、学习能力强的人才观，对同学们今后在学校的学习生活有了指引作用，即需更加注重对基础知识的巩固，注重良好心态和正确、高效的学习方式的培养。 本次活动，因为效果良好，学院将总结经验，今后更多开展此类活动，提升专业建设水平和课程教学效果。 （ 文 徐华丹 ）', 'http://info.jhc.cn/2012/1109/c201a749/page.htm', 'http://info.jhc.cn/_upload/article/images/ab/da/da2684a742f3ae4ddf6856aa80b2/18f4ae4d-a8c6-4a04-aec6-0a383734c877.jpg', '2017-05-17 14:15:57.156303');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (361, '', '王伟斌', 'admin', '2012-03-20 00:00:00', '信息学院召开辅导员科研立项工作动员会', '3月19日，信息学院召开辅导员科研立项工作动员会，以此推动辅导员“职业化、专业化、专家化”建设。 进一步完善辅导员工作机制，健全辅导员管理制度，促进辅导员队伍“三化”建设，形成一支稳定的辅导员工作专职队伍是近几年信息学院着力努力的目标。动员会上，学院党总支书记朱登胜结合自身科研经历，对如何进行科研立项、科学选题作了经验介绍。他说，辅导员在申请立项时，一定要认真完成 “选好题目、组建好团队、撰写好材料”等一系列重要工作，结合自身实际有针对性的参与课题研究；要不断提高认识，积极参与科研工作，在课题研究过程中加快自身的“三化”建设水平和能力。 （ 文 汪铭铠 ）', 'http://info.jhc.cn/2012/1109/c201a758/page.htm', NULL, '2017-05-17 14:15:57.178511');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (362, '', '王伟斌', 'admin', '2012-03-20 00:00:00', '民盟金职院支部开展“婺文化考察调研”活动', '3月18日，民盟金华职业技术学院支部由主委王成福带队，前往兰溪长乐村等开展“婺文化和生态旅游考察调研”活动。 长乐古村是一个具有悠久历史的古村落，地处金华、衢州、杭州三市交界处，自古就有“一家饭熟三县香”之说，素有“长乐福地”之称，1996年11月20日被列为全国重点文物保护单位。长乐古村是我国江南唯一的帝王文化景区，相传朱元璋在此参拜北斗而得福、得人、得天下，长乐古村离热闹的诸葛村只有1公里，却仍然保持着那份古朴和宁静，保存着完好的明清古建筑127处，人们在里面生生不息，传承着古老的八婺文化。 盟员们兴致勃勃地参观了供奉一代大儒、浙东学派中坚、素有“北山四先生”之称的金履祥的金大宗祠，对南宋理学家金履祥的家谱以及他的理学思想进行了考察；参观了明代刘伯温与宋濂的授书房、望云楼、和园、象贤厅、嘉会堂等，精美绝伦的古建筑让人流连忘返，使盟友们对八婺文化的博大精深印象深刻。 活动中，结合盟市委布置的调研课题“做精做特金华旅游业”和施新老师主持的2011年度省哲社规课题——“北山四先生” 及“四书”学研究进行了相关的考察、调研活动。此次活动为盟友们对婺文化传扬和金华旅游业的发展出谋划策提供了第一手资料，又培养了团队协作能力，增强了盟组织的凝聚力。 （ 文 杨海维 金宏伟 ）', 'http://info.jhc.cn/2012/1109/c201a754/page.htm', 'http://info.jhc.cn/_upload/article/images/36/55/1588957c4cfbb76fd0c653ae5743/a53dca41-eefc-4064-9de8-ba031f68aac6.jpg', '2017-05-17 14:15:57.201086');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (363, '', '王伟斌', 'admin', '2012-03-16 00:00:00', '信息学院召开“合丰”服务外包创业平台建设与课改衔接研讨会', '3月14日，信息学院举行服务外包创业平台建设与课程衔接研讨会。合丰公司总经理孙宇烽、信息学院党总支书记朱登胜等校企有关领导及多媒体设计相关教师、“合丰”班全体学员参加会议。 会上，“合丰”班校企负责人分别对班级工作开展及学员跟单近况进行了汇报。围绕“校企共建学生创业平台、促进设计外包驻地校园”的目标，与会人员紧密结合校企当前工作实际，从服务外包班的管理模式、创业平台框架与运行模式、与媒体设计类课程的衔接等议题献计献策，并就驻企集训、创业工作室组建、专任教师入企实践、企业项目衔接课程、共同编写实训教材等方面达成了一致意见，校企为创业平台的建立从人员、场所等方面进行了妥善安排，研讨会达成了预期目标。 日前，金华市人大会议出台关于大力发展服务外包的最新政策，美国等发达国家也大力支持外包业务发展，服务外包成为当前解决大学生就业困境的有效举措。信息学院“合丰”服务外包班“师徒对接工作室”创业模式是一种可持续性强、与课程教学结合紧密的外包创业人才培养模式，较之以就业为目标的校企合作订单培养，是一种自主创新能力更强的“新型”从业模式，校企双方对此进行深耕细作，一定能实现学校、企业、学生的多赢，极大促进校企利益共同体建设。 （ 文  宣翠仙 ）', 'http://info.jhc.cn/2012/1109/c201a764/page.htm', 'http://info.jhc.cn/_upload/article/images/f8/62/2dd9405c4d6196bb8e0f7185f79c/89b0655f-3460-442f-a17e-22521b04dc52.jpg', '2017-05-17 14:15:57.223069');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (365, '', '王伟斌', 'admin', '2012-03-15 00:00:00', '信息学院党校第十六期入党积极分子培训班举行结业典礼', '3月15日，信息学院党校第十六期入党积极分子培训班举行结业典礼。学院党校校务委员会成员、党校第十六期培训班班主任及全体学员参加典礼。 本期培训班232名学员在为期半年的学习中，顺利完成理论授课、社会公益活动、优秀共产党员影片欣赏、暑期社会实践、结业论文撰写、结业考试等各个环节的教学与考核，179名学员顺利结业，并有通信111班石洁等11名同学被授予党校第十六期入党积极分子培训班优秀学员荣誉称号。 朱登胜书记在典礼上勉励党校学员们要坚定马克思主义信仰和社会主义信念，培养高尚的爱国主义情操，树立为人民服务的意识，要努力学习，深刻领会党的路线、方针、政策，在学习和工作中发挥模范带头作用，为学院“十二五”规划顺利开展贡献自己的力量。 ( 文 周荃 )', 'http://info.jhc.cn/2012/1109/c201a769/page.htm', 'http://info.jhc.cn/_upload/article/images/8c/4d/89bc26504cc38a4e73b3e603ceb1/fc0afdc7-b556-4f75-85de-c1230e086e04.jpg', '2017-05-17 14:16:09.878644');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (366, '', '王伟斌', 'admin', '2012-03-14 00:00:00', '信息学院学生见义勇为行为受表扬', '3月13日，保卫处向信息学院发来感谢信，高度评价我院应电专业蒋浩、林辉能同学见义勇为行为，并对学院悉心培养出品学兼优的学生表达谢意。 表扬信说，针对学校就餐时间学生拥挤多次被犯罪份子行窃作案，蒋浩、林辉能两位同学利用自己学生身份不易被犯罪分子察觉的特点，多次配合保卫处深入反扒第一线进行反扒工作。在他们提供的线索下，校保卫处成功抓获两名犯罪分子，为学校平安稳定做出了自己的贡献。 此外，学院电子商务专业崔佳琪同学于3月2号早晨在学校西门转角美食门口遇见一名犯罪分子正在盗窃手机，当即就把小偷抓住并扭送到学校保卫处。后经西关派出所查实该犯罪分子系专门蹲点学校西门的惯偷，已经有不少学生受害。蒋浩、林辉能、崔佳琪等同学见义勇为的行为体现了其高尚人格，是师生们学习的榜样。 信息学院长期以来一直把做好德育工作放在教育工作首位，在学生中大力倡导发扬团结互助、文明友爱、助人为乐、拾金不昧、见义勇为等传统美德，蒋浩、林辉能、崔佳琪等同学是学院涌现出的优秀学生代表，他们的勇敢得到了师生们的普遍好评。 （ 文 汪铭铠  ）', 'http://info.jhc.cn/2012/1109/c201a777/page.htm', 'http://info.jhc.cn/_upload/article/images/1c/16/01ff9e754e1e9ce3ca19e9dcf93e/4eb8fd00-fcff-4f7f-91a9-20f63ab78970.jpg', '2017-05-17 14:16:09.91284');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (367, '', '王伟斌', 'admin', '2012-03-14 00:00:00', '信息学院第五届“5173订单班”召开实习沟通会', '3月13日，信息学院第五届“5173订单班”实习沟通会在金华利诚信息技术有限公司举行。公司方施展副总经理、信息学院陈海荣副院长及校企相关部门负责人、订单班全体学员参加会议。 订单班优秀学员代表施青介绍了自己在5173的成长经历，他鼓励大家要努力做好本职工作，在简单的岗位上争取做出最好的成绩。校企双方都十分重视订单班同学的成长，都提供最好的指导老师为订单班同学提供服务。会上，针对学员提出的毕业设计、就业协议等毕业环节的问题，实习时间与转正期限、实习管理、岗位操作中的细节等企业方问题，校企双方负责人一一做了解答，使学员们了解了政策，有了努力的方向。 最后，校企双方领导都对订单班学员提出了殷切期望，希望订单班学员要抓住公司和学院为大家创造的机会，在岗位勇挑担子、不断学习，与公司的其他员工一起进步，让自己的明天有一个更好的发展。 据悉，金华利诚信息技术有限公司是高新IT学院理事会单位，也是学校的示范性校外基地，多年来一直与信息学院开展订单培养的合作。5年来已经累计培养了200余名学员。本届订单班在实习时间安排、晋升机会、订单班教材开发、订单班交流沟通等方面，都做了积极的探索与改进，后续校企双方将在订单培养模式、项目合作、服务外包等方面开展更加深入的合作。 （ 文 楼小明 ）', 'http://info.jhc.cn/2012/1109/c201a775/page.htm', NULL, '2017-05-17 14:16:09.93514');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (368, '', '王伟斌', 'admin', '2012-03-13 00:00:00', '学无止境，在工作岗位上永葆党员先进性', '为全面落实党建育人功能，切实加强大学生思想政治工作，确保大学生党员发展的质量，近期，信息学院就毕业生党员的就业去向、在岗工作情况等展开了专题调研。 近三年我院毕业生党员就业率均为100%。毕业生跟踪调查的数据显示，毕业生党员主要在民营企业从事生产、管理和服务一线的技术工作，他们在平凡的岗位上积极发挥共产党员的先锋模范作用，许多毕业生目前都已成为企业骨干力量。调查中，许多党员也表示，企业的党组织建设还很薄弱，组织生活不像在校期间那么稳定，要保持党员的先进性，就必须自身加强党的基础理论学习，关注党的方针政策和国内外动态，与时俱进，才能不断提升自己。 近年来，信息学院通过支部生活会、党建进社团、支部QQ群、网上党校、党员服务队等载体扎实推进学生党建工作，注重对大学生党员党的理论知识的学习，加强大学生党员的身份教育，不断提高他们的社会实践水平和党性修养，在提升党员素质，促进党建带动学院发展方面发挥了积极作用。 （ 文 周荃 ）', 'http://info.jhc.cn/2012/1109/c201a782/page.htm', NULL, '2017-05-17 14:16:09.9571');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (369, '', '王伟斌', 'admin', '2012-03-12 00:00:00', '信息学院召开兼职教师教学规范培训会', '为提升兼职教师的教育教学水平，规范兼职教师的教学常规工作，让兼职教师了解学院的教学工作流程与基本的教学管理，3月10日，信息学院召开兼职教师教学规范培训会，会议由教科办组织，学院督导、项目部主任也一同参加培训会。 会上，教科办老师介绍了学院教学管理流程与各教学环节的教师工作规范，向兼职教师展示了各类教学过程材料范本，还特别向兼职教师介绍了学院网络化教学管理情况与相关功能模块的具体操作方法。学院督导就以往兼职教师容易出现的教学问题作了相应点评。 兼职教师的工作实践经验是课程实施环节一个有益的补充，本学期信息学院所聘请的兼职教师均来自于企业一线，都有丰富的工程实践经验，这些兼职教师也为促进校企融通，增加校企合作搭建了沟通桥梁。 （ 文 邱晓华 ）', 'http://info.jhc.cn/2012/1109/c201a785/page.htm', NULL, '2017-05-17 14:16:09.979422');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (370, '', '王伟斌', 'admin', '2012-03-09 00:00:00', '让早餐远离教室，让文明靠近心灵', '为加强学生的自我管理和文明意识，近日，信息工程学院学工办团委向全院师生发出“让早餐远离教室，让文明靠近心灵”的倡议，倡议内容包括：穿工作服进教室、不将早餐带入教学区、下课后带走属于自己的东西、党员学生干部以身作则、见到不良现象主动劝阻敢于提醒等。此次倡议活动在提高学生自律意识的同时，维护了正常的教学秩序，更有效地促进了学院的学风建设。 （文 金乐 ）', 'http://info.jhc.cn/2012/1109/c201a792/page.htm', NULL, '2017-05-17 14:16:10.001816');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (371, '', '王伟斌', 'admin', '2012-03-09 00:00:00', '“两会”在身边', '3月9日，金华电视台“小马开讲”栏目记者来到信息学院采访副院长王成福教授，就其在今年金华政协会议提交的《关于加快市区公共交通“油改气”发展的建议》提案进行采访报道。 据悉，该提案针对当前金华空气质量呈现恶化趋势，而人们对生存环境和生活质量要求不断提高，政府对环境污染治理的逐年重视的背景，对在市区行驶的公交车和出租车中实施“油改气”项目，从而减少市区空气污染，降低公交车和出租车的运行成本等分析了原因、提出了对策与建议。由于该提案是在国务院出台新的《环境空气质量标准》，增加PM2.5等检测指标的大背景下提出的，具有极强的针对性，因此受到了媒体的重点关注。此次“小马开讲”栏目，正是看到了该提案的重要意义和价值，特意上门采访提案当事人。 王成福教授是金华市政协委员，民盟金职院支部主委，一直致力于环境质量检测等研究，他所主持的《微细粉尘荷电凝并机理及其捕集策略研究》浙江省自然科学基金项目组成员，近几年有针对性的对金华空气质量情况进行了研究，提出了许多环境治理的具体举措，研究在提升学院教师科研水平的同时，也具有很强的社会价值，为金华环境治理工作提供了科学依据。此次这方面的研究在金华两会上得到关注和报道，相信一定能产生更大的社会效应，为金华环境治理工作做出积极贡献。 （ 文 王伟斌 ）', 'http://info.jhc.cn/2012/1109/c201a788/page.htm', 'http://info.jhc.cn/_upload/article/images/62/4a/61b6e39f4bf4ade2c4561f423b9c/b812d087-8055-4d34-a9a9-ece510756190.jpg', '2017-05-17 14:16:10.023865');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (372, '', '王伟斌', 'admin', '2012-03-06 00:00:00', '信息学院落实新学期科研工作', '3月6日，信息学院召开专题会议，传达和落实学校科研工作会议精神，部署新学期学院科研工作重点，副院长王成福主持会议。 王成福说，按照学校科研工作会议精神和学院2012年科研工作重点，今年学院的科研工作一方面要继续抓好教师科研能力和水平的提升工作，充分挖掘教师个体潜能，帮助和引导教师做好科研规划，另一方面要鼓励和引导教职工之间加强合作，培养团队合作意识，抓高层次课题立项、项目结题、社会服务能力的培养，提升学院科研整体实力。 会上，与会人员讨论学习了浙江省自然基金项目申报、金华市科技局项目申报等的政策研究和方法技巧，对学院在研的28个厅局级以上项目进展进行排查并逐个提出了结题要求，交流了学院2012年度科研考核指标。本次会议为学院新学期的科研工作指明了方向。 （ 文 邱晓华 王伟斌 ）', 'http://info.jhc.cn/2012/1109/c201a796/page.htm', NULL, '2017-05-17 14:16:10.046283');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (373, '', '王伟斌', 'admin', '2012-03-05 00:00:00', '信息学院多举措推进“三困生”就业工作', '毕业在即，做好毕业生就业指导和服务工作成为目前学院重要工作之一，而“三困生”的就业是信息学院十分关注的。为了引导学院“三困生”树立正确的就业观，及时了解“三困生”就业思想动态和需要，信息学院开设了“24小时就业帮助热线”、“就业服务QQ”、“信息就业QQ群”等多个就业平台，为“三困生”就业保驾护航。 “24小时就业帮助热线”由信息学院具备专业职业指导资格的教师负责接听，实行24小时开机，为毕业生提供就业心理辅导，工作岗位推荐，解答毕业生就业过程中遇到的困惑等服务内容，以便“三困生”多渠道地掌握就业信息，提升就业机会。该热线开通至今每天都会接到几十个毕业生求助电话，目前已成功推荐就业21人。“就业服务QQ”、“信息就业QQ群”等平台也在提供就业岗位信息、发布就业政策信息等方面发挥了积极作用，目前信息学院毕业生就业工作正稳步推进。   （ 文 赵 倩 ）', 'http://info.jhc.cn/2012/1109/c201a805/page.htm', NULL, '2017-05-17 14:16:10.068487');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (374, '', '王伟斌', 'admin', '2012-03-05 00:00:00', '一个电话，“雷锋”进家', '3月4日，信息学院 “学雷锋志愿服务小分队”一行四人来到金华市凤凰山社区，为居民沈谓涛一家提供电脑维护、维修、电脑知识普及等服务。居民沈渭涛一家是通过“信息工程学院学生党员先锋服务热线”联系上该服务队，接到电话后，该服务队在一个工作日内上门服务，受到社区居民的广泛欢迎和一致好评。 据悉，该服务队由信息学院学生党员、学生专业社团、学生干部组成，开通服务热线的目的是为了发挥党员先进性的同时，利用学生的专业技能服务大众、回馈社会。 （ 文  金 乐  汪铭铠 ）', 'http://info.jhc.cn/2012/1109/c201a799/page.htm', 'http://info.jhc.cn/_upload/article/images/40/8f/bed44fed46b99ccbb13c44bebdfd/55f8af79-0c5a-41bc-9cd7-9dd511c7ed6b.jpg', '2017-05-17 14:16:10.090514');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (375, '', '王伟斌', 'admin', '2012-03-02 00:00:00', '信息学院积极谋划新专业校企利益共同体建设', '2月29日，深圳进林科技有限公司总经理仲岩、杭州豪恩电子有限公司经理阳鑫等一行5人参观考察信息学院，就校企双方在楼宇专业与物联网方向人才培养、专业发展、实训室建设、构建产学基地等方面开展长期合作进行了沟通交流。 信息学院新增的物联网专业将于今年开始招收新生，作为一个新专业，又是一个目前非常热门和紧缺的专业，如何在专业规划、专业设置等方面走在前列，很好的培养社会急需人才，从企业吸取对专业发展的意愿就显得尤为重要。深圳市进林科技有限公司是一家产学研一体的专门从事智能交通系统民营高科技企业，是国内最早的专业ITS产品供应商之一。杭州豪恩电子有限公司是主营集成防盗报警系统的浙江知名企业，两家企业在楼宇和物联网方面都有丰富的产业经验和强大的产品开发能力。 通过参观物联网体验中心建设场地和现有楼宇安防实训室，企业方看中了信息学院未来物联网体验中心作为企业新产品展示中心的区域辐射效应，同时也充分认可学院一贯坚持的校企合作的办学理念。信息学院则看到了行业内知名企业主流产品的引进对人才培养和课程改革上带来的巨大推动力，正是基于双方的“校企利益共同体”建设需要，此次企业负责人考察后对双方合作表达了强烈愿望，为校企双方长期合作打下了坚实的基础。 （ 文 朱墨池 ）', 'http://info.jhc.cn/2012/1109/c201a816/page.htm', NULL, '2017-05-17 14:16:10.113642');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (376, '', '王伟斌', 'admin', '2012-03-02 00:00:00', '新队伍 新责任 谋求新发展', '3月1日，信息学院召开新一届学生干部任职暨“树人树德”动员大会，学院党总支书记朱登胜、全体学工办成员和32名新上任的学生干部参加会议。 朱登胜对此次当选的32名学生干部表示祝贺，并指出，2012年是我院加快发展建设的重要一年，是我院以党建工作为引领，培育高素质的党团学生干部队伍，促进学院学风建设的重要一年。新一届的学生干部要做好为同学们服务的充分准备，在服务的过程中锻炼成长，并希望他们能够加强学习，不仅是干部之间的学习，还要向指导老师学习，学习工作和处事等方面的经验，从而提高自己各方面的能力，为学院的建设添砖加瓦。 会上，院领导为任职人员颁发了聘书，学生干部代表作了入职宣誓。 （ 文 汪铭铠 金乐 ）', 'http://info.jhc.cn/2012/1109/c201a811/page.htm', NULL, '2017-05-17 14:16:10.135158');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (377, '', '王伟斌', 'admin', '2012-03-02 00:00:00', '信息学院加强“环境减排控制技术”方向科学研究', '近日，浙江省“十二五”重大科技专项实施方案和国务院新修订的《环境空气质量标准》先后发布和实施，国家和浙江省层面都对环境治理工作提出了新要求、新目标。这对高校在环境治理方面的科研工作也提供了一次难得的机遇。 信息学院环境治理科研小组在十多年科学研究与社会服务的基础上，紧紧围绕省自然基金项目“微细粉尘荷电凝并机理及其捕集策略研究”研究工作，日前又与国家级高新技术企业——金华大维电子科技有限公司开展了环境治理方面的校企合作。本次合作，我校教师将和企业工程技术人员共建技术攻关团队，下一步重点开展微细颗粒物（PM2.5）减排治理和新一代电除尘高频脉冲电源的试验研究，并适时推出相关研究产品。本次校企合作及相关研究，相信在提升我院教师科研水平和社会服务能力的同时，也能为完成我省大气污染物减排任务、促进环保产业发展、全面推进生态文明建设做出积极的贡献。 （ 文 王成福 ）', 'http://info.jhc.cn/2012/1109/c201a808/page.htm', NULL, '2017-05-17 14:16:10.157366');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (378, '', '王伟斌', 'admin', '2012-02-29 00:00:00', '学习雷锋精神，做新时期的好青年', '在3月5日雷锋纪念日即将到来之际，为了更好的弘扬雷锋精神，号召学院同学做新时期的好青年，信息学院各支部开展了丰富多彩的学雷锋活动。 2月28日，信息学院计算机专业学生支部召开“学习雷锋，争做郭明义式活雷锋”主题学习会。学习会上，大家重温了郭明义同志参加工作30多年来，始终牢记党的宗旨，立足本职岗位，大力弘扬雷锋精神，勤恳敬业、乐于助人，做出的不平凡感人事迹。大家一致认为，郭明义同志是助人为乐的道德模范，是新时期学习实践雷锋精神的优秀代表，是当代优秀共产党员的楷模。支部师生党员表示要学习和发扬郭明义同志牢记宗旨、坚定信念的优秀品质；乐于助人、无私奉献的高尚品德，要用他的精神去感染、激励更多的人，唤起大家的奉献精神，推动学院争先创优活动的深入开展和创新发展。 信息学院应电专业学生支部、软件与商务专业学生支部等也开展了重温雷锋事迹、从身边的一件好事做起等主题活动。学习活动中，许多学生党员表示，雷锋生活的时代虽然离我们有些久远，但他的螺丝钉精神、他的“有限生命投入到无限的为人民服务中去”的言语，依然如春风细雨般滋润着自己，感染着自己，成为自己做好事，做新时期好青年的强大精神力量。 （文 王伟斌 余经纬 陈德仙 ）', 'http://info.jhc.cn/2012/1109/c201a820/page.htm', 'http://info.jhc.cn/_upload/article/images/89/b4/228d7be94abfbe769dc8b3954c67/3d7e92dd-9af7-4b72-a568-c9a934a0597c.jpg|http://info.jhc.cn/_upload/article/images/89/b4/228d7be94abfbe769dc8b3954c67/5a7dff9b-0084-4c77-9500-50ecdb96385a.jpg', '2017-05-17 14:16:10.179732');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (379, '', '王伟斌', 'admin', '2012-02-28 00:00:00', '金华高新IT学院“合丰服务外包班”顺利完成校企“师徒”对接', '开学伊始，金华高新IT学院首届“合丰服务外包班”同学与企业完成“师徒”对接，顺利进入国际菜单印务设计跟单阶段。合丰公司设计部与信息化事业部相关老师参加了现场对接活动。 对接活动围绕前期试单互评、师徒现场互认、分组对接交流、互动平台搭建与运行等主题展开。首先由全体同学对前阶段的试单成品进行了展示汇报，公司设计部进行逐一点评。公司根据各位同学的技术差异与项目角色的组合需求，对学生进行了异质分组，并与8名企业师傅进行了多对一师徒互认。 对接活动中，企业师傅与学生徒弟就接前训练流程、绘图能力强化、角色分工与合作等双方关注的热点进行了激烈讨论。活动最后，校企双方在互动平台搭建、远程与现场双向互动周期与方式上达成了共识，并通过远程平台进行了为学生“量身定做”的第一单的发放。 据悉，合丰服务外包班是金华高新IT学院与金华高新技术园区探求合作育人途径，深化工学结合、校企合作人才培养模式，共同培养服务外包创业人才的一次积极探索，也是我校承接国际菜单印务的首次尝试。 （ 文 宣翠仙 ）', 'http://info.jhc.cn/2012/1109/c201a831/page.htm', 'http://info.jhc.cn/_upload/article/images/63/5e/431a2f1d4c84affabf21c08e938b/90ee7fae-154a-4bb0-898b-603574e2e3a6.jpg', '2017-05-17 14:16:23.161857');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (380, '', '王伟斌', 'admin', '2012-02-28 00:00:00', '信息学院多举措高效完成综合素质测评工作', '日前，信息学院新一轮学生综合素质测评工作顺利完成，本次测评工作，学院在认真研究学校出台的综合素质测评新办法基础上，结合本学院的特点和以往操作的经验，还有针对性地制定了《信息工程学院综合素质测评补充细则》等新举措，保障了测评工作的高效展开。 补充细致明确指出，综合素质测评在于鼓励学生全方位发展个人能力，测评中单项加分不封顶，扣分不封底的规定，为学生发挥、发展个人能力提供更广泛的平台。细则规定，评选表彰优秀学生，以量化的综合素质测评为依据，对参评奖学金的学生作了智育成绩上的硬性规定。为加强过程管理，学院要求每位学生填写综合测评表，详细记录每位学生的加减分情况，客观真实的展现综合素质的有机组成部分。在具体操作上，学院利用学生计算机操作能力强的特点，在数据汇总等方面制作了易于操作的统计表格，充分体现计算机的优势，同时也能非常直观的体现出综合素质各个成分因素情况。 出台补充细则、制作简洁明了可操作性强的新统计表格等一系列举措大大提高了测评工作的效率，本次综合素质测评过程也受到了全院师生的欢迎。 （ 文 汪铭铠 ）', 'http://info.jhc.cn/2012/1109/c201a827/page.htm', NULL, '2017-05-17 14:16:23.191533');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (381, '', '王伟斌', 'admin', '2012-02-28 00:00:00', '信息学院全面启动党团学生干部培养工程', '新学期，信息学院全面启动党团学生干部培养工程。具体内容包括：完善学生干部管理和考核机制、专题讲座、学风建设座谈会、“感恩青春 回馈社会”学生党员先锋队活动、辅导员讲堂、素质拓展、年度总结评比等系列活动。 此项工作着力于调动学生干部的工作热情，提高学生干部的道德素养、业务素质，树立学生干部的全局意识、服务意识、责任意识、创新意识，从而更好的服务于同学，服务于学院，促进学院学风建设。 （ 文 金乐 ）', 'http://info.jhc.cn/2012/1109/c201a824/page.htm', NULL, '2017-05-17 14:16:23.213765');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (510, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '校企游园', '“加油，加油……”昨天，金职院操场上不时传出阵阵加油声，这是该院信息学院和合作企业5173网络游戏服务公司开展的校企游园活动现场。25米往返多球搬运混合接力赛、动感赛跑、飞镖大战等多项趣味运动项目，让校企双方的参与者在游玩中加深了认识。据了解，5173企业和该院信息学院合作已有4年，也是该学院部分学生的实践基地和就业平台。 （本报记者 缪小芬 摄） 新闻链接：http://epaper.jhnews.com.cn/site1/jhwb/html/2010-11/08/content_1207477.htm', 'http://info.jhc.cn/2012/1109/c201a1890/page.htm', 'http://info.jhc.cn/_upload/article/images/bc/8f/b6437b0f40f3a6a17c930655032d/d8cf15a9-4bcd-44a0-910e-c5c5aa5d3c5f.jpg', '2017-05-17 14:18:18.878636');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (382, '', '王伟斌', 'admin', '2012-02-23 00:00:00', '我院2011年度自学考试工作受到好评', '近日，金华市高等教育自学考试办召开我市2011年度自学考试和非学历考试工作总结和2012年度工作部署会议。会上下发了金市高教自考办〔2012〕1号文件，表彰了2011年度金华市自学考试工作先进集体和先进工作者。我校信息学院和教务处获评2011年度“自学考试优秀考点”荣誉称号，成教学院吴维昕老师被评为2011年度“自考工作先进工作者”。 来自全市各县（市）、高校分管自考工作的同志参加了本次会议，会上各单位作了经验交流发言。我校自学考试工作在成为自学考试与职业教育区域衔接沟通中心的基础上，通过努力，2011年又成功取得自考专科专业主考学校属地化试点（全省仅两所）学校之一，这为我校学生自考享受主考校优惠政策和促进自考助学的发展起到了推进作用。这些成绩的取得，在会上普遍得到了市自考办和兄弟院校的肯定和认可。 （ 文 单天德 ）', 'http://info.jhc.cn/2012/1109/c201a835/page.htm', 'http://info.jhc.cn/_upload/article/images/26/5c/b7d6831c46538b86c494ea795af9/03977a3c-c67c-4172-bdc2-e24d759fb0ae.jpg', '2017-05-17 14:16:23.235966');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (383, '', '王伟斌', 'admin', '2012-02-11 00:00:00', '信息学院继教部获评市直机关“共产党员示范岗”荣誉称号', '近日，市直机关下发市机党〔2011〕55号文件，表彰2011年度获得金华市直机关“共产党员示范岗”称号的单位，信息学院继续教育部榜上有名。 据悉，此次评选“共产党员示范岗”活动，是市直机关为有效增强基层党组织的凝聚力和战斗力，巩固和保持共产党员先进性教育活动和机关效能建设，转变机关工作作风，优化我市发展环境，推动经济社会又好又快发展而开展的，共有23家市直机关党组织获此殊荣。 信息学院继续教育部在此次创建活动中，紧紧结合学院中心工作，对照标准，精心组织，扎实创建，努力提升创建质量和效果，真正发挥好带头示范作用，把创建活动作为一项巩固、深化保持共产党员先进性教育活动的长效机制，认真抓实、抓好、抓出成效，部门整体党建工作迈上了一个新台阶，为促进学院发展做出了突出贡献。此次继教部获此殊荣，也是对我院党建工作的一次充分肯定，必将对我院共产党员先进性教育和“创先争优”活动起到积极的促进作用。 （ 文 王伟斌 ）', 'http://info.jhc.cn/2012/1109/c201a841/page.htm', NULL, '2017-05-17 14:16:23.258133');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (384, '', '王伟斌', 'admin', '2012-01-12 00:00:00', '民盟金职院支部到金东信息产业园调研', '为了解金华市信息产业发展情况，为金华市两会提供高质量的提案做准备，民盟金职院支部计划到金华高新技术产业园、浙中信息产业园、金东信息产业园等信息产业园进行调研。1月11日，民盟支部一行三人在王成福主委率领下，到金东信息产业园调研。 金东信息产业协会周亚谊会长对金东信息产业园基本情况、2012年重点项目以及存在的问题等进行了介绍，然后调研人员分别就金东信息产业园的规模、政策及目前存在的土地、中高端人才等问题与周会长进行了交流，并实地考查了合丰等部分骨干企业，双方在热烈友好的氛围中圆满完成了调研活动。 盟员金宏伟、陈桂兰参加了调研活动。     （ 文 王成福 ）', 'http://info.jhc.cn/2012/1109/c201a848/page.htm', NULL, '2017-05-17 14:16:23.280422');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (385, '', '王伟斌', 'admin', '2012-01-12 00:00:00', '民盟金职院支部荣获盟市委参政议政先进集体', '刚从民盟金华市委员会表彰大会获悉，由于民盟金华职业技术学院支部在参政议政方面贡献突出，被民盟金华市委员会评为参政议政先进集体。同时，盟员金宏伟、刘根华被盟市委评为参政议政先进个人。 据悉，近年来民盟金职院支部紧紧围绕市委市府关于发展经济、构建和谐社会、关注民生民意等中心问题和社会热点问题，精心组织团队调研，拓展参政议政的渠道和平台，提高参政议政的能力水平和质量，认真履行参政议政职责。在今年金华市两会期间，民盟金华职业技术学院支部书面提交了《关于加大金职院经常性经费投入的建议》、《关于加快金华IT类人才培养给予政策支持的建议》、《关于新生代农民工问题的几点建议》、《开发武义江江心岛现代农业旅游的建议》4件提案以及报送相关信息多条。同时，积极参加市政协组织的市区备用水源的实地考察、方案研讨、提出建议等，为市政府建设市区备用水源提供参考。参加市政协组织的考察浙江今飞亚达轮毂有限公司和浙江今飞凯达轮毂有限公司，为工业企业转型升级出谋划策、提出建议，为我市经济社会发展作出了贡献。 （ 文 王成福 ）', 'http://info.jhc.cn/2012/1109/c201a845/page.htm', NULL, '2017-05-17 14:16:23.302682');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (386, '', '王伟斌', 'admin', '2012-01-08 00:00:00', '齐携手，共奋进，开创高新IT新辉煌', '12月27日晚的实验剧场，张灯结彩，欢歌笑语，由信息学院承办的金华高新IT学院2012年新春联欢晚会在此隆重举行。校党委书记李秋华、副书记余党军、金华高新IT学院各理事单位领导与信息学院师生欢聚一堂，共同庆祝新春佳节。 信息学院院长龚永坚在晚会上致辞，他回顾了2011年高新IT学院成立以来政校企三方秉承资源共享、优势互补、相互合作、共同发展理念，在提升人才培养质量、丰富校企合作内涵、出台政府引导政策、实施贴心就业服务等方面取得的丰硕成果。他说，信息学院2011年在学科与技能竞赛、专业建设、校企合作、基地建设等各方面取得了丰硕成果，这是学院积极探索与企业“合作办学、合作育人、合作就业、合作发展”，构建校企利益共同这一发展方向的一次集中成果展示。新的一年，学院将与企业继续深化合作平台，探索合作新途径，共同谱写校企合作新篇章。 晚会上，柔美的舞蹈让人如痴如醉，幽默的相声让人喜笑颜开，精彩的魔术表演让观众领略了见证奇迹的时刻，拉丁舞、肚皮舞则给大家带来了异域风情，由学院师生表演的诗朗诵《教师的誓言》和情景剧《放飞我们的梦想》，则展示了信息师生践行“知行合一、务实创新”校训，为学院发展默默耕耘的精神风貌。 晚会上，与会校企领导还应邀为信息学院2011年学生科技竞赛获奖人员、学风建设年优秀集体和个人进行了颁奖。 （ 文/摄影  王伟斌 ）', 'http://info.jhc.cn/2012/1109/c201a879/page.htm', 'http://info.jhc.cn/_upload/article/images/54/5c/97a7f1e042c1b4c35a9d4d6ac9b8/4357d245-c679-4436-9b53-591d57b2c78a.jpg|http://info.jhc.cn/_upload/article/images/54/5c/97a7f1e042c1b4c35a9d4d6ac9b8/5089d582-c770-449d-8bad-a31d73b678c3.jpg|http://info.jhc.cn/_upload/article/images/54/5c/97a7f1e042c1b4c35a9d4d6ac9b8/be246658-2647-4958-afb2-b09894eda872.jpg|http://info.jhc.cn/_upload/article/images/54/5c/97a7f1e042c1b4c35a9d4d6ac9b8/f36a3a4d-a51f-4049-a74a-ef5bf6c9bbad.jpg|http://info.jhc.cn/_upload/article/images/54/5c/97a7f1e042c1b4c35a9d4d6ac9b8/e3cd7fc7-059a-4b6d-a95d-212a0ee50f92.jpg', '2017-05-17 14:16:23.324978');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (387, '', '王伟斌', 'admin', '2012-01-08 00:00:00', '信息学院举办“合丰杯”校企平面设计技能对抗赛', '近日，信息学院与金华高新IT学院理事单位合丰信息科技（金华）有限公司共同举办首届“合丰杯”校企平面设计技能对杭赛，来自信息学院、机电学院的80余名学生和企业10余名技术人员在“电子菜单”平面设计、“校企文化”平面动画动制作两个赛场同场竞技，相互切磋。副院长胡野、教务处、艺术学院、信息学院等相关负责人到场观摩。 本次比赛是金华高新IT学院成立后校企合作的成果之一——“合丰”服务外包班人才培养模式的一次成果展示，该班采用“校内集训→课余接单→自主创业”的菜单印务设计人才培养模式。胡野在听取信息学院相关负责人对该模式的汇报后，对校企现场对抗赛的竞赛形式、学生选手的竞技水平、创业式订单班的校企合作新途径给予了充分肯定，他鼓励学生选手们努力学习专业技能，积极与企业师傅进行对接式跟班，早日实现用专业技能丰富课余生活、换取薪资酬劳的创业理想。 赛后，校企选手进行了双向交流，企业专家对学生组作品进行了逐一点评并提出改进意见。同学们纷纷表示，本次与企业人员的同场竞技，不仅检验了所学专业技能，更为重要的是得到了企业专家的现场指导，为缩短与企业技术能手之间的差距指明了方向，对自己今后的就业与发展有重要意义。 （ 文  宣翠仙 ）', 'http://info.jhc.cn/2012/1109/c201a872/page.htm', 'http://info.jhc.cn/_upload/article/images/31/8c/3150080f4939a500b61b31371a97/22b046fe-3687-4aaa-af7f-a555800b12cd.jpg|http://info.jhc.cn/_upload/article/images/31/8c/3150080f4939a500b61b31371a97/8789bdfe-52c7-4094-9f3f-9a4bfa9e0a66.jpg', '2017-05-17 14:16:23.347169');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (388, '', '王伟斌', 'admin', '2012-01-08 00:00:00', '信息学院举行2012年离退休教职工新春团拜会', '12月29日上午，信息学院举行2012离退休教职工新春团拜会，50余位老同志欢聚一堂，共叙佳节。 学院党总支书记朱登胜代表学院给老同志们带去了节日的祝福，他向老同志们介绍了学院近期工作，重点介绍了学院2011年的工作成就和工作亮点，同时也向老同志们介绍了学院明年的工作重点。他希望离退休老同志们能一如既往支持学院发展，并祝大家新年快乐、健康长寿。 另外，学院就学校有关退休人员绩效工资改革的文件精神作了传达。离退休老同志们十分关心学院发展，结合学院重点工作谈了许多见解。大家说，虽然退休了，但仍然要加强学习，仍要关心学院建设，使自己紧跟学院发展步伐，为学院发展尽绵薄之力。 （ 文 陈德仙 ）', 'http://info.jhc.cn/2012/1109/c201a866/page.htm', 'http://info.jhc.cn/_upload/article/images/12/58/c0c7d8744ab38457bfa835488c8e/120a9842-cb07-4e9d-9bf1-46b8b1f15f14.jpg', '2017-05-17 14:16:23.36942');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (389, '', '王伟斌', 'admin', '2012-01-08 00:00:00', '信息学院召开第一届校友代表大会', '12月31日，信息学院在总院四楼会议室召开信息学院第一届校友代表大会。校党委副书记余党军、校友办冯拾松教授应邀出席，信息学院领导、师生和来自全国各地的60余名校友欢聚一堂，共同庆祝校友大会召开。 余党军在大会上致辞，他说，作为信息学院第一任院长，非常高兴看到各位校友回校团聚。这几年学校发展日新月异，在教育改革、科学研究、招生就业、校园环境建设等各方面都发生了巨大变化，希望校友们多走走，多看看，通过校友会等平台，今后加强与学校的沟通，为学校发展献计献策。 信息学院院长龚永坚介绍了信息学院目前在专业、实训环境、师资队伍、科研、人才培养等方面取得的成绩。他希望校友们能通过校友会、校友理事会等载体，促进母校与校友、母校与校友所在地在科学研究、人才培养、实训基地建设、毕业生实习就业等方面的合作和交流，共同为学院发展而努力。 校友代表李万正、在校生代表徐俊杰等分别发言。大会通过了《金职院信息工程学院校友联谊会章程》、第一届理事会组成人员名单，余党军为当选的理事会成员颁发聘书。校友们应邀参观了学院实训室等教学场所，大家对学院现有的成绩表示祝贺，并对学院今后发展充满期待。校友们表示，愿意尽自己一份力量，通过校友会等载体，加强沟通，增进了解，为学院发展做出自己的贡献。 信息学院校友会第一届理事会召开了一届一次会议，讨论了理事会成员分工、理事会活动开展计划等内容。 （ 文/摄影 王伟斌 ）', 'http://info.jhc.cn/2012/1109/c201a856/page.htm', 'http://info.jhc.cn/_upload/article/images/15/3c/b72a5b494c3f9c1ecfad8f53f4ab/9923c1df-0b7e-4920-8a98-a10094333caf.jpg|http://info.jhc.cn/_upload/article/images/15/3c/b72a5b494c3f9c1ecfad8f53f4ab/81e41ddd-cb14-409c-92cd-44e5085b3d0a.jpg|http://info.jhc.cn/_upload/article/images/15/3c/b72a5b494c3f9c1ecfad8f53f4ab/8603e80b-1168-48dc-a18f-ea20070e5a0e.jpg|http://info.jhc.cn/_upload/article/images/15/3c/b72a5b494c3f9c1ecfad8f53f4ab/41f51f42-9fed-4a2e-b0e4-7bca505e2d86.jpg', '2017-05-17 14:16:23.39176');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (390, '', '王伟斌', 'admin', '2012-01-08 00:00:00', '筑内功、建平台，计算机基础实施分层教学显成效', '日前，2011年浙江省秋季计算机等级考试成绩公布，我校大一A班参加一级MS Office科目考试的4031名同学有3397人顺利通过，一次合格率为84.27%，为近年来我校学生参加浙江省计算机等级考试一次通过率最好的一年。 计算机基础分层教学是我校实施基础课教学改革的重要内容，信息学院计算机基础部紧跟基础课改革动向，以精品课程建设为抓手，通过共同备课、编写教材、提炼题典、资源共享等一系列举措，强筑内功，努力提升教师的教育教学能力，本次任教A班的41位老师中有13位所任教班级通过率达到了90%以上。另一方面，为克服A班同学上课学时少、训练时间短等问题，计算机基础部今年建成了基于WEB的计算机基础在线训练及自测平台，该平台有C/S与B/S两种模式，分别应用于课程教学与学生自主学习，其强大的统计分析功能使教师准确获知学生的实时学习状况，并可有效监督学生学习和有针对性地开展教学、训练。 一系列举措的扎实推进，有效地提高了《计算机基础》课程的教学效果和学生的计算机应用能力。本次考试取得较好成绩也很好地证明我校计算机基础教学改革取得了阶段性成果。 （ 文 邱晓华 ）', 'http://info.jhc.cn/2012/1109/c201a852/page.htm', NULL, '2017-05-17 14:16:23.413896');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (391, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院举行首届“宏新图ERP实施员订单班”开学典礼', '1月5日，信息学院举行首届“宏新图ERP实施员订单班”开学典礼。宏图网络公司总经理王勇、副总经理陈小平等出席开学典礼，信息学院领导班子成员、教师代表及订单班20名学员参加开学典礼。 王勇在开学典礼上发言，他说，公司已经与义乌、东阳的高职院校有订单班培养合作，公司尝到了校企合作共同进行人才培养的甜头。信息学院在国家示范高职院校建设中积累了丰富的人才培养理念，公司对与信息学院的合作前景非常看好。相信通过双方在培养方案、教学管理、技能实训、考核录用上的共同协调合作，一定能培养出符合企业需求，达到学校标准的人才。他希望订单班学员加强管理、沟通、协调能力的提升，培养艰苦实干精神，促进自身发展。 学院总支书记朱登胜希望订单班学员实现所学知识和企业实际需求的融合、企业文化和校园文化的融合、企业发展和个人成才的融合，促进学院“3+X”订单班人才培养模式新发展。 宏图网络科技有限公司是一家专业从事企业应用管理软件开发的高科技企业，主要涉足ERP管理软件开发，经过10年发展，已开发出受到行业认可的系列软件，成为专业的中小企业信息化整体解决方案服务提供商。 （文  邱晓华  摄影  王伟斌）', 'http://info.jhc.cn/2012/1109/c201a2494/page.htm', NULL, '2017-05-17 14:16:23.435984');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (392, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '我院教工党支部被授予市直机关“学习型党组织”荣获称号', '近日，从金华市直机关工委传来喜讯，我院教工党支部经推荐、考核和最终审定，被市直机关工委授予2009年度“学习型党组织”荣誉称号。 在此次创建活动中，学院教工党支部高度重视，精心组织，扎实开展创建工作，在支部内营造了浓厚的学习氛围，取得了比较明显的学习效果，有力地促进了教工支部各项工作的开展。通过创建活动，为教工党员政治思想素质的提高，教育教学能力的提升，特别是目前的示范院校建设工作的开展，注入了强大的精神动力、智力支持和思想、组织保障。 （文  陈海荣  王伟斌）', 'http://info.jhc.cn/2012/1109/c201a2490/page.htm', NULL, '2017-05-17 14:16:23.458346');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (393, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '德国迪恩市代表团来访参观我院实训中心', '', 'http://info.jhc.cn/2012/1109/c201a2476/page.htm', 'http://info.jhc.cn/_upload/article/images/c7/e9/2f967dee4a868260ec92fd5b96d4/bfd58da8-b24c-47fb-8aac-a16ec471fa14.jpg|http://info.jhc.cn/_upload/article/images/c7/e9/2f967dee4a868260ec92fd5b96d4/6947d19c-131e-4f93-96d3-0dbe9ab49ffb.jpg|http://info.jhc.cn/_upload/article/images/c7/e9/2f967dee4a868260ec92fd5b96d4/e46d0397-16f2-4379-b207-52b55aab894c.jpg|http://info.jhc.cn/_upload/article/images/c7/e9/2f967dee4a868260ec92fd5b96d4/a199006e-5b71-4071-a0d9-a54f829e1fa7.jpg|http://info.jhc.cn/_upload/article/images/c7/e9/2f967dee4a868260ec92fd5b96d4/a7d873b5-2e65-4e29-b637-29f22b102169.jpg', '2017-05-17 14:16:34.749369');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (394, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院开展示范专业对口交流活动', '1月13日，信息学院由副院长王成福带队，学院相关专业负责人走访了丽水职业技术学院，与该院就专业建设等开展对口支援交流活动。 今年是信息学院国家示范院校建设的验收年，通过三年建设，学院在专业建设、教学科研、师资培养等方面积累了较为丰富的经验。作为示范院校建设单位，将示范建设的经验和成果与其他兄弟院校分享，是示范建设的重要内容之一。本次交流活动就是旨在提升学院的服务与辐射能力，实现教育资源共享，发挥我院国家示范专业——应用电子技术的引领作用，帮助对口支援学校提高教学与实践水平。 交流活动中，王成福代表学院就应用电子专业课程体系的设置、专业人才方案、教材编写、技能培训、职业能力分析的组织等方面与丽水职业技术学院的老师进行了经验交流，并在具体实施上给予了指导，交流活动得到丽职院老师的积极评价。 （文  吴晓明）', 'http://info.jhc.cn/2012/1109/c201a2471/page.htm', NULL, '2017-05-17 14:16:34.781204');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (395, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '高职高专电子信息类专业基本技术技能行业企业访谈会在我校举行', '1月23日，由我校信息学院承办的高职高专电子信息类专业基本技术技能行业企业访谈会在我校举行，来自北京联合大学等全国20多家高职院校、人民邮电出版社等4家出版单位以及Intel有限公司、汉王科技有限公司等多家企业的专家、学者、教师等70余人参加会议。教育部高职高专电子信息类教指委主任高林教授、校党委副书记余党军教授等应邀出席。会议由信息工程学院院长龚永坚主持。 本次会议是为了进一步贯彻教育部高职高专电子信息类教指委海口会议和重庆会议精神，加快高职高专应用电子技术专业的课程体系改革与课程建设，更好的开展电子信息类教指委第二批立项课题的研究工作而召开的。 来自行业、企业的专家与高职教师们围绕电子信息类专业学生应掌握的基本技术技能点及其标准、训练方式、实训课程体系等展开了激烈讨论。与会人员普遍认为电子信息类专业应该注重实践教学理念，强调以专业技术应用能力培养为主线，注重学生的基本技能训练、专业技能训练、工程实际应用能力和综合实践技能训练，把培养具有实践动手能力强、适应相关岗位技能和创造性需要的高职学生作为目标。围绕这一目标，企业专家们结合企业人才需求，行业专家结合专业发展趋势，高职教师们结合教育教学的实际对电子信息类专业的基本技术技能进行了汇总、提取、归类，并讨论确定了相应的训练环境、条件、标准，形成了一个较为细致和可行的基本技能课程体系，为电子信息类专业的教学和课程体系改革指明了方向。 本次会议是在我校应用电子技术专业国家示范验收前夕召开的，对改进和夯实应电专业课程体系改革和课程建设成果，创新学生基本技能培养起到了积极作用。 （文/摄影  陈海荣  王伟斌    审核  陈海荣）', 'http://info.jhc.cn/2012/1109/c201a2458/page.htm', 'http://info.jhc.cn/_upload/article/images/61/e9/23f66abf455cb8087fd950892eff/918224c7-0709-4f33-855a-0fb299c4accb.jpg|http://info.jhc.cn/_upload/article/images/61/e9/23f66abf455cb8087fd950892eff/a0e8fbe9-2e02-49cd-8206-855fb40ef633.jpg|http://info.jhc.cn/_upload/article/images/61/e9/23f66abf455cb8087fd950892eff/9199874d-0139-41ef-a2bd-233f7d8e884c.jpg|http://info.jhc.cn/_upload/article/images/61/e9/23f66abf455cb8087fd950892eff/cdb4e4b9-a909-40c5-ac23-dbbceddf9f4b.jpg', '2017-05-17 14:16:34.803697');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (396, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '全国高职院校嵌入式技术教学高级师资培训班在我院开班', '1月24日，由英特尔中国公司主办、我院承办的全国高职院校嵌入式技术教学高级师资培训班正式开班。教育部、教育部高职高专电子信息类教学指导委员会及省教育厅有关领导、专家，学校院长王振洪等出席了开班仪式。 仪式上，王振洪首先代表学校党政领导班子对前来参加会议的领导、专家表示欢迎，对师资培训班的开班表示祝贺。王振洪说，嵌入式系统集硬件芯片技术与软件程序设计为一体，现已广泛应用于仪表、移动通讯等各个领域，发展空间十分广阔。全面深入地开展嵌入式系统教学，是当前高职院校适应社会经济发展和培养创新型、技能型紧缺人才的需要，此次师资培训班的开班正是为全国高职院校搭建了一个交流、学习的平台。王振洪表示，有了“政校企行”四方联动，相信嵌入式创新技术教学在全国高职院校必将会结出更多、更丰硕的成果。 教育部、教育部高职高专电子信息类教学指导委员会及省教育厅有关领导、专家，主办方负责人等也发了言。他们都表达了本次“全国高职院校嵌入式技术教学高级师资培训班”在为全国的高职院校搭建一个交流、学习平台上努力的积极肯定，认为这种“官校企行四方联动”的模式是一种很好的“校企合作、工学结合模式”的探索，必定会推动我国高职院校嵌入式创新技术教学的普及与发展。  据悉，我院承办的本次培训为期五天，主要采用理论教学、案例分析 、动手实验相结合的课程模式，由英特尔公司、红帽公司、摩托罗拉公司及清华大学的有关专家、教授现场执教，传授嵌入式技术及嵌入式创新教学技巧。来自深圳职业技术学院等全国20余个高职院校50余名专业教师参加了此次培训。此次培训活动将有助于扩大我院应用电子技术示范专业在全国的影响力，并进一步推动专业的内涵建设。 （文/摄影  王伟斌）', 'http://info.jhc.cn/2012/1109/c201a2448/page.htm', 'http://info.jhc.cn/_upload/article/images/e5/55/3e4e4de24adf8cbd00bd2fee5c68/897c6f39-92c6-4462-9fc7-3a5a6ce12258.jpg|http://info.jhc.cn/_upload/article/images/e5/55/3e4e4de24adf8cbd00bd2fee5c68/84bfb2d2-4218-4797-857f-5b8980e9aea4.jpg|http://info.jhc.cn/_upload/article/images/e5/55/3e4e4de24adf8cbd00bd2fee5c68/85f0d071-18b8-46cf-8ed5-36d04379f63c.jpg', '2017-05-17 14:16:34.825923');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (397, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院召开辅导员学习交流会', '为进一步推动“辅导员素质提升工程”的实施，1月30日，信息学院召开辅导员学习交流会，学院党总支书记朱登胜出席并主持会议，学工办、团委全体人员参加了本次会议。  会议对本学期开展的辅导员培训会、思政论文读后感及活动策划方案汇报会、辅导员系列讲座等项目的开展情况进行了总结，辅导员们针对本次素质提升工程所取得的成效各抒己见，纷纷提出自己在思政工作、专业建设等方面的独特见解。 会上，朱登胜对大家富有创意的想法表示肯定，他说，作为高校辅导员，只有找准角色，才能做好服务，我们要在生活上做同学真心的家人，在工作上做同学们热心的同仁，在学习上做同学们诚心的引路人，在思想上做同学们知心的友人，只有这样，我们辅导员工作才能真正做实，做到位。会上，他还在科研工作上为大家进行了点拨，为辅导员们的课题研究指明了方向。 （文  陈静泓）', 'http://info.jhc.cn/2012/1109/c201a2444/page.htm', NULL, '2017-05-17 14:16:34.848078');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (398, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '全国高职院校嵌入式技术教学高级师资培训班在我校圆满结束', '1月29日，全国高职院校嵌入式技术教学高级师资培训班在我校信息学院圆满结束。 在为期一周的培训中，来自全国20多所高职院校的50多名教师深入学习了嵌入式系统开发概述、Linux嵌入式开发环境、Intel嵌入式开发架构、嵌入式开发环境配置、UBOOT配置、内核编译与文件系统配置、嵌入式驱动程序开发概述、嵌入式教学环境的使用等知识，并通过直流电机驱动、以太网络通讯等项目实战，使教师们对嵌入式开发流程有了较为清晰的理解，较好地掌握了嵌入式系统软硬件设计方法及授课技巧，为今后投身嵌入式系统教学工作，培养更多目前行业所急需的嵌入式人才提供了重要师资保证。 许多参加此次培训的教师在培训后认为，随着嵌入式创新技术的迅速发展，在系统设计中加入包含自主知识产权的设计，并以ASIC或可编程逻辑器件的形式，实现功能强大的嵌入式系统，实现高性能、高质量、低成本电子产品设计，必将成为当前嵌入式系统设计的主流。 参加本次培训的陈老师说，目前许多高职院校正逐步将嵌入式片上可编程系统、嵌入式系统设计、可重用设计、IC设计、现代DSP技术等与实际应用密切相关的课程引入到教学中，并且在新的教学实验课程体系中居于核心地位。由于嵌入式技术知识更新快，学科交叉度高，应用面广，创新性强，因此对培养嵌入式创新人才的教师技术水平要求将更高，在这样的背景下，本次培训是非常及时而有效的，对我们知识的建构和教学能力的提升，将起到巨大的促进作用。 据悉，本次培训得到了英特尔公司、红帽公司、摩托罗拉公司的大力支持，清华大学的有关专家、教授也应邀现场执教。同时也为我校当前正在开展的示范验收工作提供了一次难得地与兄弟院校经验交流的机会。 （文/摄影   王伟斌）', 'http://info.jhc.cn/2012/1109/c201a2435/page.htm', 'http://info.jhc.cn/_upload/article/images/4b/2d/371b8f35456b9bd7706a1b20e1c3/19127351-e352-445a-948c-63bcd5616eaa.jpg|http://info.jhc.cn/_upload/article/images/4b/2d/371b8f35456b9bd7706a1b20e1c3/0ca2d8ab-64b2-4027-a579-b532bc192955.jpg|http://info.jhc.cn/_upload/article/images/4b/2d/371b8f35456b9bd7706a1b20e1c3/bbe52bbf-f233-41bd-89e0-4dd0a9524f81.jpg', '2017-05-17 14:16:34.870222');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (399, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院2010年新春团拜会', '', 'http://info.jhc.cn/2012/1109/c201a2423/page.htm', 'http://info.jhc.cn/_upload/article/images/b0/ea/cc7de8754fe4bfcb9b6850b28ae3/231951aa-d0ba-4b12-b463-1943daa173f3.jpg|http://info.jhc.cn/_upload/article/images/b0/ea/cc7de8754fe4bfcb9b6850b28ae3/49abb236-45d8-4325-91da-459c4e5cfc03.jpg|http://info.jhc.cn/_upload/article/images/b0/ea/cc7de8754fe4bfcb9b6850b28ae3/e5da9367-0fbf-4628-98de-3f1bdbd44b9a.jpg|http://info.jhc.cn/_upload/article/images/b0/ea/cc7de8754fe4bfcb9b6850b28ae3/e07ae030-f555-4a9f-9b48-328fbc66484c.jpg', '2017-05-17 14:16:34.892489');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (400, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院通信网络与设备专业召开课程标准论证会', '2月7日，信息学院在516会议室召开通信网络与设备专业课程标准论证会，就专业教师制定的课程标准邀请企业专家进行研讨、论证。来自浙江博尚电子有限公司、浙江电信、浙江移动等9家企业的代表应邀出席，副院长陈海荣、通信专业全体专业教师参加论证会。论证会由通信专业主任赵敏笑主持。 本次论证会，旨在通过对课程标准的论证，使通信专业教学内容和教学特点更加符合企业和社会对高职通信专业教学的要求。会上，与会企业代表就有关通信网络与设备专业核心课程的定位是否准确；课程目标是否明确、合理；课程内容是否完善并突出重点；课时安排是否合理，是否与企业需求相一致等一系列问题进行了广泛和深入讨论。企业专家们尤其对课程标准中存在的知识结构、核心专业知识以及技能在与企业需求一致性等方面的问题进行了重点论证。 在学院示范院校建设进入验收阶段之际召开的本次论证会，对完善示范建设的课程标准，获取更多适合企业一线实际，符合社会需求的课程设置，对通信专业课程标准的进一步修改和完善具有重要的指导意义。 （文/摄影  苏红富）', 'http://info.jhc.cn/2012/1109/c201a2419/page.htm', NULL, '2017-05-17 14:16:34.914707');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (401, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院医用电子仪器与维护专业召开教学标准论证会', '2月9日，信息学院医用电子仪器与维护专业在516会议室召开专业教学标准论证会。来自于行业、企业一线从事医疗仪器维护、营销、生产、管理的专家应邀参加，副院长陈海荣出席会议。 论证会以“教师介绍，专家点评，双方互动”的方式在激烈讨论、严密论证中共同探讨课程标准。专家们在听取我院专业教师的课程标准介绍后，肯定了我院医仪专业目前所设置的课程体系具有较好的合理性，但他们也结合自己的行业企业经验，提出了许多十分诚恳和宝贵的意见和建议，对教师们完善课程标准产生了很大的启发价值。 通过此次教学标准论证会，医仪专业进一步理清了“以生理参数信号的采集为主线进行设计，对维护设备进行分类，以提升学生素质为主的营销训练”的教学内容选取主线，为进一步修订完善课程标准奠定了良好的基础。 （文  刘子坚）', 'http://info.jhc.cn/2012/1109/c201a2414/page.htm', NULL, '2017-05-17 14:16:34.937051');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (402, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院领导深入学生寝室走访', '3月1日是新学期开学第一天，为切实了解学生的学习、生活情况，信息学院党总支书记朱登胜、总支委员吴晓明带领学工办人员深入学生寝室进行走访慰问。 走访期间，朱登胜代表学院领导班子向同学们表示新年的问候，并与大家展开了亲切的交谈。他说，新学期开始了，大家要从节日的氛围中尽早走出来，希望同学们以饱满的精神、充沛的热情投入新一轮的学习生活。朱登胜在与大家的交谈中还指出，公寓是大学生学习生活的一个重要活动场所，因此，大家一定要踏实搞好寝室内务，注意宿舍安全，合理安排课余时间，共同营造一个良好的公寓氛围。  新学期伊始的走访慰问，对拉近了学院领导与学生之间的心灵距离，增强了同学们爱护寝室环境的意识，提高学工线人员对学生管理工作更高的热情必将起到积极的促进作用。 （文  陈静泓）', 'http://info.jhc.cn/2012/1109/c201a2410/page.htm', NULL, '2017-05-17 14:16:34.959188');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (403, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院召开2010年新学期全体教职工会议', '3月1日下午，信息学院召开2010年新学期全体教职工会议。信息学院全体教职员工参加会议，会议由学院党总支书记朱登胜主持。 会上，朱登胜宣读了学校部分人事任免文件和学院领导班子分工调整文件。总支委员吴晓明宣读了信息学院2009年度各项表彰文件，一批在2009年度工作表现突出的班主任、优秀共产党员、先进工作者和教师教学竞赛获奖人员等得到了表彰。 院长龚永坚在会上发言，他首先表达了对全院教职员工新学期的问候和祝福。他说，新学期新气象，我们在这里表彰了2009年度的先进，就是希望全体教职员工以他们为榜样，在2010年扎实投身学院各项工作，促进学院在新一年的新发展。他说，目前学院处在示范院校建设验收的关键时期，时间紧，任务重，前期示范建设参与人员已经付出了许多艰辛的劳动，希望在新学期里，全体教职员工关心、支持和参与这项工作，确保学院示范建设任务圆满完成。他还就新学期伊始学生管理、教学工作等方面做了具体要求。 朱登胜就新学期学生管理工作，副院长陈海荣就新学期教学工作在会上做了具体部署和要求。他们希望全体教职员工尽快调整心态，以饱满的精神尽快投入新学期的工作，确保学院教育教学的顺利开展。 （ 文 / 摄影    王伟斌 ）', 'http://info.jhc.cn/2012/1109/c201a2403/page.htm', 'http://info.jhc.cn/_upload/article/images/75/9a/fa72b2224bc3aef2a49411433da6/abf4d6f5-5888-4ce9-83c1-54fca64d7a3a.jpg|http://info.jhc.cn/_upload/article/images/75/9a/fa72b2224bc3aef2a49411433da6/3209720e-925b-4fc2-a712-e412403c4b84.jpg', '2017-05-17 14:16:34.981421');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (404, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '院长王振洪等来我院检查指导新学期工作', '3月1日，开学第一天，由院长王振洪带队，校党委副书记余党军，工会主席、党委委员黄宏伟等一行来信息学院走访，检查指导新学期工作。信息学院院长龚永坚、书记朱登胜等领导班子成员接待了王振洪一行。 王振洪在座谈中指出，新学期开学在即，信息学院的领导班子一定要十分重视学生管理工作。要把学生到校情况、学生开学初的上课情况、学生的节日病等心理问题想在前面，把具体工作坐在前头，领导班子成员要认真指导班主任、辅导员做好学生管理工作的排头兵，通过走访寝室，细致了解学生的生活学习状况。针对即将到来的国家示范院校建设验收工作，王振洪说，信息学院作为示范专业建设单位，担子重，任务多，领导班子一定要在工作中注意工作艺术问题，统筹兼顾。班子成员要明确分工、明晰职责，在工作中突出工作重点，突破难点，确保示范建设任务保质保量按时完成。他还就师资团队建设、学院网站建设等问题做了具体指导。 余党军在座谈中则就青年教师的科研发展水平、学生的心理健康问题、自主招生等问题进行了具体指导，他希望信息学院领导班子开阔思路，创新思维，统筹做好相关工作。 龚永坚在座谈中对王振洪院长一行的来院工作指导表示感谢，他说，信息学院领导班子一定会认真研究落实王院长提出的学院目前存在的不足，按照王院长的指导思路和要求，落实学生管理和示范建设等工作，确保学院新学期各项工作的顺利开展。 ( 文 / 摄影   王伟斌 )', 'http://info.jhc.cn/2012/1109/c201a2397/page.htm', 'http://info.jhc.cn/_upload/article/images/2d/b3/fb7523934ab790fcf300f9cf1f55/ebf56589-5ca4-42bf-b58f-0bad4857c525.jpg|http://info.jhc.cn/_upload/article/images/2d/b3/fb7523934ab790fcf300f9cf1f55/a27613da-5661-4df3-9f2c-4288039831f8.jpg', '2017-05-17 14:16:35.003935');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (405, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '在其位，谋其职，尽其力――信息学院团学干部会议顺利召开', '3月9日晚，信息学院召开本学期首届团学干部大会。学院党总支书记朱登胜及学工办、团委、学生会等师生参加参议。会议由学院总支委员、学工办主任吴晓明主持。 朱登胜在会上发言，他说，学生干部是学生的标杆，大家在开展工作时一定要以人为本，服务于同学，做到善始善终，敢于承担责任，同时也要正确处理好学习与工作的关系，做到两手抓，两手都要实，都要突出。朱登胜说，团委、学生会、社管会三大部门是一个整体，大家要从全局的角度去思考问题，做好每一件小事，平时要相互配合，学会借力，提高执行力，保证工作效率。  会上，各部门负责人就本学期的工作构想进行了交流，为下一阶段团学工作机制、理念的全面创新奠定了基础。本次会议也标志着新一届团学干部正式走上工作岗位，对他们的培训工作也逐步展开。 （文  陈静泓）', 'http://info.jhc.cn/2012/1109/c201a2394/page.htm', NULL, '2017-05-17 14:16:35.026354');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (406, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '激情球赛，谁与争锋', '3月16日,继上周末为期两天的“激昂青春,释放活力”羽毛球赛之后，信息学院乒乓球友谊赛又燃战火，再续激情。本次乒乓球比赛与羽毛球赛一样，设男子单打、女子单打等比赛项目，采取单淘汰制。 赛场上，选手们优雅的弧线，猛力的扣杀，掌控自如的力量、旋转、落点与速度，无不体现出娴熟的技战术水平；拉拉队员们则欢呼声此起彼伏，张扬着青春的活力。火热的赛场为选手提供了切磋球技的机会，传递了信息学子拼搏的热情。 （文 陈静泓）', 'http://info.jhc.cn/2012/1109/c201a2391/page.htm', NULL, '2017-05-17 14:16:35.048164');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (407, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '副院长王成福喜获省高校教学名师奖', '近日，省教育厅、省财政厅联合下文公布省第五届高等学校教学名师奖和第三届高等学校教坛新秀奖名单，我院副院长王成福获省教学名师奖，同时获此殊荣的还有兄弟学院李群力老师。 据悉，此次评选是经各高校推荐、专家评审、省教育厅审定并公示，共评选出省高校教学名师奖50人，省高校教坛新秀101人。 （文  王伟斌）', 'http://info.jhc.cn/2012/1109/c201a2386/page.htm', NULL, '2017-05-17 14:16:48.304477');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (408, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '浙江省“嵌入式系统产品开发”技能大赛技术文件研讨会在我校召开', '3月17日，浙江省“嵌入式系统产品开发”技能大赛技术文件研讨会在我校召开。会议邀请了包括宁波职业技术学院等省内十余所高职院校的13位嵌入式技术专家参加。 本次研讨会对即将举行的2010年浙江省高等学校学生职业技能大赛暨全国职业院校技能大赛选拔赛中的“嵌入式系统产品开发”技能大赛活动安排及有关比赛事项进行了讨论和明确，并以赛促学、以赛促教，推动电子信息类专业建设和教学改革进行了深入研讨。 据悉，2010年浙江省高等学校学生职业技能大赛“嵌入式系统产品开发”项目竞赛将于4月24日至25日在我校信息学院举行。该项目比赛旨在检验参赛选手对嵌入式系统产品在提供的设计内容下的整机组装实现能力，引导高职院校关注电子行业新技术的发展趋势与技术应用方向，指导和推动电子信息类专业开展教学改革，加快电子系统产品设计与制造高技能应用型人才的培养。 （文  王伟斌  钱晓辉）', 'http://info.jhc.cn/2012/1109/c201a2382/page.htm', NULL, '2017-05-17 14:16:48.337901');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (409, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '2010年浙江省“嵌入式系统产品开发”技能大赛技术文件研讨会在我校召开', '3月17日，2010年浙江省高等学校学生职业技能大赛暨全国职业院校技能大赛选拔赛嵌入式产品开发竞赛技术文件研讨会在我校信息学院召开。会议邀请了包括宁波职业技术学院等省内十余所高职院校的13位嵌入式技术专家参加，校教务处副处长王建生应邀出席。 本次技术文件研讨会是对2010年浙江省“嵌入式系统产品开发”技能大赛规范的确定。与会者一致认为，通过此次竞赛，目的在于检验参赛选手对嵌入式系统产品在提供的设计内容下的整机组装实现能力，引导高职院校关注电子行业新技术的发展趋势与技术应用方向，指导和推动电子信息类专业开展教学改革，加快电子系统产品设计与制造高技能应用型人才的培养。会上对大赛参赛内容、竞赛具体时间及安排等做了明确。 据悉，2010年浙江省高等学校学生职业技能大赛将于4月25日至26日在我校信息学院举行。 （文  王伟斌  钱晓辉）', 'http://info.jhc.cn/2012/1109/c201a2378/page.htm', NULL, '2017-05-17 14:16:48.360099');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (410, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院党校第十二期入党积极分子培训班举行结业典礼', '3月23日，信息学院党校第十二期入党积极分子培训班举行结业典礼。本期培训班共有学员378名，在为期6个月的学习时间里，学员们积极参加党校各项教学活动，认真遵守党校各项规章制度，并自发组织了各种社会公益服务等党校活动。经过各个环节的全面考核，303名学员在今天顺利结业，并有应电091班范奇能等13名同学被授予党校第十二期入党积极分子培训班优秀学员称号。 学院党校校长、党总支书记朱登胜希望学员们要坚定共产主义信仰，牢记所肩负的责任和使命，树立为人民服务的意识。在今后的学习生活中，以更高、更严的标准要求自己，全心全意为广大同学服务，争取早日加入党组织。 新结业的303名学员将成为我院进一步壮大党员队伍的坚实后备军，为加强学院学生党组织的建设起到了巨大的推动作用。 （文 周荃  陈静泓）', 'http://info.jhc.cn/2012/1109/c201a2371/page.htm', 'http://info.jhc.cn/_upload/article/images/fc/d9/d774c4aa430b96a09a5a45be99d0/ef25dadb-5b89-40bd-ac4d-45a9e6c960df.jpg|http://info.jhc.cn/_upload/article/images/fc/d9/d774c4aa430b96a09a5a45be99d0/58ab61cc-db71-4e3f-bea5-c68d4d3f3a3f.jpg', '2017-05-17 14:16:48.382863');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (411, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院学生会开展工作交流会', '为加强学生干部间的交流与合作，3月24日晚，信息学院开展了“真诚沟通，携手共进”学生会工作交流会。 本次交流会是学院学生会为有力提升自身工作效能，在前期全院范围内开展的一次问卷调查基础上召开的。会上，两位副主席站在广大普通同学的视角对本次调查结果进行了分析，总结了大家提出的对学生会各部门目前存在的缺陷与薄弱环节，并对学生干部在开展工作中存在的不足做了点评。各部门成员围绕本次调查结果，对今后工作思路、工作方法等展开了激烈的讨论，特别针对即将到来的电脑科技节、学院运动会等大型活动中更好的服务同学等进行了深入交流。 本次交流会一方面提升了学生会干部的服务意识和责任意识，也促进各部门、各成员间的了解，为学生会下一步的具体工作指名了方向。 （文  陈静泓）', 'http://info.jhc.cn/2012/1109/c201a2368/page.htm', NULL, '2017-05-17 14:16:48.40484');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (412, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学子学以致用，服务社会', '为进一步提高学生的综合素质与实践能力，发挥协会与社团的作用，3月25日，信息学院团委学生会和青年志愿者前往金华城北街道洪湖社区开展科技下乡实践活动。 活动过程中，协会与社团成员运用平时学习积累的有关电子、家用电器设计与维护等方面的理论与实践经验，向居民耐心讲解相关知识，并帮助居民免费维修一些出了问题的家电，还就居民们提出的一些电子产品配件的选购、运用、维修维护等方面的问题做细致解答。同学们的专业技能和热心服务得到了居民们的一致好评，他们直夸同学们手艺高，心灵更美。 实践活动给同学们提供了一次社会锻炼的机会，加强了同学们学以致用，用知识回馈社会的热情和意识。   （文  邱小玲  陈静泓）', 'http://info.jhc.cn/2012/1109/c201a2365/page.htm', NULL, '2017-05-17 14:16:48.426737');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (413, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '2010年全国计算机等级考试金职院考点圆满结束', '3月27-28日，第31次全国计算机等级考试金华职业技术学院考点在信息学院实训中心进行。 我校考点根据市自考办考务会议的要求，把考试安全和考试过程的规范管理作为首要任务来抓，全体工作人员都参加考前培训。整个考务工作严格按照《全国计算机等级考试考务手册》中的各项规定执行，考试组织严密、有序。在考试过程中没有发生一起考试安全事故，考风考纪良好。我校的考务工作也得到了市教育局、市自考办相关部门的肯定和认可。 据悉，本次全国计算机等级考试我校考点共设4个考场、8个批次，共1208人参加考试。 （文 单天德）', 'http://info.jhc.cn/2012/1109/c201a2360/page.htm', NULL, '2017-05-17 14:16:48.449048');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (414, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '深入贯彻学校文件 扎实推进思政工作', '根据校党委的要求，为贯彻学校有关学生思想动态研判等文件精神，日前，信息学院召开了大学生思想动态研判会议。会议由总支委员、学工办主任吴晓明主持。 在接到学校相关文件后，学院即以问卷的形式在全院范围内开展了有关大学生思想动态的调研工作。会议根据调研结果，全面分析了当代大学生的思想现状，着重对两会、旱灾、就业规划、科学发展观理论学习与实践、和谐校园的构建等学生关注的热点问题进行研讨，并提出相关的引导对策。 会议还根据研讨结果对今后的思想动态研判工作作了全面部署，吴晓明要求辅导员多渠道、多手段加强学生思想动态管理，不断推进思想动态研判工作向常态化、制度化方向发展，有效开展思政工作，促进平安校园和和谐信息的构建。 （文 陈静泓）', 'http://info.jhc.cn/2012/1109/c201a2355/page.htm', NULL, '2017-05-17 14:16:48.471042');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (415, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学子情暖敬老院', '在清明节即将到来之际，信息学院组织青年志愿者来到金华市婺城区白龙桥敬老院，开展“春意暖，爱更浓”慰问活动。 一踏入敬老院，志愿者们便纷纷与老人们寒暄，忙着给老人们发放水果、点心等慰问品，女生们还帮老人们修剪指甲，整理卧室。为了丰富老人们的娱乐生活，志愿者们还为老人带来了精心准备的文艺表演，经典的革命老歌和越剧演唱，丰富的舞蹈和小品演出，使平日里安静的敬老院欢笑声不断。 此次慰问活动受到了金华电视台“百姓零距离”等电视节目的关注，志愿者们说，通过这次奉献爱心活动，让自己受到了尊老爱老的传统美德教育，也在潜移默化中加强了自身的思想道德素质。 （文 陈静泓  郑俊健）', 'http://info.jhc.cn/2012/1109/c201a2349/page.htm', 'http://info.jhc.cn/_upload/article/images/05/22/9863adf447069a1199fcb6a60177/16a11ede-bb0f-4c16-89bc-78524d97ea7f.jpg|http://info.jhc.cn/_upload/article/images/05/22/9863adf447069a1199fcb6a60177/f1c8d474-e65f-4786-96e9-462ee72d55be.jpg|http://info.jhc.cn/_upload/article/images/05/22/9863adf447069a1199fcb6a60177/55dd6d43-cbd6-4441-857a-ce8cf72ab99a.jpg|http://info.jhc.cn/_upload/article/images/05/22/9863adf447069a1199fcb6a60177/90037315-e547-4c9a-835d-3d3ca0a899bb.jpg', '2017-05-17 14:16:48.493522');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (416, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '发挥学生骨干作用，共创信息和谐公寓', '为进一步提高学生公寓管理水平，发挥青年学生“自我教育，自我管理，自我服务”的作用，3月28日晚，校宿舍管理中心主任贾美倩老师应邀为我院学生作了一次主题为“发挥学生骨干作用，共创信息和谐公寓”的讲座。 讲座上，贾美倩围绕宿管中心的相关工作，要求学生骨干加强与辅导员之间的沟通交流，学会做事，讲求和谐，有计划地配合辅导员推进公寓管理工作，在做好自身寝室建设的同时，协助其他寝室共同进步。 据悉，为推进公寓建设，信息学院启动了学生骨干与寝室结对子活动，将党员、建党对象、考察对象、学生干部等200多名学生骨干组建成公寓管理学生队伍，配合辅导员管理分配到的寝室。本次讲座是对该队伍一次全面深入的培训，更是我院构建和谐公寓又一个新的起点。 （文 陈静泓  郑俊健）', 'http://info.jhc.cn/2012/1109/c201a2344/page.htm', NULL, '2017-05-17 14:16:48.515818');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (417, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '奥地利布劳瑙高等职业学校师生来我校访问交流', '3月30至31日，奥地利布劳瑙高等职业学校（HTL Braunau）师生一行26人来我校信息学院访问交流，中奥双方师生就电子类课程学习模式、实践课程模式、企业实践模式等开展了广泛而深入的交流。 交流活动中，奥方师生观摩了信息学院教师的嵌入式系统课程教学，奥方教师则用我校实训场所给双方学生进行授课。通过授课，双方师生了解了对方教师授课特点、课程教学安排和学生学习方法，大家针对彼此课堂教学的不同进行了交流，同时还探讨了双方专业学习实践、企业实习、电子竞赛经验等情况。 在看了我校的校园文化读本后，奥方师生对我校的“五位一体”育人模式表示了赞赏，认为该模式中许多经验值得他们学习借鉴。而奥方学校在整个教学过程中注意专业课、实践课、基础课有机结合，让学生不仅接受基础课教育，而且参与企业项目的设计与实施，注重学生双重能力培养的经验；奥地利对教师“双师”要求、企业工作经验注重、继续教育网络的构建给了我校教师许多启发。布劳瑙高等职业学校参加世界电子竞赛的丰富经验，也成为我校许多教师重点探讨和交流的内容。 短短的两天交流时间虽短，但一起联欢、一起上课中，中奥青年学生浓厚的交流热情让彼此产生了深厚的友谊，为便于以后联系，双方学生互留电子邮箱，奥方同学还诚挚的邀请我校学生有机会到奥地利访问交流。 （文 / 摄影  王伟斌）', 'http://info.jhc.cn/2012/1109/c201a2326/page.htm', 'http://info.jhc.cn/_upload/article/images/aa/9e/a46a631f4d348e938d50cd166d3a/5dec8b42-55cc-49fd-9fbd-fbd6649b814c.jpg|http://info.jhc.cn/_upload/article/images/aa/9e/a46a631f4d348e938d50cd166d3a/2b56c3d7-a199-4e65-8b13-81b831b2e666.jpg|http://info.jhc.cn/_upload/article/images/aa/9e/a46a631f4d348e938d50cd166d3a/ed4eb2b4-9c20-4839-b959-aaf05c3bd5ac.jpg|http://info.jhc.cn/_upload/article/images/aa/9e/a46a631f4d348e938d50cd166d3a/1bf6e440-8e32-4080-92a2-8731f5e010a2.jpg|http://info.jhc.cn/_upload/article/images/aa/9e/a46a631f4d348e938d50cd166d3a/b446eb43-8e5b-4bb7-9431-9dd85ac548f1.jpg|http://info.jhc.cn/_upload/article/images/aa/9e/a46a631f4d348e938d50cd166d3a/36efe768-7eaa-4817-91af-a0dfb911c076.jpg|http://info.jhc.cn/_upload/article/images/aa/9e/a46a631f4d348e938d50cd166d3a/cbe05230-8e12-4bd9-afe8-f5e28a97fb96.jpg|http://info.jhc.cn/_upload/article/images/aa/9e/a46a631f4d348e938d50cd166d3a/389798fe-779c-4831-ac45-cb4b1d55bd2b.jpg|http://info.jhc.cn/_upload/article/images/aa/9e/a46a631f4d348e938d50cd166d3a/e3f3844c-48d7-450a-a6b4-2c53d6c9a823.jpg|http://info.jhc.cn/_upload/article/images/aa/9e/a46a631f4d348e938d50cd166d3a/4f6c60d0-7993-4924-b3c4-e4512c7132ea.jpg', '2017-05-17 14:16:48.537972');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (418, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '《金华晚报》报道奥地利布劳瑙高等职业学校学生来我校交流学习', '取经     昨天，来自奥地利布劳瑙高等职业学校（HTL Braunau)的24名学生，来到金华职业技术学院信息工程学院，和信息工程学院的师生交流机电类等专业学科内容，互相观摩对方的教师授课特点，并在晚上安排联谊活动，增进了解两国的传统文化。据了解，奥地利布劳瑙高等职业学校与许多国家及地区的学校建立了合作伙伴关系，进行学生交换、互访以及科研课题的共同研发。    （记者 缪小芬）     新闻链接：http://www.jhnews.com.cn/site1/jhwb/html/2010-03/31/content_1119650.htm', 'http://info.jhc.cn/2012/1109/c201a2321/page.htm', 'http://info.jhc.cn/_upload/article/images/ac/5c/265e9ccf43018288b2d70dab1ad3/4b12405c-d827-4111-b861-6c3001299be3.jpg', '2017-05-17 14:16:48.560129');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (419, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '金华电视台“百姓零距离”栏目报道我院志愿者走进敬老院送温暖活动', '新闻链接：http://www.36tv.cn/viewnews-70407', 'http://info.jhc.cn/2012/1109/c201a2313/page.htm', 'http://info.jhc.cn/_upload/article/images/5a/66/7e27ce694b2d942146e809a15506/5361b227-d64d-4699-b741-bedae15fb5d5.jpg', '2017-05-17 14:16:48.58245');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (420, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '缅怀我们心中的英雄', '4月1日晚，信息学院在实验剧场举行“缅怀我们心中的英雄”孟祥斌事迹报告会。孟祥斌生前所在部队领导、战友应邀出席，校党委副书记余党军、信息学院全体党员、学生干部等参加报告会。报告会由信息学院党总支书记朱登胜主持。 本次报告会报告团成员都是孟祥斌生前的亲密战友，包括政治部主任华国生、机要科参谋吕二伟、宣传科干事张璇。他们从不同的视角讲述了孟祥斌短暂而光荣的人生。华国生从同事的角度，再现了一名忠贞不渝、爱岗敬业的模范干部；吕二伟从战友的视角，展现了一个乐于助人、锐意进取的好班长；张璇用孟祥斌妻子的口吻，描绘了一位公而忘私、孝敬父母的好丈夫。金华交通广播电台记者霍箭作为报告团特殊成员，以一名亲历者的身份，讲述了孟祥斌纵身一跃、舍生取义的感人壮举。 短暂而朴实的报告会使会场的师生无不动容，每一位参加者都经历了一次深刻的心灵洗礼。许多同学说，孟祥斌刻苦钻研的优秀品质、慷慨助人的高尚情操、忠于职守的职业道德无不让人敬佩，作为新时期的大学生，我们一定要学习孟祥斌，践行社会主义核心价值体系，勤奋学习，为构建和谐校园、和谐社会贡献自己的力量。 （文 陈静泓  摄影  杨斌 ）', 'http://info.jhc.cn/2012/1109/c201a2300/page.htm', 'http://info.jhc.cn/_upload/article/images/77/92/b9cb5a4b4aebb20a86ec0842ddc7/a9a05815-08f1-4748-a701-fe329fe56c4e.jpg|http://info.jhc.cn/_upload/article/images/77/92/b9cb5a4b4aebb20a86ec0842ddc7/31ed23e6-ab88-40fb-b44a-8f1cea0b4e8c.jpg|http://info.jhc.cn/_upload/article/images/77/92/b9cb5a4b4aebb20a86ec0842ddc7/434e57bb-2296-41f6-972c-bf03928dd342.jpg|http://info.jhc.cn/_upload/article/images/77/92/b9cb5a4b4aebb20a86ec0842ddc7/3a0b8b54-2876-48fe-bb1b-3ddd5ddd77cd.jpg|http://info.jhc.cn/_upload/article/images/77/92/b9cb5a4b4aebb20a86ec0842ddc7/46948c53-b85f-4d68-811e-664b68a8a759.jpg|http://info.jhc.cn/_upload/article/images/77/92/b9cb5a4b4aebb20a86ec0842ddc7/042dacab-fb22-4879-b2e3-9a13ada2657b.jpg|http://info.jhc.cn/_upload/article/images/77/92/b9cb5a4b4aebb20a86ec0842ddc7/3718a36e-319e-4dd1-9112-7611196f3819.jpg|http://info.jhc.cn/_upload/article/images/77/92/b9cb5a4b4aebb20a86ec0842ddc7/79057f79-fa80-4e05-b94d-0596bc582267.jpg', '2017-05-17 14:16:48.604978');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (421, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院举行离退休教职工茶话会', '农历9月9日是重阳节，为了给离退休教职工带去学院的关心和祝福，10月15日，信息学院召开离退休教职工茶话会，大家欢聚一堂，共度佳节。 学院总支委员吴晓明代表学院给老同志们带去了节日的祝福，他向大家介绍了学院近期工作，重点介绍了“十二五”规划、省级特色专业建设、项目部改革、创先争优活动开展等情况。他希望离退休老同志们一如既往支持学院发展，并祝大家节日快乐、健康长寿。 离退休老同志们十分关心学院发展，结合学院重点工作谈了许多见解。大家说，虽然退休了，但仍然要加强学习，仍要关心学院建设，使自己紧跟学院发展步伐，为学院发展尽绵薄之力。 （ 文/摄影 王伟斌 ）', 'http://info.jhc.cn/2012/1109/c201a2297/page.htm', NULL, '2017-05-17 14:17:01.318962');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (422, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '缅怀孟祥斌', '4月1日晚，在金职院实验剧场，600多位信息学院的学生党员、入党积极分子以及大一、大二新生，聆听来自孟祥斌生前所在部队英雄事迹报告团所作的孟祥斌事迹报告。在清明节来临之际，学生们以这样的方式缅怀大家心目中的英雄。      （记者 吴潮宏） 新闻链接： http://www.jhnews.com.cn/site1/jhrb/html/2010-04/04/content_1120671.htm', 'http://info.jhc.cn/2012/1109/c201a2291/page.htm', 'http://info.jhc.cn/_upload/article/images/6a/7a/8ec876224acf8568cd14099aa473/a94519bf-bf07-4183-aa04-ec90296b5a84.jpg', '2017-05-17 14:17:01.412217');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (423, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '我院番茄电脑社等学生社团参与金华电视台主办的志愿者服务队伍', '新闻链接：http://www.36tv.cn/viewnews-70661', 'http://info.jhc.cn/2012/1109/c201a2288/page.htm', 'http://info.jhc.cn/_upload/article/images/d9/bb/44ecdf2e4ef389c736532026a707/1bee3595-1ddb-4033-9021-1a36b86e1f3c.jpg', '2017-05-17 14:17:01.430457');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (424, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '奥地利师生来金职院学习交流', '3月30日，金华职业技术学院信息工程学院迎来了一批特殊的客人。他们是来自奥地利布劳瑙高等职业学校（HTL Braunau）机电类专业的26名师生。此次来华，在金职院进行为期两天的学术交流。  　　下午，参观了金职院的校园设施后，客人们来到信息工程学院实验室，与24名中国学生一起体验中国职业教育课。中、奥两国48名学生，两人一组围坐在计算机前交流专业课程。  　　这次来中国,师生们利用的是当地的“复活节”假期。为了让中国人一睹奥地利民俗风采，不少女生穿上了当地的传统服饰：大红、大绿，配着夸张花边的五彩洋装，在金发碧眼、雪白肌肤的衬托下，宛如童话世界中的女孩。  　　“这是我第一次来中国，比起上海，我更喜欢金华。”19岁的奥地利男孩艾里克斯说。因为飞机直飞，他们在上海停留了两天。“上海和全世界的大城市一样，都是现代化建筑与摩天大楼，金华却很安逸，有自己的味道。”来学校之前，他们一行参观了茶花公园。“奥地利的茶花跟这儿不一样，平时我们很少有机会看到。”  　　说起金职院校园，艾里克斯的第一个印象就是“大”。“在奥地利，我们学校只有一幢教学楼，没有食堂，只有一间小型咖啡屋，供学生休息。”艾里克斯说，奥地利的校园里多是老建筑，他们的教学楼也有一段历史了。全校1000多名学生有200多人住校，宿舍楼是新建的，与教学区相通。  　　提起中国高职教育与奥地利职业教育的差异，艾里克斯说，在奥地利，上课多是小班教学，一个班18~30名学生不等。在教学上，奥地利的教育比较传统。“我们是先理论后实践的教育，不像中国这样把理论与实践结合到一起，一边上机操作一边讲理论。我们只有在实际操作时，才使用电脑。”  　　艾里克斯说，他们的教育分三大块：专业理论课、实践操作以及基础课。在奥地利，基础课包括数学、德语（奥地利母语）、英语等等。另外还有选修课，如：法语、俄语等。“我们的上课时间很长，从上午8点一直上到下午5点，不过放学后没有作业。”  　　毕业后，奥地利高职教育的选择比较宽泛。他们可以选择考大学，也可以选择直接就业。艾里克斯表示，自己准备继续念大学。  　　金职院通信网络与设备专业女生袁攀攀说，下课后，她与奥地利同学用英文交流了双方的学习情况。“与外国学生一起上课，不仅能提高英语交流能力，在专业上也能互取所长，共同进步。”她说，希望自己以后能常有这样的机会，与外国朋友交流学习。  　　奥地利老师也给奥方学生上了课，请中方师生观摩。      （记者  徐 序 吴潮宏） 新闻链接：http://www.jhnews.com.cn/site1/jhrb/html/2010-04/03/content_1120546.htm', 'http://info.jhc.cn/2012/1109/c201a2284/page.htm', 'http://info.jhc.cn/_upload/article/images/5d/f8/3db2b8f84461af163dd52ed0802f/8e6b492b-c1e4-462b-a041-f776d7a84843.jpg', '2017-05-17 14:17:01.452601');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (425, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院电气自动化技术专业召开课程标准论证会', '2月26日，信息学院召开电气自动化技术专业课程标准论证会。来自汉森机电、兰溪电光源、竞远机械等企业的专家应邀出席，电气自动化技术专业全体教师参加论证会，会议由专业主任马汝星主持。 会上，专业骨干教师首先对本专业12门主干课程标准的编制情况做了简要汇报，并提交了课程标准（论证稿）供专家论证。与会专家对课程标准进行认真地研讨和论证后对专业课程培养目标的定位、课程设置、内容选择与岗位能力融合度等方面给予了肯定，认为《PLC控制系统设计与调试》、《工厂供电技术》这两门课标切实做到了教学与实践对接，体现了职业教育课程改革的精神，同时大家也就存在的不足提出了一些中肯和富有建设性的建议，为下一轮课程标准的修订和完善提供了思路。会上与会专家还就如何进一步强化“理实一体化教育”、“课程考核如何与专业资格考试对接”等方面进行了有益的探讨。 本次论证会的召开，对于电子自动化专业课程标准的完善有着非常重要的意义，同时为校企合作开发课程也奠定了坚实的基础。 （文  俞冬丽）', 'http://info.jhc.cn/2012/1109/c201a2280/page.htm', NULL, '2017-05-17 14:17:01.473016');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (426, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院举行第十三期入党积极分子培训班开学典礼', '4月8日，信息学院第十三期入党积极分子培训班开学，学院党校校长、党总支书记朱登胜、党校校务委员会成员及151名学员参加开学典礼。 开学典礼在激昂的国歌声中拉开帷幕。朱登胜首先对全体学员表示祝贺，他勉励大家在党校认真学习党的理论知识，尤其是十七大以来党的各项路线、方阵、政策，努力学习科学文化知识，从理论上入党。要利用一切可能的机会投身社会实践，要充分发挥共产党员先进性，帮助周围同学共同成长，从行动上入党。一名党员就是一面旗帜，入党积极分子要努力对自己高标准、严要求，做好表率作用，从思想上入党。 开学典礼上，计091班的张瀚一代表本期培训班学员发言，表示会珍惜此次培训的机会，积极改造主观世界，提高思想境界，以实际行动为早日成员一名优秀的共产党员而努力。 （文  陈静泓）', 'http://info.jhc.cn/2012/1109/c201a2275/page.htm', 'http://info.jhc.cn/_upload/article/images/d2/15/e4c3c73b46ec84f333a054827a4a/6a64b035-3fe4-4516-a28e-39616a661ad0.jpg|http://info.jhc.cn/_upload/article/images/d2/15/e4c3c73b46ec84f333a054827a4a/4d54d205-4421-4838-8671-2c6506c126ac.jpg|http://info.jhc.cn/_upload/article/images/d2/15/e4c3c73b46ec84f333a054827a4a/6dd12cf7-96d0-49d9-a0bb-0b63271783a9.jpg', '2017-05-17 14:17:01.495404');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (427, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院离退休支部换届选举顺利举行', '4月16日上午，信息学院离退休支部、退休协会举行换届选举工作，全体离退休教职工参加，选举由学院党总支书记朱登胜主持。 原离退休支部书记付恒在会上传达了刚刚结束的金华市离退休支部书记工作会议精神。他说，市委市府和学校对离退休支部工作高度重视，采取了许多行之有效的措施确保离退休支部生活会开展的有内容、有意义。许多离退休支部也在会上交流了各自开展的丰富活动，使我们感觉到，离退休支部可以通过支部生活会，使退休生活有所为，有所大为。 在公平、公正、公开的环境中，离退休支部党员通过民主讨论、酝酿、提名、投票、唱票等环节，最终选举产生了以付恒为支部书记的新一届支部成员。会上还对退休协会成员进行了换届选举。 新一届离退休支部、退休协会成员在会后对新一年的离退休工作进行了讨论，并制定了新一年工作计划。 （文  王伟斌）', 'http://info.jhc.cn/2012/1109/c201a2272/page.htm', NULL, '2017-05-17 14:17:01.517526');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (428, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院举行“愉悦身心 畅享和谐”校企游园活动', '11月6日，信息学院举行“愉悦身心 畅享和谐”校企游园活动，金华利诚信息技术有限公司员工代表与学院全体教职工在欢声笑语中一同游园。 游园活动设置了5个集体项目和5个个人项目。集体项目中，“气球接力”考验团队成员运气球过程中吸气技巧以及队员间的配合，“瞎子敲锣”则对每个参赛队员的时间感、方位感提出了精确度要求；个人项目上，每当有队员在“反弹球入框”上成功弹入一只球都会引起大家的赞叹声，而“丢沙包”中精准的丢掷点产生时，大家的欢呼声也随之而起。整个游园活动精彩有余、热闹不断。 金华利诚信息技术有限公司与信息学院开展了四届“5173订单班”人才培养探索，实现了校企互赢。因此，参加此次游园活动的企业员工和学院教师都说，这样的活动，在加深校企双方共同合作的同时，也促进了双方职工的沟通和了解，对今后校企合作地深入开展大有裨益。校企双方均表示将会多举行这样的联谊活动。 （ 文 王伟斌  摄影 杨斌 ）', 'http://info.jhc.cn/2012/1109/c201a2267/page.htm', 'http://info.jhc.cn/_upload/article/images/8d/e3/e2c7097842a3bbb8bdc6961509fd/275e324b-2c8a-4ff0-a2c6-8c352d8ffef0.jpg|http://info.jhc.cn/_upload/article/images/8d/e3/e2c7097842a3bbb8bdc6961509fd/8257f4da-7d71-4c58-afe0-a9d732142602.jpg|http://info.jhc.cn/_upload/article/images/8d/e3/e2c7097842a3bbb8bdc6961509fd/c58560f0-d67b-4c22-844e-24d60b4b8053.jpg|http://info.jhc.cn/_upload/article/images/8d/e3/e2c7097842a3bbb8bdc6961509fd/03f47694-cffa-494f-802f-c286c355baed.jpg', '2017-05-17 14:17:01.539772');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (429, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '广西水利电力职业技术学院来我院考察交流', '4月12日，广西水利电力职业技术学院由计算机与信息工程系主任宁爱民带队，一行7人来我院考察交流。 宾主双方在热情友好的气氛中进行了座谈。党总支书记朱登胜就我院师资、招生、就业、科研等情况向客人作了介绍。副院长陈海荣则就我院的专业设置、顶岗实习以及当前的示范院校建设等情况与客人进行了交流。 在随后的对口专业交流中，我院各专业负责人就工学结合的办学思路及人才培养方案，实验实训室和实训基地建设经验，面向就业的人才培养体系、教学方案、操作方法等做了介绍，双方还就各自学校在专业建设上的其他经验进行了交流探讨。 （文  王伟斌）', 'http://info.jhc.cn/2012/1109/c201a2261/page.htm', 'http://info.jhc.cn/_upload/article/images/45/a3/c7bbe13e4738b1a67cd74830f879/72fbad78-94d9-46b2-a49f-83e3d07b0ce4.jpg|http://info.jhc.cn/_upload/article/images/45/a3/c7bbe13e4738b1a67cd74830f879/1ea563d5-32ca-4b96-be06-ea8c96a75fb6.jpg|http://info.jhc.cn/_upload/article/images/45/a3/c7bbe13e4738b1a67cd74830f879/bd640ad0-f1f5-46ed-a772-5040448726ff.jpg', '2017-05-17 14:17:01.561919');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (430, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '先声夺冠，唱响和谐', '——信息学院举行2010年卡拉OK大赛决赛                                      4月12日晚，信息学院“先声夺冠，唱响和谐”2010年卡拉OK大赛决赛在实验剧场隆重举行。历经一个月初赛、复赛的层层选拔，最终有8名选手一路过关斩将，成功入围本次决赛。 决赛中，选手们各施所长，尽显风采。流行乐曲、摇滚、戏曲等各种曲艺形式轮番上阵，或热情奔放，或轻松活泼，或哀婉缠绵，深情的演唱伴以优美的舞蹈，激发了场下观众澎湃激情。挥舞的荧光棒，热烈的呐喊声，将比赛一次次推向高潮。最终来自电商092班的朱章雷同学以一曲摇滚风《光辉岁月》脱颖而出，夺得本次大赛冠军。 本次卡拉OK大赛为同学们提供了一个展示自我、提升素质的艺术舞台，充分展现了同学们的青春活力，更活跃了学院的艺术氛围和文化气息。 （文  陈静泓  郑俊健  摄影  杨斌）', 'http://info.jhc.cn/2012/1109/c201a2248/page.htm', 'http://info.jhc.cn/_upload/article/images/0b/a4/d5f1d116433db2f79532e7aa18e7/0dc90503-4926-4e9c-9437-87d16df95215.jpg|http://info.jhc.cn/_upload/article/images/0b/a4/d5f1d116433db2f79532e7aa18e7/b0f0e87d-8458-40be-80d7-2187928a2f27.jpg|http://info.jhc.cn/_upload/article/images/0b/a4/d5f1d116433db2f79532e7aa18e7/1b7cdaed-5759-417f-ae3b-8dc5b77b9a76.jpg|http://info.jhc.cn/_upload/article/images/0b/a4/d5f1d116433db2f79532e7aa18e7/812f0146-5917-47e3-bc33-3c1d5348172d.jpg|http://info.jhc.cn/_upload/article/images/0b/a4/d5f1d116433db2f79532e7aa18e7/0406ee10-5392-4d37-b055-92f32f36e392.jpg|http://info.jhc.cn/_upload/article/images/0b/a4/d5f1d116433db2f79532e7aa18e7/4a9fbe7f-d228-414f-89f4-806f3308b724.jpg|http://info.jhc.cn/_upload/article/images/0b/a4/d5f1d116433db2f79532e7aa18e7/16f4e279-52bd-493b-99d2-c57fb4cebfef.jpg|http://info.jhc.cn/_upload/article/images/0b/a4/d5f1d116433db2f79532e7aa18e7/22c70f8f-1e7e-47b8-9d83-edb6c24e8efb.jpg|http://info.jhc.cn/_upload/article/images/0b/a4/d5f1d116433db2f79532e7aa18e7/2e82d03f-8784-49d4-99fc-7253e1e556d0.jpg|http://info.jhc.cn/_upload/article/images/0b/a4/d5f1d116433db2f79532e7aa18e7/02009b5b-80e8-4cce-9fe9-2595a9958fa6.jpg|http://info.jhc.cn/_upload/article/images/0b/a4/d5f1d116433db2f79532e7aa18e7/401703c3-5fb5-4682-85be-cbdbddfd3fce.jpg|http://info.jhc.cn/_upload/article/images/0b/a4/d5f1d116433db2f79532e7aa18e7/e9940e21-52bb-466e-8881-190856be4873.jpg', '2017-05-17 14:17:01.584216');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (431, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院举行兼职教师教学规范培训会', '为加强兼职教师的管理，规范兼职教师教学工作，4月10日，信息学院举行兼职教师教学规范培训会，来自各专业的20余位兼职教师参加培训会。 培训会上，副院长王成福结合《PLC控制系统设计与调试》课程，就我院课程改革情况作了说明，并对兼职教师就如何组织课堂教学，如何有效实施课堂管理等方面提出了建议和要求。学院教科办根据对兼职教师的检查、听课情况，就如何合理运用PPT进行教学等具体问题，与兼职教师展开交流互动。 本次培训会兼职教师们普遍反映很受启发，特别是在教学规范方面，通过这次培训有了更多新的认识和提高。 （文  李瑞东）', 'http://info.jhc.cn/2012/1109/c201a2246/page.htm', NULL, '2017-05-17 14:17:01.606355');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (432, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '我院学生“一元钱一瓶水”爱心送旱区活动受金华电视台采访报道', '新闻链接：http://www.36tv.cn/viewnews-71548', 'http://info.jhc.cn/2012/1109/c201a2241/page.htm', 'http://info.jhc.cn/_upload/article/images/fa/6e/dc11c168428292e00349e024dafb/a72d9435-a683-4e69-abd3-6cbf5f094ef3.jpg', '2017-05-17 14:17:01.628342');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (433, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学子在省第七届大学生程序设计竞赛中喜创新绩', '2010年第七届acm竞赛     参赛队', 'http://info.jhc.cn/2012/1109/c201a2237/page.htm', 'http://info.jhc.cn/_upload/article/images/a3/64/4098104f406c903392eccf1520a4/43205a72-9781-408c-a52f-f3ba96871348.jpg|http://info.jhc.cn/_upload/article/images/a3/64/4098104f406c903392eccf1520a4/38ddfc42-af26-4d89-991a-aa5370628908.jpg', '2017-05-17 14:17:01.650843');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (434, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院举行第三届宿舍文化节', '4月18日，大学生活动中心小广场、西教2号楼羽毛球场欢呼声、呐喊声此起彼伏，信息学院第三届宿舍文化节公寓吉尼斯比赛热闹开场。 在呼啦圈比赛现场，一个个呼啦圈如一条条彩带在选手们腰间自如地运转，大家脸上写满了争分夺秒的比赛热情；旁边“金鸡独立”的同学也互不相让，一个个纹丝不动，比拼着平衡力与耐力。单手托乒乓球赛跑、自行车慢骑比赛，每个赛场上，选手们都努力发挥着自己的最佳水平。 据悉，公寓吉尼斯是信息学院第三届宿舍文化节活动之一。文化节上还将开展“那一剪风情”剪纸比赛、“电脑视觉秀”图片征集、寝室内务技能评比等丰富多彩的活动，这些活动在为同学们的课余生活增添乐趣的同时，也必将推动学院公寓文化建设。 （文陈静泓  郑俊健）', 'http://info.jhc.cn/2012/1109/c201a2235/page.htm', NULL, '2017-05-17 14:17:01.673012');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (435, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院举行职业生涯规划讲座', '为增强学生职业意识，树立科学合理的职业观，4月18日晚，信息学院邀请NIIT企业的联想LCSE讲师、获H3C认证网络工程师仲煜为学院番茄电脑社等社团成员作职业生涯规划讲座。 仲煜的讲座结合当前就业形势，就职业生涯规划的概念、个人规划与社团发展的关系以及如何制定职业规划等问题，进行了深入浅出的阐述。讲座中他还以小组游戏的方式让大家体会职业生涯规划中分析自我、明确方向、付诸行动的各个环节，感受职业生涯规划的重要性。 （文 陈静泓）', 'http://info.jhc.cn/2012/1109/c201a2232/page.htm', NULL, '2017-05-17 14:17:13.413025');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (436, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院召开党建工作研讨会', '4月15日，信息学院在学院516会议室召开党建工作研讨会。学院各支部书记、支部委员参加会议。会议由学院党总支书记朱登胜主持。 研讨会首先对学院2010年党建工作进行了总体部署。信息学院2010党建工作将围绕“和谐信息”的主题，通过开展党建进社团、支部QQ群建设、网上党建平台建设、党员先锋岗等创新活动，提升党建工作的活力和创新力，提高党建工作服务人才培养、服务教学工作的能力和水平。同时也要通过班子民主生活会、学习型党组织创建、党员宣誓仪式、学生三下乡等常规党建工作，做细做实日常党建工作，切实发挥党员干部的先锋模范作用。 研讨会上，大家还对党建工作中的规章制度、党员发展流程及其他党建知识进行了学习和交流。 （文  王伟斌 ）', 'http://info.jhc.cn/2012/1109/c201a2229/page.htm', NULL, '2017-05-17 14:17:13.462688');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (437, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院志愿者义务家电维修服务社区', '近日，信息学院番茄电脑社成员走进社区芙峰街，参加了由金华电视台百姓零距离栏目举办的便民志愿者活动。志愿者们一边为社区居民维修小家电与电子产品，一边讲解电脑电器的日常维护方法，宣传家电的合理安全使用知识，忙的不亦乐乎。 据悉，自百姓零距离栏目便民志愿者活动启动以来，番茄电脑社、家电维修协会等众多社团积极加入志愿者队伍，先后为城北街道、阳光路等社区提供服务，在发挥专业职业技能型社团的社会辐射作用的同时，也给社团成员提供了社会实践的机会。 （文 陈静泓  郑俊健 ）', 'http://info.jhc.cn/2012/1109/c201a2227/page.htm', NULL, '2017-05-17 14:17:13.485018');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (438, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院举行“那一剪风情”剪纸比赛', '轻巧的喜鹊伫立枝头，威风凛凛的老虎跃然纸上，年年有“鱼”嬉戏莲间，4月21日，在信息学院举办的“那一剪风情”剪纸作品评比活动上，一幅幅精彩的剪纸作品吸引了评委和观众们的眼球。 活动现场，共展出了40多幅学生亲手制作的剪纸作品。这些作品内容丰富、造型多样、剪刻入微、充满情趣，无不凝聚了作者独特的构思。据悉，本次评比是信息学院宿舍文化节活动之一，旨在培养同学们的动手能力与审美情趣，传承民间剪纸技艺。  （文 陈静泓）', 'http://info.jhc.cn/2012/1109/c201a2222/page.htm', 'http://info.jhc.cn/_upload/article/images/db/04/80a8c4164a52b1fee581f5d839eb/c005cdab-499f-41be-8743-b1ec827fb8cb.jpg|http://info.jhc.cn/_upload/article/images/db/04/80a8c4164a52b1fee581f5d839eb/0ad29387-f33a-4975-bdde-941bda2b5eb4.jpg|http://info.jhc.cn/_upload/article/images/db/04/80a8c4164a52b1fee581f5d839eb/6174932a-9d12-4dfd-b6bb-a9f82b8eba0b.jpg', '2017-05-17 14:17:13.509846');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (439, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '浙江省大学生职业技能竞赛“嵌入式产品开发”项目竞赛', '4月24日，2010年浙江省大学生职业技能竞赛“嵌入式产品开发”项目竞赛在我院开赛，院长王振洪、省教育厅高教处副处长陈雷及学校相关部门负责人出席开幕式。来自省内12所高职院校的参赛师生参加开幕式。开幕式由副院长胡野主持。 王振洪在开幕式上致辞，他首先对来自省内兄弟院校的参赛师生表示欢迎。他说，我校信息学院应用电子技术专业作为国家示范性高等职业院校中央财政重点支持建设的专业，将嵌入式系统教学引入专业课程体系，并从企业引进了嵌入式开发经验丰富的工程技术人员，同时与美国ALTERA公司共建了有30个工位的SOPC联合实验室，有力地推动了我校在嵌入式系统方面的教学改革。他希望通过“嵌入式产品开发”项目竞赛平台，与兄弟院校同行深入交流，共同推进电子信息类专业教育教学改革。 陈雷在致辞中说，希望通过本次比赛，以赛为媒，引导我省高职院校关注电子行业新技术的发展趋势与技术应用方向，指导和推动电子信息类专业开展教学改革，加快我省电子系统产品设计与制造高技能应用型人才的培养。 据悉，本次竞赛采取团队比赛方式，每个参赛队由3名选手组成，参赛队在为期2天的时间内组装调试一台“探月车”，并在嵌入式开发环境下编制个性化的控制软件。全省共有27支队伍参赛，共同角逐参加全国职业院校技能大赛名额。 （ 文 王伟斌  摄影  杨斌 ）', 'http://info.jhc.cn/2012/1109/c201a2216/page.htm', 'http://info.jhc.cn/_upload/article/images/97/09/dfe9004b4e5e9dde0704007a10de/3df6a923-c5cd-4d87-bed0-551826df133d.jpg|http://info.jhc.cn/_upload/article/images/97/09/dfe9004b4e5e9dde0704007a10de/be3bfc14-216a-4017-96bc-51a9b8839cb0.jpg|http://info.jhc.cn/_upload/article/images/97/09/dfe9004b4e5e9dde0704007a10de/607e0e44-1129-4c4d-9b22-b0e22f4c039b.jpg', '2017-05-17 14:17:13.52936');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (440, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '《金华晚报》报道省大学生职业技能竞赛“嵌入式产品开发”项目比赛在我院举行', '一比高低 为期三天的2010年浙江省高校学生职业技能大赛暨全国职业院校技能大赛选拔赛“嵌入式产品开发”项目竞赛在金职院举行。昨天，来自全省高校等单位的27个代表队参加了嵌入式基础知识测试、产品系统调试与装备等内容的竞赛。据了解，嵌入式系统作为“后PC时代”的新秀，是当前最热门的IT应用领域之一。通过此项竞赛，旨在引导高职院校关注电子行业新技术的发展趋势与技术。 （记者  缪小芬 报道 ）', 'http://info.jhc.cn/2012/1109/c201a2211/page.htm', 'http://info.jhc.cn/_upload/article/images/88/45/203576a6453bae5e26489c3ca2f3/67644579-566a-4852-a6dd-bb371aedf8a7.jpg', '2017-05-17 14:17:13.551507');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (441, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学子情系玉树，捐款祈福', '为了深切悼念玉树地震中遇难同胞，表达信息学子对玉树人民的祝福，近日，信息学院开展了“祈福玉树，祈福中国”系列活动。 活动中，同学们以团支部为单位纷纷以召开班团政治课等形式了解救灾情况，为遇难者默哀，为玉树祝福。大家还踊跃捐款奉献爱心，截止4月27日，共募得捐款9800余元。学院在教学楼门厅内悬挂了签满同学们祝福语句的“信息学子，祈福玉树，祈福中国”横幅，展示了一组组全国人民抗震救灾的感人图片，以此表达信息学子对罹难同胞的深情缅怀和为玉树人民祈福。同学们表示，希望通过这一系列活动给玉树同胞送去温暖，为他们重建家园尽自己的绵薄之力。 （文  陈静泓）', 'http://info.jhc.cn/2012/1109/c201a2206/page.htm', 'http://info.jhc.cn/_upload/article/images/d7/6d/7f9d340442898538c8a973a8dd72/30f16312-6550-4269-9a75-11475fd6ed27.jpg|http://info.jhc.cn/_upload/article/images/d7/6d/7f9d340442898538c8a973a8dd72/16628f94-c418-471a-a787-9f39f6606195.jpg|http://info.jhc.cn/_upload/article/images/d7/6d/7f9d340442898538c8a973a8dd72/32777ae3-175a-407c-b5cb-49b27c388e2a.jpg', '2017-05-17 14:17:13.573945');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (442, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院第六届社团文化节开幕', '4月29日晚，信息学院“放飞青春梦想，激扬社团文化”励志影片赏析活动在实验剧场隆重举行，来自22个社团的近千名会员参加了本次活动。 活动通过美国校园电影《歌舞青春》给大家带来了一场视听盛宴，精彩的剧情、动感的音乐充分展现了青少年的青春活力，更唤起了大家对梦想、对人生的坚定信念与执着追求。本次励志影片赏析活动在引导社员培养健康向上的价值观的同时，也标志着信息学院第六届社团文化节的正式开幕。 据了解，本届社团文化节还将陆续开展轮滑公开赛、辩论赛、定向赛、家电服务进社区、青春舞会等活动，以此丰富社员生活，增进相互了解，营造积极健康文明的社团文化氛围。 （文 陈静泓）', 'http://info.jhc.cn/2012/1109/c201a2203/page.htm', NULL, '2017-05-17 14:17:13.596064');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (443, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院召开三届三次教职工暨工会会员代表大会', '5月5-6日，信息学院召开三届三次教职工暨工会会员代表大会。 本次大会是在学院总结示范建设成果、探索“后示范”时期的专业改革、寻找教学组织与管理改革的落脚点和着力点的背景下召开的。会上，龚永坚同志作了题为《总结示范专业建设经验  推进学院工作全面发展》的院长工作报告。代表们认真审议了报告，大家一致认为，报告实事求是地回顾了一年来学院的发展情况，特别是对2009年的示范建设工作做了一个回顾。报告肯定了成绩，也指出了问题，并明确了学院今后的发展目标和工作重点。 会上还审议了朱登胜同志作的工会工作报告，审议并原则通过了《信息工程学院2010年成教工作管理与奖励办法》。 （文/摄影  王伟斌）', 'http://info.jhc.cn/2012/1109/c201a2198/page.htm', 'http://info.jhc.cn/_upload/article/images/15/ce/08213c6d47c6a6d712ac80422a62/c2aeb0f6-0d99-416c-a125-b2a504504c34.jpg|http://info.jhc.cn/_upload/article/images/15/ce/08213c6d47c6a6d712ac80422a62/e29837f6-4cc7-4b3b-a7ce-c49df674d3b9.jpg|http://info.jhc.cn/_upload/article/images/15/ce/08213c6d47c6a6d712ac80422a62/cc8c1c7a-94cd-42a3-9f7d-1e92b38dff99.jpg|http://info.jhc.cn/_upload/article/images/15/ce/08213c6d47c6a6d712ac80422a62/46a7d4bb-453c-47d7-b258-31aba7c69392.jpg', '2017-05-17 14:17:13.618268');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (444, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '金职院第十届电脑节开幕', '本报讯（记者何百林）5月14日下午，金职院第十届电脑科技文化节开幕，在为期一个月的时间里，该院将先后开展健康网络普及周、专业技能展示周、创新思维活动周等一系列活动。 据了解，金职院第十届电脑科技文化节由金职院主办，金职院信息工程学院承办，文化节的主题是“信息引领时代·科技成就梦想”。该活动通过多个以相关专业知识为基础的专项比赛，让大学生在第二课堂的活动中培养动手能力，激发大学生学习专业知识和科技文化知识的热情。   新闻链接：http://epaper.jhnews.com.cn/site1/jhwb/html/2010-05/15/content_1137044.htm', 'http://info.jhc.cn/2012/1109/c201a2195/page.htm', NULL, '2017-05-17 14:17:13.641405');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (445, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '健康生活，快乐学习――信息学院举行第七届田径运动会', '5月9日，信息学院第七届田径运动会在校运动场隆重举行。学校副院长胡野及信息学院有关领导出席开幕式，学院全体教师及大一、大二全体学生参加了运动会。  本届运动会，来自35个班级的476名运动员参加了短跑、长跑、接力、跳远等19个项目的角逐。赛场上，运动员们奋勇拼搏，努力赛出水平，赛出风格；啦啦队则热情地为健儿们呐喊助威。尽管天公不作美，雨淅淅沥沥地下着，但运动员们激情不减，充分发扬了奥林匹克精神，展现了良好的精神风貌。  经过一天紧张激烈的比赛，电商092、楼宇081分获大一、大二团体总分第一名，通信091等4个班级获精神文明奖，楼宇081班的诸葛伶俐和网络091班的戴海燕分别打破跳高、跳远项目学院田径运动会记录。 （ 文 陈静泓  摄影 杨斌 ）', 'http://info.jhc.cn/2012/1109/c201a2184/page.htm', 'http://info.jhc.cn/_upload/article/images/c1/73/73a4d5d945b4b380f2a40c712582/8380c04f-0eff-4af8-802f-c1c987daef6e.jpg|http://info.jhc.cn/_upload/article/images/c1/73/73a4d5d945b4b380f2a40c712582/99a214be-1e82-46ba-aa56-9b1d17db0e22.jpg|http://info.jhc.cn/_upload/article/images/c1/73/73a4d5d945b4b380f2a40c712582/d09b5962-d10c-494e-8a95-66f00bd32b7d.jpg|http://info.jhc.cn/_upload/article/images/c1/73/73a4d5d945b4b380f2a40c712582/a42c8bcc-bbb1-4e1e-bd04-9f550d8fe686.jpg|http://info.jhc.cn/_upload/article/images/c1/73/73a4d5d945b4b380f2a40c712582/fa19c2cc-81ce-44ae-863b-6020fcd86200.jpg|http://info.jhc.cn/_upload/article/images/c1/73/73a4d5d945b4b380f2a40c712582/9542f09b-e668-477b-9de8-b46a53bc6d1a.jpg', '2017-05-17 14:17:13.662717');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (452, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '校第十届电脑科技文化节之班徽设计大赛落幕', '经过为期20多天的报名、选拔、评比，校第十届电脑科技文化节之 “智慧勾画团结，心灵描绘凝聚”班徽设计大赛日前进入决赛环节。 进入决赛的10个班徽作品设计别具一格，或以紧握的拳头代表团结，或以飞翔的天使象征护理人员，或将专业特点与班级名称进行完美融合。决赛中，通过班级风采展示、班徽整体效果评比以及选手答辩三方面的评比，最终建工学院建工091班的班徽作品以其和谐的搭配、精彩的解说和富有创意的设计理念获得本次大赛的冠军。 本次活动激发了同学们创新思维能力与动手实现能力，彰显了班级特色，有力的推动了富有凝聚力的和谐班级的构建。 （文  陈静泓）', 'http://info.jhc.cn/2012/1109/c201a2151/page.htm', 'http://info.jhc.cn/_upload/article/images/93/ed/70bfaaea4575976c0a743f421116/465435db-3d97-4402-9fa0-fca249aef9da.jpg|http://info.jhc.cn/_upload/article/images/93/ed/70bfaaea4575976c0a743f421116/de0cdaf4-ace2-47cb-afa0-e4a3c7751c39.jpg|http://info.jhc.cn/_upload/article/images/93/ed/70bfaaea4575976c0a743f421116/59fa83bb-5f90-4934-a4b2-ccec2aa08043.jpg|http://info.jhc.cn/_upload/article/images/93/ed/70bfaaea4575976c0a743f421116/39c34718-b654-4ed0-8ad9-6fdf3e62c426.jpg', '2017-05-17 14:17:26.419189');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (446, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '校第十届电脑科技文化节开幕', '5月14日，我校第十届电脑科技文化节开幕式在大学生活动中心广场隆重举行。学校副院长胡野及校相关职能部门、信息学院领导出席了开幕式。      开幕式上，胡野对本届科技文化节的举办表示热烈的祝贺，他希望同学们通过本届文化节将电脑的普及与专业学习、职业生涯规划紧密联系，共同营造浓厚的校园学术科技氛围。开幕式上举办的电脑科技文化展、大学生自主创业展销、信息学院专业图文展、电脑与家电咨询维修等活动，充分展示了信息学院专业特色与科技魅力。  据悉，本届电脑科技文化节为期一个月，主要包括健康网络普及周、专业技能展示周、创新思维活动周等，旨在通过IT讲座、电脑家电维修服务进社区以及AutoCAD平面图形设计竞赛、网店装修及运营竞赛、装机大赛等专项比赛激发学生学习专业知识和科技文化知识的热情，以促进学风建设，全力打造信息化校园。 （文 陈静泓 郑俊健）', 'http://info.jhc.cn/2012/1109/c201a2178/page.htm', 'http://info.jhc.cn/_upload/article/images/df/46/3f21e0a046a4b3c079a5e8e02ea4/1def9f53-e413-4db3-a6e3-5f9cf9a5a383.jpg|http://info.jhc.cn/_upload/article/images/df/46/3f21e0a046a4b3c079a5e8e02ea4/c8c97aad-ff18-4460-a668-d01631d15698.jpg|http://info.jhc.cn/_upload/article/images/df/46/3f21e0a046a4b3c079a5e8e02ea4/f2ff9bbf-97a9-4e00-af47-aae547427fb9.jpg|http://info.jhc.cn/_upload/article/images/df/46/3f21e0a046a4b3c079a5e8e02ea4/3625b176-1d0f-474e-a7e8-dc4e315fa56c.jpg', '2017-05-17 14:17:13.68502');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (447, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息排球健将力战群雄，再创光辉', '5月18日，校排球赛冠亚军争夺战在大学生活动中心举行。信息学院男子排球队不负众望，勇夺冠军，载誉而归。女子排球队也获得了第三名的佳绩。 本次排球赛是第十五届校运会项目之一，赛前，队员们积极备战，充分利用课余时间刻苦练习发球、传球、垫球等基本动作，不断调整技战术。比赛中，在啦啦队此起彼伏的加油声中，我院选手不畏强敌，奋勇拼搏，充分展示了排球健儿团结拼搏的精神面貌，辛勤的汗水成就了本次排球赛的突出表现。 （文 陈静泓）', 'http://info.jhc.cn/2012/1109/c201a2175/page.htm', NULL, '2017-05-17 14:17:13.707291');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (448, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院社团举行文艺舞会', '5月22日晚，校大学生活动中心一楼大厅灯光闪烁，乐声阵阵，信息学院青春逗文艺社“舞动周年庆，同秀文化节”文艺舞会在此火热进行。 舞会现场，风趣滑稽的小品、劲爆热烈的街舞、悠扬婉转的歌声将舞会一次次推向高潮，富有激情的表演感染了在场师生，大家纷纷走进舞池，随着动感的旋律翩翩起舞，曼妙的舞姿传递着青春的活力与朝气。 本场文艺舞会面向全校师生，旨在庆祝青春逗文艺社成立一周年，也给师生们提供了一个解压放松、展现自我、相互交流的平台，充分展示了社团为学生服务的宗旨。 （文  陈静泓  郑俊健）', 'http://info.jhc.cn/2012/1109/c201a2170/page.htm', 'http://info.jhc.cn/_upload/article/images/8a/88/b675b676434b877b48574503bdd1/b8401005-3819-4eb0-9f9b-488f15533cc1.jpg|http://info.jhc.cn/_upload/article/images/8a/88/b675b676434b877b48574503bdd1/23570429-b99f-47a5-801e-bbb4ff1d191c.jpg', '2017-05-17 14:17:13.72955');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (449, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '《金华日报》报道我院承办的电脑文化节', '5月17日，在金职院大学生活动中心，来自信息工程学院的志愿者熟练地帮助同学修理电脑、电风扇、吹风机等。这是金职院电脑文化节上的一幕。 金职院电脑文化节已举办十届，电脑组装、文字录入、机器人对抗赛、多媒体设计等活动项目成了大学生们展示技能的舞台。这次活动将持续到5月底。 （文/摄 金华日报记者  吴潮宏）   新闻链接：http://epaper.jhnews.com.cn/site1/jhrb/html/2010-05/20/content_1138599.htm', 'http://info.jhc.cn/2012/1109/c201a2166/page.htm', 'http://info.jhc.cn/_upload/article/images/b7/16/d2f2a21846f094a33f1a2a931207/b312c875-59de-41ff-af8f-7f511159f021.jpg', '2017-05-17 14:17:26.317926');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (450, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院赴大连东软信息学院考察CDIO教学改革', '5月20-23日，信息学院由院长龚永坚带队，计算机专业群教师一行10人就CDIO教学改革赴大连东软信息学院进行考察交流。 CDIO工程教育模式是近年来国际工程教育改革的最新成果。CDIO代表构思（Conceive）、设计（Design）、实现（Implement）和运作（Operate）,它以产品研发到产品运行的生命周期为载体,让学生以主动的、实践的、课程之间有机联系的方式学习工程。大连东软信息学院是教育部第二批CDIO教学试点学校，也是首批示范性软件职业技术学院。其基于“教育创造学生价值”所提出的TOPCARES－CDIO教育模式和网络教学平台的运用对计算机专业建设与改革有很大启发价值。 考察期间，东软信息学院副院长曹云凤教授就东软现推行的CDIO工程教育模式改革情况作了详细介绍，并与我院教师就CDIO教学模式指导下的专业建设、课程改革与实施、教学质量评价、师资队伍建设等问题进行了交流。此次考察学习对信息学院学习CDIO教学改革方面的先进经验，推进高素质应用型人才人才培养模式改革提供了有益的参考。 （文  邱晓华）', 'http://info.jhc.cn/2012/1109/c201a2161/page.htm', 'http://info.jhc.cn/_upload/article/images/4d/55/99107b9c400cae74b7588eae882f/28658b1d-d7a5-4194-b754-2daeb46b3f0a.jpg', '2017-05-17 14:17:26.374817');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (451, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '放飞青春，放飞心灵――信息学院举行放风筝活动', '5月25日是大学生心理健康日，为了丰富同学们的课余生活，缓解紧张的学习压力，信息学院举行了“放飞青春，放飞心灵”放风筝活动。 活动现场，一只只由同学们自己亲手制作的风筝，在蔚蓝的天空中自由飞翔，形态各异的风筝承载着大家的理想与希望，随风翱翔。欢快的奔跑中，同学们感受着久违的童真，体验着无尽的乐趣。 本次活动是信息学院“5.25”心理健康宣传月活动之一，学院今后还将陆续通过开展心理讲座、心理电影赏析、心理班会、团体辅导等一系列活动，帮助同学们提高心理健康水平，促进身心健康发展。 （ 文  黄蓉 ）', 'http://info.jhc.cn/2012/1109/c201a2158/page.htm', NULL, '2017-05-17 14:17:26.396967');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (459, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '交流共进促发展，总结研讨齐提升', '为进一步推动学院思政工作，加强辅导员素质建设，7月4日，信息学院召开辅导员工作经验交流会。学院党总支书记朱登胜、学工办全体人员参加了本次会议。   会上，辅导员们结合自身工作实际和经验，从公寓管理、党团工作、学风建设、招生就业、顶岗实习管理等方面分别作了汇报和交流。大家分析存在的困难，查找工作的不足，分享彼此的心得，并一致认为，团队精神与有效沟通是做好学生管理工作的重要保障。此次总结，使辅导员明确自身定位，端正工作态度，努力寻求新方法，探索新思路，为创造学生管理工作新成绩奠定了基础。   （文  陈静泓）', 'http://info.jhc.cn/2012/1109/c201a2116/page.htm', NULL, '2017-05-17 14:17:26.574959');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (453, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院举办“绿色•网络•生活”摄影展', '5月31日，经过近半个月的征集、评比，信息学院“绿色·网络·生活”摄影大赛获奖作品在西教2号楼展出。本次大赛共收到作品55组两百余幅，经过对作品的主题、艺术表现力等方面的评比，最终有10组48幅作品获奖，其中楼宇081班唐忻的黑白人物照获得本次大赛一等奖。 展出现场，一幅幅获奖作品以其独到的审美手法、精湛的构图技巧吸引着同学们的眼球。细腻的人物刻画、精美的动植物写生、静谧的古镇风情、优美的校园风光，从不同的视角展现出了人与自然的和谐景象，体现出同学们对生活的洞悉，对自然的理解。 本次摄影展呈现出了一个柔美和谐的世界，更为同学们提供了一个展示自身技能的平台，丰富了校园文化生活。 （文 陈静泓）', 'http://info.jhc.cn/2012/1109/c201a2144/page.htm', 'http://info.jhc.cn/_upload/article/images/30/dc/bc01bbd74bd7b9f8754a283e8a24/b41a19db-2c88-49d0-9c2e-9f65ff498719.jpg|http://info.jhc.cn/_upload/article/images/30/dc/bc01bbd74bd7b9f8754a283e8a24/371a5727-c962-4707-aa36-df3d0a536fbd.jpg|http://info.jhc.cn/_upload/article/images/30/dc/bc01bbd74bd7b9f8754a283e8a24/ac43ffd6-d8f8-4106-b961-06aa172706bc.jpg|http://info.jhc.cn/_upload/article/images/30/dc/bc01bbd74bd7b9f8754a283e8a24/e60c6dbe-c6f4-4ddf-98c8-fabf8b20b98b.jpg', '2017-05-17 14:17:26.441598');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (454, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院举行感恩主题教育讲座', '感恩是一种美德，更是一种责任。6月3日晚，信息学院邀请师范学院社科部王彩芳老师为同学们作了一场以“学会感恩，心怀感恩”为主题的教育讲座。 讲座中，王彩芳以“停止抱怨、学会感恩、勇担责任”为中心展开演讲，结合生动的社会实例，教育大家时刻怀揣一颗感恩之心，学会承担责任，尊重他人，在感恩中学会做人、学会做事，更好地适应社会。 讲座在一首扣人心弦的《学会感激》诗中结束，聆听讲座的许多同学表示，讲座让自己更加深切地体会到外界赋予自身的爱，对引导自己树立感恩思想，学会知恩、感恩、报恩，培养正确的世界观与人生观有很大启发意义。 （文 郑俊健）', 'http://info.jhc.cn/2012/1109/c201a2140/page.htm', 'http://info.jhc.cn/_upload/article/images/67/ad/5478c966417bb80167e31e2a8954/5ac1e1a9-1dd6-47cc-b404-57b2b33c0269.jpg|http://info.jhc.cn/_upload/article/images/67/ad/5478c966417bb80167e31e2a8954/3354b9e9-1157-4bd2-8488-aab8d118106f.jpg', '2017-05-17 14:17:26.463738');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (455, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院示范建设对外辐射力显著增强', '6月24日，由胡湘陵院长带队的福建工程学院软件学院一行8人来我校信息学院就职业教育改革等相关问题进行交流访问。 围绕应电示范专业建设理念、思路，信息学院王成福副院长向客人们介绍了学院在示范建设中的人才培养方案构建、课题体系设置、教学环境创建、校企合作模式等经验成果，并着重探讨了示范建设的特色和亮点，如课程体系改革和顶岗实习等情况。 交流中，双方教师还就高职教育办学思路和理念、校企合作经验、学生实习实训开展、工作过程系统化的课程开发方法等方面的议题作了交流讨论。 信息学院示范建设由于理念清晰、成果显著，受到了专家学者和省内外院校的认可，已有多个兄弟院校慕名来访，显示出学院对外辐射能力的不断增强。 （文/摄影  王伟斌）', 'http://info.jhc.cn/2012/1109/c201a2135/page.htm', 'http://info.jhc.cn/_upload/article/images/89/92/6e427c064a16abce385b63f81640/0b216777-b456-48ab-bff7-1701d38149f9.jpg|http://info.jhc.cn/_upload/article/images/89/92/6e427c064a16abce385b63f81640/dcf3a4a5-ea0f-4247-959c-74cc64587909.jpg', '2017-05-17 14:17:26.485939');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (456, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院离退休支部举行“七一”党员学习会', '6月25日，在建党89周年即将到来之际，信息学院离退休支部召开“七一”党员学习会，重温党的光辉历程，共叙现今美好生活。 学习会上，学院党总支书记朱登胜向老同志们介绍了2010年学院工作。他说，2010年学院各项工作稳步开展，特别是示范专业建设、学生学科竞赛等取得了显著成绩。他希望老同志们一如既往的支持学院发展。 围绕学习实践科学发展观和当前国内、国外时事，党员们重点讨论了如何推进离退休党支部建设和开展支部活动等问题，大家畅所欲言，提出了许多符合老同志年龄、身体实际情况的意见和建议。学习会上，大家还饶有兴趣地讨论了自己在老年大学学习等情况。 （文  王伟斌）', 'http://info.jhc.cn/2012/1109/c201a2129/page.htm', 'http://info.jhc.cn/_upload/article/images/d1/56/e39669f347a69b39945561a64f6d/ab4c369e-693f-4bae-a322-aa91a92282f8.jpg|http://info.jhc.cn/_upload/article/images/d1/56/e39669f347a69b39945561a64f6d/3191ca18-ba24-411a-afa4-764114f9aebf.jpg|http://info.jhc.cn/_upload/article/images/d1/56/e39669f347a69b39945561a64f6d/02f5fb8c-087f-4589-b0da-a6753205268c.jpg', '2017-05-17 14:17:26.508192');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (457, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院举行纪念建党89周年系列活动', '为了使新时期的党员加强对党的认识，不断加强党性修养，近日，信息学院在全体师生党员中举行党的知识学习会，以此纪念党的89岁生日。 学院党总支书记朱登胜希望学院新时期青年党员珍惜美好时光，自觉提高自身综合素质，深入学习实践科学发展观，培养高尚的情操，掌握科学文化知识，充分发挥共产党员的先锋模范作用，以实际行动为党旗增光添彩，投身社会主义建设的伟大事业中。 据悉，为了庆祝党的生日，信息学院还先后开展了新党员入党宣誓仪式、党员关爱基金捐款、党支部换届选举、离退休支部形势学习会等系列活动，在全院党员中形成了知党、爱党、跟党走的思想共识。 （ 文  王伟斌  周荃 ）', 'http://info.jhc.cn/2012/1109/c201a2126/page.htm', NULL, '2017-05-17 14:17:26.53033');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (458, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学子在2010年全国职业技能大赛上喜获三等奖', '从日前结束的2010年全国职业院校技能大赛上传来好消息，我校信息学院由马汝星、吕焱飞老师指导，林卓寿、王钧禾、徐俊三位同学组成的代表队在“电子设计——嵌入式产品开发”项目上喜获三等奖。 这是信息学院继成功承办2010年省大学生职业技能大赛并喜获佳绩后，首次在全国职业技能大赛上崭露头角。全国职业院校技能大赛是我国职业教育迄今为止规格最高、规模最大、项目最多的技能大赛，是各省选拔赛获一等奖团队技能精英的终极较量。此次“嵌入式产品开发”项目技能大赛，共有来自全国各省的66支代表队参加，获奖面为60%。 学院为了参加此次大赛，充分准备、积极备战，构建了最强的竞赛团队，通过参赛，有力地提升了团队技术水平，极大地丰富了大赛参赛经验。 （文  王伟斌）', 'http://info.jhc.cn/2012/1109/c201a2120/page.htm', 'http://info.jhc.cn/_upload/article/images/e3/91/d6c77f9244c1b02adbffd801ca2c/ea64c5b4-04be-444f-9224-561da8ec9c96.jpg|http://info.jhc.cn/_upload/article/images/e3/91/d6c77f9244c1b02adbffd801ca2c/55ec0543-e3cd-4e2e-aa40-f353b9880bce.jpg|http://info.jhc.cn/_upload/article/images/e3/91/d6c77f9244c1b02adbffd801ca2c/4c19aea7-227d-4d73-b5c5-2b14a00c5ccc.jpg', '2017-05-17 14:17:26.55251');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (460, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '创新实践，服务社会――信息学院2010暑期实践队走进洋埠镇', '为了解社会，深入社会，搭建新时期大学生理论联系实际，服务社会的平台，7月5日，信息学院 “百村千户行”实践队、“IT在我身边”文明网络宣传实践队、“清风”文艺实践队、企业文化调研实践队等四支实践小分队走进金华市婺城区洋埠镇，开展“创新实践，服务社会”2010年暑期“三下乡”社会实践活动。 在学院和洋埠镇政府的协调组织与大力支持下，“三下乡”实践队为当地居民开展了家用电器维修、电器安全使用知识宣传、电脑排故、知识培训、文明网络倡议等服务，并带去了自编自演的文艺表演，丰富了当地乡镇农民的业余文化。 实践过程中，队员们走进商铺、走进企业，通过走访、交流、问卷各种形式，对当地的企业文化、农村教育、农村社会经济、农村电脑普及进行了全方位的调查，以了解社会现状，为今后更好地服务社会打下坚实的基础。队员们还为居民打扫街道美化环境，得到了居民们的肯定与赞赏。 烈日炎炎，高温酷暑，挡不住实践队员们服务社会的热情与激情。相信通过社会实践这个大舞台，同学们一定能充分施展技能，感受学以致用、回馈社会的成就感和快乐感。   （文  陈静泓）', 'http://info.jhc.cn/2012/1109/c201a2113/page.htm', NULL, '2017-05-17 14:17:26.597107');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (461, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学子在第四届全国“三菱电机自动化杯”大学生自动化技能大赛上荣获特等奖', '从8月7号刚结束的第四届“三菱电机自动化杯”大学生自动化大赛暨自动化系统应用竞赛决赛上传来捷报，由我校信息学院张小杭、黄敏老师指导，陈宝燕、刘福然、庄福君三位同学组成的代表队不畏强敌，一举摘得特等奖；同时，徐月、郑磊、高浩迪组成的代表队和蔡益龙、诸葛伶俐、杨斌组成的代表队则分别在“伺服控制”和“变频调速”项目上获得2个二等奖。这是我校信息学院在今年的全国技能大赛上获得的又一好成绩。 本届大赛由中国自动化学会、教育部高职高专自动化技术类教指委主办，天津大学和三菱电机自动化（中国）有限公司承办。大赛共设特等奖2名、一等奖10名、二等奖28名及若干单项奖，获奖面69%。共有来自浙江大学、南京大学等近30所高等院校的58支代表队参加了此次决赛；普通高校代表队和高职高专代表队同场竞技、一起评奖。 该项赛事是我校第一次组织参加。为了参加此次大赛，信息学院领导重视，电气自动化工程部竞赛团队师生精心准备，在团队组建、竞赛辅导、项目演练、后勤保障等各个方面做了扎实细致工作；尤其是在暑假期间，竞赛团队师生克服种种困难，多次通宵达旦加班加点，在技术、心理等各个方面系统强化训练，最终与天津大学同台分享2个特等奖，又一次为学校在全国大赛上争得了荣誉。 （文  张小杭 王伟斌）', 'http://info.jhc.cn/2012/1109/c201a2109/page.htm', 'http://info.jhc.cn/_upload/article/images/c7/67/c821a3f3457b91861f6ac5fae37a/3f61cd07-deee-4b08-995b-b7cdf30526fe.jpg|http://info.jhc.cn/_upload/article/images/c7/67/c821a3f3457b91861f6ac5fae37a/86dd1ded-b9a5-4454-bc0e-2f234d790c0f.jpg|http://info.jhc.cn/_upload/article/images/c7/67/c821a3f3457b91861f6ac5fae37a/3c6519d2-cdd9-430a-9a80-1f2b730f7d1f.jpg', '2017-05-17 14:17:26.619309');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (462, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '交流共进促发展，总结研讨齐提升', '为进一步推动学院思政工作，加强辅导员素质建设，7月4日，信息学院召开辅导员工作经验交流会。学院党总支书记朱登胜、学工办全体人员参加了本次会议。   会上，辅导员们结合自身工作实际和经验，从公寓管理、党团工作、学风建设、招生就业、顶岗实习管理等方面分别作了汇报和交流。大家分析存在的困难，查找工作的不足，分享彼此的心得，并一致认为，团队精神与有效沟通是做好学生管理工作的重要保障。此次总结，使辅导员明确自身定位，端正工作态度，努力寻求新方法，探索新思路，为创造学生管理工作新成绩奠定了基础。   （文  陈静泓）', 'http://info.jhc.cn/2012/1109/c201a2105/page.htm', NULL, '2017-05-17 14:17:26.641806');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (463, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '创新实践，服务社会――信息学院2010暑期实践队走进洋埠镇', '为了解社会，深入社会，搭建新时期大学生理论联系实际，服务社会的平台，7月5日，信息学院 “百村千户行”实践队、“IT在我身边”文明网络宣传实践队、“清风”文艺实践队、企业文化调研实践队等四支实践小分队走进金华市婺城区洋埠镇，开展“创新实践，服务社会”2010年暑期“三下乡”社会实践活动。 在学院和洋埠镇政府的协调组织与大力支持下，“三下乡”实践队为当地居民开展了家用电器维修、电器安全使用知识宣传、电脑排故、知识培训、文明网络倡议等服务，并带去了自编自演的文艺表演，丰富了当地乡镇农民的业余文化。 实践过程中，队员们走进商铺、走进企业，通过走访、交流、问卷各种形式，对当地的企业文化、农村教育、农村社会经济、农村电脑普及进行了全方位的调查，以了解社会现状，为今后更好地服务社会打下坚实的基础。队员们还为居民打扫街道美化环境，得到了居民们的肯定与赞赏。 烈日炎炎，高温酷暑，挡不住实践队员们服务社会的热情与激情。相信通过社会实践这个大舞台，同学们一定能充分施展技能，感受学以致用、回馈社会的成就感和快乐感。   （文  陈静泓）', 'http://info.jhc.cn/2012/1109/c201a2102/page.htm', NULL, '2017-05-17 14:17:41.715667');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (464, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学子在第四届全国“三菱电机自动化杯”大学生自动化技能大赛上荣获特等奖', '从8月7号刚结束的第四届“三菱电机自动化杯”大学生自动化大赛暨自动化系统应用竞赛决赛上传来捷报，由我校信息学院张小杭、黄敏老师指导，陈宝燕、刘福然、庄福君三位同学组成的代表队不畏强敌，一举摘得特等奖；同时，徐月、郑磊、高浩迪组成的代表队和蔡益龙、诸葛伶俐、杨斌组成的代表队则分别在“伺服控制”和“变频调速”项目上获得2个二等奖。这是我校信息学院在今年的全国技能大赛上获得的又一好成绩。 本届大赛由中国自动化学会、教育部高职高专自动化技术类教指委主办，天津大学和三菱电机自动化（中国）有限公司承办。大赛共设特等奖2名、一等奖10名、二等奖28名及若干单项奖，获奖面69%。共有来自浙江大学、南京大学等近30所高等院校的58支代表队参加了此次决赛；普通高校代表队和高职高专代表队同场竞技、一起评奖。 该项赛事是我校第一次组织参加。为了参加此次大赛，信息学院领导重视，电气自动化工程部竞赛团队师生精心准备，在团队组建、竞赛辅导、项目演练、后勤保障等各个方面做了扎实细致工作；尤其是在暑假期间，竞赛团队师生克服种种困难，多次通宵达旦加班加点，在技术、心理等各个方面系统强化训练，最终与天津大学同台分享2个特等奖，又一次为学校在全国大赛上争得了荣誉。 （文  张小杭 王伟斌）', 'http://info.jhc.cn/2012/1109/c201a2097/page.htm', 'http://info.jhc.cn/_upload/article/images/6b/85/4c855aa74c0988c2f8f807af6ee3/39854812-e54b-47f9-8899-cf0b510ec8cf.jpg|http://info.jhc.cn/_upload/article/images/6b/85/4c855aa74c0988c2f8f807af6ee3/a5229dea-6b6b-4069-88b3-b912aa0769c5.jpg|http://info.jhc.cn/_upload/article/images/6b/85/4c855aa74c0988c2f8f807af6ee3/ba8411e6-5664-4e90-86eb-ee7ddd4c368d.jpg', '2017-05-17 14:17:41.88221');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (465, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学子在浙江省大学生电子设计竞赛中喜获1个一等奖，3个二等奖', '日前揭晓的TI杯浙江省第三届大学生电子设计竞赛传来喜讯，由我校信息学院廖任秀、楼尉松老师指导，朱经纬、苏宣源、林建东三位同学组成的代表队一举摘得一等奖；同时，另外三支代表队分别在“宽带前置放大电路”、“医院病床呼叫和输液监控系统”、“运水机器人”等项目中共获得3个二等奖，获奖成绩在全省25个参赛高职高专院校中名列第3。 本次大学生电子设计竞赛由浙江省大学生科技竞赛委员会主办、湖州师范学院承办，是省十大学科竞赛之一。本次比赛有来自高职高专院校150余个代表队参加，共设一等奖6个、二等奖19个、三等奖35个，获奖率为40%。在此次比赛中，我校信息学院共组织10个代表队参加，设计制作了9个实物作品，获奖率达45%，是我校参加该项赛事以来取得成绩最好的一次。 近年来，信息学院领导和各个项目部（事业部）十分重视学科竞赛和技能竞赛的组织，精心构建竞赛平台和长效机制，通过“以赛代教、以赛代学”，努力打造教师指导团队和学生创新团队，成为提升专业建设核心竞争力和持续发展力的突破点。目前信息学院各个项目部均建立了对接各自专业核心能力的学科与技能竞赛平台，一个“团队、课程、竞赛”互动对接的良性机制正逐步形成。 （文 邱晓华 王伟斌）', 'http://info.jhc.cn/2012/1109/c201a2094/page.htm', NULL, '2017-05-17 14:17:41.901599');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (466, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '校党委书记李秋华等来我院调研工作', '9月2日，校党委书记李秋华、副书记俞勇建等一行来信息学院进行调研，了解学院工作和开学准备情况。 院长龚永坚在座谈会上就信息学院在专业建设、师资团队、教学科研、校企合作、学科竞赛等方面的情况做了汇报。他说，学院在校党委的正确领导下，这几年取得了长足发展，特别是通过示范院校建设，专业建设理念更加先进，专业发展思路更加清晰，当前正以项目部改革为契机，努力推进特色专业建设和计算机专业改革。 李秋华在听取汇报后说，信息学院作为示范建设学院，积累了丰富的专业建设和发展经验。希望学院上下共同努力，在“后示范”时期，抓住学校十二五规划契机，明确发展方向，总结发展经验，制订具有前瞻性、科学性、合理性的新规划，在师资队伍建设、项目部改革等重点工作上，把握关键，解决矛盾，实现学院的可持续和跨越式发展。 （ 文/摄影  王伟斌 ）', 'http://info.jhc.cn/2012/1109/c201a2087/page.htm', 'http://info.jhc.cn/_upload/article/images/dc/87/c67fda1d438195460fd4a7a9221a/bebd1ad3-49bb-4d18-b0de-b9c981a2d762.jpg|http://info.jhc.cn/_upload/article/images/dc/87/c67fda1d438195460fd4a7a9221a/e1928522-4db1-4757-9cc4-7ea063b500d7.jpg|http://info.jhc.cn/_upload/article/images/dc/87/c67fda1d438195460fd4a7a9221a/295cc097-048e-4a3c-a98d-a03b947e9eca.jpg', '2017-05-17 14:17:41.922117');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (467, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院领导走访学生寝室', '9月5日是新学期开学第一天，也是成教新生报到的日子，为了解学生开学后的思想生活状况，落实公寓安全稳定工作，信息学院党总支书记朱登胜带领学工办人员深入寝室慰问学生。 走访过程中，朱登胜与同学们亲切交谈，他仔细询问大家假期生活情况，了解开学准备工作，同时叮嘱同学们保持寝室卫生，合理使用各种电器，积极营造一个安全的学习型寝室。走访中，朱登胜特别对成教新生表示问候，细致了解同学们对公寓建设提出的意见与建议，耐心解答大家在学习、生活等方面的问题，并鼓励同学们坚定目标、努力学习，学会自我管理，尽快融入大学生活。 （文  陈静泓）', 'http://info.jhc.cn/2012/1109/c201a2084/page.htm', NULL, '2017-05-17 14:17:41.942661');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (468, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '院长王振洪等来我院检查指导新学期开学工作', '9月6日是新学期开学第一天，在王振洪院长带领下，校领导余党军、胡野及相关职能部门负责人一行来信息学院检查指导新学期工作。 信息学院领导班子首先作汇报，教学方面，新学期将结合项目部运作机制推进工作，着力实施项目部与实训平台建设对接、与学科竞赛融合、与课程建设统一，将推进计算机教学改革、课程教学监控平台建设等工作。学生管理上，将强化服务意识、深化全员育人，提升学生管理工作能力和水平。 王振洪在听取汇报后发言，他说领导班子是一个学院发展前进的动力，希望新学期信息学院领导班子要以饱满的精神状态，以高度的责任感，肩负起学院各项工作重任。结合“十二五”规划，他希望信息学院规划工作要认真细致，要注重长效性，要特别注重内涵。王振洪最后说，学校是培养人的地方，要围绕这一中心，牢固树立以人为本思想，使我们的人才培养工作明确定位、找准方向。 （文  王伟斌  审核  朱登胜）', 'http://info.jhc.cn/2012/1109/c201a2079/page.htm', 'http://info.jhc.cn/_upload/article/images/ff/41/6eddcd5645e19cfad6b5b094aaa0/f06f613f-218a-4bdd-ab2c-7639e1c7fef2.jpg|http://info.jhc.cn/_upload/article/images/ff/41/6eddcd5645e19cfad6b5b094aaa0/cb31faba-df73-455e-bc07-be81b25f7b4a.jpg|http://info.jhc.cn/_upload/article/images/ff/41/6eddcd5645e19cfad6b5b094aaa0/ab2d75a8-87d3-469f-8f5b-b85f09ac9426.jpg', '2017-05-17 14:17:41.964757');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (469, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院召开2010年学生工作会议', '9月4日，信息学院召开2010年学生工作会议，会议主题为“提升服务意识，深化全员育人，促进学风建设”。校党委副书记余党军、学工部部长吴寿松应邀出席。 会上，余党军对信息学院学生工作给予了肯定，指出学生工作要与时俱进、居安思危，不断分析新情况、新问题，使工作保持活力和创造力。 优秀班主任和辅导员代表在会上作了主题发言，大家围绕班级日常管理、思想教育、等级考证、专升本等主题进行了经验介绍和讨论，许多班主任提出了自己在日常工作中积累的丰富经验和独到见解，引起了大家共鸣。 学院党总支书记朱登胜在总结时说，我们的学生工作要强化服务学生、全员育人意识，大家对会议各主题进行了热烈讨论，希望大家以此为载体，明确新学期学生管理工作的重点，上下齐心，共同推动学院学生工作上一个新台阶。 会议还对班主任和辅导员考核制度进行了讨论修订。 （文  陈静泓  王伟斌   摄影 王伟斌）', 'http://info.jhc.cn/2012/1109/c201a2077/page.htm', NULL, '2017-05-17 14:17:41.987075');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (607, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院计算机应用技术专业支部开展党员义务劳动', '为响应学校“创先争优”活动的号召，围绕信息学院争创寝室、教室卫生优秀率工作，从5月份开始，信息学院计算机应用技术专业学生支部组织支部党员深入学生寝室、成教教室开展义务劳动。 通过一个月的努力，学院学生寝室、教室的卫生状况有了较明显的改善，支部学生党员在思想汇报中表示，劳动让自己培养了吃苦耐劳和甘于奉献的精神，进一步增强了支部的凝聚力，更提高了身为一名党员、积极分子的责任感。今后，计算机支部将继续开展此类活动，并积极探索活动的形式，让更多的学生自发地参与到和谐校园的建设中来。', 'http://info.jhc.cn/2012/1109/c201a1252/page.htm', NULL, '2017-05-17 14:19:47.77691');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (470, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '通信工程项目部开展新学期教研活动', '9月7日，信息学院通信工程部全体教师举行新学期第一次教研活动，院长龚永坚应邀参加。 教研活动围绕“通信工程项目部运行规划与年度考核”、“通信实训室三年建设方案”及“2010年通信技能大赛”三个内容展开。赵敏笑主任介绍了项目部运行机制与本学期重点工作，并传达了学校教学会议精神，要求以技能竞赛为契机，提升教师和学生职业能力。项目部老师对相关议题进行了热烈讨论，并提出了许多建议。 龚永坚在听取大家讨论后指出，在项目部的运行中要积极探索新的工作方式，要加强对实训室的管理，加强与企业的沟通与合作；在教学过程中要培养学生的职业素养，并对教师的职业素养提出了具体要求；在专业建设方面，不要局限于课程，要做出自己的特色。 （文  余经纬）', 'http://info.jhc.cn/2012/1109/c201a2074/page.htm', NULL, '2017-05-17 14:17:42.010839');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (471, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院举行2010级成教新生开学典礼', '9月6日，信息学院2010级成教新生开学典礼在大学生活动中心举行。学院党总支书记朱登胜、相关部门负责人参加开学典礼。 朱登胜在开学典礼上致辞，他首先代表全院师生对新同学的到来表示热烈欢迎，并向同学们介绍了学院办学理念、师资队伍、教学科研、文化氛围等方面的情况。对于同学们即将开始的大学生活，朱登胜给大家提出了四点期望：勤奋学习，学会求知；勇于创新，学会做事；团结友爱，学会共处；修身养性，学会生活。 开学典礼上，教科办、学工办、继教部等负责人分别发言，介绍各部门工作内容等情况。 （文/摄影 俞冬丽）', 'http://info.jhc.cn/2012/1109/c201a2067/page.htm', 'http://info.jhc.cn/_upload/article/images/e2/aa/e5c33da546f8ae6f5dec0d114e14/e0e30dc4-1f91-4c6b-90a6-0222f1d57ef2.jpg|http://info.jhc.cn/_upload/article/images/e2/aa/e5c33da546f8ae6f5dec0d114e14/ed4b97b5-fe42-4c3c-bdba-abb9e785d29a.jpg', '2017-05-17 14:17:42.031817');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (472, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院召开班主任助理培训会', '9月15日，信息学院举行2010级新生班主任助理授聘仪式暨业务培训会 经过层层选拔，37名优秀学生从近百名申请者中脱颖而出，拿到了2010级班主任助理聘书。为了更好的发挥班主任助理的作用，同时提高他们的管理素质和水平，学院特意对他们组织培训，培训从迎新工作、党团工作以及专升本等方面展开，要求大家将安全引导、公寓建设、专业指导作为重中之重，加强对相关专业的了解，抓好班级基础文明建设，在做中思考、交流、总结，共同提高服务能力，与学院一起营造健康和谐的育人环境。 班主任助理詹华代表全体班主任助理发言，表示会珍惜这个感恩校园、锻炼自身的机会，全心全意为新生服务，通过加强对新生的了解和认识，努力帮助她们尽早适应大学环境，以饱满的姿态投入大学生活。 （文  陈静泓）', 'http://info.jhc.cn/2012/1109/c201a2064/page.htm', NULL, '2017-05-17 14:17:42.053759');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (473, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院召开暑期工作研讨会', '8月25-27日，信息学院召开主题为“理清组织机构，完善规章制度，关注重点工作”暑期工作研讨会。学院中层以上干部、09年先进工作者等参加会议。 会议以《国家中长期教育规划和发展纲要》为指导思想，以创新争优活动为载体，就教育教学改革，特别是进一步发挥项目部在教学、科研和社会服务等方面的功能进行了探讨。大家普遍认为，要发挥项目部模式优势，必须使专业建设适应社会需求，符合行业企业标准；要充分重视实训环境建设，实训室的建设要与社会对接，与课程对接；要加强师资队伍建设，关注教师职业能力提升，增强团队合作水平；要不断扩展专业的社会服务功能，提升教师社会服务水平。项目部运作中要加强沟通，明确职责，细化考核，提升核心竞争力。 会议对学院规章制度进行了讨论修订，细化内容，新增制度，以适应项目部改革需要。各部门负责人还在会上介绍了本部门重点突破工作和新学年规划。 院长龚永坚在总结发言时说，当前全院上下正在深入开展“育人成才先锋”创新争优活动，新的组织结构会带来新问题、新矛盾，但只要我们统一思想、加强协调，牢固树立为学生服务的宗旨，就能确保新机制的稳步推进。他要求各部门要立足本职，强化落实，突出重点，在示范验收基础上做好特色专业建设，促进重点专业发展，提升中外合作办学能力，将开门办学思想贯穿到教学中，促进学院各项工作取得新发展。 （撰稿  王伟斌   审核  龚永坚）', 'http://info.jhc.cn/2012/1109/c201a2057/page.htm', 'http://info.jhc.cn/_upload/article/images/4f/98/b71e2ad0463bb7aed9de0b583cf8/a8f7d4fe-ea13-441a-8ff3-3dc895cba595.jpg|http://info.jhc.cn/_upload/article/images/4f/98/b71e2ad0463bb7aed9de0b583cf8/842fc2ca-d0e4-4e53-ae35-02585bfc6262.jpg|http://info.jhc.cn/_upload/article/images/4f/98/b71e2ad0463bb7aed9de0b583cf8/89357db3-68e7-4096-8443-66c594402a93.jpg', '2017-05-17 14:17:42.075979');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (474, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '本科学历教育又一新模式在信息学院落户', '为进一步挖掘继续教育新模式，拓展学院学生继续深造途径，提升学院社会服务水平，信息学院在与浙江工业大学已有合作开办专升本课程班的基础上，经与杭州电子科技大学多次洽谈，签订了合作办学协议书，定向培养本科学历学生。 据悉，该本科教育为高起本模式，学制5年。杭州电子科技大学开设的专业全面，可与信息学院现开设的专业全部对接，为学院学生的学习提供了极大方便，目前这一新模式受到了学院学生的普遍欢迎，也吸引了许多社会上有继续深造意向的青年前来咨询。 （文  单天德）', 'http://info.jhc.cn/2012/1109/c201a2054/page.htm', NULL, '2017-05-17 14:17:42.09825');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (475, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '在新的起点上实现人生理想――信息学院举行2010级新生开学典礼', '9月20日晚，信息学院举行2010级新生开学典礼，学院领导、各部门负责人及2010级全体新生参加。开学典礼由学院党总支书记朱登胜主持。 在全场热烈的掌声中，开学典礼正式开始。龚永坚院长在开学典礼上致辞，他首先代表学院师生对2010级新生来院学习表示了热烈的欢迎和衷心的祝贺，并给大家介绍了学院基本情况，以便新生们尽快了解学院，融入学院大家庭。在致辞中，龚永坚从培养定位、专业设置、就业特色等方面分析了高职教育的办学优势，以此鼓励同学们脚踏实地、刻苦学习，成为一名具备现代职业技能素养的高职人才。最后，龚永坚对新生们提出了几点希望：志存高远，树立远大理想；学会做人、学会学习；珍惜时光，发奋学习；尊敬师长，互爱互助，珍重同学情义。 开学典礼上，教师代表、老生和新生代表分别发言，他们都希望新同学们把握来之不易的学习机会，在信息学院这块沃土上、辛勤耕耘，在新的起点上实现人生理想。各部门负责人还分别就教学管理、学生工作、继续教育等内容做了介绍，为新生们尽快融入大学生活指明了方向。 （ 文  王伟斌   审核：朱登胜）', 'http://info.jhc.cn/2012/1109/c201a2047/page.htm', 'http://info.jhc.cn/_upload/article/images/42/6e/00549d2b4e97a82c4ee949a5065e/988597dc-c45b-46ca-8719-cccb23f66d44.jpg|http://info.jhc.cn/_upload/article/images/42/6e/00549d2b4e97a82c4ee949a5065e/0155fbd8-0b29-44cf-9b8f-9c8b87976456.jpg|http://info.jhc.cn/_upload/article/images/42/6e/00549d2b4e97a82c4ee949a5065e/b7384d5b-609d-4d97-99dc-7f01b918a40d.jpg|http://info.jhc.cn/_upload/article/images/42/6e/00549d2b4e97a82c4ee949a5065e/0bf2db63-d6fe-4a9d-9371-9a039b0e6f79.jpg|http://info.jhc.cn/_upload/article/images/42/6e/00549d2b4e97a82c4ee949a5065e/d8a83fae-2f11-4cf0-8c95-55f3592bbdcc.jpg', '2017-05-17 14:17:42.120407');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (476, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院召开《计算机基础》课程培训会', '9月19日下午，信息学院举行了2010级《计算机基础》课程培训会，学院副院长陈海荣、教科办相关人员以及42位任课专兼职教师参加了本次培训会。 会上，陈海荣就顺应公共课改革、建设丰富的课程资源平台、提升课程教学质量等方面发表了重要讲话。为努力探索公共课改革，信息学院自上半年起便组建团队，开发了应用于日常教学的计算机基础测评平台和方便于学生自主学习、训练并利于老师发现问题的网络化平台，本次培训会围绕平台如何为教师授课提供服务进行了详细的讲解。 会议对《计算机基础》课程的建设思路与建设成果进行了详细介绍，分析了分层教学改革存在的问题和改进措施，并提出2010级《计算机基础》课程教学的内容与要求，为课程教学的进一步发展指明了方向。 （文  胡昕熠）', 'http://info.jhc.cn/2012/1109/c201a2044/page.htm', NULL, '2017-05-17 14:17:42.142674');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (477, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '中秋欢乐聚 遥把相思寄――信息学院新生各班举行中秋晚会', '9月22日晚，西教学楼二号楼欢快的笑声与热烈的掌声交织成一片，信息学院2010级各班新生中秋晚会正在这里如火如荼举行。 各班教室吊起拉花、挂起气球，色彩鲜艳的剪纸、彩带点缀着角角落落，一派节日欢乐祥和的气息。魔术、诗朗诵、相声、演唱、舞蹈，同学们以各种文艺形式展示着自己的才艺，传播着家乡独特的文化；有的班级还将军训成果搬上舞台，展现军人风采，有的班级则以晚会为平台，号召大家给家人发一条短信，送祝福、报平安，遥寄思乡之情。 中秋晚会让新生们在异乡感受着佳节到来之际班级大家庭团聚的幸福，给大家提供了交流大学生活，畅谈美好未来的机会，为艰苦的军训生活添加了鲜活的色彩。 （ 文  陈静泓 ）', 'http://info.jhc.cn/2012/1109/c201a2042/page.htm', NULL, '2017-05-17 14:17:54.49143');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (478, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院召开新学期学生干部会议', '为进一步加强学生干部队伍建设，提高学生干部“服务同学、提升自我”的意识和水平，9月21日晚，信息学院召开全院学生干部会议。学院党总支书记朱登胜、学工办成员参加。 朱登胜在会上发言，他指出，学生干部要从如何营造学习环境、怎样更好的服务同学、努力提升自我素质等方面对自己提出具体要求，学生干部要时刻牢记自己的职责和身份，要常怀感恩意识，严格按照学校要求，不折不扣的执行各项工作安排。朱登胜强调，学生会各部门既要各司其职，又要团结合作，积极主动的开展工作。希望学生干部调整好心态、端正态度、肩负起学生自我管理组织的各项职责。 会上，学生干部代表作了主题发言。大家纷纷表示，在新学期伊始召开学生干部大会，及时而有意义，对本学期工作思路的开拓，计划的明晰，目标的明确必将大有裨益。 （文 / 摄影  汪铭铠）', 'http://info.jhc.cn/2012/1109/c201a2039/page.htm', NULL, '2017-05-17 14:17:54.671801');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (479, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院教工团支部组织创先争优学习活动暨换届选举', '9月25日，信息学院教工团支部组织创先争优学习活动暨换届选举。学院党总支书记朱登胜应邀参加。 教工团支部书记林雪华总结了本届团支部工作，她说，教工团支部在学院党总支领导下，学习理论知识，加强业务学习，提升科研水平，以青年人的饱满斗志积极践行市级“青年文明号”和“五四红旗团支部”称号。她以李秋华书记在学校创新争优推进会上的讲话为载体，组织大家学习了学校和学院创新争优相关文件精神，并号召大家坚定党性、夯实业务，争做育人成才先锋。 朱登胜在发言时说，教工团支部是我院青年教师相互交流沟通的平台，在这里可以加强学习、促进发展，希望大家充分利用好这一载体，在当前的创先争优活动中为人师表，做创新创业先锋。 会议对团支部委员进行了换届选举，产生了以汪铭铠为书记的新一届支部委员。 （ 文  王伟斌  汪铭铠  审核：朱登胜）', 'http://info.jhc.cn/2012/1109/c201a2033/page.htm', 'http://info.jhc.cn/_upload/article/images/9e/ae/749d660c4b1e919675b645a36bdd/d7e227b0-af51-43fc-a9b1-da7bfb77ea08.jpg|http://info.jhc.cn/_upload/article/images/9e/ae/749d660c4b1e919675b645a36bdd/3448451a-e8a2-4647-8d2c-818f6518faa7.jpg|http://info.jhc.cn/_upload/article/images/9e/ae/749d660c4b1e919675b645a36bdd/09de5b1b-38b8-418b-9856-0eda7f99f490.jpg', '2017-05-17 14:17:54.69208');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (480, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院理论中心组召开创先争优主题学习会', '9月26日，信息学院党总支理论学习中心组举行创先争优主题学习会，全体中层以上干部参加，学习会由学院党总支书记朱登胜主持。 朱登胜传达了中共中央办公厅及省委、市委关于创新争优活动的相关文件精神，并带领大家重点学习了校党委书记李秋华同志在“育人成才先锋”创新争优活动推进会上的讲话《领会精神 结合实际 创新载体 全面推进我校创先争优活动》，与会同志围绕讲话内容，就进一步推进学院“育人成才先锋”创先争优活动的开展进行了热烈讨论。 学习会上，确定了“关爱学生 助推发展 共建和谐”为学院创先争优活动主题，并将服务社会、服务学生的理念贯穿到创先争优活动的始终，会议还就下一阶段理论学习活动、党员三联系制度、创新“育人成才先锋”网络学习平台等创先争优活动作出了具体安排。 （文  林雪华）', 'http://info.jhc.cn/2012/1109/c201a2030/page.htm', NULL, '2017-05-17 14:17:54.712109');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (481, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院召开创先争优活动推进会', '9月29日，信息学院召开创先争优活动推进会，就党组织和党员公开承诺、党员三联系等内容进行动员和部署，推进会由学院党总支书记朱登胜主持。 总支委员吴晓明宣读了学校关于党员公开承诺相关通知精神及学院创先争优活动工作计划书，以进一步明确学院创先争优活动安排。 朱登胜对党员承诺和三联系工作做动员，他说，创先争优活动对于进一步抓好学习实践活动整改落实，激发党支部和广大党员生机活力，保持和发展党的先进性具有重要的指导意义和现实价值。希望全院党员干部要以党员公开承诺和党员三联系等活动为切入点，加强先锋主题教育，发挥先锋模范作用，敢提诺、勇亮诺，服务师生、服务学院，以切实行动，争当优秀共产党员，为构建和谐信息而努力。 （文  王伟斌）', 'http://info.jhc.cn/2012/1109/c201a2027/page.htm', NULL, '2017-05-17 14:17:54.733093');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (482, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学子在2010级军训会操表演取得佳绩', '9月29日上午，2010级新生军训汇演在校体育运动中心隆重举行。在蒙蒙细雨中，信息学院两个参演方阵，在响亮的“信息信息，自强不息；追求卓越，争创第一”口号中接受检阅，并以扎实的基本功，最终获得“2010级学生军训成果汇演二等奖”的好成绩。 我院参演方阵分别由64名男生和64名女生组成。他们手带白手套，头扎红丝带，脸贴小红旗，精神抖擞，意气风发，展现了信息学子英勇顽强的优秀品质。这些方阵成员，是从我院2010级新生中挑选的优秀分子，在艰苦而难忘的十天军训生活中，他们不畏艰难，不管刮风下雨，都毅然挺立在训练场地上，站军姿、踢正步、打军体掌、值班站岗，体验艰苦严格的军营生活、收获丰富多彩的军训体验和感悟，相信这些经历，一定能为我院2010级新生们的大学生活的打下坚实的基础。 （ 文  汪铭铠 ）', 'http://info.jhc.cn/2012/1109/c201a2024/page.htm', NULL, '2017-05-17 14:17:54.754618');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (483, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院新生活动月拉开序幕', '为丰富大一新生课余生活，增进班级凝聚力，激发同学们锻炼身体的热情，为即将到来的第十五届校运会做好准备，近日，信息学院在东实训楼广场举办新生拔河比赛。 比赛以班级为单位，每支参赛队由10名男生5名女生共15名选手组成。比赛中，各班级精神饱满，团结一心，伴着啦啦队有节奏的口号声，参赛选手们斗志昂扬，毫不示弱。 经过激烈的比拼，应电1001班勇夺冠军。 据悉，本次比赛拉开了信息学院2010年新生活动月暨第八届新生节的活动的序幕，接下来还将陆续开展新生书画大赛、职业生涯规划大赛、演讲赛、定向赛、寝室设计大赛等一系列活动，以此丰富校园文化，帮助新生尽快融入大学生活。 （文 陈静泓）', 'http://info.jhc.cn/2012/1109/c201a2021/page.htm', NULL, '2017-05-17 14:17:54.77697');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (484, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院举办“坚持不懈，放飞梦想”励志演讲', '为激励大一新生对未来的生活充满信心，步步上进，10月11日，信息学院在各新生班教室举行了“坚持不懈，放飞梦想”巡回演讲活动。 活动中，演讲队三人一组，围绕人生理想、大学生涯等主题，以饱含激情的言语为大一新生奉上一场场感人至深的演讲，并为新生送去了诚挚的祝福，鼓励他们努力学习，学会自尊、自信、自强、自立，坚定信念，努力将梦想变为现实，描绘灿烂的人生。 新生们纷纷表示，此次演讲感受颇深，不仅敦促自己认真制定职业生涯规划，度过三年充实而有意义的大学生活，更激励自己勇于面对今后生活、学习、工作中的困难与挫折，自立自强，立志成才。 （ 文 陈静泓 ）', 'http://info.jhc.cn/2012/1109/c201a2017/page.htm', NULL, '2017-05-17 14:17:54.799005');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (485, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院各支部创先争优活动力求实效', '10月15日晚，信息学院应电学生支部教师党员结合创先争优活动为支部成员上了一堂特殊的党课。有结合自己多年带队参加国家和省级技能比赛经历，介绍如何学习电子技术课程、参赛学习准备的；有结合当前时事政治，号召学生党员如何做好五个“带头”作用的；有结合自己党的知识学习体会，就当前创先争优活动中如何理论联系实际的。平实的讲解博得了大家一片赞誉，许多学生党员都说这样的党课有具体内容、有实际针对性，加深了对创先争优活动意义的理解和参与热情。 据悉，信息学院总支要求各支部在创先争优中力求各项活动有内容、有实效，计算机等支部的家电维修下乡、通信支部的国庆国情调研等活动，都做到了党员受教育、师生得实惠的创先争优活动内涵要求。 （ 文  王伟斌 ）', 'http://info.jhc.cn/2012/1109/c201a2012/page.htm', 'http://info.jhc.cn/_upload/article/images/8f/07/759e25a84d7fb180b4c0b7f86fe6/6d17ec31-0d7b-482e-9590-5d209d1533de.jpg|http://info.jhc.cn/_upload/article/images/8f/07/759e25a84d7fb180b4c0b7f86fe6/d7308786-a887-4c88-99b7-b06054ae24b9.jpg', '2017-05-17 14:17:54.821254');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (486, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '民盟温州科技职业技术学院来我校交流', '10月15日，民盟温州科技职业技术学院主委胡国南等一行10人就民盟组织建设及活动开展等情况与我校民盟开展交流。 民盟金华职业技术学院主委王成福教授介绍了我校民盟在学校发展中履行参政党职能，努力做好自身建设、履行参政议政职责、开展社会服务活动等方面的做法。 交流中，大家普遍认为，加强盟员思想建设、建立健全运行机制、丰富组织活动内容对民盟工作至关重要，是民盟工作注重质量，保证数量，保持民盟特色的重要保证。民盟基层组织必须深入探索新形势下加强基层组织建设的新方法和新思路，才能把民盟建设成为适应新的时代要求的高素质参政党。宾主双方还就数字化校园信息化建设、学生实习组织与管理等问题作了交流探讨。 （ 文 王伟斌 ）', 'http://info.jhc.cn/2012/1109/c201a2005/page.htm', 'http://info.jhc.cn/_upload/article/images/86/d6/fab675934ab78785979e0fc11ea1/94f114ed-6c9d-484b-b7e8-863a40731967.jpg|http://info.jhc.cn/_upload/article/images/86/d6/fab675934ab78785979e0fc11ea1/807cb622-14c9-4f37-97bb-53164fed793c.jpg|http://info.jhc.cn/_upload/article/images/86/d6/fab675934ab78785979e0fc11ea1/e5169e46-62c8-4702-baea-2ef03144767d.jpg|http://info.jhc.cn/_upload/article/images/86/d6/fab675934ab78785979e0fc11ea1/605f6e9e-852e-4270-8179-61c142b7f086.jpg', '2017-05-17 14:17:54.843664');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (487, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院网络与楼宇工程部举办“物业管理信息化”学术报告', '10月15日，信息学院网络与楼宇工程部举办“物业管理信息化”学术报告，来自思源（上海）计算机软件有限公司总经理李延栋、工程师欧阳福明等两位专家应邀主讲，楼宇智能化工程技术专业相关专业教师及近百名同学参加了报告会。会议由信息学院副院长陈海荣主持。 两位物业管理信息化专家立足物业管理领域的现实环境，以房地产产业链为切入点，结合经典案例，深入浅出地阐释了物业管理信息化行业的发展历史、现状及发展趋势。报告会博得了同学们的阵阵掌声，许多同学表示，听了此次报告会，对自己开阔对现代物业管理的视野，加强专业思想，引导和规划职业发展有重要帮助。 会后，楼宇专业教师与应邀专家就《物业管理及信息化》的课程改革、物业管理系统实训平台构建、思源物业管理信息化平台引进、合作开发课程教材等事宜进行了深入交谈。 （ 文 卢航远  审核 陈海荣）', 'http://info.jhc.cn/2012/1109/c201a2000/page.htm', 'http://info.jhc.cn/_upload/article/images/ce/62/74bacafd4cc69baca01fdb487e92/e936c528-4890-4b20-b2c4-ea76fb0949b8.jpg|http://info.jhc.cn/_upload/article/images/ce/62/74bacafd4cc69baca01fdb487e92/5257ac91-d0bb-44d7-8e04-6056ab40815b.jpg', '2017-05-17 14:17:54.865822');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (488, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '金职院外包培训中心被授予省服务外包人才培育基地', '从日前在温州召开的浙江省服务外包人才培育基地工作会议上传来喜讯，我校外包培训中心被授予省国际服务外包人才培育基地。我校信息学院单天德老师还在本次会议上就服务外包人才培育工作作了典型发言。 本次会议由省商务厅主办，会上介绍了我省国际服务外包快速发展的形势，会议要求省内各高校积极融入服务外包的培训工作，促进高校毕业生的就业。此次会议对加强服务外包人才培训基地与高等学校、服务外包企业及培训机构之间的合作，拓宽服务外包领域人才培训渠道，推动我省服务外包产业的发展起了重要的促进作用。 （ 文 单天德 ）', 'http://info.jhc.cn/2012/1109/c201a1996/page.htm', NULL, '2017-05-17 14:17:54.887977');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (489, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院102名学生党员重温红军长征路', '信息学院创先争优活动把“育人成才”教育与革命传统教育、爱国主义教育有机结合。10月17日，学院党总支组织学生党员前往东阳横店以重温红军长征路的方式，追寻革命先辈足迹，牢记革命历史，弘扬长征精神。      在红军长征博览城，102名学生党员们徒步14公里沿长征步行道，体验浴血奋战湘江畔、天兵飞夺泸定桥、雪山低头迎远客、草毡泥潭志更坚等“长征”的艰难险阻；在国防科技教育园，通过观摩阅兵馆、兵器馆、现代战争馆、太空探索馆的武器实物和模型，充分领略了中国人民解放军在各个时期的武器装备及我国航空航天的发展历史。  作为信息学院创先争优的一项重要活动，本次重温红军长征路活动，以生动形象的方式加深了学生党员对党的革命史、战争史的认识，使他们在思想上受到一次深刻的爱国主义教育，每位党员都为自己是党的队伍中的一员倍感光荣和自豪。 （ 文 林雪华  摄影 杨斌 ）', 'http://info.jhc.cn/2012/1109/c201a1990/page.htm', 'http://info.jhc.cn/_upload/article/images/b8/04/4c60821042de83c0b2cbf7877176/15e62a79-933f-49a8-af29-be65ffab5894.jpg|http://info.jhc.cn/_upload/article/images/b8/04/4c60821042de83c0b2cbf7877176/9671f518-5efd-43cb-8a51-558a0b059f52.jpg|http://info.jhc.cn/_upload/article/images/b8/04/4c60821042de83c0b2cbf7877176/e713f8db-5e3f-4023-81d9-80228e7cac74.jpg', '2017-05-17 14:17:54.910411');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (490, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院举行学生寝室长培训会', '10月20日晚，信息学院举行学生寝室长培训会，以此加强寝室长对寝室卫生、安全、环境等方面管理水平。  学工办主任吴晓明在培训会上指出，宿舍是学生学习、生活的主要场所，是展现学校良好院风、管理水平和学生文明程度的重要窗口。寝室长要以高度的责任感积极工作，在辅导员老师指导下，提高寝室安全意识，共同做好防火防盗工作，要不断提高寝室内务卫生，加强各寝室间的评比。他特别指出，寝室长和寝室成员之间要相互帮助，主动沟通，共同构建温馨和谐、安全文明的寝室生活环境。 目前，信息学院形成了以“院领导—辅导员—学生党员—学生干部—寝室长”为体系的宿舍管理队伍，齐抓共管，促进学院寝室管理工作。 （ 文 汪铭铠 ）', 'http://info.jhc.cn/2012/1109/c201a1987/page.htm', NULL, '2017-05-17 14:17:54.932411');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (491, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院学生党员争先创优先锋服务队走进社区', '近日，信息学院应用电子等学生支部党员组成的学院家电维修服务队一行20余人走进阳光社区，为居民免费维修家电。 服务队队员们结合自己在电子、计算机等专业方面的特长，帮助居民们解决一些小家电、电脑等的疑难杂症，受到居民们的一致欢迎和好评。许多居民说，有些小家电坏了，拿去修嫌烦，但是扔了又不舍得，今天这些志愿者用他们的心灵手巧和精湛技术，帮我们解决了一个大问题，他们不但免费修，而且服务态度又非常好，真是一群新时期的好青年。 此次活动是信息学院党总支创先争优活动之一，旨在展示我院学生党员学以致用、服务社会、“争先创优”先锋服务的精神风貌。学院党总支今后将继续在社区和农村开展此类志愿者活动。 （ 文 金乐 ）', 'http://info.jhc.cn/2012/1109/c201a1978/page.htm', 'http://info.jhc.cn/_upload/article/images/02/3f/ca99013744019c3d8f8e54775307/5079cd73-5fc4-4e89-b4d5-34ca8dca401d.jpg|http://info.jhc.cn/_upload/article/images/02/3f/ca99013744019c3d8f8e54775307/579a5cff-612d-4e0b-80e8-af72cb516317.jpg|http://info.jhc.cn/_upload/article/images/02/3f/ca99013744019c3d8f8e54775307/53ecb7c4-6d0f-4e23-bc18-9cefb9125451.jpg|http://info.jhc.cn/_upload/article/images/02/3f/ca99013744019c3d8f8e54775307/8653d0d1-90e1-4a10-ba45-37fc217a64b2.jpg', '2017-05-17 14:18:05.920482');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (492, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院电子产品开发部召开学生概念公司成立及工作任务布置会', '10月20日，信息学院电子产品开发部召开学生概念公司成立及工作任务布置会，副院长王成福、副院长陈海荣应邀参加。 电子产品开发部主任余红娟在会上详细介绍了概念公司的组织结构、各部门职责，并下达了第一期工作任务。陈海荣在点评时指出，电子产品开发部在项目部改革的浪潮中敢于创新，很好地发挥了示范专业的带头作用，概念公司给了同学们一个很好的施展才华的平台，希望大家好好把握机会，创新创业，学有所成。 （ 文/摄影 刘子坚 ）', 'http://info.jhc.cn/2012/1109/c201a1975/page.htm', 'http://info.jhc.cn/_upload/article/images/30/88/0aefda72499faea813fd30b3d212/d49a7b43-bc87-487a-8360-0509f8420c16.jpg', '2017-05-17 14:18:05.992032');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (493, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '我校举行与市高新园区电子信息产业对接深度合作座谈会', '10月19日，我校与金华市高新园区及园区内部分骨干电子信息企业就筹建IT学院（暂定）、共同培养电子信息类专门人才举行深度合作座谈会。金华市副市长金中梁出席。金华市经济开发区管委会主任李郁华、副主任邵国强及市政府相关部门负责人，金华市电子信息产业骨干企业负责人，校顾问杜世禄、副院长胡野及信息学院院长龚永坚等有关领导等参加了座谈会。座谈会由校党委书记李秋华主持。  会上，杜世禄介绍了学校近年来深化校企合作，建立“众泰汽车学院”、“皇冠学院”校企利益共同体的有关情况及筹建IT学院的设想。信息学院有关领导汇报了IT学院筹建的初步方案。之后，政府相关部门负责人、企业家代表分析了金华电子信息技术产业的发展现状、趋势和人才需求状况，着重探讨了与区域电子信息产业发展相适应的人才培养问题。大家一致认为，电子信息技术产业作成为金华的一项支柱产业，发展势头迅猛，对专业人才需求迫切。因此，在政府的支持下，校企双方通过建立IT学院架设合作平台，结成利益共同体，能够充分整合办学资源，实现学校人才培养目标与企业岗位需求的无缝对接，为地方电子信息产业可持续发展提供人才支撑。与会人员还就IT学院这一模式的定位、规模、师资引进、政策扶持等问题提出了许多意见和建议，并期待IT学院早日成立。  副市长金中梁在听取大家的意见后指出，金职院紧紧依托市汽模配产业成立了众泰汽车学院，与电动工具骨干企业联手成立了皇冠学院，现在又积极与区域电子信息行业开展深度合作，筹建IT学院，这完全符合政、校、企的共同利益。从政府角度讲，找到了通过教育来推进市电子信息支柱产业的结合点，培养出的大量电子信息类专门人才，不但有利于我市的电子信息企业，也有利于其他企业通过信息化来推进管理现代化，进而推进金华工业整体转型升级。对学校来说，从与单个企业开展“订单培养”，到与整个产业合作培养人才，探索出了一条新的办学模式。对企业来说，则找到了一条提供急需人才的通道。会上，金中梁还就IT学院的政策支持、运作实施、组织架构等具体问题作了部署，要求政校企三方认真规划、努力探索、积极实施，早日促成IT学院的建成，并将这一学院建成区域电子信息专门人才输送基地，为区域经济发展提供强劲的人力资源“引擎”。 （ 文 王伟斌 / 摄影 林雪华 ）', 'http://info.jhc.cn/2012/1109/c201a1971/page.htm', 'http://info.jhc.cn/_upload/article/images/f1/82/2d6616f44866bf1a9558325666e1/4dc93a3d-04d2-4994-9200-167952d2e081.jpg|http://info.jhc.cn/_upload/article/images/f1/82/2d6616f44866bf1a9558325666e1/0fcbfe5a-c1c7-45c3-a67e-0c35d8e137eb.jpg|http://info.jhc.cn/_upload/article/images/f1/82/2d6616f44866bf1a9558325666e1/91c65938-5baf-4b23-9927-036ec0748909.jpg|http://info.jhc.cn/_upload/article/images/f1/82/2d6616f44866bf1a9558325666e1/e3b34507-ae5e-4f90-bf27-e2a31d1ffa65.jpg', '2017-05-17 14:18:06.014464');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (494, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院企业信息化事业部筹建新一届“5173订单班”', '10月14日下午，金华利诚信息技术有限公司人力资源部孙平经理应邀来信息学院，就组建新一届“5173订单班”向计算机专业群学生做介绍。 孙平在宣讲会上简单介绍了5173公司情况和目前订单班学生在公司工作情况，他希望同学们积极参加到订单班中来，共同为公司发展和个人成才努力。前三届订单班同学代表谈了自己在订单班学习和公司工作情况。 据悉，信息学院企业信息化事业部为新一届订单班积极规划和筹建，通过努力，10月份与金华利诚信息技术有限公司达成联合培养学生的协议，公司将从学校的计算业专业群班级中挑选优秀学生组成第四届“5173订单班”。本届订单班计划招收40人，并设立5万元奖学金奖。目前报名工作正在有序进行中。 （ 文 楼小明 ）', 'http://info.jhc.cn/2012/1109/c201a1967/page.htm', NULL, '2017-05-17 14:18:06.033461');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (495, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院理论学习中心组学习党的十七届五中全会精神', '10月21日下午，信息学院理论学习中心组召开专题会议，传达学习十七届五中全会精神。 　　学院党总支书记朱登胜传达了十七届五中全会有关中央精神，他强调全院上下要切实做好十七届五中全会精神的学习、宣传工作，特别要做好全会精神的解读和贯彻落实工作。他说，国家十二五规划中提到了很多国家战略需求，对教育特别是高职教育发展也提出了具体要求，指明了发展方向，这是学院发展的重大机遇，我们要根据全会精神，抓紧研究制定学院的十二五规划。 会议强调，在学习贯彻十七届五中全会精神时，要坚持将学习活动落实到当前的创新争优活动中去，落实到制定学院的十二五规划中去，使学习活动有内涵、见成效。 （ 文 王伟斌 ）', 'http://info.jhc.cn/2012/1109/c201a1963/page.htm', NULL, '2017-05-17 14:18:06.055721');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (496, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '金职院筹建IT学院', '本报消息（记者 金璐 通讯员 方敏）10月19日，市高新园区管委会负责人与园区内部分骨干电子信息企业老总会聚金职院，与该校共同探讨如何深度合作，拟通过建立“IT学院”（暂名）共同培养电子信息类专门人才。副市长金中梁、市政协副主席邵国强出席会议。 　　电子信息技术产业已成为我市支柱产业之一，发展势头迅猛，对适应岗位需求的高技能人才需求迫切。与会者认为，在政府的支持下，校企双方可以通过建立“IT学院”构建合作平台，充分整合办学资源，实现学校人才培养目标与企业岗位需求的无缝对接，为地方电子信息产业可持续发展提供人才支撑。 　　金中梁指出，金职院IT学院的筹建，将为电子信息相关骨干企业找到一条提供急需人才的通道，同时为金职院探索出与整个产业合作培养人才的办学新模式。通过这一平台培养大量电子信息类专门人才，不但有利于我市电子信息企业，也有利于其他企业通过信息化推进管理现代化。市高新园区、学校、企业要认真规划、努力探索、积极实施，早日促成IT学院的建立，为区域经济发展提供强劲的专业化人力资源“引擎”。 新闻链接：http://epaper.jhnews.com.cn/site1/jhrb/html/2010-10/24/content_1200574.htm', 'http://info.jhc.cn/2012/1109/c201a1960/page.htm', NULL, '2017-05-17 14:18:06.077855');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (497, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院团学组织招新工作圆满结束', '日前，随着最后一轮面试完成，信息学院团学组织2010年纳新招新工作圆满结束。本次团学组织全部19个部门进行了干事的选拔，2010级新同学近640人报名竞聘，报名率超过80%，创历史新高，共招新干事287名。 本次纳新工作以第一轮面试，第二轮笔试，第三轮面试的形式展开，通过两轮面试和一轮笔试，新生们充分展现自己的能力与特长，为各部门的干事筛选与录取提供数据及资料参考。半个多月的招新工作，每个部门都通过良好的职责安排为同学们提供了一个宽广的能力锻炼舞台和素质拓展机会。 （ 文 郑俊健 / 摄影 季委 ）', 'http://info.jhc.cn/2012/1109/c201a1956/page.htm', 'http://info.jhc.cn/_upload/article/images/e0/4d/f53011404970bbbd17f57d0b9b49/e80a9b9c-a0da-45e6-99ba-1c3654393b0f.jpg', '2017-05-17 14:18:06.10001');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (498, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '启航金秋，共筑和谐――信息学院举行迎新联欢会', '为欢迎来自五湖四海的新同学，进一步增强师生的凝聚力和向心力，10月22日晚，信息学院“启航金秋，共筑和谐”2010迎新联欢晚会在东实训楼广场激情上演。 整个晚会由“梦想的起航”、“青春的舞动”、“生命的怒放”三个环节构成，在动感的架子鼓与吉他伴奏声中，一首热情洋溢的《My Show》拉开了晚会的序幕。晚会上，展现中国与异域风情的舞蹈串烧使现场热血沸腾，或深情甜美或热情奔放的歌曲串烧让观众心潮澎湃，双簧、相声、戏曲、诗朗诵，丰富多彩的表演将晚会一次次推向高潮，让师生尽享欢乐盛宴。 喜庆祥和的晚会氛围，让新生感受到了学院大家庭的温暖，也鼓舞同学们以良好的姿态共同起航、锐意进取，编织美丽充实的大学生活。 （ 学工办  陈静泓）', 'http://info.jhc.cn/2012/1109/c201a1949/page.htm', 'http://info.jhc.cn/_upload/article/images/30/c3/c4b991274f779c302a217d8ec16d/ce56b7b0-be5a-4cb4-b5ec-121d72d05231.jpg|http://info.jhc.cn/_upload/article/images/30/c3/c4b991274f779c302a217d8ec16d/241036eb-b3db-4da4-b06d-0408bc91d473.jpg', '2017-05-17 14:18:06.122394');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (499, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院企业信息化事业部课程教学出新招', '10月27日上午，金华少儿摄影网顾方和总经理和漂亮宝贝总经理杜云峰应邀到信息学院授课，参与徐赛华老师的多媒体项目综合训练课程的现场教学。 课堂上，企业方将其即将开发的“龙蛋”（暂名）项目，有机融合到我院多媒体项目综合训练课程相关章节教学中，实现企业项目与学校教学项目的有机融合。新颖的教学形式和项目内容，吸引了同学们的极大兴趣，大家都说，以前不知道学了东西能不能在实际工作中用上，但是这次把真实的企业项目拿到了教学现场，让我们的学习目标明确、方向清晰，希望学院以后多组织这样的教学活动。 结合同学们的反映，校企双方均表示，后续将对该项目教学模式作进一步深化与落实，寻找契合点，挖掘闪光处，并逐步在学生综合技能训练中推广。 （ 文 楼小明 ）', 'http://info.jhc.cn/2012/1109/c201a1942/page.htm', 'http://info.jhc.cn/_upload/article/images/4b/f6/0fa2a5b4422b93b7283dcd1e1d6d/aa87a2e1-f662-46c6-9ff6-2e642bfa5a5a.jpg|http://info.jhc.cn/_upload/article/images/4b/f6/0fa2a5b4422b93b7283dcd1e1d6d/ae1d0c9b-6fbd-4ff6-bcd7-f951763fdc4d.jpg', '2017-05-17 14:18:06.144524');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (500, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '校党委副书记余党军来信息学院调研创先争优活动', '10月28日，校党委副书记余党军到信息学院检查指导前期开展的创先争优活动。 余党军在听取学院党总支和支部代表创先争优活动汇报后说，信息学院的创先争优活动能按照学校总体部署要求认真落实，并开展了学生党员红色之旅、特殊党课、为民家电维修下乡等特色活动，成果显著。他对后期活动提出了几点要求：一是要将创先争优活动与岗位工作职责相结合，反思岗位、立足岗位，使活动开展有依托，有实效；二要使活动的内涵落实到为师生服务、为学院发展的高度，以实实在在的工作，提升创先争优活动意义；三要发扬创新精神，广开思路，创新形式，多思考、多摸索、多总结，加强宣传，使活动开展的意义和价值深入到师生心中。 余党军还结合三联系工作，在信息学院结对一个班级，以加强对学院创先争优工作的沟通和了解。 （ 文 王伟斌 ）', 'http://info.jhc.cn/2012/1109/c201a1935/page.htm', 'http://info.jhc.cn/_upload/article/images/df/4c/9d4035f7466ca4495ed2acb9e5cc/dd26a57c-7210-47b9-8635-2b920f6f0a90.jpg|http://info.jhc.cn/_upload/article/images/df/4c/9d4035f7466ca4495ed2acb9e5cc/ef8331db-2d9f-41cf-b15f-55e1100f1361.jpg', '2017-05-17 14:18:06.166786');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (501, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院成立公寓管理委员会', '10月29日，信息学院举行学院公寓管理委员会成立仪式，学生处处长助理、校宿管中心主任贾美倩及校宿管中心全体成员应邀出席。仪式由学院党总支委员吴晓明主持。 贾美倩对信息学院公寓管理委员会成立表示祝贺，她说，作为公寓管理委员会成员，要正确面对新成立的组织，准确定位，明确职责，正视管理对象，讲究工作方法，坚持树立以人为本的管理理念。她还就我院寝室卫生和纪律等方面的现状作了点评。 学院党总支书记朱登胜要求每一位公寓管理委员会委员担负起一份责任，给自己一个目标，从自身做起，加强自我约束，在工作中重视总结反馈，努力为寝室卫生和文化建设做出应有的贡献。 据悉，学院将在宿舍管理委员会成立之际开展一系列公寓文化活动，相信此举对更好的开展学生公寓管理，打造良好的公寓氛围，创建和谐的公寓文化产生积极影响。 （ 文 汪铭铠 ）', 'http://info.jhc.cn/2012/1109/c201a1931/page.htm', NULL, '2017-05-17 14:18:06.189581');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (502, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院举办“我的青春，我的梦想”演讲比赛', '为丰富校园文化，展现新生们飞扬的个性、青春的风采，近日，信息学院举办了以“我的青春，我的梦想”为主题的演讲比赛总决赛。 比赛现场，12位从初赛中脱颖而出的选手激情澎湃，用一支支美妙的青春赞歌诠释青春的真谛，放飞绚丽多姿的梦想。洪亮的声音、质朴的语言，让现场始终散发着青春的气息，传递着大家对梦想的执着追求。来自通信1001班的莫珊珊用慷慨激昂、饱含深情的演讲征服了全场的评委与观众，最终获得本次大赛冠军。 新生们纷纷表示，本次比赛对自身是一种激励，坚定了珍惜青春、实现梦想的信念，激励自己树立明确的学习目标，在青春的舞台上实现自身价值，谱写灿烂精彩的人生。 （ 文 陈静泓 ）', 'http://info.jhc.cn/2012/1109/c201a1923/page.htm', 'http://info.jhc.cn/_upload/article/images/8c/7f/3d0dd8a34a69a4f18b6b63310c74/332be82a-ad32-4209-9895-cc0895a25b44.jpg|http://info.jhc.cn/_upload/article/images/8c/7f/3d0dd8a34a69a4f18b6b63310c74/8698c263-500e-4fe8-aee5-551f436e7070.jpg|http://info.jhc.cn/_upload/article/images/8c/7f/3d0dd8a34a69a4f18b6b63310c74/180881c4-af68-4e51-b14d-8afd2e6ff1ba.jpg', '2017-05-17 14:18:06.211203');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (503, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院举办新生书画大赛作品展', '11月1日，走进西教学楼二号楼的一楼大厅，沁人心脾的墨香迎面扑来，信息学院“妙笔生辉，和谐校园”书画大赛优秀作品正在这里展出。 展出现场，作品风格多样，手法灵活。行书、楷书、隶书、草书，毛笔、硬笔、篆刻，各式书法作品或刚劲有力，或阴柔相济。人物素描、卡通漫画、国画、油画，多样的绘画作品栩栩如生，鲜艳的色彩、精巧的构图体现了同学们丰富的想象力与娴熟的技巧。精美的作品令前来参观的同学大饱眼福，赞不绝口。 此次活动不仅展示了同学们的艺术风采，陶冶了艺术情操，更为素质教育搭建了有效载体，丰富了校园文化。 （ 文 陈静泓 ）', 'http://info.jhc.cn/2012/1109/c201a1919/page.htm', NULL, '2017-05-17 14:18:06.233538');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (504, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院举行学生会工作经验交流会', '为表达对学生会“新鲜血液”的诚挚欢迎，展示风采，搭建平台，近日，信息学院学生会开展了学生干部经验交流会。 经过为期一个月的激烈竞聘，153位新生加入了学生会大家庭，为了使这些新成员尽快融入学生会，挑起学生会自我管理组织的重担，老学生干部与大家分享了自己在学生会的收获与工作感悟，并就如何提高工作效率，如何做好学生干部工作作了交流。学生会各部门则结合自身特色，通过PPT演示、才艺表演、现场演说等形式展示了一年来取得的工作成果和对未来的展望。 交流会上还对工作表现优秀的部门进行了表彰，以此激励学生干部们齐心协力，团结一心，在今后的工作中不断进取，更好地开展学生管理工作。 （文  陈静泓  刘冬冬）', 'http://info.jhc.cn/2012/1109/c201a1917/page.htm', NULL, '2017-05-17 14:18:06.256693');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (505, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院党校举行第十四期入党积极分子培训班开学典礼', '11月3日，信息学院党校第十四期入党积极分子培训班开学典礼在东教学楼216室举行。学院党校校长、党总支书记朱登胜，党校校务委员会成员及新一期240名学员参加开学典礼。 开学典礼在庄严的国歌声中拉开帷幕。朱登胜首先对全体学员表示殷切的希望和衷心的祝福，他说，在全院上下开展创先争优活动之际，希望学员们认真学习领会党的精神，尤其是十七大以来党的各项路线、方针、政策，使自己在思想上、政治上、行动上和党中央保持一致；认真学习当代文化知识，从上好每堂课、通过每门课开始；主动参与到“关爱学生 助推发展 构建和谐”等一系列有意义的活动中去，在创建和谐校园的实践中贡献自己的力量。 开学典礼上成立了党校第十四期入党积极分子培训班团支部，并宣读了党校章程、学员守则、党校优秀学员评定规则和教学课程安排。 （ 文 周荃 ）     关 闭', 'http://info.jhc.cn/2012/1109/c201a1910/page.htm', 'http://info.jhc.cn/_upload/article/images/9b/b5/609448134d509439fffff7f6643e/658cc036-a4c9-46c3-9829-9714a458c931.jpg|http://info.jhc.cn/_upload/article/images/9b/b5/609448134d509439fffff7f6643e/72935946-ecbd-489e-8f7f-9ed94857ada6.jpg|http://info.jhc.cn/_upload/article/images/9b/b5/609448134d509439fffff7f6643e/f45b66db-86e1-49b7-8874-f0ca2611e25c.jpg', '2017-05-17 14:18:18.753106');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (506, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '浙江博尚电子有限公司到信息学院举办合作办学宣讲会', '11月4日下午，浙江博尚电子有限公司到信息学院举办合作办学宣讲会，以此加强双方在教学科研和人才培养上的合作，浙江博尚电子有限公司人事部经理王少峰出席。 王少峰在宣讲会中介绍了公司情况及发展规划、岗位需求及岗位描述、实习安排等方面的内容，他以平时的语言，展示了企业实力与文化，他说，企业目前十分重视人才培养，这次特意与金职院合作，旨在吸引更多人才到企业工作，他对在场学生发出了诚挚的加盟邀请。 浙江博尚电子有限公司是一家专业从事研发、设计、生产和销售电子类产品的科技型企业。作为信息学院示范性校外实训基地，在前期的合作中，校企双方积累了丰富的合作经验。目前由于公司发展迅速，迫切需要一大批适合公司发展的高技能人才，而我校应电、通信专业能按照企业需求来培养学生，经校企双方协商，决定开展订单班人才培养，这将是我院校企合作培养人才的又一新成果。 （ 文 赵敏笑 ）', 'http://info.jhc.cn/2012/1109/c201a1908/page.htm', NULL, '2017-05-17 14:18:18.790177');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (507, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院举行教师说课观摩会', '为促进教师间的交流，提高教师的教学水平，11月8日，信息学院举行教师说课观摩会，校第六届青年教师教学竞赛获奖者李银海、顾敏艳、宣翠仙三位老师应邀主讲，信息学院全体教师参加了此次观摩会。 会上，三位教师分别针对凸轮轴的编程与加工、酒店管理系统的应用、台历的制作等教学内容，系统地阐述了教学资源的利用、学情的分析、教学内容的实施以及教学策略的设计。精彩的说课示范使在场的教师直观地感受到了什么是说课，怎样说好课，如何更好地组织教学环节，丰富教学内容，提高教学质量。 本次观摩会为即将到来的信息学院第五届教学基本功大赛作了准备，鼓励教师们在课堂教学中大胆创新，夯实教学基本功，以提升教师素质，推动项目化教学改革。 （ 文  陈静泓 ）', 'http://info.jhc.cn/2012/1109/c201a1904/page.htm', NULL, '2017-05-17 14:18:18.812098');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (508, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院十月份优秀社团名单揭晓', '日前，由信息学院社管会组织的“每月优秀社团”十月份评选结果揭晓。大学生自我服务协会、翼天演辩社、青春痘文艺社、番茄电脑社、风行轮滑俱乐部、大学生心理协会等社团最终被评为信息学院十月份优秀社团。     信息学院每月优秀社团的参评对象为本院的18个学生社团，其中每月评选出6个优秀社团。此次评选活动主要参考“活动申请表”，“活动评估表”，“活动自评表”得分和社团满意度评比而产生。 （文 朱扬帆 ）', 'http://info.jhc.cn/2012/1109/c201a1901/page.htm', NULL, '2017-05-17 14:18:18.834343');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (511, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院团委开展野外拓展培训', '为培养学生团结协作能力、团队互助意识和艰苦奋斗精神，加强学生干部的个人素质养成， 11月6日，学院团委组织学生干部前往义乌白岩山开展素质拓展培训。 在白岩山素拓中心，学生干部们在为期两天的拓展培训中，经历了“同舟共济”、“艰难跋涉”、“大脚板”、“逃生桥”等项目的艰苦考验，活动使大家充分体会到团结、合作、勇敢、奋斗的价值所在，明白了不抛弃不放弃的精神实质。生动形象、身体力行地拓展培训活动，使学生干部在思想上树立了好的团队、好的合作、好的思考在日常工作中的重要性。 （ 文 汪铭铠 金乐 ）     关 闭', 'http://info.jhc.cn/2012/1109/c201a1884/page.htm', 'http://info.jhc.cn/_upload/article/images/06/c1/f8ea7ac64e909fc287f45cb25a56/db441a15-d8f9-406f-aa37-dfdd626dbbcd.jpg', '2017-05-17 14:18:18.901027');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (512, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '校企携手、深度合作，优势互补、互惠共赢', '为了深化校企合作，发挥校企间各自优势，11月7日，信息学院与宁海保安公司在宁海举行校企合作签约仪式，信息学院院长龚永坚、宁海保安公司总经理胡余泽代表双方在协议书上签字。 根据协议，校企双方将本着“合作共赢，互惠互利”的原则，共同培养适应行业企业岗位要求的高技能人才，推动学校人才培养工作，为企业快速发展提供人才支撑，并重点在企业员工培训、专业教师与技术骨干互聘、学生顶岗实习与暑期专业实践、工程项目合作开发、合作编制校本教材等方面进行全面合作。同时，宁海保安公司承诺连续三年、每年出资5万元，面向信息学院楼宇智能化工程技术专业设立“宁海保安励志奖学金”，以此激励学生努力学习专业知识，提升综合职业素质。与宁海保安公司的合作，代表信息学院校企合作又向纵深迈出了坚实的一步。 据悉，宁海县保安公司成立于1992年9月，是宁海县公安局唯一直属企业，也是宁海县唯一一家提供保安服务，具有设计、安装安防工程资质的专业性公司。公司下设保安、培训、技防、信息产业等部门。近年来，公司在保安队伍、安全防范工程建设等方面取得了显著成绩。先后被授予“全国先进保安服务公司”、“浙江省十佳保安服务公司”、“浙江省规范化保安服务公司”、“宁波市文明单位”等荣誉称号。 （ 文 朱墨池 ）', 'http://info.jhc.cn/2012/1109/c201a1877/page.htm', 'http://info.jhc.cn/_upload/article/images/e4/c7/1205599442b198a297c45a97ca80/16d2ac31-33a5-45d0-8172-72fecb43a340.jpg|http://info.jhc.cn/_upload/article/images/e4/c7/1205599442b198a297c45a97ca80/845ede5b-b272-4bd6-8157-14da2a5bf46e.jpg', '2017-05-17 14:18:18.923389');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (513, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院首届“博尚电子订单班”举行开学典礼', '11月9日下午，信息学院举行首届“博尚电子订单班”签约暨开学典礼，信息学院院长龚永坚和浙江博尚电子有限公司副总裁汪�d代表校企双方签订了订单班合作协议，这标志着信息学院互利双赢的校企合作又向前迈出了坚实一步。 在经过企业宣讲、专家面试等环节后，学院首届“博尚电子订单班”最终从应用电子技术和通信网络与设备两个专业招收了30名学员，他们将由校企共同完成在知识、技能、素质等各个方面的培训任务，以企业的岗位素质要求为目标，共同培养学生的专业知识和岗位技能。浙江博尚电子承诺在学生实习期满后，择优录用，并提供5万元奖学金奖励优秀学员。 在开学典礼上，汪�d希望订单班学员摆正位置，从基层做起；认真学习，打好理论基础；多实践，尽最大努力培养动手能力。龚永坚则要求订单班同学养成终身学习的良好习惯，以学生、准员工双重身份严格要求自己，遵守学校和企业的规章制度，从一点一滴做起，建立良好的职业素养和职业道德。 校企合作、开门办学是高职教育发展的一个宗旨和方向，校企双方对首届订单班都寄予厚望，希望以此为契机，为学生就业提供一个良好平台，实现学校、企业、学生、社会多赢局面。 （ 文 赵敏笑 ）', 'http://info.jhc.cn/2012/1109/c201a1869/page.htm', 'http://info.jhc.cn/_upload/article/images/17/3b/b58c50d34adfb12c00c29705b446/ad267c1f-37bf-4d68-bb68-f6e9ad480749.jpg|http://info.jhc.cn/_upload/article/images/17/3b/b58c50d34adfb12c00c29705b446/38f617b8-bc6a-4d70-bff9-343b0430cdcf.jpg|http://info.jhc.cn/_upload/article/images/17/3b/b58c50d34adfb12c00c29705b446/9c7a5f48-d1c8-4cdb-8913-5a9ccdb7e3ec.jpg', '2017-05-17 14:18:18.945516');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (514, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '王成福副院长赴乐山职业技术学院挂职锻炼', '11月10日，受学校指派，副院长王成福前往四川乐山职业技术学院挂职锻炼，担任乐山职业技术学院电子信息系副主任，为期一年。乐山职业技术学院为此专门召开欢迎会，宣读了任命文件，并颁发聘书。同时前往挂职的还有艺术学院华玉亮老师。 据悉，王成福副院长等作为我校对口支援乐山职业技术学院建设专家，将结合我校国家示范院校建设经验，就专业建设、课程开发、基地建设、教师培养、新系部的筹建以及该校争创四川省示范院校等方面开展支援，以加强两校之间的深度合作。 （ 文 王伟斌 ）', 'http://info.jhc.cn/2012/1109/c201a1864/page.htm', NULL, '2017-05-17 14:18:18.96782');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (515, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院与高新园区企业召开IT学院筹建会', '11月12日上午，校IT学院筹建小组与金华利诚信息技术有限公司、浙江博尚电子有限公司等14家单位在市开发区高新技术园区召开IT学院筹建会，进一步落实IT学院筹建工作。开发区高新技术产业局伊益亮局长主持会议。 信息学院龚永坚院长在会上首先对IT学院的组织机构、骨干企业的选取条件、企业理事单位的权利和义务、政府扶持政策争取和筹建计划等方面的问题作了介绍。结合筹备工作，各企业结合自身需求和实际情况，对IT学院的课程设置、培养途径、培养方式、考核方式等内容作了深度探讨，并就进一步拓展校企深度合作，取得校企双赢局面进行了展望。伊益亮局长对企业的积极响应表示感谢，并对下一阶段筹建小组与企业单独访谈事宜进行了布置。 作为IT学院的承办单位，信息学院与开发区高新技术产业局进行了多次沟通，在理事单位人选，政、校、企合作下的运行机制以及拟争取的政府政策支持等方面达成了共识，有力地推动了IT学院各项筹建工作的落实。 （ 文 邱晓华 ）', 'http://info.jhc.cn/2012/1109/c201a1859/page.htm', 'http://info.jhc.cn/_upload/article/images/fc/aa/e1c8a6b14ad4a5634a943f603b11/2d52c995-3bf3-4643-a2ca-c2dd3fc74cdc.jpg', '2017-05-17 14:18:18.990011');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (516, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '浙江省计算机应用与教育学会教育委员会第十四届年会在我校召开', '11月12日至14日，浙江省计算机应用与教育学会教育委员会第十四届年会在我校信息学院召开。中国科学院院士沈绪榜、校党委副书记余党军、浙江省计算机应用与教育学会有关领导、专家出席会议，来自省内24所高校近90名代表参加了本届年会。开幕式由信息学院院长龚永坚主持。 余党军在开幕式上致辞，他首先对与会代表表示热烈的欢迎，并简要介绍了我校计算机学科的建设情况。他指出，计算机应用技术作为现代产业体系发展的基础，在现代化教育中占有举足轻重的地位，希望以年会为平台，加强院校间计算机教学改革经验的交流，共享专业建设成果，推动我省计算机学科的专业建设与课程建设。 会上，沈绪榜院士作了题为《串行和并行计算的自然统一》的报告，围绕现有的三种计算模式与芯片，具体阐述了从体系结构、程序设计等方面全方位实现计算的自然统一。他还面向我校学生从数学知识、制造技术、体系结构多角度作了题为《计算机应用演变的技术》的讲座，希望同学们掌握中文、英语、计算机三种语言，努力成为“图灵式人才”。国家杰出青年基金获得者苏开乐教授以MCTK模型检测器为例，深入讲解了《模型检查多智能体系统》。 会议期间，学校党委书记李秋华看望了沈绪榜院士及与会代表，并对教育委员会的各位专家对学校专业建设等各方面给予的关心和支持表示感谢。 据悉，自本届年会筹备以来，共收到论文75篇，其中，12篇被评选为优秀论文。经过委员会的努力，论文在国内第一本专门针对计算机教育的专业杂志《计算机教育》中刊登。会议期间，全体代表就优秀论文展开了广泛交流，并对新时期的计算机应用与教育问题进行了深入探讨。 浙江省计算机应用与教育学会教育委员会年会作为计算机应用技术与教学的重要交流平台，第一次将承办权授予高职院校，这是对我校计算机学科建设与人才培养的肯定，更有助于扩大我校计算机专业的影响力，推动计算机专业教改、科研更上新的台阶。 （ 文 陈静泓  摄影林雪华 ）', 'http://info.jhc.cn/2012/1109/c201a1848/page.htm', 'http://info.jhc.cn/_upload/article/images/4a/d6/bd37837c411eb1378588f0296872/33e68722-53fd-4c03-9aa4-006f8974caab.jpg|http://info.jhc.cn/_upload/article/images/4a/d6/bd37837c411eb1378588f0296872/b9bc5472-7b15-45c7-8e00-99d6b0ca1588.jpg|http://info.jhc.cn/_upload/article/images/4a/d6/bd37837c411eb1378588f0296872/783b5086-a10b-44c9-aaa6-34d9eeadf49b.jpg|http://info.jhc.cn/_upload/article/images/4a/d6/bd37837c411eb1378588f0296872/9f3828bd-9580-4c07-9e6e-908d9319192b.jpg|http://info.jhc.cn/_upload/article/images/4a/d6/bd37837c411eb1378588f0296872/b81bdd02-289c-4f3a-9de4-161795711307.jpg', '2017-05-17 14:18:19.013509');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (517, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院成教生管理推新举措', '针对成教招生规模扩大及校区搬迁等新情况，信息学院为更好地落实对成教生教学和日常管理，日前推出了许多新举措。 新举措包括加强对成教学生的考勤工作，将成教新生早晚自习教室和全日制学生安排在一起，统一管理；形成教学检查小组，定期对成教生到课率进行抽查汇总；成立成教公寓检查小组，对成教学生公寓卫生、夜不归宿等加强检查力度；加强成教生的自我管理，充分利用和整合成教生党团组织和学生组织资源，运用科学管理方法，开展自我组织、自我控制和自我监督；建立合理、人性化的规章制度，实现合理、规范和充满人性化成教生自我管理。 这一系列新举措的实施，实现了成教管理工作的制度化与规范化，对关心成教学生的学习与生活，关爱他们的成长发挥了积极作用。 （ 文 汪铭铠 杜凤英）', 'http://info.jhc.cn/2012/1109/c201a1844/page.htm', NULL, '2017-05-17 14:18:19.034458');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (518, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院在省大学生多媒体作品设计竞赛中喜获佳绩', '从日前落幕的浙江省第九届大学生多媒体作品设计竞赛决赛上传来好消息，信息学院选送的作品中获二等奖1个、三等奖2个。其中宣翠仙、邱晓华老师指导的谢佐孟、薛超、刘清新开发的《Web+Wap双平台毕业环节跟踪管理系统》获网站类作品二等奖，另有两个作品《仿生学科普课件》和《我是你的眼》分获课件和DV作品类三等。此次获奖很好的展示了我院学生在艺术设计方面的专业技能和职业素养。 浙江省大学生多媒体作品设计竞赛是我省高校学生艺术设计类比赛中最权威的官方比赛，由浙江省大学生竞赛委员会主办，分为课件、平面、网站、动画及DV制作五大类，本次比赛吸引了省内几乎所有高校参加，共有全省各高校的900多件作品参与角逐。 （ 文 楼小明 ）', 'http://info.jhc.cn/2012/1109/c201a1840/page.htm', NULL, '2017-05-17 14:18:19.056727');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (519, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院举办就业法律知识讲座', '为切实做好毕业班学生就业工作，加强学生在今后实习与就业过程中的法律意识，11月16日晚，师范学院徐建丽副教授应邀为信息学院各专业三百余名同学作了就业法律知识讲座。 讲座中，徐建丽结合实际案例，针对与毕业生休戚相关的就业协议书、劳动合同的签订，劳动保障与劳动纠纷处理，试用期、实习期的区别与注意点等问题，为同学们通俗而生动地讲解了《劳动法》、《劳动合同法》、《就业促进法》、《合同法》等与学生就业相关的法律知识，她希望同学们在就业过程中，始终带着法律意识、维权意识和诚信意识。 本次讲座为即将毕业的同学及时补充了就业法律知识，有效增强了大家在就业过程中的自我保护意识，对学院后续就业工作的开展必将产生积极影响。 （ 文 陈静泓 赵倩 ）', 'http://info.jhc.cn/2012/1109/c201a1835/page.htm', NULL, '2017-05-17 14:18:30.325064');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (520, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '《金华晚报》报道我校学生在省大学生多媒体大赛中获佳绩', '省多媒体作品设计大赛 金职院学子获丰收 本报讯（通讯员申屠佩贞）11月12日，第九届（2010年）浙江省大学生多媒体作品设计竞赛决赛在浙江传媒学院落下帷幕，获奖名单揭晓，金华职业技术学院共获得10项大奖。  　　其中，陈奕彤平面设计作品《邮政承载的绿色记忆》荣获省一等奖；吴帅帅、章俊毅、王燕联合创作的DV《默默行走在路上的老人———学雷锋故事篇》，谢佐孟、薛超、刘清新的网站《Web+Wap双平台毕业环节跟踪管理系统》，周晓敏、葛晓霞同学设计的课件《除法的认识》三项获省二等奖；另外还有6项三等奖。  　　据了解，本赛事由浙江省大学生科技竞赛委员会主办，浙江师范大学承办，是我省各高校学生展示多媒体设计与制作水平的盛会，是浙江省官方认可的权威竞赛。本届大赛作品设“绿色记忆”海报设计、“让世界更美丽”动画设计、课件设计、网页设计及主题为“感动”的DV设计五个类别，共收到全省50多所院校参赛作品906件，其中课件118件、网站139件、平面302件、动画182件、DV165件，经过专家组初审、终审及现场演示与答辩等环节的激烈角逐，得出最终奖项。  新闻链接：http://epaper.jhnews.com.cn/site1/jhwb/html/2010-11/19/content_1212571.htm', 'http://info.jhc.cn/2012/1109/c201a1830/page.htm', NULL, '2017-05-17 14:18:30.429564');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (521, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院举行艾滋病预防知识讲座', '11月19日，信息学院邀请学校防控办老师为学院师生举办了一场“预防艾滋病，共创健康生活”艾滋病预防知识讲座。 　　防控办朱红薇老师以自己深厚的理论功底，通过深入浅出的讲解，向同学们介绍了什么是艾滋病，艾滋病的危害，如何预防艾滋病以及艾滋病传播途径等相关知识。她告诫大学生们在谈“艾”色变的当今社会，在加强自身防范意识的同时，更要洁身自好、自尊自爱， 讲座还介绍了肺结核、乙肝等其他常见传染性疾病的预防及治疗手段。提倡同学们加强体育锻炼，增强自我保护意识和能力，树立健康的生活观念。 （文 黄蓉 汪铭凯）', 'http://info.jhc.cn/2012/1109/c201a1826/page.htm', NULL, '2017-05-17 14:18:30.450012');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (522, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院开展“丝带送祝福”感恩寄情活动', '11月18日，由校心理健康中心主办，信息学院承办，旨在为同学们搭建一个表达情感、传递祝福平台的“新生心理健康宣传月”系列活动之“丝带送祝福”感恩寄情活动在稻花香广场举行。 本次活动以“丝带送祝福，感恩溢校园”为主题。同学们在丝带上写下了对亲人朋友、老师同学的祝福，以此表达他们的感恩之情，吐露他们真挚心声。工作人员们则将装载着祝福与感恩的丝带挂在树上，传递到校园的每一个角落。 在感恩节来临之际，学院希望通过此类活动，让同学们懂得珍惜亲朋好友，懂得感恩、传承感恩，感恩于心、感恩于行,帮助大家树立正确的人生观和价值观。 （ 文 黄蓉 ）', 'http://info.jhc.cn/2012/1109/c201a1821/page.htm', NULL, '2017-05-17 14:18:30.470227');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (523, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院召开第八次学生代表大会筹备会', '为做好学院第八次学生代表大会的准备工作，激发学生干部的工作热情，11月18日下午，信息学院召开第八次学生代表大会筹备工作第一次会议。信息学院第七届主席团成员与主要学生干部参加了本次会议。 会议结合第八次学代会的各项工作任务，决定成立第八次学生代表大会筹备工作秘书处，下设会议资料组、代表资格审查组、提案工作组、会务组、宣传报道组和机动组六个筹备组。会上，大家对各组的工作任务进行了沟通与布置，并对会议准备期间可能出现的问题展开了交流。 本次会议标志着我院第八次学代会正式进入筹备阶段，激励全体学生干部共同投入学代会准备工作，为学代会的顺利召开提供了保证。 （ 文 沈家锋  陈静泓 ）', 'http://info.jhc.cn/2012/1109/c201a1817/page.htm', NULL, '2017-05-17 14:18:30.491048');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (524, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '九江职业大学信息工程学院张先成副院长来我院考察交流', '11月26日，九江职业大学信息工程学院张先成副院长等来我院考察交流。院领导龚永坚、朱登胜等接待了来宾。 在交流座谈中，张先成副院长结合我院国家示范院校建设，从招生就业、专业发展、教学科研、实训室建设等多个方面与我院领导一一作了交流，彼此交流心得，共享经验。我院后示范时期吸引了众多高职同行前来交流学习，此次九江职业大学组团来我校交流，展示出我院示范建设后强大的对外辐射力。 （ 文 王伟斌 ）', 'http://info.jhc.cn/2012/1109/c201a1810/page.htm', 'http://info.jhc.cn/_upload/article/images/89/c2/24a267bc415cbb2ce02ab0e3a72b/b879318f-a4c0-4512-8a5d-02aea8537b35.jpg|http://info.jhc.cn/_upload/article/images/89/c2/24a267bc415cbb2ce02ab0e3a72b/5a97c35b-034a-440b-8f2c-19cf618959b7.jpg', '2017-05-17 14:18:30.513082');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (525, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院电气自动化专业举行师生座谈会', '为加强师生间的交流互动，增进师生感情，同时也为学生提供一个相互交流和学习的机会，信息学院电气自动化工程部11月26号举行专业师生座谈会。 座谈会上，同学们从学习中出现的问题、学习技巧、选择就业要注意的问题和如何处理好工作和学习之间的关系等方面进行了热烈谈论，老师们则根据自身的体会为大家提出了一些切实可行的建议。 活动持续了两个小时，虽然时间不长，但同学们都觉得收获颇多，同学们普遍反映，师生座谈会这种形式拉近了学生和老师之间的距离，密切了师生之间的关系，对自己改进学习方法，提高学习效率，树立正确的择业观大有帮助。  （ 文 曹胜任 ）     关 闭', 'http://info.jhc.cn/2012/1109/c201a1806/page.htm', 'http://info.jhc.cn/_upload/article/images/33/86/97a0798e4b22bd6ad5cb5705c628/69dbbf9d-6925-47dd-9266-7fd69d9a20e4.jpg', '2017-05-17 14:18:30.535262');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (526, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院首届“联想工程师”订单班开班', '11月29日，信息学院与中国联想教育集团、金色在线信息技术服务有限公司深层次合作“产品”——“联想工程师”订单班举行开学典礼。联想阳光雨露信息技术服务（北京）有限公司资源管理经理马小鹏、金色在线信息技术服务有限公司总经理陈小玲应邀出席，院领导朱登胜、陈海荣及教师代表、全体学员参加开学典礼。 本届“联想工程师”订单班是校企双方本着优势互补、资源共享、互惠多赢的原则，为共同培养IT服务外包紧缺型人才而组建的。订单班学员将先入职，再以员工身份参加订单班学习，课程编排上以联想的LCSE维修工程师认证为主体，并辅以计算机、网络、操作系统为基础理论支撑的岗位技能、业务知识、职业素养综合培训。认证权威性高，技能针对性强，课程安排与企业实际需求完全对接，可操作性高是本届订单班的特色。学员在完成培训、考取LCSE维修工程师认证后，即可进入联想就业渠道，成为联想IT服务的正式一员。 开学典礼上，企业代表对订单班学员成为联想员工表示祝贺，并结合联想IT服务外包的历史与现状提出了培训目标和具体要求。学院领导希望订单班同学珍惜此次来之不易的就业机会，努力学习，积极实践，以优异成绩展示我院学生的良好素质。 （文  朱墨池）', 'http://info.jhc.cn/2012/1109/c201a1802/page.htm', NULL, '2017-05-17 14:18:30.557396');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (527, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学子在全国通信行业职业技能大赛上收获佳绩', '从日前结束的2010年全国通信行业职业技能大赛（高职院校类）上传来喜讯，由我校信息学院选派的通信专业8名同学组成的2个参赛队，经过奋力拼搏，收获佳绩，其中邵力、庞玲、陈威、汤超智同学组成的一队获得了团体三等奖，另一参赛队获得优胜奖。 本次大赛由工业和信息化部通信行业职业技能鉴定指导中心、中国就业培训技术指导中心主办，教育部高等学校高职高专通信类专业教学指导委员会、南京信息职业技术学院承办。大赛吸引了全国35所高职院校的48个参赛队参与角逐，最终产生一等奖3个，二等奖6个，三等奖9个，获奖面37.5%，我校信息学院是首次参与此项赛事， 据悉，全国通信行业职业技能大赛结合我国通信行业发展规划及3G通信技术业务发展趋势，以提高产业自主创新能力和核心竞争力、进一步深化职业教育教学改革、切实加强技能型人才的培养为目的。大赛每年都吸引国内外众多知名通信企业到场观摩并招聘优秀选手。 （ 文 丁晨 王伟斌 ）', 'http://info.jhc.cn/2012/1109/c201a1795/page.htm', 'http://info.jhc.cn/_upload/article/images/aa/5c/866be31a4be1a15a5dd928915089/1c4bdc0d-d768-4d9f-bbec-1df1c8da1d95.jpg', '2017-05-17 14:18:30.579829');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (528, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '副院长王成福挂职乐山职业技术学院成果显著', '副院长王成福挂职乐山职业技术学院成果显著 发布人：管理员资讯来源：信息管理中心 发布时间：2010-12-01 15:47:23点击数量:1     信息学院副院长王成福自11月10日赴乐山职业技术学院挂职锻炼以来，对该校应电专业建设和重点专业人才培养方案的制定进行了细致的指导，成效显著。 在金职院的应用电子专业实践专家访谈和重点专业人才培养方案研讨过程中，王成福结合我院国家示范专业建设经验，对专业人才培养目标及专业课程建设进行了指导，并引导企业专家对应电专业工作岗位与工作任务进行了讨论分析。他还以应电专业为例，分享了自己在专业建设与课程建设等方面的经验与见解，对该校重点专业人才培养方案的制定提出了许多意见与建议。 专业建设是我校对口支援乐山职业技术学院的重要内容，此次在重点专业建设上和交流合作，必将对两校的深度合作起到重要的推进作用。 （ 文 陈静泓 ）', 'http://info.jhc.cn/2012/1109/c201a1790/page.htm', 'http://info.jhc.cn/_upload/article/images/09/9b/2011ddcd4876a2f0604c6b8194fc/5981d5fd-a425-4556-b292-308d4f9463b4.jpg', '2017-05-17 14:18:30.601946');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (546, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '我校信息学院党总支召开“公推直选”试点工作动员大会', '党的十七大提出要在基层党组织中积极探索改革党内选举制度、改进候选人提名制度和方式、逐步扩大基层党组织领导班子直接选举范围。1月13日，根据学校统一部署，信息学院党总支召开会议“公推直选”试点工作教职工动员大会。校领导俞勇建、组织部负责人出席会议。 校党委副书记、纪委书记俞勇建在动员会上指出，在创新争优活动深入开展之际，我校启动了基层党组织“公推直选”工作，旨在通过“公推直选”工作改进学校基层党组织选举制度，服务学校发展、服务社会进步、服务师生成长成才。信息学院党总支作为学校试点单位，希望全体教职工能正确认识，公正客观保证试点工作顺利进行。 信息学院党直选工作小组组长介绍了“公推直选”工作操作办法，组织部负责人宣读信息学院党总支书记、委员任职条件后，信息学院党总支组织全体教职工对意向候选人进行推荐投票。 （ 文 王伟斌 ）', 'http://info.jhc.cn/2012/1109/c201a1681/page.htm', NULL, '2017-05-17 14:18:42.414057');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (529, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院“畅扬光电”订单班举行开班典礼', '12月1日，信息学院与浙江畅扬光电科技有限公司代表签订校企合作协议，并举行了首届“畅扬光电”订单班开班典礼。公司人事部赵普询经理、钱飞应厂长应邀出席，院领导朱登胜、教师代表、订单班全体学员参加开班典礼。 “畅扬光电”订单班是信息学院电子产品开发部促进学生在新兴行业就业的一次引导和尝试活动，旨在全球气候变暖大背景下，引导学生在太阳能、电动交通工具、LED节能照明，物联网等倡导低碳和节能技术的新兴行业开拓就业渠道。本届订单班共有34名学员，他们将在校企共同制定的教学大纲下，学习LED岗位知识，进入企业岗位实践，最终实现就业。 开班典礼上，朱登胜就新兴行业就业与配合全球低碳技术促进高职专业教学改革问题上给予了充分的鼓励与肯定。企业代表也希望同学们珍惜此次就业机会，伴随着新产业与新企业的成长而成长，重视积累经验，早日成为新兴行业的开拓者。 据悉，浙江畅扬光电科技有限公司是一家主要以研发、生产、销售LED灯具为主的高科技、外向型民营企业，公司产品以家用LED灯、汽车用LED灯、信号LED灯、路灯、太阳能LED灯、景用LED灯为主。LED照明灯具的应用是“低碳技术”与“节能技术”的重大突破，是未来节能照明灯具市场的主流。 （ 文 余红娟 ）', 'http://info.jhc.cn/2012/1109/c201a1784/page.htm', 'http://info.jhc.cn/_upload/article/images/25/cc/0c840eb440d68083540ee82ca7a7/517cc2d9-7797-456a-aaa3-5697bf2092ba.jpg', '2017-05-17 14:18:30.624208');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (530, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院举办第五届教学基本功大赛说课比赛', '为切实提高教师的说课水平，夯实课堂教学基本功，提升教学质量，近日，信息学院举办了说课比赛，经过各项目部的选拔推荐，共有7位教师参加本次比赛。 各参赛教师围绕学生能力目标，以富有逻辑性的语言，配合精心制作的多媒体课件，将理实一体化的项目教学理念融入整个说课过程，阐述了教学内容、教学组织形式、教学环境、教学步骤以及考核体系等内容，展示了与行业企业合作进行基于工作过程的课程开发与设计。 此次说课比赛作为信息学院第五届教学基本功大赛的内容之一，不仅凸显了我院教师精湛的说课艺术，更激发了全体教师对课堂教学改革的深入思考，进一步推动了项目化教学改革。 （ 文 陈静泓 ）', 'http://info.jhc.cn/2012/1109/c201a1780/page.htm', NULL, '2017-05-17 14:18:30.646427');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (531, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院召开第八次学生代表大会', '12月7日，信息学院第八次学生代表大会在校大学生活动中心隆重召开。学院党总支书记朱登胜、总支委员吴晓明及教师代表、178名正式代表参加大会。     大会在庄严的国歌声中拉开帷幕。朱登胜在会上发言，他首先充分肯定了学院第七届学生会在促进学院发展和开展学生自治组织管理中的出色工作和所发挥的重要作用；对即将选举产生的第八届学生会主席团提出了几点希望，希望他们在老一届学生会干部工作的基础上敢于创新、敢于探索，工作要做的更出色，以满腔热情更好的服务学院发展、服务广大同学，为“和谐信息”建设添砖加瓦。     大会审议并通过了信息学院第七届学生会主席团的工作报告，经无记名投票，选举产生了信息学院第八届学生会主席团成员。 （ 文 周华 ）', 'http://info.jhc.cn/2012/1109/c201a1775/page.htm', NULL, '2017-05-17 14:18:30.668579');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (532, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院2010年高起本录取成绩喜人', '近日，从杭州电子科技大学成教招生部传来喜讯，我院200名参加高起本成考的同学中有160人被录取，上线率达80%。 高起本是学院今年新推出的一种本科学历教育模式。与杭州电子科技大学合作办学，具有收费标准低、互认课程多等优点，主要开设电子信息工程、计算机科学与技术等9个专业，是在校生获取本科学历的一条最佳途径。 （文 单天德）', 'http://info.jhc.cn/2012/1109/c201a1771/page.htm', NULL, '2017-05-17 14:18:30.690832');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (533, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院举行“梦，飞的更高”舞台剧晚会', '为了展示社团风采，12月12日晚，信息学院青春逗文艺社等社团在大学生活动中心组织开展了“梦，飞的更高”主题晚会，晚会以“寻找属于自己的梦想，学会‘自信、自强、自立’”为主题。 整场晚会以舞台剧的形式贯穿，出演的节目类型多样，劲爆的舞蹈、优美的歌曲、幽默的相声、深情的朗诵、经典的戏曲等，贯穿以互动游戏，使全场的气氛此起彼伏，高潮迭起。轻松祥和的晚会气氛，给了信息学子一个解压放松、展现自我、相互交流的平台，充分展示了学院社团文化的丰富多彩，体现了社团为学生服务的宗旨。 （文  卢雅梨 ）', 'http://info.jhc.cn/2012/1109/c201a1763/page.htm', 'http://info.jhc.cn/_upload/article/images/e3/13/c0a05a4d492c8c089fc045b76153/1317d96c-3766-481c-b39b-94b8e889a2c9.jpg|http://info.jhc.cn/_upload/article/images/e3/13/c0a05a4d492c8c089fc045b76153/b6d42d08-7f60-4424-8f3d-3263ae3688c7.jpg|http://info.jhc.cn/_upload/article/images/e3/13/c0a05a4d492c8c089fc045b76153/1a5fdd35-37d0-4b67-988c-971d44628ea1.jpg', '2017-05-17 14:18:42.07684');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (534, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院举行公寓党支部乒乓球赛', '为推进我院公寓文化建设，增强各公寓学生之间的团队精神和凝聚力，12月14日中午,信息学院各公寓党支部举行了公寓乒乓球赛。参赛的选手是我院5幢公寓党支部的代表，以公寓为单位参赛。 活动中，选手们以饱满热情积极迎接挑战，展示球技，交流球技，充分展现了团结一致、共同奋战的团队精神。通过这次乒乓球联赛，很好地引导学生加强体育锻炼，提倡健康文明的生活方式，活动为今后公寓党团工作奠定了良好基础。 （ 文 公寓管理委员会 汪铭铠 ）', 'http://info.jhc.cn/2012/1109/c201a1758/page.htm', NULL, '2017-05-17 14:18:42.102698');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (535, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院举行首届NIIT订单班开班典礼', '12月21日，信息学院与NIIT（印度国家信息学院）金华软件培训学校筹建的首届NIIT订单班举行开班典礼。NIIT金华软件培训学校董事长陈小玲、校长仲煜及学院党总支书记朱登胜、教师代表、订单班全体学员参加开班典礼。 NIIT以其先进的教育理念、教育模式和教育手段培养了大批软件人才，在全球IT行业享有很高的知名度。本届NIIT订单班是双方本着优势互补、资源共享、互惠多赢的原则，为共同培养IT服务外包紧缺型人才而组建的。订单班共有20名学员，主要为软件技术专业学生，他们将在NIIT金华培训学校进行三个月的准职业培训和学习，考取NIIT认证，最终在IT企业实现就业，为金华重点发展的软件外包产业提供人才支撑。 开班典礼上，企业代表和学院领导都对订单班同学寄予厚望，希望他们努力学习，积极实践，努力成为一名合格员工，实现优质就业。双方对订单班教学工作做了具体的安排。 （ 文 楼建忠 ）     关 闭', 'http://info.jhc.cn/2012/1109/c201a1752/page.htm', 'http://info.jhc.cn/_upload/article/images/cc/67/1e6332604b7084cb85f0bb3bbbe5/c4fd664b-1af7-4e28-b966-1506862624f2.jpg', '2017-05-17 14:18:42.169277');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (536, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院3个国家教学资源库子项目获批立项', '日前，我校信息学院与湖南铁道职业技术学院正式签署国家教学资源库子项目实施责任书，这标志着我校《电子电路的分析与应用》、《电子电路分析制作调试虚拟实训室》、《智能测控电子产品实验系统》等3个标志性资源库建设项目正式成为国家教学资源库建设项目，同时获得50万立项经费。 国家教学资源库建设遵循“共建共享，边建边用”原则，旨在建设一批代表国家水平、具有高等职业教育特色的标志性、共享型专业教学资源库，带动全国高职院校专业教学模式和教学方法改革，整体提升高职人才培养质量和社会服务能力。信息学院应用电子技术专业从2007年开始参与国家教学资源库建设，在国家示范院校三年建设期间，承办了示范性高职院校应用电子技术专业课程开发与教学资源库建设会议，并在全国示范建设三周年资源库建设汇报会上做主题发言，积累了丰富的资源库建设经验，形成了较为深厚的理论基础，此次子项目立项研究，对深化我院应用电子技术的教学改革具有重要意义。 据悉，此次信息学院立项的3个资源建设项目，在建设中的主要思路是运用动画、电路模拟仿真、实物测试等多种手段，系统解析课程教学中的知识点，把重点难点进行通俗化、形象化，为学生自主学习提供综合的课程教学资源。资源库建成后将弥补课内理论教学时数不足，师资水平参差不齐等问题，也将极大节约课程教学成本，对学生开展自主学习服务等方面将产生重要影响。 （ 文 王伟斌 余红娟 马汝星）', 'http://info.jhc.cn/2012/1109/c201a1748/page.htm', NULL, '2017-05-17 14:18:42.19178');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (537, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院举行教师汇报课比赛', '为锻炼教师教学能力，促进教师整体素质提高，12月22日，信息学院举行第五届教师教学基本功大赛汇报课比赛，各项目部推荐的教师代表参加了比赛。 比赛中，教师们采用不同的教学设计、教学方法和手段，充分利用多媒体和规范的板书设计，恰当地组织现场课堂教学。教师们精心选材、重点突出，以饱满的授课激情、独特的分析视角引导学生进行探究式学习，展示出了扎实的课堂教学基本功。评委从教案准备、教学实施、教学状态、教师教态、教学效果等方面对参赛老师的现场表现进行了综合考评。 此次比赛为全院教师提供了一个相互切磋、相互观摩、相互学习的平台，对提高教师的教学水平，推进学院教学工作起到了积极的促进作用。 （ 文 陈静泓 ）', 'http://info.jhc.cn/2012/1109/c201a1743/page.htm', NULL, '2017-05-17 14:18:42.213925');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (538, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院举行2011级杭电高起本开学典礼', '12月21日，信息学院举行杭州电子科技大学高起本（金华教学点）开学典礼。杭州电子科技大学成教学院院长胡建萍及我校成教学院、信息学院领导、160名新生参加开学典礼。开学典礼由信息学院党总支书记朱登胜主持。 胡建萍教授在开学典礼上对信息学院与杭电开展高起本合作办学模式给予了充分肯定，她认为该模式在促进高职学生成长成才，加强本科院校与高职的人才培养对接、教育教学交流等方面必将发挥积极作用。她鼓励新同学们学知识、学技能，为提升自己的人生价值而努力奋斗。 成教学院和信息学院领导也在会上发言，他们对新同学们都寄予了美好祝愿，希望同学们了解现代社会对大学生的技能要求，用知识和技能武装自己，实现自己的人生理想。 作为信息学院与杭电合作办学的第一批高起本学生，此次共录取了160名新生，他们将在杭电与信息学院共同制定的教学大纲下进行本科层次的课程学习。 （ 文 俞冬丽 ）', 'http://info.jhc.cn/2012/1109/c201a1739/page.htm', NULL, '2017-05-17 14:18:42.236088');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (539, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院为校企合作企业员工开展技术培训', '12月24-25日，信息学院企业信息化事业部为金华诺王机电有限公司领导、各职能部门员工共68人进行了为期两天的“EXCEL在工作中的高效实战应用”培训工作。 本次培训的Excel软件是企业进行信息化管理的重要软件，本次培训内容主要为Excel函数和透视表功能的案例介绍，通过两天时间的学习，学员们掌握了十余个常用函数的高效用法和数据透视表的实战应用，生动的案例让学员们能够举一反三，学以致用，真正提高了工作效率。很多学员培训后感叹说，原来经常要加班加点才能完成的工作，现在掌握了新方法，可以很快完成了。 金华诺王机电有限公司主要从事以绞车为核心的机电产品研制、开发与生产，是信息学院校外紧密基地，参与了“数据处理项目化教程”等教材的编写，并协助我院教师参与社会实践，通过本次培训工作，进一步拓宽了校企合作范围。 （ 文 陆建鹏 楼小明 ）', 'http://info.jhc.cn/2012/1109/c201a1736/page.htm', NULL, '2017-05-17 14:18:42.258353');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (540, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院举行“文明修身，你我同行”辩论赛', '为提高学生语言表达和逻辑思辨能力，活跃校园文化，增进交流沟通，信息学院翼天演辩社日前在2010级新生中举行了以“文明修身，你我同行”为主题的辩论赛。 经过前期的预赛，最终自动化101和通信101进入决赛环节。决赛围绕“大学生文明习惯的养成靠自律还是他律”进行，辨手们唇枪舌战，兵来将挡，各抒己见。辩手们或是温文而雅，或是咄咄逼人，或是幽默诙谐，或是大智若愚，或是慷慨陈词，虽风格迥异，却无不体现了大家对“自律，他律”这个人生主题的理解。最终自动化101技高一等，拔得头筹。 本次辩论赛以新生活动为契机，旨在深化“文明修身”主题活动，彰显大学生朝气蓬勃、积极向上的精神状态，引导同学们养成文明健康的生活方式。 （ 文 汪铭铠 ）', 'http://info.jhc.cn/2012/1109/c201a1732/page.htm', NULL, '2017-05-17 14:18:42.280649');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (541, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院CDIO 改革初见成效', '近日，信息学院宣翠仙老师主持开发的《WEB项目开发》课程在学校网评时名列前茅，被直接确定为第二批校精品课程，这标志着学院目前开展的CDIO改革工作又出新成果。 近年来，为了适应生源基本素质的变化，满足市场经济对技能型人才的需求，信息学院站在教育改革的前沿，努力开拓，大胆创新，在软件技术专业中率先引进CDIO理论，建构新颖的“CDIO工程教学模式”。目前，CDIO改革试行的四门课程都取得了较好效果，上半年陈尧妃老师主持的《数据库技术》获批省级精品课课程，此次又有课程校级立项，是对软件技术专业CDIO改革的充分肯定，相信随着改革的深入，将对学院教育教学改革产生更多积极影响。 （ 文 楼建忠 ）', 'http://info.jhc.cn/2012/1109/c201a1728/page.htm', NULL, '2017-05-17 14:18:42.304454');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (542, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院举行新年校企联欢暨颁奖晚会', '1月5日，信息学院“相约2011，新信息，新辉煌”校企联欢暨颁奖晚会在实验剧场隆重举行。校党委副书记余党军、团委副书记谢庆勇，浙江博尚电子有限公司等学院紧密合作企业代表及信息学院600余名师生观看了晚会。 在教工合唱队一首铿锵有力的《没有共产党就没有新中国》歌声中拉开晚会帷幕。刚柔并济的武术表演，富有韵味的舞蹈《新贵妃醉酒》，诙谐幽默的小品《超幸福鞋垫》，激情四溢的歌伴舞《潇洒小姐》，精彩纷呈的节目秀出了信息学子的别样风采，企业的友情出演更将晚会推向了高潮。 晚会还对2010年校第八届科技文化节各竞赛与学院省级及以上学生学科竞赛获奖项目进行了表彰，校院领导、企业代表为获奖学生颁发了证书与奖金，鼓励大家再接再厉，再创佳绩。 本场晚会生动体现了校企文化的互通融合，展示了校企合作的丰硕成果，彰显了校企合作的办学理念，为今后进一步合作共赢指明了方向。 （ 文 陈静泓  摄影 杨斌 ）', 'http://info.jhc.cn/2012/1109/c201a1706/page.htm', 'http://info.jhc.cn/_upload/article/images/73/9a/250869994e47aaf42b2444f3fccf/ecbaf39e-62af-43fd-b236-09a344bae708.jpg|http://info.jhc.cn/_upload/article/images/73/9a/250869994e47aaf42b2444f3fccf/e59f6f1e-6ba9-4aa7-94cf-cee71ed2502d.jpg|http://info.jhc.cn/_upload/article/images/73/9a/250869994e47aaf42b2444f3fccf/f8072ffd-0065-4d6c-88d0-08743e3a891e.jpg|http://info.jhc.cn/_upload/article/images/73/9a/250869994e47aaf42b2444f3fccf/898fb3c0-ff2e-4ab8-a228-57bc4d658f62.jpg|http://info.jhc.cn/_upload/article/images/73/9a/250869994e47aaf42b2444f3fccf/1d91e026-bf94-4367-a803-fb5963337434.jpg|http://info.jhc.cn/_upload/article/images/73/9a/250869994e47aaf42b2444f3fccf/a175be7c-29da-47d0-8450-46777630ed61.jpg|http://info.jhc.cn/_upload/article/images/73/9a/250869994e47aaf42b2444f3fccf/67c942a8-93ed-4971-8e0b-952f355cae55.jpg|http://info.jhc.cn/_upload/article/images/73/9a/250869994e47aaf42b2444f3fccf/8aa177f4-45ce-4ff2-a44e-63934dd1f3a1.jpg|http://info.jhc.cn/_upload/article/images/73/9a/250869994e47aaf42b2444f3fccf/a8315ec3-42ae-42a1-ad29-595c2477c188.jpg|http://info.jhc.cn/_upload/article/images/73/9a/250869994e47aaf42b2444f3fccf/018b4d9c-790a-4f0f-8c18-f315aac8b45b.jpg|http://info.jhc.cn/_upload/article/images/73/9a/250869994e47aaf42b2444f3fccf/42d6ba64-ef4f-444f-a4d2-ecc7a6589990.jpg|http://info.jhc.cn/_upload/article/images/73/9a/250869994e47aaf42b2444f3fccf/6467109c-20e4-45fe-911f-6e824d79d264.jpg', '2017-05-17 14:18:42.325035');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (543, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '金华高新IT学院理事会成立大会在我校举行', '1月10日，金华高新IT学院理事会成立大会暨第一届理事会第一次会议在我校举行。金华经济开发区、金华市经委及学校有关领导、职能部门负责人、信息学院相关教师及各理事单位代表出席会议，会议由校党委书记李秋华主持。 会上，金华职业技术学院、IT学院理事会顾问杜世禄发表讲话，他说，IT学院的成立是高职教育校企合作办学理念的创新体现，校企各方要共同努力，围绕IT行业岗位要求做好用工调查及实训基地建设，完善专业发展规划，提高人才培养质量。金华经济开发区管委会副主任、IT学院理事会理事长邵国强在会上表示，开发区将积极配合学院的发展，各理事单位将充分发挥各自优势，创新发展模式，为区域经济与社会发展提供高素质人才。 大会审议并通过了理事会章程及IT学院执行机构、专业指导委员会的人员组成，议定第一届理事会由金华市经委、经济开发区相关部门领导、学校领导及金华利诚信息技术有限公司、浙江搜富网络有限公司等区域电子信息类骨干企业技术与管理专家组成，会议还确定了IT学院挂牌仪式议程，并就政府扶持政策、如何将学院办出特色等方面展开了广泛的交流，为学院的下一步发展指明了方向。 本次大会是对前期IT学院筹建工作的一次总结，标志着我校在新的育人机制下校企合作又迈出重要步伐，理事会的成立，为政、企、校三方合作办学机制的顺利运行提供了保证，对我校人才培养乃至金华地区的信息产业发展必将起到积极的推动作用。 据悉，IT学院是由我校与金华市经济技术开发区发起，联合区域内骨干企业组建而成的校企利益共同体，以行业为指导、企业参与、学校为主体，通过联合订单班的形式培养社会所需人才，做到校企间资源共享、师资互通。 （ 文 陈静泓  ）', 'http://info.jhc.cn/2012/1109/c201a1695/page.htm', 'http://info.jhc.cn/_upload/article/images/40/48/ec838cc14734acfd49ab24f6657b/3fba7ba9-a096-4703-b487-4a6846fd2625.jpg|http://info.jhc.cn/_upload/article/images/40/48/ec838cc14734acfd49ab24f6657b/2a732c0d-b1c2-43cf-bc3e-b0b91a2209a9.jpg|http://info.jhc.cn/_upload/article/images/40/48/ec838cc14734acfd49ab24f6657b/427c3fdd-468e-4e7d-8b33-733d366cf3fa.jpg|http://info.jhc.cn/_upload/article/images/40/48/ec838cc14734acfd49ab24f6657b/0dbc4a73-375e-4b8b-a569-f9d1e7b46736.jpg', '2017-05-17 14:18:42.347203');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (544, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院召开基层党组织“公推直选”动员大会', '党的十七大提出要在基层党组织中积极探索改革党内选举制度、改进候选人提名制度和方式、逐步扩大基层党组织领导班子直接选举范围，1月13日，根据学校统一部署，信息学院党总支召开全体教职工会议，就校基层党组织“公推直选”（试点）工作进行动员。校党委副书记俞勇建、组织部部长姜华敏等应邀参加。 俞勇建在动员会上发言指出，在创新争优活动深入开展之际，我校启动了基层党组织“公推直选”工作。旨在通过“公推直选”工作改进学校基层党组织选举制度，服务学校发展、服务社会进步、服务师生成长成才。信息学院作为学校试点单位，希望能正确认识，公正客观保证此次试点工作取得圆满成功，并总结经验向全校推广。 在介绍了“公推直选”工作具体操作办法，党总支书记、委员任职条件等内容后，学院教职工进行了公推投票，经计票和条件审查，推选产生了新一届总支委员会公推候选人。 （ 文 王伟斌 ）', 'http://info.jhc.cn/2012/1109/c201a1690/page.htm', NULL, '2017-05-17 14:18:42.369414');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (545, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '紧密对接地方产业发展需求 积极打造政校企利益共同体', '1月16日，学校举行金华高新IT学院挂牌仪式，这是继众泰汽车学院、皇冠学院之后成立的又一个“校企利益共同体”，并就此揭开政、校、企三方合作、共同培养金华电子信息产业高技能人才的新篇章。      金华作为全国电子商务创业示范城市，09年电子商务交易额300多亿元，已形成金华市高新技术产业园、浙中信息产业园和金东区软件园等三大电子信息产业园，电子信息产业已成为金华市的第二大支柱产业。2009年底，仅金华市高新园区的IT企业就达247家，实现技工贸总收入71.7亿元，同比增长60.04%，2010年1—8月，实现技工贸总收入61.57亿元，同比增长93.92%，初步形成龙头企业带动、中小企业配套的由电子产品制造业、软件与系统集成业、网络与电子商务业等构成的电子信息产业链，对专业人才需求十分庞大。      目前，我校共有IT类专业四个，其中计算机应用技术专业2000年成立，已培养8届1500余名毕业生；电子商务(技术方向)专业2001年成立，已培养7届480余名毕业生；计算机网络技术专业2005年成立，已培养3届260余名毕业生；软件技术专业2008年成立，2011年将有首届毕业生。几年来，分别与5173（金华利诚）、浙江博尚电子、浙江搜富网络、金华金色在线、印度NIIT学校、杭州恒生软件、杭州创业软件等单位共同组建订单班10个，累计培养学生400余人，在订单班企业就业的学生达220余人。  为适应电子信息产业快速发展对人才的需求，为适应新形势下高职教育改革的新要求，按照“政府主导、行业指导、企共参与、学校主体”的理念，学校与金华市经济技术开发区高新技术产业园区、浙中信息产业园、金东信息软件创业园的6家电子信息骨干企业共同成立金华高新IT学院。高新IT学院成立后，将通过政校企三方的共同努力，依据金华电子信息产业发展的特点，围绕IT行业岗位要求，由校企共建校内外实训基地，共搭科教研平台，师资互通，资源共享，通过订单方式联合进行人才培养，努力构筑形成一个“合作办学、合作育人、合作就业，合作发展”的政校企利益共同体。 （ 文 陈海荣 叶 旋）', 'http://info.jhc.cn/2012/1109/c201a1686/page.htm', NULL, '2017-05-17 14:18:42.391751');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (547, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '学校隆重举行金华高新IT学院成立挂牌仪式', '1月16日上午，学校隆重举行金华高新IT学院成立挂牌仪式。市人大常委会副主任杨守春、市政协副主席邵国强，市经委、科技局等有关部门负责人，校党委书记李秋华、顾问杜世禄、校班子成员，合作企业负责人及校职能部门、二级学院负责人、信息学院师生代表500余人参加了挂牌仪式。仪式由院长王振洪主持。      仪式上，校党委副书记、副院长蒋忠樟宣读了高新IT学院理事会、执行机构组成人员名单；有关领导向高新IT学院理事单位授牌；市政协副主席邵国强和院长王振洪向高新IT学院授牌；金华利诚信息技术有限公司和浙江博尚电子有限公司负责人向高新IT学院捐赠了奖学金。      金华利诚信息技术有限公司总经理池水明作为合作企业代表发言，他说此次成立高新IT学院，将校企合作又推向了一个新的高度，通过产学研结合，把以课堂传授知识为主的学校教育与直接获取实际经验、实践能力为主的生产、科研实践有机结合，必将使学校和更多的IT企业受益，有力推动学校与行业企业的共同发展。      市政协副主席、市经济开发区管委会副主任邵国强在致辞中表示，合作创办金华高新IT学院，是开发区贯彻实施市政府“人才强市、科技强市”战略的一项重要举措，是金职院与开发区深度合作的又一重要成果，也是高校与企业在科技创新和人才培养等方面建立的合作共同体，必将进一步深化政校企和产学研合作，促进企业技术创新和人才培养，推动开发区电子信息产业持续、健康、快速发展。     校党委书记李秋华代表学校致辞。她说，在政府的支持下，学校和金华高新技术产业园区通过建立高新IT学院架设合作平台，在金华第一次吹响了IT人才培养的集结号。这一校企共同利益体的建立，不仅充分整合了多方的优质资源，实现了学校人才培养目标与企业岗位需求的无缝对接，更为地方电子信息产业可持续发展提供了人才支撑，它的成立必将形成一个多赢的局面。李秋华希望在政府和社会各界的关心、帮助、支持下，努力把高新IT学院打造成为“政府主导、行业指导、企业参与、学校培养”的区域人才培训中心、区域认证考证中心和区域技术支持与服务中心，成为学校发展的新亮点。      市人大常委会副主任杨守春对“高新IT学院”的成立表示祝贺，他说，高新IT学院的成立完全符合政、校、企三方的共同利益。从政府角度讲，找到了通过教育来推进市电子信息支柱产业的结合点，培养出的大量电子信息类专门人才，不但有利于我市的电子信息企业，也有利于其他企业通过信息化来推进管理现代化，进而推进金华工业整体转型升级；对学校来说，从与单个企业开展“订单培养”，到与整个产业合作培养人才，探索出一种新的办学模式；对企业来说，则找到了一条提供急需人才的通道。他相信高新IT学院的成立不仅能有力地推动金华乃至全省IT教育产业的发展，还将惠及数以万计的大学生，希望它能带给校企合作一种新的启示，给求才若渴的企业一种强有力的引导，前景光明。 挂牌仪式结束后，有关领导和嘉宾参观了学校IT实训基地。 （ 文 叶旋 王伟斌 ）', 'http://info.jhc.cn/2012/1109/c201a1677/page.htm', NULL, '2017-05-17 14:18:53.491268');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (548, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院党总支召开换届选举大会', '1月19日，信息学院召开党总支换届选举大会，校党委副书记、纪委书记俞勇建、组织部负责人及信息学院领导和全体教工党员参加大会。 俞勇建在大会上指出，本次换届选举大会是在全校深入开展创先争优活动之际召开的，因此如何使总支换届工作与创先争优“育人成才先锋”的主题紧密结合，如何创新党内选举方式，努力把服务学校发展、服务社会进步、服务师生成长成才的优秀人才选拔进基层党组织领导班子，全面提升基层党组织领导班子的引领力具有重大意义。他希望信息学院党员同志以高度的政治感、责任感、使命感，在此次“公推直选”换届选举中充分发扬民主，促进信息学院党内民主建设跃上一个新台阶。 通过公布和宣读换届选举办法、党员投票、计票、审查等流程，整个换届选举工作圆满结束。 （ 文/摄影 王伟斌 ）', 'http://info.jhc.cn/2012/1109/c201a1660/page.htm', 'http://info.jhc.cn/_upload/article/images/80/9d/1da4cb85432e81eac5af01c38c81/cfd36e84-bc12-4d3c-abd4-a2e65588e15f.jpg|http://info.jhc.cn/_upload/article/images/80/9d/1da4cb85432e81eac5af01c38c81/55d89624-1928-48e9-aecc-18f44cafff62.jpg|http://info.jhc.cn/_upload/article/images/80/9d/1da4cb85432e81eac5af01c38c81/eb503c6c-e0db-464b-916e-0b2bae0d8b30.jpg|http://info.jhc.cn/_upload/article/images/80/9d/1da4cb85432e81eac5af01c38c81/3d8db4ae-a5e9-474c-8cb7-8cac7f7fe9ed.jpg|http://info.jhc.cn/_upload/article/images/80/9d/1da4cb85432e81eac5af01c38c81/9f026bbb-54dd-4cde-a94c-325e39211a1c.jpg|http://info.jhc.cn/_upload/article/images/80/9d/1da4cb85432e81eac5af01c38c81/61a6c977-1baf-4dd0-9a2b-087b4a35b702.jpg|http://info.jhc.cn/_upload/article/images/80/9d/1da4cb85432e81eac5af01c38c81/1e8509f3-0d41-4c7b-a7f7-b9929f7cdd33.jpg|http://info.jhc.cn/_upload/article/images/80/9d/1da4cb85432e81eac5af01c38c81/113ae434-f1de-4fbd-9e20-d6220cde9a80.jpg', '2017-05-17 14:18:53.526065');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (549, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '学院工作总结及新春联谊会', '院长龚永坚年终总结    各类先进受表彰    学校王振洪院长参加新春联谊会    学校余党军副书记参加新春联谊会    可爱的兔子舞    搞笑版“知心爱人”    舞蹈秀    中国功夫     幽默小品：看房    京剧脸谱', 'http://info.jhc.cn/2012/1109/c201a1640/page.htm', 'http://info.jhc.cn/_upload/article/images/d0/4e/fea614de4acdae2c06c4a0ddaadb/72d52b17-bd16-4195-bdcf-a6a313e15887.jpg|http://info.jhc.cn/_upload/article/images/d0/4e/fea614de4acdae2c06c4a0ddaadb/afd4eb3d-5f96-42aa-84a2-6351b84bc7f0.jpg|http://info.jhc.cn/_upload/article/images/d0/4e/fea614de4acdae2c06c4a0ddaadb/4c0b93cb-0ebe-4780-a1ab-902cf3295ee2.jpg|http://info.jhc.cn/_upload/article/images/d0/4e/fea614de4acdae2c06c4a0ddaadb/e24f01dd-93bf-4027-8e66-9656e37a465c.jpg|http://info.jhc.cn/_upload/article/images/d0/4e/fea614de4acdae2c06c4a0ddaadb/98c1eb3e-2f50-4430-9735-6bbd2b26126f.jpg|http://info.jhc.cn/_upload/article/images/d0/4e/fea614de4acdae2c06c4a0ddaadb/bb699ebf-6c85-483e-a494-bc22880ffe1c.jpg|http://info.jhc.cn/_upload/article/images/d0/4e/fea614de4acdae2c06c4a0ddaadb/7e01af8a-42a0-4513-a610-794839e507dd.jpg|http://info.jhc.cn/_upload/article/images/d0/4e/fea614de4acdae2c06c4a0ddaadb/b194bdb9-de57-4d30-a9b9-722fc4ae8dc1.jpg|http://info.jhc.cn/_upload/article/images/d0/4e/fea614de4acdae2c06c4a0ddaadb/3e3648fb-e8dc-4e3c-b797-56f066271c8a.jpg|http://info.jhc.cn/_upload/article/images/d0/4e/fea614de4acdae2c06c4a0ddaadb/881b60f1-2f38-44ba-878a-b43bdc0db7af.jpg', '2017-05-17 14:18:53.547938');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (550, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '学校有了新学院 专为企业“造”人才', '新闻链接：http://www.36tv.cn/viewnews-111371', 'http://info.jhc.cn/2012/1109/c201a1635/page.htm', 'http://info.jhc.cn/_upload/article/images/1d/be/06581bde4a2aadf810605011cd50/b2d9a1a0-442d-421c-b286-9fea09fb2915.jpg', '2017-05-17 14:18:53.649671');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (551, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学子为民家电维修服务暖人心', '为进一步深化创先争优活动，充分发挥学院党组织的战斗堡垒作用和党员的先锋模范作用，信息学院党总支寒假期间组织开展了“送温暖”惠民大行动活动，其中携科技服务村民，用实践铸就真知——党员为民维修服务等活动把党的关怀和温暖送到基层，取得了很好的反响，真正使群众得到实惠。 为民维修服务活动中，学院家电维修协会及番茄电脑社的40多名学生党员在教师党员带领下，来到金华寺前皇社区，为居民们开展义务服务。维修损坏的家电、电脑常见故障的日常维护、日常家用电器的保养和维修常识宣传、电脑配件品牌的防假常见方法讲解等一项项活动内容受到了居民们的热烈欢迎，大家咨询这个咨询那个，同学们一个个俨然都成了“香饽饽”。对社区内行动不便的人员同学们还进行上门服务，让他们感受到冬日里的温暖。活动中队员们还以展板、海报、横幅、宣传单等多种形式进行宣传，并制作了相关的资料、卡片，分发给社区居民。 在“送温暖”惠民大行动中，信息学院党总支还开展了科技服务企业、关爱困难学生和走访困难老党员等活动。 （ 文 王伟斌 ）', 'http://info.jhc.cn/2012/1109/c201a1630/page.htm', NULL, '2017-05-17 14:18:53.681391');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (552, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '加强和改进学风建设 提升学生干部自身素质', '2月25日，信息学院召开第八届学生委员会第一次全委会议。学工办、团委负责人等等出席会议。 会上，学工办主任吴晓明老师围绕“学风建设”提出了一系列学生干部工作学习生活上的管理、考核、奖惩制度。他指出，学风建设是校园的“主旋律”，学生干部是学生中的佼佼者，理应成为学风建设的带头人和排头兵，希望学生干部能提高自身修养素质，推动和促进学院的学风建设。他说，学生干部工作上要有计划，工作做到位，要有始有终，端正思想以身作则。团委书记金乐老师对过去一年学生干部的工作进行了评价，并对新上任的学生干部寄予了新的期望。 本次会议使学生干部对今后一段时间的工作有了一个更系统的规划，这对促进我院学生组织发展有着重大意义。 （ 文 汪铭铠 ）', 'http://info.jhc.cn/2012/1109/c201a1626/page.htm', NULL, '2017-05-17 14:18:53.714613');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (553, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院开展新学期公寓卫生安全检查', '2月24日，信息学院学工办会同公寓管理委员会成员对我院学生宿舍进行了集中的安全卫生大检查。 　　从检查结果来看，宿舍安全卫生状况基本良好，但仍然存在一些问题，如个别宿舍违章使用电饭锅、热得快；多数男生洗手间脏乱；充电器充电时室内无人、多数男生寝室阳台物品（球鞋、纸箱、饮料瓶等）摆放杂乱等一些不安全、不卫生现象等。检查人员当场要求不合格寝室进行整改并记录在案。 据悉，本次大检查前学院各班级各寝室已经进行了集中的卫生大扫除。本次卫生大检查结束后，学工办还将依托公寓管理委员会和各学生干部的力量，不定期的对公寓进行卫生安全检查。 （ 文 汪铭铠 ）', 'http://info.jhc.cn/2012/1109/c201a1623/page.htm', NULL, '2017-05-17 14:18:53.737029');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (572, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院承办的全国计算机等级考试顺利举行', '近日，由信息学院承办的全国计算机等级考试金华职业技术学院考点工作顺利结束，省教育考试院领导王强、吕美卿与市自考办骆一伟主任等巡视了考点考务工作。 此次全国计算机等级考试金华职业技术学院考点在报名、发放准考证和等级证书等方面努力提升服务意识，考前对本次考试的各项工作做了周密布置，信息学院主要领导担任主考工作，重点在试卷保密、考点布置、考点封闭管理、监考教师考前培训、考风考纪等关键环节下功夫，确保了考试的顺利进行。本次考试共有1274人报考，覆盖全国计算机等级考试的一级、二级、三级和四级。 （文 单天德 ）', 'http://info.jhc.cn/2012/1109/c201a1509/page.htm', NULL, '2017-05-17 14:19:06.641106');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (554, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院努力构建安全稳定公寓环境', '构建安全稳定的公寓环境是保证学生学习生活的基础，为保证开学初学院公寓各项工作的稳定运行，信息学院由分管领导负责，深入各个寝室落实安全工作，通过突击检查与日常监督相结合，学生干部与辅导员相配合，构建了公寓管理的长效机制。 学院一开学就进行公寓安全卫生大检查，消除了存在的一些消防、财产安全隐患；3月1日，又召开“消除安全隐患，构建和谐公寓”为主题的寝室长会议，结合近期学校安全教育月活动内容，对开学以来公寓安全检查情况进行总结。在日常工作中，学院公寓管理委员会还以海报、宣传资料等载体，通过生动的案例分析，引导同学们增强“防火、防盗、防骗”意识，提高预防与应对突发事件的能力，为构建安全稳定的学院公寓环境打下了基础。 （ 文  陈静泓 ）', 'http://info.jhc.cn/2012/1109/c201a1619/page.htm', NULL, '2017-05-17 14:18:53.759193');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (555, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院多措并举扎实推进学风建设', '为扎实推进学院学风建设，营造良好的学习氛围，新学期信息学院制定了《信息工程学院2011“学风建设年”实施方案》，以期通过创新机制，完善措施，积极动员全体师生扎实推进学院学风建设。 本学期开学以来，学院结合学风建设整体情况，从考风考纪、课堂纪律、自习情况等各方面分析了当前存在的问题，明确了本年度各阶段的工作任务。学院还通过班主任、辅导员、学生干部等各个层面的动员大会，贯彻“以学风建设为主线，以公寓管理为重点，以学生干部队伍建设为基础”的工作机制，明确抓教风促学风，抓管理保学风，抓考风正学风，抓典型树学风，抓纪律导学风，通过这一系列举措充分激发学生学习的自觉性与主动性，营造良好的学习氛围，确保学风建设工作取得实绩。 （ 文 汪铭铠 陈静泓 ）', 'http://info.jhc.cn/2012/1109/c201a1615/page.htm', NULL, '2017-05-17 14:18:53.781324');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (556, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院努力提升企业师资队伍教育教学能力', '信息学院自组建“金华高新IT学院”以来，与高新园区企业的师资队伍建设合作取得了新成绩。3月6日，为提升企业教师的教育教学能力，学院举行了企业教师培训会。 副院长陈海荣首先对企业教师对于学院教育教学发展做出的贡献表示感谢，他结合学院对教师业务能力的要求，从教学工作基本规范、教学常规、教学环节质量标准等方面对企业教师进行了较为系统的培训，他希望企业教师加强教学过程的管理，因材施教，发挥自身企业实践经验丰富的特点，将企业案例带到课堂，有效开展项目化教学，与学院共同推动学院专业建设，促进校企合作。 与会人员还围绕课堂教学的组织、教学方法与艺术、教学实效等主题展开了热烈的讨论。 （ 文 陈静泓 ）', 'http://info.jhc.cn/2012/1109/c201a1611/page.htm', NULL, '2017-05-17 14:18:53.803785');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (557, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院举办首届“女生节”', '3月7日，在第101个国际劳动妇女节到来之际，信息学院在全院女生中举办了以“魅力女生，活力无限”为主题的第一届女生节。“女生节”以提高女生礼仪文化知识、提升女生维权意识为目的，旨在营造积极向上、清新高雅、健康文明的校园文化氛围。 据悉，此次女生节活动包括“我型我绣”十字绣比赛、“靓丽风景线”化妆知识培训等5个项目，丰富多彩的活动丰富了学院女大学生的精神文化生活，使女生成为学院一道靓丽的风景线，也成为学院校园文化建设的一个创新之举。 （ 文 汪铭铠 ）', 'http://info.jhc.cn/2012/1109/c201a1607/page.htm', NULL, '2017-05-17 14:18:53.826085');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (558, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院学生党员争做“闪光言行”的践行者', '开展创先争优活动的目的，是要调动广大党员的积极性和创造性，争当优秀共产党员，信息学院学生党员和学生干部在全校开展的“创先争优闪光言行”活动中积极行动，形成了你追我赶、争当先进的喜人局面。 日前，学院全体学生干部主动在寝室门上悬挂“学生干部展示牌”，将自己的信息展示在全院学生面前，主动接受同学监督，充分发挥“一名学生干部，一面先锋旗帜”的模范带头作用，并以自己日常生活中的实际行动，带动和培养学院公寓良好的学习、生活氛围，培育先进公寓文化。此外，学生党员和学生干部们主动承诺，平时上课提前5分钟进教室，不迟到不早退，到实训室上课穿实训服，为学院“学风建设年”活动做好表率。 学生党员和干部们的自我展示活动，在全院同学中产生了强烈反响，大家纷纷表示要从我做起，从小事做起，为构建学院良好的学风、院风而共同努力。 （ 文 汪铭铠 王伟斌 ）', 'http://info.jhc.cn/2012/1109/c201a1603/page.htm', NULL, '2017-05-17 14:18:53.848082');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (559, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '强化制度，狠抓落实，推动学院科研工作上新台阶', '科研工作是提高教学质量，提升教师业务水平的重要载体，新学期伊始，信息学院针对目前学院科研现状及学校科研发展规划，为进一步激活机制，对学院现有的科技管理制度进行了修订，增补了科研团队管理办法、高层次科研奖励政策、学院预研项目资助政策等6个管理制度，旨在通过强化制度，推动学院科研工作上新台阶。 其中，科研团队建设是学院今年科研工作的亮点，学院将以各专业为依托，促进科研团队建设、鼓励教师积极申报厅局级以上课题，同时强化责任、加强督促检查，让每一位教师在“有为”中达到“有位”。 （ 文 俞冬丽 ）', 'http://info.jhc.cn/2012/1109/c201a1598/page.htm', NULL, '2017-05-17 14:18:53.870456');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (560, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '民盟金职院支部被评为2010年度金华市先进盟组织', '日前，民盟金华市委下文表彰2009-2010年度金华市先进盟组织（盟金字[2011]2号），我校民盟金职院支部因成绩突出，获“金华市先进盟组织”称号。 在过去2010年，民盟金职院支部在以信息学院副院长王成福为主委的新班子领导下，全体盟员共同努力，认真学习实践科学发展观，重视组织发展与建设，积极开展支部活动，为民盟事业做出了积极贡献。此次获先进盟组织称号，极大了鼓舞了支部盟员，大家表示，要继续保持高昂的斗志和饱满的热情，以高度的政治责任感和使命感，进一步加强自身建设，做好本职工作，提高参政议政能力，发挥民盟的特色和优势，为民盟事业再谱新篇章。 （ 文 王伟斌 ）', 'http://info.jhc.cn/2012/1109/c201a1594/page.htm', NULL, '2017-05-17 14:18:53.892487');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (561, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '民盟金职院支部被评为2010年参政议政市级先进基层组织', '在校“双代会”召开前夕，从民盟金华市委再传好消息，民盟金职院支部继被评为“2010年度金华市先进盟组织” （盟金字[2011]2号）后，又被评为2010年度参政议政市级先进基层组织（盟金字[2011]4号），这是对我校民盟工作的充分肯定。 由信息学院王成福副院长担任主委的民盟金职院支部充分发挥盟员特长，调动盟员积极性，紧紧围绕市政府和学校的中心工作以及社会热点、难点问题，积极撰写参政议政提案和信息报送工作，为民盟市委会参政工作和学校发展作出了积极贡献。在荣誉面前，支部号召全体盟员在新的一年中，继续加强学习，进一步增强责任感和使命感，提高参政能力和水平，为我市经济社会平稳较快发展和学校的快速发展做出新贡献。 （ 文  王伟斌 ）', 'http://info.jhc.cn/2012/1109/c201a1590/page.htm', NULL, '2017-05-17 14:19:06.376821');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (562, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院首届“女生节”活动精彩纷呈', '信息学院首届“女生节”活动精彩纷呈 发布人：管理员资讯来源：信息管理中心 发布时间：2011-03-15 14:49:31点击数量:1     信息学院首届“女生节”各项活动日前陆续开展，成为学院一道靓丽的女生风景线。 9日，“缘”来是你送生日祝福活动，使当天生日的女生得到了学院一份特殊的生日祝福，洋溢在领到生日小礼物的女生脸上的笑容，让大家看到了学院对同学们暖暖情怀。 11日的“靓丽风景线”化妆知识培训活动，吸引了众多爱美的女生，资深美容讲师的生动讲解，让女生们认识到，美，是需要技巧的；薄施黛粉，不失雅致，是要追求境界的；如何化妆，如何化好妆，是需要学习的；美，也是需要交流的。相信这样的活动，会让学院的女生们懂得在不同场合、不同环境下，如何让自己绽放的更加美丽。 （ 文 汪铭铠 学院公寓管理委员会 ）', 'http://info.jhc.cn/2012/1109/c201a1582/page.htm', 'http://info.jhc.cn/_upload/article/images/dd/c7/24db36e5430c9ef4146797c0303d/cdca405e-a3e5-4043-9b2f-afff130b3a57.jpg|http://info.jhc.cn/_upload/article/images/dd/c7/24db36e5430c9ef4146797c0303d/ca90b8be-3773-48db-bdd7-96ac93a7f98d.jpg', '2017-05-17 14:19:06.418279');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (563, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院举行计算机类专业实施CDIO教学改革研讨会', '3月16日下午，信息学院举行计算机类专业实施CDIO教学改革动员暨研讨会，北京交通大学教授、联合国教科文组织产学合作教席主持人�私ㄖ薪淌谟ρ�参加，学校领导胡野副院长出席并作动员报告。研讨会由信息学院院长龚永坚主持。 胡野在动员时说，国家示范院校建设中，学校的专业建设和人才培养能力显著提升，为我们进一步推进校企合作，强化工学结合奠定了基础。信息学院作为国家示范专业建设主力军，有责任、有义务在后示范建设中做更多探索。此次在计算机专业群中全面推行CDIO教学改革，是适应地方产业发展、推动专业内涵提升的重要举措。他要求教师们要加强CDIO理念学习，了解CDIO模式的内涵、外延；坚定信念，努力践行；循序渐进，制定完整、可行、科学的实施方案；建立长效机制，切实解决专业建设的难点、重点和瓶颈问题。他希望大家真抓实干，为推动全校专业改革做好试点，做出积极贡献。 研讨会上，围绕软件技术专业CDIO教学改革一年实践经验，信息学院教师与查建中教授就计算机专业引进CDIO教学模式进行了互动交流。工程师职场环境设置、集成化课程体系设计、实践课程设计制作、学生主动学习教学法的实施、CDIO项目评估、教师CDIO能力培养等成为研讨会上反复讨论的内容，查教授抛出的学习进展曲线、动态项目管理、职业能力引导课程设置等则让大家领会了CDIO理论的具体实践模式，也进一步理解了做中学的方法论。整个研讨会气氛活跃，讨论激烈，充分展示了信息学院教师开展教学改革的积极性，相信此次动员会对加强学院计算机专业内涵建设，提升人才培养质量和教师系统认识CDIO工程教育模式，提高教学改革的行动力产生积极影响。 （ 文 王伟斌 陈静泓 ）', 'http://info.jhc.cn/2012/1109/c201a1570/page.htm', 'http://info.jhc.cn/_upload/article/images/01/57/e03fa3eb4dc59f26f980cab26418/0d1f4bea-e13f-4ed9-ab47-2432e69aa34e.jpg|http://info.jhc.cn/_upload/article/images/01/57/e03fa3eb4dc59f26f980cab26418/88fc0690-fbfb-46df-8cad-5dcacf15fac4.jpg|http://info.jhc.cn/_upload/article/images/01/57/e03fa3eb4dc59f26f980cab26418/7d23cf32-f0d3-4064-8ca7-27c5fa7482a5.jpg|http://info.jhc.cn/_upload/article/images/01/57/e03fa3eb4dc59f26f980cab26418/031a60d9-d417-4353-af79-ec1502a2035a.jpg|http://info.jhc.cn/_upload/article/images/01/57/e03fa3eb4dc59f26f980cab26418/dc36c83c-ea99-47e0-a92b-9f29fb0bed7c.jpg|http://info.jhc.cn/_upload/article/images/01/57/e03fa3eb4dc59f26f980cab26418/fead9000-1b9e-4344-a8b9-7e66326b9a73.jpg', '2017-05-17 14:19:06.44053');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (564, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院电子产品开发部组织学生技能月赛', '3月18日，在信息学院电子产品开发部指导下，学生概念公司和电子设计协会学生经过一个月策划、组织的学生技能月赛在东实训楼如期举行。 本次竞赛旨在提高学生创新能力和电子专业技能，设“电子技术应用”和“单片机技术应用”两个专项，大一学生参加“电子技术应用”专项月赛，大二学生参加“单片机技术应用”专项月赛。经过激烈角逐，应电1001班夏鲁杰同学和应电092班柴罗琳同学分获大一组和大二组月赛一等奖。电子产品开发部将以此为平台，努力提高学生专业比赛技能，提升专业素养和水平。 （ 文 余红娟 林洁 ）', 'http://info.jhc.cn/2012/1109/c201a1566/page.htm', NULL, '2017-05-17 14:19:06.462729');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (565, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院2010年继教教育工作受表彰', '近日，金华市高等教育自学考试办公室授予信息学院“金华市二��一��年度优秀自学考试考点”荣誉称号。这是信息学院继续教育工作在全日制成教招生、教学管理、全国计算机等级考试辅导和考务等方面，创新发展，扎实工作取得的可喜成绩，使信息学院取得了社会效益和经济效益双丰收，也是继被学校评为“二��一��年度成人教育优胜单位”后的又一殊荣。 2011年，信息学院继续教育工作将总结经验，进一步完善招生宣传机制、加强日常教学管理、提升学生培训考证和社会培训工作力度，全员动员，力争新的一年成教工作取得新发展、实现新跨越。 （ 文 单天德 ）', 'http://info.jhc.cn/2012/1109/c201a1561/page.htm', NULL, '2017-05-17 14:19:06.484932');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (566, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院学生党员先锋队在行动', '3月20日，由信息学院学生党员、学生干部等组成的学生党员先锋队一行二十余人赴金华市安地镇蒋里村进行了一次义务服务活动，活动内容包括：免费家电维修、卫生打扫、招生宣传等，受到了当地村民的热烈欢迎和一致好评。 此次活动在学以致用、回报社会的基础上进一步提升活动内涵，努力把学生党员创先争优、励志模范带头作用落到实处，并为学院的招生宣传工作提供了良好渠道。据悉，信息院学生党员先锋队活动将持续开展，做到“周周进社区、月月进村镇”。 （ 文 金乐  钱靖 ）', 'http://info.jhc.cn/2012/1109/c201a1552/page.htm', 'http://info.jhc.cn/_upload/article/images/c8/10/315ff6ac4dccac623ab0473053aa/60b515a8-5b0e-4cfd-a7d9-8546f0da26f3.jpg|http://info.jhc.cn/_upload/article/images/c8/10/315ff6ac4dccac623ab0473053aa/dc987333-8636-432b-9d4a-0cb383fee31d.jpg|http://info.jhc.cn/_upload/article/images/c8/10/315ff6ac4dccac623ab0473053aa/cd456793-b8a2-4355-8627-fd8bc339bb86.jpg|http://info.jhc.cn/_upload/article/images/c8/10/315ff6ac4dccac623ab0473053aa/8839a63c-d1b9-4b40-9f65-c98aafc5551a.jpg', '2017-05-17 14:19:06.507202');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (573, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院师生党员清明节寻访红色遗迹', '4月1日，信息学院团委组织部分师生党员参加了清明节“寻访红色遗址胜迹，踏寻革命先辈足迹，认知家乡革命历史，继承光荣革命传统”为主题的缅怀先烈活动，先后寻访参观了周恩来1939年春在金华演讲旧址、太平天国侍王府、抗日团体活动中心旧址、邵飘萍铜像等红色遗址胜迹。 寻访中，党员们踏着革命先辈的足迹，走访老红军，深入社会调查，了解家乡的革命史，感受党的光辉历程，学习革命先辈的优秀品质，寻找和谐之根、民主之根。许多同学在参观革命遗址后说，我们一定要更好的继承光荣的革命传统，树立远大理想、坚定报国信念、自觉接受爱国教育、革命传统教育和民族教育，为建设一个强大的国家奉献自己的力量。 （ 文 金乐 汪铭铠 ）', 'http://info.jhc.cn/2012/1109/c201a1503/page.htm', 'http://info.jhc.cn/_upload/article/images/bc/da/043bc6c44550a48d3adfd925b79d/c6d9f1e6-c735-4b67-8bde-5de3df5a6334.jpg|http://info.jhc.cn/_upload/article/images/bc/da/043bc6c44550a48d3adfd925b79d/4f44ef74-b67d-4886-a290-5433d9765fa3.jpg', '2017-05-17 14:19:06.662824');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (567, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院以统一着装推进学院职业文化建设', '整齐划一的工作服装，稳重大方的职业形象，近段时间成为信息学院东实训楼各实训室一道靓丽的风景线。这是信息学院为规范实训环境管理，深化职业文化建设，在全院师生及外来参观人员中正积极推进的进入实训室统一着装制度。 据悉，为推进和深化此项制度，学院前期作了大量工作。购置工作服阶段，根据电子、计算机两大专业群的工作性质与环境，综合学生意愿，学院仔细选择工作服款式，明确采购方式，还对贫困生购置服装给与相应补助，保证每位学生有工作服；着装规范阶段，学院通过院领导、相关部门、任课教师等各个层面的监督检查工作，让学生养成自觉着装意识。学院希望通过统一着装制度建设，使学生树立职业形象、提升职业文化意识，以此逐步营造良好的职业环境，促进自身职业素质养成，推动学院人才培养质量的提升。 （ 文 陈静泓 汪铭铠 ）', 'http://info.jhc.cn/2012/1109/c201a1545/page.htm', 'http://info.jhc.cn/_upload/article/images/49/f8/b77a76bb4ec88ead859f125e3158/5a90cb7f-f8ff-4de6-b434-53eedab0968d.jpg|http://info.jhc.cn/_upload/article/images/49/f8/b77a76bb4ec88ead859f125e3158/60eacb58-eb34-4060-9b37-42d5dda97a12.jpg|http://info.jhc.cn/_upload/article/images/49/f8/b77a76bb4ec88ead859f125e3158/adab1c70-e08b-42e2-a828-8201d572ec1d.jpg|http://info.jhc.cn/_upload/article/images/49/f8/b77a76bb4ec88ead859f125e3158/d4072549-5419-4738-925b-222aa8843949.jpg', '2017-05-17 14:19:06.529402');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (568, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院党校第十四期入党积极分子培训班举行结业典礼', '3月23日，信息学院党校第十四期入党积极分子培训班举行结业典礼，学院党校校务委员会成员及党校第十四期培训班全体学员等参加典礼。 在为期6个月的学习中，学习班241名学员通过理论授课、社会公益活动、优秀共产党员影片欣赏、寒假社会实践、结业论文撰写、结业考试等各个环节的教学与考核任务，有192名学员顺利结业，其中计算机1001班俞淑娃等12名同学还被授予“优秀学员”荣誉称号。 结业仪式上，学院党校校长、党总支书记朱登胜对获得“优秀学员”称号和顺利结业的学员表示衷心的祝贺，并希望同学们要树立为人民服务的意识，勇于承担，乐于奉献，保持思想的先进性，在当前全校开展的创先争优争先进的浓郁氛围中，发挥先锋模范作用，努力学习科学文化知识，学成学精，成就自己，报效祖国。 （ 文 周荃 ）', 'http://info.jhc.cn/2012/1109/c201a1537/page.htm', 'http://info.jhc.cn/_upload/article/images/60/a2/73b2067b4357a946b88146452c8f/9e7691a9-e4ce-447f-ad13-4628ef26d5cc.jpg|http://info.jhc.cn/_upload/article/images/60/a2/73b2067b4357a946b88146452c8f/d96ab0b6-3e47-4af5-a62b-e7a4212b149d.jpg', '2017-05-17 14:19:06.55167');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (569, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院召开应用电子技术专业指导委员会研讨会', '信息学院召开应用电子技术专业指导委员会研讨会 发布人：管理员资讯来源：信息管理中心 发布时间：2011-03-28 14:43:41点击数量:1      3月26日，信息学院召开应用电子技术专业指导委员会会议。来自浙江求是教仪有限公司、浙江博尚电子有限公司、金华市灵声电子有限公司等合作企业及浙师大的专家教授应邀参加。会议由副院长王成福主持。 针对目前应用电子技术专业人才培养方案的实施效果与存在的问题，专家们结合人才培养方案对需要编程的课程如何体现能力递进，课程之间的台阶如何进行分步与序化、如何针对产业行业发展来开设就业方向课程、如何建设应用电子技术专业特色和教学资源库架构等主题展开了热烈讨论。专家们针对人才培养方案的改革历程与成效，有的放矢地指出问题，并站在企业需求与专业发展的角度献计献策，对应用电子技术专业2011级人才培养方案的修订工作也给予了积极指导。 （ 文  余红娟 范灵芝 ）', 'http://info.jhc.cn/2012/1109/c201a1529/page.htm', 'http://info.jhc.cn/_upload/article/images/ab/be/147568844693aa00bad790a9519f/6378aada-f7f5-4e30-a5bd-82a5230171eb.jpg|http://info.jhc.cn/_upload/article/images/ab/be/147568844693aa00bad790a9519f/7fdf7c25-5f33-4391-bf42-8bff21d2952d.jpg', '2017-05-17 14:19:06.573889');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (570, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院学生党员先锋服务队走进江滨社区', '信息学院学生党员先锋服务队自开展进社区，走农村活动以来，在社会上引起了非常好地反响。3月27日，服务队一行30余人又来到市区江滨社区，为居民开展志愿者服务，吸引了大批居民前来咨询。 此次活动在原有的免费家电维修、卫生打扫、招生宣传等活动内容的基础上，又针对当前社会热点，增加了文明网络宣传，以此引导社区居民树立正确的上网观。活动现场，有的居民拿来许多家里坏了的电器来修，有的居民则咨询上网知识，丰富多彩的活动展示了信息学子全方位的才能，达到了学以致用、服务社会的目的，一些文艺骨干还给居民们进行了文艺表演。 （ 文 金乐 ）', 'http://info.jhc.cn/2012/1109/c201a1517/page.htm', 'http://info.jhc.cn/_upload/article/images/ef/d8/1e6449674e8fb3a19875b8b8916c/35fca5a5-6ab1-42e6-b60c-c5ac670556c6.jpg|http://info.jhc.cn/_upload/article/images/ef/d8/1e6449674e8fb3a19875b8b8916c/7797f067-147e-4bb7-a143-20596e49c178.jpg|http://info.jhc.cn/_upload/article/images/ef/d8/1e6449674e8fb3a19875b8b8916c/aad3624e-067d-4236-b791-7dc810892d80.jpg|http://info.jhc.cn/_upload/article/images/ef/d8/1e6449674e8fb3a19875b8b8916c/8182b35e-c68d-4e31-aae8-c9769cfc9446.jpg|http://info.jhc.cn/_upload/article/images/ef/d8/1e6449674e8fb3a19875b8b8916c/60ae0d94-056a-4a36-8d21-b7e1ba0250e2.jpg', '2017-05-17 14:19:06.596093');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (571, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院多措并举力促科教研工作可持续发展', '日前，信息学院完成了对全院教师近五年科研成果分析和今后三年科研申报计划的调查摸底工作，基本掌握了学院科研现状、存在的问题及今后发展方向。根据调查结果，学院在疏理国家、省、市有关政策和申报渠道的基础上，结合市级重点学科发展方向和学院9个专业特点，提出了20余个指导性研究方向，制定了学院三年科研发展目标和年度实施计划。 据悉，为实现学院科研工作的突破和可持续发展，学院还积极研究破解科研工作的对策，力求通过组建科研团队、加强项目主持人培养、修订相关激励制度、加强申报指导和过程管理等工作，营造学院教师积极参与科教研工作的氛围，目前在省级课题立项和国家教科规课题申报等方面已初见成效，进一步激发了教师的科研热情。目前，全院上下正努力以科研工作带动学院专业内涵建设，提升人才培养质量，开创“后示范”时期科研工作新局面。 （文 王成福 ）', 'http://info.jhc.cn/2012/1109/c201a1513/page.htm', NULL, '2017-05-17 14:19:06.618359');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (574, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院启动“学风建设年”系列活动', '近日，以促进学院学风建设为目的的信息学院“学风建设年”活动拉开帷幕，在启动仪式上，学院2009级、2010级一千多名学生参加集体签名活动，并发出了“创建优良学风，从我做起”的倡议，号召学院全体同学在大学期间切实做到学以致用、奋发有为。 据悉，信息学院学风建设年活动将陆续开展学风建设辩论赛、学风建设主题征文、学生技能比武、师德师风报告会、先进集体和个人展评等系列活动，旨在通过这些活动，激发广大同学的求知欲望，激励广大同学秉承“知行合一务实创新”的校训，勤奋守纪、勤学好问，勇于探索新知识，做一个合格的高素质高职人才。 （文 汪铭铠 ）', 'http://info.jhc.cn/2012/1109/c201a1498/page.htm', 'http://info.jhc.cn/_upload/article/images/f5/68/6861273b4de793f19f34e199927a/ce59a612-d723-4504-a67f-1cf95360749b.jpg', '2017-05-17 14:19:06.685017');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (575, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院团委组织“我为青春献热血”无偿献血活动', '4月7日，信息学院团委携手市中心血站开展了一次"我为青春献热血"无偿献血活动。活动中，同学们的表现积极踊跃，在短短4个小时的献血服务中，共有60多名同学参加此次献血活动，献血量达15000余毫升。 在党员和学生干部的带领下，献血现场踊跃而有序。献完血后，许多同学表示，献血活动很有意义，以后还会参与这样的活动，义务献血也是我们义不容辞的责任！同学们的献血热情受到了市中心血站工作人员的高度赞扬，他们说在当前血源相对紧张的情况下，同学们的献血为社会做出了积极贡献，体现了一名当代大学生的风采。 （ 文 金乐 ）', 'http://info.jhc.cn/2012/1109/c201a1491/page.htm', 'http://info.jhc.cn/_upload/article/images/57/57/3ae2a4e94df1bd9fb6fd551b6eb7/de4a4057-0c23-4112-a31a-6e1db02ddfee.jpg|http://info.jhc.cn/_upload/article/images/57/57/3ae2a4e94df1bd9fb6fd551b6eb7/b51f114c-96b3-46f5-812a-ede6b942fdcf.jpg|http://info.jhc.cn/_upload/article/images/57/57/3ae2a4e94df1bd9fb6fd551b6eb7/1d208a68-2a6f-429b-bf80-491a156d7d2d.jpg', '2017-05-17 14:19:18.41989');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (576, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院与杭州电子科技大学高起本项目受关注', '4月7日—11日，杭州电子科技大学成人教育学院举行2010年函授工作总结会，我校信息学院与杭州电子科技大学的高起本合作项目在全省首开先河，在成人本科学历教育中优势突出，获得杭州电子科技大学成人教育学院颁发的“开拓创新奖”，这是信息学院成人教育在与高校合作办学中的一项突出成绩。 信息学院继教部负责人单天德老师就高起本项目在总结会上作了专题介绍，得到了与会代表的普遍赞同，其他几所高职院校也表示在2011年将试点开展该项目的合作。信息学院还就函授教育教学、招生、宣传等工作在会上与其他兄弟院校同仁进行了经验交流。 （ 文 单天德 ）', 'http://info.jhc.cn/2012/1109/c201a1486/page.htm', 'http://info.jhc.cn/_upload/article/images/62/ac/4c70265f4445a1e60d9c70ed7a20/a5d2d075-04d7-44b6-ba83-2a62fc3c9c38.jpg', '2017-05-17 14:19:18.463457');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (577, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院强化顶岗实习过程管理', '为及时掌握毕业生实习动态，提高顶岗实习质量和实效，切实抓好顶岗实习过程管理，3月29日至4月10日，信息学院组织部分教师分组赴全省各地对2011届毕业生顶岗实习情况进行了现场检查指导。 检查过程中，教师们深入学生所在的企事业单位，仔细询问大家在工作过程中的岗位情况、遇到的困难及日常生活情况，并叮嘱同学们完成岗位任务的同时，认真做好毕业设计等学习任务。检查中，教师们还通过与实习单位领导及指导教师的交流访谈，全面了解学生的实习表现、工作态度、工作能力等情况，并征询实习单位用人需求及对人才培养的意见建议，以提高学院人才培养质量，提升学生就业竞争力。 顶岗实习检查是信息学院完善顶岗实习质量评价机制的一项重要举措，得到了毕业生和实习单位的普遍认可，毕业生感受到了学院对他们的重视和关心，实习单位则对我院教师现场检查工作在加强与它们沟通方面的积极作用给予了充分肯定，相信此项工作在将顶岗实习转化为学生就业通道，帮助毕业生顺利就业方面发挥重要作用。 （ 文 陈静泓 ）', 'http://info.jhc.cn/2012/1109/c201a1477/page.htm', 'http://info.jhc.cn/_upload/article/images/7e/95/abf34e80428695f3e8eaf02779cd/0b815f5f-d902-446a-a6ef-8998c0561cfa.jpg|http://info.jhc.cn/_upload/article/images/7e/95/abf34e80428695f3e8eaf02779cd/d5286de7-9cc7-4222-8203-f83b148daf7c.jpg|http://info.jhc.cn/_upload/article/images/7e/95/abf34e80428695f3e8eaf02779cd/d4a5c0fe-6827-42f0-bae4-237a10f6f2b9.jpg', '2017-05-17 14:19:18.48594');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (578, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院召开教师节座谈会', '9月10日是第26个教师节，信息学院召开教师节座谈会，新教师、部分老教师参加座谈。学院党总支书记朱登胜主持座谈会。 座谈会上，朱登胜首先代表学院向全体教职工表示节日的问候和祝福。他说，召开座谈会，就是希望听取大家的意见和建议，以便学院更好的为大家服务。 参加座谈的老师们踊跃发言。有的老师发表了自己对作为一名教师的理解和认识，有的则谈了自己对教书育人的看法；也有老师谈了目前的实际教学情况，如对目前排课的合理性发表看法，对实验室管理方式的个人理解；辅导员们则谈了自己对学生管理工作的认识。 朱登胜在最后总结发言时说，每位老师都有自己的教师节体验，但一个共同点是，大家都十分珍惜作为一名人民教师的荣誉感和责任感，都体会到了学生对自己的爱，希望每位老师都能牢记自己作为一名教师的使命，在当前正在开展的创新争优活动中，践行教书育人，管理育人，服务育人，做一名优秀的人民教师。对于大家提出的问题，学院将会认真对待，积极研究对策，努力为大家创造一个和谐的工作环境。 （ 文 / 摄影  王伟斌 ）', 'http://info.jhc.cn/2012/1109/c201a1469/page.htm', 'http://info.jhc.cn/_upload/article/images/ef/b1/03fda7c54861a063b0594ad60599/91dc89f8-8c18-44b9-b330-556b463f5db2.jpg|http://info.jhc.cn/_upload/article/images/ef/b1/03fda7c54861a063b0594ad60599/70bce47c-6b73-415c-9a31-75104ec47084.jpg|http://info.jhc.cn/_upload/article/images/ef/b1/03fda7c54861a063b0594ad60599/a9ab7cf1-6094-4e35-be49-9f7f74b88273.jpg|http://info.jhc.cn/_upload/article/images/ef/b1/03fda7c54861a063b0594ad60599/edc12bf8-c02d-4e65-a059-5d53385d7ee4.jpg|http://info.jhc.cn/_upload/article/images/ef/b1/03fda7c54861a063b0594ad60599/a529eb93-9322-49d8-a5ce-85aeb827be72.jpg', '2017-05-17 14:19:18.508006');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (589, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院2010年党建工作成效显著受表彰', '4月29日，从学校召开的2010年度党建思政宣传工作总结会上传来好消息，信息学院“为民维修服务队”被评为2010年度市直机关党建工作品牌，学院应用电子技术专业学生党支部被评为市“学习型党组织”，这些成绩是对信息学院2010年党建工作的充分肯定。 2010年，信息学院党建工作以邓小平理论和“三个代表”重要思想为指导，深入贯彻落实科学发展观，紧紧围绕学院中心工作，以科学发展观为统领，全面提升党建工作科学化水平，为学院发展提供了坚强的组织保障。2011年，学院党建工作将扎实推进创先争优活动，深入抓好党员教育培训工作，为学院“十二五”规划顺利开局贡献力量，争取党建工作取得更大成绩。 （ 文 王伟斌 ）', 'http://info.jhc.cn/2012/1109/c201a1385/page.htm', NULL, '2017-05-17 14:19:34.02241');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (579, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院扎实推进计算机专业群CDIO改革', '4月14日，信息学院结合软件技术专业细化和落实CDIO改革教研活动，就进一步推进计算机专业群CDIO改革工作进行研讨。院长龚永坚、党总支书记朱登胜及相关部门负责人参加。 研讨会上，软件技术专业教师就CDIO学习月活动安排、课程开展CDIO教学实施效果、课程教学大纲设计如何融入CDIO教学理论、教学成果奖申报等内容进行了深入讨论。在讨论中，大家认为CDIO模式特别强调在学中做，在学中规范，在学中提升，在做中学，在做中反思，在做中提炼的理念，这是高职教育改革的方向，实践经验告诉我们，必须在加强CDIO理论学习中不断深化实践，才能解决碰到的问题和困难，推进CDIO教学理论的落实。 龚永坚院长全程参与了讨论，他在总结时说，对于教学改革，有争论、有讨论是积极和主动参与改革的体现，就怕没有议论。希望计算机专业群教师抓住学校重视CDIO改革的契机，不畏艰难，深入研究，积极实践，推动学院和专业教学理念提升，促进教师教育教学能力提升，为计算机专业群改革做出贡献。 （ 文 王伟斌 楼建忠 ）', 'http://info.jhc.cn/2012/1109/c201a1462/page.htm', 'http://info.jhc.cn/_upload/article/images/aa/ff/48c6b72041da9618a5c616dae801/370ae10e-698f-4996-ae9f-5ddf0ae93b23.jpg', '2017-05-17 14:19:18.530207');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (580, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院党校举行第十五期入党积极分子培训班开学典礼', '4月15日，信息学院第十五期入党积极分子培训班开学。学院党校校长、党总支书记朱登胜，党校校务委员会成员及222名学员参加了本次开学典礼。 本期培训班经过学生自荐、班主任推荐、党校校务委员会筛选，最终产生了222名新一期党校学员。朱登胜在开学典礼上说，在中国共产党建党90周年之际，希望学员们珍惜在党校的学习机会，遵守党校的制度和纪律，提高思想政治觉悟；认真学习专业知识，提高专业技能，努力做同学们的榜样；主动参与校园、社会实践活动，为构建和谐校园贡献自己的力量。 党员代表郑江同志、学员代表郑伟康同学先后发言，他们代表学员向党组织表达了参加党校学习的喜悦心情和努力学习的决心。 开学典礼上成立了本期党校培训班团支部，宣读了党校章程、学员守则、党校优秀学员评定规则和教学课程安排等内容。 （文 周荃 ）', 'http://info.jhc.cn/2012/1109/c201a1458/page.htm', NULL, '2017-05-17 14:19:18.552478');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (581, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学子在省第八届大学生程序设计竞赛上喜获佳绩', '4月16日，浙江省第八届大学生程序设计竞赛在风景秀丽的浙江大学紫金港校区举行，来自全省76所高校的300支队伍齐聚一堂，参加了长达5个小时的不间断马拉松式编程比赛。本次竞赛获奖面为45%，我校信息学院选派的三支参赛队不负众望，最终获得一个银奖（朱一帆、罗绍泽、洪晖岳三人团队）和一个铜奖（董海、沈璐、高静雅三人团队）的好成绩。 ACM大学生程序设计竞赛是一项国际性赛事，素有“大学生的计算机奥林匹克竞赛”之称，今年的比赛更是实现了全球同步，获奖学生都成为就业市场上企业竞相争取的“香饽饽”。信息学院通过几年的实践，积累了丰富的参赛经验，形成了一套选拔、培训、再选拔、再培训的学生竞赛队伍遴选和辅导教师竞赛团队建设机制。今年，学院还积极探索将竞赛模式引入日常课程教学，开发竞赛和教学一体化网络平台，实现以教带赛，以赛促教的新模式，进一步深化了学院目前积极倡导的网络化教育教学改革。 （ 文  王伟斌 梅旭时 ）', 'http://info.jhc.cn/2012/1109/c201a1447/page.htm', 'http://info.jhc.cn/_upload/article/images/d2/52/b85d1994438fad0a6b31db5c381f/9ebbd94e-d216-4912-8c19-6d4925f90d4d.jpg|http://info.jhc.cn/_upload/article/images/d2/52/b85d1994438fad0a6b31db5c381f/824d7e16-7b0c-4a1e-bd84-c55c3401963b.jpg|http://info.jhc.cn/_upload/article/images/d2/52/b85d1994438fad0a6b31db5c381f/4a7aadae-01f9-46c9-8f8a-3203b8c6b75e.jpg|http://info.jhc.cn/_upload/article/images/d2/52/b85d1994438fad0a6b31db5c381f/afc85969-8d9f-4762-9d8e-e8e4ab36a70c.jpg', '2017-05-17 14:19:18.574744');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (582, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院顺利完成弱电实训室建设和相关师资培训', '日前，由信息学院网络与楼宇工程部承担的“弱电工程实训室”新建项目完成全部建设任务并投入运行。该实训室投入建设经费近40万元，可开展SCS（结构化布线）实训和计算机网络综合实训项目，服务于计算机网络、智能楼宇、通讯等三个专业的理实一体化课程与综合实训课的实施，是一个利用率高、共享面宽的专业综合实训室。实训室的建成，对提升学生综合布线实践技能提供了实践平台，同时还可面向企业开展IDEAL网络测试工程师、立能综合布线系统工程师等行业证书认证与培训任务。 为充分发挥实训室的作用，学院特意安排5名教师和 20位学生参加了由美国理想公司和杭州立能信息科技有限公司主办的行业证书认证培训。培训结束后，5位老师通过理论与实践考试，获得了IDEAL网络测试工程师认证和立能综合布线系统工程师证书，为保证实训室的运行提供了师资保障。 （ 文 朱墨池 ）', 'http://info.jhc.cn/2012/1109/c201a1437/page.htm', 'http://info.jhc.cn/_upload/article/images/39/41/f47cb03e468288ca9a1dc3f9999c/2329f25f-0073-47d9-8e6f-6da130148cf2.jpg|http://info.jhc.cn/_upload/article/images/39/41/f47cb03e468288ca9a1dc3f9999c/0d3aae7d-7126-4013-9a61-39b4231bcb77.jpg|http://info.jhc.cn/_upload/article/images/39/41/f47cb03e468288ca9a1dc3f9999c/2a33a3d5-caa3-44ce-8e48-de6488bf0836.jpg|http://info.jhc.cn/_upload/article/images/39/41/f47cb03e468288ca9a1dc3f9999c/c9dd498b-3d01-46bf-8e3f-825de6da993d.jpg', '2017-05-17 14:19:18.597041');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (583, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '民盟金职院支部开展拓展训练', '近日，民盟金华职业技术学院支部一行12人赴缙云黄龙山进行团队拓展训练，以此培养支部团队凝聚力。 本次活动主题是“提升团队凝聚力，考察校外实习基地”。盟员们兴致勃勃地参观了风景秀丽的黄龙山景区，为提升盟组织的战斗力，大家还兴致盎然地参加了消防演练、过桥索等野外拓展活动，有效提升了挑战自我、团队协作等能力，增强了盟组织的凝聚力。在训练之余，盟友们还与校外合作基地的负责人交流了校企合作经验和进一步合作事项，并看望了正在基地实习的十位同学，实地了解了学生的基地实习情况。 （ 文 王成福 ）', 'http://info.jhc.cn/2012/1109/c201a1432/page.htm', 'http://info.jhc.cn/_upload/article/images/9b/ca/94b82ad8456a9e1d1e869d835b7b/c25d66ee-cecf-4338-b794-93005847293f.jpg', '2017-05-17 14:19:18.619215');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (584, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院企业信息化事业部开展基于CDIO的四说活动', '4月20日，信息学院企业信息化事业部结合计算机应用技术专业细化和落实CDIO改革教研活动的要求，开展了“四说”教研活动。副院长王成福、陈海荣及相关部门负责人参加。 相关课程负责人首先结合CDIO工程教育理念，从课程定位、课程设计、课程实施与评价等方面对课程进行了说课。说课结束后，与会人员从CDIO理论出发就课程项目化教学的具体思路、CDIO能力在课程中的落实、课程如何结合环境教学、教学内容的CDIO思想融入、企业项目如何教学化改造等交流了意见，同时大家还就课程的实施环境、学生团队有效组成、教学中如何实施以突出有效性等主题进行了讨论。 通过此次主题教研，加深了部门广大教师对“四说”活动的理解，也加深了对CDIO工程教育理念实际应用的进一步认识，促进了教师对教学实施中存在问题与解决问题方法的思考。 （ 文 楼小明 ）', 'http://info.jhc.cn/2012/1109/c201a1428/page.htm', NULL, '2017-05-17 14:19:18.643157');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (585, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院第四届宿舍文化节开幕', '信息学院第四届宿舍文化节开幕 发布人：管理员资讯来源：信息管理中心 发布时间：2011-04-21 14:46:37点击数量:1     公寓文化作为校园文化的一个重要组成部分，在培养和锻炼现代人才过程中发挥着越来越重要的作用。4月20日，信息学院第四届“我的寝室，我的家”宿舍文化节开幕式隆重举行，校宿管中心主任贾美倩老师等应邀参加。 贾美倩在开幕式上致词，她充分肯定了本届宿舍文化节对丰富寝室文化和同学们业余生活所起的积极作用，希望进一步加强学生公寓文化建设，提高安全文明意识，促进沟通交流，共建和谐公寓。 本届宿舍文化节包括“全寝室参与，快乐共分享”——趣味游戏竞赛、“宿舍风采，你我与共” ——文明之屋大比拼、“我寝，我爱”——寝室吉祥物设计大赛、“我们的精彩，我们秀”——寝室文化节活动图片展等内容。开幕式上，“寝室文明，我先行”宣誓仪式隆重举行，全体人员作了“以实际行动树榜样，遵守制度，提高素质，创文明楼层，筑和谐公寓”的承诺。 （ 文 汪铭铠 ）', 'http://info.jhc.cn/2012/1109/c201a1422/page.htm', 'http://info.jhc.cn/_upload/article/images/df/e8/43ef0dc44027a9d8b20a1c77d53e/e5aba874-9bac-4ae1-836f-01a81312d32d.jpg|http://info.jhc.cn/_upload/article/images/df/e8/43ef0dc44027a9d8b20a1c77d53e/58d6727d-1f42-4b78-ac4d-ac795aed451e.jpg', '2017-05-17 14:19:18.663703');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (586, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院举行2011年金华高新IT学院专业指导委员会会议', '4月22日，信息学院举行2011年金华高新IT学院专业指导委员会会议，这是我校高新IT学院成立后召开的首次专业指导委员会会议，来自金华利诚信息技术有限公司等金华高新园区的19家企业负责人、行业专家应邀出席，信息学院领导及计算机专业群教师参加会议。 开幕式上，校企负责人首先对校企利益共同体——金华高新IT学院成立以来在发挥校企双方优势，共同培养金华电子信息产业高技能人才方面发挥的积极作用给予了肯定。金华利诚信息技术有限公司总经理、金华高新IT学院专业指导委员会主任委员池水明说，高新IT学院把学校教育与企业实践能力进行了有机结合，此次专业指导会是校企双方对IT人才培养内涵和具体举措的一次充分交流沟通，企业有责任有义务将对人才培养具体要求进行表达，以帮助学院提升对人才培养的认识，满足自身的人才需求。 信息学院院长龚永坚说，金华高新IT学院充分整合了多方的优质资源，实现了学校人才培养目标与企业岗位需求的无缝对接。学院十分重视企业专家对人才培养目标的期望和实际需求，愿意充分聆听大家的意见和建议，发挥校企合作的凝聚力，提升我们对人才培养的认识水平，为地方电子信息产业提供合格人才。 围绕“用CDIO工程教育理念来改造我们的专业”主题，企业专家和专业教师们对IT类专业典型岗位设置、专业培养目标定位、课程体系以及基于CDIO理念的能力大纲要求等内容进行了充分沟通。专家们结合实践经验，对CDIO工程教育实践中的项目构思、设计、实施、运作等方面提出了极具针对性的建议，教师们则结合自身教学情况与企业专家交流了教学实施过程中注意事项。 （ 文 王伟斌 邱晓华）', 'http://info.jhc.cn/2012/1109/c201a1413/page.htm', 'http://info.jhc.cn/_upload/article/images/c7/82/6e391d7a477fb4272f78573b831d/ac98995d-1476-4fff-a6d9-519911dd33a7.jpg|http://info.jhc.cn/_upload/article/images/c7/82/6e391d7a477fb4272f78573b831d/c5c5a94c-5777-4c31-a7c3-573d925c2d03.jpg|http://info.jhc.cn/_upload/article/images/c7/82/6e391d7a477fb4272f78573b831d/e3030888-41d5-42da-9ce4-62d27aec0c4f.jpg', '2017-05-17 14:19:18.685993');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (587, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院“我自信 我最型”首届达人秀比赛圆满结束', '4月26日晚，历时一个月，经过海选、初赛、复赛，以展示学生风采、秀出学生魅力为目地的信息学院“我自信 我最型”首届达人秀总决赛在实验剧场举行。 参加总决赛的选手均是从报名的学生中脱颖而出的信息达人，他们秉承“敢于展现自我，勇于接受挑战”的信念出现在总决赛的舞台上。达人们或街舞、或吟唱，或音乐剧，或武术的精彩表演，展现出信息学子充沛的文艺因子，横溢的艺术才华，充分突出了“达人”的主题特色。决赛上高潮迭起，博得观众掌声阵阵，深深吸引了在场的每一位同学。最终，电商Z1001班的毛颂歌以舞蹈《can’t no》一举夺得大赛冠军，大赛的最佳人气奖、最佳潜力奖等奖项也各有归属。 （ 文 金乐 汪铭铠）', 'http://info.jhc.cn/2012/1109/c201a1401/page.htm', 'http://info.jhc.cn/_upload/article/images/2a/df/6c9e74c04b319822c0f9ebddf9f0/8b275fbe-a973-4d31-8733-d10e2498069a.jpg|http://info.jhc.cn/_upload/article/images/2a/df/6c9e74c04b319822c0f9ebddf9f0/224fbea8-dcc5-49f7-9182-0fd41f806d7d.jpg|http://info.jhc.cn/_upload/article/images/2a/df/6c9e74c04b319822c0f9ebddf9f0/e9df2f9e-7206-4065-9bf7-ec2a1a605b37.jpg|http://info.jhc.cn/_upload/article/images/2a/df/6c9e74c04b319822c0f9ebddf9f0/b17d518a-3b37-47d8-9d06-4aa011e82091.jpg', '2017-05-17 14:19:18.708089');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (588, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院四届一次教职工暨工会会员代表大会顺利闭幕', '4月27日-28日，信息学院召开四届一次教职工暨工会会员代表大会。 会议全面贯彻学校四届一次教代会精神，听取和审议了龚永坚院长所作的《同心协力开好局，锐意进取续新篇》院长工作报告，报告总结了三届教代会期间信息学院在国家示范院校建设、教育教学改革、人才培养、思政工作等方面所取得的显著成绩，也分析了存在的不足。报告提出了在“十二五”规划开局之年，将继续提升学院办学水平和办学实力，促进学院各专业均衡发展，进一步深化机构改革，激活考核机制，努力实现学院又好又快发展的目标。 会议还审议了工会工作报告，审议并原则通过了《信息工程学院成教管理、考核与奖励办法》、《信息工程学院高层次科教研成果奖励办法》等文件。 （ 文 王伟斌 ）', 'http://info.jhc.cn/2012/1109/c201a1389/page.htm', 'http://info.jhc.cn/_upload/article/images/d0/4b/d5be6f2c49abaca033bea1b79902/15a2157b-e5af-4b4f-84b0-29baca65252c.jpg|http://info.jhc.cn/_upload/article/images/d0/4b/d5be6f2c49abaca033bea1b79902/e5d2f8bf-7c11-442d-a58f-234806dd9d6a.jpg|http://info.jhc.cn/_upload/article/images/d0/4b/d5be6f2c49abaca033bea1b79902/be3fbfe0-88d6-4d59-ac26-6a97b43dd2f8.jpg|http://info.jhc.cn/_upload/article/images/d0/4b/d5be6f2c49abaca033bea1b79902/be11e7e2-82a1-4f82-b799-61e36bccd4df.jpg|http://info.jhc.cn/_upload/article/images/d0/4b/d5be6f2c49abaca033bea1b79902/cecba80e-1d18-4c71-ac6f-fb748998a90e.jpg|http://info.jhc.cn/_upload/article/images/d0/4b/d5be6f2c49abaca033bea1b79902/d8b6da39-795e-4732-98e9-e05c1f24fac0.jpg', '2017-05-17 14:19:18.730338');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (590, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院企业信息化事业部校外实训顺利开展', '5月3日，信息学院计算机101班在指导老师带领下来到金华龙腾数码广场，开展IT产品营销校外实训。本次计算机2010级IT产品营销校外实训是在IT产品调研、IT产品营销方案策划等一周校内实训的基础上，为使学生实地了解IT产品营销内容、营销策略而选择在龙腾数码广场开展的，实训将历时2周。 金华龙腾数码广场是金华专业数码产品展销平台，为用户和经销商提供金华本地电脑及数码产品行情报价，涉及笔记本、电脑、手机、数码产品、软件等销售服务。实训开始前，龙腾数码广场詹继群经理结合卖场情况从实训态度、实训要求、实训纪律、接待顾客等方面对大家提出了具体要求。参加实训同学纷纷表示将以饱满的热情和积极的态度来完成这次难得的实训。随后57位同学被分配到了卖场一楼的28家门店中。 （ 文 楼小明 ）', 'http://info.jhc.cn/2012/1109/c201a1381/page.htm', NULL, '2017-05-17 14:19:34.064665');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (591, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院各党支部开展向杨善洲同志学习活动', '为进一步推进“创先争优”、“廉政文化进校园”工作，近日，信息学院网络（电商）专业学生支部举行了主题为“向杨善洲同志学习”的网络远程学习教育活动。 本次学习活动利用学院的专业优势，以网络远程学习的形式展开，学习中，大家通过下载杨善洲先进事迹学习资料、上传个人学习感想、支部书记总结形成共识为主线，结合实际，支部党员通过学习杨善洲同志精神具体内容、对当代启示意义，探讨了新时期大学生党员如何践行杨善洲精神，为社会发展贡献自己的力量。学院其他支部也开展了形式多样的向杨善洲同志学习的活动。 （ 文 赵倩 ）', 'http://info.jhc.cn/2012/1109/c201a1377/page.htm', NULL, '2017-05-17 14:19:34.086973');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (592, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院召开教学成果奖评审会', '为及时总结、推广教师在教学中积累的经验和成果，鼓励教学改革与创新，5月5日晚，信息学院组织开展教学成果奖评审。学校副院长胡野及教务处负责人等应邀出席，信息学院领导、成果负责人参加了会议。 本次申报工作得到了全体教师的积极响应，共有24项成果参评，涉及论文论著与教材、教学改革与建设、教学实施等各个层面。会上，各申报项目负责人从解决的教学问题、方法、效果、创新点与现有的教科研项目、论文支撑情况等方面进行了成果展示与汇报。评审专家对我院项目申报的积极性与指向性表示肯定，并就成果研究目的、创新点的提炼、研究方法的选取、效果的体现等方面提出了建设性的意见与建议。 评审会强化了成果负责人对项目的思考，同时为申报新一轮省级教学成果奖明确了努力方向，激励教师们积极投入一线教学，提高教育教学实践能力与研究水平，推动我院教学改革不断深入。 （ 文 陈静泓 ）', 'http://info.jhc.cn/2012/1109/c201a1373/page.htm', NULL, '2017-05-17 14:19:34.109183');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (593, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院校企互动评价实训课程教学成果', '5月6日，信息学院企业信息化事业部邀请紧密合作企业浙江中呼科技有限公司技术部鲍爱玲经理等校企互动，参与计算机102班《CIP策划与页面制作》课程实训成果评价会。 评价会首先由实训学生按实训小组对成果进行了演示，鲍爱玲结合自己的企业实践，从配色、效果、字体、页面布局、创意等方面逐组对成果进行了点评，对存在的问题提出了中肯的修改建议。这一模式使同学们对企业产品实际标准，未来岗位真实要求有了切身体会，大家纷纷表示这种评价对自己提升网页设计与制作能力有很好的促进作用。 针对此次校企互动评价产生的良好反响，信息学院将继续邀请企业专家来参与实训课程的项目教学、实训成果的评价等工作，深化与拓展校企合作内容，使校企在合作育人方面取得更多成果。 （ 文 楼小明 ）', 'http://info.jhc.cn/2012/1109/c201a1370/page.htm', NULL, '2017-05-17 14:19:34.131318');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (594, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '健康生活，快乐学习――信息学院举行第八届田径运动会', '5月9日，经过精心策划、充分准备，信息学院第八届田径运动会在校运动场举行，校领导胡野副院长、学校教务处、学生处负责人等应邀出席开幕式。学院全体教职工及大一、大二学生参加运行会。 胡野在开幕式上致辞说，运动会作为增强师生强健体格、培养健康心理、乐观心态的重要载体，在学校人才培养中具有重要作用。他希望信息学院把开展运动会等群众体育作为落实素质教育的一个重要组成部分高度重视，细致落实。他希望所有参赛的运动员努力拼搏，勇于进取，赛出风格，赛出水平，为进一步提高我校的体育运动水平做出贡献。 本届运动会共有来自34个班级的509名运动员参加了跳远、跳高、短跑、长跑等19个项目的角逐，并设立定跳远、铅球和“赶猪”三个教工项目。赛场上精彩不断，激情不减，全院师生共同谱写了“人人重视体育、人人参与健身”的新形象、新风貌。经过一天紧张比赛，最终电商1002班和网络092班分获大一、大二团体总分第一名，运动会上还打破四项学院记录。 （ 文 王伟斌 / 摄影 林雪华 ）', 'http://info.jhc.cn/2012/1109/c201a1349/page.htm', 'http://info.jhc.cn/_upload/article/images/eb/e5/9325db9e49f889d04cb7af6fef19/00a853ea-6ad6-4b19-bab7-e49f8a51c821.jpg|http://info.jhc.cn/_upload/article/images/eb/e5/9325db9e49f889d04cb7af6fef19/34d9f954-69f5-41bd-932b-ab853a67bcfe.jpg|http://info.jhc.cn/_upload/article/images/eb/e5/9325db9e49f889d04cb7af6fef19/e57344d8-5d6a-4250-910c-b26fbddaece7.jpg|http://info.jhc.cn/_upload/article/images/eb/e5/9325db9e49f889d04cb7af6fef19/7c701968-d5f4-44c3-8b2b-c9a7877e3207.jpg|http://info.jhc.cn/_upload/article/images/eb/e5/9325db9e49f889d04cb7af6fef19/809f0934-09f3-4575-8fa8-bcd0499c775e.jpg|http://info.jhc.cn/_upload/article/images/eb/e5/9325db9e49f889d04cb7af6fef19/41914e0e-fe9d-4a20-ae0d-fd2a9ca26a5e.jpg|http://info.jhc.cn/_upload/article/images/eb/e5/9325db9e49f889d04cb7af6fef19/913102ba-eb51-44d2-8bc5-b86e17b89764.jpg|http://info.jhc.cn/_upload/article/images/eb/e5/9325db9e49f889d04cb7af6fef19/8fc6861d-a443-445c-bd54-dffbae4370ef.jpg|http://info.jhc.cn/_upload/article/images/eb/e5/9325db9e49f889d04cb7af6fef19/7a613587-9c05-4d77-9571-a5fea15a003b.jpg', '2017-05-17 14:19:34.153685');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (595, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学子在首届全国高职高专院校计算机综合应用能力', '5月7-9日，由教育部高等学校高职高专计算机类专业教学指导委员会主办的“首届全国高职高专院校计算机综合应用能力大赛总决赛暨海峡两岸赛”在中央财经大学举行。经过1天半的紧张角逐，代表我校信息学院参赛的电商1002班糜超同学不负众望，最终获得常用软件组二等奖和网络应用与安全组三等奖的好成绩。 本次大赛以美国Certiport发行的IC3项目国际认证考试为竞赛标准，完全按照全球化认证标准要求，采用在线考试形式，自动阅卷，当场评分。考试内容包括Computing Fundamentals，Key Applications，Living Online三个科目。大赛共吸引了全国100多所高职高专院校的7000余名学生参赛，经过初赛、复赛环节，产生了407名选手进入最终决赛。 此次大赛参赛经历，既培养了学生的动手能力、竞争意识，使他们把学习的重心从书本知识投向实际应用，也为教师提供了一个开拓视野、与兄弟院校交流的好机会。 （ 文  陈兴威 楼小明 ）', 'http://info.jhc.cn/2012/1109/c201a1345/page.htm', NULL, '2017-05-17 14:19:34.175896');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (596, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院开展学风建设问卷调查活动', '为掌握学院学风现状，了解学生在学习中的困惑和期待，为学院教学改革和学风建设提供数据支撑，4-5月，信息学院开展了“我与学院同发展”学风建设问卷调查活动。 此次调查问卷主要内容涉及学习目标和动机、学习态度、学风建设必要性和效果等方面的内容。对象为全院所有学生，采取网上调查和纸质问卷两种方式开展。针对大二、大三学生发出网上问卷调查，大一学生发放纸质问卷，共回收有效问卷1377份。 通过问卷调查活动，了解和掌握了学生在学风建设上的思想动态，收集了同学们最真实的意见和建议，指明了学院“学风建设年”活动的方向，也强化了学生管理工作为学生服务的思路，为今后学风建设工作的高效化和信息化打下了坚实基础。 （ 文 汪铭铠 ）', 'http://info.jhc.cn/2012/1109/c201a1342/page.htm', NULL, '2017-05-17 14:19:34.198104');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (597, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院组织党员干部参观“惩防渎职侵权犯罪”展览', '4月12日，信息学院组织学院党员干部参观正在我校举行的“法治与责任——全国检察机关惩治和预防渎职侵权犯罪展览金华巡展”。学院党员干部们怀着凝重的心情，认真参观了图文并茂的展览长廊，一个个贪污贿赂典型案例的揭露和分析，引起了大家的深刻反思和强烈反响。 参观后许多老师说，以前对渎职侵权犯罪不是很了解，看展览后才知道，国家机关工作人员不作为或者乱作为就是渎职侵权犯罪。这类犯罪不仅使公共财产、国家和人民利益遭受重大损失，而且严重损害党和政府的形象及威信。通过这样的反面案例教育，相信一定能促使干部恪尽职守，勤政廉政。有的老师表示，展览中学到了不少东西，而且展板中有不少金华本地的案例，这些身边的案例给了自己更深的触动。 （ 文 王伟斌 / 摄影  林雪华 ）', 'http://info.jhc.cn/2012/1109/c201a1331/page.htm', 'http://info.jhc.cn/_upload/article/images/7d/cf/0a79dde64b8b82cd04030eee8231/512c25a5-ccf0-42d7-b082-c41261190f2b.jpg|http://info.jhc.cn/_upload/article/images/7d/cf/0a79dde64b8b82cd04030eee8231/d89e95dd-db6f-46f6-857b-1c12450e6517.jpg', '2017-05-17 14:19:34.222207');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (598, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '关于召开2011年北京CDIO区域性国际会议的通知', '根据教育部国际合作与交流司批件（教外司际[2010]1578号），在中国教育部和中国工程院的支持下，在国内有关大学、企业的积极响应下，在国际CDIO工程教育组织领导人的建议和支持下，2011年北京CDIO区域性国际会议将于2011年5月8～11日在北京召开。  一、会议主题和任务 本次会议的主题是：全面深入研究、实施CDIO工程教育模式，为产业界培养卓越工程人才。 会议的主要任务为： (1)     交流与总结国内外CDIO工程教育模式的研究和实践经验 (2)     展示国内外CDIO工程教育模式的成果 (3)     促进国内外CDIO工程教育研究与实践的机构、大学和产业之间的合作   (4)     接收本区域内大学加入CDIO国际联盟的申请 (5)     讨论下一届会议有关议题  二、会议组织机构  会议总主席 宁 滨，北京交通大学校长  指导委员会 主席： David Wisler， 美国工程院院士, 美国机械工程师协会（ASME）高级副总裁 Ed Crawley， 美国工程院院士，MIT讲座教授  副主席： 朱高峰， 中国工程院院士 Johan De Graeve，比利时鲁汶联合工程大学校长，北京交通大学荣誉博士，联合国教科文组织产学合作教席联合主持人 顾佩华， 加拿大工程院院士，汕头大学副校长  委员： 吴爱华， 教育部高教司处长、国家示范软件学院建设办公室主任 范 唯， 教育部职成司高职高专处处长 Dendev Badarch, 联合国教科文组织莫斯科办事处主任、UNESCO信息与教育研究所所长 郑家茂, 东南大学副校长 余建星, 天津大学副校长 涂善东, 华东理工大学副校长 陈启元， 中南大学副校长 朱 宏, 成都电子科技大学副校长 孔祥东, 燕山大学副校长 姚寿广，江苏科技大学副校长 陈小虎，南京金陵科技学院党委书记 陈希舜，台湾科技大学校长，中国台湾 颜鸿森，成功大学常务副校长、讲座教授，中国台湾 姜嘉乐, 高等工程教育研究杂志常务副总编辑 周定文, 成都信息工程学院院长 谢可滔, 广东白云学院董事长 王伯庆，麦可思教育数据公司总裁 姚  霆，中国数字图书馆董事长 陈继权，浙江亚龙科技集团公司董事长 Nhut Tan Ho, 越南国立大学（胡志明市）CDIO总顾问，越南 Kamol Kitsawad, Registrar of Assumption University, Thailand Hee Joh Liang， Deputy Principal, Singapore Polytechnic, Singapore Thanya Kiatiwat, Dean of Engineering, Kasetsart University, Thailand Chan Yew Meng, Senior Director, School of Engineering (Electronics), Nanyang Polytechnic (NYP), Singapore 程序委员会 主席： 查建中，联合国教科文组织产学合作教席主持人，北京交通大学教授 副主席： 郭文莉，北京石油化工学院院长 唐振明，中软国际高级副总裁 苏志刚，宁波职业技术学院院长 委员： 史忠健， 青岛职业技术学院院长 卢  苇， 北京交通大学软件学院院长，国家示范软件学院建设办公室副主任 陆小华, 汕头大学教务处处长 温 涛， 大连东软信息学院院长 吴中江， 南京工程学院副院长 胡振文， 石家庄铁路职业技术学院院长 傅 松， 清华大学航空航天学院副院长 刘沛清, 北京航空航天大学航空科学与工程学院副院长 江志斌， 上海交通大学教务处处长 周硕彦, 台湾科技大学国际长、教授, 中国台湾 子委员会： 技术委员会： 主席： 顾学雍， 清华大学工业工程系副教授 副主席： 单强，苏州工业园区职业技术学院院长 委员： Pee Suat Hoon, Director of Department of Educational Development, Singapore Polytechnic, Singapore 康 雁，东北大学中荷生物医学信息工程学院院长 李秋实，北京航空航天大学能源与动力工程学院副院长 刘红琳，北京石油化工学院教务处处长 施晓秋，温州大学教务处处长 Michael Cheong, Assistant Director, School of Engineering, Nanyang Polytechnic, Singapore  三、会议内容 大会的内容为： (1)     由国际CDIO工程教育模式专家开展CDIO教育模式培训 (2)     国内外工程教育专家的主旨报告，介绍工程教育前沿的研究和实践。主旨报告专家有： 潘云鹤，中国工程院常务副院长、工程教育委员会主任 David Wisler， 美国工程院院士 Ed Crawley， 美国工程院院士，MIT讲座教授 Johan De Graeve，比利时鲁汶联合工程大学校长，北京交通大学荣誉博士，联合国教科文组织产学合作教席联合主持人 Dendev Badarch, Director of UNESCO Moscow office, Acting Director of UNESCO IITE Hwang Dae Joon，韩国成均馆大学教务长，UNESCO IITE理事 顾学雍，清华大学副教授，UNESCO产学合作教席顾问 (3)     国内外CDIO研究和实施的先进典型经验报告。部分典型经验报告的单位为： 北京交通大学软件学院、中软国际集团 天津大学 汕头大学 北京石油化工学院 燕山大学 东软大连信息学院 苏州工业园区职业技术学院 新加坡南洋理工学院 新加坡理工学院 会议欢迎各开展CDIO工程教育模式的学校提交经验报告（截止3月底），经评审决定是否在大会报告。 (4)     国内外CDIO学生项目团队的学习实践和成果报告。 四、参加会议人员 参加会议的嘉宾、人员有： （1）      教育部、中国工程院的领导；大学校长、联合国教科文组织的专家； （2）      国际CDIO组织领导、专家；国际国内知名企业负责人； （3）      国内外开展CDIO研究和实践工作的高校领导、教师、学生； （4）      参与CDIO工程教育模式项目实践的学生项目团队； （5）      希望进行CDIO工程教育模式实践的大学教学管理人员、教师、学生。 五、会议时间和地点 会议时间：2011年5月8～11日       地点：北京中苑宾馆 会议初步的时间安排为：', 'http://info.jhc.cn/2012/1109/c201a1327/page.htm', NULL, '2017-05-17 14:19:34.242604');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (599, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院以推动课程实施深化教学改革', '为及时总结推广教师在教学中积累的宝贵经验和成果，鼓励教师实施教学改革与创新，5月17日，信息学院开展了以“创新教学手段，推动课程实施”为主题的全院教师教研活动。 活动中，吕焱飞和王静两位学院教学成果奖申报项目负责人分别围绕“构建社会化学习环境的实践与探索”和“师生互动型课堂教学的方法研究”两个主题，与全体教师分享了自身在教学实施中进行的有益尝试，阐述了教学过程中以技术平台构建社会化学习环境，或通过分组教学、以组考核、作业分层、师生互动等手段提高学生参与课程活动的积极性，培养学生自主学习能力等教学实施研究。活动还结合学生代表的课程学习心得总结，明确了有效的教学方法与手段在推动课程实施中的重要性。 本次全院教研活动促进了教师教学经验的交流与借鉴，对激励学院教师以学生为本，关注教学方法、手段与形式的创新，有效组织课堂教学，将形成性考核落到实处，提高学生学习质量等发挥了积极作用，有力推动了学院教学改革的不断深入。 （文 陈静泓 ）', 'http://info.jhc.cn/2012/1109/c201a1323/page.htm', NULL, '2017-05-17 14:19:34.264881');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (600, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院应用电子支部开展建党90年主题教育活动', '4月22日，信息学院应用电子技术专业学生支部开展了以唱红歌、廉政知识问答、优秀毕业党员经验分享等为载体的“90年党旗飘扬，我为党旗添光彩”建党90周年党员主题教育活动。 唱红歌环节，《红梅赞》、《北京的金山上》、《我和我的祖国》等一首首耳熟能详的经典老歌，党员学生们或独唱、或合唱、或对唱，深情的演绎，动情的歌声，唱出了大家对党和对党的事业的无限忠诚，唱出了新一代党员热爱党、拥护党、跟党走主动性和自觉性。 党的历史和廉政知识问答，则让党员学生们在一种轻松的氛围中进一步了解了党的光辉历程，了解了党内的法规和纪律。在经验介绍环节，优秀毕业生党员们从求职经历、自信心培养、求职心态把握等方面，介绍了自己在求职和职场上碰到的挫折、收获的感想、积累的经验、取得的成绩，让大一、大二的学生党员们从身边的人和事中汲取对人生、对事业的品味和思考，激发他们对大学学习的深层次认识。 （ 文 王伟斌  陈德仙 ）', 'http://info.jhc.cn/2012/1109/c201a1300/page.htm', 'http://info.jhc.cn/_upload/article/images/f3/6b/54dc199746938afc4e5dd37a46b8/f08e0e9b-ed2e-466e-b4bd-c9c5ae6f2600.jpg|http://info.jhc.cn/_upload/article/images/f3/6b/54dc199746938afc4e5dd37a46b8/7127c0fb-8b5f-49e4-83f4-dc412b3fbd4c.jpg|http://info.jhc.cn/_upload/article/images/f3/6b/54dc199746938afc4e5dd37a46b8/5ff42906-0049-441a-9b0d-35bd70f34b36.jpg|http://info.jhc.cn/_upload/article/images/f3/6b/54dc199746938afc4e5dd37a46b8/228d92fe-921e-46be-a854-e849834c1b75.jpg|http://info.jhc.cn/_upload/article/images/f3/6b/54dc199746938afc4e5dd37a46b8/59616103-664a-4de8-abe7-01eb0783413f.jpg|http://info.jhc.cn/_upload/article/images/f3/6b/54dc199746938afc4e5dd37a46b8/310b5fbc-4fcf-4ecb-b346-941cadb4ee82.jpg|http://info.jhc.cn/_upload/article/images/f3/6b/54dc199746938afc4e5dd37a46b8/1db2b021-2ebe-412f-8c72-6b3e488c69e2.jpg|http://info.jhc.cn/_upload/article/images/f3/6b/54dc199746938afc4e5dd37a46b8/4d355140-954e-4cbf-b59b-028c57cafdf8.jpg', '2017-05-17 14:19:34.287215');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (601, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院开展毕业生文明离校主题教育活动', '毕业生离校前夕向来是学校安全稳定的敏感、关键时期，为了维护学校正常教学和生活秩序，信息学院在毕业生离校工作中坚持“以人为本，德育为先”的教育导向，陆续开展了“平安离校、感恩离校、文明离校”为主题的文明离校系列活动。 “文明离校”主题教育系列活动主要包括：发放“文明离校要倡导，你我责任需践行”离校倡议书，号召全体毕业生齐心协力，严格要求自己，站好最后一班岗，做到文明离校；“红色誓词忆在心，表率作用行于身”学生党员重温入党誓词，倡议全体学生党员继续传承党性、发扬党风，身体力行、争当表率，在全体毕业生中带头做好文明离校工作，真正起到先锋模范作用；“莘莘学子行在外，母校关怀系永久”建立校友信息库，方便毕业生与学校的联系与沟通。 通过这一系列活动的开展，很好地引导和保证了2011届毕业生离校各项工作的开展，目前信息学院毕业生离校工作进展顺利，倡导了积极文明的离校旋律，营造了热情和谐的毕业生离校氛围。 （ 文 汪铭铠 ）', 'http://info.jhc.cn/2012/1109/c201a1296/page.htm', NULL, '2017-05-17 14:19:34.309367');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (602, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '我校第十一届电脑科技文化节开幕', '5月27日，由信息学院承办的我校第十一届电脑科技文化节开幕式在大学生活动中心广场隆重举行。学校党委副书记余党军及校相关职能部门、信息学院领导出席了开幕式。  开幕式上，余党军对本届科技文化节的举办表示热烈的祝贺，他希望同学们通过本届文化节将电脑的普及与专业学习、职业生涯规划紧密联系，共同营造浓厚的校园学术科技氛围。开幕式上举办的电脑科技文化展、大学生自主创业展销、信息学院学生技能作品展示、现场装机大赛、电脑与家电咨询维修等活动，充分展示了信息学院专业特色与科技魅力。  据悉，本届电脑科技文化节为期一个月，主要包括科技宣传普及篇、科技竞赛篇、科技创新篇等，旨在通过IT讲座、电脑家电维修服务进社区以及AutoCAD平面图形设计竞赛、网店装修及运营竞赛、装机大赛等专项比赛激发学生学习专业知识和科技文化知识的热情，以促进学风建设，全力打造信息化校园。 （ 文 金乐 ）', 'http://info.jhc.cn/2012/1109/c201a1283/page.htm', 'http://info.jhc.cn/_upload/article/images/af/62/7b4a85f947b7892ec13bedf574b0/0db86b94-9e4f-457e-9b45-153d678f7e71.jpg|http://info.jhc.cn/_upload/article/images/af/62/7b4a85f947b7892ec13bedf574b0/82556264-7442-4cd8-ac52-37cbb8bab7c7.jpg|http://info.jhc.cn/_upload/article/images/af/62/7b4a85f947b7892ec13bedf574b0/2942c511-fa02-4906-9c43-81a1eb199039.jpg', '2017-05-17 14:19:34.331551');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (603, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '心怀感恩，志向未来', '5月30日，信息学院举行2011届学生毕业典礼。院领导、教师代表和来自9个专业的869名毕业生参加了毕业典礼。  毕业典礼围绕“心怀感恩，志向未来”主题精心营造氛围，毕业生和教师代表、在校生代表一一发言，令人感动，其中毕业生代表在发言时，用饱含深情的话语回顾了大学三年学习、生活点滴，以及期间结下的难忘的师生情谊、同学情谊，同时还表示会感激身边的人和事，以饱满的精神状态迎接人生的新挑战，这一番话语令全场的人都动容不已。其后，毕业生代表向32位当选学院2011届毕业生“我最喜爱的老师”一一献花，全体毕业生向任课教师鞠躬行谢师礼，毕业班同学还为母校捐赠LED显示系统，无不显示了毕业生感恩母校的拳拳之心。  毕业典礼上，信息学院负责人勉励同学们面对新挑战，敢于担当，不畏磨难和艰辛，发扬信息人“敢于竞争，善于转化”的拼搏精神，一步一个脚印，踏实工作，在新的起点上，早日实现自己的人生理想。', 'http://info.jhc.cn/2012/1109/c201a1272/page.htm', 'http://info.jhc.cn/_upload/article/images/ff/92/6400873447dbb5eee90e275ab216/1f5d549e-7925-4092-94cc-ab76a7bd5eeb.jpg|http://info.jhc.cn/_upload/article/images/ff/92/6400873447dbb5eee90e275ab216/36818047-db67-41f4-94e4-c84390b9af5f.jpg|http://info.jhc.cn/_upload/article/images/ff/92/6400873447dbb5eee90e275ab216/11bd02b8-4a1d-4118-877a-a4f69ef65216.jpg|http://info.jhc.cn/_upload/article/images/ff/92/6400873447dbb5eee90e275ab216/d7ade42e-6555-4e3f-ab07-a3a567ef2467.jpg', '2017-05-17 14:19:47.668784');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (604, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院2011届毕业生赠送母校LED显示屏', '近日，信息学院西教2号教学楼大厅门口一块LED显示屏引起了学院师生的关注，它鲜红的色彩、宽大的屏幕、动态的文字显示替代了原先一条条的横幅，显得大气、美观，成为学院一道靓丽的风景线。 据悉，这是信息学院今年刚刚毕业的2011届同学在离开母校之际，为感谢母校三年的培养，感谢学院老师三年的教诲，赠送给母校的一份特殊礼物。为了筹集购买LED显示屏的经费，毕业班学生党员、学生干部细致动员，做好表率，全体毕业班同学则积极响应，踊跃捐款。许多毕业班同学说，自己虽然还没有什么经济能力，但是尽自己的一点微薄之力为母校做点贡献，是一件很开心快乐的事情，希望母校能越来越好。毕业班同学的这份特殊礼物，为学院目前正在积极进行中的校友会筹备工作奠定了良好的基础。 毕业生赠送的该LED显示屏净长10.56米，净宽0.48米，总价10000元，该显示屏的投入使用，将为信息学院在思政宣传、教学通知、学生活动等方面发挥积极作用。 （ 文 王伟斌 ）', 'http://info.jhc.cn/2012/1109/c201a1264/page.htm', 'http://info.jhc.cn/_upload/article/images/25/c1/0d55f0f54414a1b60249ef1b37cb/25357cfe-f2e8-4a8e-908e-66e3f5ca5a64.jpg|http://info.jhc.cn/_upload/article/images/25/c1/0d55f0f54414a1b60249ef1b37cb/ef56396a-81d2-4337-abf9-25f3db86cbe3.jpg', '2017-05-17 14:19:47.710196');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (605, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院全面推进基于CDIO的计算机专业群改造', '为充分发挥校企利益共同体的资源优势，深化金华高新IT学院建设和CDIO改革，全面改造计算机专业群，6月9日，信息学院召开计算机专业群改造工作推进会。 会议围绕计算机专业群改造工作目标，将改造工作分为CDIO工程教育改革、高新IT学院与特色专业建设、资源库建设三个子项目，并设置了相应的项目团队。会上明确了各个团队的工作内容，要求充分运用团队的主观能动性，加强学习与交流，发挥软件技术专业的引领示范作用，先行探索实施CDIO工程教育改革，为完善高新IT学院运行机制，深化计算机特色专业建设，搭建共享型专业资源库网络平台提供借鉴，全面推动计算机专业群改造。 会议不仅进一步明确了计算机专业群改造团队建设与分工，更是一次启动全面建设工作的动员，为CDIO工程教育理念的深入贯彻，专业内涵、教师教学教改能力及人才培养质量的提升提供了保障。 （ 文 陈静泓 ）', 'http://info.jhc.cn/2012/1109/c201a1260/page.htm', NULL, '2017-05-17 14:19:47.732202');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (606, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院第六届教学基本功大赛“四说”比赛开赛', '为提高教师课程建设、教育教学等综合能力，深化专业教学改革，5月9日，信息学院第六届教学基本功大赛“四说”比赛拉开帷幕，全体教师观摩了本次比赛。 本场比赛由企业信息化事业部和软件事业开发部两个项目部各派出一个教学团队，围绕“说专业、说课程、说课堂、说项目”四个方面开展。比赛中，各团队针对自身专业与课程建设的阶段与层次，明确人才培养目标等专业基本建设要素，对专业、课程、课堂、项目的组织建设进行了精彩汇报，并与现场教师展开互动，很好地达到了交流学习的目的。 本次比赛是信息学院“四说”比赛第一场，其他项目部还将陆续参加比赛，以进一步促进教师间的相互学习，加强专业内涵建设，提高课程教学质量。 （ 文 陈静泓 ）', 'http://info.jhc.cn/2012/1109/c201a1255/page.htm', NULL, '2017-05-17 14:19:47.754546');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (608, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院通信专业召开暑期专业实践企业学生双选会', '为了使通信专业的学生更好的适应通信企业岗位要求，提高暑期专业实践活动效果，为今后就业打下坚实的基础，6月15日，信息学院通信与网络设备专业邀请了三维通信股份有限公司、浙江方大通信有限公司、东信网络等知名通信企业来院参加暑期专业实践企业学生双选会。 双选会上，用人单位代表就单位的基本情况以及人才需求等方面作了介绍，并就学生在专业实践要求、工作岗位条件、企业培训等方面提出的问题作了充分的解释。经过企业和学生面对面的充分沟通和面试，通信091班40人中有20多人现场就和企业达成了初步实习意向。双选会有效解决了通信专业学生暑期专业实习岗位的问题，加强了专业与企业的交流和合作。 （ 文 苏红富 ）', 'http://info.jhc.cn/2012/1109/c201a1248/page.htm', NULL, '2017-05-17 14:19:47.799271');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (609, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '牢记政治生日 不忘入党誓词', '“袁攀攀同志，你好！今天是你的入党纪念日。……践行入党誓词，永葆共产党员的先进本色，以实际行动迎接建党90周年。同时祝你身体健康，学习、工作、生活愉快！”这是信息学院通信与自动化支部所有学生党员在政治生日当天都会收到的以支部名义发送的一条祝福飞信。 为学生党员过“政治生日”是信息学院“1+3”（支部生活会+QQ群、飞信短信、电子邮箱）党建工作模式的功能体现，通过飞信短信这一形式，可以提醒每一个党员无论何时何地都不能忘记自己是一名共产党员，认识到作为一名共产党员的光荣和责任。 在刚刚召开的通信学生支部大会上，还专门为过政治生日的十六位同志送上生日祝福，并重温一次入党誓词。支部负责人说，希望以此让学生党员牢记入党誓词，在学风建设、技能竞赛、寝室卫生、文明修身、社会工作、志愿服务等方面发挥旗帜引领作用，带动全院学子深入开展创先争优活动，以实际行动向建党90周年献礼。 （ 文 汪铭铠 ）', 'http://info.jhc.cn/2012/1109/c201a1245/page.htm', NULL, '2017-05-17 14:19:47.821115');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (610, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院重视做好班主任助理选拔培训工作', '6月22日晚，信息学院组织学院新一期班主任助理第一次培训，主要对“如何引导新生提升学历”、“如何信息化管理班级”等两方面做了详细培训。此次培训工作是信息学院开展的班主任助理选拔和培训工作的内容之一。学院后期还将就“如何做一名优秀的班主任助理”、“如何引导新生做好公寓管理工作”等方面对班主任助理做系统的培训。 据悉，信息学院充分认识到班主任助理是学生思想政治教育工作队伍的重要组成部分，是进行学生思想政治教育的一支重要力量。为抓好队伍建设，前期做了大量工作，新一期班主任助理选报共收到有效报名表95份，通过资格审查和面试选拔了40名优秀学生担任2011级班主任助理，他们将在新的学期走上工作岗位。 （ 文 汪铭铠 ）', 'http://info.jhc.cn/2012/1109/c201a1241/page.htm', NULL, '2017-05-17 14:19:47.843483');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (611, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院召开期末学生思想动态研判会', '为及时了解和掌握学院学生思想动态，更好的开展学院学生思想政治教育工作，6月23日上午，信息学院召开了2010学年第二学期期末大学生思想动态研判会。 研判会分析了近期学院思政动态，并指出只有及时掌握学生思想动态，认真分析研究不同时期学生思想状况，才能有效地做好大学生思想政治教育工作，确保学校的安全与稳定。针对学期末学生思想政治工作，研判会提出几个要求：一是要进一步加强学生思想动态的了解，通过各种渠道及时掌握学生思想状况，充分做好文明离校工作；二是要根据期末时间学生思想的特点，有针对性地开展工作，努力做好诚信教育工作；三是要加强辅导员间的沟通，努力解决学生中存在的实际困难和问题，把各自的工作做实做好做到位。 （ 文 汪铭铠 ）', 'http://info.jhc.cn/2012/1109/c201a1237/page.htm', NULL, '2017-05-17 14:19:47.865783');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (612, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院稳步推进高层次科教研预研项目研究', '为了提升学院科教研整体水平与教师理论联系实际的能力，更好地为地方经济社会发展服务，5月27日，信息学院召开学院高层次预研项目评审会，学院学术委员会和院外4位专家参加评审。 据悉，信息学院按照“方向相近、便于指导、团队协作”的原则在学期初组建了12个科教研团队，团队模式很好的促进了学院科研活动的开展，此次高层次预研项目申报共收到学院70多项申请，几乎达到每人一项。经过专家认真评审，最终确定省级预研立项课题5项、厅级预研立项课题12项。学院将根据预研项目管理办法，给予立项的预研课题配套经费支持，同时与项目负责人签订责任书，制定与落实预研项目实施计划，以此来有效推进预研项目落实并提升学院高层次科研项目研究水平。 （ 文 王成福 ）', 'http://info.jhc.cn/2012/1109/c201a1233/page.htm', NULL, '2017-05-17 14:19:47.88801');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (613, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '牢记入党誓词 喜迎党的生日', '6月28日，在建党90周年前夕，信息学院举行迎“七·一”新党员入党宣誓仪式。学院党总支书记朱登胜、各学生党支部书记和学院全体新党员参加本次仪式。 在阳光明媚的金湖畔，伴着庄严的《国际歌》，49名新党员面对鲜红的党旗庄严宣誓，愿意为党的事业奋斗终生。仪式上，朱登胜还代表学院党总支为新加入党组织的同志发放了入党生日贺卡，他希望新党员们在今后的学习、工作、生活中时刻牢记誓言，加强党性修养和理论知识的学习，充分发挥党员的先锋模范作用，以饱满的热情投入学习与工作，实现自己的人生价值。 据悉，信息学院党总支将以庆祝建党90周年和创先争优活动为契机，开展系列党建活动，以此加强党员队伍建设，以实际行动践行对党的誓言。 （ 文 周荃 ）', 'http://info.jhc.cn/2012/1109/c201a1222/page.htm', 'http://info.jhc.cn/_upload/article/images/24/8d/7d5a4654422fa2283aba4d370e6c/45de54ea-97fa-4bed-b701-f3fc748ece48.jpg|http://info.jhc.cn/_upload/article/images/24/8d/7d5a4654422fa2283aba4d370e6c/da6c6352-0bd7-428d-af7b-7bbc2896626b.jpg', '2017-05-17 14:19:47.910277');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (620, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院举行教师节青年教师座谈会', '为增强青年教工的团组织意识，更好地团结和凝聚青年教工，促使广大青年教工爱岗敬业， 9月8日，信息学院举行教师节青年教师座谈会 会上，团员教师代表发出了师德倡议书。围绕师德话题，团员教师们结合自己的成长经历发表了感言，既有自己的心得体会，也有还存在的疑难困惑。朱登胜书记结合自身经历，指出青年教师要准确定位、明确目标、抓住机遇、利用特长，主动适应教育对象的需要，主动适应学院发展的需要。并强调大家要珍惜在团支部的日子，希望大家能在这一大家庭中快乐成长。 本次教工团支部为5位超龄团员举行了离团仪式并赠送了离团纪念品。同时，有一位新团员加入，为团支部注入了新的血液。 （ 文 汪铭铠 ）', 'http://info.jhc.cn/2012/1109/c201a1195/page.htm', NULL, '2017-05-17 14:19:57.677727');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (614, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院离退休党支部获评金华市“五好”离退休干部党支部', '从6月29日召开的金华市纪念建党90周年大会上传来好消息，根据金市组〔2011〕18号文件，我校信息学院离退休支部被评为金华市“五好”离退休干部党支部，成为教育系统唯一获此殊荣的离退休支部。 据悉，此次全市“五好”离退休支部评比活动，是为了表彰金华市“八婺先锋”创先争优活动中，以“支部班子好、党员队伍好、组织设置好、活动开展好、群众反映好”为目标，坚持以科学发展观为指导，围绕中心，服务大局，充分发挥离退休干部党支部战斗堡垒作用的一批优秀党支部。信息学院离退休支部在创先争优活动中，积极转变党组织的工作理念、方法和机制，不断加强和改进支部建设和思想政治建设，充分发挥离退休党员的先锋模范作用，为“和谐信息”建设发挥了积极作用，此次获评“五好”支部，将进一步激励离退休支部珍惜荣誉，继续发挥示范带头作用，争取为学校和学院发展做出更大贡献。 （ 文 王伟斌 ）', 'http://info.jhc.cn/2012/1109/c201a1218/page.htm', NULL, '2017-05-17 14:19:47.932422');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (615, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '金华婺城区农村水电工培训在我校信息学院开班', '8月23日，金华市婺城区农村水电工培训班开班仪式在我校信息学院举行，来自婺城区各乡镇的66名水电工将参加本次为期10天的培训班学习。 金华市婺城区农村水电工培训班是为了提升农村用电用水安全水平，服务婺城区新农村建设，由政府出资的免费培训项目，旨在让培训班学员学习水电工工种的基础知识、职业要求、职业意识、职业态度等内容，提升他们作为一名农村水电工的业务技能和良好的职业道德和职业素养。 为了保证此次培训的质量，信息学院前期做了大量准备工作，提供最好的培训场所确保优质培训环境，选派了最优秀的教师参与培训任务，确保培训效果和质量，同时在后勤保障上做了大量细致工作，此次培训也得到了婺城区农培办的充分认可，双方以此次培训为契机，建立了很好的合作关系。 （ 文 单天德 马汝星 ）', 'http://info.jhc.cn/2012/1109/c201a1215/page.htm', NULL, '2017-05-17 14:19:47.954708');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (616, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院暑期计算机专业群改造工作有序推进', '8月25日，信息学院召开计算机专业群改造工作暑期第二阶段情况汇报会，围绕金华高新IT学院运行、CDIO工程教育改革、计算机应用技术专业校级特色专业建设和专业资源库建设等工作，各子项目负责人汇报展示了暑期阶段性任务完成情况，并对存在的问题进行了深入的分析交流。  据悉，“以深化金华高新IT学院建设和CDIO改革等为基础，全面改造计算机专业群”是信息学院2011年度的重点工作。为此，学院专门成立领导小组以落实此项工作的推进。利用暑假时间，学院专门制定工作计划，采用集中办公、阶段汇报等方式有序推进计算机专业群改造阶段性工作的有效开展。目前在专业课程一二级项目开发、教学资源库建设、网络化课程实施平台开发等方面已积累了一定的经验和成果，专业改造初见成效，为继续深化改革力度和提升进度作了有力保障。 （ 文 邱晓华 ）', 'http://info.jhc.cn/2012/1109/c201a1211/page.htm', NULL, '2017-05-17 14:19:47.977073');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (617, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院召开2011年学生工作会议', '8月26日，信息学院召开主题为“以学风建设年为契机 全面提高学生综合素质”的学生工作会议。学工部部长吴寿松、学院领导及全体班主任辅导员参加会议。会议由学院党总支书记朱登胜主持。 会上，职能部门负责人、优秀班主任、辅导员代表围绕“服务学生成长，服务学生成才”的学生工作主题，各抒己见，做了主题发言。吴寿松从加强校园维稳工作和学校育人成才高度，指出要以思想政治教育为根本，守住安全稳定底线、学风建设主线和制度建设防线，瞄准学生的健康安全、成长成才、就业发展三个点，切实做好新时期的学生管理工作。校十佳班主任代表方勇和徐赛华老师从作为一名普通班主任角度，介绍了小处着手，大处着眼，如何铸造“学习型”班级的成功经验和如何用爱去帮扶学生成长，促成学生成才的工作理念。 会议围绕代表们主题发言，从学院学风建设、安全稳定工作、综合素质测评改革等方面进行了分组讨论，形成了要从提高教师自身素质、端正工作态度、优化工作方法来提高学生管理工作水平的共识。  ( 文 汪铭铠 )', 'http://info.jhc.cn/2012/1109/c201a1206/page.htm', NULL, '2017-05-17 14:19:57.552564');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (618, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '我校信息学院承办2011年金华市网络管理技能大赛', '近日， 2011年金华市网络管理技能大赛操作技能比赛在我校信息学院组网与调试实训室举行。本次比赛由金华市人力资源和社会保障局、金华市总工会联合主办，我校信息学院承办，共有27名来自各县市区的选手参加。 据悉，本次比赛只在提高我市网络管理人员的技能水平，内容包括理论知识和操作技能两部分，按两者总成绩决出名次，其中信息学院承担操作技能比赛的组织与评分。操作技能比赛具体项目包括网络组建及网络互连、网络服务器系统配置。比赛每人一组，每组配备一名裁判，要求选手在120分钟内按题目要求独立完成服务器、交换机、路由器的配置，得分高且用时少者获胜。比赛结束后各组裁判按照统一评分要求打分，做到公平公正。 经过一天的激烈角逐，选手们纷纷表示通过技能比武活动达到了互相交流，共同提高的目的。 （ 文  朱墨池 ）', 'http://info.jhc.cn/2012/1109/c201a1202/page.htm', NULL, '2017-05-17 14:19:57.634072');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (619, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院举行2011级成教新生开学典礼', '9月5日上午，信息学院举行2011级成教专科新生开学典礼暨入学教育。学院党总支书记朱登胜、各职能部门负责人及全体成教新生参加开学典礼。 开学典礼上，朱登胜对同学们来院学习表示真诚的祝贺和诚挚的欢迎，他介绍了我院现有师资、教学条件等情况，希望新生们要树立学习信心、加强自我管理、珍惜大学生活、调整学习方法、注重个人修养，为自己走向成功夯实基础。各职能部门负责人就安全管理、教学管理、学籍管理等方面内容做了介绍。 开学典礼结束上，针对学籍注册、课程安排、学习纪律、安全稳定等方面的规定与要求对新生们作了入学教育 （ 文 单天德 俞冬丽）', 'http://info.jhc.cn/2012/1109/c201a1198/page.htm', NULL, '2017-05-17 14:19:57.655348');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (621, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学子用志愿者服务度过“中秋节”', '9月12日是我国传统的中秋佳节，信息学院志愿者团来到金华市动物园开展“中秋狂欢夜”志愿服务活动。从下午3点开始，同学们分成若干小组奔赴各个服务站点进行志愿服务。志愿者们协助工作人员工作，极大减轻工作人员压力，方便游客参观。 2010级志愿者小赵看着游客对自己的评价高兴的说：“这是我进入大学以来参加的第一个志愿服务活动，虽然有点累，但心中非常高兴，这个中秋节虽然不能和家人团聚但是过的很有意义，我今后还会参加更多的志愿服务活动，为我们的生活环境贡献自己的一份力量。”  志愿活动在晚上10点结束，志愿者们用自己的行动诠释着志愿服务精神。主办方给每位志愿者发放月饼表示感谢和送上中秋的祝福。 （ 文 汪铭铠 ）', 'http://info.jhc.cn/2012/1109/c201a1191/page.htm', NULL, '2017-05-17 14:19:57.700714');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (622, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院召开公寓管理工作现状分析会议', '9月8日，信息学院召开了公寓管理工作现状分析会议。会议就有关信息工程学院公寓管理的卫生、安全等现状进行了分析并对相关应对措施进行了讨论。 会上就信息学院存在公寓卫生优秀率一般、合格率偏低的问题进行了分析和讨论，会上要求，将宿管会职责由原来的“管学生”向“服务学生”转变，还就公寓卫生管理达成了共识。同时，针对上学期公寓发生的两起起火事件做了详细的剖析，强调了消防安全的重要性和严肃性。会议布置了《公寓用电安全责任书》的签署以及安全宣传工作。 （ 文 汪铭铠 ）', 'http://info.jhc.cn/2012/1109/c201a1187/page.htm', NULL, '2017-05-17 14:19:57.722164');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (623, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院开展感恩教师节活动', '“老师您辛苦了，祝您节日快乐。”9日早上，在每一堂上课前，班上的学生自发的全体起立，向老师齐声问候。 在第27个教师节来临之际，信息学院学生会向全体同学发出以一种最朴实的方式来报答老师们恩情的倡议：深深鞠躬，问一声“老师，您辛苦了！”，送上自己的感恩之情；完成一份最棒的作业，积极参与每一次课堂讨论，送上一个最好的自己。 此外，院团委也在团委的微博上发起了“给老师的一句话”活动，此项活动得到了同学们广泛的支持，大家分分留下了自己对老师节日的祝福，崇敬和感恩之情溢于言表。 （ 文 汪铭铠 ）', 'http://info.jhc.cn/2012/1109/c201a1183/page.htm', NULL, '2017-05-17 14:19:57.744351');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (624, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院举行金华电信招工协商会议', '9月14日，信息工程学院邀请浙江省电信实业集团公司金华市分公司赵凯经理一行5人来学院进行用工协调会。此次协调会议是金华电信年度弱电工程类新募派遣型员工计划的一部分。 协调会上，就本年度电信新募员工人数、工作性质、实习期待遇、录用方案与标准等内容进行了讨论，并针对楼宇、网络、通信3个专业的100多名学生进行了宣讲和发放问卷，为学生们的就业搭建了一个平台。 （ 文 朱墨池 ）', 'http://info.jhc.cn/2012/1109/c201a1179/page.htm', NULL, '2017-05-17 14:19:57.76665');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (625, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院以细致安排做好新生接待准备工作', '一年一度的迎新工作马上开始，为做好新生报到接待工作，信息学院专门成立了迎新工作领导小组，负责协调学院迎新工作。 9月13日，领导小组专门召开迎新工作协调会，协调和布置工作，切实做好新生接待、军训、始业教育等安排工作；9月14日，学院召开新生班主任会议和新生班主任助理会议，充分落实各项工作安排；9月15日，召开迎新志愿者动员会，充分调到广大志愿者的积极性和能动性。全院上下已经形成高度重视，大家都精神饱满，期待新生的到来。 同时，9月16日中午，学院专门组织班主任助理、志愿者队伍专门对每一个新生寝室进行走访和核查，确保每个寝室的安全万无一失。同时，也对寝室卫生做最后的核查，给新生一个良好的环境。     虽然17号开始才是新生正式报到日，但今天已经有少部分新生陆续到来。做好充分准备的各个小组为前来报到的新生作出了热情的接待，并安排好新生的食宿工作。截止16日下午3点，已经接待新生62人。 目前，学院新生接待工作已全部准备就绪，全体接待人员正以饱满热情迎接2011级新生的到来。 （ 文 汪铭铠 ）', 'http://info.jhc.cn/2012/1109/c201a1175/page.htm', NULL, '2017-05-17 14:19:57.789126');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (626, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '我校自动控制与信息工程研究所举行2011年度产品开发研讨会', '9月15日，我校自动控制与信息工程研究所举行2011年度产品开发研讨会。校党委副书记、研究所所长余党军教授，校科技处负责人，信息学院部分教师参加会议。会议由副所长王成福教授主持。 本次研讨会围绕应用项目研究、产品开发和科技服务工作展开，旨在进一步拓展教师科研视野和提升科研水平。来自校企合作企业金华利诚信息技术有限公司和浙江师范大学的专家学者就项目开发的成功做法与技术经验以及“如何做横向课题”等内容做了精彩介绍，并与信息学院教师进行了交流讨论。研讨会很好的促进了信息学院教师对新技术应用、横向课题研究等方面的认识。 据悉，近年来，自动控制与信息工程研究所结合地方经济社会发展需求，凝练研究方向，不断提高科研水平和社会服务能力，在自动控制技术应用、嵌入式系统研究与应用、计算机信息管理系统等领域取得了较为丰富的成果。     （文 俞冬丽 王伟斌 ）', 'http://info.jhc.cn/2012/1109/c201a1165/page.htm', 'http://info.jhc.cn/_upload/article/images/af/00/6fe341b9490c91fe2407ca628370/7c929d45-e474-4a60-8aec-e0280db49e7e.jpg|http://info.jhc.cn/_upload/article/images/af/00/6fe341b9490c91fe2407ca628370/9657e4fe-cc5d-4f61-977a-e1bc91d085bb.jpg', '2017-05-17 14:19:57.811231');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (632, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '实践总结相结合 群策群力谋发展', '近日，民盟金职院支部召开2011年上半年工作总结及下半年工作安排会议，全体支部委员参加了会议，会议由王成福主委主持。 上半年，民盟金职院支部在加强自身学习和组织建设、参政议政方面成效显著，特别是在庆祝中国共产党建党90周年和纪念民盟成立７０周年活动，参加金华市政协关于市区备用水源的调研，全体盟员参加的“提升团队凝聚力，考察校外实习基地”活动等方面积极工作，成绩喜人，为学校发展和金华经济社会建设做出贡献。会上，副主委金宏伟传达了省民盟组织工作会议的精神与重要内容，为支部下一步发展指明了方向。组织委员郑小兰介绍了新加波职业教育产业化的成功经验，为教师做好本职工作提供典范。 会上，委员们畅所欲言，认真分析工作得失，集体研究并提出支部下半年工作重点，主要将在加强管理创新理念学习，重视自身组织建设，时刻关心社会热点难点问题，加大信息报送力度，开展市区三大科技园区电子信息产业发展状况调研，做好本职工作，组织团队郊游健身活动，增强团队凝集力，为盟组织的建设与发展集思广益。 ( 文 杨海维 王伟斌 )', 'http://info.jhc.cn/2012/1109/c201a1134/page.htm', NULL, '2017-05-17 14:20:09.445244');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (627, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院举行2011级新生开学典礼', '9月19日，信息学院举行2011级新生开学典礼，学院领导、各部门负责人及2011级全体新生参加。开学典礼由副院长陈海荣主持。 在雄壮的国歌声中，开学典礼正式开始。龚永坚院长在开学典礼上致辞，他首先代表学院师生对2011级新生来院学习表示热烈的欢迎和衷心的祝贺，并简要介绍了学院基本情况，以便新生们尽快了解学院，融入学院大家庭。在致辞中，龚永坚从培养定位、专业设置、就业特色等方面分析了高职教育的办学优势，以此鼓励同学们脚踏实地、刻苦学习，使自己成长为一名具备现代职业技能素养的优秀高职人才。最后，龚永坚对新生们提出了几点希望：珍惜大学美好时光，勤奋学习，努力实践，学会做人、学会学习、学会做事，提升自己的综合素质，尊敬师长，互爱互助，珍重同学情义。 开学典礼上，教师代表、老生和新生代表分别发言，他们都希望新同学们把握来之不易的大学学习机会，树立远大理想，实现自己的人生价值。各部门负责人还分别就教学管理、学生工作、继续教育等内容做了介绍，为新生们尽快融入大学生活指明了方向。 （ 文 王伟斌 ）', 'http://info.jhc.cn/2012/1109/c201a1157/page.htm', 'http://info.jhc.cn/_upload/article/images/72/90/86fa8af44978a70068eb75badac1/a9e76f52-e054-495e-bf5a-b502263ecdb4.jpg|http://info.jhc.cn/_upload/article/images/72/90/86fa8af44978a70068eb75badac1/876683a5-fdb8-4760-9978-6cb6be03bbf2.jpg', '2017-05-17 14:19:57.833395');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (628, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '校领导亲切走访信息学院新生寝室', '9月18日晚，校党委副书记、副院长蒋忠樟，副院长杨艳等一行在信息学院院长龚永坚等陪同下走访了学院部分新生寝室，亲切看望2011级新同学。 在新生寝室，校领导与同学们亲切交谈，嘘寒问暖，仔细询问每一个寝室的情况和同学们的个人情况，耐心的回答同学们提出的问题。走访期间，校领导们还关切地提醒同学们开学初期要注意安全，尽快融入大学生活，希望同学在大学阶段努力学习，积极实践，为自己的职业生涯夯实基础，度过一段美好的大学时光。    （ 文 汪铭铠 ）', 'http://info.jhc.cn/2012/1109/c201a1153/page.htm', 'http://info.jhc.cn/_upload/article/images/10/12/f6fcf96a43478542a05e031aebf9/a93bc2f5-ef7d-4a6e-9b61-94cdec04ef03.jpg', '2017-05-17 14:19:57.855773');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (629, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院邀请企业人员上职业生涯规划课程', '9月22日，一改以往由校内教师讲授职业生涯规划课程的惯例，信息学院企业信息化事业部邀请校企合作企业——金华利诚信息技术有限公司培训师徐卫芳老师来院为计算机专业2010级学生上了一堂生动的职业生涯规划课。 课堂上，徐卫芳老师结合公司的实际情况，用真实案例给大家讲述了如何用积极心态来面对学习工作中的困难、如何在职业中做事先做人的道理等内容。通过企业真实案例的讲述与分析，同学们表示，企业老师的讲授，学习该课程的思路更加清晰了，对职业生源规划也更加有实际认识了。对于这种新的授课方式，同学们认为不仅感受了企业文化，又能学习到企业对员工的真实要求，希望有更多这样的课程。     据悉，邀请企业讲师来校讲课是金华高新IT学院下半年开展的企业文化进校园活动的前奏。为提升学生对职业生涯规划的认识，提高学习的主动性，信息学院将对企业教师参与职业规划课程进行研究，以提高该课程的针对性和实效性。     （ 文 楼小明 ）', 'http://info.jhc.cn/2012/1109/c201a1148/page.htm', NULL, '2017-05-17 14:19:57.877904');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (630, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院开展2011级新生专业教育', '为加强新生对所学专业的认识，提高学习兴趣，制定合理的职业规划，9月26日，信息学院各专业有序开展了2011级新生专业介绍活动。 活动中，各专业负责人就本专业的建设情况、课程设置、就业前景等方面进行了说明，着重讲解了专业人才培养计划与学生素质培养目标。计算机类专业特邀金华高新IT学院理事单位——金华智联信息技术有限公司项目经理徐舜先生作了一场精彩的报告。徐舜结合自身工作经历，详细介绍了相关行业的人才需求现状与发展前沿，生动阐述了端正专业思想，明确学习目的，扎实专业技能的重要性，并围绕为谁学习、怎么学习、如何行动等问题与同学们进行了充分的交流。 专业介绍作为新生入学教育的重要一课，强化了学生对所学专业的认同感，为他们尽快调整心态，明确专业目标，做好生涯规划打下了良好的基础。同时，邀请高新IT学院理事单位人员来作报告，加强了校企合作，提升了对人才培养目标的共识。 （ 文 陈静泓 楼小明 ）', 'http://info.jhc.cn/2012/1109/c201a1144/page.htm', NULL, '2017-05-17 14:19:57.900066');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (631, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院党校第十五期入党积极分子培训班举行结业典礼', '9月27日，信息学院党校第十五期入党积极分子培训班举行结业典礼。学院党校校务委员会成员、党校第十五期培训班班主任及全体学员参加典礼。 本期培训班223名学员在为期6个月的学习中，顺利完成理论授课、社会公益活动、优秀共产党员影片欣赏、暑期社会实践、结业论文撰写、结业考试等各个环节的教学与考核，最终有194名学员顺利结业，其中计算机1001班郭滨珊等9名同学被授予党校第十五期入党积极分子培训班优秀学员荣誉称号。 结业仪式上，党校校长朱登胜代表学院党总支、学院党校对获得“优秀学员”称号和圆满结业的学员们表示衷心的祝贺，他希望同学们要本着为人民服务的宗旨，发扬先辈优秀传统，舍小我，顾大我；今后要继续加强学习实践马列主义、毛泽东思想、邓小平理论和“三个代表”重要思想，扎实贯彻落实科学发展观，提高专业技能，努力回报社会。 （ 文 周荃 ）', 'http://info.jhc.cn/2012/1109/c201a1137/page.htm', 'http://info.jhc.cn/_upload/article/images/14/d7/9c05861649e7b398de6a8750e54a/44cf0e7a-9c62-4b24-8c7e-d5d7bb818284.jpg', '2017-05-17 14:20:09.394009');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (633, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '天意怜幽草，人间重晚晴', '岁岁重阳，今又重阳。9月28日，民盟金职院支部委员在主委王成福率领下，走访慰问了吕积川、吴德培、李程鹏、陈今文、陈瑞卿等5位离退休盟员。 “九九”重阳节，传统敬老日。委员们走进离退休盟员的家里，或者来到离退休盟员住院的病榻前，询问他们的身体状况，关心他们的生活情况，汇报民盟支部的工作，向老盟员们表示节日的问候，并送上慰问品，祝愿他们健康长寿，并恳请他们给支部建设发展献计献策、提出宝贵建议与意见。离退休盟员十分感谢组织的关怀，表示要为民盟教育事业发挥余热。 （ 文 金宏伟 王伟斌 ）', 'http://info.jhc.cn/2012/1109/c201a1127/page.htm', 'http://info.jhc.cn/_upload/article/images/d9/58/f9a217ce49a1a4ccdb8c38ddb1fb/a32af724-7efa-4e7b-bcf7-428e7102feb7.jpg|http://info.jhc.cn/_upload/article/images/d9/58/f9a217ce49a1a4ccdb8c38ddb1fb/7f9427a3-a537-4baf-83be-8551e1fc611e.jpg', '2017-05-17 14:20:09.467365');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (634, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '展专业技能，迎新生入学', '9月29日，信息学院一年一度的迎新晚会在东实训楼广场举行。本届迎新晚会作为新生始业教育的一部分，将促进新生融入大学，适应大学的晚会目的表现的淋漓精致。虽然天空中飘着毛毛细雨，但是大家的热情丝毫不减，随着新生班主任合唱节目和新生代表才艺展示，晚会气氛达到了高潮。 本次晚会亮点纷呈，首次实现了全程摄像视频直播效果，引来了现场观众的一致好评。更值得骄傲的是，现场的所有效果都是由我们学生自己实现的。摄像机是我们楼宇专业的学生用监控摄像头改装的，现场抽奖软件是我们自己学生设计调试的，甚至于现场的电源线也是我们电子的学生自己亲手接的。本次晚会不仅起到了迎新效果，也让我们学生学习的技能有用武之地，极大的鼓励了学生的学习热情，同时也能通过现场的特效设备引起新生对专业的学习兴趣。 （ 文 汪铭铠 ）', 'http://info.jhc.cn/2012/1109/c201a1124/page.htm', NULL, '2017-05-17 14:20:09.489762');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (635, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院学生专业实习载誉归来', '9月30日，经过一个月的企业生产实习，信息学院应电101班同学顺利完成“电子产品装接”实习任务，载誉归来。 对于这次实习，来自金华市灵声电子有限公司的评价是“你们学校是不是将最优秀的学生安排给我们？他们个个表现很优秀！”；来自兰溪市电光源有限公司的评价是“学生很安心在企业，每个学生都很努力！”；金华市南天计量科技有限公司尹华军说，“这次有学生主动提出加班，是过去所没有的，他们认真钻研的精神让我感到近几年学生的实习表现越来越好，学生与企业的融合越来越好了，现在我们有信心在实习中增加企业的管理方式。”。 企业对学生实习的溢美之词，是同学们努力付出的结果。实习中，应电101班同学发扬不怕苦、不怕累精神，在“单调乏味”的生产一线安心工作，坚守自己的岗位。他们中73%的同学吃住在工厂，一个月安心在厂里，服从企业的安排，同学都把吃苦当成是一种磨砺，他们说，“没几天时间，坚持一下就过去了”，朴实无华的语言中充份展示出学生对个人成长真诚、从容面对的勇气与决心。 学生的实习，学院老师也付出了辛勤劳动。应电101班主任郑惠群老师，对班级管理严格中带有慈母般的关切，深受学生爱戴，这次实习，她忙前忙后，细致安排，在学生思想引导等方面起到了积极的作用。应用电子技术专业后续还有三个班实习，相信应电101班的敬业态度与精神一定能带动其他班同学克服困难，努力在企业一线奋斗，深入了解企业，了解社会，珍惜劳动机会，体会工作的辛苦，扎扎实实地上好人生的重要一课，实现从学生角色向企业员工角色的转换。 (  文 余红娟 )', 'http://info.jhc.cn/2012/1109/c201a1115/page.htm', 'http://info.jhc.cn/_upload/article/images/e0/ea/e8d8c2524697a6afbc2b23c7d83e/9fd3c1d1-1150-4e4b-9fb3-e23e2e59a388.jpg|http://info.jhc.cn/_upload/article/images/e0/ea/e8d8c2524697a6afbc2b23c7d83e/abeecf93-3006-4cac-aa1b-3d54ee461bb1.jpg|http://info.jhc.cn/_upload/article/images/e0/ea/e8d8c2524697a6afbc2b23c7d83e/958d4130-7302-4c7f-8206-d3b3bea11f7c.jpg', '2017-05-17 14:20:09.511912');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (636, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院第二届NIIT订单班开班', '10月8日，信息学院第二届NIIT订单班开班典礼在NIIT金华软件培训学校举行。NIIT金华软件培训学校董事长陈小玲、校长仲煜、信息学院党总支书记朱登胜、教师代表及订单班全体学员参加开班典礼。 开班典礼上，校企双方领导都对NIIT订单班学生寄予了殷切希望，希望他们认真学习，在订单班努力实践校企合作的新模式、新方法，结合校企双方的优势，取长补短，为自身发展而努力奋斗。 据悉，NIIT以先进的教育理念、教育模式和教育手段培养了大批软件人才，在全世界IT行业具有很高的知名度。本届NIIT订单班是校企双方本着优势互补、资源共享、互惠多赢的原则，为共同培养IT服务外包紧缺型人才而组建的。本届NIIT订单班共有20名学员，他们将在印度NIIT金华培训学校进行三个月的准职业培训和学习，考取NIIT认证，并最终实现就业，这是信息学院为培养金华重点发展的软件外包产业人才的一条新途径。 （ 文 楼建忠 ）', 'http://info.jhc.cn/2012/1109/c201a1111/page.htm', NULL, '2017-05-17 14:20:09.534332');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (637, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院力促《计算机文化基础》课程教学改革', '为统一思想认识和充分利用现有教学资源，进一步提高计算机基础分层教学效果，10月10日，信息学院召开了全校性《计算机文化基础》课程教研活动，本学期有《计算机文化基础》授课任务的全校兼任、兼课和专任教师一同参加了本次活动，会议由陈海荣副院长主持。 会上，来自浙江师范大学的吴建平老师介绍了提高省计算机等级考试通过率的教学经验，讲解了计算机基础考试系统的操作要领，并着重介绍如何利用系统掌握学生的学习水平并做好有的放矢地辅导，从而提高计算机等级通过率。陈海荣在会上提出计算机基础教学应更好地培养学生信息素养，在充分利用好网络教学平台的基础上，进一步提高全校计算机等级通过率。学院相关部门就计算机文化基础课程授课效果考核、考前培训等做了介绍。 信息学院为提高授课和学生自主学习效果，自2010年7月起与浙师大联合开发计算机基础网络考试平台，该平台最大的优势在于能分析测试结果，为教师、学生的教学能做出明确指向，有利于教学效果提升。 ( 文 邱晓华 )', 'http://info.jhc.cn/2012/1109/c201a1106/page.htm', NULL, '2017-05-17 14:20:09.5564');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (638, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院多措并举提升教师高层次项目研究水平', '为切实提高省自然基金项目的申报质量，提升教师的科研水平，10月12日下午，信息学院召开省自然基金申报研讨会，浙师大数理信息学院模式识别研究所所长郑忠龙博士应邀作学术报告，学院各科研团队负责人、骨干教师参加了研讨会。 会上，郑忠龙结合自身主持三项国家自然科学基金项目以及其它相关项目的经验，针对今年自然基金申报的总体要求、政策变化、申报要求等作了介绍，并就申报材料撰写中研究内容、撰写要求、研究方法、技术路线等问题进行了详细指导，他鼓励大家积极大胆申报、认真精心准备、注重创新、体现专业素质，并就有关的课题思路及存在的问题同与会人员进行了深入的交流探讨，提出了许多宝贵意见。 据悉，信息学院为提升教师高层次项目研究水平，通过组建科研团队、出台奖励政策、利用研究所资源开展学术活动等多项举措，有力地提升了教师科学研究水平。 （ 文 陈静泓 ）', 'http://info.jhc.cn/2012/1109/c201a1103/page.htm', NULL, '2017-05-17 14:20:09.578631');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (639, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '第六届全国信息技术应用水平大赛预赛在我校信息学院举行', '10月16日，第六届全国信息技术应用水平大赛金华职业技术学院赛区预赛在信息学院举行，共有来自全国的389名选手分别参加了嵌入式开发与应用、单片机开发与应用、office办公自动化高级应用、C语言程序设计及3G移动通信技术等5项个人赛。 第六届信息技术应用水平大赛由教育部教育管理信息中心主办，报名人数满50人的参赛学校可自行安排组织预赛，比赛包括计算机应用技术、计算机辅助技术、电子信息技术与移动互联技术等4个模块的个人赛，以及电子系统设计、Android应用开发等团体赛，预赛采取统一时间网上在线考试、系统自动阅卷，实时出成绩的方式进行，成绩优胜者将参加随后进行的复赛或决赛。 本次比赛为学生提供了展示个人信息技术应用水平的平台，进一步培养了学生的创新动手能力，提升了学生的信息素养。 （ 文 赵云 ）', 'http://info.jhc.cn/2012/1109/c201a1096/page.htm', 'http://info.jhc.cn/_upload/article/images/41/db/f288b60c446d8a5a55537f49902e/da46660e-633e-446b-9461-e9e9076fb2d3.jpg|http://info.jhc.cn/_upload/article/images/41/db/f288b60c446d8a5a55537f49902e/3e526133-bd78-42cf-a87d-90024183bc07.jpg', '2017-05-17 14:20:09.600992');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (647, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '江西现代职业技术学院来我院作经验交流', '10月26日，江西现代职业技术学院电子系王连英、冯小玲等5位老师来我院就电子类专业建设作经验交流。副院长王成福、电子类相关专业主任等参加交流座谈。 针对岗位应职能力与可持续发展能力培养关系，如何关注毕业生对企业的忠诚度，通识教育与技能培养关系、专业的社会服务能力如何培养等几个方面的问题，宾主双方做了深度交流。我院老师结合自身国家示范建设经验，针对这些问题作了经验介绍，江西现代职业技术学院的老师则结合自身在骨干院校建设中遇到的困难和问题提出了一些疑问，双方做了交流探讨。 来访的老师还参观了我院实训楼，了解了我院实训环境和学生实训等情况。 （ 文 王伟斌 ）', 'http://info.jhc.cn/2012/1109/c201a1056/page.htm', NULL, '2017-05-17 14:20:21.271126');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (640, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院电子产品开发部工会组织野外拓展活动', '10月19日，金秋送爽，信息学院电子产品开发部工会组织了一次别开生面的工会活动——CS野外拓展。除了工会小组全体成员积极参加外，工会小组还邀请了金华南天计量技术有限公司、阿西尔电子等校企合作比较紧密的企业代表，以及浙师大信息学院的老师一起参加了活动。 在CS野外拓展活动现场，大家戎装革履，穿戴整齐，认真听从教官的指示，分组活动。活动在轻松欢快的氛围中开展，紧张刺激的拓展活动不但联络了大家的感情，同时也增强了大家的团队协作意识，促进了团队的融合发展。 活动之余，老师们还与企业代表们一起讨论了学生生产实习相关的管理问题，进一步促进学校与企业之间的交流。这种活动形式受到了大家的一致好评。 （ 文 张莹 ）', 'http://info.jhc.cn/2012/1109/c201a1089/page.htm', 'http://info.jhc.cn/_upload/article/images/9e/30/768f189741be9c1a66676680cd1a/d58fe110-2cd3-488d-b7a6-3a2282947b80.jpg', '2017-05-17 14:20:09.623139');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (641, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '浙师大余水宝教授应邀来我院作“大学生涯与职业能力培养”专题报告', '10月19日晚，为了引领和指导刚刚步入大学生活的学子们走好大学三年路，激励他们迎接挑战和勇于创新，受信息学院龚永坚院长的邀请，浙江师范大学教务处副处长、浙江省高校电气信息教指委委员余水宝教授来我院师生作“大学生涯与职业能力培养”的专题报告。 基于当今社会IT技术行业对人才素质的新要求，余处长从“认识自我、规划自我、实现自我、竞赛与成才”四个方面，全面阐述了大学生涯与职业能力培养的方方面面。结合自身三十余年的育人经历，余教授在规划自我中提出大学生要立志做“6P”素质的人才，即“人品、文凭、水平、作品、产品、精品”； 在实现自我中余教授要求大学生“夯实基础注重‘实’，学好专业强调‘用’，强化技能讲究‘精’，培养意识苛求‘新’，持之以恒力争‘久’”；要珍惜学校的学习环境与学习条件，不仅要学好教学计划规定的学习任务，还应利用课余时间自主学习，自主创新。余教授通过自己几十年来在IT技术行业所做的创新尝试与学生创新意识培养的经验，鼓励师生要善于发现现有技术和产品的不足，勇于改进产品的性能，勇于尝试前人没做过的小发明；余教授还要求积极参加各级各类电子设计大赛和技能大赛，他说“电子设计大赛是强化专业技能、培养创新意识、检验品质毅力、实现作品产品、促进成才成功的最佳途径”，激励学生树立信心立长志、早拜师、苦学艺、勤实践，从大一开始，从现在做起。 本次报告由信息学院王成福副院长主持，在校实验剧场历时一个半小时的报告中，电子专业群的老师和400余名学生认真聆听，气氛活跃，效果明显，会后就有学生要求余教授留下联系电话，次日还有学生找到浙师大登门求教。电子产品开发部的全体老师表示将对本次报告的精神作好落实，吸收大一新生参加电子设计协会，组织学生定期开展课外创新学习活动，为好学而勤奋的学生提供一个良好的自我发展平台。 （ 文 余红娟 ）', 'http://info.jhc.cn/2012/1109/c201a1086/page.htm', NULL, '2017-05-17 14:20:09.645387');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (642, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '访绍兴古城忆辛亥岁月，扬先烈精神塑民族脊梁', '时值建党90周年，又逢辛亥革命一百周年，为了更好的缅怀和记忆这些历史，10月22-23日，信息学院教工党员赴绍兴红色圣地，开展了“访绍兴古城忆辛亥岁月，扬先烈精神塑民族脊梁”党员活动。 怀着对革命先烈的无限敬仰，教工党员们走进革命圣地，体会一个个情景记载的那段历史。鲁迅故居，再现了三味书屋、百草园、鲁镇的原貌，大量的手稿、实物、模型，真实完整地反映了鲁迅“横眉冷对千夫指，俯首甘为孺子牛”的凌然一生。周恩来故居，珍贵的历史文物、文献、照片，生动形象地再现了周恩来总理为人民服务的革命人风采。秋瑾故居，则还原了这位“鉴湖女侠”当年读书生活与开展革命活动的场景，展现了巾帼英雄不朽的风姿。越中名士苑则以石雕的形式，尽现绍兴历代名人风采。 重温辛亥历史，追寻先辈足迹，红色之旅使全体教工党员对革命历史有了更深入的解读，增强了使命感与责任感。参观后，大家纷纷表示，要继承先烈遗志，立足教育本职，在当前创先争优活动中，兢兢业业做事，勤勤恳恳做人，服务学校，服务师生，为学校的发展添砖加瓦，为建党90年奉献自己的力量。 （ 文 陈静泓 ）', 'http://info.jhc.cn/2012/1109/c201a1079/page.htm', 'http://info.jhc.cn/_upload/article/images/cb/82/eb4402554faea7ad05cf2fa2b989/a98239c7-ac50-4daa-909a-5a045afeb4e8.jpg', '2017-05-17 14:20:09.66768');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (643, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院通信支部开展纪念辛亥革命100周年党员教育活动', '10月24日晚，信息学院通信与自动化专业学生支部开展了“纪念辛亥革命100周年”党员教育活动。 通过介绍学习，全体支部成员重温了辛亥革命这一伟大历史事件，并全面详细地了解了辛亥革命爆发的原因、历史背景、革命的经过以及重大影响，学习了胡锦涛主席在辛亥革命百年纪念大会上的讲话精神。随后，党支部成员观看了电影《辛亥革命》，感受了100年前以孙文为代表的老一辈革命家为了救亡图存而奉献一生的爱国情怀。再现了辛亥革命的先驱们，为中华民族的伟大复兴百折不挠、前赴后继，为了黎民百姓的幸福，付出鲜血与汗水的历史。 通信支部要求全体学生党员认真学习、深入思考，从胡锦涛总书记的讲话中，提高自身思想认识；同时号召全体学生党员要不断加强理论学习，努力增强自身的党性修养，成为一名优秀的中国共产党党员。会后，支部成员纷纷表示，要遵守学校的制度和纪律，提高思想政治觉悟；认真学习专业知识，提高专业技能，努力做同学们的榜样，主动参与校园、社会实践活动，为构建和谐校园贡献自己的力量。 （ 文 汪铭铠）', 'http://info.jhc.cn/2012/1109/c201a1073/page.htm', 'http://info.jhc.cn/_upload/article/images/23/e5/87bb191b44ceb63cd012be027b21/eabbd191-3648-44fd-99bf-f747bdfffb90.jpg', '2017-05-17 14:20:09.6897');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (644, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '绿源集团来信息学院招聘电子群专业毕业生', '10月25日，绿源集团来我院招聘电子群专业毕业生。本次招聘岗位主要是生产管理线长储备约50名，要求毕业生具有敬业负责、开放合作、高效执行、关注品质、主动学习的品德：具备团队管理、现场管理、辅导培养下属、改进创新、成本控制的管理才能；具有较强的操作技能水平以及现场处理及解决问题的能力。 绿源集团将对招聘的线长储备进行二个阶段的内训，第一阶段50名生产管理线长储备单组一条生产线，让学员系统学习装配技能与管理技能，进行自我管理，同时开放绿源网络大学学习基础管理能力、有效沟通技巧、班组长理论培训等让学生系统学习绿源文化，通过理论和实操考核提高专业技能，第二阶段，让学员分散到各条线上，学习培训现场处理和解决问题的能力以及团队管理能力，同时通过绿源网络大学培训OJT宝典-部属培育与工作教导方法、直线经理人的四项修炼等，第二阶段的理论和实操考核后学生即可转为正式员工，融入集团。 电子产品生产企业生产管理能力培养是应用电子技术专业的核心能力之一，招聘会当天就有四十多学生提交了入职申请，为了让学生有充份的思想准备，集团将报名时间延到11月20日，本次招聘学校和绿源集团都非常重视，在学生实习与就业上绿源集团与学校达成多项合作计划，为推进绿色新能源技术发展，双方都给以极高的重视。 据悉，绿源集团是全球领先的电动个人交通工具的制造商，集团下辖浙江绿源电动车有限公司、绿源电动车（山东）有限公司、福建一洲动力科技有限公司、绿源锂电自行车基地、绿源电动三轮车基地五个主要制造基地，以及多个研究中心和生产实体。现拥有员工3000余名，整车年产能200万辆，电池生产能力500多万节。在国内拥有数千家专卖店，相关从业人员逾万名，精心打造的健康的销售网络正迅速向全国推进，同时以稳健的步伐向国际市场进军。  （ 文 余红娟 ）', 'http://info.jhc.cn/2012/1109/c201a1069/page.htm', NULL, '2017-05-17 14:20:09.712078');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (645, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '开展新技术讲座，提升学生专业素养', '10月27日，校东教1-215教室座无虚席，掌声不断，由信息学院邀请的NIIT高级企培讲师杨天所作的“云计算发展与应用”讲座正在此举行。 杨天毕业于华东政法大学，精通Java（J2EE,J2ME）、.NET、SQL Server、Linux等软件开发工具、数据库和操作系统；曾经担任上海南洋软件有限公司软件工程师、美国博朗上海公司技术经理、上海讯豚信息技术有限公司技术副总裁。本次讲座是信息学院企业信息化事业部为提升学生专业素养，使学生了解和学习当前最新信息技术而特意举办的。杨天结合自身多年的软件开发经验，从云计算机的发展、软件应用、编程语言、开发环境等方面对学生们进行了通俗易懂的介绍，将高端的新技术内容形象又生动地展示在了大家面前。 精彩的讲座，引来了众多同学关注的眼神，许多同学都说，这样的讲座，让自己了解新技术，新知识，很好的开拓了自己的眼界。针对现场调查反映出的学生对从事软件行业意愿比较高，以及此次讲座的热烈反响，信息学院将在下一步继续举办类似的讲座与培训，以不断更新学生对新知识，新技术的了解，提升学生专业素养。 （ 文 楼小明 ）', 'http://info.jhc.cn/2012/1109/c201a1066/page.htm', NULL, '2017-05-17 14:20:21.170675');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (646, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院党校举行第16期入党积极分子培训班开学典礼', '10月27日，信息学院第十六期入党积极分子培训班开学。学院党校校长、党总支书记朱登胜，党校校务委员会成员及232名学员参加了本次开学典礼。 本期培训班经过学生自荐、班主任推荐、党校校务委员会筛选，最终产生了232名新一期党校学员。党校校长朱登胜对全体学员表示殷切的希望和衷心的祝福，他希望学员们牢固树立端正的入党动机，珍惜在党校的学习机会，遵守党校的制度和纪律；认真学习科学文化知识，在学习、品德上体现先进性，在同学中发挥先锋队作用，带领他们共同成长；积极参加校内外社会实践活动，在服务和工作中成长，为创建和谐社会、和谐校园贡献自己的力量。党员代表徐俊杰、学员代表何佳利同学先后发言，向党组织表达了同学们参加党校的喜悦心情和认真学习的决心。 开学典礼上还成立了本期党校培训班团支部，并宣读了党校章程、学员守则、党校优秀学员评定规则和教学课程安排。 （ 文 周荃 ）', 'http://info.jhc.cn/2012/1109/c201a1059/page.htm', 'http://info.jhc.cn/_upload/article/images/8e/67/aee669e849f6bfa0ef1385581aa4/c44003f1-5859-43f0-945f-10c08fdd6929.jpg|http://info.jhc.cn/_upload/article/images/8e/67/aee669e849f6bfa0ef1385581aa4/77cfd0f7-e786-4898-8519-93f09dede7a9.jpg', '2017-05-17 14:20:21.251361');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (648, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '金华高新IT学院挂牌成立', '1月16日，学校举行金华高新IT学院挂牌仪式，这是继众泰汽车学院、皇冠学院之后成立的又一个“校企利益共同体”，并就此揭开政、校、企三方合作、共同培养金华电子信息产业高技能人才的新篇章。      金华作为全国电子商务创业示范城市，09年电子商务交易额300多亿元，已形成金华市高新技术产业园、浙中信息产业园和金东区软件园等三大电子信息产业园，电子信息产业已成为金华市的第二大支柱产业。2009年底，仅金华市高新园区的IT企业就达247家，实现技工贸总收入71.7亿元，同比增长60.04%，2010年1—8月，实现技工贸总收入61.57亿元，同比增长93.92%，初步形成龙头企业带动、中小企业配套的由电子产品制造业、软件与系统集成业、网络与电子商务业等构成的电子信息产业链，对专业人才需求十分庞大。      目前，我校共有IT类专业四个，其中计算机应用技术专业2000年成立，已培养8届1500余名毕业生；电子商务(技术方向)专业2001年成立，已培养7届480余名毕业生；计算机网络技术专业2005年成立，已培养3届260余名毕业生；软件技术专业2008年成立，2011年将有首届毕业生。几年来，分别与5173（金华利诚）、浙江博尚电子、浙江搜富网络、金华金色在线、印度NIIT学校、杭州恒生软件、杭州创业软件等单位共同组建订单班10个，累计培养学生400余人，在订单班企业就业的学生达220余人。  为适应电子信息产业快速发展对人才的需求，为适应新形势下高职教育改革的新要求，按照“政府主导、行业指导、企共参与、学校主体”的理念，学校与金华市经济技术开发区高新技术产业园区、浙中信息产业园、金东信息软件创业园的6家电子信息骨干企业共同成立金华高新IT学院。高新IT学院成立后，将通过政校企三方的共同努力，依据金华电子信息产业发展的特点，围绕IT行业岗位要求，由校企共建校内外实训基地，共搭科教研平台，师资互通，资源共享，通过订单方式联合进行人才培养，努力构筑形成一个“合作办学、合作育人、合作就业，合作发展”的政校企利益共同体。 （ 文 陈海荣 叶 旋）', 'http://info.jhc.cn/2012/1109/c201a1052/page.htm', NULL, '2017-05-17 14:20:21.292045');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (649, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '《金华晚报》报道金华高新IT学院首届运动会', '', 'http://info.jhc.cn/2012/1109/c201a1046/page.htm', 'http://info.jhc.cn/_upload/article/images/85/90/8a26a40f405584eb34c962f46883/1c4666f3-9753-4592-9e41-fea70b60ee5d.jpg', '2017-05-17 14:20:21.312915');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (650, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院召开公寓管理委员会干部交流会', '11月1日，信息学院公寓管理委员会举行成立一周年干部交流会。学工办全体成员、公寓管理委员会和全体楼层长参加。 会上邀请了经管学院总辅导员为与会人员介绍了他们工作运作模式，给予了工作上很大的借鉴意义。会上，学工办吴晓明主任对部门成立一年中的工作表现做了简单分析，督促我们做好各项工作，同时指出，针对不足应坦率接受、及时弥补，并对部门后续工作开展应具备的能力做了目的性的表态。 公寓管理委员会通过这样的沟通和交流的方式，在工作方式中寻找突破点，让学院的公寓建设更健全、更完满。 （ 文 信息学院公寓管理委员会 ）', 'http://info.jhc.cn/2012/1109/c201a1043/page.htm', NULL, '2017-05-17 14:20:21.335177');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (651, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院毕业生受到用人单位热捧', '11月1日，浙江东晶电子股份有限公司、浙江英特来光电科技有限公司和浙江银宇纺织公司举行联合招聘会，招聘信息学院电子类相关专业毕业生。本次联合招聘提供岗位共计120个，要求毕业生应具备爱岗敬业、认真负责的工作态度，过硬的实践操作能力和吃苦耐劳的精神。 此次招聘，三家公司人事主管、部门经理都坐镇招聘会现场，耐心回答了福利待遇、发展前景等同学们关心的热点问题。并对公司历史、发展现状、职业前景都作了详细介绍。招聘现场，同学们报名十分踊跃，当场就有超过160人填写了应聘申请。浙江英特来光电科技有限公司的连经理表示，他们非常欢迎同学们到该公司寻找发展机会，并将给大家提供技术培训、锻炼机会等发展平台。 据信息学院毕业生就业工作负责人介绍，目前已有近20家企业来我院招聘毕业生，今年企业到学院招聘毕业生的时间有所提前，招聘人数也明显增加，毕业生普遍受到用人单位的认可。这些现象说明，学院通过组建金华高新IT学院，探索校企合作新的办学模式，提高人才培养质量，努力为企业提供急需和合格人才等措施发挥了积极作用。 （文  赵倩）', 'http://info.jhc.cn/2012/1109/c201a1039/page.htm', NULL, '2017-05-17 14:20:21.357322');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (652, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院实习工作在学校督导检查中获好评', '11月1日，根据学校安排，校督导处吴仁艳处长等一行来到信息学院学生实习单位——兰溪电光源有限公司，看望应用电子技术专业实习学生，了解学生实习情况。在与实习单位领导与员工就学生实习情况做了交流与沟通，并在实习现场询问学生对岗位认识、工作流程、企业产品质量管理等方面的问题后，检查组人员充分肯定了我院学生在企业实习中乐观向上的工作态度、吃苦耐劳的工作精神。特别表扬了同学们通过探索思考发明新工具，提高生产工作效率的创造性和创新性。 在与企业人员、信息学院师生的交谈中，督导们根据学校要求，对学生实习工作提出了几点期望：一是同学们要学会做人、学会吃苦，在艰苦的岗位上历练斗志，培养工作责任心；二是要善于在岗位中发现问题和寻找解决问题的方法，针对当前大多数企业里以简单重复性劳动为主的特点，督导鼓励同学们发挥电子专业技术专长，学会运用自动化手段去改造企业生产环境，发挥高职学生的聪明才智为制造业服务；三是同学们要学习企业的标准化管理，提高管理能力，体会与思考良好的管理在提高企业生产效益中的作用。 （ 文 余红娟 ）', 'http://info.jhc.cn/2012/1109/c201a1035/page.htm', NULL, '2017-05-17 14:20:21.379509');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (653, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '《中国纺织人才网》报道金华高新IT学院运动会', '10月30日，首届金华市高新IT学院运动会在金华职业技术学院田径场隆重举行。金华开发区高新技术产业局局长伊益亮、金华职业技术学院校领导王振洪、胡野、金华利诚信息技术有限公司总经理池水明、金华智联科技有限公司总经理李晓峰等高新IT企业的领导员工以及金职院信息工程学院师生共7个代表队，350余人参加了本次运动会。 　　本次运动会以“团结、文明、合作、创新”为主题，共设置动感赛跑、飞镖团体大战、8×150米接力等九大集体项目，集中考量团队成员间的协作配合能力；穿针引线、反弹球入筐、实心球后抛等七大个人项目，对参赛选手灵活反应能力要求较高。各运动员纷纷卯足全劲，全力拼搏，经过一番热烈的角逐，各代表队赛出了成绩，赛出了风采。 　　我司智联科技对本次比赛表示了极大的关注与重视，公司员工参赛热度高昂。比赛现场，我司参赛选手从容应战，团结奋斗的凝聚力得到了在场所有与赛选手的肯定。最终，在集体项目中获得了气球接力、动感赛跑等三个第一名，瞎子敲锣、8×150米接力等三个第二名，男团、女团羽毛球和乒乓球亦取得佳绩，荣获三个第三名。个人项目收获也颇丰，四个获得一等奖。最终，我司智联科技拿下团体总分第三名，成绩喜人。 本次活动的举办方金华高新IT学院，系金华高新园区管委会及各IT企业与金职院在科技创新和人才培养等方面建立合作共同体的一次有益探索。本次运动会的举行，良好地搭建了企业与学校双方交流和竞技的平台，在促进双方共融、互动上发挥了积极性的意义。 新闻链接：http://news.texhr.cn/v28875-1.htm', 'http://info.jhc.cn/2012/1109/c201a1030/page.htm', NULL, '2017-05-17 14:20:21.401866');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (654, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院又有一批学生成功就业', '近日从中国电信金华分公司传来消息，信息学院有10名学生被该公司提前录用为实习生，这标志着学院又有一批学生成功实现了就业。 此次学生顺利实现就业，是信息学院与企业积极沟通、紧密配合、严谨合作的结果。本次招聘录用工作要求高、过程严，历经校企沟通、组织宣讲、问卷、报名、初选、专业推荐、面试等环节，最终经过招工单位科学谨慎选择，确定10名入围名单。据悉，入围同学将在本学期参加金华电信的岗前培训、分批企业实习，经过考核合格后将正式走上工作岗位。 （ 文 朱墨池 ）', 'http://info.jhc.cn/2012/1109/c201a1026/page.htm', NULL, '2017-05-17 14:20:21.424255');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (655, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院教师科学研究成果喜报频传', '近日，2011年金华市级科学技术研究计划（工业类）拟立项目进行公示，信息学院成绩喜人，由院长龚永坚主持的《三维手绘汽车造型输入方法的研究》、俞冬丽主持的《高精度实时数据采集与分析系统研发》2个项目获批重点项目立项，另有《基于3G的农村数字广播系统研发》等2个项目获批一般项目立项。 据悉，信息学院2011年加大了对科研工作的考核和奖励力度，通过组建科研团队、出台考核奖励政策等措施，极大的提高了教师参与科研的热情。此次获批金华市科技局2个重点项目立项，是近几年来在该项目上的一次较大突破。加上早前2011年度浙江省自然科学基金项目《微细粉尘荷电凝并机理及其捕集策略研究》（主持人：王成福）、浙江省社科联研究课题《浙江省新型农民培养模式与机制研究》（主持人：王成福）等项目的成功立项，学院今年在科研上的一系列举措初显成效。 （ 文 王伟斌 ）', 'http://info.jhc.cn/2012/1109/c201a1023/page.htm', NULL, '2017-05-17 14:20:21.446359');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (656, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '温州大学物理与电子信息工程学院来我院考察交流', '11月5日，温州大学物理与电子信息工程学院通信工程专业负责人韦文生副教授来我院考察交流。 在对口专业交流中，宾主双方就工学结合的人才培养方案，实验实训室和实训基地建设经验，面向就业的人才培养体系、教学方案、操作方法、师资培养等做了交流探讨。后示范时期学院在专业建设等各方面的辐射作用一直吸引兄弟同行前来参观学习，也很好地促进了学院的对外交流。 （ 文 赵敏笑 ）', 'http://info.jhc.cn/2012/1109/c201a1019/page.htm', NULL, '2017-05-17 14:20:21.469395');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (657, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院开展大学生职业生涯规划知识普及活动', '11月7日，信息学院邀请国家二级心理咨询师俞旭红为学院300余名新生开展了“大学生人格特征与职业生涯规划”知识讲座。专家充分运用心理健康知识，分析了大学生特有的人格特征，引导同学如何正确把握自己的性格类型，并结合各种不同的性格特征阐述大学生在职业生涯规划中应发扬及规避的特点。 该讲座是信息学院“心灵起航，从XIN开始”大学生职业生涯规划知识普及活动的一部分。本次知识普及活动面向全院大一新生，内容包括新生职业生涯规划问卷调查、励志心理电影展播、“大学生人格特征与职业生涯规划”讲座，以及“我的未来我做主”职业生涯规划书评比等内容。希望通过这一系列活动的开展，帮助同学们更好地认识自我、接纳自我、开拓创新，从而树立正确的职业生涯规划理念及意识，加强职业规划能力，促进自身和谐发展。 （ 文 黄蓉 ）', 'http://info.jhc.cn/2012/1109/c201a1014/page.htm', NULL, '2017-05-17 14:20:21.490763');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (658, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院为毕业生就业保驾护航', '毕业生在签订就业协议等环节中经常会因为各种原因与用人单位发生纠纷，许多毕业生因为不了解相关法律法规，在处理此类问题时常处于弱势地位，无法保障自己的合法权益。信息学院考虑到2011届毕业生目前正面临就业，为增强他们的法律意识，有效保障自身权益，11月10日，学院特邀社科部副教授、兼职律师徐建丽为2012届毕业生举行了一场极具针对性的法律知识讲座。 徐建丽老师的讲座围绕“如何保障毕业生的合法劳动权益”的主题展开。她首先向大家介绍了我国基本的劳动法和国家的就业政策知识，并结合实际案例，列举就业求职过程当中常见的法律问题及维权的法律手段和途径。针对同学们提出的有关就业协议书的一些具体问题，徐建丽希望毕业生能重视就业协议书，因为它关系到就业、个人信用和法律维权事宜，并用具体的法律知识为大家做了问题解答。讲座反响强烈，深得了毕业生欢迎，大家普遍反映讲座及时，内容针对性强，对自己求职就业起到了积极的保驾护航作用。 （ 文 余经纬 ）', 'http://info.jhc.cn/2012/1109/c201a1011/page.htm', NULL, '2017-05-17 14:20:21.513053');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (659, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '我院教师在校第六届青年教师教学竞赛上喜获佳绩', '从日前结束的金华职业技术学院第六届青年教师教学竞赛上传来好消息，我院参赛教师经过学院选拔、听课评议组评议等环节，顺利通过预赛，最终在决赛中发挥出色，以良好的教育教学技能和水平喜获佳绩。其中宣翠仙老师获二等奖、尹华军（兼职教师）和陈尧飞老师获三等奖。 本次获奖的尹华军老师，作为兼职教师，从繁忙的企业事务中抽出时间，认真钻研教育教学工作，课堂教学水平稳步提升，得到了同行和学生的充分认可，本次获奖对其教学能力是一个最好的肯定。另两名教师也在赛前充分准备，为提高我院青年教师的教育教学水平，交流推广青年教师的教学技能和经验，提高学院教育教学质量树立了很好的榜样，做出了积极贡献。 （ 文 王伟斌 ）', 'http://info.jhc.cn/2012/1109/c201a1007/page.htm', NULL, '2017-05-17 14:20:32.682375');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (660, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院举办《中国共产党的光辉历程》主题讲座', '为贯彻落实团中央关于“学党史，知党情，跟党走”主题教育活动的通知精神，引导广大青年学生进一步坚定跟党走中国特色社会主义道路的理想信念，近日，信息学院第16期入党积极分子培训班特别邀请校社科部主任王红芳教授做了《中国共产党的光辉历程》主题讲座。学院党校全体学员聆听了本次讲座。 讲座围绕“中国共产党取得最辉煌的成就是什么”这一主题，从“毛泽东思想的诞生”、“中国特色社会主义理论体系”、“我国目前发展状况”和“科学发展观”四个方面展开。王红芳老师以历史的印记让生活在和平年代的我们重温了党的艰难诞生及发生的几次重大波折直到现在的成长壮大，要求同学们时刻牢记当代青年的历史使命，不断提高自身素质，自觉担当起时代的重任，坚定理想信念，增强社会责任感和历史使命感，成为拥有正确信仰的一代新人。 许多同学听完报告后说，讲座让自己深刻了解到作为社会主义事业的接班人，必须全面深入地了解党的奋斗历程和光辉业绩，才能在思想上和党中央保持一致，自觉坚持党的领导，坚定不移地走中国特色社会主义道路。 （ 文 周荃 ）', 'http://info.jhc.cn/2012/1109/c201a996/page.htm', 'http://info.jhc.cn/_upload/article/images/19/8c/8b0c3dd448f3b5e47aa1fdd2c64f/58c4a801-b08a-4de4-8752-cc98ade7baef.jpg|http://info.jhc.cn/_upload/article/images/19/8c/8b0c3dd448f3b5e47aa1fdd2c64f/1d576c13-afb7-4355-8e7c-2804850b4d6c.jpg', '2017-05-17 14:20:32.747144');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (661, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院第四届“5173订单班”开课', '11月18日，信息学院第四届“5173订单班”53名学员开始了订单班课程的正式教学。5173业务交易中心总监郭宇洲和人力资源经理孙平结合企业和自身体会，分别给学员们作了“5173公司介绍和发展规划”、“职业生涯规划”的讲座，使学员们在认识和了解5173公司的同时，也对今后自己的发展规划、奋斗目标以及如何完善自我等方面有了更具体的认识，对大学最后阶段的学习有了明确的目标。     本届“5173订单班”的课程是在校企双方通过深入沟通后确定的，课程安排与企业实际需求接轨，可操作性强，内容涉及通用技能、业务知识、入职培训等多个方面。目前每周二、四下午授课，将持续6周。 学员们对开课反映良好，大家一致认为订单班为大家提供了一个难得的学习和就业机会，并表示会抓住这次机遇，认真学习专业知识，增强实践技能，培养协作精神，努力成为企业需要的高素质技能人才。 （ 文 楼小明 ）', 'http://info.jhc.cn/2012/1109/c201a993/page.htm', NULL, '2017-05-17 14:20:32.769186');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (662, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '创新订单班培养模式 促进学生创业成才', '11月21日下午，由信息学院和IT学会理事单位合丰信息科技有限公司合作的首届合丰信息服务外包班举行开班仪式。合丰信息科技有限公司总经理孙宇烽、信息学院党总支书记朱登胜及订单班全体学员等参加开班典礼。 开班典礼上，孙宇烽总经理向全体学员介绍了从软件学习、订单制作到自主创业的工作室服务外包运作模式，并对学员们提出了殷切期望，希望他们努力践行校企合作新模式，结合校企双方的优势，为自身发展努力奋斗。朱登胜书记希望学员们珍惜机会，利用课余时间提高实践能力，树立创新创业的思想，实现自己的人生价值。 据悉，合丰信息科技（金华）有限公司是一家中美合资公司，主要从事国内外的信息技术外包服务（ITO）、业务流程外包服务（BPO）和知识流程外包服务（KPO）。其业务覆盖美国东部及中部的19个州，是金华市政府重点海内外英才引进项目企业。公司业务与规模一直处于急剧扩张状态，庞大的美国外包业务量使公司急需大量熟悉国际化规则、熟练印务设计的专门人才。此次创业式的服务外包订单班模式，不仅使学生能利用课余时间接触企业真实项目，提高专业技能水平与就业能力，同时能承接来单制作业务，实现学生用智慧、技能换取薪资酬劳的理想，是我院计算机专业群订单班开设的一种新模式。 （ 文 楼小明 宣翠仙 ）', 'http://info.jhc.cn/2012/1109/c201a988/page.htm', NULL, '2017-05-17 14:20:32.791712');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (663, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院.Net软件工程师订单班开班', '11月25日，信息学院.Net软件工程师订单班正式开课，此次订单班由信息学院主办，委托NIIT（印度国家信息技术学院）金华中心培养并推荐实习工作，共有15名分别来自计算机应用和计算机网络2个专业的同学参加订单班，他们将在4个月的实训中熟练掌握.Net技术，成为一名能够胜任岗位要求的.Net程序员。 开班典礼上，金华中心仲煜校长向同学们提出了一些作为职业人所必备的要求和素质，并希望同学们改变以往的习惯，全身心的投入到学习中去，早日成为一名合格的职业人。NIIT就业指导老师对班级进行了游戏分组，并选举出班级总经理、项目经理、财务经理等职务，和同学们共同制定班级纪律，以软件企业的制度为基础，通过上下班签到、周例会、目标管理等方式，进行订单班的管理运行。 JAVA软件工程师订单班还迎来了远道而来的NIIT总部的项目总监狄仁老师，狄仁老师用英语风趣的和同学进行了沟通，勉励同学们认真学习，将来能够成为一名出色的软件工程师，进入软件大企业工作。 （ 文 楼小明 ）', 'http://info.jhc.cn/2012/1109/c201a983/page.htm', 'http://info.jhc.cn/_upload/article/images/3f/79/e7520a434724a0248d6e8c7ee05b/2f7bc064-696f-46d7-bac3-d2e9bd517faa.jpg|http://info.jhc.cn/_upload/article/images/3f/79/e7520a434724a0248d6e8c7ee05b/c8d4f07e-5a05-45aa-95a2-174626f92dd0.jpg', '2017-05-17 14:20:32.813752');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (664, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院风行轮滑社赴杭参加“下沙杯”轮滑交流赛', '飞转的轮子、飘逸的身影，2011年第二届“下沙杯”轮滑交流赛在浙江工商大学举行。信息学院风行轮滑社应邀参加了比赛。 据悉，本次比赛有包括我校在内的杭州、绍兴、湖州、海宁、金华、宁波等地20多所高校的30多支代表队近400人参加了比赛。信息学院风行轮滑社一行六人参加了轮滑拔河、速度过桩、花式绕桩、Slide平地刹停等四项比赛。通过参赛，我校轮滑爱好者加大了技术交流的空间，加深了和其他高校间的交流，对促进社团发展起到了积极作用。', 'http://info.jhc.cn/2012/1109/c201a980/page.htm', NULL, '2017-05-17 14:20:32.835929');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (665, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院开展技能竞赛指导讲座', '为了进一步促进学院学科竞赛和学院社团发展，更好地培养技术性人才参加高层次学科竞赛，近日，信息学院开展了“IT技术、技能备战”学科技能竞赛系列讲座。讲座分别邀请到了企业信息化事业部楼小明老师、电气自动化工程部张小杭老师等学科竞赛的指导老师作了两场学科竞赛系列讲座。  讲座围绕计算机类竞赛和电子技术类两个方面展开，分别从ACM程序、电子商务、嵌入式开发以及DSP程序、挑战杯等介绍了各个竞赛的特点和参赛要求。两位老师以历届学科竞赛的参赛情况及竞赛结果，鼓励同学们积极踊跃地报名参加学科竞赛。 老师们精彩的演讲给同学们都留下了深刻的印象，这次的讲座也让同学们充分了解到学科竞赛的意义，同时也提高了同学们参加学科竞赛的热情，为以后的学科竞赛人才选拔奠定了基础。 ( 文 社团管理委员会 )', 'http://info.jhc.cn/2012/1109/c201a976/page.htm', NULL, '2017-05-17 14:20:32.858213');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (666, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '我校学子在省大学生多媒体作品设计竞赛中喜获佳绩', '日前，浙江省第十届大学生多媒体作品设计竞赛成绩揭晓，我校学子成绩斐然，共获一等奖3项，二等奖6项，三等奖5项。其中，由信息学院选送的作品《虚拟立体车库实训平台》和《心路》,艺术学院选送的作品《设计触动我心》分获课件、DV、平面类作品一等奖。本届比赛也是我校参加该项赛事以来获一等奖数量最多、获奖率最高、获奖面最广的一次。 据悉，浙江省多媒体作品设计竞赛是由省大学生科技竞赛委员会推出的全省高校赛事之一，由浙江省大学生竞赛委员会主办，设课件、平面、网站、动画及DV制作五大类，是我省参赛门类最广的一项综合性赛事，已成功举办10届，本次比赛共有来自全省70多所高校的900多件作品参加角逐。 此次我校成绩突出，是学校各级领导高度重视、各部门（学院）通力合作的结果。年初，由教务处牵头，信息学院组织，艺术学院和师范学院共同参与，建立了学生队伍选拔、二级学院强化培训、校内预选赛的创新人才培养与学科竞赛长效机制。暑假，学校组织各类竞赛指导教师参加校际竞赛经验交流与学习，共同探讨依托学科竞赛提升多媒体创新实践能力的培养途径，并在下半年分阶段进行了系统性轮训与选拔。竞赛取得的突破性成绩，为推进我校学科竞赛长效机制的有序运行开创了良好局面。  （ 文 宣翠仙 ）', 'http://info.jhc.cn/2012/1109/c201a973/page.htm', NULL, '2017-05-17 14:20:32.880448');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (667, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学子在全国电子设计与技能大赛选拔赛中喜获佳绩', '日前，由本科、高职统一比赛的“国信长天杯”第三届全国电子专业人才设计与技能大赛选拔赛结果揭晓，信息学院在此次选拔赛中喜获佳绩，其中，电子设计与开发项目获一等奖2项，二等奖1项，三等奖4项；单片机设计与开发项目获二等奖2项，三等奖3项，优秀奖3项；嵌入式设计与开发项目获一等奖1项。根据比赛规则，本次大赛二等奖以上的6个同学将于12月下旬赴北京参加决赛。 据悉，“国信长天杯”全国电子人才设计与技能大赛是工业和信息化部人才交流中心主办的全国信息技术类专业竞赛。大赛以“立足行业、突出实践、广泛参与、促进就业” 为宗旨，经过两届大赛的成功举办，大赛“重视工程实践素质、竞赛过程公平公正”的两大特色获得了电子信息技术类院系师生的广泛赞誉和信任。本届比赛在全国40余座城市同时开赛，来自全国300余所高校，4000余名电子信息类大学生参加。大赛设电子设计与开发、单片机设计与开发、嵌入式设计与开发等项目。 信息学院此次在该赛事上取得的优异成绩，标志着学院在学生创新能力培养上取得了成效。近几年，信息学院十分关注学生创新意识及创新能力的培养，投入大量经费于各类学科竞赛，同时狠抓学风建设，积极推动学生创新团队的建设，涌现出了一大批学习能力强，对电子技术兴趣浓厚的同学，通过艰苦磨练，练就了过硬的本领，为大赛取得好成绩奠定了坚实的基础。 （ 文 余红娟 刘子坚 ）', 'http://info.jhc.cn/2012/1109/c201a970/page.htm', NULL, '2017-05-17 14:20:32.902643');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (668, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学子在2011年全国通信行业职业技能大赛中成绩斐然', '11月26-27日，2011年全国通信行业职业技能大赛（电信机务员——下一代网络及信息技术应用）在北京信息职业技术学院举行，信息学院参赛选手成绩斐然，共取得了1个团体一等奖，1个团体三等奖的好成绩。这是近年来信息学院在全国一类竞赛中获得的最好成绩。 全国通信行业职业技能大赛（电信机务员——下一代网络及信息技术应用）是由工业和信息化部、人力资源和社会保障部、中华全国总工会、共青团中央等四部委联合主办的国家一类竞赛。大赛由工业和信息化部通信行业职业鉴定中心承办，教育部高等学校高职高专电子信息类专业教学指导委员会、北京信息职业技术学院、中兴通讯NC教育管理中心协办。本次比赛共有来自全国22所高职院校38只代表队参赛。大赛结合国务院《关于加快培育和发展战略性新兴产业的决定》中重点扶植和发展的战略新兴产业——新一代信息技术，旨在推动高校培养下一代网络和信息技术的迅猛发展所需要的大批量、高素质应用技能型人才，大赛以下一代网络技术为基础、在统一融合的业务平台上对语音、数据、多媒体等业务的应用技能进行竞赛。竞赛项目包括：企业融合通信业务技能、网真视讯业务技能。                                                     本次大赛取得的优异成绩，充分展示了信息学院梯队建设、团队培养的学科竞赛长效机制的优势，也是应用电子技术、通信网络与设备、计算机网络技术等多专业发挥各自优势，分工合作，积极协调的结果，为完成多技术融合的高新技术应用领域大赛积累了丰富经验。 （ 文 余红娟 苏红富 ）', 'http://info.jhc.cn/2012/1109/c201a963/page.htm', 'http://info.jhc.cn/_upload/article/images/51/2b/644cccdf43179a28032ff6086725/4683a603-88b9-4536-a6b3-3bb65501b26c.jpg', '2017-05-17 14:20:32.925036');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (669, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '龚永坚院长获11月份浙江“创先争优闪光言行之星”称号', '据浙江在线之江先锋网消息，11月份浙江“创先争优闪光言行之星”日前产生，经过层层展示评比，我校信息学院院长龚永坚获评11月份浙江“创先争优闪光言行之星”。 据悉，“创先争优闪光言行”展示评选活动是省委创先争优活动领导小组为进一步激发广大基层党组织和党员干事创业的热情和动力，形成比学习、比工作、比奉献和学先进、赶先进、当先进的浓厚氛围，展示宣传创先争优活动中涌现出的先进典型和感人事迹，深化履诺评诺，兴起创先争优活动新高潮而在全省范围内开展的。“创先争优闪光言行之星”旨在评选出浙江省基层党组织和党员在服务发展、服务社会、服务群众、服务党员方面的先进事迹和典型做法。 龚永坚院长作为一名党员干部，用自己的行动，在推动学院创新发展、深化学院教学改革等方面呕心沥血，做出了突出贡献，并且获得了浙江省师德先进个人、浙江省教学名师，金华市拔尖人才等诸多荣誉，此次获评浙江省“创先争优闪光言行之星”，是对他工作的一次肯定，也是对信息学院创先争优工作的一次肯定，必将激励学院师生珍惜荣誉，在创先争优活动中艰苦创业，为学院发展做出更大贡献。 相关链接：http://zjnews.zjol.com.cn/05zjnews/system/2011/12/05/018051618.shtml （ 文 王伟斌 ）', 'http://info.jhc.cn/2012/1109/c201a959/page.htm', NULL, '2017-05-17 14:20:32.947218');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (670, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '学校隆重举行纪念“一二•九”歌咏活动', '为纪念“一二•九”学生爱国运动76周年，激励我校大学生发扬爱国运动精神，发奋学习，立志成才，12月9日晚，由校党委主办，宣传部、信息学院共同承办的纪念“一二•九”爱国运动歌咏活动在实验剧场隆重举行。党委副书记余党军，各相关职能部门负责人、各学院总支书记、学工办团委负责人及师生代表等500余人观看了比赛。 余党军在致辞中指出，开展纪念“一二•九”爱国运动歌咏活动，是我校贯彻落实中共中央十七届六中全会关于推动社会主义文化大发展大繁荣，努力建设社会主义文化强国的具体体现；是宏扬革命传统、激发爱国热情、凝聚青年人心、展现青春风采的平台；更是着力营造我校优秀、积极的校园文化氛围的需要。     本次歌咏比赛主题为“大学生爱祖国”，共有11支代表队500余人参加。随着《游击队歌》、《洪湖水，浪打浪》、《浏阳河》等一首首耳熟能详的主旋律歌曲一一唱响，整个实验剧场汇聚成了红色的海洋，各代表队通过不同形式深情演绎了他们的爱党爱国之热情，同时也充分展示自己学院的特色和文化底蕴，精彩的表演获得评委和现场观众的一致好评。经过激烈比拼，最终，医学院代表队以一首《上海叫卖小调》获得一等奖，师范学院、信息学院获二等奖，其他学院分获三等奖和优秀奖。 （ 文 汪铭铠 ）', 'http://info.jhc.cn/2012/1109/c201a951/page.htm', 'http://info.jhc.cn/_upload/article/images/87/6e/fd41c8ca4f1cb2bed6e9f2cc50be/8f38966b-1b70-4db8-837e-324d50fefd8b.jpg', '2017-05-17 14:20:32.969414');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (671, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院举行“消防安全”演讲比赛', '11月是学校消防安全月，为了提高同学们的消防意识，积极落实学校消防安全知识普及活动，11月14日，信息学院举行了一场以“消防安全”为主题的演讲比赛。 本次演讲比赛经过报名和预赛，共有12名同学进入决赛。比赛中，参赛选手围绕着消防安全知识，消防自救措施，从自己的视角出发，用所见所闻和鲜活生动的例子向同学们介绍了消防安全措施的重要性，唤起大家重视消防安全，珍爱生命的意识。 通过演讲比赛这种同学们喜闻乐见的形式，加大了学院消防知识宣传的力度，提高了师生的防火防灾意识，起到了非常好的引导与提醒作用，为学院公寓消防管理工作的开展打下了结实的基础。 （ 文 汪铭铠 ）', 'http://info.jhc.cn/2012/1109/c201a947/page.htm', NULL, '2017-05-17 14:20:32.991758');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (672, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院网络化管理助推毕业环节教学工作', '为帮助即将毕业的2012届同学明确顶岗实习与毕业设计的规章制度，促进校、企、学生的多方互动互赢，12月12-13日，信息学院以专业为单位召开了2012届毕业生顶岗实习与毕业设计动员大会。结合网络化管理工作，学院教科办及各专业负责人详细介绍了毕业教学环节各阶段工作安排，强调了顶岗实习与毕业设计的能力培养目标，细致分析了基于教学质量管理系统的网络化管理办法。 据悉，针对毕业教学环节中出现的师生互动缺乏等问题，信息学院近几年推出了毕业环节的网络化管理研究，经过几年的运行，毕业生的顶岗实习和毕业论文设计等教学工作的时效性明显提高，考核体系进一步完善，有力的促进了毕业教学工作的开展。2012届毕业教学工作仍将在网络化环境中进行管理，此次召开专题动员会，旨在引导同学们在实习过程中充分发挥主观能动性，及时有效的与指导老师沟通，认真完成毕业设计，不断增强岗位意识，学会吃苦耐劳，为职业素养与岗位能力的提升打下良好的基础。 （ 文 陈静泓 ）', 'http://info.jhc.cn/2012/1109/c201a943/page.htm', NULL, '2017-05-17 14:20:33.022329');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (673, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '省科协重点学术活动暨省电源学会学术研讨会在我校举行', '12月9日-11日，浙江省科协2011年重点学术活动暨浙江省电源学会学术研讨会——“电力电子节能新技术与新应用研讨会”在我校信息学院举行。会议邀请了浙江大学、合肥工业大学、飞利浦照明系统与控制全球研发中心等单位的著名专家作专题学术报告。浙江省电源学会50余家理事单位代表、省内电力电子与电气工程领域的技术专家和高校教师共计120余人参加了会议。校领导蒋忠樟副院长应邀出席，学校科技处领导、信息学院领导和电子类专业教师参加会议。  蒋忠樟在开幕式上致辞。他对来自国内知名高校、行业企业的各位专家学者来我校参加学术交流表示欢迎，并向来宾介绍了学校办学情况和“五位一体”育人模式，同时他希望与会专家能为我校的教育教学改革和专业建设提出宝贵的意见和建议。浙江省电源学会理事长、中国电源学会学术工作委员会主任、浙江大学工学部副主任徐德鸿教授致开幕词。他首先对我校为筹备本次年会所作的细致工作表示感谢。接着，他从国家经济持续发展和能源安全战略的高度，分析了电力电子技术在实现电能高效利用、新能源开发、提高生产效率等方面的重要作用。他指出，本次学术研讨会重点对光伏发电、智能照明、模块电源等前沿电源技术进行专题学术报告和论文交流，本次研讨也必将对促进浙江省电源技术的产学研交流与合作、推广电源新技术起到积极作用。 会议期间，各位专家学者和行业代表参观了我校电子信息技术中心，对我校在推进校企利益共同体建设、高端技能型人才培养、校内实验实训环境建设、教师自制实训设备、教师教科研工作等方面给予了高度肯定。有近40余家会员单位的乐清电源行业协会下属的公司老总、技术专家对我校电子电气类专业的办学理念和人才培养表现了极大兴趣，并就下一步共建电力电子联合实验室、开展人才订单培养等方面达成了初步的合作意向。来自杭州、嘉兴、湖州等地的电源企业也就加强校企合作、毕业生就业等方面进行了洽谈。 据悉，信息学院于今年年初向浙江省电源学会提出承办本次省科协重点学术活动，后经理事会常务理事会议研究决定选址我校举行。面对当前浙江省和金华市在LED照明、光伏产业、新能源等领域快速发展的形势下，承办此次学术会议，对我校电子类专业对接区域产业升级和战略性新兴产业，做特做强专业均具有重要意义。研讨会上国内知名学者精彩的专题学术报告和论文交流，也为我院教师了解和学习电源技术新动向、新发展提供了一次难得的学习机会。 （文 陈海荣 王伟斌  / 摄影林雪华）', 'http://info.jhc.cn/2012/1109/c201a937/page.htm', 'http://info.jhc.cn/_upload/article/images/ed/ea/852f2d1c4a7385ccea0fef4e83c1/366ea5fe-6f72-4182-9199-daddbceaccf4.jpg', '2017-05-17 14:20:43.087979');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (674, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '金职院外包培训中心工作受到省商务厅肯定', '12月9日—10日，由浙江省商务厅主办、金华市商务局及金东区信息软件园协办的省服务外包人才培训工作会议在金举行。会上，我校信息学院外包培训中心工作受到省商务厅的肯定，单天德老师应邀在会上对我校外包培训中心筹建以来的服务外包人才培养、培训机构建设等方面的工作发言，向兄弟单位作了经验介绍。  据悉，本次会议是在我省国际服务外包蓬勃发展的背景下，为进一步做好服务外包人才培训机构的扶持培育工作，提高各市服务外包人才培训工作水平而召开的，会上还介绍了“国际服务外包人才培训综合考试管理办法”等文件，对加快我省服务外包人才培养，切实提升服务外包人才质量，推动我省服务外包产业的发展有着重要的促进作用。   （ 文 单天德 ）', 'http://info.jhc.cn/2012/1109/c201a931/page.htm', 'http://info.jhc.cn/_upload/article/images/1e/47/29cabdf143e8ac05a6f4b788a0b8/cb4496a8-e87c-4b60-8b3f-d691d46d444b.jpg', '2017-05-17 14:20:43.142746');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (675, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '爱心在金职涌动', '“人间自有真情在，献出鲜血播下爱”，12月14日,信息学院得知我市血库告急的消息，在稻花香广场举行了无偿献血活动，一群群年轻的学子以自己的实际行动向社会奉献了一片爱心。  中午12时，前来献血的同学在报名处排起了长长的队伍。负责采血工作的金华市血站工作人员给同学们详细介绍了无偿献血的相关知识和注意事项，让同学们更好的了解无偿献血的益处和献血的现实意义。  据悉，今天一天共有近80名学生参加了此次献血活动，献血量达20000cc。这次献血活动充分体现了同学们热心助人、积极参加社会公益事业的良好品质和精神风貌。献血中的无私奉献、勇敢团结的精神激励我们创造更美好的生活! ( 文 汪铭铠 )', 'http://info.jhc.cn/2012/1109/c201a922/page.htm', 'http://info.jhc.cn/_upload/article/images/4a/1a/b63e59d7427e9dd3d191bd2b2e86/4fde9d6f-7ec9-411e-a966-43cb97602a48.jpg|http://info.jhc.cn/_upload/article/images/4a/1a/b63e59d7427e9dd3d191bd2b2e86/0b885bad-ebf5-4b27-816e-8ce4f06c8ab3.jpg', '2017-05-17 14:20:43.162533');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (676, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '我院王静老师获联想校企合作班班主任认证', '近日，FY11联想发布班主任认证成绩，我院王静老师获得FY11联想校企合作班班主任认证。 成绩认定书说，鉴于浙江金华职业技术学院校企合作班的管理需要，为做好专班管理人员的储备工作，联想教育培训部于12月13日组织了班主任远程认证，王静老师工作经验丰富，已经管理两届合作班，熟悉合作实施要求，顺利通过班主任认证。对王静老师的评语是，有很丰富的管理经验，对工作有责任心，有方法，思路清晰。建议深入了解校企合作的模式，加强对学生服务规范的指导。本次王静老师获得联想的班主任认证，表明我院校企合作，特别是与联想的校企合作又迈上了一个新台阶。 （ 文 王伟斌 ）', 'http://info.jhc.cn/2012/1109/c201a917/page.htm', NULL, '2017-05-17 14:20:43.182158');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (677, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院召开第四次团代会暨第九次学代会', '青春激扬，号角嘹亮。12月18日晚，共青团金华职业技术学院信息工程学院第四次团员代表大会暨第九次学生代表大会在大学生活动中心隆重举行。院党总支书记朱登胜、校团委副书记程春鹏、党总支委员吴晓明等出席大会，来自全院各班级的203名师生团员、学生代表参加大会。大会由团委副书记郑江同学主持。    大会在庄严的国歌声中开幕。程春鹏代表校团委致辞，他对大会的胜利召开表示祝贺，对我院上届共青团、学生工作给予了高度评价和充分认可，同时对今后的工作寄予厚望。朱登胜在会上发言，他希望各位代表带领全体同学积极参加学校“第二课堂”活动，善于理论联系实际，恪守底线，培养爱国爱校情怀，发挥学生组织的自身优势，秉承“知行合一、务实创新”的校训精神，不懈努力，勤奋工作，培养对国家的忠诚、对党的信仰、对学院的责任。    院团委副书记金乐、学生会主席徐俊杰分别代表上届团委和学生会作了大会工作总结报告。大会还选举产生了金乐等11位团委委员会委员以及13位学生委员会委员。 （ 文 汪铭铠 ）', 'http://info.jhc.cn/2012/1109/c201a908/page.htm', 'http://info.jhc.cn/_upload/article/images/ae/3c/cfff31084ed385dce1d56755bf22/12e61f11-4009-42b1-ab91-dc42be858235.jpg|http://info.jhc.cn/_upload/article/images/ae/3c/cfff31084ed385dce1d56755bf22/08b2bcfb-ad00-416c-a290-3340985e846d.jpg', '2017-05-17 14:20:43.203431');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (678, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院举办“翼博杯”新生辩论赛', '为提高学院学生的思辨能力，培养同学们的团队精神，丰富校园活动，信息学院翼天演辩社日前在2011级新生中举行了以“智慧思辨求真知 青春激荡话理想”为主题的辩论赛。 经过一个半月预赛、决赛角逐，最终电商112班和最佳辩手队进入总结赛，争夺比赛的冠军。决赛围绕“大学生应该坚持理想还是接受现实”展开，辩手们唇枪舌战、据理力争，时而咄咄逼人，时而诙谐幽默,充分展示了班级魅力以及团队协作性，体现了辩手们对“理想、现实”这一矛盾体的思考，引发场下观众的深思。最终电商112班技高一筹，赢得比赛的冠军。 本次辩论赛旨在体现学生朝气蓬勃、积极向上的精神风貌，引导同学们正确的树立人生观、价值观、世界观，更好的面对大学生活。 ( 文 汪铭铠 )', 'http://info.jhc.cn/2012/1109/c201a903/page.htm', NULL, '2017-05-17 14:20:43.22574');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (679, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '我院电气自动化教学团队获批校级优秀教学团队', '近日，学校下发《金华职业技术学院关于公布第一届校级优秀教学团队评审结果的通知》文件，我院电气自动化技术专业教学团队获批学校第一届校级优秀教学团队。 据悉，我院电气自动化技术专业教学团队在前期建设过程中，以课程建设、专业建设为基础，以深化教学改革与教育创新为动力，形成团队合作的良好机制，在师资建设上，注重“双师”结构的教学团队建设，努力提高教师素质和教学能力，推动教学内容和教学方法的改革和研究，促进教学研讨和教学经验交流，培养了一支可持续发展的教学队伍。此次获批首届校级优秀教学团队，对于电气自动化技术专业加大教学团队建设力度，充分发挥教学团队在教改、科研、人才培养中的带头和示范作用，推动学院师资队伍建设，不断提高学院教育教学质量必将发挥积极作用。 （ 文 王伟斌 ）', 'http://info.jhc.cn/2012/1109/c201a900/page.htm', NULL, '2017-05-17 14:20:43.247882');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (680, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学子在全国信息技术应用水平大赛喜获佳绩', '12月20日，第六届全国信息技术应用水平大赛在北京大学百周年纪念讲堂落下帷幕，信息学子肖旭、胡枭飞分别在嵌入式开发与应用和3G移动通信技术项目中取得全国二等奖，由于我校组织工作出色，还荣获大赛组织奖。 据悉，全国信息技术应用水平大赛是由教育部信息管理中心主办，主要面向高校、高职、中职及民办院校在校学生，旨在考察学生的动手能力，提高学生的就业竞争力。大赛历经预赛、复赛、决赛三个阶段，全国有152404人参加了预赛，其中17632人通过选拔进入复赛，再经过激烈的复赛，1062名选手进入决赛。参赛选手中50%为本科院校的学生，竞争异常激烈。 信息学院十分重视“学生创新意识的培养，创新能力的提高”，十分关注学生的信息技术应用能力。本次大赛的成绩再次展示了信息学院梯队建设、团队培养的学科竞赛长效机制的优越性，为培养学生应用信息技术能力积累了宝贵经验。 ( 文 刘子坚 ）', 'http://info.jhc.cn/2012/1109/c201a897/page.htm', NULL, '2017-05-17 14:20:43.270306');
INSERT INTO news (id, author, origin, publisher, pubtime, title, content, website, content_img, create_at) VALUES (681, '', '王伟斌', 'admin', '2012-01-06 00:00:00', '信息学院第五届“5173订单班”举行开班典礼', '12月21日上午，信息学院第五届“5173电子商务订单班”举行开班典礼。金华利诚信息技术有限公司总经理池水明出席，学院党总支书记朱登胜、各部门负责人及第五届5173电子商务订单班全体学员参加。典礼由学院副院长陈海荣主持。 池水明在发言时说，这一届5173订单班是在校企进一步深化合作，构建利益共同体的基础上，对历届5173订单班成长经历调研的基础上组建的，公司方在思考与总结后对这一届订单班提供了最优越的待遇与条件，希望同学们珍惜机会，努力工作，早日成为公司岗位上的骨干。订单班学员代表吴淑君代表全体订单班学员表示，将珍惜学校与企业给予的机会，用实际行动向大家汇报。朱登胜希望大家要尊重同学和同事，加强相互沟通，积极主动学习，并要始终为企业服务、为企业做贡献，从而促进自身成长。 本届订单班已经是信息学院与金华利诚信息技术有限公司校企合作的第五届了，公司方已累计为订单班学生提共了24万奖学金，目前已有110余人在公司就业。 （ 文 楼小明 ）', 'http://info.jhc.cn/2012/1109/c201a894/page.htm', NULL, '2017-05-17 14:20:43.299479');
